INSERT INTO `en_transliteration` VALUES (1, 'Bismi Allahi a **l** rrahmani a **l** rraheem **i**');
INSERT INTO `en_transliteration` VALUES (2, 'Alhamdu lillahi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3, 'A **l** rrahmani a **l** rraheem **i**');
INSERT INTO `en_transliteration` VALUES (4, 'Maliki yawmi a **l** ddeen **i**');
INSERT INTO `en_transliteration` VALUES (5, 'Iyyaka na\'budu waiyyaka nasta\'een **u**');
INSERT INTO `en_transliteration` VALUES (6, 'Ihdina a **l** ssirata almustaqeem **a**');
INSERT INTO `en_transliteration` VALUES (7, 'Sirata allatheena an\'amta \'alayhim ghayri almaghdoobi \'alayhim wala a **l** ddalleen **a**');
INSERT INTO `en_transliteration` VALUES (8, 'Aliflammeem');
INSERT INTO `en_transliteration` VALUES (9, 'Thalika alkitabu la rayba feehi hudan lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (10, 'Allatheena yuminoona bi **a** lghaybi wayuqeemoona a **l** ssalata wamimma razaqnahum yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (11, 'Wa **a** llatheena yuminoona bima onzila ilayka wama onzila min qablika wabi **a** lakhirati hum yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (12, 'Olaika \'ala hudan min rabbihim waolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (13, 'Inna allatheena kafaroo sawaon \'alayhim aanthartahum am lam tunthirhum la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (14, 'Khatama Allahu \'ala quloobihim wa\'ala sam\'ihim wa\'ala absarihim ghishawatun walahum \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (15, 'Wamina a **l** nnasi man yaqoolu amanna biAllahi wabi **a** lyawmi alakhiri wama hum bimumineen **a**');
INSERT INTO `en_transliteration` VALUES (16, 'Yukhadi\'oona Allaha wa **a** llatheena amanoo wama yakhda\'oona illa anfusahum wama yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (17, 'Fee quloobihim maradun fazadahumu Allahu maradan walahum \'athabun aleemun bima kanoo yakthiboon **a**');
INSERT INTO `en_transliteration` VALUES (18, 'Waitha qeela lahum la tufsidoo fee alardi qaloo innama nahnu muslihoon **a**');
INSERT INTO `en_transliteration` VALUES (19, 'Ala innahum humu almufsidoona walakin la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (20, 'Waitha qeela lahum aminoo kamaamana a **l** nnasu qaloo anuminu kamaamana a **l** ssufahao ala innahum humu a **l** ssufahao walakin la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (21, 'Waitha laqoo allatheena amanoo qaloo amanna waitha khalaw ila shayateenihim qaloo inna ma\'akum innama nahnu mustahzioon **a**');
INSERT INTO `en_transliteration` VALUES (22, 'Allahu yastahzio bihim wayamudduhum fee tughyanihim ya\'mahoon **a**');
INSERT INTO `en_transliteration` VALUES (23, 'Olaika allatheena ishtarawoo a **l** ddalalata bi **a** lhuda fama rabihat tijaratuhum wama kanoo muhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (24, 'Mathaluhum kamathali allathee istawqada naran falamma adaat mahawlahu thahaba Allahu binoorihim watarakahum fee _th_ ulumatin la yubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (25, 'Summun bukmun \'umyun fahum la yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (26, 'Aw kasayyibin mina a **l** ssamai feehi _th_ ulumatun wara\'dun wabarqun yaj\'aloona asabi\'ahum fee athanihim mina a **l** ssawa\'iqi hathara almawti wa **A** llahu muheetun bi **a** lkafireen **a**');
INSERT INTO `en_transliteration` VALUES (27, 'Yakadu albarqu yakhtafu absarahum kullama adaa lahum mashaw feehi waitha a _th_ lama \'alayhim qamoo walaw shaa Allahu lathahaba bisam\'ihim waabsarihim inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (28, 'Ya ayyuha a **l** nnasu o\'budoo rabbakumu allathee khalaqakum wa **a** llatheena min qablikum la\'allakum tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (29, 'Allathee ja\'ala lakumu alarda firashan wa **al** ssamaa binaan waanzala mina a **l** ssamai maan faakhraja bihi mina a **l** ththamarati rizqan lakum fala taj\'aloo lillahi andadan waantum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (30, 'Wain kuntum fee raybin mimma nazzana \'ala \'abdina fatoo bisooratin min mithlihi wa **o** d\'oo shuhadaakum min dooni Allahi in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (31, 'Fain lam taf\'aloo walan taf\'aloo fa **i** ttaqoo a **l** nnara allatee waqooduha a **l** nnasu wa **a** lhijaratu o\'iddat lilkafireen **a**');
INSERT INTO `en_transliteration` VALUES (32, 'Wabashshiri allatheena amanoo wa\'amiloo a **l** ssalihati anna lahum jannatin tajree min tahtiha alanharu kullama ruziqoo minha min thamaratin rizqan qaloo hatha allathee ruziqna min qablu waotoo bihi mutashabihan walahum feeha azwajun mutahharatun wahum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (33, 'Inna Allaha la yastahyee an yadriba mathalan ma ba\'oodatan fama fawqaha faamma allatheena amanoo faya\'lamoona annahu alhaqqu min rabbihim waamma allatheena kafaroo fayaqooloona matha arada Allahu bihatha mathalan yudillu bihi katheeran wayahdee bihi katheeran wama yudillu bihi illa alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (34, 'Allatheena yanqudoona \'ahda Allahi min ba\'di meethaqihi wayaqta\'oona ma amara Allahu bihi an yoosala wayufsidoona fee alardi olaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (35, 'Kayfa takfuroona bi **A** llahi wakuntum amwatan faahyakum thumma yumeetukum thumma yuhyeekum thumma ilayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (36, 'Huwa allathee khalaqa lakum ma fee alardi jamee\'an thumma istawa ila a **l** ssamai fasawwahunna sab\'a samawatin wahuwa bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (37, 'Waith qala rabbuka lilmalaikati innee ja\'ilun fee alardi khaleefatan qaloo ataj\'alu feeha man yufsidu feeha wayasfiku a **l** ddimaa wanahnu nusabbihu bihamdika wanuqaddisu laka qala innee a\'lamu ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (38, 'Wa\'allama adama alasmaa kullaha thumma \'aradahum \'ala almalaikati faqala anbioonee biasmai haolai in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (39, 'Qaloo subhanaka la \'ilma lana illa ma \'allamtana innaka anta al\'aleemu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (40, 'Qala yaadamu anbihum biasmaihim falamma anbaahum biasmaihim qala alam aqul lakum innee a\'lamu ghayba a **l** ssamawati wa **a** lardi waa\'lamu ma tubdoona wama kuntum taktumoon **a**');
INSERT INTO `en_transliteration` VALUES (41, 'Waith qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa aba wa **i** stakbara wakana mina alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (42, 'Waqulna yaadamu oskun anta wazawjuka aljannata wakula minha raghadan haythu shituma wala taqraba hathihi a **l** shshajarata fatakoona mina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (43, 'Faazallahuma a **l** shshaytanu \'anha faakhrajahuma mimma kana feehi waqulna ihbitoo ba\'dukum liba\'din \'aduwwun walakum fee alardi mustaqarrun wamata\'un ilaheen **in**');
INSERT INTO `en_transliteration` VALUES (44, 'Fatalaqqaadamu min rabbihi kalimatin fataba \'alayhi innahu huwa a **l** ttawwabu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (45, 'Qulna ihbitoo minha jamee\'an faimma yatiyannakum minnee hudan faman tabi\'a hudaya fala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (46, 'Wa **a** llatheena kafaroo wakaththaboo biayatina olaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (47, 'Ya banee israeela othkuroo ni\'matiya allatee an\'amtu \'alaykum waawfoo bi\'ahdee oofi bi\'ahdikum waiyyaya fa **i** rhaboon **i**');
INSERT INTO `en_transliteration` VALUES (48, 'Waaminoo bima anzatu musaddiqan lima ma\'akum wala takoonoo awwala kafirin bihi wala tashtaroo biayatee thamanan qaleelan waiyyaya fa **i** ttaqoon **i**');
INSERT INTO `en_transliteration` VALUES (49, 'Wala talbisoo alhaqqa bi **a** lbatili wataktumoo alhaqqa waantum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (50, 'Waaqeemoo a **l** ssalata waatoo a **l** zzakata wa **i** rka\'oo ma\'a a **l** rraki\'een **a**');
INSERT INTO `en_transliteration` VALUES (51, 'Atamuroona a **l** nnasa bi **a** lbirri watansawna anfusakum waantum tatloona alkitaba afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (52, 'Wa **i** sta\'eenoo bi **al** ssabri wa **al** ssalati wainnaha lakabeeratun illa \'ala alkhashi\'een **a**');
INSERT INTO `en_transliteration` VALUES (53, 'Allatheena ya _th_ unnoona annahum mulaqoo rabbihim waannahum ilayhi raji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (54, 'Ya banee israeela othkuroo ni\'matiya allatee an\'amtu \'alaykum waannee faddatukum \'ala al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (55, 'Wa **i** ttaqoo yawman la tajzee nafsun \'an nafsin shayan wala yuqbalu minha shafa\'atun wala yukhathu minha \'adlun wala hum yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (56, 'Waith najjaynakum min ali fir\'awna yasoomoonakum sooa al\'athabi yuthabbihoona abnaakum wayastahyoona nisaakum wafee thalikum balaon min rabbikum \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (57, 'Waith faraqna bikumu albahra faanjaynakum waaghraqnaala fir\'awna waantum tan _th_ uroon **a**');
INSERT INTO `en_transliteration` VALUES (58, 'Waith wa\'adna moosa arba\'eena laylatan thumma ittakhathtumu al\'ijla min ba\'dihi waantum _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (59, 'Thumma \'afawna \'ankum min ba\'di thalika la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (60, 'Waithatayna moosa alkitaba wa **a** lfurqana la\'allakum tahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (61, 'Waith qala moosa liqawmihi ya qawmi innakum _th_ alamtum anfusakum bi **i** ttikhathikumu al\'ijla fatooboo ila bariikum fa **o** qtuloo anfusakum thalikum khayrun lakum \'inda bariikum fataba \'alaykum innahu huwa a **l** ttawwabu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (62, 'Waith qultum ya moosa lan numina laka hatta nara Allaha jahratan faakhathatkumu a **l** ssa\'iqatu waantum tan _th_ uroon **a**');
INSERT INTO `en_transliteration` VALUES (63, 'Thumma ba\'athnakum min ba\'di mawtikum la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (64, 'Wa _th_ allana \'alaykumu alghamama waanzana \'alaykumu almanna wa **al** ssalwa kuloo min tayyibati ma razaqnakum wama _th_ alamoona walakin kanoo anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (65, 'Waith qulna odkhuloo hathihi alqaryata fakuloo minhahaythu shitum raghadan wa **o** dkhuloo albaba sujjadan waqooloo hittatun naghfir lakum khatayakum wasanazeedu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (66, 'Fabaddala allatheena _th_ alamoo qawlan ghayra allathee qeela lahum faanzana \'ala allatheena _th_ alamoo rijzan mina a **l** ssamai bima kanoo yafsuqoon **a**');
INSERT INTO `en_transliteration` VALUES (67, 'Waithi istasqa moosa liqawmihi faqulna idrib bi\'asaka alhajara fa **i** nfajarat minhu ithnata \'ashrata \'aynan qad \'alima kullu onasin mashrabahum kuloo wa **i** shraboo min rizqi Allahi wala ta\'thaw fee alardi mufsideen **a**');
INSERT INTO `en_transliteration` VALUES (68, 'Waith qultum ya moosa lan nasbira \'alata\'amin wahidin fa **o** d\'u lana rabbaka yukhrij lana mimma tunbitu alardu min baqliha waqiththaiha wafoomiha wa\'adasiha wabasaliha qala atastabdiloona allathee huwa adna bi **a** llathee huwa khayrun ihbitoo misran fainna lakum ma saatum waduribat \'alayhimu a **l** ththillatu wa **a** lmaskanatu wabaoo bighadabin mina Allahi thalika biannahum kanoo yakfuroona biayati Allahi wayaqtuloona a **l** nnabiyyeena bighayri alhaqqi thalika bima \'asaw wakanoo ya\'tadoon **a**');
INSERT INTO `en_transliteration` VALUES (69, 'Inna allatheena amanoo wa **a** llatheena hadoo wa **al** nnasara wa **al** ssabieena man amana biAllahi wa **a** lyawmi alakhiri wa\'amila salihan falahum ajruhum \'inda rabbihim wala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (70, 'Waith akhathna meethaqakum warafa\'na fawqakumu a **l** ttoora khuthoo maataynakum biquwwatin wa **o** thkuroo ma feehi la\'allakum tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (71, 'Thumma tawallaytum min ba\'di thalika falawla fadlu Allahi \'alaykum warahmatuhu lakuntum mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (72, 'Walaqad \'alimtumu allatheena i\'tadaw minkum fee a **l** ssabti faqulna lahum koonoo qiradatan khasieen **a**');
INSERT INTO `en_transliteration` VALUES (73, 'Faja\'anaha nakalan lima bayna yadayha wama khalfaha wamaw\'i _th_ atan lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (74, 'Waith qala moosa liqawmihi inna Allaha yamurukum an tathbahoo baqaratan qaloo atattakhithuna huzuwan qala a\'oothu biAllahi an akoona mina aljahileen **a**');
INSERT INTO `en_transliteration` VALUES (75, 'Qaloo od\'u lana rabbaka yubayyin lana ma hiya qala innahu yaqoolu innaha baqaratun la faridun wala bikrun \'awanun bayna thalika fa **i** f\'aloo ma tumaroon **a**');
INSERT INTO `en_transliteration` VALUES (76, 'Qaloo od\'u lana rabbaka yubayyin lana ma lawnuha qala innahu yaqoolu innaha baqaratun safrao faqi\'un lawnuha tasurru a **l** nna _th_ ireen **a**');
INSERT INTO `en_transliteration` VALUES (77, 'Qaloo od\'u lana rabbaka yubayyin lana ma hiya inna albaqara tashabaha \'alayna wainna in shaa Allahu lamuhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (78, 'Qala innahu yaqoolu innaha baqaratun lathaloolun tutheeru alarda wala tasqee alhartha musallamatun la shiyata feeha qaloo alana jita bi **a** lhaqqi fathabahooha wama kadoo yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (79, 'Waith qatatum nafsan fa **i** ddaratum feeha wa **A** llahu mukhrijun ma kuntum taktumoon **a**');
INSERT INTO `en_transliteration` VALUES (80, 'Faqulna idriboohu biba\'diha kathalika yuhyee Allahu almawta wayureekum ayatihi la\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (81, 'Thumma qasat quloobukum min ba\'di thalika fahiya ka **a** lhijarati aw ashaddu qaswatan wainna mina alhijarati lama yatafajjaru minhu alanharu wainna minha lama yashshaqqaqu fayakhruju minhu almao wainna minha lama yahbitu min khashyati Allahi wama Allahu bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (82, 'Afatatma\'oona an yuminoo lakum waqad kana fareequn minhum yasma\'oona kalama Allahi thumma yuharrifoonahu min ba\'di ma \'aqaloohu wahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (83, 'Waitha laqoo allatheena amanoo qaloo amanna waitha khala ba\'duhum ila ba\'din qaloo atuhaddithoonahum bima fataha Allahu \'alaykum liyuhajjookum bihi \'inda rabbikum afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (84, 'Awala ya\'lamoona anna Allaha ya\'lamu ma yusirroona wama yu\'linoon **a**');
INSERT INTO `en_transliteration` VALUES (85, 'Waminhum ommiyyoona la ya\'lamoona alkitaba illa amaniyya wain hum illa ya _th_ unnoon **a**');
INSERT INTO `en_transliteration` VALUES (86, 'Fawaylun lillatheena yaktuboona alkitaba biaydeehim thumma yaqooloona hatha min \'indi Allahi liyashtaroo bihi thamanan qaleelan fawaylun lahum mimma katabat aydeehim wawaylun lahum mimma yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (87, 'Waqaloo lan tamassana a **l** nnaru illa ayyaman ma\'doodatan qul attakhathtum \'inda Allahi \'ahdan falan yukhlifa Allahu \'ahdahu am taqooloona \'ala Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (88, 'Bala man kasaba sayyiatan waahatat bihi khateeatuhu faolaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (89, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati olaika ashabu aljannati hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (90, 'Waith akhathna meethaqa banee israeela la ta\'budoona illa Allaha wabi **a** lwalidayni ihsanan wathee alqurba wa **a** lyatama wa **a** lmasakeeni waqooloo li **l** nnasi husnan waaqeemoo a **l** ssalata waatoo a **l** zzakata thumma tawallaytum illa qaleelan minkum waantum mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (91, 'Waith akhathna meethaqakum la tasfikoona dimaakum wala tukhrijoona anfusakum min diyarikum thumma aqrartum waantum tashhadoon **a**');
INSERT INTO `en_transliteration` VALUES (92, 'Thumma antum haolai taqtuloona anfusakum watukhrijoona fareeqan minkum min diyarihim ta _th_ aharoona \'alayhim bi **a** lithmi wa **a** l\'udwani wain yatookum osara tufadoohum wahuwa muharramun \'alaykum ikhrajuhum afatuminoona biba\'di alkitabi watakfuroona biba\'din fama jazao man yaf\'alu thalika minkum illa khizyun fee alhayati a **l** ddunya wayawma alqiyamati yuraddoona ila ashaddi al\'athabi wama Allahu bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (93, 'Olaika allatheena ishtarawoo alhayata a **l** ddunya bi **a** lakhirati fala yukhaffafu \'anhumu al\'athabu wala hum yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (94, 'Walaqad atayna moosa alkitaba waqaffayna min ba\'dihi bi **al** rrusuli waatayna \'eesa ibna maryama albayyinati waayyadnahu biroohi alqudusi afakullama jaakum rasoolun bima la tahwa anfusukumu istakbartum fafareeqan kaththabtum wafareeqan taqtuloon **a**');
INSERT INTO `en_transliteration` VALUES (95, 'Waqaloo quloobuna ghulfun bal la\'anahumu Allahu bikufrihim faqaleelan ma yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (96, 'Walamma jaahum kitabun min \'indi Allahi musaddiqun lima ma\'ahum wakanoo min qablu yastaftihoona \'ala allatheena kafaroo falamma jaahum ma \'arafoo kafaroo bihi fala\'natu Allahi \'ala alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (97, 'Bisama ishtaraw bihi anfusahum an yakfuroo bima anzala Allahu baghyan an yunazzila Allahu min fadlihi \'ala man yashao min \'ibadihi fabaoo bighadabin \'ala ghadabin walilkafireena \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (98, 'Waitha qeela lahum aminoo bima anzala Allahu qaloo numinu bima onzila \'alayna wayakfuroona bima waraahu wahuwa alhaqqu musaddiqan lima ma\'ahum qul falima taqtuloona anbiyaa Allahi min qablu in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (99, 'Walaqad jaakum moosa bi **a** lbayyinati thumma ittakhathtumu al\'ijla min ba\'dihi waantum _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (100, 'Waith akhathna meethaqakum warafa\'na fawqakumu a **l** ttoora khuthoo maataynakum biquwwatin wa **i** sma\'oo qaloo sami\'na wa\'asayna waoshriboo fee quloobihimu al\'ijla bikufrihim qul bisama yamurukum bihi eemanukum in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (101, 'Qul in kanat lakumu a **l** ddaru alakhiratu \'inda Allahi khalisatan min dooni a **l** nnasi fatamannawoo almawta in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (102, 'Walan yatamannawhu abadan bima qaddamat aydeehim wa **A** llahu \'aleemun bia **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (103, 'Walatajidannahum ahrasa a **l** nnasi \'alahayatin wamina allatheena ashrakoo yawaddu ahaduhum law yu\'ammaru alfa sanatin wama huwa bimuzahzihihi mina al\'athabi an yu\'ammara wa **A** llahu baseerun bima ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (104, 'Qul man kana \'aduwwan lijibreela fainnahu nazzalahu \'ala qalbika biithni Allahi musaddiqan lima bayna yadayhi wahudan wabushra lilmumineen **a**');
INSERT INTO `en_transliteration` VALUES (105, 'Man kana \'aduwwan lillahi wamalaikatihi warusulihi wajibreela wameekala fainna Allaha \'aduwwun lilkafireen **a**');
INSERT INTO `en_transliteration` VALUES (106, 'Walaqad anzana ilayka ayatin bayyinatin wama yakfuru biha illa alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (107, 'Awakullama \'ahadoo \'ahdan nabathahu fareequn minhum bal aktharuhum la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (108, 'Walamma jaahum rasoolun min \'indi Allahi musaddiqun lima ma\'ahum nabatha fareequn mina allatheena ootoo alkitaba kitaba Allahi waraa _th_ uhoorihim kaannahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (109, 'Wa **i** ttaba\'oo ma tatloo a **l** shshayateenu \'ala mulki sulaymana wama kafara sulaymanu walakinna a **l** shshayateena kafaroo yu\'allimoona a **l** nnasa a **l** ssihra wama onzila \'ala almalakayni bibabila haroota wamaroota wama yu\'allimani min ahadin hatta yaqoola innama nahnu fitnatun fala takfur fayata\'allamoona minhuma ma yufarriqoona bihi bayna almari wazawjihi wama hum bidarreena bihi min ahadin illa biithni Allahi wayata\'allamoona ma yadurruhum wala yanfa\'uhum walaqad \'alimoo lamani ishtarahu ma lahu fee alakhirati min khalaqin walabisa ma sharaw bihi anfusahum law kanoo ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (110, 'Walaw annahum amanoo wa **i** ttaqaw lamathoobatun min \'indi Allahi khayrun law kanoo ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (111, 'Ya ayyuha allatheena amanoo la taqooloo ra\'ina waqooloo on _th_ urna wa **i** sma\'oo walilkafireena \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (112, 'Ma yawaddu allatheena kafaroo min ahli alkitabi wala almushrikeena an yunazzala \'alaykum min khayrin min rabbikum wa **A** llahu yakhtassu birahmatihi man yashao wa **A** llahu thoo alfadli al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (113, 'Ma nansakh min ayatin aw nunsiha nati bikhayrin minha aw mithliha alam ta\'lam anna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (114, 'Alam ta\'lam anna Allaha lahu mulku a **l** ssamawati wa **a** lardi wama lakum min dooni Allahi min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (115, 'Am tureedoona an tasaloo rasoolakum kama suila moosa min qablu waman yatabaddali alkufra bi **a** leemani faqad dalla sawaa a **l** ssabeel **i**');
INSERT INTO `en_transliteration` VALUES (116, 'Wadda katheerun min ahli alkitabi law yaruddoonakum min ba\'di eemanikum kuffaran hasadan min \'indi anfusihim min ba\'di ma tabayyana lahumu alhaqqu fa **o** \'foo wa **i** sfahoo hatta yatiya Allahu biamrihi inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (117, 'Waaqeemoo a **l** ssalata waatoo a **l** zzakata wama tuqaddimoo lianfusikum min khayrin tajidoohu \'inda Allahi inna Allaha bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (118, 'Waqaloo lan yadkhula aljannata illa man kana hoodan aw nasara tilka amaniyyuhum qul hatoo burhanakum in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (119, 'Bala man aslama wajhahu lillahi wahuwa muhsinun falahu ajruhu \'inda rabbihi wala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (120, 'Waqalati alyahoodu laysati a **l** nnasara \'ala shayin waqalati a **l** nnasara laysati alyahoodu \'ala shayin wahum yatloona alkitaba kathalika qala allatheena la ya\'lamoona mithla qawlihim fa **A** llahu yahkumu baynahum yawma alqiyamati feema kanoo feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (121, 'Waman a _th_ lamu mimman mana\'a masajida Allahi an yuthkara feeha ismuhu wasa\'a fee kharabiha olaika ma kana lahum an yadkhulooha illa khaifeena lahum fee a **l** ddunya khizyun walahum fee alakhirati \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (122, 'Walillahi almashriqu wa **a** lmaghribu faaynama tuwalloo fathamma wajhu Allahi inna Allaha wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (123, 'Waqaloo itakhatha Allahu waladan subhanahu bal lahu ma fee a **l** ssamawati wa **a** lardi kullun lahu qanitoon **a**');
INSERT INTO `en_transliteration` VALUES (124, 'Badee\'u a **l** ssamawati wa **a** lardi waitha qada amran fainnama yaqoolu lahu kun fayakoon **u**');
INSERT INTO `en_transliteration` VALUES (125, 'Waqala allatheena la ya\'lamoona lawla yukallimuna Allahu aw tateenaayatun kathalika qala allatheena min qablihim mithla qawlihim tashabahat quloobuhum qad bayyanna alayati liqawmin yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (126, 'Inna arsanaka bi **a** lhaqqi basheeran wanatheeran wala tusalu Aaan ashabi aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (127, 'Walan tarda \'anka alyahoodu wala a **l** nnasarahatta tattabi\'a millatahum qul inna huda Allahi huwa alhuda walaini ittaba\'ta ahwaahum ba\'da allathee jaaka mina al\'ilmi ma laka mina Allahi min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (128, 'Allatheena ataynahumu alkitaba yatloonahu haqqa tilawatihi olaika yuminoona bihi waman yakfur bihi faolaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (129, 'Ya banee israeela othkuroo ni\'matiya allatee an\'amtu \'alaykum waannee faddatukum \'ala al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (130, 'Wa **i** ttaqoo yawman la tajzee nafsun \'an nafsin shayan wala yuqbalu minha \'adlun wala tanfa\'uha shafa\'atun wala hum yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (131, 'Waithi ibtala ibraheema rabbuhu bikalimatin faatammahunna qala innee ja\'iluka li **l** nnasi imaman qala wamin thurriyyatee qala la yanalu \'ahdee a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (132, 'Waith ja\'ana albayta mathabatan li **l** nnasi waamnan wa **i** ttakhithoo min maqami ibraheema musallan wa\'ahidna ila ibraheema waisma\'eela an tahhira baytiya li **l** ttaifeena wa **a** l\'akifeena wa **al** rrukka\'i a **l** ssujood **i**');
INSERT INTO `en_transliteration` VALUES (133, 'Waith qala ibraheemu rabbi ij\'al hatha baladan aminan wa **o** rzuq ahlahu mina a **l** ththamarati man amana minhum bi **A** llahi wa **a** lyawmi alakhiri qala waman kafara faomatti\'uhu qaleelan thumma adtarruhu ila \'athabi a **l** nnari wabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (134, 'Waith yarfa\'u ibraheemu alqawa\'ida mina albayti waisma\'eelu rabbana taqabbal minna innaka anta a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (135, 'Rabbana wa **i** j\'ana muslimayni laka wamin thurriyyatina ommatan muslimatan laka waarina manasikana watub \'alayna innaka anta a **l** ttawwabu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (136, 'Rabbana wa **i** b\'ath feehim rasoolan minhum yatloo \'alayhim ayatika wayu\'allimuhumu alkitaba wa **a** lhikmata wayuzakkeehim innaka anta al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (137, 'Waman yarghabu \'an millati ibraheema illa man safiha nafsahu walaqadi istafaynahu fee a **l** ddunya wainnahu fee alakhirati lamina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (138, 'Ith qala lahu rabbuhu aslim qala aslamtu lirabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (139, 'Wawassa biha ibraheemu baneehi waya\'qoobu ya baniyya inna Allaha istafa lakumu a **l** ddeena fala tamootunna illa waantum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (140, 'Am kuntum shuhadaa ithhadara ya\'qooba almawtu ith qala libaneehi ma ta\'budoona min ba\'dee qaloo na\'budu ilahaka wailaha abaika ibraheema waisma\'eela waishaqa ilahan wahidan wanahnu lahu muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (141, 'Tilka ommatun qad khalat laha ma kasabat walakum ma kasabtum wala tusaloona \'amma kano ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (142, 'Waqaloo koonoo hoodan aw nasara tahtadoo qul bal millata ibraheema haneefan wama kana mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (143, 'Qooloo amanna bi **A** llahi wama onzila ilayna wama onzila ila ibraheema waisma\'eela waishaqa waya\'qooba wa **a** lasbati wama ootiya moosa wa\'eesa wama ootiya a **l** nnabiyyoona min rabbihim la nufarriqu bayna ahadin minhum wanahnu lahu muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (144, 'Fain amanoo bimithli maamantum bihi faqadi ihtadaw wain tawallaw fainnama hum fee shiqaqin fasayakfeekahumu Allahu wahuwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (145, 'Sibghata Allahi waman ahsanu mina Allahi sibghatan wanahnu lahu \'abidoon **a**');
INSERT INTO `en_transliteration` VALUES (146, 'Qul atuhajjoonana fee Allahi wahuwa rabbuna warabbukum walana a\'maluna walakum a\'malukum wanahnu lahu mukhlisoon **a**');
INSERT INTO `en_transliteration` VALUES (147, 'Am taqooloona inna ibraheema waisma\'eela waishaqa waya\'qooba wa **a** lasbata kanoo hoodan aw nasara qul aantum a\'lamu ami Allahu waman a _th_ lamu mimman katama shahadatan \'indahu mina Allahi wama Allahu bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (148, 'Tilka ommatun qad khalat laha ma kasabat walakum ma kasabtum wala tusaloona \'amma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (149, 'Sayaqoolu a **l** ssufahao mina a **l** nnasi ma wallahum \'an qiblatihimu allatee kanoo \'alayha qul lillahi almashriqu wa **a** lmaghribu yahdee man yashao ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (150, 'Wakathalika ja\'anakum ommatan wasatan litakoonoo shuhadaa \'ala a **l** nnasi wayakoona a **l** rrasoolu \'alaykum shaheedan wama ja\'ana alqiblata allatee kunta \'alayha illa lina\'lama man yattabi\'u a **l** rrasoola mimman yanqalibu \'ala \'aqibayhi wain kanat lakabeeratan illa \'ala allatheena hada Allahu wama kana Allahu liyudee\'a eemanakum inna Allaha bi **al** nnasi laraoofun raheem **un**');
INSERT INTO `en_transliteration` VALUES (151, 'Qad nara taqalluba wajhika fee a **l** ssamai falanuwalliyannaka qiblatan tardaha fawalli wajhaka shatra almasjidi alharami wahaythu ma kuntum fawalloo wujoohakum shatrahu wainna allatheena ootoo alkitaba laya\'lamoona annahu alhaqqu min rabbihim wama Allahu bighafilin \'amma ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (152, 'Walain atayta allatheena ootoo alkitaba bikulli ayatin ma tabi\'oo qiblataka wama anta bitabi\'in qiblatahum wama ba\'duhum bitabi\'in qiblata ba\'din walaini ittaba\'ta ahwaahum min ba\'di ma jaaka mina al\'ilmi innaka ithan lamina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (153, 'Allatheena ataynahumu alkitaba ya\'rifoonahu kama ya\'rifoona abnaahum wainna fareeqan minhum layaktumoona alhaqqa wahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (154, 'Alhaqqu min rabbika fala takoonanna mina almumtareen **a**');
INSERT INTO `en_transliteration` VALUES (155, 'Walikullin wijhatun huwa muwalleeha fa **i** stabiqoo alkhayrati ayna ma takoonoo yati bikumu Allahu jamee\'an inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (156, 'Wamin haythu kharajta fawalli wajhaka shatra almasjidi alharami wainnahu lalhaqqu min rabbika wama Allahu bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (157, 'Wamin haythu kharajta fawalli wajhaka shatra almasjidi alharami wahaythu ma kuntum fawalloo wujoohakum shatrahu lialla yakoona li **l** nnasi \'alaykum hujjatun illa allatheena _th_ alamoo minhum fala takhshawhum wa **i** khshawnee waliotimma ni\'matee \'alaykum wala\'allakum tahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (158, 'Kama arsana feekum rasoolan minkum yatloo \'alaykum ayatina wayuzakkeekum wayu\'allimukumu alkitaba wa **a** lhikmata wayu\'allimukum ma lam takoonoo ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (159, 'Fa **o** thkuroonee athkurkum wa **o** shkuroo lee wala takfuroon **i**');
INSERT INTO `en_transliteration` VALUES (160, 'Ya ayyuha allatheena amanoo ista\'eenoo bi **al** ssabri wa **al** ssalati inna Allaha ma\'a a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (161, 'Wala taqooloo liman yuqtalu fee sabeeli Allahi amwatun bal ahyaon walakin la tash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (162, 'Walanabluwannakum bishayin mina alkhawfi wa **a** ljoo\'i wanaqsin mina alamwali wa **a** lanfusi wa **al** ththamarati wabashshiri a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (163, 'Allatheena itha asabathum museebatun qaloo inna lillahi wainna ilayhi raji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (164, 'Olaika \'alayhim salawatun min rabbihim warahmatun waolaika humu almuhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (165, 'Inna a **l** ssafa wa **a** lmarwata min sha\'airi Allahi faman hajja albayta awi i\'tamara fala junaha \'alayhi an yattawwafa bihima waman tatawwa\'a khayran fainna Allaha shakirun \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (166, 'Inna allatheena yaktumoona ma anzana mina albayyinati wa **a** lhuda min ba\'di ma bayyannahu li **l** nnasi fee alkitabi olaika yal\'anuhumu Allahu wayal\'anuhumu alla\'inoon **a**');
INSERT INTO `en_transliteration` VALUES (167, 'Illa allatheena taboo waaslahoo wabayyanoo faolaika atoobu \'alayhim waana a **l** ttawwabu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (168, 'Inna allatheena kafaroo wamatoo wahum kuffarun olaika \'alayhim la\'natu Allahi wa **a** lmalaikati wa **al** nnasi ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (169, 'Khalideena feeha la yukhaffafu \'anhumu al\'athabu wala hum yun _th_ aroon **a**');
INSERT INTO `en_transliteration` VALUES (170, 'Wailahukum ilahun wahidun la ilaha illa huwa a **l** rrahmanu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (171, 'Inna fee khalqi a **l** ssamawati wa **a** lardi wa **i** khtilafi allayli wa **al** nnahari wa **a** lfulki allatee tajree fee albahri bima yanfa\'u a **l** nnasa wama anzala Allahu mina a **l** ssamai min main faahya bihi alarda ba\'da mawtiha wabaththa feeha min kulli dabbatin watasreefi a **l** rriyahi wa **al** ssahabi almusakhkhari bayna a **l** ssamai wa **a** lardi laayatin liqawmin ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (172, 'Wamina a **l** nnasi man yattakhithu min dooni Allahi andadan yuhibboonahum kahubbi Allahi wa **a** llatheena amanoo ashaddu hubban lillahi walaw yara allatheena _th_ alamoo ith yarawna al\'athaba anna alquwwata lillahi jamee\'an waanna Allaha shadeedu al\'athab **i**');
INSERT INTO `en_transliteration` VALUES (173, 'Ith tabarraa allatheena ittubi\'oo mina allatheena ittaba\'oo waraawoo al\'athaba wataqatta\'at bihimu alasbab **u**');
INSERT INTO `en_transliteration` VALUES (174, 'Waqala allatheena ittaba\'oo law anna lana karratan fanatabarraa minhum kama tabarraoo minna kathalika yureehimu Allahu a\'malahum hasaratin \'alayhim wama hum bikharijeena mina a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (175, 'Ya ayyuha a **l** nnasu kuloo mimma fee alardi halalan tayyiban wala tattabi\'oo khutuwati a **l** shshaytani innahu lakum \'aduwwun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (176, 'Innama yamurukum bi **al** ssooi wa **a** lfahshai waan taqooloo \'ala Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (177, 'Waitha qeela lahumu ittabi\'oo ma anzala Allahu qaloo bal nattabi\'u ma alfayna \'alayhi abaana awalaw kana abaohum la ya\'qiloona shayan wala yahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (178, 'Wamathalu allatheena kafaroo kamathali allathee yan\'iqu bima la yasma\'u illa du\'aan wanidaan summun bukmun \'umyun fahum la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (179, 'Ya ayyuha allatheena amanoo kuloo min tayyibati ma razaqnakum wa **o** shkuroo lillahi in kuntum iyyahu ta\'budoon **a**');
INSERT INTO `en_transliteration` VALUES (180, 'Innamaharrama \'alaykumu almaytata wa **al** ddama walahma alkhinzeeri wama ohilla bihi lighayri Allahi famani idturra ghayra baghin wala \'adin fala ithma \'alayhi inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (181, 'Inna allatheena yaktumoona ma anzala Allahu mina alkitabi wayashtaroona bihi thamanan qaleelan olaika ma yakuloona fee butoonihim illa a **l** nnara wala yukallimuhumu Allahu yawma alqiyamati wala yuzakkeehim walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (182, 'Olaika allatheena ishtarawoo a **l** ddalalata bi **a** lhuda wa **a** l\'athaba bi **a** lmaghfirati fama asbarahum \'ala a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (183, 'Thalika bianna Allaha nazzala alkitaba bi **a** lhaqqi wainna allatheena ikhtalafoo fee alkitabi lafee shiqaqin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (184, 'Laysa albirra an tuwalloo wujoohakum qibala almashriqi wa **a** lmaghribi walakinna albirra man amana bi **A** llahi wa **a** lyawmi alakhiri wa **a** lmalaikati wa **a** lkitabi wa **al** nnabiyyeena waata almala \'alahubbihi thawee alqurba wa **a** lyatama wa **a** lmasakeena wa **i** bna assabeeli wa **al** ssaileena wafee a **l** rriqabi waaqama a **l** ssalata waata a **l** zzakata wa **a** lmoofoona bi\'ahdihim itha \'ahadoo wa **al** ssabireena fee albasai wa **al** ddarrai waheena albasi olaika allatheena sadaqoo waolaika humu almuttaqoon **a**');
INSERT INTO `en_transliteration` VALUES (185, 'Ya ayyuha allatheena amanoo kutiba \'alaykumu alqisasu fee alqatla alhurru bi **a** lhurri wa **a** l\'abdu bi **a** l\'abdi wa **a** lontha bi **a** lontha faman \'ufiya lahu min akheehi shayon fa **i** ttiba\'un bi **a** lma\'roofi waadaon ilayhi biihsanin thalika takhfeefun min rabbikum warahmatun famani i\'tada ba\'da thalika falahu \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (186, 'Walakum fee alqisasi hayatun ya olee alalbabi la\'allakum tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (187, 'Kutiba \'alaykum ithahadara ahadakumu almawtu in taraka khayran alwasiyyatu lilwalidayni wa **a** laqrabeena bi **a** lma\'roofi haqqan \'ala almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (188, 'Faman baddalahu ba\'dama sami\'ahu fainnama ithmuhu \'ala allatheena yubaddiloonahu inna Allaha samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (189, 'Faman khafa min moosin janafan aw ithman faaslaha baynahum fala ithma \'alayhi inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (190, 'Ya ayyuha allatheena amanoo kutiba \'alaykumu a **l** ssiyamu kama kutiba \'ala allatheena min qablikum la\'allakum tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (191, 'Ayyaman ma\'doodatin faman kana minkum mareedan aw \'ala safarin fa\'iddatun min ayyamin okhara wa\'ala allatheena yuteeqoonahu fidyatun ta\'amu miskeenin faman tatawwa\'a khayran fahuwa khayrun lahu waan tasoomoo khayrun lakum in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (192, 'Shahru ramadana allathee onzila feehi alquranu hudan li **l** nnasi wabayyinatin mina alhuda wa **a** lfurqani faman shahida minkumu a **l** shshahra falyasumhu waman kana mareedan aw \'ala safarin fa\'iddatun min ayyamin okhara yureedu Allahu bikumu alyusra wala yureedu bikumu al\'usra walitukmiloo al\'iddata walitukabbiroo Allaha \'ala ma hadakum wala\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (193, 'Waitha saalaka \'ibadee \'annee fainnee qareebun ojeebu da\'wata a **l** dda\'i itha da\'ani falyastajeeboo lee walyuminoo bee la\'allahum yarshudoon **a**');
INSERT INTO `en_transliteration` VALUES (194, 'Ohilla lakum laylata a **l** ssiyami a **l** rrafathu ila nisaikum hunna libasun lakum waantum libasun lahunna \'alima Allahu annakum kuntum takhtanoona anfusakum fataba \'alaykum wa\'afa \'ankum fa **a** lana bashiroohunna wa **i** btaghoo ma kataba Allahu lakum wakuloo wa **i** shraboo hatta yatabayyana lakumu alkhaytu alabyadu mina alkhayti alaswadi mina alfajri thumma atimmoo a **l** ssiyama ila allayli wala tubashiroohunna waantum \'akifoona fee almasajidi tilka hudoodu Allahi fala taqrabooha kathalika yubayyinu Allahu ayatihi li **l** nnasi la\'allahum yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (195, 'Wala takuloo amwalakum baynakum bi **a** lbatili watudloo biha ila alhukkami litakuloo fareeqan min amwali a **l** nnasi bi **a** lithmi waantum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (196, 'Yasaloonaka \'ani alahillati qul hiya mawaqeetu li **l** nnasi wa **a** lhajji walaysa albirru bian tatoo albuyoota min _th_ uhooriha walakinna albirra mani ittaqa watoo albuyoota min abwabiha wa **i** ttaqoo Allaha la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (197, 'Waqatiloo fee sabeeli Allahi allatheena yuqatiloonakum wala ta\'tadoo inna Allaha la yuhibbu almu\'tadeen **a**');
INSERT INTO `en_transliteration` VALUES (198, 'Wa **o** qtuloohum haythu thaqiftumoohum waakhrijoohum min haythu akhrajookum wa **a** lfitnatu ashaddu mina alqatli wala tuqatiloohum \'inda almasjidi alharami hatta yuqatilookum feehi fain qatalookum fa **o** qtuloohum kathalika jazao alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (199, 'Faini intahaw fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (200, 'Waqatiloohum hatta la takoona fitnatun wayakoona a **l** ddeenu lillahi faini intahaw fala \'udwana illa \'ala a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (201, 'A **l** shshahru alharamu bi **al** shshahri alharami wa **a** lhurumatu qisasun famani i\'tada \'alaykum fa **i** \'tadoo \'alayhi bimithli ma i\'tada \'alaykum wa **i** ttaqoo Allaha wa **i** \'lamoo anna Allaha ma\'a almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (202, 'Waanfiqoo fee sabeeli Allahi wala tulqoo biaydeekum ila a **l** ttahlukati waahsinoo inna Allaha yuhibbu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (203, 'Waatimmoo alhajja wa **a** l\'umrata lillahi fain ohsirtum fama istaysara mina alhadyi wala tahliqoo ruoosakum hatta yablugha alhadyu mahillahu faman kana minkum mareedan aw bihi athan min rasihi fafidyatun min siyamin aw sadaqatin aw nusukin faitha amintum faman tamatta\'a bi **a** l\'umrati ila alhajji fama istaysara mina alhadyi faman lam yajid fasiyamu thalathati ayyamin fee alhajji wasab\'atin itha raja\'tum tilka \'asharatun kamilatun thalika liman lam yakun ahluhu hadiree almasjidi alharami wa **i** ttaqoo Allaha wa **i** \'lamoo anna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (204, 'Alhajju ashhurun ma\'loomatun faman farada feehinna alhajja fala rafatha wala fusooqa wala jidala fee alhajji wama taf\'aloo min khayrin ya\'lamhu Allahu watazawwadoo fainna khayra a **l** zzadi a **l** ttaqwa wa **i** ttaqooni ya olee alalbab **i**');
INSERT INTO `en_transliteration` VALUES (205, 'Laysa \'alaykum junahun an tabtaghoo fadlan min rabbikum faitha afadtum min \'arafatin fa **o** thkuroo Allaha \'inda almash\'ari alharami wa **o** thkuroohu kama hadakum wain kuntum min qablihi lamina a **l** ddalleen **a**');
INSERT INTO `en_transliteration` VALUES (206, 'Thumma afeedoo min haythu afada a **l** nnasu wa **i** staghfiroo Allaha inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (207, 'Faitha qadaytum manasikakum fa **o** thkuroo Allaha kathikrikum abaakum aw ashadda thikran famina a **l** nnasi man yaqoolu rabbanaatina fee a **l** ddunya wama lahu fee alakhirati min khalaq **in**');
INSERT INTO `en_transliteration` VALUES (208, 'Waminhum man yaqoolu rabbanaatina fee a **l** ddunyahasanatan wafee alakhirati hasanatan waqina \'athaba a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (209, 'Olaika lahum naseebun mimma kasaboo wa **A** llahu saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (210, 'Wa **o** thkuroo Allaha fee ayyamin ma\'doodatin faman ta\'ajjala fee yawmayni fala ithma \'alayhi waman taakhkhara fala ithma \'alayhi limani ittaqa wa **i** ttaqoo Allaha wa **i** \'lamoo annakum ilayhi tuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (211, 'Wamina a **l** nnasi man yu\'jibuka qawluhu fee alhayati a **l** ddunya wayushhidu Allaha \'ala ma fee qalbihi wahuwa aladdu alkhisam **i**');
INSERT INTO `en_transliteration` VALUES (212, 'Waitha tawalla sa\'a fee alardi liyufsida feeha wayuhlika alhartha wa **al** nnasla wa **A** llahu la yuhibbu alfasad **a**');
INSERT INTO `en_transliteration` VALUES (213, 'Waitha qeela lahu ittaqi Allaha akhathathu al\'izzatu bi **a** lithmi fahasbuhu jahannamu walabisa almihad **u**');
INSERT INTO `en_transliteration` VALUES (214, 'Wamina a **l** nnasi man yashree nafsahu ibtighaa mardati Allahi wa **A** llahu raoofun bi **a** l\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (215, 'Ya ayyuha allatheena amanoo odkhuloo fee a **l** ssilmi kaffatan wala tattabi\'oo khutuwati a **l** shshaytani innahu lakum \'aduwwun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (216, 'Fain zalatum min ba\'di ma jaatkumu albayyinatu fa **i** \'lamoo anna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (217, 'Hal yan _th_ uroona illa an yatiyahumu Allahu fee _th_ ulalin mina alghamami wa **a** lmalaikatu waqudiya alamru waila Allahi turja\'u alomoor **u**');
INSERT INTO `en_transliteration` VALUES (218, 'Sal banee israeela kam ataynahum min ayatin bayyinatin waman yubaddil ni\'mata Allahi min ba\'di ma jaathu fainna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (219, 'Zuyyina lillatheena kafaroo alhayatu a **l** ddunya wayaskharoona mina allatheena amanoo wa **a** llatheena ittaqaw fawqahum yawma alqiyamati wa **A** llahu yarzuqu man yashao bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (220, 'Kana a **l** nnasu ommatan wahidatan faba\'atha Allahu a **l** nnabiyyeena mubashshireena wamunthireena waanzala ma\'ahumu alkitaba bi **a** lhaqqi liyahkuma bayna a **l** nnasi feema ikhtalafoo feehi wama ikhtalafa feehi illa allatheena ootoohu min ba\'di ma jaathumu albayyinatu baghyan baynahum fahada Allahu allatheena amanoo lima ikhtalafoo feehi mina alhaqqi biithnihi wa **A** llahu yahdee man yashao ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (221, 'Am hasibtum an tadkhuloo aljannata walamma yatikum mathalu allatheena khalaw min qablikum massathumu albasao wa **al** ddarrao wazulziloo hatta yaqoola a **l** rrasoolu wa **a** llatheena amanoo ma\'ahu mata nasru Allahi ala inna nasra Allahi qareeb **un**');
INSERT INTO `en_transliteration` VALUES (222, 'Yasaloonaka matha yunfiqoona qul ma anfaqtum min khayrin falilwalidayni wa **a** laqrabeena wa **a** lyatama wa **a** lmasakeeni wa **i** bni a **l** ssabeeli wama taf\'aloo min khayrin fainna Allaha bihi \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (223, 'Kutiba \'alaykumu alqitalu wahuwa kurhun lakum wa\'asa an takrahoo shayan wahuwa khayrun lakum wa\'asa an tuhibboo shayan wahuwa sharrun lakum wa **A** llahu ya\'lamu waantum la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (224, 'Yasaloonaka \'ani a **l** shshahri alharami qitalin feehi qul qitalun feehi kabeerun wasaddun \'an sabeeli Allahi wakufrun bihi wa **a** lmasjidi alharami waikhraju ahlihi minhu akbaru \'inda Allahi wa **a** lfitnatu akbaru mina alqatli wala yazaloona yuqatiloonakum hatta yaruddookum \'an deenikum ini istata\'oo waman yartadid minkum \'an deenihi fayamut wahuwa kafirun faolaika habitat a\'maluhum fee a **l** ddunya wa **a** lakhirati waolaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (225, 'Inna allatheena amanoo wa **a** llatheena hajaroo wajahadoo fee sabeeli Allahi olaika yarjoona rahmata Allahi wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (226, 'Yasaloonaka \'ani alkhamri wa **a** lmaysiri qul feehima ithmun kabeerun wamanafi\'u li **l** nnasi waithmuhuma akbaru min naf\'ihima wayasaloonaka matha yunfiqoona quli al\'afwa kathalika yubayyinu Allahu lakumu alayati la\'allakum tatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (227, 'Fee a **l** ddunya wa **a** lakhirati wayasaloonaka \'ani alyatama qul islahun lahum khayrun wain tukhalitoohum faikhwanukum wa **A** llahu ya\'lamu almufsida mina almuslihi walaw shaa Allahu laa\'natakum inna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (228, 'Wala tankihoo almushrikati hatta yuminna walaamatun muminatun khayrun min mushrikatin walaw a\'jabatkum wala tunkihoo almushrikeena hatta yuminoo wala\'abdun muminun khayrun min mushrikin walaw a\'jabakum olaika yad\'oona ila a **l** nnari wa **A** llahu yad\'oo ila aljannati wa **a** lmaghfirati biithnihi wayubayyinu ayatihi li **l** nnasi la\'allahum yatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (229, 'Wayasaloonaka \'ani almaheedi qul huwa athan fa **i** \'taziloo a **l** nnisaa fee almaheedi wala taqraboohunna hatta yathurna faitha tatahharna fatoohunna min haythu amarakumu Allahu inna Allaha yuhibbu a **l** ttawwabeena wayuhibbu almutatahhireen **a**');
INSERT INTO `en_transliteration` VALUES (230, 'Nisaokum harthun lakum fatoo harthakum anna shitum waqaddimoo lianfusikum wa **i** ttaqoo Allaha wa **i** \'lamoo annakum mulaqoohu wabashshiri almumineen **a**');
INSERT INTO `en_transliteration` VALUES (231, 'Wala taj\'aloo Allaha \'urdatan liaymanikum an tabarroo watattaqoo watuslihoo bayna a **l** nnasi wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (232, 'La yuakhithukumu Allahu bi **a** llaghwi fee aymanikum walakin yuakhithukum bima kasabat quloobukum wa **A** llahu ghafoorun haleem **un**');
INSERT INTO `en_transliteration` VALUES (233, 'Lillatheena yuloona min nisaihim tarabbusu arba\'ati ashhurin fain faoo fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (234, 'Wain \'azamoo a **l** ttalaqa fainna Allaha samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (235, 'Wa **a** lmutallaqatu yatarabbasna bianfusihinna thalathata qurooin wala yahillu lahunna an yaktumna ma khalaqa Allahu fee arhamihinna in kunna yuminna bi **A** llahi wa **a** lyawmi alakhiri wabu\'oolatuhunna ahaqqu biraddihinna fee thalika in aradoo islahan walahunna mithlu allathee \'alayhinna bi **a** lma\'roofi wali **l** rrijali \'alayhinna darajatun wa **A** llahu \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (236, 'A **l** ttalaqu marratani faimsakun bima\'roofin aw tasreehun biihsanin wala yahillu lakum an takhuthoo mimmaataytumoohunna shayan illa an yakhafa alla yuqeemahudooda Allahi fain khiftum alla yuqeemahudooda Allahi fala junaha \'alayhima feema iftadat bihi tilka hudoodu Allahi fala ta\'tadooha waman yata\'adda hudooda Allahi faolaika humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (237, 'Fain tallaqaha fala tahillu lahu min ba\'du hatta tankiha zawjan ghayrahu fain tallaqaha fala junaha \'alayhima an yataraja\'a in _th_ anna an yuqeemahudooda Allahi watilka hudoodu Allahi yubayyinuha liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (238, 'Waithatallaqtumu a **l** nnisaa fabalaghna ajalahunna faamsikoohunna bima\'roofin aw sarrihoohunna bima\'roofin wala tumsikoohunna diraran lita\'tadoo waman yaf\'al thalika faqad _th_ alama nafsahu wala tattakhithoo ayati Allahi huzuwan wa **o** thkuroo ni\'mata Allahi \'alaykum wama anzala \'alaykum mina alkitabi wa **a** lhikmati ya\'i _th_ ukum bihi wa **i** ttaqoo Allaha wa **i** \'lamoo anna Allaha bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (239, 'Waithatallaqtumu a **l** nnisaa fabalaghna ajalahunna fala ta\'duloohunna an yankihna azwajahunna itha taradaw baynahum bi **a** lma\'roofi thalika yoo\'a _th_ u bihi man kana minkum yuminu bi **A** llahi wa **a** lyawmi alakhiri thalikum azka lakum waatharu wa **A** llahu ya\'lamu waantum la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (240, 'Wa **a** lwalidatu yurdi\'na awladahunna hawlayni kamilayni liman arada an yutimma a **l** rrada\'ata wa\'ala almawloodi lahu rizquhunna wakiswatuhunna bi **a** lma\'roofi la tukallafu nafsun illa wus\'aha la tudarra walidatun biwaladiha wala mawloodun lahu biwaladihi wa\'ala alwarithi mithlu thalika fain arada fisalan \'an taradin minhuma watashawurin fala junaha \'alayhima wain aradtum an tastardi\'oo awladakum fala junaha \'alaykum itha sallamtum maataytum bi **a** lma\'roofi wa **i** ttaqoo Allaha wa **i** \'lamoo anna Allaha bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (241, 'Wa **a** llatheena yutawaffawna minkum wayatharoona azwajan yatarabbasna bianfusihinna arba\'ata ashhurin wa\'ashran faitha balaghna ajalahunna fala junaha \'alaykum feema fa\'ana fee anfusihinna bi **a** lma\'roofi wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (242, 'Wala junaha \'alaykum feema \'arradtum bihi min khitbati a **l** nnisai aw aknantum fee anfusikum \'alima Allahu annakum satathkuroonahunna walakin la tuwa\'idoohunna sirran illa an taqooloo qawlan ma\'roofan wala ta\'zimoo \'uqdata a **l** nnikahi hatta yablugha alkitabu ajalahu wa **i** \'lamoo anna Allaha ya\'lamu ma fee anfusikum fa **i** htharoohu wa **i** \'lamoo anna Allaha ghafoorun haleem **un**');
INSERT INTO `en_transliteration` VALUES (243, 'La junaha \'alaykum in tallaqtumu a **l** nnisaa ma lam tamassoohunna aw tafridoo lahunna fareedatan wamatti\'oohunna \'ala almoosi\'i qadaruhu wa\'ala almuqtiri qadaruhu mata\'an bi **a** lma\'roofi haqqan \'ala almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (244, 'Wain tallaqtumoohunna min qabli an tamassoohunna waqad faradtum lahunna fareedatan fanisfu ma faradtum illa an ya\'foona aw ya\'fuwa allathee biyadihi \'uqdatu a **l** nnikahi waan ta\'foo aqrabu li **l** ttaqwa wala tansawoo alfadla baynakum inna Allaha bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (245, 'Hafi _th_ oo \'ala a **l** ssalawati wa **al** ssalati alwusta waqoomoo lillahi qaniteen **a**');
INSERT INTO `en_transliteration` VALUES (246, 'Fain khiftum farijalan aw rukbanan faitha amintum fa **o** thkuroo Allaha kama \'allamakum ma lam takoonoo ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (247, 'Wa **a** llatheena yutawaffawna minkum wayatharoona azwajan wasiyyatan liazwajihim mata\'an ila alhawli ghayra ikhrajin fain kharajna fala junaha \'alaykum fee ma fa\'ana fee anfusihinna min ma\'roofin wa **A** llahu \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (248, 'Walilmutallaqati mata\'un bi **a** lma\'roofi haqqan \'ala almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (249, 'Kathalika yubayyinu Allahu lakum ayatihi la\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (250, 'Alam tara ila allatheena kharajoo min diyarihim wahum oloofun hathara almawti faqala lahumu Allahu mootoo thumma ahyahum inna Allaha lathoo fadlin \'ala a **l** nnasi walakinna akthara a **l** nnasi la yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (251, 'Waqatiloo fee sabeeli Allahi wa **i** \'lamoo anna Allaha samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (252, 'Man tha allathee yuqridu Allaha qardan hasanan fayuda\'ifahu lahu ad\'afan katheeratan wa **A** llahu yaqbidu wayabsutu wailayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (253, 'Alam tara ila almalai min banee israeela min ba\'di moosa ith qaloo linabiyyin lahumu ib\'ath lana malikan nuqatil fee sabeeli Allahi qala hal \'asaytum in kutiba \'alaykumu alqitalu alla tuqatiloo qaloo wama lana alla nuqatila fee sabeeli Allahi waqad okhrijna min diyarina waabnaina falamma kutiba \'alayhimu alqitalu tawallaw illa qaleelan minhum wa **A** llahu \'aleemun bi **al** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (254, 'Waqala lahum nabiyyuhum inna Allaha qad ba\'atha lakum taloota malikan qaloo anna yakoonu lahu almulku \'alayna wanahnu ahaqqu bi **a** lmulki minhu walam yuta sa\'atan mina almali qala inna Allaha istafahu \'alaykum wazadahu bastatan fee al\'ilmi wa **a** ljismi wa **A** llahu yutee mulkahu man yashao wa **A** llahu wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (255, 'Waqala lahum nabiyyuhum inna ayata mulkihi an yatiyakumu a **l** ttabootu feehi sakeenatun min rabbikum wabaqiyyatun mimma taraka alu moosa waalu haroona tahmiluhu almalaikatu inna fee thalika laayatan lakum in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (256, 'Falamma fasala talootu bi **a** ljunoodi qala inna Allaha mubtaleekum binaharin faman shariba minhu falaysa minnee waman lam yat\'amhu fainnahu minnee illa mani ightarafa ghurfatan biyadihi fashariboo minhu illa qaleelan minhum falamma jawazahu huwa wa **a** llatheena amanoo ma\'ahu qaloo lataqata lana alyawma bijaloota wajunoodihi qala allatheena ya _th_ unnoona annahum mulaqoo Allahi kam min fiatin qaleelatin ghalabat fiatan katheeratan biithni Allahi wa **A** llahu ma\'a a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (257, 'Walamma barazoo lijaloota wajunoodihi qaloo rabbana afrigh \'alaynasabran wathabbit aqdamana wa **o** nsurna \'ala alqawmi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (258, 'Fahazamoohum biithni Allahi waqatala dawoodu jaloota waatahu Allahu almulka wa **a** lhikmata wa\'allamahu mimma yashao walawla daf\'u Allahi a **l** nnasa ba\'dahum biba\'din lafasadati alardu walakinna Allaha thoo fadlin \'ala al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (259, 'Tilka ayatu Allahi natlooha \'alayka bi **a** lhaqqi wainnaka lamina almursaleen **a**');
INSERT INTO `en_transliteration` VALUES (260, 'Tilka a **l** rrusulu faddana ba\'dahum \'ala ba\'din minhum man kallama Allahu warafa\'a ba\'dahum darajatin waatayna \'eesa ibna maryama albayyinati waayyadnahu biroohi alqudusi walaw shaa Allahu ma iqtatala allatheena min ba\'dihim min ba\'di ma jaathumu albayyinatu walakini ikhtalafoo faminhum man amana waminhum man kafara walaw shaa Allahu ma iqtataloo walakinna Allaha yaf\'alu ma yureed **u**');
INSERT INTO `en_transliteration` VALUES (261, 'Ya ayyuha allatheena amanoo anfiqoo mimma razaqnakum min qabli an yatiya yawmun la bay\'un feehi wala khullatun wala shafa\'atun wa **a** lkafiroona humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (262, 'Allahu la ilaha illa huwa alhayyu alqayyoomu la takhuthuhu sinatun wala nawmun lahu ma fee a **l** ssamawati wama fee alardi man tha allathee yashfa\'u \'indahu illa biithnihi ya\'lamu ma bayna aydeehim wama khalfahum wala yuheetoona bishayin min \'ilmihi illa bima shaa wasi\'a kursiyyuhu a **l** ssamawati wa **a** larda wala yaooduhu hif _th_ uhuma wahuwa al\'aliyyu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (263, 'La ikraha fee a **l** ddeeni qad tabayyana a **l** rrushdu mina alghayyi faman yakfur bi **al** ttaghooti wayumin bi **A** llahi faqadi istamsaka bi **a** l\'urwati alwuthqa la infisama laha wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (264, 'Allahu waliyyu allatheena amanoo yukhrijuhum mina a **l** _thth_ ulumati ila a **l** nnoori wa **a** llatheena kafaroo awliyaohumu a **l** ttaghootu yukhrijoonahum mina a **l** nnoori ila a **l** _thth_ ulumati olaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (265, 'Alam tara ila allathee hajja ibraheema fee rabbihi an atahu Allahu almulka ith qala ibraheemu rabbiya allathee yuhyee wayumeetu qala ana ohyee waomeetu qala ibraheemu fainna Allaha yatee bi **al** shshamsi mina almashriqi fati biha mina almaghribi fabuhita allathee kafara wa **A** llahu la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (266, 'Aw ka **a** llathee marra \'ala qaryatin wahiya khawiyatun \'ala \'urooshiha qala anna yuhyee hathihi Allahu ba\'da mawtiha faamatahu Allahu miata \'amin thumma ba\'athahu qala kam labithta qala labithtu yawman aw ba\'da yawmin qala bal labithta miata \'amin fa **o** n _th_ ur ilata\'amika washarabika lam yatasannah wa **o** n _th_ ur ilahimarika walinaj\'alaka ayatan li **l** nnasi wa **o** n _th_ ur ila al\'i _th_ ami kayfa nunshizuha thumma naksooha lahman falamma tabayyana lahu qala a\'lamu anna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (267, 'Waith qala ibraheemu rabbi arinee kayfa tuhyee almawta qala awalam tumin qala bala walakin liyatmainna qalbee qala fakhuth arba\'atan mina a **l** ttayri fasurhunna ilayka thumma ij\'al \'ala kulli jabalin minhunna juzan thumma od\'uhunna yateenaka sa\'yan wa **i** \'lam anna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (268, 'Mathalu allatheena yunfiqoona amwalahum fee sabeeli Allahi kamathali habbatin anbatat sab\'a sanabila fee kulli sunbulatin miatu habbatin wa **A** llahu yuda\'ifu liman yashao wa **A** llahu wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (269, 'Allatheena yunfiqoona amwalahum fee sabeeli Allahi thumma la yutbi\'oona ma anfaqoo mannan wala athan lahum ajruhum \'inda rabbihim wala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (270, 'Qawlun ma\'roofun wamaghfiratun khayrun min sadaqatin yatba\'uha athan wa **A** llahu ghaniyyun haleem **un**');
INSERT INTO `en_transliteration` VALUES (271, 'Ya ayyuha allatheena amanoo la tubtiloo sadaqatikum bi **a** lmanni wa **a** latha ka **a** llathee yunfiqu malahu riaa a **l** nnasi wala yuminu bi **A** llahi wa **a** lyawmi alakhiri famathaluhu kamathali safwanin \'alayhi turabun faasabahu wabilun fatarakahu sadan la yaqdiroona \'ala shayin mimma kasaboo wa **A** llahu la yahdee alqawma alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (272, 'Wamathalu allatheena yunfiqoona amwalahumu ibtighaa mardati Allahi watathbeetan min anfusihim kamathali jannatin birabwatin asabaha wabilun faatat okulahadi\'fayni fain lam yusibha wabilun fatallun wa **A** llahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (273, 'Ayawaddu ahadukum an takoona lahu jannatun min nakheelin waa\'nabin tajree min tahtiha alanharu lahu feeha min kulli a **l** ththamarati waasabahu alkibaru walahu thurriyyatun du\'afao faasabaha i\'sarun feehi narun fa **i** htaraqat kathalika yubayyinu Allahu lakumu alayati la\'allakum tatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (274, 'Ya ayyuha allatheena amanoo anfiqoo min tayyibati ma kasabtum wamimma akhrajna lakum mina alardi wala tayammamoo alkhabeetha minhu tunfiqoona walastum biakhitheehi illa an tughmidoo feehi wa **i** \'lamoo anna Allaha ghaniyyun hameed **un**');
INSERT INTO `en_transliteration` VALUES (275, 'A **l** shshaytanu ya\'idukumu alfaqra wayamurukum bi **a** lfahshai wa **A** llahu ya\'idukum maghfiratan minhu wafadlan wa **A** llahu wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (276, 'Yutee alhikmata man yashao waman yuta alhikmata faqad ootiya khayran katheeran wama yaththakkaru illa oloo alalbab **i**');
INSERT INTO `en_transliteration` VALUES (277, 'Wama anfaqtum min nafaqatin aw nathartum min nathrin fainna Allaha ya\'lamuhu wama li **l** _thth_ alimeena min ansar **in**');
INSERT INTO `en_transliteration` VALUES (278, 'In tubdoo a **l** ssadaqati fani\'imma hiya wain tukhfooha watutooha alfuqaraa fahuwa khayrun lakum wayukaffiru \'ankum min sayyiatikum wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (279, 'Laysa \'alayka hudahum walakinna Allaha yahdee man yashao wama tunfiqoo min khayrin falianfusikum wama tunfiqoona illa ibtighaa wajhi Allahi wama tunfiqoo min khayrin yuwaffa ilaykum waantum la tu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (280, 'Lilfuqarai allatheena ohsiroo fee sabeeli Allahi la yastatee\'oona darban fee alardi yahsabuhumu aljahilu aghniyaa mina a **l** tta\'affufi ta\'rifuhum biseemahum la yasaloona a **l** nnasa ilhafan wama tunfiqoo min khayrin fainna Allaha bihi \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (281, 'Allatheena yunfiqoona amwalahum bi **a** llayli wa **al** nnahari sirran wa\'alaniyatan falahum ajruhum \'inda rabbihim wala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (282, 'Allatheena yakuloona a **l** rriba la yaqoomoona illa kama yaqoomu allathee yatakhabbatuhu a **l** shshaytanu mina almassi thalika biannahum qaloo innama albay\'u mithlu a **l** rriba waahalla Allahu albay\'a waharrama a **l** rriba faman jaahu maw\'i _th_ atun min rabbihi fa **i** ntaha falahu ma salafa waamruhu ila Allahi waman \'ada faolaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (283, 'Yamhaqu Allahu a **l** rriba wayurbee a **l** ssadaqati wa **A** llahu la yuhibbu kulla kaffarin atheem **in**');
INSERT INTO `en_transliteration` VALUES (284, 'Inna allatheena amanoo wa\'amiloo a **l** ssalihati waaqamoo a **l** ssalata waatawoo a **l** zzakata lahum ajruhum \'inda rabbihim wala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (285, 'Ya ayyuha allatheena amanoo ittaqoo Allaha watharoo ma baqiya mina a **l** rriba in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (286, 'Fain lam taf\'aloo fathanoo biharbin mina Allahi warasoolihi wain tubtum falakum ruoosu amwalikum la ta _th_ limoona wala tu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (287, 'Wain kana thoo \'usratin fana _th_ iratun ila maysaratin waan tasaddaqoo khayrun lakum in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (288, 'Wa **i** ttaqoo yawman turja\'oona feehi ila Allahi thumma tuwaffa kullu nafsin ma kasabat wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (289, 'Ya ayyuha allatheena amanoo itha tadayantum bidaynin ila ajalin musamman fa **o** ktuboohu walyaktub baynakum katibun bi **a** l\'adli wala yaba katibun an yaktuba kama \'allamahu Allahu falyaktub walyumlili allathee \'alayhi alhaqqu walyattaqi Allaha rabbahu wala yabkhas minhu shayan fain kana allathee \'alayhi alhaqqu safeehan aw da\'eefan aw la yastatee\'u an yumilla huwa falyumlil waliyyuhu bi **a** l\'adli wa **i** stashhidoo shaheedayni min rijalikum fain lam yakoona rajulayni farajulun wa **i** mraatani mimman tardawna mina a **l** shshuhadai an tadilla ihdahuma fatuthakkira ihdahuma alokhra wala yaba a **l** shshuhadao itha ma du\'oo wala tasamoo an taktuboohu sagheeran aw kabeeran ila ajalihi thalikum aqsatu \'inda Allahi waaqwamu li **l** shshahadati waadna alla tartaboo illa an takoona tijaratan hadiratan tudeeroonaha baynakum falaysa \'alaykum junahun alla taktubooha waashhidoo itha tabaya\'tum wala yudarra katibun wala shaheedun wain taf\'aloo fainnahu fusooqun bikum wa **i** ttaqoo Allaha wayu\'allimukumu Allahu wa **A** llahu bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (290, 'Wain kuntum \'ala safarin walam tajidoo katiban farihanun maqboodatun fain amina ba\'dukum ba\'dan falyuaddi allatheeitumina amanatahu walyattaqi Allaha rabbahu wala taktumoo a **l** shshahadata waman yaktumha fainnahu athimun qalbuhu wa **A** llahu bima ta\'maloona \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (291, 'Lillahi ma fee a **l** ssamawati wama fee alardi wain tubdoo ma fee anfusikum aw tukhfoohu yuhasibkum bihi Allahu fayaghfiru liman yashao wayu\'aththibu man yashao wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (292, 'Amana a **l** rrasoolu bima onzila ilayhi min rabbihi wa **a** lmuminoona kullun amana bi **A** llahi wamalaikatihi wakutubihi warusulihi la nufarriqu bayna ahadin min rusulihi waqaloo sami\'na waata\'na ghufranaka rabbana wailayka almaseer **u**');
INSERT INTO `en_transliteration` VALUES (293, 'La yukallifu Allahu nafsan illa wus\'aha laha ma kasabat wa\'alayha ma iktasabat rabbana la tuakhithna in naseena aw akhtana rabbana wala tahmil \'alayna isran kamahamatahu \'ala allatheena min qablina rabbana wala tuhammilna ma lataqata lana bihi wa **o** \'fu \'anna wa **i** ghfir lana wa **i** rhamna anta mawlana fa **o** nsurna \'ala alqawmi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (294, 'Aliflammeem');
INSERT INTO `en_transliteration` VALUES (295, 'Allahu la ilaha illa huwa alhayyu alqayyoom **u**');
INSERT INTO `en_transliteration` VALUES (296, 'Nazzala \'alayka alkitaba bi **a** lhaqqi musaddiqan lima bayna yadayhi waanzala a **l** ttawrata wa **a** linjeel **a**');
INSERT INTO `en_transliteration` VALUES (297, 'Min qablu hudan li **l** nnasi waanzala alfurqana inna allatheena kafaroo biayati Allahi lahum \'athabun shadeedun wa **A** llahu \'azeezun thoo intiqam **in**');
INSERT INTO `en_transliteration` VALUES (298, 'Inna Allaha la yakhfa \'alayhi shayon fee alardi wala fee a **l** ssama **i**');
INSERT INTO `en_transliteration` VALUES (299, 'Huwa allathee yusawwirukum fee alarhami kayfa yashao la ilaha illa huwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (300, 'Huwa allathee anzala \'alayka alkitaba minhu ayatun muhkamatun hunna ommu alkitabi waokharu mutashabihatun faamma allatheena fee quloobihim zayghun fayattabi\'oona ma tashabaha minhu ibtighaa alfitnati wa **i** btighaa taweelihi wama ya\'lamu taweelahu illa Allahu wa **al** rrasikhoona fee al\'ilmi yaqooloona amanna bihi kullun min \'indi rabbina wama yaththakkaru illa oloo alalbab **i**');
INSERT INTO `en_transliteration` VALUES (301, 'Rabbana la tuzigh quloobana ba\'da ith hadaytana wahab lana min ladunka rahmatan innaka anta alwahhab **u**');
INSERT INTO `en_transliteration` VALUES (302, 'Rabbana innaka jami\'u a **l** nnasi liyawmin la rayba feehi inna Allaha la yukhlifu almee\'ad **a**');
INSERT INTO `en_transliteration` VALUES (303, 'Inna allatheena kafaroo lan tughniya \'anhum amwaluhum wala awladuhum mina Allahi shayan waolaika hum waqoodu a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (304, 'Kadabi ali fir\'awna wa **a** llatheena min qablihim kaththaboo biayatina faakhathahumu Allahu bithunoobihim wa **A** llahu shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (305, 'Qul lillatheena kafaroo satughlaboona watuhsharoona ila jahannama wabisa almihad **u**');
INSERT INTO `en_transliteration` VALUES (306, 'Qad kana lakum ayatun fee fiatayni iltaqata fiatun tuqatilu fee sabeeli Allahi waokhra kafiratun yarawnahum mithlayhim raya al\'ayni wa **A** llahu yuayyidu binasrihi man yashao inna fee thalika la\'ibratan liolee alabsar **i**');
INSERT INTO `en_transliteration` VALUES (307, 'Zuyyina li **l** nnasi hubbu a **l** shshahawati mina a **l** nnisai wa **a** lbaneena wa **a** lqanateeri almuqantarati mina a **l** ththahabi wa **a** lfiddati wa **a** lkhayli almusawwamati wa **a** lan\'ami wa **a** lharthi thalika mata\'u alhayati a **l** ddunya wa **A** llahu \'indahu husnu almaab **i**');
INSERT INTO `en_transliteration` VALUES (308, 'Qul aonabbiokum bikhayrin min thalikum lillatheena ittaqaw \'inda rabbihim jannatun tajree min tahtiha alanharu khalideena feeha waazwajun mutahharatun waridwanun mina Allahi wa **A** llahu baseerun bi **a** l\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (309, 'Allatheena yaqooloona rabbana innanaamanna fa **i** ghfir lanathunoobana waqina \'athaba a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (310, 'A **l** ssabireena wa **al** ssadiqeena wa **a** lqaniteena wa **a** lmunfiqeena wa **a** lmustaghfireena bi **a** lashar **i**');
INSERT INTO `en_transliteration` VALUES (311, 'Shahida Allahu annahu la ilaha illa huwa wa **a** lmalaikatu waoloo al\'ilmi qaiman bi **a** lqisti la ilaha illa huwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (312, 'Inna a **l** ddeena \'inda Allahi alislamu wama ikhtalafa allatheena ootoo alkitaba illa min ba\'di ma jaahumu al\'ilmu baghyan baynahum waman yakfur biayati Allahi fainna Allaha saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (313, 'Fain hajjooka faqul aslamtu wajhiya lillahi wamani ittaba\'ani waqul lillatheena ootoo alkitaba wa **a** lommiyyeena aaslamtum fain aslamoo faqadi ihtadaw wain tawallaw fainnama \'alayka albalaghu wa **A** llahu baseerun bi **a** l\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (314, 'Inna allatheena yakfuroona biayati Allahi wayaqtuloona a **l** nnabiyyeena bighayri haqqin wayaqtuloona allatheena yamuroona bi **a** lqisti mina a **l** nnasi fabashshirhum bi\'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (315, 'Olaika allatheena habitat a\'maluhum fee a **l** ddunya wa **a** lakhirati wama lahum min nasireen **a**');
INSERT INTO `en_transliteration` VALUES (316, 'Alam tara ila allatheena ootoo naseeban mina alkitabi yud\'awna ila kitabi Allahi liyahkuma baynahum thumma yatawalla fareequn minhum wahum mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (317, 'Thalika biannahum qaloo lan tamassana a **l** nnaru illa ayyaman ma\'doodatin wagharrahum fee deenihim ma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (318, 'Fakayfa itha jama\'nahum liyawmin la rayba feehi wawuffiyat kullu nafsin ma kasabat wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (319, 'Quli allahumma malika almulki tutee almulka man tashao watanzi\'u almulka mimman tashao watu\'izzu man tashao watuthillu man tashao biyadika alkhayru innaka \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (320, 'Tooliju allayla fee a **l** nnahari watooliju a **l** nnahara fee allayli watukhriju alhayya mina almayyiti watukhriju almayyita mina alhayyi watarzuqu man tashao bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (321, 'La yattakhithi almuminoona alkafireena awliyaa min dooni almumineena waman yaf\'al thalika falaysa mina Allahi fee shayin illa an tattaqoo minhum tuqatan wayuhaththirukumu Allahu nafsahu waila Allahi almaseer **u**');
INSERT INTO `en_transliteration` VALUES (322, 'Qul in tukhfoo ma fee sudoorikum aw tubdoohu ya\'lamhu Allahu waya\'lamu ma fee a **l** ssamawati wama fee alardi wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (323, 'Yawma tajidu kullu nafsin ma \'amilat min khayrin muhdaran wama \'amilat min sooin tawaddu law anna baynaha wabaynahu amadan ba\'eedan wayuhaththirukumu Allahu nafsahu wa **A** llahu raoofun bi **a** l\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (324, 'Qul in kuntum tuhibboona Allaha fa **i** ttabi\'oonee yuhbibkumu Allahu wayaghfir lakum thunoobakum wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (325, 'Qul atee\'oo Allaha wa **al** rrasoola fain tawallaw fainna Allaha la yuhibbu alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (326, 'Inna Allaha istafaadama wanoohan waala ibraheema waala \'imrana \'ala al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (327, 'Thurriyyatan ba\'duha min ba\'din wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (328, 'Ith qalati imraatu \'imrana rabbi innee nathartu laka ma fee batnee muharraran fataqabbal minnee innaka anta a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (329, 'Falamma wada\'atha qalat rabbi innee wada\'tuha ontha wa **A** llahu a\'lamu bima wada\'at walaysa a **l** ththakaru ka **a** lontha wainnee sammaytuha maryama wainnee o\'eethuha bika wathurriyyataha mina a **l** shshaytani a **l** rrajeem **i**');
INSERT INTO `en_transliteration` VALUES (330, 'Fataqabbalaha rabbuha biqaboolin hasanin waanbataha nabatan hasanan wakaffalaha zakariyya kullama dakhala \'alayha zakariyya almihraba wajada \'indaha rizqan qala ya maryamu anna laki hatha qalat huwa min \'indi Allahi inna Allaha yarzuqu man yashao bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (331, 'Hunalika da\'a zakariyya rabbahu qala rabbi hab lee min ladunka thurriyyatan tayyibatan innaka samee\'u a **l** ddu\'a **i**');
INSERT INTO `en_transliteration` VALUES (332, 'Fanadathu almalaikatu wahuwa qaimun yusallee fee almihrabi anna Allaha yubashshiruka biyahya musaddiqan bikalimatin mina Allahi wasayyidan wahasooran wanabiyyan mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (333, 'Qala rabbi anna yakoonu lee ghulamun waqad balaghaniya alkibaru wa **i** mraatee \'aqirun qala kathalika Allahu yaf\'alu ma yasha **o**');
INSERT INTO `en_transliteration` VALUES (334, 'Qala rabbi ij\'al lee ayatan qala ayatuka alla tukallima a **l** nnasa thalathata ayyamin illa ramzan wa **o** thkur rabbaka katheeran wasabbih bi **a** l\'ashiyyi wa **a** libkar **i**');
INSERT INTO `en_transliteration` VALUES (335, 'Waith qalati almalaikatu ya maryamu inna Allaha istafaki watahharaki wa **i** stafaki \'ala nisai al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (336, 'Ya maryamu oqnutee lirabbiki wa **o** sjudee wa **i** rka\'ee ma\'a a **l** rraki\'een **a**');
INSERT INTO `en_transliteration` VALUES (337, 'Thalika min anbai alghaybi nooheehi ilayka wama kunta ladayhim ith yulqoona aqlamahum ayyuhum yakfulu maryama wama kunta ladayhim ith yakhtasimoon **a**');
INSERT INTO `en_transliteration` VALUES (338, 'Ith qalati almalaikatu ya maryamu inna Allaha yubashshiruki bikalimatin minhu ismuhu almaseehu \'eesa ibnu maryama wajeehan fee a **l** ddunya wa **a** lakhirati wamina almuqarrabeen **a**');
INSERT INTO `en_transliteration` VALUES (339, 'Wayukallimu a **l** nnasa fee almahdi wakahlan wamina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (340, 'Qalat rabbi anna yakoonu lee waladun walam yamsasnee basharun qala kathaliki Allahu yakhluqu ma yashao itha qada amran fainnama yaqoolu lahu kun fayakoon **u**');
INSERT INTO `en_transliteration` VALUES (341, 'Wayu\'allimuhu alkitaba wa **a** lhikmata wa **al** ttawrata wa **a** linjeel **a**');
INSERT INTO `en_transliteration` VALUES (342, 'Warasoolan ila banee israeela annee qad jitukum biayatin min rabbikum annee akhluqu lakum mina a **l** tteeni kahayati a **l** ttayri faanfukhu feehi fayakoonu tayran biithni Allahi waobrio alakmaha wa **a** labrasa waohyee almawta biithni Allahi waonabbiokum bima takuloona wama taddakhiroona fee buyootikum inna fee thalika laayatan lakum in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (343, 'Wamusaddiqan lima bayna yadayya mina a **l** ttawrati waliohilla lakum ba\'da allathee hurrima \'alaykum wajitukum biayatin min rabbikum fa **i** ttaqoo Allaha waatee\'oon **i**');
INSERT INTO `en_transliteration` VALUES (344, 'Inna Allaha rabbee warabbukum fa **o** \'budoohu hathasiratun mustaqeem **un**');
INSERT INTO `en_transliteration` VALUES (345, 'Falamma ahassa \'eesa minhumu alkufra qala man ansaree ila Allahi qala alhawariyyoona nahnu ansaru Allahi amanna bi **A** llahi wa **i** shhad bianna muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (346, 'Rabbanaamanna bima anzata wa **i** ttaba\'na a **l** rrasoola fa **o** ktubna ma\'a a **l** shshahideen **a**');
INSERT INTO `en_transliteration` VALUES (347, 'Wamakaroo wamakara Allahu wa **A** llahu khayru almakireen **a**');
INSERT INTO `en_transliteration` VALUES (348, 'Ith qala Allahu ya \'eesa innee mutawaffeeka warafi\'uka ilayya wamutahhiruka mina allatheena kafaroo waja\'ilu allatheena ittaba\'ooka fawqa allatheena kafaroo ila yawmi alqiyamati thumma ilayya marji\'ukum faahkumu baynakum feema kuntum feehi takhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (349, 'Faamma allatheena kafaroo fao\'aththibuhum \'athaban shadeedan fee a **l** ddunya wa **a** lakhirati wama lahum min nasireen **a**');
INSERT INTO `en_transliteration` VALUES (350, 'Waamma allatheena amanoo wa\'amiloo a **l** ssalihati fayuwaffeehim ojoorahum wa **A** llahu la yuhibbu a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (351, 'Thalika natloohu \'alayka mina alayati wa **al** ththikri alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (352, 'Inna mathala \'eesa \'inda Allahi kamathali adama khalaqahu min turabin thumma qala lahu kun fayakoon **u**');
INSERT INTO `en_transliteration` VALUES (353, 'Alhaqqu min rabbika fala takun mina almumtareen **a**');
INSERT INTO `en_transliteration` VALUES (354, 'Faman hajjaka feehi min ba\'di ma jaaka mina al\'ilmi faqul ta\'alaw nad\'u abnaana waabnaakum wanisaana wanisaakum waanfusana waanfusakum thumma nabtahil fanaj\'al la\'nata Allahi \'ala alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (355, 'Inna hatha lahuwa alqasasu alhaqqu wama min ilahin illa Allahu wainna Allaha lahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (356, 'Fain tawallaw fainna Allaha \'aleemun bi **a** lmufsideen **a**');
INSERT INTO `en_transliteration` VALUES (357, 'Qul ya ahla alkitabi ta\'alaw ila kalimatin sawain baynana wabaynakum alla na\'buda illa Allaha wala nushrika bihi shayan wala yattakhitha ba\'duna ba\'dan arbaban min dooni Allahi fain tawallaw faqooloo ishhadoo bianna muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (358, 'Ya ahla alkitabi lima tuhajjoona fee ibraheema wama onzilati a **l** ttawratu wa **a** linjeelu illa min ba\'dihi afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (359, 'Haantum haolai hajajtum feema lakum bihi \'ilmun falima tuhajjoona feema laysa lakum bihi \'ilmun wa **A** llahu ya\'lamu waantum la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (360, 'Ma kana ibraheemu yahoodiyyan wala nasraniyyan walakin kana haneefan musliman wama kana mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (361, 'Inna awla a **l** nnasi biibraheema lallatheena ittaba\'oohu wahatha a **l** nnabiyyu wa **a** llatheena amanoo wa **A** llahu waliyyu almumineen **a**');
INSERT INTO `en_transliteration` VALUES (362, 'Waddat taifatun min ahli alkitabi law yudilloonakum wama yudilloona illa anfusahum wama yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (363, 'Ya ahla alkitabi lima takfuroona biayati Allahi waantum tashhadoon **a**');
INSERT INTO `en_transliteration` VALUES (364, 'Ya ahla alkitabi lima talbisoona alhaqqa bi **a** lbatili wataktumoona alhaqqa waantum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (365, 'Waqalat taifatun min ahli alkitabi aminoo bi **a** llathee onzila \'ala allatheena amanoo wajha a **l** nnahari wa **o** kfuroo akhirahu la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (366, 'Wala tuminoo illa liman tabi\'a deenakum qul inna alhuda huda Allahi an yuta ahadun mithla ma ooteetum aw yuhajjookum \'inda rabbikum qul inna alfadla biyadi Allahi yuteehi man yashao wa **A** llahu wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (367, 'Yakhtassu birahmatihi man yashao wa **A** llahu thoo alfadli al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (368, 'Wamin ahli alkitabi man in tamanhu biqintarin yuaddihi ilayka waminhum man in tamanhu bideenarin la yuaddihi ilayka illa ma dumta \'alayhi qaiman thalika biannahum qaloo laysa \'alayna fee alommiyyeena sabeelun wayaqooloona \'ala Allahi alkathiba wahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (369, 'Bala man awfa bi\'ahdihi wa **i** ttaqa fainna Allaha yuhibbu almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (370, 'Inna allatheena yashtaroona bi\'ahdi Allahi waaymanihim thamanan qaleelan olaika la khalaqa lahum fee alakhirati wala yukallimuhumu Allahu wala yan _th_ uru ilayhim yawma alqiyamati wala yuzakkeehim walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (371, 'Wainna minhum lafareeqan yalwoona asinatahum bi **a** lkitabi litahsaboohu mina alkitabi wama huwa mina alkitabi wayaqooloona huwa min \'indi Allahi wama huwa min \'indi Allahi wayaqooloona \'ala Allahi alkathiba wahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (372, 'Ma kana libasharin an yutiyahu Allahu alkitaba wa **a** lhukma wa **al** nnubuwwata thumma yaqoola li **l** nnasi koonoo \'ibadan lee min dooni Allahi walakin koonoo rabbaniyyeena bima kuntum tu\'allimoona alkitaba wabima kuntum tadrusoon **a**');
INSERT INTO `en_transliteration` VALUES (373, 'Wala yamurakum an tattakhithoo almalaikata wa **al** nnabiyyeena arbaban ayamurukum bi **a** lkufri ba\'da ith antum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (374, 'Waith akhatha Allahu meethaqa a **l** nnabiyyeena lamaataytukum min kitabin wahikmatin thumma jaakum rasoolun musaddiqun lima ma\'akum latuminunna bihi walatansurunnahu qala aaqrartum waakhathtum \'alathalikum isree qaloo aqrarna qala fa **i** shhadoo waana ma\'akum mina a **l** shshahideen **a**');
INSERT INTO `en_transliteration` VALUES (375, 'Faman tawalla ba\'da thalika faolaika humu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (376, 'Afaghayra deeni Allahi yabghoona walahu aslama man fee a **l** ssamawati wa **a** lardi taw\'an wakarhan wailayhi yurja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (377, 'Qul amanna bi **A** llahi wama onzila \'alayna wama onzila \'ala ibraheema waisma\'eela waishaqa waya\'qooba wa **a** lasbati wama ootiya moosa wa\'eesa wa **al** nnabiyyoona min rabbihim la nufarriqu bayna ahadin minhum wanahnu lahu muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (378, 'Waman yabtaghi ghayra alislami deenan falan yuqbala minhu wahuwa fee alakhirati mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (379, 'Kayfa yahdee Allahu qawman kafaroo ba\'da eemanihim washahidoo anna a **l** rrasoola haqqun wajaahumu albayyinatu wa **A** llahu la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (380, 'Olaika jazaohum anna \'alayhim la\'nata Allahi wa **a** lmalaikati wa **al** nnasi ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (381, 'Khalideena feeha la yukhaffafu \'anhumu al\'athabu wala hum yun _th_ aroon **a**');
INSERT INTO `en_transliteration` VALUES (382, 'Illa allatheena taboo min ba\'di thalika waaslahoo fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (383, 'Inna allatheena kafaroo ba\'da eemanihim thumma izdadoo kufran lan tuqbala tawbatuhum waolaika humu a **l** ddalloon **a**');
INSERT INTO `en_transliteration` VALUES (384, 'Inna allatheena kafaroo wamatoo wahum kuffarun falan yuqbala min ahadihim milo alardi thahaban walawi iftada bihi olaika lahum \'athabun aleemun wama lahum min nasireen **a**');
INSERT INTO `en_transliteration` VALUES (385, 'Lan tanaloo albirra hatta tunfiqoo mimma tuhibboona wama tunfiqoo min shayin fainna Allaha bihi \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (386, 'Kullu a **l** tta\'ami kana hillan libanee israeela illa maharrama israeelu \'ala nafsihi min qabli an tunazzala a **l** ttawratu qul fatoo bi **al** ttawrati fa **o** tlooha in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (387, 'Famani iftara \'ala Allahi alkathiba min ba\'di thalika faolaika humu a **l** _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (388, 'Qul sadaqa Allahu fa **i** ttabi\'oo millata ibraheema haneefan wama kana mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (389, 'Inna awwala baytin wudi\'a li **l** nnasi lallathee bibakkata mubarakan wahudan lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (390, 'Feehi ayatun bayyinatun maqamu ibraheema waman dakhalahu kana aminan walillahi \'ala a **l** nnasi hijju albayti mani istata\'a ilayhi sabeelan waman kafara fainna Allaha ghaniyyun \'ani al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (391, 'Qul ya ahla alkitabi lima takfuroona biayati Allahi wa **A** llahu shaheedun \'ala ma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (392, 'Qul ya ahla alkitabi lima tasuddoona \'an sabeeli Allahi man amana tabghoonaha \'iwajan waantum shuhadao wama Allahu bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (393, 'Ya ayyuha allatheena amanoo in tutee\'oo fareeqan mina allatheena ootoo alkitaba yaruddookum ba\'da eemanikum kafireen **a**');
INSERT INTO `en_transliteration` VALUES (394, 'Wakayfa takfuroona waantum tutla \'alaykum ayatu Allahi wafeekum rasooluhu waman ya\'tasim bi **A** llahi faqad hudiya ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (395, 'Ya ayyuha allatheena amanoo ittaqoo Allaha haqqa tuqatihi wala tamootunna illa waantum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (396, 'Wa **i** \'tasimoo bihabli Allahi jamee\'an wala tafarraqoo wa **o** thkuroo ni\'mata Allahi \'alaykum ith kuntum a\'daan faallafa bayna quloobikum faasbahtum bini\'matihi ikhwanan wakuntum \'ala shafahufratin mina a **l** nnari faanqathakum minha kathalika yubayyinu Allahu lakum ayatihi la\'allakum tahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (397, 'Watakun minkum ommatun yad\'oona ila alkhayri wayamuroona bi **a** lma\'roofi wayanhawna \'ani almunkari waolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (398, 'Wala takoonoo ka **a** llatheena tafarraqoo wa **i** khtalafoo min ba\'di ma jaahumu albayyinatu waolaika lahum \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (399, 'Yawma tabyaddu wujoohun wataswaddu wujoohun faamma allatheena iswaddat wujoohuhum akafartum ba\'da eemanikum fathooqoo al\'athaba bima kuntum takfuroon **a**');
INSERT INTO `en_transliteration` VALUES (400, 'Waamma allatheena ibyaddat wujoohuhum fafee rahmati Allahi hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (401, 'Tilka ayatu Allahi natlooha \'alayka bi **a** lhaqqi wama Allahu yureedu _th_ ulman lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (402, 'Walillahi ma fee a **l** ssamawati wama fee alardi waila Allahi turja\'u alomoor **u**');
INSERT INTO `en_transliteration` VALUES (403, 'Kuntum khayra ommatin okhrijat li **l** nnasi tamuroona bi **a** lma\'roofi watanhawna \'ani almunkari watuminoona bi **A** llahi walaw amana ahlu alkitabi lakana khayran lahum minhumu almuminoona waaktharuhumu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (404, 'Lan yadurrookum illa athan wain yuqatilookum yuwallookumu aladbara thumma la yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (405, 'Duribat \'alayhimu a **l** ththillatu ayna ma thuqifoo illa bihablin mina Allahi wahablin mina a **l** nnasi wabaoo bighadabin mina Allahi waduribat \'alayhimu almaskanatu thalika biannahum kanoo yakfuroona biayati Allahi wayaqtuloona alanbiyaa bighayri haqqin thalika bima \'asaw wakanoo ya\'tadoon **a**');
INSERT INTO `en_transliteration` VALUES (406, 'Laysoo sawaan min ahli alkitabi ommatun qaimatun yatloona ayati Allahi anaa allayli wahum yasjudoon **a**');
INSERT INTO `en_transliteration` VALUES (407, 'Yuminoona bi **A** llahi wa **a** lyawmi alakhiri wayamuroona bi **a** lma\'roofi wayanhawna \'ani almunkari wayusari\'oona fee alkhayrati waolaika mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (408, 'Wama yaf\'aloo min khayrin falan yukfaroohu wa **A** llahu \'aleemun bi **a** lmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (409, 'Inna allatheena kafaroo lan tughniya \'anhum amwaluhum wala awladuhum mina Allahi shayan waolaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (410, 'Mathalu ma yunfiqoona fee hathihi alhayati a **l** ddunya kamathali reehin feehasirrun asabat hartha qawmin _th_ alamoo anfusahum faahlakathu wama _th_ alamahumu Allahu walakin anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (411, 'Ya ayyuha allatheena amanoo la tattakhithoo bitanatan min doonikum la yaloonakum khabalan waddoo ma \'anittum qad badati albaghdao min afwahihim wama tukhfee sudooruhum akbaru qad bayyanna lakumu alayati in kuntum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (412, 'Haantum olai tuhibboonahum wala yuhibboonakum watuminoona bi **a** lkitabi kullihi waitha laqookum qaloo amanna waitha khalaw \'addoo \'alaykumu alanamila mina alghay _th_ i qul mootoo bighay _th_ ikum inna Allaha \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (413, 'In tamsaskum hasanatun tasuhum wain tusibkum sayyiatun yafrahoo biha wain tasbiroo watattaqoo la yadurrukum kayduhum shayan inna Allaha bima ya\'maloona muheet **un**');
INSERT INTO `en_transliteration` VALUES (414, 'Waith ghadawta min ahlika tubawwio almumineena maqa\'ida lilqitali wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (415, 'Ith hammat taifatani minkum an tafshala wa **A** llahu waliyyuhuma wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (416, 'Walaqad nasarakumu Allahu bibadrin waantum athillatun fa **i** ttaqoo Allaha la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (417, 'Ith taqoolu lilmumineena alan yakfiyakum an yumiddakum rabbukum bithalathati alafin mina almalaikati munzaleen **a**');
INSERT INTO `en_transliteration` VALUES (418, 'Bala in tasbiroo watattaqoo wayatookum min fawrihim hatha yumdidkum rabbukum bikhamsati alafin mina almalaikati musawwimeen **a**');
INSERT INTO `en_transliteration` VALUES (419, 'Wama ja\'alahu Allahu illa bushra lakum walitatmainna quloobukum bihi wama a **l** nnasru illa min \'indi Allahi al\'azeezi alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (420, 'Liyaqta\'a tarafan mina allatheena kafaroo aw yakbitahum fayanqaliboo khaibeen **a**');
INSERT INTO `en_transliteration` VALUES (421, 'Laysa laka mina alamri shayon aw yatooba \'alayhim aw yu\'aththibahum fainnahum _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (422, 'Walillahi ma fee a **l** ssamawati wama fee alardi yaghfiru liman yashao wayu\'aththibu man yashao wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (423, 'Ya ayyuha allatheena amanoo la takuloo a **l** rriba ad\'afan muda\'afatan wa **i** ttaqoo Allaha la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (424, 'Wa **i** ttaqoo a **l** nnara allatee o\'iddat lilkafireen **a**');
INSERT INTO `en_transliteration` VALUES (425, 'Waatee\'oo Allaha wa **al** rrasoola la\'allakum turhamoon **a**');
INSERT INTO `en_transliteration` VALUES (426, 'Wasari\'oo ila maghfiratin min rabbikum wajannatin \'arduha a **l** ssamawatu wa **a** lardu o\'iddat lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (427, 'Allatheena yunfiqoona fee a **l** ssarrai wa **al** ddarrai wa **a** lka _th_ imeena alghay _th_ a wa **a** l\'afeena \'ani a **l** nnasi wa **A** llahu yuhibbu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (428, 'Wa **a** llatheena itha fa\'aloo fahishatan aw _th_ alamoo anfusahum thakaroo Allaha fa **i** staghfaroo lithunoobihim waman yaghfiru a **l** ththunooba illa Allahu walam yusirroo \'ala ma fa\'aloo wahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (429, 'Olaika jazaohum maghfiratun min rabbihim wajannatun tajree min tahtiha alanharu khalideena feeha wani\'ma ajru al\'amileen **a**');
INSERT INTO `en_transliteration` VALUES (430, 'Qad khalat min qablikum sunanun faseeroo fee alardi fa **o** n _th_ uroo kayfa kana \'aqibatu almukaththibeen **a**');
INSERT INTO `en_transliteration` VALUES (431, 'Hatha bayanun li **l** nnasi wahudan wamaw\'i _th_ atun lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (432, 'Wala tahinoo wala tahzanoo waantumu ala\'lawna in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (433, 'In yamsaskum qarhun faqad massa alqawma qarhun mithluhu watilka alayyamu nudawiluha bayna a **l** nnasi waliya\'lama Allahu allatheena amanoo wayattakhitha minkum shuhadaa wa **A** llahu la yuhibbu a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (434, 'Waliyumahhisa Allahu allatheena amanoo wayamhaqa alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (435, 'Am hasibtum an tadkhuloo aljannata walamma ya\'lami Allahu allatheena jahadoo minkum waya\'lama a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (436, 'Walaqad kuntum tamannawna almawta min qabli an talqawhu faqad raaytumoohu waantum tan _th_ uroon **a**');
INSERT INTO `en_transliteration` VALUES (437, 'Wama muhammadun illa rasoolun qad khalat min qablihi a **l** rrusulu afain mata aw qutila inqalabtum \'ala a\'qabikum waman yanqalib \'ala \'aqibayhi falan yadurra Allaha shayan wasayajzee Allahu a **l** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (438, 'Wama kana linafsin an tamoota illa biithni Allahi kitaban muajjalan waman yurid thawaba a **l** ddunya nutihi minha waman yurid thawaba alakhirati nutihi minha wasanajzee a **l** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (439, 'Wakaayyin min nabiyyin qatala ma\'ahu ribbiyyoona katheerun fama wahanoo lima asabahum fee sabeeli Allahi wamada\'ufoo wama istakanoo wa **A** llahu yuhibbu a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (440, 'Wama kana qawlahum illa an qaloo rabbana ighfir lanathunoobana waisrafana fee amrina wathabbit aqdamana wa **o** nsurna \'ala alqawmi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (441, 'Faatahumu Allahu thawaba a **l** ddunya wahusna thawabi alakhirati wa **A** llahu yuhibbu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (442, 'Ya ayyuha allatheena amanoo in tutee\'oo allatheena kafaroo yaruddookum \'ala a\'qabikum fatanqaliboo khasireen **a**');
INSERT INTO `en_transliteration` VALUES (443, 'Bali Allahu mawlakum wahuwa khayru a **l** nnasireen **a**');
INSERT INTO `en_transliteration` VALUES (444, 'Sanulqee fee quloobi allatheena kafaroo a **l** rru\'ba bima ashrakoo bi **A** llahi ma lam yunazzil bihi sultanan wamawahumu a **l** nnaru wabisa mathwa a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (445, 'Walaqad sadaqakumu Allahu wa\'dahu ith tahussoonahum biithnihi hatta itha fashiltum watanaza\'tum fee alamri wa\'asaytum min ba\'di ma arakum ma tuhibboona minkum man yureedu a **l** ddunya waminkum man yureedu alakhirata thumma sarafakum \'anhum liyabtaliyakum walaqad \'afa \'ankum wa **A** llahu thoo fadlin \'ala almumineen **a**');
INSERT INTO `en_transliteration` VALUES (446, 'Ith tus\'idoona wala talwoona \'ala ahadin wa **al** rrasoolu yad\'ookum fee okhrakum faathabakum ghamman bighammin likayla tahzanoo \'ala ma fatakum wala ma asabakum wa **A** llahu khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (447, 'Thumma anzala \'alaykum min ba\'di alghammi amanatan nu\'asan yaghshataifatan minkum wataifatun qad ahammathum anfusuhum ya _th_ unnoona bi **A** llahi ghayra alhaqqi _th_ anna aljahiliyyati yaqooloona hal lana mina alamri min shayin qul inna alamra kullahu lillahi yukhfoona fee anfusihim ma la yubdoona laka yaqooloona law kana lana mina alamri shayon ma qutilna hahuna qul law kuntum fee buyootikum labaraza allatheena kutiba \'alayhimu alqatlu ila madaji\'ihim waliyabtaliya Allahu ma fee sudoorikum waliyumahhisa ma fee quloobikum wa **A** llahu \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (448, 'Inna allatheena tawallaw minkum yawma iltaqa aljam\'ani innama istazallahumu a **l** shshaytanu biba\'di ma kasaboo walaqad \'afa Allahu \'anhum inna Allaha ghafoorun haleem **un**');
INSERT INTO `en_transliteration` VALUES (449, 'Ya ayyuha allatheena amanoo la takoonoo ka **a** llatheena kafaroo waqaloo liikhwanihim ithadaraboo fee alardi aw kanoo ghuzzan law kanoo \'indana ma matoo wama qutiloo liyaj\'ala Allahu thalika hasratan fee quloobihim wa **A** llahu yuhyee wayumeetu wa **A** llahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (450, 'Walain qutiltum fee sabeeli Allahi aw muttum lamaghfiratun mina Allahi warahmatun khayrun mimma yajma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (451, 'Walain muttum aw qutiltum laila Allahi tuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (452, 'Fabima rahmatin mina Allahi linta lahum walaw kunta fa _thth_ an ghalee _th_ a alqalbi la **i** nfaddoo min hawlika fa **o** \'fu \'anhum wa **i** staghfir lahum washawirhum fee alamri faitha \'azamta fatawakkal \'ala Allahi inna Allaha yuhibbu almutawakkileen **a**');
INSERT INTO `en_transliteration` VALUES (453, 'In yansurkumu Allahu fala ghaliba lakum wain yakhthulkum faman tha allathee yansurukum min ba\'dihi wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (454, 'Wama kana linabiyyin an yaghulla waman yaghlul yati bima ghalla yawma alqiyamati thumma tuwaffa kullu nafsin ma kasabat wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (455, 'Afamani ittaba\'a ridwana Allahi kaman baa bisakhatin mina Allahi wamawahu jahannamu wabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (456, 'Hum darajatun \'inda Allahi wa **A** llahu baseerun bima ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (457, 'Laqad manna Allahu \'ala almumineena ith ba\'atha feehim rasoolan min anfusihim yatloo \'alayhim ayatihi wayuzakkeehim wayu\'allimuhumu alkitaba wa **a** lhikmata wain kanoo min qablu lafee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (458, 'Awalamma asabatkum museebatun qad asabtum mithlayha qultum anna hatha qul huwa min \'indi anfusikum inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (459, 'Wama asabakum yawma iltaqa aljam\'ani fabiithni Allahi waliya\'lama almumineen **a**');
INSERT INTO `en_transliteration` VALUES (460, 'Waliya\'lama allatheena nafaqoo waqeela lahum ta\'alaw qatiloo fee sabeeli Allahi awi idfa\'oo qaloo law na\'lamu qitalan la **i** ttaba\'nakum hum lilkufri yawmaithin aqrabu minhum lileemani yaqooloona biafwahihim ma laysa fee quloobihim wa **A** llahu a\'lamu bima yaktumoon **a**');
INSERT INTO `en_transliteration` VALUES (461, 'Allatheena qaloo liikhwanihim waqa\'adoo law ata\'oona ma qutiloo qul fa **i** draoo \'an anfusikumu almawta in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (462, 'Wala tahsabanna allatheena qutiloo fee sabeeli Allahi amwatan bal ahyaon \'inda rabbihim yurzaqoon **a**');
INSERT INTO `en_transliteration` VALUES (463, 'Fariheena bimaatahumu Allahu min fadlihi wayastabshiroona bi **a** llatheena lam yalhaqoo bihim min khalfihim alla khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (464, 'Yastabshiroona bini\'matin mina Allahi wafadlin waanna Allaha la yudee\'u ajra almumineen **a**');
INSERT INTO `en_transliteration` VALUES (465, 'Allatheena istajaboo lillahi wa **al** rrasooli min ba\'di ma asabahumu alqarhu lillatheena ahsanoo minhum wa **i** ttaqaw ajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (466, 'Allatheena qala lahumu a **l** nnasu inna a **l** nnasa qad jama\'oo lakum fa **i** khshawhum fazadahum eemanan waqaloo hasbuna Allahu wani\'ma alwakeel **u**');
INSERT INTO `en_transliteration` VALUES (467, 'Fa **i** nqalaboo bini\'matin mina Allahi wafadlin lam yamsashum sooon wa **i** ttaba\'oo ridwana Allahi wa **A** llahu thoo fadlin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (468, 'Innamathalikumu a **l** shshaytanu yukhawwifu awliyaahu fala takhafoohum wakhafooni in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (469, 'Wala yahzunka allatheena yusari\'oona fee alkufri innahum lan yadurroo Allaha shayan yureedu Allahu alla yaj\'ala lahum ha _thth_ an fee alakhirati walahum \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (470, 'Inna allatheena ishtarawoo alkufra bi **a** leemani lan yadurroo Allaha shayan walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (471, 'Wala yahsabanna allatheena kafaroo annama numlee lahum khayrun lianfusihim innama numlee lahum liyazdadoo ithman walahum \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (472, 'Ma kana Allahu liyathara almumineena \'ala ma antum \'alayhi hatta yameeza alkhabeetha mina a **l** ttayyibi wama kana Allahu liyutli\'akum \'ala alghaybi walakinna Allaha yajtabee min rusulihi man yashao faaminoo bi **A** llahi warusulihi wain tuminoo watattaqoo falakum ajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (473, 'Wala yahsabanna allatheena yabkhaloona bimaatahummu Allahu min fadlihi huwa khayran lahum bal huwa sharrun lahum sayutawwaqoona ma bakhiloo bihi yawma alqiyamati walillahi meerathu a **l** ssamawati wa **a** lardi wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (474, 'Laqad sami\'a Allahu qawla allatheena qaloo inna Allaha faqeerun wanahnu aghniyaon sanaktubu ma qaloo waqatlahumu alanbiyaa bighayri haqqin wanaqoolu thooqoo \'athaba alhareeq **i**');
INSERT INTO `en_transliteration` VALUES (475, 'Thalika bima qaddamat aydeekum waanna Allaha laysa bi _th_ allamin lil\'abeed **i**');
INSERT INTO `en_transliteration` VALUES (476, 'Allatheena qaloo inna Allaha \'ahida ilayna alla numina lirasoolin hatta yatiyana biqurbanin takuluhu a **l** nnaru qul qad jaakum rusulun min qablee bi **a** lbayyinati wabi **a** llathee qultum falima qatatumoohum in kutum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (477, 'Fain kaththabooka faqad kuththiba rusulun min qablika jaoo bi **a** lbayyinati wa **al** zzuburi wa **a** lkitabi almuneer **i**');
INSERT INTO `en_transliteration` VALUES (478, 'Kullu nafsin thaiqatu almawti wainnama tuwaffawna ojoorakum yawma alqiyamati faman zuhziha \'ani a **l** nnari waodkhila aljannata faqad faza wama alhayatu a **l** ddunya illa mata\'u alghuroor **i**');
INSERT INTO `en_transliteration` VALUES (479, 'Latublawunna fee amwalikum waanfusikum walatasma\'unna mina allatheena ootoo alkitaba min qablikum wamina allatheena ashrakoo athan katheeran wain tasbiroo watattaqoo fainna thalika min \'azmi alomoor **i**');
INSERT INTO `en_transliteration` VALUES (480, 'Waith akhatha Allahu meethaqa allatheena ootoo alkitaba latubayyinunnahu li **l** nnasi wala taktumoonahu fanabathoohu waraa _th_ uhoorihim wa **i** shtaraw bihi thamanan qaleelan fabisa ma yashtaroon **a**');
INSERT INTO `en_transliteration` VALUES (481, 'La tahsabanna allatheena yafrahoona bima ataw wayuhibboona an yuhmadoo bima lam yaf\'aloo fala tahsabannahum bimafazatin mina al\'athabi walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (482, 'Walillahi mulku a **l** ssamawati wa **a** lardi wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (483, 'Inna fee khalqi a **l** ssamawati wa **a** lardi wa **i** khtilafi allayli wa **al** nnahari laayatin liolee alalbab **i**');
INSERT INTO `en_transliteration` VALUES (484, 'Allatheena yathkuroona Allaha qiyaman waqu\'oodan wa\'ala junoobihim wayatafakkaroona fee khalqi a **l** ssamawati wa **a** lardi rabbana ma khalaqta hatha batilan subhanaka faqina \'athaba a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (485, 'Rabbana innaka man tudkhili a **l** nnara faqad akhzaytahu wama li **l** _thth_ alimeena min ansar **in**');
INSERT INTO `en_transliteration` VALUES (486, 'Rabbana innana sami\'na munadiyan yunadee lileemani an aminoo birabbikum faamanna rabbana fa **i** ghfir lanathunoobana wakaffir \'anna sayyiatina watawaffana ma\'a alabrar **i**');
INSERT INTO `en_transliteration` VALUES (487, 'Rabbana waatina ma wa\'adtana \'ala rusulika wala tukhzina yawma alqiyamati innaka la tukhlifu almee\'ad **a**');
INSERT INTO `en_transliteration` VALUES (488, 'Fa **i** stajaba lahum rabbuhum annee la odee\'u \'amala \'amilin minkum min thakarin aw ontha ba\'dukum min ba\'din fa **a** llatheena hajaroo waokhrijoo min diyarihim waoothoo fee sabeelee waqataloo waqutiloo laokaffiranna \'anhum sayyiatihim walaodkhilannahum jannatin tajree min tahtiha alanharu thawaban min \'indi Allahi wa **A** llahu \'indahu husnu a **l** ththawab **i**');
INSERT INTO `en_transliteration` VALUES (489, 'La yaghurrannaka taqallubu allatheena kafaroo fee albilad **i**');
INSERT INTO `en_transliteration` VALUES (490, 'Mata\'un qaleelun thumma mawahum jahannamu wabisa almihad **u**');
INSERT INTO `en_transliteration` VALUES (491, 'Lakini allatheena ittaqaw rabbahum lahum jannatun tajree min tahtiha alanharu khalideena feeha nuzulan min \'indi Allahi wama \'inda Allahi khayrun lilabrar **i**');
INSERT INTO `en_transliteration` VALUES (492, 'Wainna min ahli alkitabi laman yuminu bi **A** llahi wama onzila ilaykum wama onzila ilayhim khashi\'eena lillahi la yashtaroona biayati Allahi thamanan qaleelan olaika lahum ajruhum \'inda rabbihim inna Allaha saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (493, 'Ya ayyuha allatheena amanoo isbiroo wasabiroo warabitoo wa **i** ttaqoo Allaha la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (494, 'Ya ayyuha a **l** nnasu ittaqoo rabbakumu allathee khalaqakum min nafsin wahidatin wakhalaqa minha zawjaha wabaththa minhuma rijalan katheeran wanisaan wa **i** ttaqoo Allaha allathee tasaaloona bihi wa **a** larhama inna Allaha kana \'alaykum raqeeb **an**');
INSERT INTO `en_transliteration` VALUES (495, 'Waatoo alyatama amwalahum wala tatabaddaloo alkhabeetha bi **al** ttayyibi wala takuloo amwalahum ila amwalikum innahu kana hooban kabeera **n**');
INSERT INTO `en_transliteration` VALUES (496, 'Wain khiftum alla tuqsitoo fee alyatama fa **i** nkihoo mataba lakum mina a **l** nnisai mathna wathulatha waruba\'a fain khiftum alla ta\'diloo fawahidatan aw ma malakat aymanukum thalika adna alla ta\'ooloo');
INSERT INTO `en_transliteration` VALUES (497, 'Waatoo a **l** nnisaa saduqatihinna nihlatan fain tibna lakum \'an shayin minhu nafsan fakuloohu haneean mareea **n**');
INSERT INTO `en_transliteration` VALUES (498, 'Wala tutoo a **l** ssufahaa amwalakumu allatee ja\'ala Allahu lakum qiyaman wa **o** rzuqoohum feeha wa **o** ksoohum waqooloo lahum qawlan ma\'roofa **n**');
INSERT INTO `en_transliteration` VALUES (499, 'Wa **i** btaloo alyatamahatta itha balaghoo a **l** nnikaha fain anastum minhum rushdan fa **i** dfa\'oo ilayhim amwalahum wala takulooha israfan wabidaran an yakbaroo waman kana ghaniyyan falyasta\'fif waman kana faqeeran falyakul bi **a** lma\'roofi faitha dafa\'tum ilayhim amwalahum faashhidoo \'alayhim wakafa bi **A** llahi haseeba **n**');
INSERT INTO `en_transliteration` VALUES (500, 'Li **l** rrijali naseebun mimma taraka alwalidani wa **a** laqraboona wali **l** nnisai naseebun mimma taraka alwalidani wa **a** laqraboona mimma qalla minhu aw kathura naseeban mafrooda **n**');
INSERT INTO `en_transliteration` VALUES (501, 'Waithahadara alqismata oloo alqurba wa **a** lyatama wa **a** lmasakeenu fa **o** rzuqoohum minhu waqooloo lahum qawlan ma\'roofa **n**');
INSERT INTO `en_transliteration` VALUES (502, 'Walyakhsha allatheena law tarakoo min khalfihim thurriyyatan di\'afan khafoo \'alayhim falyattaqoo Allaha walyaqooloo qawlan sadeeda **n**');
INSERT INTO `en_transliteration` VALUES (503, 'Inna allatheena yakuloona amwala alyatama _th_ ulman innama yakuloona fee butoonihim naran wasayaslawna sa\'eera **n**');
INSERT INTO `en_transliteration` VALUES (504, 'Yooseekumu Allahu fee awladikum li **l** ththakari mithlu ha _thth_ i alonthayayni fain kunna nisaan fawqa ithnatayni falahunna thulutha ma taraka wain kanat wahidatan falaha a **l** nnisfu waliabawayhi likulli wahidin minhuma a **l** ssudusu mimma taraka in kana lahu waladun fain lam yakun lahu waladun wawarithahu abawahu faliommihi a **l** ththuluthu fain kana lahu ikhwatun faliommihi a **l** ssudusu min ba\'di wasiyyatin yoosee biha aw daynin abaokum waabnaokum la tadroona ayyuhum aqrabu lakum naf\'an fareedatan mina Allahi inna Allaha kana \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (505, 'Walakum nisfu ma taraka azwajukum in lam yakun lahunna waladun fain kana lahunna waladun falakumu a **l** rrubu\'u mimma tarakna min ba\'di wasiyyatin yooseena biha aw daynin walahunna a **l** rrubu\'u mimma taraktum in lam yakun lakum waladun fain kana lakum waladun falahunna a **l** ththumunu mimma taraktum min ba\'di wasiyyatin toosoona biha aw daynin wain kana rajulun yoorathu kalalatan awi imraatun walahu akhun aw okhtun falikulli wahidin minhuma a **l** ssudusu fain kanoo akthara min thalika fahum shurakao fee a **l** ththuluthi min ba\'di wasiyyatin yoosa biha aw daynin ghayra mudarrin wasiyyatan mina Allahi wa **A** llahu \'aleemun haleem **un**');
INSERT INTO `en_transliteration` VALUES (506, 'Tilka hudoodu Allahi waman yuti\'i Allaha warasoolahu yudkhilhu jannatin tajree min tahtiha alanharu khalideena feeha wathalika alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (507, 'Waman ya\'si Allaha warasoolahu wayata\'adda hudoodahu yudkhilhu naran khalidan feeha walahu \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (508, 'Wa **a** llatee yateena alfahishata min nisaikum fa **i** stashhidoo \'alayhinna arba\'atan minkum fain shahidoo faamsikoohunna fee albuyooti hatta yatawaffahunna almawtu aw yaj\'ala Allahu lahunna sabeela **n**');
INSERT INTO `en_transliteration` VALUES (509, 'Wa **a** llathani yatiyaniha minkum faathoohuma fain taba waaslaha faa\'ridoo \'anhuma inna Allaha kana tawwaban raheema **n**');
INSERT INTO `en_transliteration` VALUES (510, 'Innama a **l** ttawbatu \'ala Allahi lillatheena ya\'maloona a **l** ssooa bijahalatin thumma yatooboona min qareebin faolaika yatoobu Allahu \'alayhim wakana Allahu \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (511, 'Walaysati a **l** ttawbatu lillatheena ya\'maloona a **l** ssayyiati hatta ithahadara ahadahumu almawtu qala innee tubtu alana wala allatheena yamootoona wahum kuffarun olaika a\'tadna lahum \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (512, 'Ya ayyuha allatheena amanoo la yahillu lakum an tarithoo a **l** nnisaa karhan wala ta\'duloohunna litathhaboo biba\'di maataytumoohunna illa an yateena bifahishatin mubayyinatin wa\'ashiroohunna bi **a** lma\'roofi fain karihtumoohunna fa\'asa an takrahoo shayan wayaj\'ala Allahu feehi khayran katheera **n**');
INSERT INTO `en_transliteration` VALUES (513, 'Wain aradtumu istibdala zawjin makana zawjin waataytum ihdahunna qintaran fala takhuthoo minhu shayan atakhuthoonahu buhtanan waithman mubeena **n**');
INSERT INTO `en_transliteration` VALUES (514, 'Wakayfa takhuthoonahu waqad afda ba\'dukum ila ba\'din waakhathna minkum meethaqan ghalee _th_ a **n**');
INSERT INTO `en_transliteration` VALUES (515, 'Wala tankihoo ma nakaha abaokum mina a **l** nnisai illa ma qad salafa innahu kana fahishatan wamaqtan wasaa sabeela **n**');
INSERT INTO `en_transliteration` VALUES (516, 'Hurrimat \'alaykum ommahatukum wabanatukum waakhawatukum wa\'ammatukum wakhalatukum wabanatu alakhi wabanatu alokhti waommahatukumu allatee arda\'nakum waakhawatukum mina a **l** rrada\'ati waommahatu nisaikum warabaibukumu allatee fee hujoorikum min nisaikumu allatee dakhatum bihinna fain lam takoonoo dakhatum bihinna fala junaha \'alaykum wahalailu abnaikumu allatheena min aslabikum waan tajma\'oo bayna alokhtayni illa ma qad salafa inna Allaha kana ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (517, 'Wa **a** lmuhsanatu mina a **l** nnisai illa ma malakat aymanukum kitaba Allahi \'alaykum waohilla lakum ma waraa thalikum an tabtaghoo biamwalikum muhsineena ghayra musafiheena fama istamta\'tum bihi minhunna faatoohunna ojoorahunna fareedatan wala junaha \'alaykum feema taradaytum bihi min ba\'di alfareedati inna Allaha kana \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (518, 'Waman lam yastati\' minkum tawlan an yankiha almuhsanati almuminati famin ma malakat aymanukum min fatayatikumu almuminati wa **A** llahu a\'lamu bieemanikum ba\'dukum min ba\'din fa **i** nkihoohunna biithni ahlihinna waatoohunna ojoorahunna bi **a** lma\'roofi muhsanatin ghayra masafihatin wala muttakhithati akhdanin faitha ohsinna fain atayna bifahishatin fa\'alayhinna nisfu ma \'ala almuhsanati mina al\'athabi thalika liman khashiya al\'anata minkum waan tasbiroo khayrun lakum wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (519, 'Yureedu Allahu liyubayyina lakum wayahdiyakum sunana allatheena min qablikum wayatooba \'alaykum wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (520, 'Wa **A** llahu yureedu an yatooba \'alaykum wayureedu allatheena yattabi\'oona a **l** shshahawati an tameeloo maylan \'a _th_ eem **an**');
INSERT INTO `en_transliteration` VALUES (521, 'Yureedu Allahu an yukhaffifa \'ankum wakhuliqa alinsanu da\'eefa **n**');
INSERT INTO `en_transliteration` VALUES (522, 'Ya ayyuha allatheena amanoo la takuloo amwalakum baynakum bi **a** lbatili illa an takoona tijaratan \'an taradin minkum wala taqtuloo anfusakum inna Allaha kana bikum raheema **n**');
INSERT INTO `en_transliteration` VALUES (523, 'Waman yaf\'al thalika \'udwanan wa _th_ ulman fasawfa nusleehi naran wakana thalika \'ala Allahi yaseera **n**');
INSERT INTO `en_transliteration` VALUES (524, 'In tajtaniboo kabaira ma tunhawna \'anhu nukaffir \'ankum sayyiatikum wanudkhilkum mudkhalan kareema **n**');
INSERT INTO `en_transliteration` VALUES (525, 'Wala tatamannaw ma faddala Allahu bihi ba\'dakum \'ala ba\'din li **l** rrijali naseebun mimma iktasaboo wali **l** nnisai naseebun mimma iktasabna wa **i** saloo Allaha min fadlihi inna Allaha kana bikulli shayin \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (526, 'Walikullin ja\'ana mawaliya mimma taraka alwalidani wa **a** laqraboona wa **a** llatheena \'aqadat aymanukum faatoohum naseebahum inna Allaha kana \'ala kulli shayin shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (527, 'A **l** rrijalu qawwamoona \'ala a **l** nnisai bima faddala Allahu ba\'dahum \'ala ba\'din wabima anfaqoo min amwalihim fa **al** ssalihatu qanitatun hafi _th_ atun lilghaybi bimahafi _th_ a Allahu wa **a** llatee takhafoona nushoozahunna fa\'i _th_ oohunna wa **o** hjuroohunna fee almadaji\'i wa **i** driboohunna fain ata\'nakum fala tabghoo \'alayhinna sabeelan inna Allaha kana \'aliyyan kabeera **n**');
INSERT INTO `en_transliteration` VALUES (528, 'Wain khiftum shiqaqa baynihima fa **i** b\'athoo hakaman min ahlihi wahakaman min ahliha in yureeda islahan yuwaffiqi Allahu baynahuma inna Allaha kana \'aleeman khabeera **n**');
INSERT INTO `en_transliteration` VALUES (529, 'Wa **o** \'budoo Allaha wala tushrikoo bihi shayan wabi **a** lwalidayni ihsanan wabithee alqurba wa **a** lyatama wa **a** lmasakeeni wa **a** ljari thee alqurba wa **a** ljari aljunubi wa **al** ssahibi bi **a** ljanbi wa **i** bni a **l** ssabeeli wama malakat aymanukum inna Allaha la yuhibbu man kana mukhtalan fakhoora **n**');
INSERT INTO `en_transliteration` VALUES (530, 'Allatheena yabkhaloona wayamuroona a **l** nnasa bi **a** lbukhli wayaktumoona maatahumu Allahu min fadlihi waa\'tadna lilkafireena \'athaban muheena **n**');
INSERT INTO `en_transliteration` VALUES (531, 'Wa **a** llatheena yunfiqoona amwalahum riaa a **l** nnasi wala yuminoona bi **A** llahi wala bi **a** lyawmi alakhiri waman yakuni a **l** shshaytanu lahu qareenan fasaa qareena **n**');
INSERT INTO `en_transliteration` VALUES (532, 'Wamatha \'alayhim law amanoo bi **A** llahi wa **a** lyawmi alakhiri waanfaqoo mimma razaqahumu Allahu wakana Allahu bihim \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (533, 'Inna Allaha la ya _th_ limu mithqala tharratin wain taku hasanatan yuda\'ifha wayuti min ladunhu ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (534, 'Fakayfa itha jina min kulli ommatin bishaheedin wajina bika \'ala haolai shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (535, 'Yawmaithin yawaddu allatheena kafaroo wa\'asawoo a **l** rrasoola law tusawwa bihimu alardu wala yaktumoona Allaha hadeetha **n**');
INSERT INTO `en_transliteration` VALUES (536, 'Ya ayyuha allatheena amanoo la taqraboo a **l** ssalata waantum sukarahatta ta\'lamoo ma taqooloona wala junuban illa \'abiree sabeelin hatta taghtasiloo wain kuntum marda aw \'ala safarin aw jaa ahadun minkum mina alghaiti aw lamastumu a **l** nnisaa falam tajidoo maan fatayammamoo sa\'eedan tayyiban fa **i** msahoo biwujoohikum waaydeekum inna Allaha kana \'afuwwan ghafoora **n**');
INSERT INTO `en_transliteration` VALUES (537, 'Alam tara ila allatheena ootoo naseeban mina alkitabi yashtaroona a **l** ddalalata wayureedoona an tadilloo a **l** ssabeel **a**');
INSERT INTO `en_transliteration` VALUES (538, 'Wa **A** llahu a\'lamu bia\'daikum wakafa bi **A** llahi waliyyan wakafa bi **A** llahi naseera **n**');
INSERT INTO `en_transliteration` VALUES (539, 'Mina allatheena hadoo yuharrifoona alkalima \'an mawadi\'ihi wayaqooloona sami\'na wa\'asayna wa **i** sma\' ghayra musma\'in wara\'ina layyan biasinatihim wata\'nan fee a **l** ddeeni walaw annahum qaloo sami\'na waata\'na wa **i** sma\' wa **o** n _th_ urna lakana khayran lahum waaqwama walakin la\'anahumu Allahu bikufrihim fala yuminoona illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (540, 'Ya ayyuha allatheena ootoo alkitaba aminoo bima nazzana musaddiqan lima ma\'akum min qabli an natmisa wujoohan fanaruddaha \'ala adbariha aw nal\'anahum kama la\'anna ashaba a **l** ssabti wakana amru Allahi maf\'oola **n**');
INSERT INTO `en_transliteration` VALUES (541, 'Inna Allaha la yaghfiru an yushraka bihi wayaghfiru ma doona thalika liman yashao waman yushrik bi **A** llahi faqadi iftara ithman \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (542, 'Alam tara ila allatheena yuzakkoona anfusahum bali Allahu yuzakkee man yashao wala yu _th_ lamoona fateela **n**');
INSERT INTO `en_transliteration` VALUES (543, 'On _th_ ur kayfa yaftaroona \'ala Allahi alkathiba wakafa bihi ithman mubeena **n**');
INSERT INTO `en_transliteration` VALUES (544, 'Alam tara ila allatheena ootoo naseeban mina alkitabi yuminoona bi **a** ljibti wa **al** ttaghooti wayaqooloona lillatheena kafaroo haolai ahda mina allatheena amanoo sabeela **n**');
INSERT INTO `en_transliteration` VALUES (545, 'Olaika allatheena la\'anahumu Allahu waman yal\'ani Allahu falan tajida lahu naseera **n**');
INSERT INTO `en_transliteration` VALUES (546, 'Am lahum naseebun mina almulki faithan la yutoona a **l** nnasa naqeera **n**');
INSERT INTO `en_transliteration` VALUES (547, 'Am yahsudoona a **l** nnasa \'ala maatahumu Allahu min fadlihi faqad ataynaala ibraheema alkitaba wa **a** lhikmata waataynahum mulkan \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (548, 'Faminhum man amana bihi waminhum man sadda \'anhu wakafa bijahannama sa\'eera **n**');
INSERT INTO `en_transliteration` VALUES (549, 'Inna allatheena kafaroo biayatina sawfa nusleehim naran kullama nadijat julooduhum baddanahum juloodan ghayraha liyathooqoo al\'athaba inna Allaha kana \'azeezan hakeema **n**');
INSERT INTO `en_transliteration` VALUES (550, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati sanudkhiluhum jannatin tajree min tahtiha alanharu khalideena feeha abadan lahum feeha azwajun mutahharatun wanudkhiluhum _th_ illan _th_ aleela **n**');
INSERT INTO `en_transliteration` VALUES (551, 'Inna Allaha yamurukum an tuaddoo alamanati ila ahliha waithahakamtum bayna a **l** nnasi an tahkumoo bi **a** l\'adli inna Allaha ni\'imma ya\'i _th_ ukum bihi inna Allaha kana samee\'an baseera **n**');
INSERT INTO `en_transliteration` VALUES (552, 'Ya ayyuha allatheena amanoo atee\'oo Allaha waatee\'oo a **l** rrasoola waolee alamri minkum fain tanaza\'tum fee shayin faruddoohu ila Allahi wa **al** rrasooli in kuntum tuminoona bi **A** llahi wa **a** lyawmi alakhiri thalika khayrun waahsanu taweela **n**');
INSERT INTO `en_transliteration` VALUES (553, 'Alam tara ila allatheena yaz\'umoona annahum amanoo bima onzila ilayka wama onzila min qablika yureedoona an yatahakamoo ila a **l** ttaghooti waqad omiroo an yakfuroo bihi wayureedu a **l** shshaytanu an yudillahum dalalan ba\'eeda **n**');
INSERT INTO `en_transliteration` VALUES (554, 'Waitha qeela lahum ta\'alaw ila ma anzala Allahu waila a **l** rrasooli raayta almunafiqeena yasuddoona \'anka sudooda **n**');
INSERT INTO `en_transliteration` VALUES (555, 'Fakayfa itha asabathum museebatun bima qaddamat aydeehim thumma jaooka yahlifoona bi **A** llahi in aradna illa ihsanan watawfeeqa **n**');
INSERT INTO `en_transliteration` VALUES (556, 'Olaika allatheena ya\'lamu Allahu ma fee quloobihim faa\'rid \'anhum wa\'i _th_ hum waqul lahum fee anfusihim qawlan baleegha **n**');
INSERT INTO `en_transliteration` VALUES (557, 'Wama arsana min rasoolin illa liyuta\'a biithni Allahi walaw annahum ith _th_ alamoo anfusahum jaooka fa **i** staghfaroo Allaha wa **i** staghfara lahumu a **l** rrasoolu lawajadoo Allaha tawwaban raheema **n**');
INSERT INTO `en_transliteration` VALUES (558, 'Fala warabbika la yuminoona hatta yuhakkimooka feema shajara baynahum thumma la yajidoo fee anfusihim harajan mimma qadayta wayusallimoo tasleema **n**');
INSERT INTO `en_transliteration` VALUES (559, 'Walaw anna katabna \'alayhim ani oqtuloo anfusakum awi okhrujoo min diyarikum ma fa\'aloohu illa qaleelun minhum walaw annahum fa\'aloo ma yoo\'a _th_ oona bihi lakana khayran lahum waashadda tathbeeta **n**');
INSERT INTO `en_transliteration` VALUES (560, 'Waithan laataynahum min ladunna ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (561, 'Walahadaynahum siratan mustaqeema **n**');
INSERT INTO `en_transliteration` VALUES (562, 'Waman yuti\'i Allaha wa **al** rrasoola faolaika ma\'a allatheena an\'ama Allahu \'alayhim mina a **l** nnabiyyeena wa **al** ssiddeeqeena wa **al** shshuhadai wa **al** ssaliheena wahasuna olaika rafeeqa **n**');
INSERT INTO `en_transliteration` VALUES (563, 'Thalika alfadlu mina Allahi wakafa bi **A** llahi \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (564, 'Ya ayyuha allatheena amanoo khuthoo hithrakum fa **i** nfiroo thubatin awi infiroo jamee\'a **n**');
INSERT INTO `en_transliteration` VALUES (565, 'Wainna minkum laman layubattianna fain asabatkum museebatun qala qad an\'ama Allahu \'alayya ith lam akun ma\'ahum shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (566, 'Walain asabakum fadlun mina Allahi layaqoolanna kaan lam takun baynakum wabaynahu mawaddatun ya laytanee kuntu ma\'ahum faafooza fawzan \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (567, 'Falyuqatil fee sabeeli Allahi allatheena yashroona alhayata a **l** ddunya bi **a** lakhirati waman yuqatil fee sabeeli Allahi fayuqtal aw yaghlib fasawfa nuteehi ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (568, 'Wama lakum la tuqatiloona fee sabeeli Allahi wa **a** lmustad\'afeena mina a **l** rrijali wa **al** nnisai wa **a** lwildani allatheena yaqooloona rabbana akhrijna min hathihi alqaryati a **l** _thth_ alimi ahluha wa **i** j\'al lana min ladunka waliyyan wa **i** j\'al lana min ladunka naseera **n**');
INSERT INTO `en_transliteration` VALUES (569, 'Allatheena amanoo yuqatiloona fee sabeeli Allahi wa **a** llatheena kafaroo yuqatiloona fee sabeeli a **l** ttaghooti faqatiloo awliyaa a **l** shshaytani inna kayda a **l** shshaytani kana da\'eefa **n**');
INSERT INTO `en_transliteration` VALUES (570, 'Alam tara ila allatheena qeela lahum kuffoo aydiyakum waaqeemoo a **l** ssalata waatoo a **l** zzakata falamma kutiba \'alayhimu alqitalu itha fareequn minhum yakhshawna a **l** nnasa kakhashyati Allahi aw ashadda khashyatan waqaloo rabbana lima katabta \'alayna alqitala lawla akhkhartana ila ajalin qareebin qul mata\'u a **l** ddunya qaleelun wa **a** lakhiratu khayrun limani ittaqa wala tu _th_ lamoona fateela **n**');
INSERT INTO `en_transliteration` VALUES (571, 'Aynama takoonoo yudrikkumu almawtu walaw kuntum fee buroojin mushayyadatin wain tusibhum hasanatun yaqooloo hathihi min \'indi Allahi wain tusibhum sayyiatun yaqooloo hathihi min \'indika qul kullun min \'indi Allahi famali haolai alqawmi la yakadoona yafqahoona hadeetha **n**');
INSERT INTO `en_transliteration` VALUES (572, 'Ma asabaka min hasanatin famina Allahi wama asabaka min sayyiatin famin nafsika waarsanaka li **l** nnasi rasoolan wakafa bi **A** llahi shaheedan');
INSERT INTO `en_transliteration` VALUES (573, 'Man yuti\'i a **l** rrasoola faqad ata\'a Allaha waman tawalla fama arsanaka \'alayhim hafee _th_ a **n**');
INSERT INTO `en_transliteration` VALUES (574, 'Wayaqooloona ta\'atun faitha barazoo min \'indika bayyata taifatun minhum ghayra allathee taqoolu wa **A** llahu yaktubu ma yubayyitoona faa\'rid \'anhum watawakkal \'ala Allahi wakafa bi **A** llahi wakeela **n**');
INSERT INTO `en_transliteration` VALUES (575, 'Afala yatadabbaroona alqurana walaw kana min \'indi ghayri Allahi lawajadoo feehi ikhtilafan katheera **n**');
INSERT INTO `en_transliteration` VALUES (576, 'Waitha jaahum amrun mina alamni awi alkhawfi atha\'oo bihi walaw raddoohu ila a **l** rrasooli waila olee alamri minhum la\'alimahu allatheena yastanbitoonahu minhum walawla fadlu Allahi \'alaykum warahmatuhu la **i** ttaba\'tumu a **l** shshaytana illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (577, 'Faqatil fee sabeeli Allahi la tukallafu illa nafsaka waharridi almumineena \'asa Allahu an yakuffa basa allatheena kafaroo wa **A** llahu ashaddu basan waashaddu tankeela **n**');
INSERT INTO `en_transliteration` VALUES (578, 'Man yashfa\' shafa\'atan hasanatan yakun lahu naseebun minha waman yashfa\' shafa\'atan sayyiatan yakun lahu kiflun minha wakana Allahu \'ala kulli shayin muqeeta **n**');
INSERT INTO `en_transliteration` VALUES (579, 'Waithahuyyeetum bitahiyyatin fahayyoo biahsana minha aw ruddooha inna Allaha kana \'ala kulli shayin haseeba **n**');
INSERT INTO `en_transliteration` VALUES (580, 'Allahu la ilaha illa huwa layajma\'annakum ila yawmi alqiyamati la rayba feehi waman asdaqu mina Allahi hadeetha **n**');
INSERT INTO `en_transliteration` VALUES (581, 'Fama lakum fee almunafiqeena fiatayni wa **A** llahu arkasahum bima kasaboo atureedoona an tahdoo man adalla Allahu waman yudlili Allahu falan tajida lahu sabeela **n**');
INSERT INTO `en_transliteration` VALUES (582, 'Waddoo law takfuroona kama kafaroo fatakoonoona sawaan fala tattakhithoo minhum awliyaa hatta yuhajiroo fee sabeeli Allahi fain tawallaw fakhuthoohum wa **o** qtuloohum haythu wajadtumoohum wala tattakhithoo minhum waliyyan wala naseera **n**');
INSERT INTO `en_transliteration` VALUES (583, 'Illa allatheena yasiloona ila qawmin baynakum wabaynahum meethaqun aw jaookum hasirat sudooruhum an yuqatilookum aw yuqatiloo qawmahum walaw shaa Allahu lasallatahum \'alaykum falaqatalookum faini i\'tazalookum falam yuqatilookum waalqaw ilaykumu a **l** ssalama fama ja\'ala Allahu lakum \'alayhim sabeela **n**');
INSERT INTO `en_transliteration` VALUES (584, 'Satajidoona akhareena yureedoona an yamanookum wayamanoo qawmahum kulla ma ruddoo ila alfitnati orkisoo feeha fain lam ya\'tazilookum wayulqoo ilaykumu a **l** ssalama wayakuffoo aydiyahum fakhuthoohum wa **o** qtuloohum haythu thaqiftumoohum waolaikum ja\'ana lakum \'alayhim sultanan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (585, 'Wama kana limuminin an yaqtula muminan illa khataan waman qatala muminan khataan fatahreeru raqabatin muminatin wadiyatun musallamatun ila ahlihi illa an yassaddaqoo fain kana min qawmin \'aduwwin lakum wahuwa muminun fatahreeru raqabatin muminatin wain kana min qawmin baynakum wabaynahum meethaqun fadiyatun musallamatun ila ahlihi watahreeru raqabatin muminatin faman lam yajid fasiyamu shahrayni mutatabi\'ayni tawbatan mina Allahi wakana Allahu \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (586, 'Waman yaqtul muminan muta\'ammidan fajazaohu jahannamu khalidan feeha waghadiba Allahu \'alayhi wala\'anahu waa\'adda lahu \'athaban \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (587, 'Ya ayyuha allatheena amanoo ithadarabtum fee sabeeli Allahi fatabayyanoo wala taqooloo liman alqa ilaykumu a **l** ssalama lasta muminan tabtaghoona \'arada alhayati a **l** ddunya fa\'inda Allahi maghanimu katheeratun kathalika kuntum min qablu famanna Allahu \'alaykum fatabayyanoo inna Allaha kana bima ta\'maloona khabeera **n**');
INSERT INTO `en_transliteration` VALUES (588, 'La yastawee alqa\'idoona mina almumineena ghayru olee a **l** ddarari wa **a** lmujahidoona fee sabeeli Allahi biamwalihim waanfusihim faddala Allahu almujahideena biamwalihim waanfusihim \'ala alqa\'ideena darajatan wakullan wa\'ada Allahu alhusna wafaddala Allahu almujahideena \'ala alqa\'ideena ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (589, 'Darajatin minhu wamaghfiratan warahmatan wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (590, 'Inna allatheena tawaffahumu almalaikatu _th_ alimee anfusihim qaloo feema kuntum qaloo kunna mustad\'afeena fee alardi qaloo alam takun ardu Allahi wasi\'atan fatuhajiroo feeha faolaika mawahum jahannamu wasaat maseera **n**');
INSERT INTO `en_transliteration` VALUES (591, 'Illa almustad\'afeena mina a **l** rrijali wa **al** nnisai wa **a** lwildani la yastatee\'oona heelatan wala yahtadoona sabeela **n**');
INSERT INTO `en_transliteration` VALUES (592, 'Faolaika \'asa Allahu an ya\'fuwa \'anhum wakana Allahu \'afuwwan ghafoora **n**');
INSERT INTO `en_transliteration` VALUES (593, 'Waman yuhajir fee sabeeli Allahi yajid fee alardi muraghaman katheeran wasa\'atan waman yakhruj min baytihi muhajiran ila Allahi warasoolihi thumma yudrikhu almawtu faqad waqa\'a ajruhu \'ala Allahi wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (594, 'Waithadarabtum fee alardi falaysa \'alaykum junahun an taqsuroo mina a **l** ssalati in khiftum an yaftinakumu allatheena kafaroo inna alkafireena kanoo lakum \'aduwwan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (595, 'Waitha kunta feehim faaqamta lahumu a **l** ssalata fataqum taifatun minhum ma\'aka walyakhuthoo aslihatahum faitha sajadoo falyakoonoo min waraikum watati taifatun okhra lam yusalloo falyusalloo ma\'aka walyakhuthoo hithrahum waaslihatahum wadda allatheena kafaroo law taghfuloona \'an aslihatikum waamti\'atikum fayameeloona \'alaykum maylatan wahidatan wala junaha \'alaykum in kana bikum athan min matarin aw kuntum marda an tada\'oo aslihatakum wakhuthoo hithrakum inna Allaha a\'adda lilkafireena \'athaban muheena **n**');
INSERT INTO `en_transliteration` VALUES (596, 'Faitha qadaytumu a **l** ssalata fa **o** thkuroo Allaha qiyaman waqu\'oodan wa\'ala junoobikum faitha itmanantum faaqeemoo a **l** ssalata inna a **l** ssalata kanat \'ala almumineena kitaban mawqoota **n**');
INSERT INTO `en_transliteration` VALUES (597, 'Wala tahinoo fee ibtighai alqawmi in takoonoo talamoona fainnahum yalamoona kama talamoona watarjoona mina Allahi ma la yarjoona wakana Allahu \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (598, 'Inna anzana ilayka alkitaba bi **a** lhaqqi litahkuma bayna a **l** nnasi bima araka Allahu wala takun lilkhaineena khaseema **n**');
INSERT INTO `en_transliteration` VALUES (599, 'Wa **i** staghfiri Allaha inna Allaha kana ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (600, 'Wala tujadil \'ani allatheena yakhtanoona anfusahum inna Allaha la yuhibbu man kana khawwanan atheema **n**');
INSERT INTO `en_transliteration` VALUES (601, 'Yastakhfoona mina a **l** nnasi wala yastakhfoona mina Allahi wahuwa ma\'ahum ith yubayyitoona ma la yarda mina alqawli wakana Allahu bima ya\'maloona muheeta **n**');
INSERT INTO `en_transliteration` VALUES (602, 'Haantum haolai jadatum \'anhum fee alhayati a **l** ddunya faman yujadilu Allaha \'anhum yawma alqiyamati am man yakoonu \'alayhim wakeela **n**');
INSERT INTO `en_transliteration` VALUES (603, 'Waman ya\'mal sooan aw ya _th_ lim nafsahu thumma yastaghfiri Allaha yajidi Allaha ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (604, 'Waman yaksib ithman fainnama yaksibuhu \'ala nafsihi wakana Allahu \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (605, 'Waman yaksib khateeatan aw ithman thumma yarmi bihi bareean faqadi ihtamala buhtanan waithman mubeena **n**');
INSERT INTO `en_transliteration` VALUES (606, 'Walawla fadlu Allahi \'alayka warahmatuhu lahammat taifatun minhum an yudillooka wama yudilloona illa anfusahum wama yadurroonaka min shayin waanzala Allahu \'alayka alkitaba wa **a** lhikmata wa\'allamaka ma lam takun ta\'lamu wakana fadlu Allahi \'alayka \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (607, 'La khayra fee katheerin min najwahum illa man amara bisadaqatin aw ma\'roofin aw islahin bayna a **l** nnasi waman yaf\'al thalika ibtighaa mardati Allahi fasawfa nuteehi ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (608, 'Waman yushaqiqi a **l** rrasoola min ba\'di ma tabayyana lahu alhuda wayattabi\' ghayra sabeeli almumineena nuwallihi ma tawalla wanuslihi jahannama wasaat maseera **n**');
INSERT INTO `en_transliteration` VALUES (609, 'Inna Allaha la yaghfiru an yushraka bihi wayaghfiru ma doona thalika liman yashao waman yushrik bi **A** llahi faqad dalla dalalan ba\'eeda **n**');
INSERT INTO `en_transliteration` VALUES (610, 'In yad\'oona min doonihi illa inathan wain yad\'oona illa shaytanan mareeda **n**');
INSERT INTO `en_transliteration` VALUES (611, 'La\'anahu Allahu waqala laattakhithanna min \'ibadika naseeban mafrooda **n**');
INSERT INTO `en_transliteration` VALUES (612, 'Walaodillannahum walaomanniyannahum walaamurannahum falayubattikunna athana alan\'ami walaamurannahum falayughayyirunna khalqa Allahi waman yattakhithi a **l** shshaytana waliyyan min dooni Allahi faqad khasira khusranan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (613, 'Ya\'iduhum wayumanneehim wama ya\'iduhumu a **l** shshaytanu illa ghuroora **n**');
INSERT INTO `en_transliteration` VALUES (614, 'Olaika mawahum jahannamu wala yajidoona \'anha maheesa **n**');
INSERT INTO `en_transliteration` VALUES (615, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati sanudkhiluhum jannatin tajree min tahtiha alanharu khalideena feeha abadan wa\'da Allahi haqqan waman asdaqu mina Allahi qeela **n**');
INSERT INTO `en_transliteration` VALUES (616, 'Laysa biamaniyyikum wala amaniyyi ahli alkitabi man ya\'mal sooan yujza bihi wala yajid lahu min dooni Allahi waliyyan wala naseera **n**');
INSERT INTO `en_transliteration` VALUES (617, 'Waman ya\'mal mina a **l** ssalihati min thakarin aw ontha wahuwa muminun faolaika yadkhuloona aljannata wala yu _th_ lamoona naqeera **n**');
INSERT INTO `en_transliteration` VALUES (618, 'Waman ahsanu deenan mimman aslama wajhahu lillahi wahuwa muhsinun wa **i** ttaba\'a millata ibraheema haneefan wa **i** ttakhatha Allahu ibraheema khaleela **n**');
INSERT INTO `en_transliteration` VALUES (619, 'Walillahi ma fee a **l** ssamawati wama fee alardi wakana Allahu bikulli shayin muheeta **n**');
INSERT INTO `en_transliteration` VALUES (620, 'Wayastaftoonaka fee a **l** nnisai quli Allahu yufteekum feehinna wama yutla \'alaykum fee alkitabi fee yatama a **l** nnisai allatee la tutoonahunna ma kutiba lahunna watarghaboona an tankihoohunna wa **a** lmustad\'afeena mina alwildani waan taqoomoo lilyatama bi **a** lqisti wama taf\'aloo min khayrin fainna Allaha kana bihi \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (621, 'Waini imraatun khafat min ba\'liha nushoozan aw i\'radan fala junaha \'alayhima an yusliha baynahumasulhan wa **al** ssulhu khayrun waohdirati alanfusu a **l** shshuhha wain tuhsinoo watattaqoo fainna Allaha kana bima ta\'maloona khabeera **n**');
INSERT INTO `en_transliteration` VALUES (622, 'Walan tastatee\'oo an ta\'diloo bayna a **l** nnisai walaw harastum fala tameeloo kulla almayli fatatharooha ka **a** lmu\'allaqati wain tuslihoo watattaqoo fainna Allaha kana ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (623, 'Wain yatafarraqa yughni Allahu kullan min sa\'atihi wakana Allahu wasi\'an hakeema **n**');
INSERT INTO `en_transliteration` VALUES (624, 'Walillahi ma fee a **l** ssamawati wama fee alardi walaqad wassayna allatheena ootoo alkitaba min qablikum waiyyakum ani ittaqoo Allaha wain takfuroo fainna lillahi ma fee a **l** ssamawati wama fee alardi wakana Allahu ghaniyyan hameeda **n**');
INSERT INTO `en_transliteration` VALUES (625, 'Walillahi ma fee a **l** ssamawati wama fee alardi wakafa bi **A** llahi wakeela **n**');
INSERT INTO `en_transliteration` VALUES (626, 'In yasha yuthhibkum ayyuha a **l** nnasu wayati biakhareena wakana Allahu \'alathalika qadeera **n**');
INSERT INTO `en_transliteration` VALUES (627, 'Man kana yureedu thawaba a **l** ddunya fa\'inda Allahi thawabu a **l** ddunya wa **a** lakhirati wakana Allahu samee\'an baseera **n**');
INSERT INTO `en_transliteration` VALUES (628, 'Ya ayyuha allatheena amanoo koonoo qawwameena bi **a** lqisti shuhadaa lillahi walaw \'ala anfusikum awi alwalidayni wa **a** laqrabeena in yakun ghaniyyan aw faqeeran fa **A** llahu awla bihima fala tattabi\'oo alhawa an ta\'diloo wain talwoo aw tu\'ridoo fainna Allaha kana bima ta\'maloona khabeera **n**');
INSERT INTO `en_transliteration` VALUES (629, 'Ya ayyuha allatheena amanoo aminoo bi **A** llahi warasoolihi wa **a** lkitabi allathee nazzala \'ala rasoolihi wa **a** lkitabi allathee anzala min qablu waman yakfur bi **A** llahi wamalaikatihi wakutubihi warusulihi wa **a** lyawmi alakhiri faqad dalla dalalan ba\'eeda **n**');
INSERT INTO `en_transliteration` VALUES (630, 'Inna allatheena amanoo thumma kafaroo thumma amanoo thumma kafaroo thumma izdadoo kufran lam yakuni Allahu liyaghfira lahum wala liyahdiyahum sabeela **n**');
INSERT INTO `en_transliteration` VALUES (631, 'Bashshiri almunafiqeena bianna lahum \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (632, 'Allatheena yattakhithoona alkafireena awliyaa min dooni almumineena ayabtaghoona \'indahumu al\'izzata fainna al\'izzata lillahi jamee\'a **n**');
INSERT INTO `en_transliteration` VALUES (633, 'Waqad nazzala \'alaykum fee alkitabi an itha sami\'tum ayati Allahi yukfaru biha wayustahzao biha fala taq\'udoo ma\'ahum hatta yakhoodoo fee hadeethin ghayrihi innakum ithan mithluhum inna Allaha jami\'u almunafiqeena wa **a** lkafireena fee jahannama jamee\'a **n**');
INSERT INTO `en_transliteration` VALUES (634, 'Allatheena yatarabbasoona bikum fain kana lakum fathun mina Allahi qaloo alam nakun ma\'akum wain kana lilkafireena naseebun qaloo alam nastahwith \'alaykum wanamna\'kum mina almumineena fa **A** llahu yahkumu baynakum yawma alqiyamati walan yaj\'ala Allahu lilkafireena \'ala almumineena sabeela **n**');
INSERT INTO `en_transliteration` VALUES (635, 'Inna almunafiqeena yukhadi\'oona Allaha wahuwa khadi\'uhum waitha qamoo ila a **l** ssalati qamoo kusala yuraoona a **l** nnasa wala yathkuroona Allaha illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (636, 'Muthabthabeena bayna thalika la ila haolai wala ila haolai waman yudlili Allahu falan tajida lahu sabeela **n**');
INSERT INTO `en_transliteration` VALUES (637, 'Ya ayyuha allatheena amanoo la tattakhithoo alkafireena awliyaa min dooni almumineena atureedoona an taj\'aloo lillahi \'alaykum sultanan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (638, 'Inna almunafiqeena fee a **l** ddarki alasfali mina a **l** nnari walan tajida lahum naseera **n**');
INSERT INTO `en_transliteration` VALUES (639, 'Illa allatheena taboo waaslahoo wa **i** \'tasamoo bi **A** llahi waakhlasoo deenahum lillahi faolaika ma\'a almumineena wasawfa yuti Allahu almumineena ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (640, 'Ma yaf\'alu Allahu bi\'athabikum in shakartum waamantum wakana Allahu shakiran \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (641, 'La yuhibbu Allahu aljahra bi **al** ssooi mina alqawli illa man _th_ ulima wakana Allahu samee\'an \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (642, 'In tubdoo khayran aw tukhfoohu aw ta\'foo \'an sooin fainna Allaha kana \'afuwwan qadeera **n**');
INSERT INTO `en_transliteration` VALUES (643, 'Inna allatheena yakfuroona bi **A** llahi warusulihi wayureedoona an yufarriqoo bayna Allahi warusulihi wayaqooloona numinu biba\'din wanakfuru biba\'din wayureedoona an yattakhithoo bayna thalika sabeela **n**');
INSERT INTO `en_transliteration` VALUES (644, 'Olaika humu alkafiroona haqqan waa\'tadna lilkafireena \'athaban muheena **n**');
INSERT INTO `en_transliteration` VALUES (645, 'Wa **a** llatheena amanoo bi **A** llahi warusulihi walam yufarriqoo bayna ahadin minhum olaika sawfa yuteehim ojoorahum wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (646, 'Yasaluka ahlu alkitabi an tunazzila \'alayhim kitaban mina a **l** ssamai faqad saaloo moosa akbara min thalika faqaloo arina Allaha jahratan faakhathathumu a **l** ssa\'iqatu bi _th_ ulmihim thumma ittakhathoo al\'ijla min ba\'di ma jaathumu albayyinatu fa\'afawna \'an thalika waatayna moosa sultanan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (647, 'Warafa\'na fawqahumu a **l** ttoora bimeethaqihim waqulna lahumu odkhuloo albaba sujjadan waqulna lahum la ta\'doo fee a **l** ssabti waakhathna minhum meethaqan ghalee _th_ a **n**');
INSERT INTO `en_transliteration` VALUES (648, 'Fabima naqdihim meethaqahum wakufrihim biayati Allahi waqatlihimu alanbiyaa bighayri haqqin waqawlihim quloobuna ghulfun bal taba\'a Allahu \'alayha bikufrihim fala yuminoona illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (649, 'Wabikufrihim waqawlihim \'ala maryama buhtanan \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (650, 'Waqawlihim inna qatana almaseeha \'eesa ibna maryama rasoola Allahi wama qataloohu wamasalaboohu walakin shubbiha lahum wainna allatheena ikhtalafoo feehi lafee shakkin minhu ma lahum bihi min \'ilmin illa ittiba\'a a **l** _thth_ anni wama qataloohu yaqeena **n**');
INSERT INTO `en_transliteration` VALUES (651, 'Bal rafa\'ahu Allahu ilayhi wakana Allahu \'azeezan hakeema **n**');
INSERT INTO `en_transliteration` VALUES (652, 'Wain min ahli alkitabi illa layuminanna bihi qabla mawtihi wayawma alqiyamati yakoonu \'alayhim shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (653, 'Fabi _th_ ulmin mina allatheena hadoo harramna \'alayhim tayyibatin ohillat lahum wabisaddihim \'an sabeeli Allahi katheera **n**');
INSERT INTO `en_transliteration` VALUES (654, 'Waakhthihimu a **l** rriba waqad nuhoo \'anhu waaklihim amwala a **l** nnasi bi **a** lbatili waa\'tadna lilkafireena minhum \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (655, 'Lakini a **l** rrasikhoona fee al\'ilmi minhum wa **a** lmuminoona yuminoona bima onzila ilayka wama onzila min qablika wa **a** lmuqeemeena a **l** ssalata wa **a** lmutoona a **l** zzakata wa **a** lmuminoona bi **A** llahi wa **a** lyawmi alakhiri olaika sanuteehim ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (656, 'Inna awhayna ilayka kama awhayna ila noohin wa **al** nnabiyyeena min ba\'dihi waawhayna ila ibraheema waisma\'eela waishaqa waya\'qooba wa **a** lasbati wa\'eesa waayyooba wayoonusa waharoona wasulaymana waatayna dawooda zaboora **n**');
INSERT INTO `en_transliteration` VALUES (657, 'Warusulan qad qasasnahum \'alayka min qablu warusulan lam naqsushum \'alayka wakallama Allahu moosa takleema **n**');
INSERT INTO `en_transliteration` VALUES (658, 'Rusulan mubashshireena wamunthireena lialla yakoona li **l** nnasi \'ala Allahi hujjatun ba\'da a **l** rrusuli wakana Allahu \'azeezan hakeema **n**');
INSERT INTO `en_transliteration` VALUES (659, 'Lakini Allahu yashhadu bima anzala ilayka anzalahu bi\'ilmihi wa **a** lmalaikatu yashhadoona wakafa bi **A** llahi shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (660, 'Inna allatheena kafaroo wasaddoo \'an sabeeli Allahi qad dalloo dalalan ba\'eeda **n**');
INSERT INTO `en_transliteration` VALUES (661, 'Inna allatheena kafaroo wa _th_ alamoo lam yakuni Allahu liyaghfira lahum wala liyahdiyahum tareeqa **n**');
INSERT INTO `en_transliteration` VALUES (662, 'Illatareeqa jahannama khalideena feeha abadan wakana thalika \'ala Allahi yaseera **n**');
INSERT INTO `en_transliteration` VALUES (663, 'Ya ayyuha a **l** nnasu qad jaakumu a **l** rrasoolu bi **a** lhaqqi min rabbikum faaminoo khayran lakum wain takfuroo fainna lillahi ma fee a **l** ssamawati wa **a** lardi wakana Allahu \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (664, 'Ya ahla alkitabi la taghloo fee deenikum wala taqooloo \'ala Allahi illa alhaqqa innama almaseehu \'eesa ibnu maryama rasoolu Allahi wakalimatuhu alqaha ila maryama waroohun minhu faaminoo bi **A** llahi warusulihi wala taqooloo thalathatun intahoo khayran lakum innama Allahu ilahun wahidun subhanahu an yakoona lahu waladun lahu ma fee a **l** ssamawati wama fee alardi wakafa bi **A** llahi wakeela **n**');
INSERT INTO `en_transliteration` VALUES (665, 'Lan yastankifa almaseehu an yakoona \'abdan lillahi wala almalaikatu almuqarraboona waman yastankif \'an \'ibadatihi wayastakbir fasayahshuruhum ilayhi jamee\'a **n**');
INSERT INTO `en_transliteration` VALUES (666, 'Faamma allatheena amanoo wa\'amiloo a **l** ssalihati fayuwaffeehim ojoorahum wayazeeduhum min fadlihi waamma allatheena istankafoo wa **i** stakbaroo fayu\'aththibuhum \'athaban aleeman wala yajidoona lahum min dooni Allahi waliyyan wala naseera **n**');
INSERT INTO `en_transliteration` VALUES (667, 'Ya ayyuha a **l** nnasu qad jaakum burhanun min rabbikum waanzana ilaykum nooran mubeena **n**');
INSERT INTO `en_transliteration` VALUES (668, 'Faamma allatheena amanoo bi **A** llahi wa **i** \'tasamoo bihi fasayudkhiluhum fee rahmatin minhu wafadlin wayahdeehim ilayhi siratan mustaqeema **n**');
INSERT INTO `en_transliteration` VALUES (669, 'Yastaftoonaka quli Allahu yufteekum fee alkalalati ini imruon halaka laysa lahu waladun walahu okhtun falaha nisfu ma taraka wahuwa yarithuha in lam yakun laha waladun fain kanata ithnatayni falahuma a **l** ththuluthani mimma taraka wain kanoo ikhwatan rijalan wanisaan fali **l** ththakari mithlu ha _thth_ i alonthayayni yubayyinu Allahu lakum an tadilloo wa **A** llahu bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (670, 'Ya ayyuha allatheena amanoo awfoo bi **a** l\'uqoodi ohillat lakum baheematu alan\'ami illa ma yutla \'alaykum ghayra muhillee a **l** ssaydi waantum hurumun inna Allaha yahkumu ma yureed **u**');
INSERT INTO `en_transliteration` VALUES (671, 'Ya ayyuha allatheena amanoo la tuhilloo sha\'aira Allahi wala a **l** shshahra alharama wala alhadya wala alqalaida walaammeena albayta alharama yabtaghoona fadlan min rabbihim waridwanan waithahalatum fa **i** stadoo wala yajrimannakum shanaanu qawmin an saddookum \'ani almasjidi alharami an ta\'tadoo wata\'awanoo \'ala albirri wa **al** ttaqwa wala ta\'awanoo \'ala alithmi wa **a** l\'udwani wa **i** ttaqoo Allaha inna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (672, 'Hurrimat \'alaykumu almaytatu wa **al** ddamu walahmu alkhinzeeri wama ohilla lighayri Allahi bihi wa **a** lmunkhaniqatu wa **a** lmawqoothatu wa **a** lmutaraddiyatu wa **al** nnateehatu wama akala a **l** ssabu\'u illa ma thakkaytum wamathubiha \'ala a **l** nnusubi waan tastaqsimoo bi **a** lazlami thalikum fisqun alyawma yaisa allatheena kafaroo min deenikum fala takhshawhum wa **i** khshawni alyawma akmatu lakum deenakum waatmamtu \'alaykum ni\'matee waradeetu lakumu alislama deenan famani idturra fee makhmasatin ghayra mutajanifin liithmin fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (673, 'Yasaloonaka matha ohilla lahum qul ohilla lakumu a **l** ttayyibatu wama \'allamtum mina aljawarihi mukallibeena tu\'allimoonahunna mimma \'allamakumu Allahu fakuloo mimma amsakna \'alaykum wa **o** thkuroo isma Allahi \'alayhi wa **i** ttaqoo Allaha inna Allaha saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (674, 'Alyawma ohilla lakumu a **l** ttayyibatu wata\'amu allatheena ootoo alkitaba hillun lakum wata\'amukum hillun lahum wa **a** lmuhsanatu mina almuminati wa **a** lmuhsanatu mina allatheena ootoo alkitaba min qablikum ithaataytumoohunna ojoorahunna muhsineena ghayra musafiheena wala muttakhithee akhdanin waman yakfur bi **a** leemani faqad habita \'amaluhu wahuwa fee alakhirati mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (675, 'Ya ayyuha allatheena amanoo itha qumtum ila a **l** ssalati fa **i** ghsiloo wujoohakum waaydiyakum ila almarafiqi wa **i** msahoo biruoosikum waarjulakum ila alka\'bayni wain kuntum junuban fa **i** ttahharoo wain kuntum marda aw \'ala safarin aw jaa ahadun minkum mina alghaiti aw lamastumu a **l** nnisaa falam tajidoo maan fatayammamoo sa\'eedan tayyiban fa **i** msahoo biwujoohikum waaydeekum minhu ma yureedu Allahu liyaj\'ala \'alaykum min harajin walakin yureedu liyutahhirakum waliyutimma ni\'matahu \'alaykum la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (676, 'Wa **o** thkuroo ni\'mata Allahi \'alaykum wameethaqahu allathee wathaqakum bihi ith qultum sami\'na waata\'na wa **i** ttaqoo Allaha inna Allaha \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (677, 'Ya ayyuha allatheena amanoo koonoo qawwameena lillahi shuhadaa bi **a** lqisti wala yajrimannakum shanaanu qawmin \'ala alla ta\'diloo i\'diloo huwa aqrabu li **l** ttaqwa wa **i** ttaqoo Allaha inna Allaha khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (678, 'Wa\'ada Allahu allatheena amanoo wa\'amiloo a **l** ssalihati lahum maghfiratun waajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (679, 'Wa **a** llatheena kafaroo wakaththaboo biayatina olaika ashabu aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (680, 'Ya ayyuha allatheena amanoo othkuroo ni\'mata Allahi \'alaykum ith hamma qawmun an yabsutoo ilaykum aydiyahum fakaffa aydiyahum \'ankum wa **i** ttaqoo Allaha wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (681, 'Walaqad akhatha Allahu meethaqa banee israeela waba\'athna minhumu ithnay \'ashara naqeeban waqala Allahu innee ma\'akum lain aqamtumu a **l** ssalata waataytumu a **l** zzakata waamantum birusulee wa\'azzartumoohum waaqradtumu Allaha qardan hasanan laokaffiranna \'ankum sayyiatikum walaodkhilannakum jannatin tajree min tahtiha alanharu faman kafara ba\'da thalika minkum faqad dalla sawaa a **l** ssabeel **i**');
INSERT INTO `en_transliteration` VALUES (682, 'Fabima naqdihim meethaqahum la\'annahum waja\'ana quloobahum qasiyatan yuharrifoona alkalima \'an mawadi\'ihi wanasoo ha _thth_ an mimmathukkiroo bihi wala tazalu tattali\'u \'ala khainatin minhum illa qaleelan minhum fa **o** \'fu \'anhum wa **i** sfah inna Allaha yuhibbu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (683, 'Wamina allatheena qaloo inna nasara akhathna meethaqahum fanasoo ha _thth_ an mimmathukkiroo bihi faaghrayna baynahumu al\'adawata wa **a** lbaghdaa ila yawmi alqiyamati wasawfa yunabbiohumu Allahu bima kanoo yasna\'oon **a**');
INSERT INTO `en_transliteration` VALUES (684, 'Ya ahla alkitabi qad jaakum rasooluna yubayyinu lakum katheeran mimma kuntum tukhfoona mina alkitabi waya\'foo \'an katheerin qad jaakum mina Allahi noorun wakitabun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (685, 'Yahdee bihi Allahu mani ittaba\'a ridwanahu subula a **l** ssalami wayukhrijuhum mina a **l** _thth_ ulumati ila a **l** nnoori biithnihi wayahdeehim ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (686, 'Laqad kafara allatheena qaloo inna Allaha huwa almaseehu ibnu maryama qul faman yamliku mina Allahi shayan in arada an yuhlika almaseeha ibna maryama waommahu waman fee alardi jamee\'an walillahi mulku a **l** ssamawati wa **a** lardi wama baynahuma yakhluqu ma yashao wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (687, 'Waqalati alyahoodu wa **al** nnasara nahnu abnao Allahi waahibbaohu qul falima yu\'aththibukum bithunoobikum bal antum basharun mimman khalaqa yaghfiru liman yashao wayu\'aththibu man yashao walillahi mulku a **l** ssamawati wa **a** lardi wama baynahuma wailayhi almaseer **u**');
INSERT INTO `en_transliteration` VALUES (688, 'Ya ahla alkitabi qad jaakum rasooluna yubayyinu lakum \'ala fatratin mina a **l** rrusuli an taqooloo ma jaana min basheerin wala natheerin faqad jaakum basheerun wanatheerun wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (689, 'Waith qala moosa liqawmihi ya qawmi othkuroo ni\'mata Allahi \'alaykum ith ja\'ala feekum anbiyaa waja\'alakum mulookan waatakum ma lam yuti ahadan mina al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (690, 'Ya qawmi odkhuloo alarda almuqaddasata allatee kataba Allahu lakum wala tartaddoo \'ala adbarikum fatanqaliboo khasireen **a**');
INSERT INTO `en_transliteration` VALUES (691, 'Qaloo ya moosa inna feeha qawman jabbareena wainna lan nadkhulahahatta yakhrujoo minha fain yakhrujoo minha fainna dakhiloon **a**');
INSERT INTO `en_transliteration` VALUES (692, 'Qala rajulani mina allatheena yakhafoona an\'ama Allahu \'alayhima odkhuloo \'alayhimu albaba faitha dakhatumoohu fainnakum ghaliboona wa\'ala Allahi fatawakkaloo in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (693, 'Qaloo ya moosa inna lan nadkhulaha abadan ma damoo feeha faithhab anta warabbuka faqatila inna hahuna qa\'idoon **a**');
INSERT INTO `en_transliteration` VALUES (694, 'Qala rabbi innee la amliku illa nafsee waakhee fa **o** fruq baynana wabayna alqawmi alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (695, 'Qala fainnaha muharramatun \'alayhim arba\'eena sanatan yateehoona fee alardi fala tasa \'ala alqawmi alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (696, 'Wa **o** tlu \'alayhim nabaa ibnay adama bi **a** lhaqqi ith qarraba qurbanan fatuqubbila min ahadihima walam yutaqabbal mina alakhari qala laaqtulannaka qala innama yataqabbalu Allahu mina almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (697, 'Lain basatta ilayya yadaka litaqtulanee ma ana bibasitin yadiya ilayka liaqtulaka innee akhafu Allaha rabba al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (698, 'Innee oreedu an tabooa biithmee waithmika fatakoona min ashabi a **l** nnari wathalika jazao a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (699, 'Fatawwa\'at lahu nafsuhu qatla akheehi faqatalahu faasbaha mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (700, 'Faba\'atha Allahu ghuraban yabhathu fee alardi liyuriyahu kayfa yuwaree sawata akheehi qala ya waylata a\'ajaztu an akoona mithla hatha alghurabi faowariya sawata akhee faasbaha mina a **l** nnadimeen **a**');
INSERT INTO `en_transliteration` VALUES (701, 'Min ajli thalika katabna \'ala banee israeela annahu man qatala nafsan bighayri nafsin aw fasadin fee alardi fakaannama qatala a **l** nnasa jamee\'an waman ahyaha fakaannama ahya a **l** nnasa jamee\'an walaqad jaa thum rusuluna bi **a** lbayyinati thumma inna katheeran minhum ba\'da thalika fee alardi lamusrifoon **a**');
INSERT INTO `en_transliteration` VALUES (702, 'Innama jazao allatheena yuhariboona Allaha warasoolahu wayas\'awna fee alardi fasadan an yuqattaloo aw yusallaboo aw tuqatta\'a aydeehim waarjuluhum min khilafin aw yunfaw mina alardi thalika lahum khizyun fee a **l** ddunya walahum fee alakhirati \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (703, 'Illa allatheena taboo min qabli an taqdiroo \'alayhim fa **i** \'lamoo anna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (704, 'Ya ayyuha allatheena amanoo ittaqoo Allaha wa **i** btaghoo ilayhi alwaseelata wajahidoo fee sabeelihi la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (705, 'Inna allatheena kafaroo law anna lahum ma fee alardi jamee\'an wamithlahu ma\'ahu liyaftadoo bihi min \'athabi yawmi alqiyamati ma tuqubbila minhum walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (706, 'Yureedoona an yakhrujoo mina a **l** nnari wama hum bikharijeena minha walahum \'athabun muqeem **un**');
INSERT INTO `en_transliteration` VALUES (707, 'Wa **al** ssariqu wa **al** ssariqatu fa **i** qta\'oo aydiyahuma jazaan bima kasaba nakalan mina Allahi wa **A** llahu \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (708, 'Faman taba min ba\'di _th_ ulmihi waaslaha fainna Allaha yatoobu \'alayhi inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (709, 'Alam ta\'lam anna Allaha lahu mulku a **l** ssamawati wa **a** lardi yu\'aththibu man yashao wayaghfiru liman yashao wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (710, 'Ya ayyuha a **l** rrasoolu la yahzunka allatheena yusari\'oona fee alkufri mina allatheena qaloo amanna biafwahihim walam tumin quloobuhum wamina allatheena hadoo samma\'oona lilkathibi samma\'oona liqawmin akhareena lam yatooka yuharrifoona alkalima min ba\'di mawadi\'ihi yaqooloona in ooteetum hatha fakhuthoohu wain lam tutawhu fa **i** htharoo waman yuridi Allahu fitnatahu falan tamlika lahu mina Allahi shayan olaika allatheena lam yuridi Allahu an yutahhira quloobahum lahum fee a **l** ddunya khizyun walahum fee alakhirati \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (711, 'Samma\'oona lilkathibi akkaloona li **l** ssuhti fain jaooka fa **o** hkum baynahum aw a\'rid \'anhum wain tu\'rid \'anhum falan yadurrooka shayan wain hakamta fa **o** hkum baynahum bi **a** lqisti inna Allaha yuhibbu almuqsiteen **a**');
INSERT INTO `en_transliteration` VALUES (712, 'Wakayfa yuhakkimoonaka wa\'indahumu a **l** ttawratu feehahukmu Allahi thumma yatawallawna min ba\'di thalika wama olaika bi **a** lmumineen **a**');
INSERT INTO `en_transliteration` VALUES (713, 'Inna anzana a **l** ttawrata feeha hudan wanoorun yahkumu biha a **l** nnabiyyoona allatheena aslamoo lillatheena hadoo wa **al** rrabbaniyyoona wa **a** lahbaru bima istuhfi _th_ oo min kitabi Allahi wakanoo \'alayhi shuhadaa fala takhshawoo a **l** nnasa wa **i** khshawni wala tashtaroo biayatee thamanan qaleelan waman lam yahkum bima anzala Allahu faolaika humu alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (714, 'Wakatabna \'alayhim feeha anna a **l** nnafsa bi **al** nnafsi wa **a** l\'ayna bi **a** l\'ayni wa **a** lanfa bi **a** lanfi wa **a** lothuna bi **a** lothuni wa **al** ssinna bi **al** ssinni wa **a** ljurooha qisasun faman tasaddaqa bihi fahuwa kaffaratun lahu waman lam yahkum bima anzala Allahu faolaika humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (715, 'Waqaffayna \'alaatharihim bi\'eesa ibni maryama musaddiqan lima bayna yadayhi mina a **l** ttawrati waataynahu alinjeela feehi hudan wanoorun wamusaddiqan lima bayna yadayhi mina a **l** ttawrati wahudan wamaw\'i _th_ atan lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (716, 'Walyahkum ahlu alinjeeli bima anzala Allahu feehi waman lam yahkum bima anzala Allahu faolaika humu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (717, 'Waanzana ilayka alkitaba bi **a** lhaqqi musaddiqan lima bayna yadayhi mina alkitabi wamuhayminan \'alayhi fa **o** hkum baynahum bima anzala Allahu wala tattabi\' ahwaahum \'amma jaaka mina alhaqqi likullin ja\'ana minkum shir\'atan waminhajan walaw shaa Allahu laja\'alakum ommatan wahidatan walakin liyabluwakum fee maatakum fa **i** stabiqoo alkhayrati ila Allahi marji\'ukum jamee\'an fayunabbiokum bima kuntum feehi takhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (718, 'Waani ohkum baynahum bima anzala Allahu wala tattabi\' ahwaahum wa **i** htharhum an yaftinooka \'an ba\'di ma anzala Allahu ilayka fain tawallaw fa **i** \'lam annama yureedu Allahu an yuseebahum biba\'di thunoobihim wainna katheeran mina a **l** nnasi lafasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (719, 'Afahukma aljahiliyyati yabghoona waman ahsanu mina Allahu hukman liqawmin yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (720, 'Ya ayyuha allatheena amanoo la tattakhithoo alyahooda wa **al** nnasara awliyaa ba\'duhum awliyao ba\'din waman yatawallahum minkum fainnahu minhum inna Allaha la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (721, 'Fatara allatheena fee quloobihim maradun yusari\'oona feehim yaqooloona nakhsha an tuseebana dairatun fa\'asa Allahu an yatiya bi **a** lfathi aw amrin min \'indihi fayusbihoo \'ala ma asarroo fee anfusihim nadimeen **a**');
INSERT INTO `en_transliteration` VALUES (722, 'Wayaqoolu allatheena amanoo ahaolai allatheena aqsamoo bi **A** llahi jahda aymanihim innahum lama\'akum habitat a\'maluhum faasbahoo khasireen **a**');
INSERT INTO `en_transliteration` VALUES (723, 'Ya ayyuha allatheena amanoo man yartadda minkum \'an deenihi fasawfa yatee Allahu biqawmin yuhibbuhum wayuhibboonahu athillatin \'ala almumineena a\'izzatin \'ala alkafireena yujahidoona fee sabeeli Allahi wala yakhafoona lawmata laimin thalika fadlu Allahi yuteehi man yashao wa **A** llahu wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (724, 'Innama waliyyukumu Allahu warasooluhu wa **a** llatheena amanoo allatheena yuqeemoona a **l** ssalata wayutoona a **l** zzakata wahum raki\'oon **a**');
INSERT INTO `en_transliteration` VALUES (725, 'Waman yatawalla Allaha warasoolahu wa **a** llatheena amanoo fainna hizba Allahi humu alghaliboon **a**');
INSERT INTO `en_transliteration` VALUES (726, 'Ya ayyuha allatheena amanoo la tattakhithoo allatheena ittakhathoo deenakum huzuwan wala\'iban mina allatheena ootoo alkitaba min qablikum wa **a** lkuffara awliyaa wa **i** ttaqoo Allaha in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (727, 'Waitha nadaytum ila a **l** ssalati ittakhathooha huzuwan wala\'iban thalika biannahum qawmun la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (728, 'Qul ya ahla alkitabi hal tanqimoona minna illa an amanna bi **A** llahi wama onzila ilayna wama onzila min qablu waanna aktharakum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (729, 'Qul hal onabbiokum bisharrin min thalika mathoobatan \'inda Allahi man la\'anahu Allahu waghadiba \'alayhi waja\'ala minhumu alqiradata wa **a** lkhanazeera wa\'abada a **l** ttaghooti olaika sharrun makanan waadallu \'an sawai a **l** ssabeel **i**');
INSERT INTO `en_transliteration` VALUES (730, 'Waitha jaookum qaloo amanna waqad dakhaloo bi **a** lkufri wahum qad kharajoo bihi wa **A** llahu a\'lamu bima kanoo yaktumoon **a**');
INSERT INTO `en_transliteration` VALUES (731, 'Watara katheeran minhum yusari\'oona fee alithmi wa **a** l\'udwani waaklihimu a **l** ssuhta labisa ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (732, 'Lawla yanhahumu a **l** rrabbaniyyoona wa **a** lahbaru \'an qawlihimu alithma waaklihimu a **l** ssuhta labisa ma kanoo yasna\'oon **a**');
INSERT INTO `en_transliteration` VALUES (733, 'Waqalati alyahoodu yadu Allahi maghloolatun ghullat aydeehim walu\'inoo bima qaloo bal yadahu mabsootatani yunfiqu kayfa yashao walayazeedanna katheeran minhum ma onzila ilayka min rabbika tughyanan wakufran waalqayna baynahumu al\'adawata wa **a** lbaghdaa ila yawmi alqiyamati kullama awqadoo naran lilharbi atfaaha Allahu wayas\'awna fee alardi fasadan wa **A** llahu la yuhibbu almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (734, 'Walaw anna ahla alkitabi amanoo wa **i** ttaqaw lakaffarna \'anhum sayyiatihim walaadkhanahum jannati a **l** nna\'eem **i**');
INSERT INTO `en_transliteration` VALUES (735, 'Walaw annahum aqamoo a **l** ttawrata wa **a** linjeela wama onzila ilayhim min rabbihim laakaloo min fawqihim wamin tahti arjulihim minhum ommatun muqtasidatun wakatheerun minhum saa ma ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (736, 'Ya ayyuha a **l** rrasoolu balligh ma onzila ilayka min rabbika wain lam taf\'al fama ballaghta risalatahu wa **A** llahu ya\'simuka mina a **l** nnasi inna Allaha la yahdee alqawma alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (737, 'Qul ya ahla alkitabi lastum \'ala shayin hatta tuqeemoo a **l** ttawrata wa **a** linjeela wama onzila ilaykum min rabbikum walayazeedanna katheeran minhum ma onzila ilayka min rabbika tughyanan wakufran fala tasa \'ala alqawmi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (738, 'Inna allatheena amanoo wa **a** llatheena hadoo wa **al** ssabioona wa **al** nnasara man amana bi **A** llahi wa **a** lyawmi alakhiri wa\'amila salihan fala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (739, 'Laqad akhathna meethaqa banee israeela waarsana ilayhim rusulan kullama jaahum rasoolun bima la tahwa anfusuhum fareeqan kaththaboo wafareeqan yaqtuloon **a**');
INSERT INTO `en_transliteration` VALUES (740, 'Wahasiboo alla takoona fitnatun fa\'amoo wasammoo thumma taba Allahu \'alayhim thumma \'amoo wasammoo katheerun minhum wa **A** llahu baseerun bima ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (741, 'Laqad kafara allatheena qaloo inna Allaha huwa almaseehu ibnu maryama waqala almaseehu ya banee israeela o\'budoo Allaha rabbee warabbakum innahu man yushrik bi **A** llahi faqad harrama Allahu \'alayhi aljannata wamawahu a **l** nnaru wama li **l** _thth_ alimeena min ansar **in**');
INSERT INTO `en_transliteration` VALUES (742, 'Laqad kafara allatheena qaloo inna Allaha thalithu thalathatin wama min ilahin illa ilahun wahidun wain lam yantahoo \'amma yaqooloona layamassanna allatheena kafaroo minhum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (743, 'Afala yatooboona ila Allahi wayastaghfiroonahu wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (744, 'Ma almaseehu ibnu maryama illa rasoolun qad khalat min qablihi a **l** rrusulu waommuhu siddeeqatun kana yakulani a **l** tta\'ama on _th_ ur kayfa nubayyinu lahumu alayati thumma on _th_ ur anna yufakoon **a**');
INSERT INTO `en_transliteration` VALUES (745, 'Qul ata\'budoona min dooni Allahi ma la yamliku lakum darran wala naf\'an wa **A** llahu huwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (746, 'Qul ya ahla alkitabi la taghloo fee deenikum ghayra alhaqqi wala tattabi\'oo ahwaa qawmin qad dalloo min qablu waadalloo katheeran wadalloo \'an sawai a **l** ssabeel **i**');
INSERT INTO `en_transliteration` VALUES (747, 'Lu\'ina allatheena kafaroo min banee israeela \'ala lisani dawooda wa\'eesa ibni maryama thalika bima \'asaw wakanoo ya\'tadoon **a**');
INSERT INTO `en_transliteration` VALUES (748, 'Kanoo la yatanahawna \'an munkarin fa\'aloohu labisa ma kanoo yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (749, 'Tara katheeran minhum yatawallawna allatheena kafaroo labisa ma qaddamat lahum anfusuhum an sakhita Allahu \'alayhim wafee al\'athabi hum khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (750, 'Walaw kanoo yuminoona bi **A** llahi wa **al** nnabiyyi wama onzila ilayhi ma ittakhathoohum awliyaa walakinna katheeran minhum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (751, 'Latajidanna ashadda a **l** nnasi \'adawatan lillatheena amanoo alyahooda wa **a** llatheena ashrakoo walatajidanna aqrabahum mawaddatan lillatheena amanoo allatheena qaloo inna nasarathalika bianna minhum qisseeseena waruhbanan waannahum la yastakbiroon **a**');
INSERT INTO `en_transliteration` VALUES (752, 'Waitha sami\'oo ma onzila ila a **l** rrasooli tara a\'yunahum tafeedu mina a **l** ddam\'i mimma \'arafoo mina alhaqqi yaqooloona rabbanaamanna fa **o** ktubna ma\'a a **l** shshahideen **a**');
INSERT INTO `en_transliteration` VALUES (753, 'Wama lana la numinu bi **A** llahi wama jaana mina alhaqqi wanatma\'u an yudkhilana rabbuna ma\'a alqawmi a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (754, 'Faathabahumu Allahu bima qaloo jannatin tajree min tahtiha alanharu khalideena feeha wathalika jazao almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (755, 'Wa **a** llatheena kafaroo wakaththaboo biayatina olaika ashabu aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (756, 'Ya ayyuha allatheena amanoo la tuharrimoo tayyibati ma ahalla Allahu lakum wala ta\'tadoo inna Allaha la yuhibbu almu\'tadeen **a**');
INSERT INTO `en_transliteration` VALUES (757, 'Wakuloo mimma razaqakumu Allahu halalan tayyiban wa **i** ttaqoo Allaha allathee antum bihi muminoon **a**');
INSERT INTO `en_transliteration` VALUES (758, 'La yuakhithukumu Allahu bi **a** llaghwi fee aymanikum walakin yuakhithukum bima \'aqqadtumu alaymana fakaffaratuhu it\'amu \'asharati masakeena min awsati ma tut\'imoona ahleekum aw kiswatuhum aw tahreeru raqabatin faman lam yajid fasiyamu thalathati ayyamin thalika kaffaratu aymanikum ithahalaftum wa **i** hfa _th_ oo aymanakum kathalika yubayyinu Allahu lakum ayatihi la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (759, 'Ya ayyuha allatheena amanoo innama alkhamru wa **a** lmaysiru wa **a** lansabu wa **a** lazlamu rijsun min \'amali a **l** shshaytani fa **i** jtaniboohu la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (760, 'Innama yureedu a **l** shshaytanu an yooqi\'a baynakumu al\'adawata wa **a** lbaghdaa fee alkhamri wa **a** lmaysiri wayasuddakum \'an thikri Allahi wa\'ani a **l** ssalati fahal antum muntahoon **a**');
INSERT INTO `en_transliteration` VALUES (761, 'Waatee\'oo Allaha waatee\'oo a **l** rrasoola wa **i** htharoo fain tawallaytum fa **i** \'lamoo annama \'ala rasoolina albalaghu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (762, 'Laysa \'ala allatheena amanoo wa\'amiloo a **l** ssalihati junahun feemata\'imoo itha ma ittaqaw waamanoo wa\'amiloo a **l** ssalihati thumma ittaqaw waamanoo thumma ittaqaw waahsanoo wa **A** llahu yuhibbu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (763, 'Ya ayyuha allatheena amanoo layabluwannakumu Allahu bishayin mina a **l** ssaydi tanaluhu aydeekum warimahukum liya\'lama Allahu man yakhafuhu bi **a** lghaybi famani i\'tada ba\'da thalika falahu \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (764, 'Ya ayyuha allatheena amanoo la taqtuloo a **l** ssayda waantum hurumun waman qatalahu minkum muta\'ammidan fajazaon mithlu ma qatala mina a **l** nna\'ami yahkumu bihi thawa \'adlin minkum hadyan baligha alka\'bati aw kaffaratun ta\'amu masakeena aw \'adlu thalika siyaman liyathooqa wabala amrihi \'afa Allahu \'amma salafa waman \'ada fayantaqimu Allahu minhu wa **A** llahu \'azeezun thoo intiqam **in**');
INSERT INTO `en_transliteration` VALUES (765, 'Ohilla lakum saydu albahri wata\'amuhu mata\'an lakum wali **l** ssayyarati wahurrima \'alaykum saydu albarri ma dumtum huruman wa **i** ttaqoo Allaha allathee ilayhi tuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (766, 'Ja\'ala Allahu alka\'bata albayta alharama qiyaman li **l** nnasi wa **al** shshahra alharama wa **a** lhadya wa **a** lqalaida thalika lita\'lamoo anna Allaha ya\'lamu ma fee a **l** ssamawati wama fee alardi waanna Allaha bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (767, 'I\'lamoo anna Allaha shadeedu al\'iqabi waanna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (768, 'Ma \'ala a **l** rrasooli illa albalaghu wa **A** llahu ya\'lamu ma tubdoona wama taktumoon **a**');
INSERT INTO `en_transliteration` VALUES (769, 'Qul la yastawee alkhabeethu wa **al** ttayyibu walaw a\'jabaka kathratu alkhabeethi fa **i** ttaqoo Allaha ya olee alalbabi la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (770, 'Ya ayyuha allatheena amanoo la tasaloo \'an ashyaa in tubda lakum tasukum wain tasaloo \'anhaheena yunazzalu alquranu tubda lakum \'afa Allahu \'anha wa **A** llahu ghafoorun haleem **un**');
INSERT INTO `en_transliteration` VALUES (771, 'Qad saalaha qawmun min qablikum thumma asbahoo biha kafireen **a**');
INSERT INTO `en_transliteration` VALUES (772, 'Ma ja\'ala Allahu min baheeratin wala saibatin wala waseelatin walahamin walakinna allatheena kafaroo yaftaroona \'ala Allahi alkathiba waaktharuhum la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (773, 'Waitha qeela lahum ta\'alaw ila ma anzala Allahu waila a **l** rrasooli qaloo hasbuna ma wajadna \'alayhi abaana awalaw kana abaohum la ya\'lamoona shayan wala yahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (774, 'Ya ayyuha allatheena amanoo \'alaykum anfusakum la yadurrukum man dalla itha ihtadaytum ila Allahi marji\'ukum jamee\'an fayunabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (775, 'Ya ayyuha allatheena amanoo shahadatu baynikum ithahadara ahadakumu almawtu heena alwasiyyati ithnani thawa \'adlin minkum aw akharani min ghayrikum in antum darabtum fee alardi faasabatkum museebatu almawti tahbisoonahuma min ba\'di a **l** ssalati fayuqsimani bi **A** llahi ini irtabtum la nashtaree bihi thamanan walaw kana tha qurba wala naktumu shahadata Allahi inna ithan lamina alathimeen **a**');
INSERT INTO `en_transliteration` VALUES (776, 'Fain \'uthira \'ala annahuma istahaqqa ithman faakharani yaqoomani maqamahuma mina allatheena istahaqqa \'alayhimu alawlayani fayuqsimani bi **A** llahi lashahadatuna ahaqqu min shahadatihima wama i\'tadayna inna ithan lamina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (777, 'Thalika adna an yatoo bi **al** shshahadati \'ala wajhiha aw yakhafoo an turadda aymanun ba\'da aymanihim wa **i** ttaqoo Allaha wa **i** sma\'oo wa **A** llahu la yahdee alqawma alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (778, 'Yawma yajma\'u Allahu a **l** rrusula fayaqoolu matha ojibtum qaloo la \'ilma lana innaka anta \'allamu alghuyoob **i**');
INSERT INTO `en_transliteration` VALUES (779, 'Ith qala Allahu ya \'eesa ibna maryama othkur ni\'matee \'alayka wa\'ala walidatika ith ayyadtuka biroohi alqudusi tukallimu a **l** nnasa fee almahdi wakahlan waith \'allamtuka alkitaba wa **a** lhikmata wa **al** ttawrata wa **a** linjeela waith takhluqu mina a **l** tteeni kahayati a **l** ttayri biithnee fatanfukhu feeha fatakoonu tayran biithnee watubrio alakmaha wa **a** labrasa biithnee waith tukhriju almawta biithnee waith kafaftu banee israeela \'anka ith jitahum bi **a** lbayyinati faqala allatheena kafaroo minhum in hatha illa sihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (780, 'Waith awhaytu ila alhawariyyeena an aminoo bee wabirasoolee qaloo amanna wa **i** shhad biannana muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (781, 'Ith qala alhawariyyoona ya \'eesa ibna maryama hal yastatee\'u rabbuka an yunazzila \'alayna maidatan mina a **l** ssamai qala ittaqoo Allaha in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (782, 'Qaloo nureedu an nakula minha watatmainna quloobuna wana\'lama an qad sadaqtana wanakoona \'alayha mina a **l** shshahideen **a**');
INSERT INTO `en_transliteration` VALUES (783, 'Qala \'eesa ibnu maryama allahumma rabbana anzil \'alayna maidatan mina a **l** ssamai takoonu lana \'eedan liawwalina waakhirina waayatan minka wa **o** rzuqna waanta khayru a **l** rraziqeen **a**');
INSERT INTO `en_transliteration` VALUES (784, 'Qala Allahu innee munazziluha \'alaykum faman yakfur ba\'du minkum fainnee o\'aththibuhu \'athaban la o\'aththibuhu ahadan mina al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (785, 'Waith qala Allahu ya \'eesa ibna maryama aanta qulta li **l** nnasi ittakhithoonee waommiya ilahayni min dooni Allahi qala subhanaka ma yakoonu lee an aqoola ma laysa lee bihaqqin in kuntu qultuhu faqad \'alimtahu ta\'lamu ma fee nafsee wala a\'lamu ma fee nafsika innaka anta \'allamu alghuyoob **i**');
INSERT INTO `en_transliteration` VALUES (786, 'Ma qultu lahum illa ma amartanee bihi ani o\'budoo Allaha rabbee warabbakum wakuntu \'alayhim shaheedan ma dumtu feehim falamma tawaffaytanee kunta anta a **l** rraqeeba \'alayhim waanta \'ala kulli shayin shaheed **un**');
INSERT INTO `en_transliteration` VALUES (787, 'In tu\'aththibhum fainnahum \'ibaduka wain taghfir lahum fainnaka anta al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (788, 'Qala Allahu hatha yawmu yanfa\'u a **l** ssadiqeena sidquhum lahum jannatun tajree min tahtiha alanharu khalideena feeha abadan radiya Allahu \'anhum waradoo \'anhu thalika alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (789, 'Lillahi mulku a **l** ssamawati wa **a** lardi wama feehinna wahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (790, 'Alhamdu lillahi allathee khalaqa a **l** ssamawati wa **a** larda waja\'ala a **l** _thth_ ulumati wa **al** nnoora thumma allatheena kafaroo birabbihim ya\'diloon **a**');
INSERT INTO `en_transliteration` VALUES (791, 'Huwa allathee khalaqakum min teenin thumma qada ajalan waajalun musamman \'indahu thumma antum tamtaroon **a**');
INSERT INTO `en_transliteration` VALUES (792, 'Wahuwa Allahu fee a **l** ssamawati wafee alardi ya\'lamu sirrakum wajahrakum waya\'lamu ma taksiboon **a**');
INSERT INTO `en_transliteration` VALUES (793, 'Wama tateehim min ayatin min ayati rabbihim illa kanoo \'anha mu\'rideen **a**');
INSERT INTO `en_transliteration` VALUES (794, 'Faqad kaththaboo bi **a** lhaqqi lamma jaahum fasawfa yateehim anbao ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (795, 'Alam yaraw kam ahlakna min qablihim min qarnin makkannahum fee alardi ma lam numakkin lakum waarsana a **l** ssamaa \'alayhim midraran waja\'ana alanhara tajree min tahtihim faahlaknahum bithunoobihim waanshana min ba\'dihim qarnan akhareen **a**');
INSERT INTO `en_transliteration` VALUES (796, 'Walaw nazzana \'alayka kitaban fee qirtasin falamasoohu biaydeehim laqala allatheena kafaroo in hatha illa sihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (797, 'Waqaloo lawla onzila \'alayhi malakun walaw anzana malakan laqudiya alamru thumma la yun _th_ aroon **a**');
INSERT INTO `en_transliteration` VALUES (798, 'Walaw ja\'anahu malakan laja\'anahu rajulan walalabasna \'alayhim ma yalbisoon **a**');
INSERT INTO `en_transliteration` VALUES (799, 'Walaqadi istuhzia birusulin min qablika fahaqa bi **a** llatheena sakhiroo minhum ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (800, 'Qul seeroo fee alardi thumma on _th_ uroo kayfa kana \'aqibatu almukaththibeen **a**');
INSERT INTO `en_transliteration` VALUES (801, 'Qul liman ma fee a **l** ssamawati wa **a** lardi qul lillahi kataba \'ala nafsihi a **l** rrahmata layajma\'annakum ila yawmi alqiyamati la rayba feehi allatheena khasiroo anfusahum fahum la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (802, 'Walahu ma sakana fee allayli wa **al** nnahari wahuwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (803, 'Qul aghayra Allahi attakhithu waliyyan fatiri a **l** ssamawati wa **a** lardi wahuwa yut\'imu wala yut\'amu qul innee omirtu an akoona awwala man aslama wala takoonanna mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (804, 'Qul innee akhafu in \'asaytu rabbee \'athaba yawmin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (805, 'Man yusraf \'anhu yawmaithin faqad rahimahu wathalika alfawzu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (806, 'Wain yamsaska Allahu bidurrin fala kashifa lahu illa huwa wain yamsaska bikhayrin fahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (807, 'Wahuwa alqahiru fawqa \'ibadihi wahuwa alhakeemu alkhabeer **u**');
INSERT INTO `en_transliteration` VALUES (808, 'Qul ayyu shayin akbaru shahadatan quli Allahu shaheedun baynee wabaynakum waoohiya ilayya hatha alquranu lionthirakum bihi waman balagha ainnakum latashhadoona anna ma\'a Allahi alihatan okhra qul la ashhadu qul innama huwa ilahun wahidun wainnanee bareeon mimma tushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (809, 'Allatheena ataynahumu alkitaba ya\'rifoonahu kama ya\'rifoona abnaahum allatheena khasiroo anfusahum fahum la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (810, 'Waman a _th_ lamu mimmani iftara \'ala Allahi kathiban aw kaththaba biayatihi innahu la yuflihu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (811, 'Wayawma nahshuruhum jamee\'an thumma naqoolu lillatheena ashrakoo ayna shurakaokumu allatheena kuntum taz\'umoon **a**');
INSERT INTO `en_transliteration` VALUES (812, 'Thumma lam takun fitnatuhum illa an qaloo wa **A** llahi rabbina ma kunna mushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (813, 'On _th_ ur kayfa kathaboo \'ala anfusihim wadalla \'anhum ma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (814, 'Waminhum man yastami\'u ilayka waja\'ana \'ala quloobihim akinnatan an yafqahoohu wafee athanihim waqran wain yaraw kulla ayatin la yuminoo bihahatta itha jaooka yujadiloonaka yaqoolu allatheena kafaroo in hatha illa asateeru alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (815, 'Wahum yanhawna \'anhu wayanawna \'anhu wain yuhlikoona illa anfusahum wama yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (816, 'Walaw tara ith wuqifoo \'ala a **l** nnari faqaloo ya laytana nuraddu wala nukaththiba biayati rabbina wanakoona mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (817, 'Bal bada lahum ma kanoo yukhfoona min qablu walaw ruddoo la\'adoo lima nuhoo \'anhu wainnahum lakathiboon **a**');
INSERT INTO `en_transliteration` VALUES (818, 'Waqaloo in hiya illahayatuna a **l** ddunya wama nahnu bimab\'ootheen **a**');
INSERT INTO `en_transliteration` VALUES (819, 'Walaw tara ith wuqifoo \'ala rabbihim qala alaysa hatha bi **a** lhaqqi qaloo bala warabbina qala fathooqoo al\'athaba bima kuntum takfuroon **a**');
INSERT INTO `en_transliteration` VALUES (820, 'Qad khasira allatheena kaththaboo biliqai Allahi hatta itha jaathumu a **l** ssa\'atu baghtatan qaloo yahasratana \'ala ma farratna feeha wahum yahmiloona awzarahum \'ala _th_ uhoorihim ala saa ma yaziroon **a**');
INSERT INTO `en_transliteration` VALUES (821, 'Wama alhayatu a **l** ddunya illa la\'ibun walahwun wala **l** ddaru alakhirati khayrun lillatheena yattaqoona afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (822, 'Qad na\'lamu innahu layahzunuka allathee yaqooloona fainnahum la yukaththiboonaka walakinna a **l** _thth_ alimeena biayati Allahi yajhadoon **a**');
INSERT INTO `en_transliteration` VALUES (823, 'Walaqad kuththibat rusulun min qablika fasabaroo \'ala ma kuththiboo waoothoo hatta atahum nasruna wala mubaddila likalimati Allahi walaqad jaaka min nabai almursaleen **a**');
INSERT INTO `en_transliteration` VALUES (824, 'Wain kana kabura \'alayka i\'raduhum faini istata\'ta an tabtaghiya nafaqan fee alardi aw sullaman fee a **l** ssamai fatatiyahum biayatin walaw shaa Allahu lajama\'ahum \'ala alhuda fala takoonanna mina aljahileen **a**');
INSERT INTO `en_transliteration` VALUES (825, 'Innama yastajeebu allatheena yasma\'oona wa **a** lmawta yab\'athuhumu Allahu thumma ilayhi yurja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (826, 'Waqaloo lawla nuzzila \'alayhi ayatun min rabbihi qul inna Allaha qadirun \'ala an yunazzila ayatan walakinna aktharahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (827, 'Wama min dabbatin fee alardi walatairin yateeru bijanahayhi illa omamun amthalukum ma farratna fee alkitabi min shayin thumma ila rabbihim yuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (828, 'Wa **a** llatheena kaththaboo biayatinasummun wabukmun fee a **l** _thth_ ulumati man yashai Allahu yudlilhu waman yasha yaj\'alhu \'alasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (829, 'Qul araaytakum in atakum \'athabu Allahi aw atatkumu a **l** ssa\'atu aghayra Allahi tad\'oona in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (830, 'Bal iyyahu tad\'oona fayakshifu ma tad\'oona ilayhi in shaa watansawna ma tushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (831, 'Walaqad arsana ila omamin min qablika faakhathnahum bi **a** lbasai wa **al** ddarrai la\'allahum yatadarra\'oon **a**');
INSERT INTO `en_transliteration` VALUES (832, 'Falawla ith jaahum basuna tadarra\'oo walakin qasat quloobuhum wazayyana lahumu a **l** shshaytanu ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (833, 'Falamma nasoo mathukkiroo bihi fatahna \'alayhim abwaba kulli shayin hatta itha farihoo bima ootoo akhathnahum baghtatan faitha hum mublisoon **a**');
INSERT INTO `en_transliteration` VALUES (834, 'Faquti\'a dabiru alqawmi allatheena _th_ alamoo wa **a** lhamdu lillahi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (835, 'Qul araaytum in akhatha Allahu sam\'akum waabsarakum wakhatama \'ala quloobikum man ilahun ghayru Allahi yateekum bihi on _th_ ur kayfa nusarrifu alayati thumma hum yasdifoon **a**');
INSERT INTO `en_transliteration` VALUES (836, 'Qul araaytakum in atakum \'athabu Allahi baghtatan aw jahratan hal yuhlaku illa alqawmu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (837, 'Wama nursilu almursaleena illa mubashshireena wamunthireena faman amana waaslaha fala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (838, 'Wa **a** llatheena kaththaboo biayatina yamassuhumu al\'athabu bima kanoo yafsuqoon **a**');
INSERT INTO `en_transliteration` VALUES (839, 'Qul la aqoolu lakum \'indee khazainu Allahi wala a\'lamu alghayba wala aqoolu lakum innee malakun in attabi\'u illa ma yooha ilayya qul hal yastawee ala\'ma wa **a** lbaseeru afala tatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (840, 'Waanthir bihi allatheena yakhafoona an yuhsharoo ila rabbihim laysa lahum min doonihi waliyyun wala shafee\'un la\'allahum yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (841, 'Wala tatrudi allatheena yad\'oona rabbahum bi **a** lghadati wa **a** l\'ashiyyi yureedoona wajhahu ma \'alayka min hisabihim min shayin wama min hisabika \'alayhim min shayin fatatrudahum fatakoona mina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (842, 'Wakathalika fatanna ba\'dahum biba\'din liyaqooloo ahaolai manna Allahu \'alayhim min baynina alaysa Allahu bia\'lama bi **al** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (843, 'Waitha jaaka allatheena yuminoona biayatina faqul salamun \'alaykum kataba rabbukum \'ala nafsihi a **l** rrahmata annahu man \'amila minkum sooan bijahalatin thumma taba min ba\'dihi waaslaha faannahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (844, 'Wakathalika nufassilu alayati walitastabeena sabeelu almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (845, 'Qul innee nuheetu an a\'buda allatheena tad\'oona min dooni Allahi qul la attabi\'u ahwaakum qad dalatu ithan wama ana mina almuhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (846, 'Qul innee \'ala bayyinatin min rabbee wakaththabtum bihi ma \'indee ma tasta\'jiloona bihi ini alhukmu illa lillahi yaqussu alhaqqa wahuwa khayru alfasileen **a**');
INSERT INTO `en_transliteration` VALUES (847, 'Qul law anna \'indee ma tasta\'jiloona bihi laqudiya alamru baynee wabaynakum wa **A** llahu a\'lamu bi **al** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (848, 'Wa\'indahu mafatihu alghaybi la ya\'lamuha illa huwa waya\'lamu ma fee albarri wa **a** lbahri wama tasqutu min waraqatin illa ya\'lamuha walahabbatin fee _th_ ulumati alardi wala ratbin wala yabisin illa fee kitabin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (849, 'Wahuwa allathee yatawaffakum bi **a** llayli waya\'lamu ma jarahtum bi **al** nnahari thumma yab\'athukum feehi liyuqda ajalun musamman thumma ilayhi marji\'ukum thumma yunabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (850, 'Wahuwa alqahiru fawqa \'ibadihi wayursilu \'alaykum hafa _th_ atan hatta itha jaa ahadakumu almawtu tawaffathu rusuluna wahum la yufarritoon **a**');
INSERT INTO `en_transliteration` VALUES (851, 'Thumma ruddoo ila Allahi mawlahumu alhaqqi ala lahu alhukmu wahuwa asra\'u alhasibeen **a**');
INSERT INTO `en_transliteration` VALUES (852, 'Qul man yunajjeekum min _th_ ulumati albarri wa **a** lbahri tad\'oonahu tadarru\'an wakhufyatan lain anjana min hathihi lanakoonanna mina a **l** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (853, 'Quli Allahu yunajjeekum minha wamin kulli karbin thumma antum tushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (854, 'Qul huwa alqadiru \'ala an yab\'atha \'alaykum \'athaban min fawqikum aw min tahti arjulikum aw yalbisakum shiya\'an wayutheeqa ba\'dakum basa ba\'din on _th_ ur kayfa nusarrifu alayati la\'allahum yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (855, 'Wakaththaba bihi qawmuka wahuwa alhaqqu qul lastu \'alaykum biwakeel **in**');
INSERT INTO `en_transliteration` VALUES (856, 'Likulli nabain mustaqarrun wasawfa ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (857, 'Waitha raayta allatheena yakhoodoona fee ayatina faa\'rid \'anhum hatta yakhoodoo fee hadeethin ghayrihi waimma yunsiyannaka a **l** shshaytanu fala taq\'ud ba\'da a **l** ththikra ma\'a alqawmi a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (858, 'Wama \'ala allatheena yattaqoona min hisabihim min shayin walakin thikra la\'allahum yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (859, 'Wathari allatheena ittakhathoo deenahum la\'iban walahwan wagharrathumu alhayatu a **l** ddunya wathakkir bihi an tubsala nafsun bima kasabat laysa laha min dooni Allahi waliyyun wala shafee\'un wain ta\'dil kulla \'adlin la yukhath minha olaika allatheena obsiloo bima kasaboo lahum sharabun min hameemin wa\'athabun aleemun bima kanoo yakfuroon **a**');
INSERT INTO `en_transliteration` VALUES (860, 'Qul anad\'oo min dooni Allahi ma la yanfa\'una wala yadurruna wanuraddu \'ala a\'qabina ba\'da ith hadana Allahu ka **a** llatheeistahwathu a **l** shshayateenu fee alardi hayrana lahu ashabun yad\'oonahu ila alhuda itina qul inna huda Allahi huwa alhuda waomirna linuslima lirabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (861, 'Waan aqeemoo a **l** ssalata wa **i** ttaqoohu wahuwa allathee ilayhi tuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (862, 'Wahuwa allathee khalaqa a **l** ssamawati wa **a** larda bi **a** lhaqqi wayawma yaqoolu kun fayakoonu qawluhu alhaqqu walahu almulku yawma yunfakhu fee a **l** ssoori \'alimu alghaybi wa **al** shshahadati wahuwa alhakeemu alkhabeer **u**');
INSERT INTO `en_transliteration` VALUES (863, 'Waith qala ibraheemu liabeehi azara atattakhithu asnaman alihatan innee araka waqawmaka fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (864, 'Wakathalika nuree ibraheema malakoota a **l** ssamawati wa **a** lardi waliyakoona mina almooqineen **a**');
INSERT INTO `en_transliteration` VALUES (865, 'Falamma janna \'alayhi allaylu raa kawkaban qala hatha rabbee falamma afala qala la ohibbu alafileen **a**');
INSERT INTO `en_transliteration` VALUES (866, 'Falamma raa alqamara bazighan qala hatha rabbee falamma afala qala lain lam yahdinee rabbee laakoonanna mina alqawmi a **l** ddalleen **a**');
INSERT INTO `en_transliteration` VALUES (867, 'Falamma raa a **l** shshamsa bazighatan qala hatha rabbee hatha akbaru falamma afalat qala ya qawmi innee bareeon mimma tushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (868, 'Innee wajjahtu wajhiya lillathee fatara a **l** ssamawati wa **a** larda haneefan wama ana mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (869, 'Wahajjahu qawmuhu qala atuhajjoonnee fee Allahi waqad hadani wala akhafu ma tushrikoona bihi illa an yashaa rabbee shayan wasi\'a rabbee kulla shayin \'ilman afala tatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (870, 'Wakayfa akhafu ma ashraktum wala takhafoona annakum ashraktum bi **A** llahi ma lam yunazzil bihi \'alaykum sultanan faayyu alfareeqayni ahaqqu bi **a** lamni in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (871, 'Allatheena amanoo walam yalbisoo eemanahum bi _th_ ulmin olaika lahumu alamnu wahum muhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (872, 'Watilka hujjatunaataynaha ibraheema \'ala qawmihi narfa\'u darajatin man nashao inna rabbaka hakeemun \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (873, 'Wawahabna lahu ishaqa waya\'qooba kullan hadayna wanoohan hadayna min qablu wamin thurriyyatihi dawooda wasulaymana waayyooba wayoosufa wamoosa waharoona wakathalika najzee almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (874, 'Wazakariyya wayahya wa\'eesa wa **i** lyasa kullun mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (875, 'Waisma\'eela wa **i** lyasa\'a wayoonusa walootan wakullan faddana \'ala al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (876, 'Wamin abaihim wathurriyyatihim waikhwanihim wa **i** jtabaynahum wahadaynahum ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (877, 'Thalika huda Allahi yahdee bihi man yashao min \'ibadihi walaw ashrakoo lahabita \'anhum ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (878, 'Olaika allatheena ataynahumu alkitaba wa **a** lhukma wa **al** nnubuwwata fain yakfur biha haolai faqad wakkana biha qawman laysoo biha bikafireen **a**');
INSERT INTO `en_transliteration` VALUES (879, 'Olaika allatheena hada Allahu fabihudahumu iqtadih qul la asalukum \'alayhi ajran in huwa illathikra lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (880, 'Wama qadaroo Allaha haqqa qadrihi ith qaloo ma anzala Allahu \'ala basharin min shayin qul man anzala alkitaba allathee jaa bihi moosa nooran wahudan li **l** nnasi taj\'aloonahu qarateesa tubdoonaha watukhfoona katheeran wa\'ullimtum ma lam ta\'lamoo antum walaabaokum quli Allahu thumma tharhum fee khawdihim yal\'aboon **a**');
INSERT INTO `en_transliteration` VALUES (881, 'Wahatha kitabun anzanahu mubarakun musaddiqu allathee bayna yadayhi walitunthira omma alqura waman hawlaha wa **a** llatheena yuminoona bi **a** lakhirati yuminoona bihi wahum \'alasalatihim yuhafi _th_ oon **a**');
INSERT INTO `en_transliteration` VALUES (882, 'Waman a _th_ lamu mimmani iftara \'ala Allahi kathiban aw qala oohiya ilayya walam yooha ilayhi shayon waman qala saonzilu mithla ma anzala Allahu walaw tara ithi a **l** _thth_ alimoona fee ghamarati almawti wa **a** lmalaikatu basitoo aydeehim akhrijoo anfusakumu alyawma tujzawna \'athaba alhooni bima kuntum taqooloona \'ala Allahi ghayra alhaqqi wakuntum \'an ayatihi tastakbiroon **a**');
INSERT INTO `en_transliteration` VALUES (883, 'Walaqad jitumoona furada kama khalaqnakum awwala marratin wataraktum ma khawwanakum waraa _th_ uhoorikum wama nara ma\'akum shufa\'aakumu allatheena za\'amtum annahum feekum shurakao laqad taqatta\'a baynakum wadalla \'ankum ma kuntum taz\'umoon **a**');
INSERT INTO `en_transliteration` VALUES (884, 'Inna Allaha faliqu alhabbi wa **al** nnawa yukhriju alhayya mina almayyiti wamukhriju almayyiti mina alhayyi thalikumu Allahu faanna tufakoon **a**');
INSERT INTO `en_transliteration` VALUES (885, 'Faliqu alisbahi waja\'ala allayla sakanan wa **al** shshamsa wa **a** lqamara husbanan thalika taqdeeru al\'azeezi al\'aleem **i**');
INSERT INTO `en_transliteration` VALUES (886, 'Wahuwa allathee ja\'ala lakumu a **l** nnujooma litahtadoo biha fee _th_ ulumati albarri wa **a** lbahri qad fassana alayati liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (887, 'Wahuwa allathee anshaakum min nafsin wahidatin famustaqarrun wamustawda\'un qad fassana alayati liqawmin yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (888, 'Wahuwa allathee anzala mina a **l** ssamai maan faakhrajna bihi nabata kulli shayin faakhrajna minhu khadiran nukhriju minhu habban mutarakiban wamina a **l** nnakhli min tal\'iha qinwanun daniyatun wajannatin min a\'nabin wa **al** zzaytoona wa **al** rrummana mushtabihan waghayra mutashabihin on _th_ uroo ila thamarihi itha athmara wayan\'ihi inna fee thalikum laayatin liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (889, 'Waja\'aloo lillahi shurakaa aljinna wakhalaqahum wakharaqoo lahu baneena wabanatin bighayri \'ilmin subhanahu wata\'ala \'amma yasifoon **a**');
INSERT INTO `en_transliteration` VALUES (890, 'Badee\'u a **l** ssamawati wa **a** lardi anna yakoonu lahu waladun walam takun lahu sahibatun wakhalaqa kulla shayin wahuwa bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (891, 'Thalikumu Allahu rabbukum la ilaha illa huwa khaliqu kulli shayin fa **o** \'budoohu wahuwa \'ala kulli shayin wakeel **un**');
INSERT INTO `en_transliteration` VALUES (892, 'La tudrikuhu alabsaru wahuwa yudriku alabsara wahuwa allateefu alkhabeer **u**');
INSERT INTO `en_transliteration` VALUES (893, 'Qad jaakum basairu min rabbikum faman absara falinafsihi waman \'amiya fa\'alayha wama ana \'alaykum bihafee _th_ **in**');
INSERT INTO `en_transliteration` VALUES (894, 'Wakathalika nusarrifu alayati waliyaqooloo darasta walinubayyinahu liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (895, 'Ittabi\' ma oohiya ilayka min rabbika la ilaha illa huwa waa\'rid \'ani almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (896, 'Walaw shaa Allahu ma ashrakoo wama ja\'anaka \'alayhim hafee _th_ an wama anta \'alayhim biwakeel **in**');
INSERT INTO `en_transliteration` VALUES (897, 'Wala tasubboo allatheena yad\'oona min dooni Allahi fayasubboo Allaha \'adwan bighayri \'ilmin kathalika zayyanna likulli ommatin \'amalahum thumma ila rabbihim marji\'uhum fayunabbiohum bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (898, 'Waaqsamoo bi **A** llahi jahda aymanihim lain jaathum ayatun layuminunna biha qul innama alayatu \'inda Allahi wama yush\'irukum annaha itha jaat la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (899, 'Wanuqallibu afidatahum waabsarahum kama lam yuminoo bihi awwala marratin wanatharuhum fee tughyanihim ya\'mahoon **a**');
INSERT INTO `en_transliteration` VALUES (900, 'Walaw annana nazzana ilayhimu almalaikata wakallamahumu almawta wahasharna \'alayhim kulla shayin qubulan ma kanoo liyuminoo illa an yashaa Allahu walakinna aktharahum yajhaloon **a**');
INSERT INTO `en_transliteration` VALUES (901, 'Wakathalika ja\'ana likulli nabiyyin \'aduwwan shayateena alinsi wa **a** ljinni yoohee ba\'duhum ila ba\'din zukhrufa alqawli ghurooran walaw shaa rabbuka ma fa\'aloohu fatharhum wama yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (902, 'Walitasgha ilayhi afidatu allatheena la yuminoona bi **a** lakhirati waliyardawhu waliyaqtarifoo ma hum muqtarifoon **a**');
INSERT INTO `en_transliteration` VALUES (903, 'Afaghayra Allahi abtaghee hakaman wahuwa allathee anzala ilaykumu alkitaba mufassalan wa **a** llatheena ataynahumu alkitaba ya\'lamoona annahu munazzalun min rabbika bi **a** lhaqqi fala takoonanna mina almumtareen **a**');
INSERT INTO `en_transliteration` VALUES (904, 'Watammat kalimatu rabbika sidqan wa\'adlan la mubaddila likalimatihi wahuwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (905, 'Wain tuti\' akthara man fee alardi yudillooka \'an sabeeli Allahi in yattabi\'oona illa a **l** _thth_ anna wain hum illa yakhrusoon **a**');
INSERT INTO `en_transliteration` VALUES (906, 'Inna rabbaka huwa a\'lamu man yadillu \'an sabeelihi wahuwa a\'lamu bi **a** lmuhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (907, 'Fakuloo mimmathukira ismu Allahi \'alayhi in kuntum biayatihi mumineen **a**');
INSERT INTO `en_transliteration` VALUES (908, 'Wama lakum alla takuloo mimmathukira ismu Allahi \'alayhi waqad fassala lakum maharrama \'alaykum illa ma idturirtum ilayhi wainna katheeran layudilloona biahwaihim bighayri \'ilmin inna rabbaka huwa a\'lamu bi **a** lmu\'tadeen **a**');
INSERT INTO `en_transliteration` VALUES (909, 'Watharoo _th_ ahira alithmi wabatinahu inna allatheena yaksiboona alithma sayujzawna bima kanoo yaqtarifoon **a**');
INSERT INTO `en_transliteration` VALUES (910, 'Wala takuloo mimma lam yuthkari ismu Allahi \'alayhi wainnahu lafisqun wainna a **l** shshayateena layoohoona ila awliyaihim liyujadilookum wain ata\'tumoohum innakum lamushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (911, 'Awa man kana maytan faahyaynahu waja\'ana lahu nooran yamshee bihi fee a **l** nnasi kaman mathaluhu fee a **l** _thth_ ulumati laysa bikharijin minha kathalika zuyyina lilkafireena ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (912, 'Wakathalika ja\'ana fee kulli qaryatin akabira mujrimeeha liyamkuroo feeha wama yamkuroona illa bianfusihim wama yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (913, 'Waitha jaathum ayatun qaloo lan numina hatta nuta mithla ma ootiya rusulu Allahi Allahu a\'lamu haythu yaj\'alu risalatahu sayuseebu allatheena ajramoo sagharun \'inda Allahi wa\'athabun shadeedun bima kanoo yamkuroon **a**');
INSERT INTO `en_transliteration` VALUES (914, 'Faman yuridi Allahu an yahdiyahu yashrahsadrahu lilislami waman yurid an yudillahu yaj\'al sadrahu dayyiqan harajan kaannama yassa\'\'adu fee a **l** ssamai kathalika yaj\'alu Allahu a **l** rrijsa \'ala allatheena la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (915, 'Wahathasiratu rabbika mustaqeeman qad fassana alayati liqawmin yaththakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (916, 'Lahum daru a **l** ssalami \'inda rabbihim wahuwa waliyyuhum bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (917, 'Wayawma yahshuruhum jamee\'an ya ma\'shara aljinni qadi istakthartum mina alinsi waqala awliyaohum mina alinsi rabbana istamta\'a ba\'duna biba\'din wabalaghna ajalana allathee ajjata lana qala a **l** nnaru mathwakum khalideena feeha illa ma shaa Allahu inna rabbaka hakeemun \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (918, 'Wakathalika nuwallee ba\'da a **l** _thth_ alimeena ba\'dan bima kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (919, 'Ya ma\'shara aljinni wa **a** linsi alam yatikum rusulun minkum yaqussoona \'alaykum ayatee wayunthiroonakum liqaa yawmikum hatha qaloo shahidna \'ala anfusina wagharrathumu alhayatu a **l** ddunya washahidoo \'ala anfusihim annahum kanoo kafireen **a**');
INSERT INTO `en_transliteration` VALUES (920, 'Thalika an lam yakun rabbuka muhlika alqura bi _th_ ulmin waahluha ghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (921, 'Walikullin darajatun mimma \'amiloo wama rabbuka bighafilin \'amma ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (922, 'Warabbuka alghaniyyu thoo a **l** rrahmati in yasha yuthhibkum wayastakhlif min ba\'dikum ma yashao kama anshaakum min thurriyyati qawmin akhareen **a**');
INSERT INTO `en_transliteration` VALUES (923, 'Inna ma too\'adoona laatin wama antum bimu\'jizeen **a**');
INSERT INTO `en_transliteration` VALUES (924, 'Qul ya qawmi i\'maloo \'ala makanatikum innee \'amilun fasawfa ta\'lamoona man takoonu lahu \'aqibatu a **l** ddari innahu la yuflihu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (925, 'Waja\'aloo lillahi mimmatharaa mina alharthi wa **a** lan\'ami naseeban faqaloo hatha lillahi biza\'mihim wahatha lishurakaina fama kana lishurakaihim fala yasilu ila Allahi wama kana lillahi fahuwa yasilu ila shurakaihim saa ma yahkumoon **a**');
INSERT INTO `en_transliteration` VALUES (926, 'Wakathalika zayyana likatheerin mina almushrikeena qatla awladihim shurakaohum liyurdoohum waliyalbisoo \'alayhim deenahum walaw shaa Allahu ma fa\'aloohu fatharhum wama yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (927, 'Waqaloo hathihi an\'amun waharthun hijrun la yat\'amuha illa man nashao biza\'mihim waan\'amun hurrimat _th_ uhooruha waan\'amun la yathkuroona isma Allahi \'alayha iftiraan \'alayhi sayajzeehim bima kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (928, 'Waqaloo ma fee butooni hathihi alan\'ami khalisatun lithukoorina wamuharramun \'ala azwajina wain yakun maytatan fahum feehi shurakao sayajzeehim wasfahum innahu hakeemun \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (929, 'Qad khasira allatheena qataloo awladahum safahan bighayri \'ilmin waharramoo ma razaqahumu Allahu iftiraan \'ala Allahi qad dalloo wama kanoo muhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (930, 'Wahuwa allathee anshaa jannatin ma\'rooshatin waghayra ma\'rooshatin wa **al** nnakhla wa **al** zzar\'a mukhtalifan okuluhu wa **al** zzaytoona wa **al** rrummana mutashabihan waghayra mutashabihin kuloo min thamarihi itha athmara waatoo haqqahu yawma hasadihi wala tusrifoo innahu la yuhibbu almusrifeen **a**');
INSERT INTO `en_transliteration` VALUES (931, 'Wamina alan\'ami hamoolatan wafarshan kuloo mimma razaqakumu Allahu wala tattabi\'oo khutuwati a **l** shshaytani innahu lakum \'aduwwun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (932, 'Thamaniyata azwajin mina a **l** ddani ithnayni wamina alma\'zi ithnayni qul a **l** ththakarayni harrama ami alonthayayni amma ishtamalat \'alayhi arhamu alonthayayni nabbioonee bi\'ilmin in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (933, 'Wamina alibili ithnayni wamina albaqari ithnayni qul a **l** ththakarayni harrama ami alonthayayni amma ishtamalat \'alayhi arhamu alonthayayni am kuntum shuhadaa ith wassakumu Allahu bihatha faman a _th_ lamu mimmani iftara \'ala Allahi kathiban liyudilla a **l** nnasa bighayri \'ilmin inna Allaha la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (934, 'Qul la ajidu fee ma oohiya ilayya muharraman \'alata\'imin yat\'amuhu illa an yakoona maytatan aw daman masfoohan aw lahma khinzeerin fainnahu rijsun aw fisqan ohilla lighayri Allahi bihi famani idturra ghayra baghin wala \'adin fainna rabbaka ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (935, 'Wa\'ala allatheena hadoo harramna kulla thee _th_ ufurin wamina albaqari wa **a** lghanami harramna \'alayhim shuhoomahuma illa mahamalat _th_ uhooruhuma awi alhawaya aw ma ikhtalata bi\'a _th_ min thalika jazaynahum bibaghyihim wainna lasadiqoon **a**');
INSERT INTO `en_transliteration` VALUES (936, 'Fain kaththabooka faqul rabbukum thoo rahmatin wasi\'atin wala yuraddu basuhu \'ani alqawmi almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (937, 'Sayaqoolu allatheena ashrakoo law shaa Allahu ma ashrakna walaabaona walaharramna min shayin kathalika kaththaba allatheena min qablihim hattathaqoo basana qul hal \'indakum min \'ilmin fatukhrijoohu lana in tattabi\'oona illa a **l** _thth_ anna wain antum illa takhrusoon **a**');
INSERT INTO `en_transliteration` VALUES (938, 'Qul falillahi alhujjatu albalighatu falaw shaa lahadakum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (939, 'Qul halumma shuhadaakumu allatheena yashhadoona anna Allaha harrama hatha fain shahidoo fala tashhad ma\'ahum wala tattabi\' ahwaa allatheena kaththaboo biayatina wa **a** llatheena la yuminoona bi **a** lakhirati wahum birabbihim ya\'diloon **a**');
INSERT INTO `en_transliteration` VALUES (940, 'Qul ta\'alaw atlu maharrama rabbukum \'alaykum alla tushrikoo bihi shayan wabi **a** lwalidayni ihsanan wala taqtuloo awladakum min imlaqin nahnu narzuqukum waiyyahum wala taqraboo alfawahisha ma _th_ ahara minha wama batana wala taqtuloo a **l** nnafsa allatee harrama Allahu illa bi **a** lhaqqi thalikum wassakum bihi la\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (941, 'Wala taqraboo mala alyateemi illa bi **a** llatee hiya ahsanu hatta yablugha ashuddahu waawfoo alkayla wa **a** lmeezana bi **a** lqisti la nukallifu nafsan illa wus\'aha waitha qultum fa **i** \'diloo walaw kana tha qurba wabi\'ahdi Allahi awfoo thalikum wassakum bihi la\'allakum tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (942, 'Waanna hathasiratee mustaqeeman fa **i** ttabi\'oohu wala tattabi\'oo a **l** ssubula fatafarraqa bikum \'an sabeelihi thalikum wassakum bihi la\'allakum tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (943, 'Thumma atayna moosa alkitaba tamaman \'ala allathee ahsana watafseelan likulli shayin wahudan warahmatan la\'allahum biliqai rabbihim yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (944, 'Wahatha kitabun anzanahu mubarakun fa **i** ttabi\'oohu wa **i** ttaqoo la\'allakum turhamoon **a**');
INSERT INTO `en_transliteration` VALUES (945, 'An taqooloo innama onzila alkitabu \'alataifatayni min qablina wain kunna \'an dirasatihim laghafileen **a**');
INSERT INTO `en_transliteration` VALUES (946, 'Aw taqooloo law anna onzila \'alayna alkitabu lakunna ahda minhum faqad jaakum bayyinatun min rabbikum wahudan warahmatun faman a _th_ lamu mimman kaththaba biayati Allahi wasadafa \'anha sanajzee allatheena yasdifoona \'an ayatina sooa al\'athabi bima kanoo yasdifoon **a**');
INSERT INTO `en_transliteration` VALUES (947, 'Hal yan _th_ uroona illa an tatiyahumu almalaikatu aw yatiya rabbuka aw yatiya ba\'du ayati rabbika yawma yatee ba\'du ayati rabbika la yanfa\'u nafsan eemanuha lam takun amanat min qablu aw kasabat fee eemaniha khayran quli inta _th_ iroo inna munta _th_ iroon **a**');
INSERT INTO `en_transliteration` VALUES (948, 'Inna allatheena farraqoo deenahum wakanoo shiya\'an lasta minhum fee shayin innama amruhum ila Allahi thumma yunabbiohum bima kanoo yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (949, 'Man jaa bi **a** lhasanati falahu \'ashru amthaliha waman jaa bi **al** ssayyiati fala yujza illa mithlaha wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (950, 'Qul innanee hadanee rabbee ilasiratin mustaqeemin deenan qiyaman millata ibraheema haneefan wama kana mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (951, 'Qul inna salatee wanusukee wamahyaya wamamatee lillahi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (952, 'La shareeka lahu wabithalika omirtu waana awwalu almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (953, 'Qul aghayra Allahi abghee rabban wahuwa rabbu kulli shayin wala taksibu kullu nafsin illa \'alayha wala taziru waziratun wizra okhra thumma ila rabbikum marji\'ukum fayunabbiokum bima kuntum feehi takhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (954, 'Wahuwa allathee ja\'alakum khalaifa alardi warafa\'a ba\'dakum fawqa ba\'din darajatin liyabluwakum fee maatakum inna rabbaka saree\'u al\'iqabi wainnahu laghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (955, 'Aliflammeemsad');
INSERT INTO `en_transliteration` VALUES (956, 'Kitabun onzila ilayka fala yakun fee sadrika harajun minhu litunthira bihi wathikra lilmumineen **a**');
INSERT INTO `en_transliteration` VALUES (957, 'Ittabi\'oo ma onzila ilaykum min rabbikum wala tattabi\'oo min doonihi awliyaa qaleelan ma tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (958, 'Wakam min qaryatin ahlaknaha fajaaha basuna bayatan aw hum qailoon **a**');
INSERT INTO `en_transliteration` VALUES (959, 'Fama kana da\'wahum ith jaahum basuna illa an qaloo inna kunna _th_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (960, 'Falanasalanna allatheena orsila ilayhim walanasalanna almursaleen **a**');
INSERT INTO `en_transliteration` VALUES (961, 'Falanaqussanna \'alayhim bi\'ilmin wama kunna ghaibeen **a**');
INSERT INTO `en_transliteration` VALUES (962, 'Wa **a** lwaznu yawmaithini alhaqqu faman thaqulat mawazeenuhu faolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (963, 'Waman khaffat mawazeenuhu faolaika allatheena khasiroo anfusahum bima kanoo biayatina ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (964, 'Walaqad makkannakum fee alardi waja\'ana lakum feeha ma\'ayisha qaleelan ma tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (965, 'Walaqad khalaqnakum thumma sawwarnakum thumma qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa lam yakun mina a **l** ssajideen **a**');
INSERT INTO `en_transliteration` VALUES (966, 'Qala ma mana\'aka alla tasjuda ith amartuka qala ana khayrun minhu khalaqtanee min narin wakhalaqtahu min teen **in**');
INSERT INTO `en_transliteration` VALUES (967, 'Qala fa **i** hbit minha fama yakoonu laka an tatakabbara feeha fa **o** khruj innaka mina a **l** ssaghireen **a**');
INSERT INTO `en_transliteration` VALUES (968, 'Qala an _th_ irnee ila yawmi yub\'athoon **a**');
INSERT INTO `en_transliteration` VALUES (969, 'Qala innaka mina almun _th_ areen **a**');
INSERT INTO `en_transliteration` VALUES (970, 'Qala fabima aghwaytanee laaq\'udanna lahum sirataka almustaqeem **a**');
INSERT INTO `en_transliteration` VALUES (971, 'Thumma laatiyannahum min bayni aydeehim wamin khalfihim wa\'an aymanihim wa\'an shamailihim wala tajidu aktharahum shakireen **a**');
INSERT INTO `en_transliteration` VALUES (972, 'Qala okhruj minha mathooman madhooran laman tabi\'aka minhum laamlaanna jahannama minkum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (973, 'Wayaadamu oskun anta wazawjuka aljannata fakula min haythu shituma wala taqraba hathihi a **l** shshajarata fatakoona mina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (974, 'Fawaswasa lahuma a **l** shshaytanu liyubdiya lahuma ma wooriya \'anhuma min sawatihima waqala ma nahakuma rabbukuma \'an hathihi a **l** shshajarati illa an takoona malakayni aw takoona mina alkhalideen **a**');
INSERT INTO `en_transliteration` VALUES (975, 'Waqasamahuma innee lakuma lamina a **l** nnasiheen **a**');
INSERT INTO `en_transliteration` VALUES (976, 'Fadallahuma bighuroorin falammathaqa a **l** shshajarata badat lahuma sawatuhuma watafiqa yakhsifani \'alayhima min waraqi aljannati wanadahuma rabbuhuma alam anhakuma \'an tilkuma a **l** shshajarati waaqul lakuma inna a **l** shshaytana lakuma \'aduwwun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (977, 'Qala rabbana _th_ alamna anfusana wain lam taghfir lana watarhamna lanakoonanna mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (978, 'Qala ihbitoo ba\'dukum liba\'din \'aduwwun walakum fee alardi mustaqarrun wamata\'un ilaheen **in**');
INSERT INTO `en_transliteration` VALUES (979, 'Qala feeha tahyawna wafeeha tamootoona waminha tukhrajoon **a**');
INSERT INTO `en_transliteration` VALUES (980, 'Ya banee adama qad anzana \'alaykum libasan yuwaree sawatikum wareeshan walibasu a **l** ttaqwathalika khayrun thalika min ayati Allahi la\'allahum yaththakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (981, 'Ya banee adama la yaftinannakumu a **l** shshaytanu kama akhraja abawaykum mina aljannati yanzi\'u \'anhuma libasahuma liyuriyahuma sawatihima innahu yarakum huwa waqabeeluhu min haythu la tarawnahum inna ja\'ana a **l** shshayateena awliyaa lillatheena la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (982, 'Waitha fa\'aloo fahishatan qaloo wajadna \'alayhaabaana wa **A** llahu amarana biha qul inna Allaha la yamuru bi **a** lfahshai ataqooloona \'ala Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (983, 'Qul amara rabbee bi **a** lqisti waaqeemoo wujoohakum \'inda kulli masjidin wa **o** d\'oohu mukhliseena lahu a **l** ddeena kama badaakum ta\'oodoon **a**');
INSERT INTO `en_transliteration` VALUES (984, 'Fareeqan hada wafareeqan haqqa \'alayhimu a **l** ddalalatu innahumu ittakhathoo a **l** shshayateena awliyaa min dooni Allahi wayahsaboona annahum muhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (985, 'Ya banee adama khuthoo zeenatakum \'inda kulli masjidin wakuloo wa **i** shraboo wala tusrifoo innahu la yuhibbu almusrifeen **a**');
INSERT INTO `en_transliteration` VALUES (986, 'Qul man harrama zeenata Allahi allatee akhraja li\'ibadihi wa **al** ttayyibati mina a **l** rrizqi qul hiya lillatheena amanoo fee alhayati a **l** ddunya khalisatan yawma alqiyamati kathalika nufassilu alayati liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (987, 'Qul innamaharrama rabbiya alfawahisha ma _th_ ahara minha wama batana wa **a** lithma wa **a** lbaghya bighayri alhaqqi waan tushrikoo bi **A** llahi ma lam yunazzil bihi sultanan waan taqooloo \'ala Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (988, 'Walikulli ommatin ajalun faitha jaa ajaluhum la yastakhiroona sa\'atan wala yastaqdimoon **a**');
INSERT INTO `en_transliteration` VALUES (989, 'Ya banee adama imma yatiyannakum rusulun minkum yaqussoona \'alaykum ayatee famani ittaqa waaslaha fala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (990, 'Wa **a** llatheena kaththaboo biayatina wa **i** stakbaroo \'anha olaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (991, 'Faman a _th_ lamu mimmani iftara \'ala Allahi kathiban aw kaththaba biayatihi olaika yanaluhum naseebuhum mina alkitabi hatta itha jaathum rusuluna yatawaffawnahum qaloo ayna ma kuntum tad\'oona min dooni Allahi qaloo dalloo \'anna washahidoo \'ala anfusihim annahum kanoo kafireen **a**');
INSERT INTO `en_transliteration` VALUES (992, 'Qala odkhuloo fee omamin qad khalat min qablikum mina aljinni wa **a** linsi fee a **l** nnari kullama dakhalat ommatun la\'anat okhtahahatta itha iddarakoo feeha jamee\'an qalat okhrahum lioolahum rabbana haolai adalloona faatihim \'athaban di\'fan mina a **l** nnari qala likullin di\'fun walakin la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (993, 'Waqalat oolahum liokhrahum fama kana lakum \'alayna min fadlin fathooqoo al\'athaba bima kuntum taksiboon **a**');
INSERT INTO `en_transliteration` VALUES (994, 'Inna allatheena kaththaboo biayatina wa **i** stakbaroo \'anha la tufattahu lahum abwabu a **l** ssamai wala yadkhuloona aljannata hatta yalija aljamalu fee sammi alkhiyati wakathalika najzee almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (995, 'Lahum min jahannama mihadun wamin fawqihim ghawashin wakathalika najzee a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (996, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati la nukallifu nafsan illa wus\'aha olaika ashabu aljannati hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (997, 'Wanaza\'na ma fee sudoorihim min ghillin tajree min tahtihimu alanharu waqaloo alhamdu lillahi allathee hadana lihatha wama kunna linahtadiya lawla an hadana Allahu laqad jaat rusulu rabbina bi **a** lhaqqi wanoodoo an tilkumu aljannatu oorithtumooha bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (998, 'Wanada ashabu aljannati ashaba a **l** nnari an qad wajadna ma wa\'adana rabbunahaqqan fahal wajadtum ma wa\'ada rabbukum haqqan qaloo na\'am faaththana muaththinun baynahum an la\'natu Allahi \'ala a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (999, 'Allatheena yasuddoona \'an sabeeli Allahi wayabghoonaha \'iwajan wahum bi **a** lakhirati kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1000, 'Wabaynahumahijabun wa\'ala ala\'rafi rijalun ya\'rifoona kullan biseemahum wanadaw ashaba aljannati an salamun \'alaykum lam yadkhulooha wahum yatma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1001, 'Waithasurifat absaruhum tilqaa ashabi a **l** nnari qaloo rabbana la taj\'ana ma\'a alqawmi a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1002, 'Wanada ashabu ala\'rafi rijalan ya\'rifoonahum biseemahum qaloo ma aghna \'ankum jam\'ukum wama kuntum tastakbiroon **a**');
INSERT INTO `en_transliteration` VALUES (1003, 'Ahaolai allatheena aqsamtum la yanaluhumu Allahu birahmatin odkhuloo aljannata la khawfun \'alaykum wala antum tahzanoon **a**');
