INSERT INTO `en_transliteration` VALUES (1005, 'Allatheena ittakhathoo deenahum lahwan wala\'iban wagharrathumu alhayatu a **l** ddunya fa **a** lyawma nansahum kama nasoo liqaa yawmihim hatha wama kanoo biayatina yajhadoon **a**');
INSERT INTO `en_transliteration` VALUES (1006, 'Walaqad jinahum bikitabin fassanahu \'ala \'ilmin hudan warahmatan liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1007, 'Hal yan _th_ uroona illa taweelahu yawma yatee taweeluhu yaqoolu allatheena nasoohu min qablu qad jaat rusulu rabbina bi **a** lhaqqi fahal lana min shufa\'aa fayashfa\'oo lana aw nuraddu fana\'mala ghayra allathee kunna na\'malu qad khasiroo anfusahum wadalla \'anhum ma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (1008, 'Inna rabbakumu Allahu allathee khalaqa a **l** ssamawati wa **a** larda fee sittati ayyamin thumma istawa \'ala al\'arshi yughshee allayla a **l** nnahara yatlubuhu hatheethan wa **al** shshamsa wa **a** lqamara wa **al** nnujooma musakhkharatin biamrihi ala lahu alkhalqu wa **a** lamru tabaraka Allahu rabbu al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (1009, 'Od\'oo rabbakum tadarru\'an wakhufyatan innahu la yuhibbu almu\'tadeen **a**');
INSERT INTO `en_transliteration` VALUES (1010, 'Wala tufsidoo fee alardi ba\'da islahiha wa **o** d\'oohu khawfan watama\'an inna rahmata Allahi qareebun mina almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1011, 'Wahuwa allathee yursilu a **l** rriyaha bushran bayna yaday rahmatihi hatta itha aqallat sahaban thiqalan suqnahu libaladin mayyitin faanzana bihi almaa faakhrajna bihi min kulli a **l** ththamarati kathalika nukhriju almawta la\'allakum tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1012, 'Wa **a** lbaladu a **l** ttayyibu yakhruju nabatuhu biithni rabbihi wa **a** llathee khabutha la yakhruju illa nakidan kathalika nusarrifu alayati liqawmin yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1013, 'Laqad arsana noohan ila qawmihi faqala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu innee akhafu \'alaykum \'athaba yawmin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (1014, 'Qala almalao min qawmihi inna lanaraka fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1015, 'Qala ya qawmi laysa bee dalalatun walakinnee rasoolun min rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (1016, 'Oballighukum risalati rabbee waansahu lakum waa\'lamu mina Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1017, 'Awa\'ajibtum an jaakum thikrun min rabbikum \'ala rajulin minkum liyunthirakum walitattaqoo wala\'allakum turhamoon **a**');
INSERT INTO `en_transliteration` VALUES (1018, 'Fakaththaboohu faanjaynahu wa **a** llatheena ma\'ahu fee alfulki waaghraqna allatheena kaththaboo biayatina innahum kanoo qawman \'ameen **a**');
INSERT INTO `en_transliteration` VALUES (1019, 'Waila \'adin akhahum hoodan qala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu afala tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1020, 'Qala almalao allatheena kafaroo min qawmihi inna lanaraka fee safahatin wainna lana _th_ unnuka mina alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (1021, 'Qala ya qawmi laysa bee safahatun walakinnee rasoolun min rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (1022, 'Oballighukum risalati rabbee waana lakum nasihun ameen **un**');
INSERT INTO `en_transliteration` VALUES (1023, 'Awa\'ajibtum an jaakum thikrun min rabbikum \'ala rajulin minkum liyunthirakum wa **o** thkuroo ith ja\'alakum khulafaa min ba\'di qawmi noohin wazadakum fee alkhalqi bastatan fa **o** thkuroo alaa Allahi la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (1024, 'Qaloo ajitana lina\'buda Allaha wahdahu wanathara ma kana ya\'budu abaona fatina bima ta\'iduna in kunta mina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1025, 'Qala qad waqa\'a \'alaykum min rabbikum rijsun waghadabun atujadiloonanee fee asmain sammaytumooha antum waabaokum ma nazzala Allahu biha min sultanin fa **i** nta _th_ iroo innee ma\'akum mina almunta _th_ ireen **a**');
INSERT INTO `en_transliteration` VALUES (1026, 'Faanjaynahu wa **a** llatheena ma\'ahu birahmatin minna waqata\'na dabira allatheena kaththaboo biayatina wama kanoo mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1027, 'Waila thamooda akhahum salihan qala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu qad jaatkum bayyinatun min rabbikum hathihi naqatu Allahi lakum ayatan fatharooha takul fee ardi Allahi wala tamassooha bisooin fayakhuthakum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1028, 'Wa **o** thkuroo ith ja\'alakum khulafaa min ba\'di \'adin wabawwaakum fee alardi tattakhithoona min suhooliha qusooran watanhitoona aljibala buyootan fa **o** thkuroo alaa Allahi wala ta\'thaw fee alardi mufsideen **a**');
INSERT INTO `en_transliteration` VALUES (1029, 'Qala almalao allatheena istakbaroo min qawmihi lillatheena istud\'ifoo liman amana minhum ata\'lamoona anna salihan mursalun min rabbihi qaloo inna bima orsila bihi muminoon **a**');
INSERT INTO `en_transliteration` VALUES (1030, 'Qala allatheena istakbaroo inna bi **a** llathee amantum bihi kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1031, 'Fa\'aqaroo a **l** nnaqata wa\'ataw \'an amri rabbihim waqaloo yasalihu itina bima ta\'iduna in kunta mina almursaleen **a**');
INSERT INTO `en_transliteration` VALUES (1032, 'Faakhathathumu a **l** rrajfatu faasbahoo fee darihim jathimeen **a**');
INSERT INTO `en_transliteration` VALUES (1033, 'Fatawalla \'anhum waqala ya qawmi laqad ablaghtukum risalata rabbee wanasahtu lakum walakin la tuhibboona a **l** nnasiheen **a**');
