INSERT INTO `en_transliteration` VALUES (1035, 'Innakum latatoona a **l** rrijala shahwatan min dooni a **l** nnisai bal antum qawmun musrifoon **a**');
INSERT INTO `en_transliteration` VALUES (1036, 'Wama kana jawaba qawmihi illa an qaloo akhrijoohum min qaryatikum innahum onasun yatatahharoon **a**');
INSERT INTO `en_transliteration` VALUES (1037, 'Faanjaynahu waahlahu illa imraatahu kanat mina alghabireen **a**');
INSERT INTO `en_transliteration` VALUES (1038, 'Waamtarna \'alayhim mataran fa **o** n _th_ ur kayfa kana \'aqibatu almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1039, 'Waila madyana akhahum shu\'ayban qala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu qad jaatkum bayyinatun min rabbikum faawfoo alkayla wa **a** lmeezana wala tabkhasoo a **l** nnasa ashyaahum wala tufsidoo fee alardi ba\'da islahihathalikum khayrun lakum in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1040, 'Wala taq\'udoo bikulli siratin too\'idoona watasuddoona \'an sabeeli Allahi man amana bihi watabghoonaha \'iwajan wa **o** thkuroo ith kuntum qaleelan fakaththarakum wa **o** n _th_ uroo kayfa kana \'aqibatu almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (1041, 'Wain kana taifatun minkum amanoo bi **a** llathee orsiltu bihi wataifatun lam yuminoo fa **i** sbiroo hatta yahkuma Allahu baynana wahuwa khayru alhakimeen **a**');
INSERT INTO `en_transliteration` VALUES (1042, 'Qala almalao allatheena istakbaroo min qawmihi lanukhrijannaka ya shu\'aybu wa **a** llatheena amanoo ma\'aka min qaryatina aw lata\'oodunna fee millatina qala awalaw kunna kariheen **a**');
