INSERT INTO `en_transliteration` VALUES (1044, 'Waqala almalao allatheena kafaroo min qawmihi laini ittaba\'tum shu\'ayban innakum ithan lakhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (1045, 'Faakhathathumu a **l** rrajfatu faasbahoo fee darihim jathimeen **a**');
INSERT INTO `en_transliteration` VALUES (1046, 'Allatheena kaththaboo shu\'ayban kaan lam yaghnaw feeha allatheena kaththaboo shu\'ayban kanoo humu alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (1047, 'Fatawalla \'anhum waqala ya qawmi laqad ablaghtukum risalati rabbee wanasahtu lakum fakayfa asa \'ala qawmin kafireen **a**');
INSERT INTO `en_transliteration` VALUES (1048, 'Wama arsana fee qaryatin min nabiyyin illa akhathna ahlaha bi **a** lbasai wa **al** ddarrai la\'allahum yaddarra\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1049, 'Thumma baddana makana a **l** ssayyiati alhasanata hatta \'afaw waqaloo qad massa abaana a **l** ddarrao wa **al** ssarrao faakhathnahum baghtatan wahum la yash\'uroon **a**');
