INSERT INTO `en_transliteration` VALUES (1051, 'Afaamina ahlu alqura an yatiyahum basuna bayatan wahum naimoon **a**');
INSERT INTO `en_transliteration` VALUES (1052, 'Awa amina ahlu alqura an yatiyahum basunaduhan wahum yal\'aboon **a**');
INSERT INTO `en_transliteration` VALUES (1053, 'Afaaminoo makra Allahi fala yamanu makra Allahi illa alqawmu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (1054, 'Awalam yahdi lillatheena yarithoona alarda min ba\'di ahliha an law nashao asabnahum bithunoobihim wanatba\'u \'ala quloobihim fahum la yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1055, 'Tilka alqura naqussu \'alayka min anbaiha walaqad jaathum rusuluhum bi **a** lbayyinati fama kanoo liyuminoo bima kaththaboo min qablu kathalika yatba\'u Allahu \'ala quloobi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1056, 'Wama wajadna liaktharihim min \'ahdin wain wajadna aktharahum lafasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1057, 'Thumma ba\'athna min ba\'dihim moosa biayatina ila fir\'awna wamalaihi fa _th_ alamoo biha fa **o** nu _th_ ur kayfa kana \'aqibatu almufsideen **a**');
