INSERT INTO `en_transliteration` VALUES (1059, 'Haqeequn \'ala an la aqoola \'ala Allahi illa alhaqqa qad jitukum bibayyinatin min rabbikum faarsil ma\'iya banee israeel **a**');
INSERT INTO `en_transliteration` VALUES (1060, 'Qala in kunta jita biayatin fati biha in kunta mina a **l** ssadiqeen **a**');
