INSERT INTO `en_transliteration` VALUES (1082, 'Qala moosa liqawmihi ista\'eenoo bi **A** llahi wa **i** sbiroo inna alarda lillahi yoorithuha man yashao min \'ibadihi wa **a** l\'aqibatu lilmuttaqeen **a**');
