INSERT INTO `en_transliteration` VALUES (1087, 'Faarsana \'alayhimu a **l** ttoofana wa **a** ljarada wa **a** lqummala wa **al** ddafadi\'a wa **al** ddama ayatin mufassalatin fa **i** stakbaroo wakanoo qawman mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1088, 'Walamma waqa\'a \'alayhimu a **l** rrijzu qaloo ya moosa od\'u lana rabbaka bima \'ahida \'indaka lain kashafta \'anna a **l** rrijza lanuminanna laka walanursilanna ma\'aka banee israeel **a**');
INSERT INTO `en_transliteration` VALUES (1089, 'Falamma kashafna \'anhumu a **l** rrijza ila ajalin hum balighoohu itha hum yankuthoon **a**');
INSERT INTO `en_transliteration` VALUES (1090, 'Fa **i** ntaqamna minhum faaghraqnahum fee alyammi biannahum kaththaboo biayatina wakanoo \'anha ghafileen **a**');
INSERT INTO `en_transliteration` VALUES (1091, 'Waawrathna alqawma allatheena kanoo yustad\'afoona mashariqa alardi wamagharibaha allatee barakna feeha watammat kalimatu rabbika alhusna \'ala banee israeela bimasabaroo wadammarna ma kana yasna\'u fir\'awnu waqawmuhu wama kanoo ya\'rishoon **a**');
INSERT INTO `en_transliteration` VALUES (1092, 'Wajawazna bibanee israeela albahra faataw \'ala qawmin ya\'kufoona \'ala asnamin lahum qaloo ya moosa ij\'al lana ilahan kama lahum alihatun qala innakum qawmun tajhaloon **a**');
INSERT INTO `en_transliteration` VALUES (1093, 'Inna haolai mutabbarun ma hum feehi wabatilun ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1094, 'Qala aghayra Allahi abgheekum ilahan wahuwa faddalakum \'ala al\'alameen **a**');
