INSERT INTO `en_transliteration` VALUES (1105, 'Qala rabbi ighfir lee waliakhee waadkhilna fee rahmatika waanta arhamu a **l** rrahimeen **a**');
INSERT INTO `en_transliteration` VALUES (1106, 'Inna allatheena ittakhathoo al\'ijla sayanaluhum ghadabun min rabbihim wathillatun fee alhayati a **l** ddunya wakathalika najzee almuftareen **a**');
INSERT INTO `en_transliteration` VALUES (1107, 'Wa **a** llatheena \'amiloo a **l** ssayyiati thumma taboo min ba\'diha waamanoo inna rabbaka min ba\'diha laghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1108, 'Walamma sakata \'an moosa alghadabu akhatha alalwaha wafee nuskhatiha hudan warahmatun lillatheena hum lirabbihim yarhaboon **a**');
INSERT INTO `en_transliteration` VALUES (1109, 'Wa **i** khtara moosa qawmahu sab\'eena rajulan limeeqatina falamma akhathathumu a **l** rrajfatu qala rabbi law shita ahlaktahum min qablu waiyyaya atuhlikuna bima fa\'ala a **l** ssufahao minna in hiya illa fitnatuka tudillu biha man tashao watahdee man tashao anta waliyyuna fa **i** ghfir lana wa **i** rhamna waanta khayru alghafireen **a**');
INSERT INTO `en_transliteration` VALUES (1110, 'Wa **o** ktub lana fee hathihi a **l** ddunyahasanatan wafee alakhirati inna hudna ilayka qala \'athabee oseebu bihi man ashao warahmatee wasi\'at kulla shayin fasaaktubuha lillatheena yattaqoona wayutoona a **l** zzakata wa **a** llatheena hum biayatina yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1111, 'Allatheena yattabi\'oona a **l** rrasoola a **l** nnabiyya alommiyya allathee yajidoonahu maktooban \'indahum fee a **l** ttawrati wa **a** linjeeli yamuruhum bi **a** lma\'roofi wayanhahum \'ani almunkari wayuhillu lahumu a **l** ttayyibati wayuharrimu \'alayhimu alkhabaitha wayada\'u \'anhum israhum wa **a** laghlala allatee kanat \'alayhim fa **a** llatheena amanoo bihi wa\'azzaroohu wanasaroohu wa **i** ttaba\'oo a **l** nnoora allathee onzila ma\'ahu olaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (1112, 'Qul ya ayyuha a **l** nnasu innee rasoolu Allahi ilaykum jamee\'an allathee lahu mulku a **l** ssamawati wa **a** lardi la ilaha illa huwa yuhyee wayumeetu faaminoo bi **A** llahi warasoolihi a **l** nnabiyyi alommiyyi allathee yuminu bi **A** llahi wakalimatihi wa **i** ttabi\'oohu la\'allakum tahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (1113, 'Wamin qawmi moosa ommatun yahdoona bi **a** lhaqqi wabihi ya\'diloon **a**');
INSERT INTO `en_transliteration` VALUES (1114, 'Waqatta\'nahumu ithnatay \'ashrata asbatan omaman waawhayna ila moosa ithi istasqahu qawmuhu ani idrib bi\'asaka alhajara fa **i** nbajasat minhu ithnata \'ashrata \'aynan qad \'alima kullu onasin mashrabahum wa _th_ allana \'alayhimu alghamama waanzana \'alayhimu almanna wa **al** ssalwa kuloo min tayyibati ma razaqnakum wama _th_ alamoona walakin kanoo anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (1115, 'Waith qeela lahumu oskunoo hathihi alqaryata wakuloo minhahaythu shitum waqooloo hittatun wa **o** dkhuloo albaba sujjadan naghfir lakum khateeatikum sanazeedu almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1116, 'Fabaddala allatheena _th_ alamoo minhum qawlan ghayra allathee qeela lahum faarsana \'alayhim rijzan mina a **l** ssamai bima kanoo ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (1117, 'Wa **i** salhum \'ani alqaryati allatee kanat hadirata albahri ith ya\'doona fee a **l** ssabti ith tateehim heetanuhum yawma sabtihim shurra\'an wayawma la yasbitoona la tateehim kathalika nabloohum bima kanoo yafsuqoon **a**');
INSERT INTO `en_transliteration` VALUES (1118, 'Waith qalat ommatun minhum lima ta\'i _th_ oona qawman Allahu muhlikuhum aw mu\'aththibuhum \'athaban shadeedan qaloo ma\'thiratan ila rabbikum wala\'allahum yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1119, 'Falamma nasoo mathukkiroo bihi anjayna allatheena yanhawna \'ani a **l** ssooi waakhathna allatheena _th_ alamoo bi\'athabin baeesin bima kanoo yafsuqoon **a**');
INSERT INTO `en_transliteration` VALUES (1120, 'Falamma \'ataw \'an ma nuhoo \'anhu qulna lahum koonoo qiradatan khasieen **a**');
INSERT INTO `en_transliteration` VALUES (1121, 'Waith taaththana rabbuka layab\'athanna \'alayhim ila yawmi alqiyamati man yasoomuhum sooa al\'athabi inna rabbaka lasaree\'u al\'iqabi wainnahu laghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1122, 'Waqatta\'nahum fee alardi omaman minhumu a **l** ssalihoona waminhum doona thalika wabalawnahum bi **a** lhasanati wa **al** ssayyiati la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1123, 'Fakhalafa min ba\'dihim khalfun warithoo alkitaba yakhuthoona \'arada hatha aladna wayaqooloona sayughfaru lana wain yatihim \'aradun mithluhu yakhuthoohu alam yukhath \'alayhim meethaqu alkitabi an la yaqooloo \'ala Allahi illa alhaqqa wadarasoo ma feehi wa **al** ddaru alakhiratu khayrun lillatheena yattaqoona afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1124, 'Wa **a** llatheena yumassikoona bi **a** lkitabi waaqamoo a **l** ssalata inna la nudee\'u ajra almusliheen **a**');
INSERT INTO `en_transliteration` VALUES (1125, 'Waith nataqna aljabala fawqahum kaannahu _th_ ullatun wa _th_ annoo annahu waqi\'un bihim khuthoo maataynakum biquwwatin wa **o** thkuroo ma feehi la\'allakum tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1126, 'Waith akhatha rabbuka min banee adama min _th_ uhoorihim thurriyyatahum waashhadahum \'ala anfusihim alastu birabbikum qaloo bala shahidna an taqooloo yawma alqiyamati inna kunna \'an hatha ghafileen **a**');
INSERT INTO `en_transliteration` VALUES (1127, 'Aw taqooloo innama ashraka abaona min qablu wakunnathurriyyatan min ba\'dihim afatuhlikuna bima fa\'ala almubtiloon **a**');
INSERT INTO `en_transliteration` VALUES (1128, 'Wakathalika nufassilu alayati wala\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1129, 'Wa **o** tlu \'alayhim nabaa allathee ataynahu ayatina fa **i** nsalakha minha faatba\'ahu a **l** shshaytanu fakana mina alghaween **a**');
INSERT INTO `en_transliteration` VALUES (1130, 'Walaw shina larafa\'nahu biha walakinnahu akhlada ila alardi wa **i** ttaba\'a hawahu famathaluhu kamathali alkalbi in tahmil \'alayhi yalhath aw tatrukhu yalhath thalika mathalu alqawmi allatheena kaththaboo biayatina fa **o** qsusi alqasasa la\'allahum yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1131, 'Saa mathalan alqawmu allatheena kaththaboo biayatina waanfusahum kanoo ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (1132, 'Man yahdi Allahu fahuwa almuhtadee waman yudlil faolaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (1133, 'Walaqad tharana lijahannama katheeran mina aljinni wa **a** linsi lahum quloobun la yafqahoona biha walahum a\'yunun la yubsiroona biha walahum athanun la yasma\'oona biha olaika ka **a** lan\'ami bal hum adallu olaika humu alghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (1134, 'Walillahi alasmao alhusna fa **o** d\'oohu biha watharoo allatheena yulhidoona fee asmaihi sayujzawna ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1135, 'Wamimman khalaqna ommatun yahdoona bi **a** lhaqqi wabihi ya\'diloon **a**');
INSERT INTO `en_transliteration` VALUES (1136, 'Wa **a** llatheena kaththaboo biayatina sanastadrijuhum min haythu la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1137, 'Waomlee lahum inna kaydee mateen **un**');
INSERT INTO `en_transliteration` VALUES (1138, 'Awalam yatafakkaroo ma bisahibihim min jinnatin in huwa illa natheerun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1139, 'Awalam yan _th_ uroo fee malakooti a **l** ssamawati wa **a** lardi wama khalaqa Allahu min shayin waan \'asa an yakoona qadi iqtaraba ajaluhum fabiayyi hadeethin ba\'dahu yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1140, 'Man yudlili Allahu fala hadiya lahu wayatharuhum fee tughyanihim ya\'mahoon **a**');
INSERT INTO `en_transliteration` VALUES (1141, 'Yasaloonaka \'ani a **l** ssa\'ati ayyana mursaha qul innama \'ilmuha \'inda rabbee la yujalleeha liwaqtiha illa huwa thaqulat fee a **l** ssamawati wa **a** lardi la tateekum illa baghtatan yasaloonaka kaannaka hafiyyun \'anha qul innama \'ilmuha \'inda Allahi walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1142, 'Qul la amliku linafsee naf\'an waladarran illa ma shaa Allahu walaw kuntu a\'lamu alghayba la **i** stakthartu mina alkhayri wama massaniya a **l** ssooo in ana illa natheerun wabasheerun liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1143, 'Huwa allathee khalaqakum min nafsin wahidatin waja\'ala minha zawjaha liyaskuna ilayha falamma taghashshahahamalat hamlan khafeefan famarrat bihi falamma athqalat da\'awa Allaha rabbahuma lain ataytanasalihan lanakoonanna mina a **l** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (1144, 'Falammaatahumasalihan ja\'ala lahu shurakaa feemaatahuma fata\'ala Allahu \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (1145, 'Ayushrikoona ma la yakhluqu shayan wahum yukhlaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1146, 'Wala yastatee\'oona lahum nasran wala anfusahum yansuroon **a**');
INSERT INTO `en_transliteration` VALUES (1147, 'Wain tad\'oohum ila alhuda la yattabi\'ookum sawaon \'alaykum ada\'awtumoohum am antum samitoon **a**');
INSERT INTO `en_transliteration` VALUES (1148, 'Inna allatheena tad\'oona min dooni Allahi \'ibadun amthalukum fa **o** d\'oohum falyastajeeboo lakum in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1149, 'Alahum arjulun yamshoona biha am lahum aydin yabtishoona biha am lahum a\'yunun yubsiroona biha am lahum athanun yasma\'oona biha quli od\'oo shurakaakum thumma keedooni fala tun _th_ iroon **i**');
INSERT INTO `en_transliteration` VALUES (1150, 'Inna waliyyiya Allahu allathee nazzala alkitaba wahuwa yatawalla a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (1151, 'Wa **a** llatheena tad\'oona min doonihi la yastatee\'oona nasrakum wala anfusahum yansuroon **a**');
INSERT INTO `en_transliteration` VALUES (1152, 'Wain tad\'oohum ila alhuda la yasma\'oo watarahum yan _th_ uroona ilayka wahum la yubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (1153, 'Khuthi al\'afwa wamur bi **a** l\'urfi waa\'rid \'ani aljahileen **a**');
INSERT INTO `en_transliteration` VALUES (1154, 'Waimma yanzaghannaka mina a **l** shshaytani nazghun fa **i** sta\'ith bi **A** llahi innahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1155, 'Inna allatheena ittaqaw itha massahum taifun mina a **l** shshaytani tathakkaroo faitha hum mubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (1156, 'Waikhwanuhum yamuddoonahum fee alghayyi thumma la yuqsiroon **a**');
INSERT INTO `en_transliteration` VALUES (1157, 'Waitha lam tatihim biayatin qaloo lawla ijtabaytaha qul innama attabi\'u ma yooha ilayya min rabbee hatha basairu min rabbikum wahudan warahmatun liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1158, 'Waitha quria alquranu fa **i** stami\'oo lahu waansitoo la\'allakum turhamoon **a**');
INSERT INTO `en_transliteration` VALUES (1159, 'Wa **o** thkur rabbaka fee nafsika tadarru\'an wakheefatan wadoona aljahri mina alqawli bi **a** lghuduwwi wa **a** lasali wala takun mina alghafileen **a**');
INSERT INTO `en_transliteration` VALUES (1160, 'Inna allatheena \'inda rabbika la yastakbiroona \'an \'ibadatihi wayusabbihoonahu walahu yasjudoon **a**');
INSERT INTO `en_transliteration` VALUES (1161, 'Yasaloonaka \'ani alanfali quli alanfalu lillahi wa **al** rrasooli fa **i** ttaqoo Allaha waaslihoo thata baynikum waatee\'oo Allaha warasoolahu in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1162, 'Innama almuminoona allatheena ithathukira Allahu wajilat quloobuhum waitha tuliyat \'alayhim ayatuhu zadathum eemanan wa\'ala rabbihim yatawakkaloon **a**');
INSERT INTO `en_transliteration` VALUES (1163, 'Allatheena yuqeemoona a **l** ssalata wamimma razaqnahum yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (1164, 'Olaika humu almuminoona haqqan lahum darajatun \'inda rabbihim wamaghfiratun warizqun kareem **un**');
INSERT INTO `en_transliteration` VALUES (1165, 'Kama akhrajaka rabbuka min baytika bi **a** lhaqqi wainna fareeqan mina almumineena lakarihoon **a**');
INSERT INTO `en_transliteration` VALUES (1166, 'Yujadiloonaka fee alhaqqi ba\'dama tabayyana kaannama yusaqoona ila almawti wahum yan _th_ uroon **a**');
INSERT INTO `en_transliteration` VALUES (1167, 'Waith ya\'idukumu Allahu ihda a **l** ttaifatayni annaha lakum watawaddoona anna ghayra thati a **l** shshawkati takoonu lakum wayureedu Allahu an yuhiqqa alhaqqa bikalimatihi wayaqta\'a dabira alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1168, 'Liyuhiqqa alhaqqa wayubtila albatila walaw kariha almujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (1169, 'Ith tastagheethoona rabbakum fa **i** stajaba lakum annee mumiddukum bialfin mina almalaikati murdifeen **a**');
INSERT INTO `en_transliteration` VALUES (1170, 'Wama ja\'alahu Allahu illa bushra walitatmainna bihi quloobukum wama a **l** nnasru illa min \'indi Allahi inna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1171, 'Ith yughashsheekumu a **l** nnu\'asa amanatan minhu wayunazzilu \'alaykum mina a **l** ssamai maan liyutahhirakum bihi wayuthhiba \'ankum rijza a **l** shshaytani waliyarbita \'ala quloobikum wayuthabbita bihi alaqdam **a**');
INSERT INTO `en_transliteration` VALUES (1172, 'Ith yoohee rabbuka ila almalaikati annee ma\'akum fathabbitoo allatheena amanoo saolqee fee quloobi allatheena kafaroo a **l** rru\'ba fa **i** driboo fawqa ala\'naqi wa **i** driboo minhum kulla banan **in**');
INSERT INTO `en_transliteration` VALUES (1173, 'Thalika biannahum shaqqoo Allaha warasoolahu waman yushaqiqi Allaha warasoolahu fainna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (1174, 'Thalikum fathooqoohu waanna lilkafireena \'athaba a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (1175, 'Ya ayyuha allatheena amanoo itha laqeetumu allatheena kafaroo zahfan fala tuwalloohumu aladbar **a**');
INSERT INTO `en_transliteration` VALUES (1176, 'Waman yuwallihim yawmaithin duburahu illa mutaharrifan liqitalin aw mutahayyizan ila fiatin faqad baa bighadabin mina Allahi wamawahu jahannamu wabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (1177, 'Falam taqtuloohum walakinna Allaha qatalahum wama ramayta ith ramayta walakinna Allaha rama waliyubliya almumineena minhu balaan hasanan inna Allaha samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1178, 'Thalikum waanna Allaha moohinu kaydi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1179, 'In tastaftihoo faqad jaakumu alfathu wain tantahoo fahuwa khayrun lakum wain ta\'oodoo na\'ud walan tughniya \'ankum fiatukum shayan walaw kathurat waanna Allaha ma\'a almumineen **a**');
INSERT INTO `en_transliteration` VALUES (1180, 'Ya ayyuha allatheena amanoo atee\'oo Allaha warasoolahu wala tawallaw \'anhu waantum tasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1181, 'Wala takoonoo ka **a** llatheena qaloo sami\'na wahum la yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1182, 'Inna sharra a **l** ddawabbi \'inda Allahi a **l** ssummu albukmu allatheena la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1183, 'Walaw \'alima Allahu feehim khayran laasma\'ahum walaw asma\'ahum latawallaw wahum mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (1184, 'Ya ayyuha allatheena amanoo istajeeboo lillahi wali **l** rrasooli itha da\'akum lima yuhyeekum wa **i** \'lamoo anna Allaha yahoolu bayna almari waqalbihi waannahu ilayhi tuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (1185, 'Wa **i** ttaqoo fitnatan la tuseebanna allatheena _th_ alamoo minkum khassatan wa **i** \'lamoo anna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (1186, 'Wa **o** thkuroo ith antum qaleelun mustad\'afoona fee alardi takhafoona an yatakhattafakumu a **l** nnasu faawakum waayyadakum binasrihi warazaqakum mina a **l** ttayyibati la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1187, 'Ya ayyuha allatheena amanoo la takhoonoo Allaha wa **al** rrasoola watakhoonoo amanatikum waantum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1188, 'Wa **i** \'lamoo annama amwalukum waawladukum fitnatun waanna Allaha \'indahu ajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (1189, 'Ya ayyuha allatheena amanoo in tattaqoo Allaha yaj\'al lakum furqanan wayukaffir \'ankum sayyiatikum wayaghfir lakum wa **A** llahu thoo alfadli al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (1190, 'Waith yamkuru bika allatheena kafaroo liyuthbitooka aw yaqtulooka aw yukhrijooka wayamkuroona wayamkuru Allahu wa **A** llahu khayru almakireen **a**');
INSERT INTO `en_transliteration` VALUES (1191, 'Waitha tutla \'alayhim ayatuna qaloo qad sami\'na law nashao laqulna mithla hatha in hatha illa asateeru alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (1192, 'Waith qaloo allahumma in kana hatha huwa alhaqqa min \'indika faamtir \'alaynahijaratan mina a **l** ssamai awi itina bi\'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (1193, 'Wama kana Allahu liyu\'aththibahum waanta feehim wama kana Allahu mu\'aththibahum wahum yastaghfiroon **a**');
INSERT INTO `en_transliteration` VALUES (1194, 'Wama lahum alla yu\'aththibahumu Allahu wahum yasuddoona \'ani almasjidi alharami wama kanoo awliyaahu in awliyaohu illa almuttaqoona walakinna aktharahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1195, 'Wama kana salatuhum \'inda albayti illa mukaan watasdiyatan fathooqoo al\'athaba bima kuntum takfuroon **a**');
INSERT INTO `en_transliteration` VALUES (1196, 'Inna allatheena kafaroo yunfiqoona amwalahum liyasuddoo \'an sabeeli Allahi fasayunfiqoonaha thumma takoonu \'alayhim hasratan thumma yughlaboona wa **a** llatheena kafaroo ila jahannama yuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (1197, 'Liyameeza Allahu alkhabeetha mina a **l** ttayyibi wayaj\'ala alkhabeetha ba\'dahu \'ala ba\'din fayarkumahu jamee\'an fayaj\'alahu fee jahannama olaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (1198, 'Qul lillatheena kafaroo in yantahoo yughfar lahum ma qad salafa wain ya\'oodoo faqad madat sunnatu alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (1199, 'Waqatiloohum hatta la takoona fitnatun wayakoona a **l** ddeenu kulluhu lillahi faini intahaw fainna Allaha bima ya\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (1200, 'Wain tawallaw fa **i** \'lamoo anna Allaha mawlakum ni\'ma almawla wani\'ma a **l** nnaseer **u**');
INSERT INTO `en_transliteration` VALUES (1201, 'Wa **i** \'lamoo annama ghanimtum min shayin faanna lillahi khumusahu wali **l** rrasooli walithee alqurba wa **a** lyatama wa **a** lmasakeeni wa **i** bni a **l** ssabeeli in kuntum amantum bi **A** llahi wama anzana \'ala \'abdina yawma alfurqani yawma iltaqa aljam\'ani wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (1202, 'Ith antum bi **a** l\'udwati a **l** ddunya wahum bi **a** l\'udwati alquswa wa **al** rrakbu asfala minkum walaw tawa\'adtum la **i** khtalaftum fee almee\'adi walakin liyaqdiya Allahu amran kana maf\'oolan liyahlika man halaka \'an bayyinatin wayahya man hayya \'an bayyinatin wainna Allaha lasamee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1203, 'Ith yureekahumu Allahu fee manamika qaleelan walaw arakahum katheeran lafashiltum walatanaza\'tum fee alamri walakinna Allaha sallama innahu \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (1204, 'Waith yureekumoohum ithi iltaqaytum fee a\'yunikum qaleelan wayuqallilukum fee a\'yunihim liyaqdiya Allahu amran kana maf\'oolan waila Allahi turja\'u alomoor **u**');
INSERT INTO `en_transliteration` VALUES (1205, 'Ya ayyuha allatheena amanoo itha laqeetum fiatan fa **o** thbutoo wa **o** thkuroo Allaha katheeran la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (1206, 'Waatee\'oo Allaha warasoolahu wala tanaza\'oo fatafshaloo watathhaba reehukum wa **i** sbiroo inna Allaha ma\'a a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (1207, 'Wala takoonoo ka **a** llatheena kharajoo min diyarihim bataran wariaa a **l** nnasi wayasuddoona \'an sabeeli Allahi wa **A** llahu bima ya\'maloona muheet **un**');
INSERT INTO `en_transliteration` VALUES (1208, 'Waith zayyana lahumu a **l** shshaytanu a\'malahum waqala la ghaliba lakumu alyawma mina a **l** nnasi wainnee jarun lakum falamma taraati alfiatani nakasa \'ala \'aqibayhi waqala innee bareeon minkum innee ara ma la tarawna innee akhafu Allaha wa **A** llahu shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (1209, 'Ith yaqoolu almunafiqoona wa **a** llatheena fee quloobihim maradun gharra haolai deenuhum waman yatawakkal \'ala Allahi fainna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1210, 'Walaw tara ith yatawaffa allatheena kafaroo almalaikatu yadriboona wujoohahum waadbarahum wathooqoo \'athaba alhareeq **i**');
INSERT INTO `en_transliteration` VALUES (1211, 'Thalika bima qaddamat aydeekum waanna Allaha laysa bi _th_ allamin lil\'abeed **i**');
INSERT INTO `en_transliteration` VALUES (1212, 'Kadabi ali fir\'awna wa **a** llatheena min qablihim kafaroo biayati Allahi faakhathahumu Allahu bithunoobihim inna Allaha qawiyyun shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (1213, 'Thalika bianna Allaha lam yaku mughayyiran ni\'matan an\'amaha \'ala qawmin hatta yughayyiroo ma bianfusihim waanna Allaha samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1214, 'Kadabi ali fir\'awna wa **a** llatheena min qablihim kaththaboo biayati rabbihim faahlaknahum bithunoobihim waaghraqnaala fir\'awna wakullun kanoo _th_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1215, 'Inna sharra a **l** ddawabbi \'inda Allahi allatheena kafaroo fahum la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1216, 'Allatheena \'ahadta minhum thumma yanqudoona \'ahdahum fee kulli marratin wahum la yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1217, 'Faimma tathqafannahum fee alharbi fasharrid bihim man khalfahum la\'allahum yaththakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1218, 'Waimma takhafanna min qawmin khiyanatan fa **i** nbith ilayhim \'ala sawain inna Allaha la yuhibbu alkhaineen **a**');
INSERT INTO `en_transliteration` VALUES (1219, 'Wala yahsabanna allatheena kafaroo sabaqoo innahum la yu\'jizoon **a**');
INSERT INTO `en_transliteration` VALUES (1220, 'Waa\'iddoo lahum ma istata\'tum min quwwatin wamin ribati alkhayli turhiboona bihi \'aduwwa Allahi wa\'aduwwakum waakhareena min doonihim la ta\'lamoonahumu Allahu ya\'lamuhum wama tunfiqoo min shayin fee sabeeli Allahi yuwaffa ilaykum waantum la tu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1221, 'Wain janahoo li **l** ssalmi fa **i** jnah laha watawakkal \'ala Allahi innahu huwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (1222, 'Wain yureedoo an yakhda\'ooka fainna hasbaka Allahu huwa allathee ayyadaka binasrihi wabi **a** lmumineen **a**');
INSERT INTO `en_transliteration` VALUES (1223, 'Waallafa bayna quloobihim law anfaqta ma fee alardi jamee\'an ma allafta bayna quloobihim walakinna Allaha allafa baynahum innahu \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1224, 'Ya ayyuha a **l** nnabiyyu hasbuka Allahu wamani ittaba\'aka mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (1225, 'Ya ayyuha a **l** nnabiyyu harridi almumineena \'ala alqitali in yakun minkum \'ishroona sabiroona yaghliboo miatayni wain yakun minkum miatun yaghliboo alfan mina allatheena kafaroo biannahum qawmun la yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (1226, 'Alana khaffafa Allahu \'ankum wa\'alima anna feekum da\'fan fain yakun minkum miatun sabiratun yaghliboo miatayni wain yakun minkum alfun yaghliboo alfayni biithni Allahi wa **A** llahu ma\'a a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (1227, 'Ma kana linabiyyin an yakoona lahu asrahatta yuthkhina fee alardi tureedoona \'arada a **l** ddunya wa **A** llahu yureedu alakhirata wa **A** llahu \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1228, 'Lawla kitabun mina Allahi sabaqa lamassakum feema akhathtum \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (1229, 'Fakuloo mimma ghanimtum halalan tayyiban wa **i** ttaqoo Allaha inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1230, 'Ya ayyuha a **l** nnabiyyu qul liman fee aydeekum mina alasra in ya\'lami Allahu fee quloobikum khayran yutikum khayran mimma okhitha minkum wayaghfir lakum wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1231, 'Wain yureedoo khiyanataka faqad khanoo Allaha min qablu faamkana minhum wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1232, 'Inna allatheena amanoo wahajaroo wajahadoo biamwalihim waanfusihim fee sabeeli Allahi wa **a** llatheena awaw wanasaroo olaika ba\'duhum awliyao ba\'din wa **a** llatheena amanoo walam yuhajiroo ma lakum min walayatihim min shayin hatta yuhajiroo waini istansarookum fee a **l** ddeeni fa\'alaykumu a **l** nnasru illa \'ala qawmin baynakum wabaynahum meethaqun wa **A** llahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (1233, 'Wa **a** llatheena kafaroo ba\'duhum awliyao ba\'din illa taf\'aloohu takun fitnatun fee alardi wafasadun kabeer **un**');
INSERT INTO `en_transliteration` VALUES (1234, 'Wa **a** llatheena amanoo wahajaroo wajahadoo fee sabeeli Allahi wa **a** llatheena awaw wanasaroo olaika humu almuminoona haqqan lahum maghfiratun warizqun kareem **un**');
INSERT INTO `en_transliteration` VALUES (1235, 'Wa **a** llatheena amanoo min ba\'du wahajaroo wajahadoo ma\'akum faolaika minkum waoloo alarhami ba\'duhum awla biba\'din fee kitabi Allahi inna Allaha bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1236, 'Baraatun mina Allahi warasoolihi ila allatheena \'ahadtum mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (1237, 'Faseehoo fee alardi arba\'ata ashhurin wa **i** \'lamoo annakum ghayru mu\'jizee Allahi waanna Allaha mukhzee alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1238, 'Waathanun mina Allahi warasoolihi ila a **l** nnasi yawma alhajji alakbari anna Allaha bareeon mina almushrikeena warasooluhu fain tubtum fahuwa khayrun lakum wain tawallaytum fa **i** \'lamoo annakum ghayru mu\'jizee Allahi wabashshiri allatheena kafaroo bi\'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (1239, 'Illa allatheena \'ahadtum mina almushrikeena thumma lam yanqusookum shayan walam yu _th_ ahiroo \'alaykum ahadan faatimmoo ilayhim \'ahdahum ila muddatihim inna Allaha yuhibbu almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (1240, 'Faitha insalakha alashhuru alhurumu fa **o** qtuloo almushrikeena haythu wajadtumoohum wakhuthoohum wa **o** hsuroohum wa **o** q\'udoo lahum kulla marsadin fain taboo waaqamoo a **l** ssalata waatawoo a **l** zzakata fakhalloo sabeelahum inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1241, 'Wain ahadun mina almushrikeena istajaraka faajirhu hatta yasma\'a kalama Allahi thumma ablighhu mamanahu thalika biannahum qawmun la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1242, 'Kayfa yakoonu lilmushrikeena \'ahdun \'inda Allahi wa\'inda rasoolihi illa allatheena \'ahadtum \'inda almasjidi alharami fama istaqamoo lakum fa **i** staqeemoo lahum inna Allaha yuhibbu almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (1243, 'Kayfa wain ya _th_ haroo \'alaykum la yarquboo feekum illan walathimmatan yurdoonakum biafwahihim wataba quloobuhum waaktharuhum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (1244, 'Ishtaraw biayati Allahi thamanan qaleelan fasaddoo \'an sabeelihi innahum saa ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1245, 'La yarquboona fee muminin illan walathimmatan waolaika humu almu\'tadoon **a**');
INSERT INTO `en_transliteration` VALUES (1246, 'Fain taboo waaqamoo a **l** ssalata waatawoo a **l** zzakata faikhwanukum fee a **l** ddeeni wanufassilu alayati liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1247, 'Wain nakathoo aymanahum min ba\'di \'ahdihim wata\'anoo fee deenikum faqatiloo aimmata alkufri innahum la aymana lahum la\'allahum yantahoon **a**');
INSERT INTO `en_transliteration` VALUES (1248, 'Ala tuqatiloona qawman nakathoo aymanahum wahammoo biikhraji a **l** rrasooli wahum badaookum awwala marratin atakhshawnahum fa **A** llahu ahaqqu an takhshawhu in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1249, 'Qatiloohum yu\'aththibhumu Allahu biaydeekum wayukhzihim wayansurkum \'alayhim wayashfi sudoora qawmin mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1250, 'Wayuthhib ghay _th_ a quloobihim wayatoobu Allahu \'ala man yashao wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1251, 'Am hasibtum an tutrakoo walamma ya\'lami Allahu allatheena jahadoo minkum walam yattakhithoo min dooni Allahi wala rasoolihi wala almumineena waleejatan wa **A** llahu khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1252, 'Ma kana lilmushrikeena an ya\'muroo masajida Allahi shahideena \'ala anfusihim bi **a** lkufri olaika habitat a\'maluhum wafee a **l** nnari hum khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (1253, 'Innama ya\'muru masajida Allahi man amana bi **A** llahi wa **a** lyawmi alakhiri waaqama a **l** ssalata waata a **l** zzakata walam yakhsha illa Allaha fa\'asa olaika an yakoonoo mina almuhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (1254, 'Aja\'atum siqayata alhajji wa\'imarata almasjidi alharami kaman amana bi **A** llahi wa **a** lyawmi alakhiri wajahada fee sabeeli Allahi la yastawoona \'inda Allahi wa **A** llahu la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1255, 'Allatheena amanoo wahajaroo wajahadoo fee sabeeli Allahi biamwalihim waanfusihim a\' _th_ amu darajatan \'inda Allahi waolaika humu alfaizoon **a**');
INSERT INTO `en_transliteration` VALUES (1256, 'Yubashshiruhum rabbuhum birahmatin minhu waridwanin wajannatin lahum feeha na\'eemun muqeem **un**');
INSERT INTO `en_transliteration` VALUES (1257, 'Khalideena feeha abadan inna Allaha \'indahu ajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (1258, 'Ya ayyuha allatheena amanoo la tattakhithoo abaakum waikhwanakum awliyaa ini istahabboo alkufra \'ala aleemani waman yatawallahum minkum faolaika humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (1259, 'Qul in kana abaokum waabnaokum waikhwanukum waazwajukum wa\'asheeratukum waamwalun iqtaraftumooha watijaratun takhshawna kasadaha wamasakinu tardawnaha ahabba ilaykum mina Allahi warasoolihi wajihadin fee sabeelihi fatarabbasoo hatta yatiya Allahu biamrihi wa **A** llahu la yahdee alqawma alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1260, 'Laqad nasarakumu Allahu fee mawatina katheeratin wayawma hunaynin ith a\'jabatkum kathratukum falam tughni \'ankum shayan wadaqat \'alaykumu alardu bima rahubat thumma wallaytum mudbireen **a**');
INSERT INTO `en_transliteration` VALUES (1261, 'Thumma anzala Allahu sakeenatahu \'ala rasoolihi wa\'ala almumineena waanzala junoodan lam tarawha wa\'aththaba allatheena kafaroo wathalika jazao alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1262, 'Thumma yatoobu Allahu min ba\'di thalika \'ala man yashao wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1263, 'Ya ayyuha allatheena amanoo innama almushrikoona najasun fala yaqraboo almasjida alharama ba\'da \'amihim hatha wain khiftum \'aylatan fasawfa yughneekumu Allahu min fadlihi in shaa inna Allaha \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1264, 'Qatiloo allatheena la yuminoona bi **A** llahi wala bi **a** lyawmi alakhiri wala yuharrimoona maharrama Allahu warasooluhu wala yadeenoona deena alhaqqi mina allatheena ootoo alkitaba hatta yu\'too aljizyata \'an yadin wahum saghiroon **a**');
INSERT INTO `en_transliteration` VALUES (1265, 'Waqalati alyahoodu \'uzayrun ibnu Allahi waqalati a **l** nnasara almaseehu ibnu Allahi thalika qawluhum biafwahihim yudahioona qawla allatheena kafaroo min qablu qatalahumu Allahu anna yufakoon **a**');
INSERT INTO `en_transliteration` VALUES (1266, 'Ittakhathoo ahbarahum waruhbanahum arbaban min dooni Allahi wa **a** lmaseeha ibna maryama wama omiroo illa liya\'budoo ilahan wahidan la ilaha illa huwa subhanahu \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (1267, 'Yureedoona an yutfioo noora Allahi biafwahihim wayaba Allahu illa an yutimma noorahu walaw kariha alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1268, 'Huwa allathee arsala rasoolahu bi **a** lhuda wadeeni alhaqqi liyu _th_ hirahu \'ala a **l** ddeeni kullihi walaw kariha almushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (1269, 'Ya ayyuha allatheena amanoo inna katheeran mina alahbari wa **al** rruhbani layakuloona amwala a **l** nnasi bi **a** lbatili wayasuddoona \'an sabeeli Allahi wa **a** llatheena yaknizoona a **l** ththahaba wa **a** lfiddata wala yunfiqoonaha fee sabeeli Allahi fabashshirhum bi\'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (1270, 'Yawma yuhma \'alayha fee nari jahannama fatukwa biha jibahuhum wajunoobuhum wa _th_ uhooruhum hatha ma kanaztum lianfusikum fathooqoo ma kuntum taknizoon **a**');
INSERT INTO `en_transliteration` VALUES (1271, 'Inna \'iddata a **l** shshuhoori \'inda Allahi ithna \'ashara shahran fee kitabi Allahi yawma khalaqa a **l** ssamawati wa **a** larda minha arba\'atun hurumun thalika a **l** ddeenu alqayyimu fala ta _th_ limoo feehinna anfusakum waqatiloo almushrikeena kaffatan kama yuqatiloonakum kaffatan wa **i** \'lamoo anna Allaha ma\'a almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (1272, 'Innama a **l** nnaseeo ziyadatun fee alkufri yudallu bihi allatheena kafaroo yuhilloonahu \'aman wayuharrimoonahu \'aman liyuwatioo \'iddata maharrama Allahu fayuhilloo maharrama Allahu zuyyina lahum sooo a\'malihim wa **A** llahu la yahdee alqawma alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1273, 'Ya ayyuha allatheena amanoo ma lakum itha qeela lakumu infiroo fee sabeeli Allahi iththaqatum ila alardi aradeetum bi **a** lhayati a **l** ddunya mina alakhirati fama mata\'u alhayati a **l** ddunya fee alakhirati illa qaleel **un**');
INSERT INTO `en_transliteration` VALUES (1274, 'Illa tanfiroo yu\'aththibkum \'athaban aleeman wayastabdil qawman ghayrakum wala tadurroohu shayan wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (1275, 'Illa tansuroohu faqad nasarahu Allahu ith akhrajahu allatheena kafaroo thaniya ithnayni ith huma fee alghari ith yaqoolu lisahibihi la tahzan inna Allaha ma\'ana faanzala Allahu sakeenatahu \'alayhi waayyadahu bijunoodin lam tarawha waja\'ala kalimata allatheena kafaroo a **l** ssufla wakalimatu Allahi hiya al\'ulya wa **A** llahu \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1276, 'Infiroo khifafan wathiqalan wajahidoo biamwalikum waanfusikum fee sabeeli Allahi thalikum khayrun lakum in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1277, 'Law kana \'aradan qareeban wasafaran qasidan la **i** ttaba\'ooka walakin ba\'udat \'alayhimu a **l** shshuqqatu wasayahlifoona bi **A** llahi lawi istata\'na lakharajna ma\'akum yuhlikoona anfusahum wa **A** llahu ya\'lamu innahum lakathiboon **a**');
INSERT INTO `en_transliteration` VALUES (1278, '\'afa Allahu \'anka lima athinta lahum hatta yatabayyana laka allatheena sadaqoo wata\'lama alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (1279, 'La yastathinuka allatheena yuminoona bi **A** llahi wa **a** lyawmi alakhiri an yujahidoo biamwalihim waanfusihim wa **A** llahu \'aleemun bi **a** lmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (1280, 'Innama yastathinuka allatheena la yuminoona bi **A** llahi wa **a** lyawmi alakhiri wa **i** rtabat quloobuhum fahum fee raybihim yataraddadoon **a**');
INSERT INTO `en_transliteration` VALUES (1281, 'Walaw aradoo alkhurooja laa\'addoo lahu \'uddatan walakin kariha Allahu inbi\'athahum fathabbatahum waqeela oq\'udoo ma\'a alqa\'ideen **a**');
INSERT INTO `en_transliteration` VALUES (1282, 'Law kharajoo feekum ma zadookum illa khabalan walaawda\'oo khilalakum yabghoonakumu alfitnata wafeekum samma\'oona lahum wa **A** llahu \'aleemun bi **al** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1283, 'Laqadi ibtaghawoo alfitnata min qablu waqallaboo laka alomoora hatta jaa alhaqqu wa _th_ ahara amru Allahi wahum karihoon **a**');
INSERT INTO `en_transliteration` VALUES (1284, 'Waminhum man yaqoolu ithan lee wala taftinnee ala fee alfitnati saqatoo wainna jahannama lamuheetatun bi **a** lkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1285, 'In tusibka hasanatun tasuhum wain tusibka museebatun yaqooloo qad akhathna amrana min qablu wayatawallaw wahum farihoon **a**');
INSERT INTO `en_transliteration` VALUES (1286, 'Qul lan yuseebana illa ma kataba Allahu lana huwa mawlana wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1287, 'Qul hal tarabbasoona bina illa ihda alhusnayayni wanahnu natarabbasu bikum an yuseebakumu Allahu bi\'athabin min \'indihi aw biaydeena fatarabbasoo inna ma\'akum mutarabbisoon **a**');
INSERT INTO `en_transliteration` VALUES (1288, 'Qul anfiqoo taw\'an aw karhan lan yutaqabbala minkum innakum kuntum qawman fasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1289, 'Wama mana\'ahum an tuqbala minhum nafaqatuhum illa annahum kafaroo bi **A** llahi wabirasoolihi wala yatoona a **l** ssalata illa wahum kusala wala yunfiqoona illa wahum karihoon **a**');
INSERT INTO `en_transliteration` VALUES (1290, 'Fala tu\'jibka amwaluhum wala awladuhum innama yureedu Allahu liyu\'aththibahum biha fee alhayati a **l** ddunya watazhaqa anfusuhum wahum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1291, 'Wayahlifoona bi **A** llahi innahum laminkum wama hum minkum walakinnahum qawmun yafraqoon **a**');
INSERT INTO `en_transliteration` VALUES (1292, 'Law yajidoona maljaan aw magharatin aw muddakhalan lawallaw ilayhi wahum yajmahoon **a**');
INSERT INTO `en_transliteration` VALUES (1293, 'Waminhum man yalmizuka fee a **l** ssadaqati fain o\'too minha radoo wain lam yu\'taw minha itha hum yaskhatoon **a**');
INSERT INTO `en_transliteration` VALUES (1294, 'Walaw annahum radoo maatahumu Allahu warasooluhu waqaloo hasbuna Allahu sayuteena Allahu min fadlihi warasooluhu inna ila Allahi raghiboon **a**');
INSERT INTO `en_transliteration` VALUES (1295, 'Innama a **l** ssadaqatu lilfuqarai wa **a** lmasakeeni wa **a** l\'amileena \'alayha wa **a** lmuallafati quloobuhum wafee a **l** rriqabi wa **a** lgharimeena wafee sabeeli Allahi wa **i** bni a **l** ssabeeli fareedatan mina Allahi wa **A** llahu \'aleemun hakeemun');
INSERT INTO `en_transliteration` VALUES (1296, 'Waminhumu allatheena yuthoona a **l** nnabiyya wayaqooloona huwa othunun qul othunu khayrin lakum yuminu bi **A** llahi wayuminu lilmumineena warahmatun lillatheena amanoo minkum wa **a** llatheena yuthoona rasoola Allahi lahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1297, 'Yahlifoona bi **A** llahi lakum liyurdookum wa **A** llahu warasooluhu ahaqqu an yurdoohu in kanoo mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1298, 'Alam ya\'lamoo annahu man yuhadidi Allaha warasoolahu faanna lahu nara jahannama khalidan feehathalika alkhizyu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (1299, 'Yahtharu almunafiqoona an tunazzala \'alayhim sooratun tunabbiohum bima fee quloobihim quli istahzioo inna Allaha mukhrijun ma tahtharoon **a**');
INSERT INTO `en_transliteration` VALUES (1300, 'Walain saatahum layaqoolunna innama kunna nakhoodu wanal\'abu qul abi **A** llahi waayatihi warasoolihi kuntum tastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (1301, 'La ta\'tathiroo qad kafartum ba\'da eemanikum in na\'fu \'an taifatin minkum nu\'aththib taifatan biannahum kanoo mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1302, 'Almunafiqoona wa **a** lmunafiqatu ba\'duhum min ba\'din yamuroona bi **a** lmunkari wayanhawna \'ani alma\'roofi wayaqbidoona aydiyahum nasoo Allaha fanasiyahum inna almunafiqeena humu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (1303, 'Wa\'ada Allahu almunafiqeena wa **a** lmunafiqati wa **a** lkuffara nara jahannama khalideena feeha hiya hasbuhum wala\'anahumu Allahu walahum \'athabun muqeem **un**');
INSERT INTO `en_transliteration` VALUES (1304, 'Ka **a** llatheena min qablikum kanoo ashadda minkum quwwatan waakthara amwalan waawladan fa **i** stamta\'oo bikhalaqihim fa **i** stamta\'tum bikhalaqikum kama istamta\'a allatheena min qablikum bikhalaqihim wakhudtum ka **a** llathee khadoo olaika habitat a\'maluhum fee a **l** ddunya wa **a** lakhirati waolaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (1305, 'Alam yatihim nabao allatheena min qablihim qawmi noohin wa\'adin wathamooda waqawmi ibraheema waashabi madyana wa **a** lmutafikati atathum rusuluhum bi **a** lbayyinati fama kana Allahu liya _th_ limahum walakin kanoo anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (1306, 'Wa **a** lmuminoona wa **a** lmuminatu ba\'duhum awliyao ba\'din yamuroona bi **a** lma\'roofi wayanhawna \'ani almunkari wayuqeemoona a **l** ssalata wayutoona a **l** zzakata wayutee\'oona Allaha warasoolahu olaika sayarhamuhumu Allahu inna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1307, 'Wa\'ada Allahu almumineena wa **a** lmuminati jannatin tajree min tahtiha alanharu khalideena feeha wamasakina tayyibatan fee jannati \'adnin waridwanun mina Allahi akbaru thalika huwa alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (1308, 'Ya ayyuha a **l** nnabiyyu jahidi alkuffara wa **a** lmunafiqeena wa **o** ghlu _th_ \'alayhim wamawahum jahannamu wabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (1309, 'Yahlifoona bi **A** llahi ma qaloo walaqad qaloo kalimata alkufri wakafaroo ba\'da islamihim wahammoo bima lam yanaloo wama naqamoo illa an aghnahumu Allahu warasooluhu min fadlihi fain yatooboo yaku khayran lahum wain yatawallaw yu\'aththibhumu Allahu \'athaban aleeman fee a **l** ddunya wa **a** lakhirati wama lahum fee alardi min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (1310, 'Waminhum man \'ahada Allaha lain atana min fadlihi lanassaddaqanna walanakoonanna mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (1311, 'Falammaatahum min fadlihi bakhiloo bihi watawallaw wahum mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (1312, 'Faa\'qabahum nifaqan fee quloobihim ila yawmi yalqawnahu bima akhlafoo Allaha ma wa\'adoohu wabima kanoo yakthiboon **a**');
INSERT INTO `en_transliteration` VALUES (1313, 'Alam ya\'lamoo anna Allaha ya\'lamu sirrahum wanajwahum waanna Allaha \'allamu alghuyoob **i**');
INSERT INTO `en_transliteration` VALUES (1314, 'Allatheena yalmizoona almuttawwi\'eena mina almumineena fee a **l** ssadaqati wa **a** llatheena la yajidoona illa juhdahum fayaskharoona minhum sakhira Allahu minhum walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1315, 'Istaghfir lahum aw la tastaghfir lahum in tastaghfir lahum sab\'eena marratan falan yaghfira Allahu lahum thalika biannahum kafaroo bi **A** llahi warasoolihi wa **A** llahu la yahdee alqawma alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1316, 'Fariha almukhallafoona bimaq\'adihim khilafa rasooli Allahi wakarihoo an yujahidoo biamwalihim waanfusihim fee sabeeli Allahi waqaloo la tanfiroo fee alharri qul naru jahannama ashaddu harran law kanoo yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (1317, 'Falyadhakoo qaleelan walyabkoo katheeran jazaan bima kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (1318, 'Fain raja\'aka Allahu ilataifatin minhum fa **i** stathanooka lilkhurooji faqul lan takhrujoo ma\'iya abadan walan tuqatiloo ma\'iya \'aduwwan innakum radeetum bi **a** lqu\'oodi awwala marratin fa **o** q\'udoo ma\'a alkhalifeen **a**');
INSERT INTO `en_transliteration` VALUES (1319, 'Wala tusalli \'ala ahadin minhum mata abadan wala taqum \'ala qabrihi innahum kafaroo bi **A** llahi warasoolihi wamatoo wahum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (1320, 'Wala tu\'jibka amwaluhum waawladuhum innama yureedu Allahu an yu\'aththibahum biha fee a **l** ddunya watazhaqa anfusuhum wahum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1321, 'Waitha onzilat sooratun an aminoo bi **A** llahi wajahidoo ma\'a rasoolihi istathanaka oloo a **l** ttawli minhum waqaloo tharna nakun ma\'a alqa\'ideen **a**');
INSERT INTO `en_transliteration` VALUES (1322, 'Radoo bian yakoonoo ma\'a alkhawalifi watubi\'a \'ala quloobihim fahum la yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (1323, 'Lakini a **l** rrasoolu wa **a** llatheena amanoo ma\'ahu jahadoo biamwalihim waanfusihim waolaika lahumu alkhayratu waolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (1324, 'a\'adda Allahu lahum jannatin tajree min tahtiha alanharu khalideena feehathalika alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (1325, 'Wajaa almu\'aththiroona mina ala\'rabi liyuthana lahum waqa\'ada allatheena kathaboo Allaha warasoolahu sayuseebu allatheena kafaroo minhum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1326, 'Laysa \'ala a **l** ddu\'afai wala \'ala almarda wala \'ala allatheena la yajidoona ma yunfiqoona harajun itha nasahoo lillahi warasoolihi ma \'ala almuhsineena min sabeelin wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1327, 'Wala \'ala allatheena itha ma atawka litahmilahum qulta la ajidu ma ahmilukum \'alayhi tawallaw waa\'yunuhum tafeedu mina a **l** ddam\'i hazanan alla yajidoo ma yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (1328, 'Innama a **l** ssabeelu \'ala allatheena yastathinoonaka wahum aghniyao radoo bian yakoonoo ma\'a alkhawalifi wataba\'a Allahu \'ala quloobihim fahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1329, 'Ya\'tathiroona ilaykum itha raja\'tum ilayhim qul la ta\'tathiroo lan numina lakum qad nabbaana Allahu min akhbarikum wasayara Allahu \'amalakum warasooluhu thumma turaddoona ila \'alimi alghaybi wa **al** shshahadati fayunabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1330, 'Sayahlifoona bi **A** llahi lakum itha inqalabtum ilayhim litu\'ridoo \'anhum faa\'ridoo \'anhum innahum rijsun wamawahum jahannamu jazaan bima kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (1331, 'Yahlifoona lakum litardaw \'anhum fain tardaw \'anhum fainna Allaha la yarda \'ani alqawmi alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1332, 'Ala\'rabu ashaddu kufran wanifaqan waajdaru alla ya\'lamoo hudooda ma anzala Allahu \'ala rasoolihi wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1333, 'Wamina ala\'rabi man yattakhithu ma yunfiqu maghraman wayatarabbasu bikumu a **l** ddawaira \'alayhim dairatu a **l** ssawi wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1334, 'Wamina ala\'rabi man yuminu bi **A** llahi wa **a** lyawmi alakhiri wayattakhithu ma yunfiqu qurubatin \'inda Allahi wasalawati a **l** rrasooli ala innaha qurbatun lahum sayudkhiluhumu Allahu fee rahmatihi inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1335, 'Wa **al** ssabiqoona alawwaloona mina almuhajireena wa **a** lansari wa **a** llatheena ittaba\'oohum biihsanin radiya Allahu \'anhum waradoo \'anhu waa\'adda lahum jannatin tajree tahtaha alanharu khalideena feeha abadan thalika alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (1336, 'Wamimman hawlakum mina ala\'rabi munafiqoona wamin ahli almadeenati maradoo \'ala a **l** nnifaqi la ta\'lamuhum nahnu na\'lamuhum sanu\'aththibuhum marratayni thumma yuraddoona ila \'athabin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (1337, 'Waakharoona i\'tarafoo bithunoobihim khalatoo \'amalan salihan waakhara sayyian \'asa Allahu an yatooba \'alayhim inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1338, 'Khuth min amwalihim sadaqatan tutahhiruhum watuzakkeehim biha wasalli \'alayhim inna salataka sakanun lahum wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1339, 'Alam ya\'lamoo anna Allaha huwa yaqbalu a **l** ttawbata \'an \'ibadihi wayakhuthu a **l** ssadaqati waanna Allaha huwa a **l** ttawwabu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (1340, 'Waquli i\'maloo fasayara Allahu \'amalakum warasooluhu wa **a** lmuminoona wasaturaddoona ila \'alimi alghaybi wa **al** shshahadati fayunabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1341, 'Waakharoona murjawna liamri Allahi imma yu\'aththibuhum waimma yatoobu \'alayhim wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1342, 'Wa **a** llatheena ittakhathoo masjidan diraran wakufran watafreeqan bayna almumineena wairsadan liman haraba Allaha warasoolahu min qablu walayahlifunna in aradna illa alhusna wa **A** llahu yashhadu innahum lakathiboon **a**');
INSERT INTO `en_transliteration` VALUES (1343, 'La taqum feehi abadan lamasjidun ossisa \'ala a **l** ttaqwa min awwali yawmin ahaqqu an taqooma feehi feehi rijalun yuhibboona an yatatahharoo wa **A** llahu yuhibbu almuttahhireen **a**');
INSERT INTO `en_transliteration` VALUES (1344, 'Afaman assasa bunyanahu \'ala taqwa mina Allahi waridwanin khayrun am man assasa bunyanahu \'ala shafa jurufin harin fa **i** nhara bihi fee nari jahannama wa **A** llahu la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1345, 'La yazalu bunyanuhumu allathee banaw reebatan fee quloobihim illa an taqatta\'a quloobuhum wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1346, 'Inna Allaha ishtara mina almumineena anfusahum waamwalahum bianna lahumu aljannata yuqatiloona fee sabeeli Allahi fayaqtuloona wayuqtaloona wa\'dan \'alayhi haqqan fee a **l** ttawrati wa **a** linjeeli wa **a** lqurani waman awfa bi\'ahdihi mina Allahi fa **i** stabshiroo bibay\'ikumu allathee baya\'tum bihi wathalika huwa alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (1347, 'A **l** ttaiboona al\'abidoona alhamidoona a **l** ssaihoona a **l** rraki\'oona a **l** ssajidoona alamiroona bi **a** lma\'roofi wa **al** nnahoona \'ani almunkari wa **a** lhafi _th_ oona lihudoodi Allahi wabashshiri almumineen **a**');
INSERT INTO `en_transliteration` VALUES (1348, 'Ma kana li **l** nnabiyyi wa **a** llatheena amanoo an yastaghfiroo lilmushrikeena walaw kanoo olee qurba min ba\'di ma tabayyana lahum annahum ashabu aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (1349, 'Wama kana istighfaru ibraheema liabeehi illa \'an maw\'idatin wa\'adaha iyyahu falamma tabayyana lahu annahu \'aduwwun lillahi tabarraa minhu inna ibraheema laawwahun haleem **un**');
INSERT INTO `en_transliteration` VALUES (1350, 'Wama kana Allahu liyudilla qawman ba\'da ith hadahum hatta yubayyina lahum ma yattaqoona inna Allaha bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1351, 'Inna Allaha lahu mulku a **l** ssamawati wa **a** lardi yuhyee wayumeetu wama lakum min dooni Allahi min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (1352, 'Laqad taba Allahu \'ala a **l** nnabiyyi wa **a** lmuhajireena wa **a** lansari allatheena ittaba\'oohu fee sa\'ati al\'usrati min ba\'di ma kada yazeeghu quloobu fareeqin minhum thumma taba \'alayhim innahu bihim raoofun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1353, 'Wa\'ala a **l** ththalathati allatheena khullifoo hatta ithadaqat \'alayhimu alardu bima rahubat wadaqat \'alayhim anfusuhum wa _th_ annoo an la maljaa mina Allahi illa ilayhi thumma taba \'alayhim liyatooboo inna Allaha huwa a **l** ttawwabu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (1354, 'Ya ayyuha allatheena amanoo ittaqoo Allaha wakoonoo ma\'a a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1355, 'Ma kana liahli almadeenati waman hawlahum mina ala\'rabi an yatakhallafoo \'an rasooli Allahi wala yarghaboo bianfusihim \'an nafsihi thalika biannahum la yuseebuhum _th_ amaon wala nasabun wala makhmasatun fee sabeeli Allahi wala yataoona mawtian yaghee _th_ u alkuffara wala yanaloona min \'aduwwin naylan illa kutiba lahum bihi \'amalun salihun inna Allaha la yudee\'u ajra almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1356, 'Wala yunfiqoona nafaqatan sagheeratan wala kabeeratan wala yaqta\'oona wadiyan illa kutiba lahum liyajziyahumu Allahu ahsana ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1357, 'Wama kana almuminoona liyanfiroo kaffatan falawla nafara min kulli firqatin minhum taifatun liyatafaqqahoo fee a **l** ddeeni waliyunthiroo qawmahum itha raja\'oo ilayhim la\'allahum yahtharoon **a**');
INSERT INTO `en_transliteration` VALUES (1358, 'Ya ayyuha allatheena amanoo qatiloo allatheena yaloonakum mina alkuffari walyajidoo feekum ghil _th_ atan wa **i** \'lamoo anna Allaha ma\'a almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (1359, 'Waitha ma onzilat sooratun faminhum man yaqoolu ayyukum zadathu hathihi eemanan faamma allatheena amanoo fazadathum eemanan wahum yastabshiroon **a**');
INSERT INTO `en_transliteration` VALUES (1360, 'Waamma allatheena fee quloobihim maradun fazadathum rijsan ila rijsihim wamatoo wahum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1361, 'Awala yarawna annahum yuftanoona fee kulli \'amin marratan aw marratayni thumma la yatooboona wala hum yaththakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1362, 'Waitha ma onzilat sooratun na _th_ ara ba\'duhum ila ba\'din hal yarakum min ahadin thumma insarafoo sarafa Allahu quloobahum biannahum qawmun la yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (1363, 'Laqad jaakum rasoolun min anfusikum \'azeezun \'alayhi ma \'anittum hareesun \'alaykum bi **a** lmumineena raoofun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1364, 'Fain tawallaw faqul hasbiya Allahu la ilaha illa huwa \'alayhi tawakkatu wahuwa rabbu al\'arshi al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (1365, 'Aliflamra tilka ayatu alkitabi alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (1366, 'Akana li **l** nnasi \'ajaban an awhayna ila rajulin minhum an anthiri a **l** nnasa wabashshiri allatheena amanoo anna lahum qadama sidqin \'inda rabbihim qala alkafiroona inna hatha lasahirun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1367, 'Inna rabbakumu Allahu allathee khalaqa a **l** ssamawati wa **a** larda fee sittati ayyamin thumma istawa \'ala al\'arshi yudabbiru alamra ma min shafee\'in illa min ba\'di ithnihi thalikumu Allahu rabbukum fa **o** \'budoohu afala tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1368, 'Ilayhi marji\'ukum jamee\'an wa\'da Allahi haqqan innahu yabdao alkhalqa thumma yu\'eeduhu liyajziya allatheena amanoo wa\'amiloo a **l** ssalihati bi **a** lqisti wa **a** llatheena kafaroo lahum sharabun min hameemin wa\'athabun aleemun bima kanoo yakfuroon **a**');
INSERT INTO `en_transliteration` VALUES (1369, 'Huwa allathee ja\'ala a **l** shshamsa diyaan wa **a** lqamara nooran waqaddarahu manazila lita\'lamoo \'adada a **l** ssineena wa **a** lhisaba ma khalaqa Allahu thalika illa bi **a** lhaqqi yufassilu alayati liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1370, 'Inna fee ikhtilafi allayli wa **al** nnahari wama khalaqa Allahu fee a **l** ssamawati wa **a** lardi laayatin liqawmin yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1371, 'Inna allatheena la yarjoona liqaana waradoo bi **a** lhayati a **l** ddunya wa **i** tmaannoo biha wa **a** llatheena hum \'an ayatina ghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (1372, 'Olaika mawahumu a **l** nnaru bima kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (1373, 'Inna allatheena amanoo wa\'amiloo a **l** ssalihati yahdeehim rabbuhum bieemanihim tajree min tahtihimu alanharu fee jannati a **l** nna\'eem **i**');
INSERT INTO `en_transliteration` VALUES (1374, 'Da\'wahum feeha subhanaka allahumma watahiyyatuhum feeha salamun waakhiru da\'wahum ani alhamdu lillahi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (1375, 'Walaw yu\'ajjilu Allahu li **l** nnasi a **l** shsharra isti\'jalahum bi **a** lkhayri laqudiya ilayhim ajaluhum fanatharu allatheena la yarjoona liqaana fee tughyanihim ya\'mahoon **a**');
INSERT INTO `en_transliteration` VALUES (1376, 'Waitha massa alinsana a **l** ddurru da\'ana lijanbihi aw qa\'idan aw qaiman falamma kashafna \'anhu durrahu marra kaan lam yad\'una iladurrin massahu kathalika zuyyina lilmusrifeena ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1377, 'Walaqad ahlakna alquroona min qablikum lamma _th_ alamoo wajaathum rusuluhum bi **a** lbayyinati wama kanoo liyuminoo kathalika najzee alqawma almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1378, 'Thumma ja\'anakum khalaifa fee alardi min ba\'dihim linan _th_ ura kayfa ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1379, 'Waitha tutla \'alayhim ayatuna bayyinatin qala allatheena la yarjoona liqaana iti biquranin ghayri hatha aw baddilhu qul ma yakoonu lee an obaddilahu min tilqai nafsee in attabi\'u illa ma yooha ilayya innee akhafu in \'asaytu rabbee \'athaba yawmin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (1380, 'Qul law shaa Allahu ma talawtuhu \'alaykum wala adrakum bihi faqad labithtu feekum \'umuran min qablihi afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1381, 'Faman a _th_ lamu mimmani iftara \'ala Allahi kathiban aw kaththaba biayatihi innahu la yuflihu almujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (1382, 'Waya\'budoona min dooni Allahi ma la yadurruhum wala yanfa\'uhum wayaqooloona haolai shufa\'aona \'inda Allahi qul atunabbioona Allaha bima la ya\'lamu fee a **l** ssamawati wala fee alardi subhanahu wata\'ala \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (1383, 'Wama kana a **l** nnasu illa ommatan wahidatan fa **i** khtalafoo walawla kalimatun sabaqat min rabbika laqudiya baynahum feema feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (1384, 'Wayaqooloona lawla onzila \'alayhi ayatun min rabbihi faqul innama alghaybu lillahi fa **i** nta _th_ iroo innee ma\'akum mina almunta _th_ ireen **a**');
INSERT INTO `en_transliteration` VALUES (1385, 'Waitha athaqna a **l** nnasa rahmatan min ba\'di darraa massathum itha lahum makrun fee ayatina quli Allahu asra\'u makran inna rusulana yaktuboona ma tamkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1386, 'Huwa allathee yusayyirukum fee albarri wa **a** lbahri hatta itha kuntum fee alfulki wajarayna bihim bireehin tayyibatin wafarihoo biha jaatha reehun \'asifun wajaahumu almawju min kulli makanin wa _th_ annoo annahum oheeta bihim da\'awoo Allaha mukhliseena lahu a **l** ddeena lain anjaytana min hathihi lanakoonanna mina a **l** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (1387, 'Falamma anjahum itha hum yabghoona fee alardi bighayri alhaqqi ya ayyuha a **l** nnasu innama baghyukum \'ala anfusikum mata\'a alhayati a **l** ddunya thumma ilayna marji\'ukum fanunabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1388, 'Innama mathalu alhayati a **l** ddunya kamain anzanahu mina a **l** ssamai fa **i** khtalata bihi nabatu alardi mimma yakulu a **l** nnasu wa **a** lan\'amu hatta itha akhathati alardu zukhrufaha wa **i** zzayyanat wa _th_ anna ahluha annahum qadiroona \'alayha ataha amruna laylan aw naharan faja\'anahahaseedan kaan lam taghna bi **a** lamsi kathalika nufassilu alayati liqawmin yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1389, 'Wa **A** llahu yad\'oo ila dari a **l** ssalami wayahdee man yashao ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (1390, 'Lillatheena ahsanoo alhusna waziyadatun wala yarhaqu wujoohahum qatarun walathillatun olaika ashabu aljannati hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (1391, 'Wa **a** llatheena kasaboo a **l** ssayyiati jazao sayyiatin bimithliha watarhaquhum thillatun ma lahum mina Allahi min \'asimin kaannama oghshiyat wujoohuhum qita\'an mina allayli mu _th_ liman olaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (1392, 'Wayawma nahshuruhum jamee\'an thumma naqoolu lillatheena ashrakoo makanakum antum washurakaokum fazayyana baynahum waqala shurakaohum ma kuntum iyyana ta\'budoon **a**');
INSERT INTO `en_transliteration` VALUES (1393, 'Fakafa bi **A** llahi shaheedan baynana wabaynakum in kunna \'an \'ibadatikum laghafileen **a**');
INSERT INTO `en_transliteration` VALUES (1394, 'Hunalika tabloo kullu nafsin ma aslafat waruddoo ila Allahi mawlahumu alhaqqi wadalla \'anhum ma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (1395, 'Qul man yarzuqukum mina a **l** ssamai wa **a** lardi amman yamliku a **l** ssam\'a wa **a** labsara waman yukhriju alhayya mina almayyiti wayukhriju almayyita mina alhayyi waman yudabbiru alamra fasayaqooloona Allahu faqul afala tattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1396, 'Fathalikumu Allahu rabbukumu alhaqqu famatha ba\'da alhaqqi illa a **l** ddalalu faanna tusrafoon **a**');
INSERT INTO `en_transliteration` VALUES (1397, 'Kathalika haqqat kalimatu rabbika \'ala allatheena fasaqoo annahum la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1398, 'Qul hal min shurakaikum man yabdao alkhalqa thumma yu\'eeduhu quli Allahu yabdao alkhalqa thumma yu\'eeduhu faanna tufakoon **a**');
INSERT INTO `en_transliteration` VALUES (1399, 'Qul hal min shurakaikum man yahdee ila alhaqqi quli Allahu yahdee lilhaqqi afaman yahdee ila alhaqqi ahaqqu an yuttaba\'a amman la yahiddee illa an yuhda fama lakum kayfa tahkumoon **a**');
INSERT INTO `en_transliteration` VALUES (1400, 'Wama yattabi\'u aktharuhum illa _th_ annan inna a **l** _thth_ anna la yughnee mina alhaqqi shayan inna Allaha \'aleemun bima yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (1401, 'Wama kana hatha alquranu an yuftara min dooni Allahi walakin tasdeeqa allathee bayna yadayhi watafseela alkitabi la rayba feehi min rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (1402, 'Am yaqooloona iftarahu qul fatoo bisooratin mithlihi wa **o** d\'oo mani istata\'tum min dooni Allahi in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1403, 'Bal kaththaboo bima lam yuheetoo bi\'ilmihi walamma yatihim taweeluhu kathalika kaththaba allatheena min qablihim fa **o** n _th_ ur kayfa kana \'aqibatu a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1404, 'Waminhum man yuminu bihi waminhum man la yuminu bihi warabbuka a\'lamu bi **a** lmufsideen **a**');
INSERT INTO `en_transliteration` VALUES (1405, 'Wain kaththabooka faqul lee \'amalee walakum \'amalukum antum bareeoona mimma a\'malu waana bareeon mimma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1406, 'Waminhum man yastami\'oona ilayka afaanta tusmi\'u a **l** ssumma walaw kanoo la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1407, 'Waminhum man yan _th_ uru ilayka afaanta tahdee al\'umya walaw kanoo la yubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (1408, 'Inna Allaha la ya _th_ limu a **l** nnasa shayan walakinna a **l** nnasa anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (1409, 'Wayawma yahshuruhum kaan lam yalbathoo illa sa\'atan mina a **l** nnahari yata\'arafoona baynahum qad khasira allatheena kaththaboo biliqai Allahi wama kanoo muhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (1410, 'Waimma nuriyannaka ba\'da allathee na\'iduhum aw natawaffayannaka failayna marji\'uhum thumma Allahu shaheedun \'ala ma yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (1411, 'Walikulli ommatin rasoolun faitha jaa rasooluhum qudiya baynahum bi **a** lqisti wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1412, 'Wayaqooloona mata hatha alwa\'du in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1413, 'Qul la amliku linafsee darran wala naf\'an illa ma shaa Allahu likulli ommatin ajalun itha jaa ajaluhum fala yastakhiroona sa\'atan wala yastaqdimoon **a**');
INSERT INTO `en_transliteration` VALUES (1414, 'Qul araaytum in atakum \'athabuhu bayatan aw naharan matha yasta\'jilu minhu almujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (1415, 'Athumma itha ma waqa\'a amantum bihi alana waqad kuntum bihi tasta\'jiloon **a**');
INSERT INTO `en_transliteration` VALUES (1416, 'Thumma qeela lillatheena _th_ alamoo thooqoo \'athaba alkhuldi hal tujzawna illa bima kuntum taksiboon **a**');
INSERT INTO `en_transliteration` VALUES (1417, 'Wayastanbioonaka ahaqqun huwa qul ee warabbee innahu lahaqqun wama antum bimu\'jizeen **a**');
INSERT INTO `en_transliteration` VALUES (1418, 'Walaw anna likulli nafsin _th_ alamat ma fee alardi la **i** ftadat bihi waasarroo a **l** nnadamata lamma raawoo al\'athaba waqudiya baynahum bi **a** lqisti wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1419, 'Ala inna lillahi ma fee a **l** ssamawati wa **a** lardi ala inna wa\'da Allahi haqqun walakinna aktharahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1420, 'Huwa yuhyee wayumeetu wailayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1421, 'Ya ayyuha a **l** nnasu qad jaatkum maw\'i _th_ atun min rabbikum washifaon lima fee a **l** ssudoori wahudan warahmatun lilmumineen **a**');
INSERT INTO `en_transliteration` VALUES (1422, 'Qul bifadli Allahi wabirahmatihi fabithalika falyafrahoo huwa khayrun mimma yajma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1423, 'Qul araaytum ma anzala Allahu lakum min rizqin faja\'atum minhu haraman wahalalan qul allahu athina lakum am \'ala Allahi taftaroon **a**');
INSERT INTO `en_transliteration` VALUES (1424, 'Wama _th_ annu allatheena yaftaroona \'ala Allahi alkathiba yawma alqiyamati inna Allaha lathoo fadlin \'ala a **l** nnasi walakinna aktharahum la yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1425, 'Wama takoonu fee shanin wama tatloo minhu min quranin wala ta\'maloona min \'amalin illa kunna \'alaykum shuhoodan ith tufeedoona feehi wama ya\'zubu \'an rabbika min mithqali tharratin fee alardi wala fee a **l** ssamai wala asghara min thalika wala akbara illa fee kitabin mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1426, 'Ala inna awliyaa Allahi la khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (1427, 'Allatheena amanoo wakanoo yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1428, 'Lahumu albushra fee alhayati a **l** ddunya wafee alakhirati la tabdeela likalimati Allahi thalika huwa alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (1429, 'Wala yahzunka qawluhum inna al\'izzata lillahi jamee\'an huwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (1430, 'Ala inna lillahi man fee a **l** ssamawati waman fee alardi wama yattabi\'u allatheena yad\'oona min dooni Allahi shurakaa in yattabi\'oona illa a **l** _thth_ anna wain hum illa yakhrusoon **a**');
INSERT INTO `en_transliteration` VALUES (1431, 'Huwa allathee ja\'ala lakumu allayla litaskunoo feehi wa **al** nnahara mubsiran inna fee thalika laayatin liqawmin yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1432, 'Qaloo ittakhatha Allahu waladan subhanahu huwa alghaniyyu lahu ma fee a **l** ssamawati wama fee alardi in \'indakum min sultanin bihatha ataqooloona \'ala Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1433, 'Qul inna allatheena yaftaroona \'ala Allahi alkathiba la yuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (1434, 'Mata\'un fee a **l** ddunya thumma ilayna marji\'uhum thumma nutheequhumu al\'athaba a **l** shshadeeda bima kanoo yakfuroon **a**');
INSERT INTO `en_transliteration` VALUES (1435, 'Wa **o** tlu \'alayhim nabaa noohin ith qala liqawmihi ya qawmi in kana kabura \'alaykum maqamee watathkeeree biayati Allahi fa\'ala Allahi tawakkatu faajmi\'oo amrakum washurakaakum thumma la yakun amrukum \'alaykum ghummatan thumma iqdoo ilayya wala tun _th_ iroon **i**');
INSERT INTO `en_transliteration` VALUES (1436, 'Fain tawallaytum fama saatukum min ajrin in ajriya illa \'ala Allahi waomirtu an akoona mina almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (1437, 'Fakaththaboohu fanajjaynahu waman ma\'ahu fee alfulki waja\'anahum khalaifa waaghraqna allatheena kaththaboo biayatina fa **o** n _th_ ur kayfa kana \'aqibatu almunthareen **a**');
INSERT INTO `en_transliteration` VALUES (1438, 'Thumma ba\'athna min ba\'dihi rusulan ila qawmihim fajaoohum bi **a** lbayyinati fama kanoo liyuminoo bima kaththaboo bihi min qablu kathalika natba\'u \'ala quloobi almu\'tadeen **a**');
INSERT INTO `en_transliteration` VALUES (1439, 'Thumma ba\'athna min ba\'dihim moosa waharoona ila fir\'awna wamalaihi biayatina fa **i** stakbaroo wakanoo qawman mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1440, 'Falamma jaahumu alhaqqu min \'indina qaloo inna hatha lasihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1441, 'Qala moosa ataqooloona lilhaqqi lamma jaakum asihrun hatha wala yuflihu a **l** ssahiroon **a**');
INSERT INTO `en_transliteration` VALUES (1442, 'Qaloo ajitana litalfitana \'amma wajadna \'alayhi abaana watakoona lakuma alkibriyao fee alardi wama nahnu lakuma bimumineen **a**');
INSERT INTO `en_transliteration` VALUES (1443, 'Waqala fir\'awnu itoonee bikulli sahirin \'aleem **in**');
INSERT INTO `en_transliteration` VALUES (1444, 'Falamma jaa a **l** ssaharatu qala lahum moosa alqoo ma antum mulqoon **a**');
INSERT INTO `en_transliteration` VALUES (1445, 'Falamma alqaw qala moosa ma jitum bihi a **l** ssihru inna Allaha sayubtiluhu inna Allaha la yuslihu \'amala almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (1446, 'Wayuhiqqu Allahu alhaqqa bikalimatihi walaw kariha almujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (1447, 'Famaamana limoosa illathurriyyatun min qawmihi \'ala khawfin min fir\'awna wamalaihim an yaftinahum wainna fir\'awna la\'alin fee alardi wainnahu lamina almusrifeen **a**');
INSERT INTO `en_transliteration` VALUES (1448, 'Waqala moosa ya qawmi in kuntum amantum bi **A** llahi fa\'alayhi tawakkaloo in kuntum muslimeen **a**');
INSERT INTO `en_transliteration` VALUES (1449, 'Faqaloo \'ala Allahi tawakkana rabbana la taj\'ana fitnatan lilqawmi a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1450, 'Wanajjina birahmatika mina alqawmi alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1451, 'Waawhayna ila moosa waakheehi an tabawwaa liqawmikuma bimisra buyootan wa **i** j\'aloo buyootakum qiblatan waaqeemoo a **l** ssalata wabashshiri almumineen **a**');
INSERT INTO `en_transliteration` VALUES (1452, 'Waqala moosa rabbana innaka atayta fir\'awna wamalaahu zeenatan waamwalan fee alhayati a **l** ddunya rabbana liyudilloo \'an sabeelika rabbana itmis \'ala amwalihim wa **o** shdud \'ala quloobihim fala yuminoo hatta yarawoo al\'athaba alaleem **a**');
INSERT INTO `en_transliteration` VALUES (1453, 'Qala qad ojeebat da\'watukuma fa **i** staqeema wala tattabi\'anni sabeela allatheena la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1454, 'Wajawazna bibanee israeela albahra faatba\'ahum fir\'awnu wajunooduhu baghyan wa\'adwan hatta itha adrakahu algharaqu qala amantu annahu la ilaha illa allathee amanat bihi banoo israeela waana mina almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (1455, 'Alana waqad \'asayta qablu wakunta mina almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (1456, 'Fa **a** lyawma nunajjeeka bibadanika litakoona liman khalfaka ayatan wainna katheeran mina a **l** nnasi \'an ayatina laghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (1457, 'Walaqad bawwana banee israeela mubawwaa sidqin warazaqnahum mina a **l** ttayyibati fama ikhtalafoo hatta jaahumu al\'ilmu inna rabbaka yaqdee baynahum yawma alqiyamati feema kanoo feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (1458, 'Fain kunta fee shakkin mimma anzanna ilayka fa **i** sali allatheena yaqraoona alkitaba min qablika laqad jaaka alhaqqu min rabbika fala takoonanna mina almumtareen **a**');
INSERT INTO `en_transliteration` VALUES (1459, 'Wala takoonanna mina allatheena kaththaboo biayati Allahi fatakoona mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (1460, 'Inna allatheena haqqat \'alayhim kalimatu rabbika la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1461, 'Walaw jaathum kullu ayatin hatta yarawoo al\'athaba alaleem **a**');
INSERT INTO `en_transliteration` VALUES (1462, 'Falawla kanat qaryatun amanat fanafa\'aha eemanuha illa qawma yoonusa lammaamanoo kashafna \'anhum \'athaba alkhizyi fee alhayati a **l** ddunya wamatta\'nahum ilaheen **in**');
INSERT INTO `en_transliteration` VALUES (1463, 'Walaw shaa rabbuka laamana man fee alardi kulluhum jamee\'an afaanta tukrihu a **l** nnasa hatta yakoonoo mumineen **a**');
INSERT INTO `en_transliteration` VALUES (1464, 'Wama kana linafsin an tumina illa biithni Allahi wayaj\'alu a **l** rrijsa \'ala allatheena la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1465, 'Quli on _th_ uroo matha fee a **l** ssamawati wa **a** lardi wama tughnee alayatu wa **al** nnuthuru \'an qawmin la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1466, 'Fahal yanta _th_ iroona illa mithla ayyami allatheena khalaw min qablihim qul fa **i** nta _th_ iroo innee ma\'akum mina almunta _th_ ireen **a**');
INSERT INTO `en_transliteration` VALUES (1467, 'Thumma nunajjee rusulana wa **a** llatheena amanoo kathalika haqqan \'alayna nunjee almumineen **a**');
INSERT INTO `en_transliteration` VALUES (1468, 'Qul ya ayyuha a **l** nnasu in kuntum fee shakkin min deenee fala a\'budu allatheena ta\'budoona min dooni Allahi walakin a\'budu Allaha allathee yatawaffakum waomirtu an akoona mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (1469, 'Waan aqim wajhaka li **l** ddeeni haneefan wala takoonanna mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (1470, 'Wala tad\'u min dooni Allahi ma la yanfa\'uka wala yadurruka fain fa\'ata fainnaka ithan mina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1471, 'Wain yamsaska Allahu bidurrin fala kashifa lahu illa huwa wain yuridka bikhayrin fala radda lifadlihi yuseebu bihi man yashao min \'ibadihi wahuwa alghafooru a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (1472, 'Qul ya ayyuha a **l** nnasu qad jaakumu alhaqqu min rabbikum famani ihtada fainnama yahtadee linafsihi waman dalla fainnama yadillu \'alayha wama ana \'alaykum biwakeel **in**');
INSERT INTO `en_transliteration` VALUES (1473, 'Wa **i** ttabi\' ma yooha ilayka wa **i** sbir hatta yahkuma Allahu wahuwa khayru alhakimeen **a**');
INSERT INTO `en_transliteration` VALUES (1474, 'Aliflamra kitabun ohkimat ayatuhu thumma fussilat min ladun hakeemin khabeer **in**');
INSERT INTO `en_transliteration` VALUES (1475, 'Alla ta\'budoo illa Allaha innanee lakum minhu natheerun wabasheer **un**');
INSERT INTO `en_transliteration` VALUES (1476, 'Waani istaghfiroo rabbakum thumma tooboo ilayhi yumatti\'kum mata\'an hasanan ila ajalin musamman wayuti kulla thee fadlin fadlahu wain tawallaw fainee akhafu \'alaykum \'athaba yawmin kabeer **in**');
INSERT INTO `en_transliteration` VALUES (1477, 'Ila Allahi marji\'ukum wahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (1478, 'Ala innahum yathnoona sudoorahum liyastakhfoo minhu alaheena yastaghshoona thiyabahum ya\'lamu ma yusirroona wama yu\'linoona innahu \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (1479, 'Wama min dabbatin fee alardi illa \'ala Allahi rizquha waya\'lamu mustaqarraha wamustawda\'aha kullun fee kitabin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1480, 'Wahuwa allathee khalaqa a **l** ssamawati wa **a** larda fee sittati ayyamin wakana \'arshuhu \'ala almai liyabluwakum ayyukum ahsanu \'amalan walain qulta innakum mab\'oothoona min ba\'di almawti layaqoolanna allatheena kafaroo in hatha illa sihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1481, 'Walain akhkharna \'anhumu al\'athaba ila ommatin ma\'doodatin layaqoolunna ma yahbisuhu ala yawma yateehim laysa masroofan \'anhum wahaqa bihim ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (1482, 'Walain athaqna alinsana minna rahmatan thumma naza\'naha minhu innahu layaoosun kafoor **un**');
INSERT INTO `en_transliteration` VALUES (1483, 'Walain athaqnahu na\'maa ba\'da darraa massathu layaqoolanna thahaba a **l** ssayyiatu \'annee innahu lafarihun fakhoor **un**');
INSERT INTO `en_transliteration` VALUES (1484, 'Illa allatheena sabaroo wa\'amiloo a **l** ssalihati olaika lahum maghfiratun waajrun kabeer **un**');
INSERT INTO `en_transliteration` VALUES (1485, 'Fala\'allaka tarikun ba\'da ma yooha ilayka wadaiqun bihi sadruka an yaqooloo lawla onzila \'alayhi kanzun aw jaa ma\'ahu malakun innama anta natheerun wa **A** llahu \'ala kulli shayin wakeel **un**');
INSERT INTO `en_transliteration` VALUES (1486, 'Am yaqooloona iftarahu qul fatoo bi\'ashri suwarin mithlihi muftarayatin wa **o** d\'oo mani istata\'tum min dooni Allahi in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1487, 'Fai llam yastajeeboo lakum fa **i** \'lamoo annama onzila bi\'ilmi Allahi waan la ilaha illa huwa fahal antum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (1488, 'Man kana yureedu alhayata a **l** ddunya wazeenataha nuwaffi ilayhim a\'malahum feeha wahum feeha la yubkhasoon **a**');
INSERT INTO `en_transliteration` VALUES (1489, 'Olaika allatheena laysa lahum fee alakhirati illa a **l** nnaru wahabita masana\'oo feeha wabatilun ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1490, 'Afaman kana \'ala bayyinatin min rabbihi wayatloohu shahidun minhu wamin qablihi kitabu moosa imaman warahmatan olaika yuminoona bihi waman yakfur bihi mina alahzabi fa **al** nnaru maw\'iduhu fala taku fee miryatin minhu innahu alhaqqu min rabbika walakinna akthara a **l** nnasi la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1491, 'Waman a _th_ lamu mimmani iftara \'ala Allahi kathiban olaika yu\'radoona \'ala rabbihim wayaqoolu alashhadu haolai allatheena kathaboo \'ala rabbihim ala la\'natu Allahi \'ala a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1492, 'Allatheena yasuddoona \'an sabeeli Allahi wayabghoonaha \'iwajan wahum bi **a** lakhirati hum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1493, 'Olaika lam yakoonoo mu\'jizeena fee alardi wama kana lahum min dooni Allahi min awliyaa yuda\'afu lahumu al\'athabu ma kanoo yastatee\'oona a **l** ssam\'a wama kanoo yubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (1494, 'Olaika allatheena khasiroo anfusahum wadalla \'anhum ma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (1495, 'La jarama annahum fee alakhirati humu alakhsaroon **a**');
INSERT INTO `en_transliteration` VALUES (1496, 'Inna allatheena amanoo wa\'amiloo a **l** salihati waakhbatoo ila rabbihim olaika ashabu aljannati hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (1497, 'Mathalu alfareeqayni ka **a** la\'ma wa **a** lasammi wa **a** lbaseeri wa **al** ssamee\'i hal yastawiyani mathalan afala tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1498, 'Walaqad arsana noohan ila qawmihi innee lakum natheerun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1499, 'An la ta\'budoo illa Allaha innee akhafu \'alaykum \'athaba yawmin aleem **in**');
INSERT INTO `en_transliteration` VALUES (1500, 'Faqala almalao allatheena kafaroo min qawmihi ma naraka illa basharan mithlana wama naraka ittaba\'aka illa allatheena hum arathiluna badiya a **l** rrayi wama nara lakum \'alayna min fadlin bal na _th_ unnukum kathibeen **a**');
INSERT INTO `en_transliteration` VALUES (1501, 'Qala ya qawmi araaytum in kuntu \'ala bayyinatin min rabbee waatanee rahmatan min \'indihi fa\'ummiyat \'alaykum anulzimukumooha waantum laha karihoon **a**');
INSERT INTO `en_transliteration` VALUES (1502, 'Waya qawmi la asalukum \'alayhi malan in ajriya illa \'ala Allahi wama ana bitaridi allatheena amanoo innahum mulaqoo rabbihim walakinnee arakum qawman tajhaloon **a**');
INSERT INTO `en_transliteration` VALUES (1503, 'Waya qawmi man yansurunee mina Allahi in taradtuhum afala tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1504, 'Wala aqoolu lakum \'indee khazainu Allahi wala a\'lamu alghayba wala aqoolu innee malakun wala aqoolu lillatheena tazdaree a\'yunukum lan yutiyahumu Allahu khayran Allahu a\'lamu bima fee anfusihim innee ithan lamina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1505, 'Qaloo ya noohu qad jadatana faaktharta jidalana fatina bima ta\'iduna in kunta mina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1506, 'Qala innama yateekum bihi Allahu in shaa wama antum bimu\'jizeen **a**');
INSERT INTO `en_transliteration` VALUES (1507, 'Wala yanfa\'ukum nushee in aradtu an ansaha lakum in kana Allahu yureedu an yughwiyakum huwa rabbukum wailayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1508, 'Am yaqooloona iftarahu qul ini iftaraytuhu fa\'alayya ijramee waana bareeon mimma tujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (1509, 'Waoohiya ila noohin annahu lan yumina min qawmika illa man qad amana fala tabtais bima kanoo yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (1510, 'Wa **i** sna\'i alfulka bia\'yunina wawahyina wala tukhatibnee fee allatheena _th_ alamoo innahum mughraqoon **a**');
INSERT INTO `en_transliteration` VALUES (1511, 'Wayasna\'u alfulka wakullama marra \'alayhi malaon min qawmihi sakhiroo minhu qala in taskharoo minna fainna naskharu minkum kama taskharoon **a**');
INSERT INTO `en_transliteration` VALUES (1512, 'Fasawfa ta\'lamoona man yateehi \'athabun yukhzeehi wayahillu \'alayhi \'athabun muqeem **un**');
INSERT INTO `en_transliteration` VALUES (1513, 'Hatta itha jaa amruna wafara a **l** ttannooru qulna ihmil feeha min kullin zawjayni ithnayni waahlaka illa man sabaqa \'alayhi alqawlu waman amana wamaamana ma\'ahu illa qaleel **un**');
INSERT INTO `en_transliteration` VALUES (1514, 'Waqala irkaboo feeha bismi Allahi majraha wamursaha inna rabbee laghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1515, 'Wahiya tajree bihim fee mawjin ka **a** ljibali wanada noohunu ibnahu wakana fee ma\'zilin ya bunayya irkab ma\'ana wala takun ma\'a alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (1516, 'Qala saawee ila jabalin ya\'simunee mina almai qala la \'asima alyawma min amri Allahi illa man rahima wahala baynahuma almawju fakana mina almughraqeen **a**');
INSERT INTO `en_transliteration` VALUES (1517, 'Waqeela ya ardu ibla\'ee maaki waya samao aqli\'ee wagheeda almao waqudiya alamru wa **i** stawat \'ala aljoodiyyi waqeela bu\'dan lilqawmi a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1518, 'Wanada noohun rabbahu faqala rabbi inna ibnee min ahlee wainna wa\'daka alhaqqu waanta ahkamu alhakimeen **a**');
INSERT INTO `en_transliteration` VALUES (1519, 'Qala ya noohu innahu laysa min ahlika innahu \'amalun ghayru salihin fala tasani ma laysa laka bihi \'ilmun innee a\'i _th_ uka an takoona mina aljahileen **a**');
INSERT INTO `en_transliteration` VALUES (1520, 'Qala rabbi innee a\'oothu bika an asalaka ma laysa lee bihi \'ilmun wailla taghfir lee watarhamnee akun mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (1521, 'Qeela ya noohu ihbit bisalamin minna wabarakatin \'alayka wa\'ala omamin mimman ma\'aka waomamun sanumatti\'uhum thumma yamassuhum minna \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1522, 'Tilka min anbai alghaybi nooheeha ilayka ma kunta ta\'lamuha anta wala qawmuka min qabli hatha fa **i** sbir inna al\'aqibata lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (1523, 'Waila \'adin akhahum hoodan qala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu in antum illa muftaroon **a**');
INSERT INTO `en_transliteration` VALUES (1524, 'Ya qawmi la asalukum \'alayhi ajran in ajriya illa \'ala allathee fataranee afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1525, 'Waya qawmi istaghfiroo rabbakum thumma tooboo ilayhi yursili a **l** ssamaa \'alaykum midraran wayazidkum quwwatan ila quwwatikum wala tatawallaw mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1526, 'Qaloo ya hoodu ma jitana bibayyinatin wama nahnu bitarikee alihatina \'an qawlika wama nahnu laka bimumineen **a**');
INSERT INTO `en_transliteration` VALUES (1527, 'In naqoolu illa i\'taraka ba\'du alihatina bisooin qala innee oshhidu Allaha wa **i** shhadoo annee bareeon mimma tushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (1528, 'Min doonihi fakeedoonee jamee\'an thumma la tun _th_ iroon **i**');
INSERT INTO `en_transliteration` VALUES (1529, 'Innee tawakkatu \'ala Allahi rabbee warabbikum ma min dabbatin illa huwa akhithun binasiyatiha inna rabbee \'alasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (1530, 'Fain tawallaw faqad ablaghtukum ma orsiltu bihi ilaykum wayastakhlifu rabbee qawman ghayrakum wala tadurroonahu shayan inna rabbee \'ala kulli shayin hafee _th_ **un**');
INSERT INTO `en_transliteration` VALUES (1531, 'Walamma jaa amruna najjayna hoodan wa **a** llatheena amanoo ma\'ahu birahmatin minna wanajjaynahum min \'athabin ghalee _th_ **in**');
INSERT INTO `en_transliteration` VALUES (1532, 'Watilka \'adun jahadoo biayati rabbihim wa\'asaw rusulahu wa **i** ttaba\'oo amra kulli jabbarin \'aneed **in**');
INSERT INTO `en_transliteration` VALUES (1533, 'Waotbi\'oo fee hathihi a **l** ddunya la\'natan wayawma alqiyamati ala inna \'adan kafaroo rabbahum ala bu\'dan li\'adin qawmi hood **in**');
INSERT INTO `en_transliteration` VALUES (1534, 'Waila thamooda akhahum salihan qala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu huwa anshaakum mina alardi wa **i** sta\'marakum feeha fa **i** staghfiroohu thumma tooboo ilayhi inna rabbee qareebun mujeeb **un**');
INSERT INTO `en_transliteration` VALUES (1535, 'Qaloo yasalihu qad kunta feena marjuwwan qabla hatha atanhana an na\'buda ma ya\'budu abaona wainnana lafee shakkin mimma tad\'oona ilayhi mureeb **un**');
INSERT INTO `en_transliteration` VALUES (1536, 'Qala ya qawmi araaytum in kuntu \'ala bayyinatin min rabbee waatanee minhu rahmatan faman yansurunee mina Allahi in \'asaytuhu fama tazeedoonanee ghayra takhseer **in**');
INSERT INTO `en_transliteration` VALUES (1537, 'Waya qawmi hathihi naqatu Allahi lakum ayatan fatharooha takul fee ardi Allahi wala tamassooha bisooin fayakhuthakum \'athabun qareeb **un**');
INSERT INTO `en_transliteration` VALUES (1538, 'Fa\'aqarooha faqala tamatta\'oo fee darikum thalathata ayyamin thalika wa\'dun ghayru makthoob **in**');
INSERT INTO `en_transliteration` VALUES (1539, 'Falamma jaa amruna najjaynasalihan wa **a** llatheena amanoo ma\'ahu birahmatin minna wamin khizyi yawmiithin inna rabbaka huwa alqawiyyu al\'azeez **u**');
INSERT INTO `en_transliteration` VALUES (1540, 'Waakhatha allatheena _th_ alamoo a **l** ssayhatu faasbahoo fee diyarihim jathimeen **a**');
INSERT INTO `en_transliteration` VALUES (1541, 'Kaan lam yaghnaw feeha ala inna thamooda kafaroo rabbahum ala bu\'dan lithamood **a**');
INSERT INTO `en_transliteration` VALUES (1542, 'Walaqad jaat rusuluna ibraheema bi **a** lbushra qaloo salaman qala salamun fama labitha an jaa bi\'ijlin haneeth **in**');
INSERT INTO `en_transliteration` VALUES (1543, 'Falamma raa aydiyahum la tasilu ilayhi nakirahum waawjasa minhum kheefatan qaloo la takhaf inna orsilna ila qawmi loot **in**');
INSERT INTO `en_transliteration` VALUES (1544, 'Wa **i** mraatuhu qaimatun fadahikat fabashsharnaha biishaqa wamin warai ishaqa ya\'qoob **a**');
INSERT INTO `en_transliteration` VALUES (1545, 'Qalat ya waylata aalidu waana \'ajoozun wahatha ba\'lee shaykhan inna hatha lashayon \'ajeeb **un**');
INSERT INTO `en_transliteration` VALUES (1546, 'Qaloo ata\'jabeena min amri Allahi rahmatu Allahi wabarakatuhu \'alaykum ahla albayti innahu hameedun majeed **un**');
INSERT INTO `en_transliteration` VALUES (1547, 'Falammathahaba \'an ibraheema a **l** rraw\'u wajaathu albushra yujadiluna fee qawmi loot **in**');
INSERT INTO `en_transliteration` VALUES (1548, 'Inna ibraheema lahaleemun awwahun muneeb **un**');
INSERT INTO `en_transliteration` VALUES (1549, 'Ya ibraheemu a\'rid \'an hatha innahu qad jaa amru rabbika wainnahum ateehim \'athabun ghayru mardood **in**');
INSERT INTO `en_transliteration` VALUES (1550, 'Walamma jaat rusuluna lootan seea bihim wadaqa bihim thar\'an waqala hatha yawmun \'aseeb **un**');
INSERT INTO `en_transliteration` VALUES (1551, 'Wajaahu qawmuhu yuhra\'oona ilayhi wamin qablu kanoo ya\'maloona a **l** ssayyiati qala ya qawmi haolai banatee hunna atharu lakum fa **i** ttaqoo Allaha wala tukhzooni fee dayfee alaysa minkum rajulun rasheed **un**');
INSERT INTO `en_transliteration` VALUES (1552, 'Qaloo laqad \'alimta ma lana fee banatika min haqqin wainnaka lata\'lamu ma nureed **u**');
INSERT INTO `en_transliteration` VALUES (1553, 'Qala law anna lee bikum quwwatan aw awee ila ruknin shadeed **in**');
INSERT INTO `en_transliteration` VALUES (1554, 'Qaloo ya lootu inna rusulu rabbika lan yasiloo ilayka faasri biahlika biqit\'in mina allayli wala yatafit minkum ahadun illa imraataka innahu museebuha ma asabahum inna maw\'idahumu a **l** ssubhu alaysa a **l** ssubhu biqareeb **in**');
INSERT INTO `en_transliteration` VALUES (1555, 'Falamma jaa amruna ja\'ana \'aliyaha safilaha waamtarna \'alayhahijaratan min sijjeelin mandood **in**');
INSERT INTO `en_transliteration` VALUES (1556, 'Musawwamatan \'inda rabbika wama hiya mina a **l** _thth_ alimeena biba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (1557, 'Waila madyana akhahum shu\'ayban qala ya qawmi o\'budoo Allaha ma lakum min ilahin ghayruhu wala tanqusoo almikyala wa **a** lmeezana innee arakum bikhayrin wainnee akhafu \'alaykum \'athaba yawmin muheet **in**');
INSERT INTO `en_transliteration` VALUES (1558, 'Waya qawmi awfoo almikyala wa **a** lmeezana bi **a** lqisti wala tabkhasoo a **l** nnasa ashyaahum wala ta\'thaw fee alardi mufsideen **a**');
INSERT INTO `en_transliteration` VALUES (1559, 'Baqiyyatu Allahi khayrun lakum in kuntum mumineena wama ana \'alaykum bihafee _th_ **in**');
INSERT INTO `en_transliteration` VALUES (1560, 'Qaloo ya shu\'aybu asalatuka tamuruka an natruka ma ya\'budu abaona aw an naf\'ala fee amwalina ma nashao innaka laanta alhaleemu a **l** rrasheed **u**');
INSERT INTO `en_transliteration` VALUES (1561, 'Qala ya qawmi araaytum in kuntu \'ala bayyinatin min rabbee warazaqanee minhu rizqan hasanan wama oreedu an okhalifakum ila ma anhakum \'anhu in oreedu illa alislaha ma istata\'tu wama tawfeeqee illa bi **A** llahi \'alayhi tawakkatu wailayhi oneeb **u**');
INSERT INTO `en_transliteration` VALUES (1562, 'Waya qawmi la yajrimannakum shiqaqee an yuseebakum mithlu ma asaba qawma noohin aw qawma hoodin aw qawma salihin wama qawmu lootin minkum biba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (1563, 'Wa **i** staghfiroo rabbakum thumma tooboo ilayhi inna rabbee raheemun wadood **un**');
INSERT INTO `en_transliteration` VALUES (1564, 'Qaloo ya shu\'aybu ma nafqahu katheeran mimma taqoolu wainna lanaraka feenada\'eefan walawla rahtuka larajamnaka wama anta \'alayna bi\'azeez **in**');
INSERT INTO `en_transliteration` VALUES (1565, 'Qala ya qawmi arahtee a\'azzu \'alaykum mina Allahi wa **i** ttakhathtumoohu waraakum _th_ ihriyyan inna rabbee bima ta\'maloona muheet **un**');
INSERT INTO `en_transliteration` VALUES (1566, 'Waya qawmi i\'maloo \'ala makanatikum innee \'amilun sawfa ta\'lamoona man yateehi \'athabun yukhzeehi waman huwa kathibun wa **i** rtaqiboo inne ma\'akum raqeeb **un**');
INSERT INTO `en_transliteration` VALUES (1567, 'Walamma jaa amruna najjayna shu\'ayban wa **a** llatheena amanoo ma\'ahu birahmatin minna waakhathati allatheena _th_ alamoo a **l** ssayhatu faasbahoo fee diyarihim jathimeen **a**');
INSERT INTO `en_transliteration` VALUES (1568, 'Kaan lam yaghnaw feeha ala bu\'dan limadyana kama ba\'idat thamood **u**');
INSERT INTO `en_transliteration` VALUES (1569, 'Walaqad arsana moosa biayatina wasultanin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1570, 'Ila fir\'awna wamalaihi fa **i** ttaba\'oo amra fir\'awna wama amru fir\'awna birasheed **in**');
INSERT INTO `en_transliteration` VALUES (1571, 'Yaqdumu qawmahu yawma alqiyamati faawradahumu a **l** nnara wabisa alwirdu almawrood **u**');
INSERT INTO `en_transliteration` VALUES (1572, 'Waotbi\'oo fee hathihi la\'natan wayawma alqiyamati bisa a **l** rrifdu almarfood **u**');
INSERT INTO `en_transliteration` VALUES (1573, 'Thalika min anbai alqura naqussuhu \'alayka minha qaimun wahaseed **un**');
INSERT INTO `en_transliteration` VALUES (1574, 'Wama _th_ alamnahum walakin _th_ alamoo anfusahum fama aghnat \'anhum alihatuhumu allatee yad\'oona min dooni Allahi min shayin lamma jaa amru rabbika wama zadoohum ghayra tatbeeb **in**');
INSERT INTO `en_transliteration` VALUES (1575, 'Wakathalika akhthu rabbika itha akhatha alqura wahiya _th_ alimatun inna akhthahu aleemun shadeed **un**');
INSERT INTO `en_transliteration` VALUES (1576, 'Inna fee thalika laayatan liman khafa \'athaba alakhirati thalika yawmun majmoo\'un lahu a **l** nnasu wathalika yawmun mashhoodun');
INSERT INTO `en_transliteration` VALUES (1577, 'Wama nuakhkhiruhu illa liajalin ma\'dood **in**');
INSERT INTO `en_transliteration` VALUES (1578, 'Yawma yati la takallamu nafsun illa biithnihi faminhum shaqiyyun wasa\'eed **in**');
INSERT INTO `en_transliteration` VALUES (1579, 'Faamma allatheena shaqoo fafee a **l** nnari lahum feeha zafeerun washaheeq **un**');
INSERT INTO `en_transliteration` VALUES (1580, 'Khalideena feeha ma damati a **l** ssamawatu wa **a** lardu illa ma shaa rabbuka inna rabbaka fa\'\'alun lima yureed **u**');
INSERT INTO `en_transliteration` VALUES (1581, 'Waamma allatheena su\'idoo fafee aljannati khalideena feeha ma damati a **l** ssamawatu wa **a** lardu illa ma shaa rabbuka \'ataan ghayra majthooth **in**');
INSERT INTO `en_transliteration` VALUES (1582, 'Fala taku fee miryatin mimma ya\'budu haolai ma ya\'budoona illa kama ya\'budu abaohum min qablu wainna lamuwaffoohum naseebahum ghayra manqoos **in**');
INSERT INTO `en_transliteration` VALUES (1583, 'Walaqad atayna moosa alkitaba fa **i** khtulifa feehi walawla kalimatun sabaqat min rabbika laqudiya baynahum wainnahum lafee shakkin minhu mureeb **un**');
INSERT INTO `en_transliteration` VALUES (1584, 'Wainna kullan lamma layuwaffiyannahum rabbuka a\'malahum innahu bima ya\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (1585, 'Fa **i** staqim kama omirta waman taba ma\'aka wala tatghaw innahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (1586, 'Wala tarkanoo ila allatheena _th_ alamoo fatamassakumu a **l** nnaru wama lakum min dooni Allahi min awliyaa thumma la tunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (1587, 'Waaqimi a **l** ssalata tarafayi a **l** nnahari wazulafan mina allayli inna alhasanati yuthhibna a **l** ssayyiati thalika thikra li **l** ththakireen **a**');
INSERT INTO `en_transliteration` VALUES (1588, 'Wa **i** sbir fainna Allaha la yudee\'u ajra almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1589, 'Falawla kana mina alqurooni min qablikum oloo baqiyyatin yanhawna \'ani alfasadi fee alardi illa qaleelan mimman anjayna minhum wa **i** ttaba\'a allatheena _th_ alamoo ma otrifoo feehi wakanoo mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1590, 'Wama kana rabbuka liyuhlika alqura bi _th_ ulmin waahluha muslihoon **a**');
INSERT INTO `en_transliteration` VALUES (1591, 'Walaw shaa rabbuka laja\'ala a **l** nnasa ommatan wahidatan wala yazaloona mukhtalifeen **a**');
INSERT INTO `en_transliteration` VALUES (1592, 'Illa man rahima rabbuka walithalika khalaqahum watammat kalimatu rabbika laamlaanna jahannama mina aljinnati wa **al** nnasi ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (1593, 'Wakullan naqussu \'alayka min anbai a **l** rrusuli ma nuthabbitu bihi fuadaka wajaaka fee hathihi alhaqqu wamaw\'i _th_ atun wathikra lilmumineen **a**');
INSERT INTO `en_transliteration` VALUES (1594, 'Waqul lillatheena la yuminoona i\'maloo \'ala makanatikum inna \'amiloon **a**');
INSERT INTO `en_transliteration` VALUES (1595, 'Wa **i** nta _th_ iroo inna munta _th_ iroon **a**');
INSERT INTO `en_transliteration` VALUES (1596, 'Walillahi ghaybu a **l** ssamawati wa **a** lardi wailayhi yurja\'u alamru kulluhu fa **o** \'budhu watawakkal \'alayhi wama rabbuka bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1597, 'Aliflamra tilka ayatu alkitabi almubeen **u**');
INSERT INTO `en_transliteration` VALUES (1598, 'Inna anzanahu quranan \'arabiyyan la\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1599, 'Nahnu naqussu \'alayka ahsana alqasasi bima awhayna ilayka hatha alqurana wain kunta min qablihi lamina alghafileen **a**');
INSERT INTO `en_transliteration` VALUES (1600, 'Ith qala yoosufu liabeehi ya abati innee raaytu ahada \'ashara kawkaban wa **al** shshamsa wa **a** lqamara raaytuhum lee sajideen **a**');
INSERT INTO `en_transliteration` VALUES (1601, 'Qala ya bunayya la taqsus ruyaka \'ala ikhwatika fayakeedoo laka kaydan inna a **l** shshaytana lilinsani \'aduwwun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1602, 'Wakathalika yajtabeeka rabbuka wayu\'allimuka min taweeli alahadeethi wayutimmu ni\'matahu \'alayka wa\'alaali ya\'qooba kama atammaha \'ala abawayka min qablu ibraheema waishaqa inna rabbaka \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (1603, 'Laqad kana fee yoosufa waikhwatihi ayatun li **l** ssaileen **a**');
INSERT INTO `en_transliteration` VALUES (1604, 'Ith qaloo layoosufu waakhoohu ahabbu ila abeena minna wanahnu \'usbatun inna abana lafee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1605, 'Oqtuloo yoosufa awi itrahoohu ardan yakhlu lakum wajhu abeekum watakoonoo min ba\'dihi qawman saliheen **a**');
INSERT INTO `en_transliteration` VALUES (1606, 'Qala qailun minhum la taqtuloo yoosufa waalqoohu fee ghayabati aljubbi yataqithu ba\'du a **l** ssayyarati in kuntum fa\'ileen **a**');
INSERT INTO `en_transliteration` VALUES (1607, 'Qaloo ya abana ma laka la tamanna \'ala yoosufa wainna lahu lanasihoon **a**');
INSERT INTO `en_transliteration` VALUES (1608, 'Arsilhu ma\'ana ghadan yarta\' wayal\'ab wainna lahu lahafi _th_ oon **a**');
INSERT INTO `en_transliteration` VALUES (1609, 'Qala innee layahzununee an thathhaboo bihi waakhafu an yakulahu a **l** ththibu waantum \'anhu ghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (1610, 'Qaloo lain akalahu a **l** ththibu wanahnu \'usbatun inna ithan lakhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (1611, 'Falammathahaboo bihi waajma\'oo an yaj\'aloohu fee ghayabati aljubbi waawhayna ilayhi latunabiannahum biamrihim hatha wahum la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (1612, 'Wajaoo abahum \'ishaan yabkoon **a**');
INSERT INTO `en_transliteration` VALUES (1613, 'Qaloo ya abana innathahabna nastabiqu watarakna yoosufa \'inda mata\'ina faakalahu a **l** ththibu wama anta bimuminin lana walaw kunnasadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1614, 'Wajaoo \'ala qameesihi bidamin kathibin qala bal sawwalat lakum anfusukum amran fasabrun jameelun wa **A** llahu almusta\'anu \'ala ma tasifoon **a**');
INSERT INTO `en_transliteration` VALUES (1615, 'Wajaat sayyaratun faarsaloo waridahum faadla dalwahu qala ya bushra hatha ghulamun waasarroohu bida\'atan wa **A** llahu \'aleemun bima ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1616, 'Washarawhu bithamanin bakhsin darahima ma\'doodatin wakanoo feehi mina a **l** zzahideen **a**');
INSERT INTO `en_transliteration` VALUES (1617, 'Waqala allathee ishtarahu min misra li **i** mraatihi akrimee mathwahu \'asa an yanfa\'ana aw nattakhithahu waladan wakathalika makkanna liyoosufa fee alardi walinu\'allimahu min taweeli alahadeethi wa **A** llahu ghalibun \'ala amrihi walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1618, 'Walamma balagha ashuddahu ataynahu hukman wa\'ilman wakathalika najzee almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1619, 'Warawadathu allatee huwa fee baytiha \'an nafsihi waghallaqati alabwaba waqalat hayta laka qala ma\'atha Allahi innahu rabbee ahsana mathwaya innahu la yuflihu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (1620, 'Walaqad hammat bihi wahamma biha lawla an raa burhana rabbihi kathalika linasrifa \'anhu a **l** ssooa wa **a** lfahshaa innahu min \'ibadina almukhlaseen **a**');
INSERT INTO `en_transliteration` VALUES (1621, 'Wa **i** stabaqa albaba waqaddat qameesahu min duburin waalfaya sayyidaha lada albabi qalat ma jazao man arada biahlika sooan illa an yusjana aw \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1622, 'Qala hiya rawadatnee \'an nafsee washahida shahidun min ahliha in kana qameesuhu qudda min qubulin fasadaqat wahuwa mina alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (1623, 'Wain kana qameesuhu qudda min duburin fakathabat wahuwa mina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1624, 'Falamma raa qameesahu qudda min duburin qala innahu min kaydikunna inna kaydakunna \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (1625, 'Yoosufu a\'rid \'an hatha wa **i** staghfiree lithanbiki innaki kunti mina alkhatieen **a**');
INSERT INTO `en_transliteration` VALUES (1626, 'Waqala niswatun fee almadeenati imraatu al\'azeezi turawidu fataha \'an nafsihi qad shaghafahahubban inna lanaraha fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1627, 'Falamma sami\'at bimakrihinna arsalat ilayhinna waa\'tadat lahunna muttakaan waatat kulla wahidatin minhunna sikkeenan waqalati okhruj \'alayhinna falamma raaynahu akbarnahu waqatta\'na aydiyahunna waqulna hasha lillahi ma hatha basharan in hatha illa malakun kareem **un**');
INSERT INTO `en_transliteration` VALUES (1628, 'Qalat fathalikunna allathee lumtunnanee feehi walaqad rawadtuhu \'an nafsihi fa **i** st\'sama walain lam yaf\'al maamuruhu layusjananna walayakoonan mina a **l** ssaghireen **a**');
INSERT INTO `en_transliteration` VALUES (1629, 'Qala rabbi a **l** ssijnu ahabbu ilayya mimma yad\'oonanee ilayhi wailla tasrif \'annee kaydahunna asbu ilayhinna waakun mina aljahileen **a**');
INSERT INTO `en_transliteration` VALUES (1630, 'Fa **i** stajaba lahu rabbuhu fasarafa \'anhu kaydahunna innahu huwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (1631, 'Thumma bada lahum min ba\'di ma raawoo alayati layasjununnahu hattaheen **in**');
INSERT INTO `en_transliteration` VALUES (1632, 'Wadakhala ma\'ahu a **l** ssijna fatayani qala ahaduhuma innee aranee a\'siru khamran waqala alakharu innee aranee ahmilu fawqa rasee khubzan takulu a **l** ttayru minhu nabbina bitaweelihi inna naraka mina almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1633, 'Qala la yateekumata\'amun turzaqanihi illa nabbatukuma bitaweelihi qabla an yatiyakumathalikuma mimma \'allamanee rabbee innee taraktu millata qawmin la yuminoona bi **A** llahi wahum bi **a** lakhirati hum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1634, 'Wa **i** ttaba\'tu millata abaee ibraheema waishaqa waya\'qooba ma kana lana an nushrika bi **A** llahi min shayin thalika min fadli Allahi \'alayna wa\'ala a **l** nnasi walakinna akthara a **l** nnasi la yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1635, 'Yasahibayi a **l** ssijni aarbabun mutafarriqoona khayrun ami Allahu alwahidu alqahhar **u**');
INSERT INTO `en_transliteration` VALUES (1636, 'Ma ta\'budoona min doonihi illa asmaan sammaytumooha antum waabaokum ma anzala Allahu biha min sultanin ini alhukmu illa lillahi amara alla ta\'budoo illa iyyahu thalika a **l** ddeenu alqayyimu walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1637, 'Yasahibayi a **l** ssijni amma ahadukuma fayasqee rabbahu khamran waamma alakharu fayuslabu fatakulu a **l** ttayru min rasihi qudiya alamru allathee feehi tastaftiyan **i**');
INSERT INTO `en_transliteration` VALUES (1638, 'Waqala lillathee _th_ anna annahu najin minhuma othkurnee \'inda rabbika faansahu a **l** shshaytanu thikra rabbihi falabitha fee a **l** ssijni bid\'a sineen **a**');
INSERT INTO `en_transliteration` VALUES (1639, 'Waqala almaliku innee ara sab\'a baqaratin simanin yakuluhunna sab\'un \'ijafun wasab\'a sunbulatin khudrin waokhara yabisatin ya ayyuha almalao aftoonee fee ruyaya in kuntum li **l** rruya ta\'buroon **a**');
INSERT INTO `en_transliteration` VALUES (1640, 'Qaloo adghathu ahlamin wama nahnu bitaweeli alahlami bi\'alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1641, 'Waqala allathee naja minhuma wa **i** ddakara ba\'da ommatin ana onabbiokum bitaweelihi faarsiloon **i**');
INSERT INTO `en_transliteration` VALUES (1642, 'Yoosufu ayyuha a **l** ssiddeequ aftina fee sab\'i baqaratin simanin yakuluhunna sab\'un \'ijafun wasab\'i sunbulatin khudrin waokhara yabisatin la\'allee arji\'u ila a **l** nnasi la\'allahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1643, 'Qala tazra\'oona sab\'a sineena daaban famahasadtum fatharoohu fee sunbulihi illa qaleelan mimma takuloon **a**');
INSERT INTO `en_transliteration` VALUES (1644, 'Thumma yatee min ba\'di thalika sab\'un shidadun yakulna ma qaddamtum lahunna illa qaleelan mimma tuhsinoon **a**');
INSERT INTO `en_transliteration` VALUES (1645, 'Thumma yatee min ba\'di thalika \'amun feehi yughathu a **l** nnasu wafeehi ya\'siroon **a**');
INSERT INTO `en_transliteration` VALUES (1646, 'Waqala almaliku itoonee bihi falamma jaahu a **l** rrasoolu qala irji\' ila rabbika fa **i** salhu ma balu a **l** nniswati allatee qatta\'na aydiyahunna inna rabbee bikaydihinna \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1647, 'Qala ma khatbukunna ith rawadtunna yoosufa \'an nafsihi qulna hasha lillahi ma \'alimna \'alayhi min sooin qalati imraatu al\'azeezi alana hashasa alhaqqu ana rawadtuhu \'an nafsihi wainnahu lamina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1648, 'Thalika liya\'lama annee lam akhunhu bi **a** lghaybi waanna Allaha la yahdee kayda alkhaineen **a**');
INSERT INTO `en_transliteration` VALUES (1649, 'Wama obarrio nafsee inna a **l** nnafsa laammaratun bi **al** ssooi illa ma rahima rabbee inna rabbee ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1650, 'Waqala almaliku itoonee bihi astakhlishu linafsee falamma kallamahu qala innaka alyawma ladayna makeenun ameen **un**');
INSERT INTO `en_transliteration` VALUES (1651, 'Qala ij\'anee \'ala khazaini alardi innee hafee _th_ un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1652, 'Wakathalika makanna liyoosufa fee alardi yatabawwao minhahaythu yashao nuseebu birahmatina man nashao wala nudee\'u ajra almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1653, 'Walaajru alakhirati khayrun lillatheena amanoo wakanoo yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (1654, 'Wajaa ikhwatu yoosufa fadakhaloo \'alayhi fa\'arafahum wahum lahu munkiroon **a**');
INSERT INTO `en_transliteration` VALUES (1655, 'Walamma jahhazahum bijahazihim qala itoonee biakhin lakum min abeekum ala tarawna annee oofee alkayla waana khayru almunzileen **a**');
INSERT INTO `en_transliteration` VALUES (1656, 'Fain lam tatoonee bihi fala kayla lakum \'indee wala taqraboon **i**');
INSERT INTO `en_transliteration` VALUES (1657, 'Qaloo sanurawidu \'anhu abahu wainna lafa\'iloon **a**');
INSERT INTO `en_transliteration` VALUES (1658, 'Waqala lifityanihi ij\'aloo bida\'atahum fee rihalihim la\'allahum ya\'rifoonaha itha inqalaboo ila ahlihim la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (1659, 'Falamma raja\'oo ila abeehim qaloo ya abana muni\'a minna alkaylu faarsil ma\'ana akhana naktal wainna lahu lahafi _th_ oon **a**');
INSERT INTO `en_transliteration` VALUES (1660, 'Qala hal amanukum \'alayhi illa kama amintukum \'ala akheehi min qablu fa **A** llahu khayrun hafi _th_ an wahuwa arhamu a **l** rrahimeen **a**');
INSERT INTO `en_transliteration` VALUES (1661, 'Walamma fatahoo mata\'ahum wajadoo bida\'atahum ruddat ilayhim qaloo ya abana ma nabghee hathihi bida\'atuna ruddat ilayna wanameeru ahlana wanahfa _th_ u akhana wanazdadu kayla ba\'eerin thalika kaylun yaseer **un**');
INSERT INTO `en_transliteration` VALUES (1662, 'Qala lan orsilahu ma\'akum hatta tutooni mawthiqan mina Allahi latatunnanee bihi illa an yuhata bikum falammaatawhu mawthiqahum qala Allahu \'ala ma naqoolu wakeel **un**');
INSERT INTO `en_transliteration` VALUES (1663, 'Waqala ya baniyya la tadkhuloo min babin wahidin wa **o** dkhuloo min abwabin mutafarriqatin wama oghnee \'ankum mina Allahi min shayin ini alhukmu illa lillahi \'alayhi tawakkatu wa\'alayhi falyatawakkali almutawakkiloon **a**');
INSERT INTO `en_transliteration` VALUES (1664, 'Walamma dakhaloo min haythu amarahum aboohum ma kana yughnee \'anhum mina Allahi min shayin illahajatan fee nafsi ya\'qooba qadaha wainnahu lathoo \'ilmin lima \'allamnahu walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1665, 'Walamma dakhaloo \'ala yoosufa awa ilayhi akhahu qala innee ana akhooka fala tabtais bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (1666, 'Falamma jahhazahum bijahazihim ja\'ala a **l** ssiqayata fee rahli akheehi thumma aththana muaththinun ayyatuha al\'eeru innakum lasariqoon **a**');
INSERT INTO `en_transliteration` VALUES (1667, 'Qaloo waaqbaloo \'alayhim matha tafqidoon **a**');
INSERT INTO `en_transliteration` VALUES (1668, 'Qaloo nafqidu suwa\'a almaliki waliman jaa bihi himlu ba\'eerin waana bihi za\'eem **un**');
INSERT INTO `en_transliteration` VALUES (1669, 'Qaloo ta **A** llahi laqad \'alimtum ma jina linufsida fee alardi wama kunna sariqeen **a**');
INSERT INTO `en_transliteration` VALUES (1670, 'Qaloo fama jazaohu in kuntum kathibeen **a**');
INSERT INTO `en_transliteration` VALUES (1671, 'Qaloo jazaohu man wujida fee rahlihi fahuwa jazaohu kathalika najzee a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1672, 'Fabadaa biaw\'iyatihim qabla wi\'ai akheehi thumma istakhrajaha min wi\'ai akheehi kathalika kidna liyoosufa ma kana liyakhutha akhahu fee deeni almaliki illa an yashaa Allahu narfa\'u darajatin man nashao wafawqa kulli thee \'ilmin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1673, 'Qaloo in yasriq faqad saraqa akhun lahu min qablu faasarraha yoosufu fee nafsihi walam yubdiha lahum qala antum sharrun makanan wa **A** llahu a\'lamu bima tasifoon **a**');
INSERT INTO `en_transliteration` VALUES (1674, 'Qaloo ya ayyuha al\'azeezu inna lahu aban shaykhan kabeeran fakhuth ahadana makanahu inna naraka mina almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1675, 'Qala ma\'atha Allahi an nakhutha illa man wajadna mata\'ana \'indahu inna ithan la _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (1676, 'Falamma istayasoo minhu khalasoo najiyyan qala kabeeruhum alam ta\'lamoo anna abakum qad akhatha \'alaykum mawthiqan mina Allahi wamin qablu ma farrattum fee yoosufa falan abraha alarda hatta yathana lee abee aw yahkuma Allahu lee wahuwa khayru alhakimeen **a**');
INSERT INTO `en_transliteration` VALUES (1677, 'Irji\'oo ila abeekum faqooloo ya abana inna ibnaka saraqa wama shahidna illa bima \'alimna wama kunna lilghaybi hafi _th_ een **a**');
INSERT INTO `en_transliteration` VALUES (1678, 'Wa **i** sali alqaryata allatee kunna feeha wa **a** l\'eera allatee aqbana feeha wainna lasadiqoon **a**');
INSERT INTO `en_transliteration` VALUES (1679, 'Qala bal sawwalat lakum anfusukum amran fasabrun jameelun \'asa Allahu an yatiyanee bihim jamee\'an innahu huwa al\'aleemu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (1680, 'Watawalla \'anhum waqala ya asafa \'ala yoosufa wa **i** byaddat \'aynahu mina alhuzni fahuwa ka _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (1681, 'Qaloo ta **A** llahi taftao tathkuru yoosufa hatta takoona haradan aw takoona mina alhalikeen **a**');
INSERT INTO `en_transliteration` VALUES (1682, 'Qala innama ashkoo baththee wahuznee ila Allahi waa\'lamu mina Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1683, 'Ya baniyya ithhaboo fatahassasoo min yoosufa waakheehi wala tayasoo min rawhi Allahi innahu la yayasu min rawhi Allahi illa alqawmu alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (1684, 'Falamma dakhaloo \'alayhi qaloo ya ayyuha al\'azeezu massana waahlana a **l** ddurru wajina bibida\'atin muzjatin faawfi lana alkayla watasaddaq \'alayna inna Allaha yajzee almutasaddiqeen **a**');
INSERT INTO `en_transliteration` VALUES (1685, 'Qala hal \'alimtum ma fa\'atum biyoosufa waakheehi ith antum jahiloon **a**');
INSERT INTO `en_transliteration` VALUES (1686, 'Qaloo ainnaka laanta yoosufa qala ana yoosufu wahatha akhee qad manna Allahu \'alayna innahu man yattaqi wayasbir fainna Allaha la yudee\'u ajra almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (1687, 'Qaloo ta **A** llahi laqad atharaka Allahu \'alayna wain kunna lakhatieen **a**');
INSERT INTO `en_transliteration` VALUES (1688, 'Qala la tathreeba \'alaykumu alyawma yaghfiru Allahu lakum wahuwa arhamu a **l** rrahimeen **a**');
INSERT INTO `en_transliteration` VALUES (1689, 'Ithhaboo biqameesee hatha faalqoohu \'ala wajhi abee yati baseeran watoonee biahlikum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (1690, 'Walamma fasalati al\'eeru qala aboohum innee laajidu reeha yoosufa lawla an tufannidoon **i**');
INSERT INTO `en_transliteration` VALUES (1691, 'Qaloo ta **A** llahi innaka lafee dalalika alqadeem **i**');
INSERT INTO `en_transliteration` VALUES (1692, 'Falamma an jaa albasheeru alqahu \'ala wajhihi fa **i** rtadda baseeran qala alam aqul lakum innee a\'lamu mina Allahi ma la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1693, 'Qaloo ya abana istaghfir lanathunoobana inna kunna khatieen **a**');
INSERT INTO `en_transliteration` VALUES (1694, 'Qala sawfa astaghfiru lakum rabbee innahu huwa alghafooru a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (1695, 'Falamma dakhaloo \'ala yoosufa awa ilayhi abawayhi waqala odkhuloo misra in shaa Allahu amineen **a**');
INSERT INTO `en_transliteration` VALUES (1696, 'Warafa\'a abawayhi \'ala al\'arshi wakharroo lahu sujjadan waqala ya abati hatha taweelu ruyaya min qablu qad ja\'alaha rabbee haqqan waqad ahsana bee ith akhrajanee mina a **l** ssijni wajaa bikum mina albadwi min ba\'di an nazagha a **l** shshaytanu baynee wabayna ikhwatee inna rabbee lateefun lima yashao innahu huwa al\'aleemu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (1697, 'Rabbi qad ataytanee mina almulki wa\'allamtanee min taweeli alahadeethi fatira a **l** ssamawati wa **a** lardi anta waliyyee fee a **l** ddunya wa **a** lakhirati tawaffanee musliman waalhiqnee bi **al** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (1698, 'Thalika min anbai alghaybi nooheehi ilayka wama kunta ladayhim ith ajma\'oo amrahum wahum yamkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1699, 'Wama aktharu a **l** nnasi walaw harasta bimumineen **a**');
INSERT INTO `en_transliteration` VALUES (1700, 'Wama tasaluhum \'alayhi min ajrin in huwa illathikrun lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (1701, 'Wakaayyin min ayatin fee a **l** ssamawati wa **a** lardi yamurroona \'alayha wahum \'anha mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (1702, 'Wama yuminu aktharuhum bi **A** llahi illa wahum mushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (1703, 'Afaaminoo an tatiyahum ghashiyatun min \'athabi Allahi aw tatiyahumu a **l** ssa\'atu baghtatan wahum la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (1704, 'Qul hathihi sabeelee ad\'oo ila Allahi \'ala baseeratin ana wamani ittaba\'anee wasubhana Allahi wama ana mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (1705, 'Wama arsana min qablika illa rijalan noohee ilayhim min ahli alqura afalam yaseeroo fee alardi fayan _th_ uroo kayfa kana \'aqibatu allatheena min qablihim waladaru alakhirati khayrun lillatheena ittaqaw afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1706, 'Hatta itha istayasa a **l** rrusulu wa _th_ annoo annahum qad kuthiboo jaahum nasruna fanujjiya man nashao wala yuraddu basuna \'ani alqawmi almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (1707, 'Laqad kana fee qasasihim \'ibratun liolee alalbabi ma kana hadeethan yuftara walakin tasdeeqa allathee bayna yadayhi watafseela kulli shayin wahudan warahmatan liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1708, 'Aliflammeemra tilka ayatu alkitabi wa **a** llathee onzila ilayka min rabbika alhaqqu walakinna akthara a **l** nnasi la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1709, 'Allahu allathee rafa\'a a **l** ssamawati bighayri \'amadin tarawnaha thumma istawa \'ala al\'arshi wasakhkhara a **l** shshamsa wa **a** lqamara kullun yajree liajalin musamman yudabbiru alamra yufassilu alayati la\'allakum biliqai rabbikum tooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (1710, 'Wahuwa allathee madda alarda waja\'ala feeha rawasiya waanharan wamin kulli a **l** ththamarati ja\'ala feeha zawjayni ithnayni yughshee allayla a **l** nnahara inna fee thalika laayatin liqawmin yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1711, 'Wafee alardi qita\'un mutajawiratun wajannatun min a\'nabin wazar\'un wanakheelun sinwanun waghayru sinwanin yusqa bimain wahidin wanufaddilu ba\'daha \'ala ba\'din fee alokuli inna fee thalika laayatin liqawmin ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (1712, 'Wain ta\'jab fa\'ajabun qawluhum aitha kunna turaban ainna lafee khalqin jadeedin olaika allatheena kafaroo birabbihim waolaika alaghlalu fee a\'naqihim waolaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (1713, 'Wayasta\'jiloonaka bi **al** ssayyiati qabla alhasanati waqad khalat min qablihimu almathulatu wainna rabbaka lathoo maghfiratin li **l** nnasi \'ala _th_ ulmihim wainna rabbaka lashadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (1714, 'Wayaqoolu allatheena kafaroo lawla onzila \'alayhi ayatun min rabbihi innama anta munthirun walikulli qawmin had **in**');
INSERT INTO `en_transliteration` VALUES (1715, 'Allahu ya\'lamu ma tahmilu kullu ontha wama tagheedu alarhamu wama tazdadu wakullu shayin \'indahu bimiqdar **in**');
INSERT INTO `en_transliteration` VALUES (1716, '\'alimu alghaybi wa **al** shshahadati alkabeeru almuta\'al **i**');
INSERT INTO `en_transliteration` VALUES (1717, 'Sawaon minkum man asarra alqawla waman jahara bihi waman huwa mustakhfin bi **a** llayli wasaribun bi **al** nnahar **i**');
INSERT INTO `en_transliteration` VALUES (1718, 'Lahu mu\'aqqibatun min bayni yadayhi wamin khalfihi yahfa _th_ oonahu min amri Allahi inna Allaha la yughayyiru ma biqawmin hatta yughayyiroo ma bianfusihim waitha arada Allahu biqawmin sooan fala maradda lahu wama lahum min doonihi min wal **in**');
INSERT INTO `en_transliteration` VALUES (1719, 'Huwa allathee yureekumu albarqa khawfan watama\'an wayunshio a **l** ssahaba a **l** ththiqal **a**');
INSERT INTO `en_transliteration` VALUES (1720, 'Wayusabbihu a **l** rra\'du bihamdihi wa **a** lmalaikatu min kheefatihi wayursilu a **l** ssawa\'iqa fayuseebu biha man yashao wahum yujadiloona fee Allahi wahuwa shadeedu almihal **i**');
INSERT INTO `en_transliteration` VALUES (1721, 'Lahu da\'watu alhaqqi wa **a** llatheena yad\'oona min doonihi la yastajeeboona lahum bishayin illa kabasiti kaffayhi ila almai liyablugha fahu wama huwa bibalighihi wama du\'ao alkafireena illa fee dalal **in**');
INSERT INTO `en_transliteration` VALUES (1722, 'Walillahi yasjudu man fee a **l** ssamawati wa **a** lardi taw\'an wakarhan wa _th_ ilaluhum bi **a** lghuduwwi wa **a** lasal **i**');
INSERT INTO `en_transliteration` VALUES (1723, 'Qul man rabbu a **l** ssamawati wa **a** lardi quli Allahu qul afa **i** ttakhathtum min doonihi awliyaa la yamlikoona lianfusihim naf\'an waladarran qul hal yastawee ala\'ma wa **a** lbaseeru am hal tastawee a **l** _thth_ ulumatu wa **al** nnooru am ja\'aloo lillahi shurakaa khalaqoo kakhalqihi fatashabaha alkhalqu \'alayhim quli Allahu khaliqu kulli shayin wahuwa alwahidu alqahhar **u**');
INSERT INTO `en_transliteration` VALUES (1724, 'Anzala mina a **l** ssamai maan fasalat awdiyatun biqadariha fa **i** htamala a **l** ssaylu zabadan rabiyan wamimma yooqidoona \'alayhi fee a **l** nnari ibtighaa hilyatin aw mata\'in zabadun mithluhu kathalika yadribu Allahu alhaqqa wa **a** lbatila faamma a **l** zzabadu fayathhabu jufaan waamma ma yanfa\'u a **l** nnasa fayamkuthu fee alardi kathalika yadribu Allahu alamthal **a**');
INSERT INTO `en_transliteration` VALUES (1725, 'Lillatheena istajaboo lirabbihimu alhusna wa **a** llatheena lam yastajeeboo lahu law anna lahum ma fee alardi jamee\'an wamithlahu ma\'ahu la **i** ftadaw bihi olaika lahum sooo alhisabi wamawahum jahannamu wabisa almihad **u**');
INSERT INTO `en_transliteration` VALUES (1726, 'Afaman ya\'lamu annama onzila ilayka min rabbika alhaqqu kaman huwa a\'ma innama yatathakkaru oloo alalbab **i**');
INSERT INTO `en_transliteration` VALUES (1727, 'Allatheena yoofoona bi\'ahdi Allahi wala yanqudoona almeethaq **a**');
INSERT INTO `en_transliteration` VALUES (1728, 'Wa **a** llatheena yasiloona ma amara Allahu bihi an yoosala wayakhshawna rabbahum wayakhafoona sooa alhisab **i**');
INSERT INTO `en_transliteration` VALUES (1729, 'Wa **a** llatheena sabaroo ibtighaa wajhi rabbihim waaqamoo a **l** ssalata waanfaqoo mimma razaqnahum sirran wa\'alaniyatan wayadraoona bi **a** lhasanati a **l** ssayyiata olaika lahum \'uqba a **l** ddar **i**');
INSERT INTO `en_transliteration` VALUES (1730, 'Jannatu \'adnin yadkhuloonaha waman salaha min abaihim waazwajihim wathurriyyatihim wa **a** lmalaikatu yadkhuloona \'alayhim min kulli bab **in**');
INSERT INTO `en_transliteration` VALUES (1731, 'Salamun \'alaykum bimasabartum fani\'ma \'uqba a **l** ddar **i**');
INSERT INTO `en_transliteration` VALUES (1732, 'Wa **a** llatheena yanqudoona \'ahda Allahi min ba\'di meethaqihi wayaqta\'oona ma amara Allahu bihi an yoosala wayufsidoona fee alardi olaika lahumu alla\'natu walahum sooo a **l** ddar **i**');
INSERT INTO `en_transliteration` VALUES (1733, 'Allahu yabsutu a **l** rrizqa liman yashao wayaqdiru wafarihoo bi **a** lhayati a **l** ddunya wama alhayatu a **l** ddunya fee alakhirati illa mata\' **un**');
INSERT INTO `en_transliteration` VALUES (1734, 'Wayaqoolu allatheena kafaroo lawla onzila \'alayhi ayatun min rabbihi qul inna Allaha yudillu man yashao wayahdee ilayhi man anab **a**');
INSERT INTO `en_transliteration` VALUES (1735, 'Allatheena amanoo watatmainnu quloobuhum bithikri Allahi ala bithikri Allahi tatmainnu alquloob **u**');
INSERT INTO `en_transliteration` VALUES (1736, 'Allatheena amanoo wa\'amiloo a **l** ssalihati tooba lahum wahusnu maab **in**');
INSERT INTO `en_transliteration` VALUES (1737, 'Kathalika arsanaka fee ommatin qad khalat min qabliha omamun litatluwa \'alayhimu allathee awhayna ilayka wahum yakfuroona bi **al** rrahmani qul huwa rabbee la ilaha illa huwa \'alayhi tawakkatu wailayhi matab **i**');
INSERT INTO `en_transliteration` VALUES (1738, 'Walaw anna quranan suyyirat bihi aljibalu aw qutti\'at bihi alardu aw kullima bihi almawta bal lillahi alamru jamee\'an afalam yayasi allatheena amanoo an law yashao Allahu lahada a **l** nnasa jamee\'an wala yazalu allatheena kafaroo tuseebuhum bimasana\'oo qari\'atun aw tahullu qareeban min darihim hatta yatiya wa\'du Allahi inna Allaha la yukhlifu almee\'ad **a**');
INSERT INTO `en_transliteration` VALUES (1739, 'Walaqadi istuhzia birusulin min qablika faamlaytu lillatheena kafaroo thumma akhathtuhum fakayfa kana \'iqab **i**');
INSERT INTO `en_transliteration` VALUES (1740, 'Afaman huwa qaimun \'ala kulli nafsin bima kasabat waja\'aloo lillahi shurakaa qul sammoohum am tunabbioonahu bima la ya\'lamu fee alardi am bi _th_ ahirin mina alqawli bal zuyyina lillatheena kafaroo makruhum wasuddoo \'ani a **l** ssabeeli waman yudlili Allahu fama lahu min had **in**');
INSERT INTO `en_transliteration` VALUES (1741, 'Lahum \'athabun fee alhayati a **l** ddunya wala\'athabu alakhirati ashaqqu wama lahum mina Allahi min waq **in**');
INSERT INTO `en_transliteration` VALUES (1742, 'Mathalu aljannati allatee wu\'ida almuttaqoona tajree min tahtiha alanharu okuluha daimun wa _th_ illuha tilka \'uqba allatheena ittaqaw wa\'uqba alkafireena a **l** nnar **u**');
INSERT INTO `en_transliteration` VALUES (1743, 'Wa **a** llatheena ataynahumu alkitaba yafrahoona bima onzila ilayka wamina alahzabi man yunkiru ba\'dahu qul innama omirtu an a\'buda Allaha wala oshrika bihi ilayhi ad\'oo wailayhi maab **i**');
INSERT INTO `en_transliteration` VALUES (1744, 'Wakathalika anzanahu hukman \'arabiyyan walaini ittaba\'ta ahwaahum ba\'da ma jaaka mina al\'ilmi ma laka mina Allahi min waliyyin wala waq **in**');
INSERT INTO `en_transliteration` VALUES (1745, 'Walaqad arsana rusulan min qablika waja\'ana lahum azwajan wathurriyyatan wama kana lirasoolin an yatiya biayatin illa biithni Allahi likulli ajalin kitab **un**');
INSERT INTO `en_transliteration` VALUES (1746, 'Yamhoo Allahu ma yashao wayuthbitu wa\'indahu ommu alkitab **i**');
INSERT INTO `en_transliteration` VALUES (1747, 'Wain ma nuriyannaka ba\'da allathee na\'iduhum aw natawaffayannaka fainnama \'alayka albalaghu wa\'alayna alhisab **u**');
INSERT INTO `en_transliteration` VALUES (1748, 'Awalam yaraw anna natee alarda nanqusuha min atrafiha wa **A** llahu yahkumu la mu\'aqqiba lihukmihi wahuwa saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (1749, 'Waqad makara allatheena min qablihim falillahi almakru jamee\'an ya\'lamu ma taksibu kullu nafsin wasaya\'lamu alkuffaru liman \'uqba a **l** ddar **i**');
INSERT INTO `en_transliteration` VALUES (1750, 'Wayaqoolu allatheena kafaroo lasta mursalan qul kafa bi **A** llahi shaheedan baynee wabaynakum waman \'indahu \'ilmu alkitab **i**');
INSERT INTO `en_transliteration` VALUES (1751, 'Aliflamra kitabun anzanahu ilayka litukhrija a **l** nnasa mina a **l** _thth_ ulumati ila a **l** nnoori biithni rabbihim ilasirati al\'azeezi alhameed **i**');
INSERT INTO `en_transliteration` VALUES (1752, 'Allahi allathee lahu ma fee a **l** ssamawati wama fee alardi wawaylun lilkafireena min \'athabin shadeed **in**');
INSERT INTO `en_transliteration` VALUES (1753, 'Allatheena yastahibboona alhayata a **l** ddunya \'ala alakhirati wayasuddoona \'an sabeeli Allahi wayabghoonaha \'iwajan olaika fee dalalin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (1754, 'Wama arsana min rasoolin illa bilisani qawmihi liyubayyina lahum fayudillu Allahu man yashao wayahdee man yashao wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (1755, 'Walaqad arsana moosa biayatina an akhrij qawmaka mina a **l** _thth_ ulumati ila a **l** nnoori wathakkirhum biayyami Allahi inna fee thalika laayatin likulli sabbarin shakoor **in**');
INSERT INTO `en_transliteration` VALUES (1756, 'Waith qala moosa liqawmihi othkuroo ni\'mata Allahi \'alaykum ith anjakum min ali fir\'awna yasoomoonakum sooa al\'athabi wayuthabbihoona abnaakum wayastahyoona nisaakum wafee thalikum balaon min rabbikum \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (1757, 'Waith taaththana rabbukum lain shakartum laazeedannakum walain kafartum inna \'athabee lashadeed **un**');
INSERT INTO `en_transliteration` VALUES (1758, 'Waqala moosa in takfuroo antum waman fee alardi jamee\'an fainna Allaha laghaniyyun hameed **un**');
INSERT INTO `en_transliteration` VALUES (1759, 'Alam yatikum nabao allatheena min qablikum qawmi noohin wa\'adin wathamooda wa **a** llatheena min ba\'dihim la ya\'lamuhum illa Allahu jaathum rusuluhum bi **a** lbayyinati faraddoo aydiyahum fee afwahihim waqaloo inna kafarna bima orsiltum bihi wainna lafee shakkin mimma tad\'oonana ilayhi mureeb **un**');
INSERT INTO `en_transliteration` VALUES (1760, 'Qalat rusuluhum afee Allahi shakkun fatiri a **l** ssamawati wa **a** lardi yad\'ookum liyaghfira lakum min thunoobikum wayuakhkhirakum ila ajalin musamman qaloo in antum illa basharun mithluna tureedoona an tasuddoona \'amma kana ya\'budu abaona fatoona bisultanin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1761, 'Qalat lahum rusuluhum in nahnu illa basharun mithlukum walakinna Allaha yamunnu \'ala man yashao min \'ibadihi wama kana lana an natiyakum bisultanin illa biithni Allahi wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (1762, 'Wama lana alla natawakkala \'ala Allahi waqad hadana subulana walanasbiranna \'ala maathaytumoona wa\'ala Allahi falyatawakkali almutawakkiloon **a**');
INSERT INTO `en_transliteration` VALUES (1763, 'Waqala allatheena kafaroo lirusulihim lanukhrijannakum min ardina aw lata\'oodunna fee millatina faawha ilayhim rabbuhum lanuhlikanna a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (1764, 'Walanuskinannakumu alarda min ba\'dihim thalika liman khafa maqamee wakhafa wa\'eed **i**');
INSERT INTO `en_transliteration` VALUES (1765, 'Wa **i** staftahoo wakhaba kullu jabbarin \'aneed **in**');
INSERT INTO `en_transliteration` VALUES (1766, 'Min waraihi jahannamu wayusqa min main sadeed **in**');
INSERT INTO `en_transliteration` VALUES (1767, 'Yatajarra\'uhu wala yakadu yuseeghuhu wayateehi almawtu min kulli makanin wama huwa bimayyitin wamin waraihi \'athabun ghalee _th_ **un**');
INSERT INTO `en_transliteration` VALUES (1768, 'Mathalu allatheena kafaroo birabbihim a\'maluhum karamadin ishtaddat bihi a **l** rreehu fee yawmin \'asifin la yaqdiroona mimma kasaboo \'ala shayin thalika huwa a **l** ddalalu alba\'eed **u**');
INSERT INTO `en_transliteration` VALUES (1769, 'Alam tara anna Allaha khalaqa a **l** ssamawati wa **a** larda bi **a** lhaqqi in yasha yuthhibkum wayati bikhalqin jadeed **in**');
INSERT INTO `en_transliteration` VALUES (1770, 'Wamathalika \'ala Allahi bi\'azeez **in**');
INSERT INTO `en_transliteration` VALUES (1771, 'Wabarazoo lillahi jamee\'an faqala a **l** ddu\'afao lillatheena istakbaroo inna kunna lakum taba\'an fahal antum mughnoona \'anna min \'athabi Allahi min shayin qaloo law hadana Allahu lahadaynakum sawaon \'alayna ajazi\'na am sabarna ma lana min mahees **in**');
INSERT INTO `en_transliteration` VALUES (1772, 'Waqala a **l** shshaytanu lamma qudiya alamru inna Allaha wa\'adakum wa\'da alhaqqi wawa\'adtukum faakhlaftukum wama kana liya \'alaykum min sultanin illa an da\'awtukum fa **i** stajabtum lee fala taloomoonee waloomoo anfusakum ma ana bimusrikhikum wama antum bimusrikhiyya innee kafartu bima ashraktumooni min qablu inna a **l** _thth_ alimeena lahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (1773, 'Waodkhila allatheena amanoo wa\'amiloo a **l** ssalihati jannatin tajree min tahtiha alanharu khalideena feeha biithni rabbihim tahiyyatuhum feeha salam **un**');
INSERT INTO `en_transliteration` VALUES (1774, 'Alam tara kayfa daraba Allahu mathalan kalimatan tayyibatan kashajaratin tayyibatin asluha thabitun wafar\'uha fee a **l** ssama **i**');
INSERT INTO `en_transliteration` VALUES (1775, 'Tutee okulaha kulla heenin biithni rabbiha wayadribu Allahu alamthala li **l** nnasi la\'allahum yatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (1776, 'Wamathalu kalimatin khabeethatin kashajaratin khabeethatin ijtuththat min fawqi alardi ma laha min qarar **in**');
INSERT INTO `en_transliteration` VALUES (1777, 'Yuthabbitu Allahu allatheena amanoo bi **a** lqawli a **l** ththabiti fee alhayati a **l** ddunya wafee alakhirati wayudillu Allahu a **l** _thth_ alimeena wayaf\'alu Allahu ma yasha **o**');
INSERT INTO `en_transliteration` VALUES (1778, 'Alam tara ila allatheena baddaloo ni\'mata Allahi kufran waahalloo qawmahum dara albawar **i**');
INSERT INTO `en_transliteration` VALUES (1779, 'Jahannama yaslawnaha wabisa alqarar **u**');
INSERT INTO `en_transliteration` VALUES (1780, 'Waja\'aloo lillahi andadan liyudilloo \'an sabeelihi qul tamatta\'oo fainna maseerakum ila a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (1781, 'Qul li\'ibadiya allatheena amanoo yuqeemoo a **l** ssalata wayunfiqoo mimma razaqnahum sirran wa\'alaniyatan min qabli an yatiya yawmun la bay\'un feehi wala khilal **un**');
INSERT INTO `en_transliteration` VALUES (1782, 'Allahu allathee khalaqa a **l** ssamawati wa **a** larda waanzala mina a **l** ssamai maan faakhraja bihi mina a **l** ththamarati rizqan lakum wasakhkhara lakumu alfulka litajriya fee albahri biamrihi wasakhkhara lakumu alanhar **a**');
INSERT INTO `en_transliteration` VALUES (1783, 'Wasakhkhara lakumu a **l** shshamsa wa **a** lqamara daibayni wasakhkhara lakumu allayla wa **al** nnahar **a**');
INSERT INTO `en_transliteration` VALUES (1784, 'Waatakum min kulli ma saatumoohu wain ta\'uddoo ni\'mata Allahi la tuhsooha inna alinsana la _th_ aloomun kaffar **un**');
INSERT INTO `en_transliteration` VALUES (1785, 'Waith qala ibraheemu rabbi ij\'al hatha albalada aminan wa **o** jnubnee wabaniyya an na\'buda alasnam **a**');
INSERT INTO `en_transliteration` VALUES (1786, 'Rabbi innahunna adlana katheeran mina a **l** nnasi faman tabi\'anee fainnahu minnee waman \'asanee fainnaka ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (1787, 'Rabbana innee askantu min thurriyyatee biwadin ghayri thee zar\'in \'inda baytika almuharrami rabbana liyuqeemoo a **l** ssalata fa **i** j\'al afidatan mina a **l** nnasi tahwee ilayhim wa **o** rzuqhum mina a **l** ththamarati la\'allahum yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (1788, 'Rabbana innaka ta\'lamu ma nukhfee wama nu\'linu wama yakhfa \'ala Allahi min shayin fee alardi wala fee a **l** ssama **i**');
INSERT INTO `en_transliteration` VALUES (1789, 'Alhamdu lillahi allathee wahaba lee \'ala alkibari isma\'eela waishaqa inna rabbee lasamee\'u a **l** ddu\'a **i**');
INSERT INTO `en_transliteration` VALUES (1790, 'Rabbi ij\'anee muqeema a **l** ssalati wamin thurriyyatee rabbana wtaqabbal du\'a **i**');
INSERT INTO `en_transliteration` VALUES (1791, 'Rabbana ighfir lee waliwalidayya walilmumineena yawma yaqoomu alhisab **u**');
INSERT INTO `en_transliteration` VALUES (1792, 'Wala tahsabanna Allaha ghafilan \'amma ya\'malu a **l** _thth_ alimoona innama yuakhkhiruhum liyawmin tashkhasu feehi alabsar **u**');
INSERT INTO `en_transliteration` VALUES (1793, 'Muhti\'eena muqni\'ee ruoosihim la yartaddu ilayhim tarfuhum waafidatuhum hawa **on**');
INSERT INTO `en_transliteration` VALUES (1794, 'Waanthiri a **l** nnasa yawma yateehimu al\'athabu fayaqoolu allatheena _th_ alamoo rabbana akhkhirna ila ajalin qareebin nujib da\'wataka wanattabi\'i a **l** rrusula awalam takoonoo aqsamtum min qablu ma lakum min zawal **in**');
INSERT INTO `en_transliteration` VALUES (1795, 'Wasakantum fee masakini allatheena _th_ alamoo anfusahum watabayyana lakum kayfa fa\'ana bihim wadarabna lakumu alamthal **a**');
INSERT INTO `en_transliteration` VALUES (1796, 'Waqad makaroo makrahum wa\'inda Allahi makruhum wain kana makruhum litazoola minhu aljibal **u**');
INSERT INTO `en_transliteration` VALUES (1797, 'Fala tahsabanna Allaha mukhlifa wa\'dihi rusulahu inna Allaha \'azeezun thoo intiqam **in**');
INSERT INTO `en_transliteration` VALUES (1798, 'Yawma tubaddalu alardu ghayra alardi wa **al** ssamawatu wabarazoo lillahi alwahidi alqahhar **i**');
INSERT INTO `en_transliteration` VALUES (1799, 'Watara almujrimeena yawmaithin muqarraneena fee alasfad **i**');
INSERT INTO `en_transliteration` VALUES (1800, 'Sarabeeluhum min qatranin wataghsha wujoohahumu a **l** nnar **u**');
INSERT INTO `en_transliteration` VALUES (1801, 'Liyajziya Allahu kulla nafsin ma kasabat inna Allaha saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (1802, 'Hatha balaghun li **l** nnasi waliyuntharoo bihi waliya\'lamoo annama huwa ilahun wahidun waliyaththakkara oloo alalbab **i**');
INSERT INTO `en_transliteration` VALUES (1803, 'Aliflamra tilka ayatu alkitabi waquranin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (1804, 'Rubama yawaddu allatheena kafaroo law kanoo muslimeen **a**');
INSERT INTO `en_transliteration` VALUES (1805, 'Tharhum yakuloo wayatamatta\'oo wayulhihimu alamalu fasawfa ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (1806, 'Wama ahlakna min qaryatin illa walaha kitabun ma\'loom **un**');
INSERT INTO `en_transliteration` VALUES (1807, 'Ma tasbiqu min ommatin ajalaha wama yastakhiroon **a**');
