INSERT INTO `en_transliteration` VALUES (1816, 'Walaw fatahna \'alayhim baban mina a **l** ssamai fa _th_ alloo feehi ya\'rujoon **a**');
INSERT INTO `en_transliteration` VALUES (1817, 'Laqaloo innama sukkirat absaruna bal nahnu qawmun mashooroon **a**');
INSERT INTO `en_transliteration` VALUES (1818, 'Walaqad ja\'ana fee a **l** ssamai buroojan wazayyannaha li **l** nna _th_ ireen **a**');
INSERT INTO `en_transliteration` VALUES (1819, 'Wahafi _th_ naha min kulli shaytanin rajeem **in**');
INSERT INTO `en_transliteration` VALUES (1820, 'Illa mani istaraqa a **l** ssam\'a faatba\'ahu shihabun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (1821, 'Wa **a** larda madadnaha waalqayna feeha rawasiya waanbatna feeha min kulli shayin mawzoon **in**');
