INSERT INTO `en_transliteration` VALUES (1827, 'Wainna rabbaka huwa yahshuruhum innahu hakeemun \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (1828, 'Walaqad khalaqna alinsana min sasalin min hamain masnoon **in**');
INSERT INTO `en_transliteration` VALUES (1829, 'Wa **a** ljanna khalaqnahu min qablu min nari a **l** ssamoom **i**');
INSERT INTO `en_transliteration` VALUES (1830, 'Waith qala rabbuka lilmalaikati innee khaliqun basharan min sasalin min hamain masnoon **in**');
INSERT INTO `en_transliteration` VALUES (1831, 'Faitha sawwaytuhu wanafakhtu feehi min roohee faqa\'oo lahu sajideen **a**');
