INSERT INTO `en_transliteration` VALUES (2094, 'Inna \'ibadee laysa laka \'alayhim sultanun wakafa birabbika wakeela **n**');
INSERT INTO `en_transliteration` VALUES (2095, 'Rabbukumu allathee yuzjee lakumu alfulka fee albahri litabtaghoo min fadlihi innahu kana bikum raheema **n**');
INSERT INTO `en_transliteration` VALUES (2096, 'Waitha massakumu a **l** ddurru fee albahri dalla man tad\'oona illa iyyahu falamma najjakum ila albarri a\'radtum wakana alinsanu kafoora **n**');
INSERT INTO `en_transliteration` VALUES (2097, 'Afaamintum an yakhsifa bikum janiba albarri aw yursila \'alaykum hasiban thumma la tajidoo lakum wakeela **n**');
INSERT INTO `en_transliteration` VALUES (2098, 'Am amintum an yu\'eedakum feehi taratan okhra fayursila \'alaykum qasifan mina a **l** rreehi fayughriqakum bima kafartum thumma la tajidoo lakum \'alayna bihi tabee\'a **n**');
INSERT INTO `en_transliteration` VALUES (2099, 'Walaqad karramna banee adama wahamanahum fee albarri wa **a** lbahri warazaqnahum mina a **l** ttayyibati wafaddanahum \'ala katheerin mimman khalaqna tafdeela **n**');
INSERT INTO `en_transliteration` VALUES (2100, 'Yawma nad\'oo kulla onasin biimamihim faman ootiya kitabahu biyameenihi faolaika yaqraoona kitabahum wala yu _th_ lamoona fateela **n**');
INSERT INTO `en_transliteration` VALUES (2101, 'Waman kana fee hathihi a\'ma fahuwa fee alakhirati a\'ma waadallu sabeela **n**');
INSERT INTO `en_transliteration` VALUES (2102, 'Wain kadoo layaftinoonaka \'ani allathee awhayna ilayka litaftariya \'alayna ghayrahu waithan la **i** ttakhathooka khaleela **n**');
INSERT INTO `en_transliteration` VALUES (2103, 'Walawla an thabbatnaka laqad kidta tarkanu ilayhim shayan qaleela **n**');
INSERT INTO `en_transliteration` VALUES (2104, 'Ithan laathaqnaka di\'fa alhayati wadi\'fa almamati thumma la tajidu laka \'alayna naseera **n**');
INSERT INTO `en_transliteration` VALUES (2105, 'Wain kadoo layastafizzoonaka mina alardi liyukhrijooka minha waithan la yalbathoona khilafaka illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (2106, 'Sunnata man qad arsana qablaka min rusulina wala tajidu lisunnatina tahweela **n**');
INSERT INTO `en_transliteration` VALUES (2107, 'Aqimi a **l** ssalata lidulooki a **l** shshamsi ila ghasaqi allayli waqurana alfajri inna qurana alfajri kana mashhooda **n**');
INSERT INTO `en_transliteration` VALUES (2108, 'Wamina allayli fatahajjad bihi nafilatan laka \'asa an yab\'athaka rabbuka maqaman mahmooda **n**');
INSERT INTO `en_transliteration` VALUES (2109, 'Waqul rabbi adkhilnee mudkhala sidqin waakhrijnee mukhraja sidqin wa **i** j\'al lee min ladunka sultanan naseera **n**');
INSERT INTO `en_transliteration` VALUES (2110, 'Waqul jaa alhaqqu wazahaqa albatilu inna albatila kana zahooqa **n**');
INSERT INTO `en_transliteration` VALUES (2111, 'Wanunazzilu mina alqurani ma huwa shifaon warahmatun lilmumineena wala yazeedu a **l** _thth_ alimeena illa khasara **n**');
INSERT INTO `en_transliteration` VALUES (2112, 'Waitha an\'amna \'ala alinsani a\'rada wanaa bijanibihi waitha massahu a **l** shsharru kana yaoosa **n**');
INSERT INTO `en_transliteration` VALUES (2113, 'Qul kullun ya\'malu \'ala shakilatihi farabbukum a\'lamu biman huwa ahda sabeela **n**');
INSERT INTO `en_transliteration` VALUES (2114, 'Wayasaloonaka \'ani a **l** rroohi quli a **l** rroohu min amri rabbee wama ooteetum mina al\'ilmi illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (2115, 'Walain shina lanathhabanna bi **a** llathee awhayna ilayka thumma la tajidu laka bihi \'alayna wakeela **n**');
INSERT INTO `en_transliteration` VALUES (2116, 'Illa rahmatan min rabbika inna fadlahu kana \'alayka kabeera **n**');
INSERT INTO `en_transliteration` VALUES (2117, 'Qul laini ijtama\'ati alinsu wa **a** ljinnu \'ala an yatoo bimithli hatha alqurani la yatoona bimithlihi walaw kana ba\'duhum liba\'din _th_ aheera **n**');
INSERT INTO `en_transliteration` VALUES (2118, 'Walaqad sarrafna li **l** nnasi fee hatha alqurani min kulli mathalin faaba aktharu a **l** nnasi illa kufoora **n**');
INSERT INTO `en_transliteration` VALUES (2119, 'Waqaloo lan numina laka hatta tafjura lana mina alardi yanboo\'a **n**');
INSERT INTO `en_transliteration` VALUES (2120, 'Aw takoona laka jannatun min nakheelin wa\'inabin fatufajjira alanhara khilalaha tafjeera **n**');
INSERT INTO `en_transliteration` VALUES (2121, 'Aw tusqita a **l** ssamaa kama za\'amta \'alayna kisafan aw tatiya bi **A** llahi wa **a** lmalaikati qabeela **n**');
INSERT INTO `en_transliteration` VALUES (2122, 'Aw yakoona laka baytun min zukhrufin aw tarqa fee a **l** ssamai walan numina liruqiyyika hatta tunazzila \'alayna kitaban naqraohu qul subhana rabbee hal kuntu illa basharan rasoola **n**');
INSERT INTO `en_transliteration` VALUES (2123, 'Wama mana\'a a **l** nnasa an yuminoo ith jaahumu alhuda illa an qaloo aba\'atha Allahu basharan rasoola **n**');
INSERT INTO `en_transliteration` VALUES (2124, 'Qul law kana fee alardi malaikatun yamshoona mutmainneena lanazzana \'alayhim mina a **l** ssamai malakan rasoola **n**');
INSERT INTO `en_transliteration` VALUES (2125, 'Qul kafa bi **A** llahi shaheedan baynee wabaynakum innahu kana bi\'ibadihi khabeeran baseera **n**');
INSERT INTO `en_transliteration` VALUES (2126, 'Waman yahdi Allahu fahuwa almuhtadi waman yudlil falan tajida lahum awliyaa min doonihi wanahshuruhum yawma alqiyamati \'ala wujoohihim \'umyan wabukman wasumman mawahum jahannamu kullama khabat zidnahum sa\'eera **n**');
INSERT INTO `en_transliteration` VALUES (2127, 'Thalika jazaohum biannahum kafaroo biayatina waqaloo aitha kunna \'i _th_ aman warufatan ainna lamab\'oothoona khalqan jadeeda **n**');
INSERT INTO `en_transliteration` VALUES (2128, 'Awalam yaraw anna Allaha allathee khalaqa a **l** ssamawati wa **a** larda qadirun \'ala an yakhluqa mithlahum waja\'ala lahum ajalan la rayba feehi faaba a **l** _thth_ alimoona illa kufoora **n**');
INSERT INTO `en_transliteration` VALUES (2129, 'Qul law antum tamlikoona khazaina rahmati rabbee ithan laamsaktum khashyata alinfaqi wakana alinsanu qatoora **n**');
INSERT INTO `en_transliteration` VALUES (2130, 'Walaqad atayna moosa tis\'a ayatin bayyinatin fa **i** sal banee israeela ith jaahum faqala lahu fir\'awnu innee laa _th_ unnuka ya moosa mashoora **n**');
INSERT INTO `en_transliteration` VALUES (2131, 'Qala laqad \'alimta ma anzala haolai illa rabbu a **l** ssamawati wa **a** lardi basaira wainnee laa _th_ unnuka ya fir\'awnu mathboora **n**');
INSERT INTO `en_transliteration` VALUES (2132, 'Faarada an yastafizzahum mina alardi faaghraqnahu waman ma\'ahu jamee\'a **n**');
INSERT INTO `en_transliteration` VALUES (2133, 'Waqulna min ba\'dihi libanee israeela oskunoo alarda faitha jaa wa\'du alakhirati jina bikum lafeefa **n**');
INSERT INTO `en_transliteration` VALUES (2134, 'Wabi **a** lhaqqi anzanahu wabi **a** lhaqqi nazala wama arsanaka illa mubashshiran wanatheera **n**');
INSERT INTO `en_transliteration` VALUES (2135, 'Waquranan faraqnahu litaqraahu \'ala a **l** nnasi \'ala mukthin wanazzanahu tanzeela **n**');
INSERT INTO `en_transliteration` VALUES (2136, 'Qul aminoo bihi aw la tuminoo inna allatheena ootoo al\'ilma min qablihi itha yutla \'alayhim yakhirroona lilathqani sujjada **n**');
INSERT INTO `en_transliteration` VALUES (2137, 'Wayaqooloona subhana rabbina in kana wa\'du rabbina lamaf\'oola **n**');
INSERT INTO `en_transliteration` VALUES (2138, 'Wayakhirroona lilathqani yabkoona wayazeeduhum khushoo\'a **n**');
INSERT INTO `en_transliteration` VALUES (2139, 'Quli od\'oo Allaha awi od\'oo a **l** rrahmana ayyan ma tad\'oo falahu alasmao alhusna wala tajhar bisalatika wala tukhafit biha wa **i** btaghi bayna thalika sabeela **n**');
INSERT INTO `en_transliteration` VALUES (2140, 'Waquli alhamdu lillahi allathee lam yattakhith waladan walam yakun lahu shareekun fee almulki walam yakun lahu waliyyun mina a **l** ththulli wakabbirhu takbeera **n**');
INSERT INTO `en_transliteration` VALUES (2141, 'Alhamdu lillahi allathee anzala \'ala \'abdihi alkitaba walam yaj\'al lahu \'iwaja **n**');
INSERT INTO `en_transliteration` VALUES (2142, 'Qayyiman liyunthira basan shadeedan min ladunhu wayubashshira almumineena allatheena ya\'maloona a **l** ssalihati anna lahum ajran hasana **n**');
INSERT INTO `en_transliteration` VALUES (2143, 'Makitheena feehi abada **n**');
INSERT INTO `en_transliteration` VALUES (2144, 'Wayunthira allatheena qaloo ittakhatha Allahu walada **n**');
INSERT INTO `en_transliteration` VALUES (2145, 'Ma lahum bihi min \'ilmin wala liabaihim kaburat kalimatan takhruju min afwahihim in yaqooloona illa kathiba **n**');
INSERT INTO `en_transliteration` VALUES (2146, 'Fala\'allaka bakhi\'un nafsaka \'alaatharihim in lam yuminoo bihatha alhadeethi asafa **n**');
INSERT INTO `en_transliteration` VALUES (2147, 'Inna ja\'ana ma \'ala alardi zeenatan laha linabluwahum ayyuhum ahsanu \'amala **n**');
INSERT INTO `en_transliteration` VALUES (2148, 'Wainna laja\'iloona ma \'alayhasa\'eedan juruza **n**');
INSERT INTO `en_transliteration` VALUES (2149, 'Am hasibta anna ashaba alkahfi wa **al** rraqeemi kanoo min ayatina \'ajaba **n**');
INSERT INTO `en_transliteration` VALUES (2150, 'Ith awa alfityatu ila alkahfi faqaloo rabbanaatina min ladunka rahmatan wahayyi lana min amrina rashada **n**');
INSERT INTO `en_transliteration` VALUES (2151, 'Fadarabna \'alaathanihim fee alkahfi sineena \'adada **n**');
INSERT INTO `en_transliteration` VALUES (2152, 'Thumma ba\'athnahum lina\'lama ayyu alhizbayni ahsa lima labithoo amada **n**');
INSERT INTO `en_transliteration` VALUES (2153, 'Nahnu naqussu \'alayka nabaahum bi **a** lhaqqi innahum fityatun amanoo birabbihim wazidnahum huda **n**');
INSERT INTO `en_transliteration` VALUES (2154, 'Warabatna \'ala quloobihim ith qamoo faqaloo rabbuna rabbu a **l** ssamawati wa **a** lardi lan nad\'uwa min doonihi ilahan laqad qulna ithan shatata **n**');
INSERT INTO `en_transliteration` VALUES (2155, 'Haolai qawmuna ittakhathoo min doonihi alihatan lawla yatoona \'alayhim bisultanin bayyinin faman a _th_ lamu mimmani iftara \'ala Allahi kathiba **n**');
INSERT INTO `en_transliteration` VALUES (2156, 'Waithi i\'tazatumoohum wama ya\'budoona illa Allaha fawoo ila alkahfi yanshur lakum rabbukum min rahmatihi wayuhayyi lakum min amrikum mirfaqa **n**');
INSERT INTO `en_transliteration` VALUES (2157, 'Watara a **l** shshamsa ithatala\'at tazawaru \'an kahfihim thata alyameeni waitha gharabat taqriduhum thata a **l** shshimali wahum fee fajwatin minhu thalika min ayati Allahi man yahdi Allahu fahuwa almuhtadi waman yudlil falan tajida lahu waliyyan murshida **n**');
INSERT INTO `en_transliteration` VALUES (2158, 'Watahsabuhum ayqa _th_ an wahum ruqoodun wanuqallibuhum thata alyameeni wathata a **l** shshimali wakalbuhum basitun thira\'ayhi bi **a** lwaseedi lawi ittala\'ta \'alayhim lawallayta minhum firaran walamulita minhum ru\'ba **n**');
INSERT INTO `en_transliteration` VALUES (2159, 'Wakathalika ba\'athnahum liyatasaaloo baynahum qala qailun minhum kam labithtum qaloo labithna yawman aw ba\'da yawmin qaloo rabbukum a\'lamu bima labithtum fa **i** b\'athoo ahadakum biwariqikum hathihi ila almadeenati falyan _th_ ur ayyuha azkata\'aman falyatikum birizqin minhu walyatalattaf wala yush\'iranna bikum ahada **n**');
INSERT INTO `en_transliteration` VALUES (2160, 'Innahum in ya _th_ haroo \'alaykum yarjumookum aw yu\'eedookum fee millatihim walan tuflihoo ithan abada **n**');
INSERT INTO `en_transliteration` VALUES (2161, 'Wakathalika a\'tharna \'alayhim liya\'lamoo anna wa\'da Allahi haqqun waanna a **l** sa\'ata la rayba feeha ith yatanaza\'oona baynahum amrahum faqaloo ibnoo \'alayhim bunyanan rabbuhum a\'lamu bihim qala allatheena ghalaboo \'ala amrihim lanattakhithanna \'alayhim masjida **n**');
INSERT INTO `en_transliteration` VALUES (2162, 'Sayaqooloona thalathatun rabi\'uhum kalbuhum wayaqooloona khamsatun sadisuhum kalbuhum rajman bi **a** lghaybi wayaqooloona sab\'atun wathaminuhum kalbuhum qul rabbee a\'lamu bi\'iddatihim ma ya\'lamuhum illa qaleelun fala tumari feehim illa miraan _th_ ahiran wala tastafti feehim minhum ahada **n**');
INSERT INTO `en_transliteration` VALUES (2163, 'Wala taqoolanna lishayin innee fa\'ilun thalika ghada **n**');
INSERT INTO `en_transliteration` VALUES (2164, 'Illa an yashaa Allahu wa **o** thkur rabbaka itha naseeta waqul \'asa an yahdiyani rabbee liaqraba min hatha rashada **n**');
INSERT INTO `en_transliteration` VALUES (2165, 'Walabithoo fee kahfihim thalatha miatin sineena wa **i** zdadoo tis\'a **n**');
INSERT INTO `en_transliteration` VALUES (2166, 'Quli Allahu a\'lamu bima labithoo lahu ghaybu a **l** ssamawati wa **a** lardi absir bihi waasmi\' ma lahum min doonihi min waliyyin wala yushriku fee hukmihi ahada **n**');
INSERT INTO `en_transliteration` VALUES (2167, 'Wa **o** tlu ma oohiya ilayka min kitabi rabbika la mubaddila likalimatihi walan tajida min doonihi multahada **n**');
INSERT INTO `en_transliteration` VALUES (2168, 'Wa **i** sbir nafsaka ma\'a allatheena yad\'oona rabbahum bi **a** lghadati wa **a** l\'ashiyyi yureedoona wajhahu wala ta\'du \'aynaka \'anhum tureedu zeenata alhayati a **l** ddunya wala tuti\' man aghfana qalbahu \'an thikrina wa **i** ttaba\'a hawahu wakana amruhu furuta **n**');
INSERT INTO `en_transliteration` VALUES (2169, 'Waquli alhaqqu min rabbikum faman shaa falyumin waman shaa falyakfur inna a\'tadna li **l** _thth_ alimeena naran ahata bihim suradiquha wain yastagheethoo yughathoo bimain ka **a** lmuhli yashwee alwujooha bisa a **l** shsharabu wasaat murtafaqa **n**');
INSERT INTO `en_transliteration` VALUES (2170, 'Inna allatheena amanoo wa\'amiloo a **l** ssalihati inna la nudee\'u ajra man ahsana \'amala **n**');
INSERT INTO `en_transliteration` VALUES (2171, 'Olaika lahum jannatu \'adnin tajree min tahtihimu alanharu yuhallawna feeha min asawira min thahabin way **a** lbasoona thiyaban khudran min sundusin waistabraqin muttakieena feeha \'ala alaraiki ni\'ma a **l** ththawabu wahasunat murtafaqa **n**');
INSERT INTO `en_transliteration` VALUES (2172, 'Wa **i** drib lahum mathalan rajulayni ja\'ana liahadihima jannatayni min a\'nabin wahafafnahuma binakhlin waja\'ana baynahuma zar\'a **n**');
INSERT INTO `en_transliteration` VALUES (2173, 'Kilta aljannatayni atat okulaha walam ta _th_ lim minhu shayan wafajjarna khilalahuma nahara **n**');
INSERT INTO `en_transliteration` VALUES (2174, 'Wakana lahu thamarun faqala lisahibihi wahuwa yuhawiruhu ana aktharu minka malan waa\'azzu nafara **n**');
INSERT INTO `en_transliteration` VALUES (2175, 'Wadakhala jannatahu wahuwa _th_ alimun linafsihi qala ma a _th_ unnu an tabeeda hathihi abada **n**');
INSERT INTO `en_transliteration` VALUES (2176, 'Wama a _th_ unnu a **l** ssa\'ata qaimatan walain rudidtu ila rabbee laajidanna khayran minha munqalaba **n**');
INSERT INTO `en_transliteration` VALUES (2177, 'Qala lahu sahibuhu wahuwa yuhawiruhu akafarta bi **a** llathee khalaqaka min turabin thumma min nutfatin thumma sawwaka rajula **n**');
INSERT INTO `en_transliteration` VALUES (2178, 'Lakinna huwa Allahu rabbee wala oshriku birabbee ahada **n**');
INSERT INTO `en_transliteration` VALUES (2179, 'Walawla ith dakhata jannataka qulta ma shaa Allahu la quwwata illa bi **A** llahi in tarani ana aqalla minka malan wawalada **n**');
INSERT INTO `en_transliteration` VALUES (2180, 'Fa\'asa rabbee an yutiyani khayran min jannatika wayursila \'alayhahusbanan mina a **l** ssamai fatusbiha sa\'eedan zalaqa **n**');
INSERT INTO `en_transliteration` VALUES (2181, 'Aw yusbiha maoha ghawran falan tastatee\'a lahu talaba **n**');
INSERT INTO `en_transliteration` VALUES (2182, 'Waoheeta bithamarihi faasbaha yuqallibu kaffayhi \'ala ma anfaqa feeha wahiya khawiyatun \'ala \'urooshiha wayaqoolu ya laytanee lam oshrik birabbee ahada **n**');
INSERT INTO `en_transliteration` VALUES (2183, 'Walam takun lahu fiatun yansuroonahu min dooni Allahi wama kana muntasira **n**');
INSERT INTO `en_transliteration` VALUES (2184, 'Hunalika alwalayatu lillahi alhaqqi huwa khayrun thawaban wakhayrun \'uqba **n**');
INSERT INTO `en_transliteration` VALUES (2185, 'Wa **i** drib lahum mathala alhayati a **l** ddunya kamain anzanahu mina a **l** ssamai fa **i** khtalata bihi nabatu alardi faasbaha hasheeman tathroohu a **l** rriyahu wakana Allahu \'ala kulli shayin muqtadira **n**');
INSERT INTO `en_transliteration` VALUES (2186, 'Almalu wa **a** lbanoona zeenatu alhayati a **l** ddunya wa **a** lbaqiyatu a **l** ssalihatu khayrun \'inda rabbika thawaban wakhayrun amala **n**');
INSERT INTO `en_transliteration` VALUES (2187, 'Wayawma nusayyiru aljibala watara alarda barizatan wahasharnahum falam nughadir minhum ahada **n**');
INSERT INTO `en_transliteration` VALUES (2188, 'Wa\'uridoo \'ala rabbika saffan laqad jitumoona kama khalaqnakum awwala marratin bal za\'amtum allan naj\'ala lakum maw\'ida **n**');
INSERT INTO `en_transliteration` VALUES (2189, 'Wawudi\'a alkitabu fatara almujrimeena mushfiqeena mimma feehi wayaqooloona ya waylatana ma lihatha alkitabi la yughadiru sagheeratan wala kabeeratan illa ahsaha wawajadoo ma \'amiloo hadiran wala ya _th_ limu rabbuka ahada **n**');
INSERT INTO `en_transliteration` VALUES (2190, 'Waith qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa kana mina aljinni fafasaqa \'an amri rabbihi afatattakhithoonahu wathurriyyatahu awliyaa min doonee wahum lakum \'aduwwun bisa li **l** _thth_ alimeena badala **n**');
INSERT INTO `en_transliteration` VALUES (2191, 'Ma ashhadtuhum khalqa a **l** ssamawati wa **a** lardi wala khalqa anfusihim wama kuntu muttakhitha almudilleena \'aduda **n**');
INSERT INTO `en_transliteration` VALUES (2192, 'Wayawma yaqoolu nadoo shurakaiya allatheena za\'amtum fada\'awhum falam yastajeeboo lahum waja\'ana baynahum mawbiqa **n**');
INSERT INTO `en_transliteration` VALUES (2193, 'Waraa almujrimoona a **l** nnara fa _th_ annoo annahum muwaqi\'ooha walam yajidoo \'anha masrifa **n**');
INSERT INTO `en_transliteration` VALUES (2194, 'Walaqad sarrafna fee hatha alqurani li **l** nnasi min kulli mathalin wakana alinsanu akthara shayin jadala **n**');
INSERT INTO `en_transliteration` VALUES (2195, 'Wama mana\'a a **l** nnasa an yuminoo ith jaahumu alhuda wayastaghfiroo rabbahum illa an tatiyahum sunnatu alawwaleena aw yatiyahumu al\'athabu qubula **n**');
INSERT INTO `en_transliteration` VALUES (2196, 'Wama nursilu almursaleena illa mubashshireena wamunthireena wayujadilu allatheena kafaroo bi **a** lbatili liyudhidoo bihi alhaqqa wa **i** ttakhathoo ayatee wama onthiroo huzuwa **n**');
INSERT INTO `en_transliteration` VALUES (2197, 'Waman a _th_ lamu mimman thukkira biayati rabbihi faa\'rada \'anha wanasiya ma qaddamat yadahu inna ja\'ana \'ala quloobihim akinnatan an yafqahoohu wafee athanihim waqran wain tad\'uhum ila alhuda falan yahtadoo ithan abada **n**');
INSERT INTO `en_transliteration` VALUES (2198, 'Warabbuka alghafooru thoo a **l** rrahmati law yuakhithuhum bima kasaboo la\'ajjala lahumu al\'athaba bal lahum maw\'idun lan yajidoo min doonihi mawila **n**');
INSERT INTO `en_transliteration` VALUES (2199, 'Watilka alqura ahlaknahum lamma _th_ alamoo waja\'ana limahlikihim maw\'ida **n**');
INSERT INTO `en_transliteration` VALUES (2200, 'Waith qala moosa lifatahu la abrahu hatta ablugha majma\'a albahrayni aw amdiya huquba **n**');
INSERT INTO `en_transliteration` VALUES (2201, 'Falamma balagha majma\'a baynihima nasiyahootahuma fa **i** ttakhatha sabeelahu fee albahri saraba **n**');
INSERT INTO `en_transliteration` VALUES (2202, 'Falamma jawaza qala lifatahu atina ghadaana laqad laqeena min safarina hatha nasaba **n**');
INSERT INTO `en_transliteration` VALUES (2203, 'Qala araayta ith awayna ila a **l** ssakhrati fainnee naseetu alhoota wama ansaneehu illa a **l** shshaytanu an athkurahu wa **i** ttakhatha sabeelahu fee albahri \'ajaba **n**');
INSERT INTO `en_transliteration` VALUES (2204, 'Qala thalika ma kunna nabghi fa **i** rtadda \'alaatharihima qasasa **n**');
INSERT INTO `en_transliteration` VALUES (2205, 'Fawajada \'abdan min \'ibadinaataynahu rahmatan min \'indina wa\'allamnahu min ladunna \'ilma **n**');
INSERT INTO `en_transliteration` VALUES (2206, 'Qala lahu moosa hal attabi\'uka \'ala an tu\'allimani mimma \'ullimta rushda **n**');
INSERT INTO `en_transliteration` VALUES (2207, 'Qala innaka lan tastatee\'a ma\'iya sabra **n**');
