INSERT INTO `en_transliteration` VALUES (2513, 'Awalam yara allatheena kafaroo anna a **l** ssamawati wa **a** larda kanata ratqan fafataqnahuma waja\'ana mina almai kulla shayin hayyin afala yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (2514, 'Waja\'ana fee alardi rawasiya an tameeda bihim waja\'ana feeha fijajan subulan la\'allahum yahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (2515, 'Waja\'ana a **l** ssamaa saqfan mahfoo _th_ an wahum \'an ayatiha mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (2516, 'Wahuwa allathee khalaqa allayla wa **al** nnahara wa **al** shshamsa wa **a** lqamara kullun fee falakin yasbahoon **a**');
INSERT INTO `en_transliteration` VALUES (2517, 'Wama ja\'ana libasharin min qablika alkhulda afain mitta fahumu alkhalidoon **a**');
INSERT INTO `en_transliteration` VALUES (2518, 'Kullu nafsin thaiqatu almawti wanablookum bi **al** shsharri wa **a** lkhayri fitnatan wailayna turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2519, 'Waitha raaka allatheena kafaroo in yattakhithoonaka illa huzuwan ahatha allathee yathkuru alihatakum wahum bithikri a **l** rrahmani hum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (2520, 'Khuliqa alinsanu min \'ajalin saoreekum ayatee fala tasta\'jiloon **a**');
INSERT INTO `en_transliteration` VALUES (2521, 'Wayaqooloona mata hatha alwa\'du in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (2522, 'Law ya\'lamu allatheena kafaroo heena la yakuffoona \'an wujoohihimu a **l** nnara wala \'an _th_ uhoorihim wala hum yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (2523, 'Bal tateehim baghtatan fatabhatuhum fala yastatee\'oona raddaha wala hum yun _th_ aroon **a**');
INSERT INTO `en_transliteration` VALUES (2524, 'Walaqadi istuhzia birusulin min qablika fahaqa bi **a** llatheena sakhiroo minhum ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (2525, 'Qul man yaklaokum bi **a** llayli wa **al** nnahari mina a **l** rrahmani bal hum \'an thikri rabbihim mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (2526, 'Am lahum alihatun tamna\'uhum min doonina la yastatee\'oona nasra anfusihim wala hum minna yushaboon **a**');
INSERT INTO `en_transliteration` VALUES (2527, 'Bal matta\'na haolai waabaahum hattatala \'alayhimu al\'umuru afala yarawna anna natee alarda nanqusuha min atrafiha afahumu alghaliboon **a**');
INSERT INTO `en_transliteration` VALUES (2528, 'Qul innama onthirukum bi **a** lwahyi wala yasma\'u a **l** ssummu a **l** ddu\'aa itha ma yuntharoon **a**');
INSERT INTO `en_transliteration` VALUES (2529, 'Walain massathum nafhatun min \'athabi rabbika layaqoolunna ya waylana inna kunna _th_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (2530, 'Wanada\'u almawazeena alqista liyawmi alqiyamati fala tu _th_ lamu nafsun shayan wain kana mithqala habbatin min khardalin atayna biha wakafa binahasibeen **a**');
INSERT INTO `en_transliteration` VALUES (2531, 'Walaqad atayna moosa waharoona alfurqana wadiyaan wathikran lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (2532, 'Allatheena yakhshawna rabbahum bi **a** lghaybi wahum mina a **l** ssa\'ati mushfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (2533, 'Wahathathikrun mubarakun anzanahu afaantum lahu munkiroon **a**');
INSERT INTO `en_transliteration` VALUES (2534, 'Walaqad atayna ibraheema rushdahu min qablu wakunna bihi \'alimeen **a**');
INSERT INTO `en_transliteration` VALUES (2535, 'Ith qala liabeehi waqawmihi ma hathihi a **l** ttamatheelu allatee antum laha \'akifoon **a**');
INSERT INTO `en_transliteration` VALUES (2536, 'Qaloo wajadnaabaana laha \'abideen **a**');
INSERT INTO `en_transliteration` VALUES (2537, 'Qala laqad kuntum antum waabaokum fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (2538, 'Qaloo ajitana bi **a** lhaqqi am anta mina alla\'ibeen **a**');
INSERT INTO `en_transliteration` VALUES (2539, 'Qala bal rabbukum rabbu a **l** ssamawati wa **a** lardi allathee fatarahunna waana \'alathalikum mina a **l** shshahideen **a**');
INSERT INTO `en_transliteration` VALUES (2540, 'Wata **A** llahi laakeedanna asnamakum ba\'da an tuwalloo mudbireen **a**');
INSERT INTO `en_transliteration` VALUES (2541, 'Faja\'alahum juthathan illa kabeeran lahum la\'allahum ilayhi yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2542, 'Qaloo man fa\'ala hatha bialihatina innahu lamina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (2543, 'Qaloo sami\'na fatan yathkuruhum yuqalu lahu ibraheem **u**');
INSERT INTO `en_transliteration` VALUES (2544, 'Qaloo fatoo bihi \'ala a\'yuni a **l** nnasi la\'allahum yashhadoon **a**');
INSERT INTO `en_transliteration` VALUES (2545, 'Qaloo aanta fa\'ata hatha bialihatina ya ibraheem **u**');
INSERT INTO `en_transliteration` VALUES (2546, 'Qala bal fa\'alahu kabeeruhum hatha fa **i** saloohum in kanoo yantiqoon **a**');
INSERT INTO `en_transliteration` VALUES (2547, 'Faraja\'oo ila anfusihim faqaloo innakum antumu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (2548, 'Thumma nukisoo \'ala ruoosihim laqad \'alimta ma haolai yantiqoon **a**');
INSERT INTO `en_transliteration` VALUES (2549, 'Qala afata\'budoona min dooni Allahi ma la yanfa\'ukum shayan wala yadurrukum');
INSERT INTO `en_transliteration` VALUES (2550, 'Offin lakum walima ta\'budoona min dooni Allahi afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (2551, 'Qaloo harriqoohu wa **o** nsuroo alihatakum in kuntum fa\'ileen **a**');
INSERT INTO `en_transliteration` VALUES (2552, 'Qulna ya naru koonee bardan wasalaman \'ala ibraheem **a**');
INSERT INTO `en_transliteration` VALUES (2553, 'Waaradoo bihi kaydan faja\'anahumu alakhsareen **a**');
INSERT INTO `en_transliteration` VALUES (2554, 'Wanajjaynahu walootan ila alardi allatee barakna feeha lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (2555, 'Wawahabna lahu ishaqa waya\'qooba nafilatan wakullan ja\'anasaliheen **a**');
INSERT INTO `en_transliteration` VALUES (2556, 'Waja\'anahum aimmatan yahdoona biamrina waawhayna ilayhim fi\'la alkhayrati waiqama a **l** ssalati waeetaa a **l** zzakati wakanoo lana \'abideen **a**');
INSERT INTO `en_transliteration` VALUES (2557, 'Walootan ataynahu hukman wa\'ilman wanajjaynahu mina alqaryati allatee kanat ta\'malu alkhabaitha innahum kanoo qawma sawin fasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (2558, 'Waadkhanahu fee rahmatina innahu mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (2559, 'Wanoohan ith nada min qablu fa **i** stajabna lahu fanajjaynahu waahlahu mina alkarbi al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (2560, 'Wanasarnahu mina alqawmi allatheena kaththaboo biayatina innahum kanoo qawma sawin faaghraqnahum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (2561, 'Wadawooda wasulaymana ith yahkumani fee alharthi ith nafashat feehi ghanamu alqawmi wakunna lihukmihim shahideen **a**');
INSERT INTO `en_transliteration` VALUES (2562, 'Fafahhamnaha sulaymana wakullan ataynahukman wa\'ilman wasakhkharna ma\'a dawooda aljibala yusabbihna wa **al** ttayra wakunna fa\'ileen **a**');
INSERT INTO `en_transliteration` VALUES (2563, 'Wa\'allamnahu san\'ata laboosin lakum lituhsinakum min basikum fahal antum shakiroon **a**');
INSERT INTO `en_transliteration` VALUES (2564, 'Walisulaymana a **l** rreeha \'asifatan tajree biamrihi ila alardi allatee barakna feeha wakunna bikulli shayin \'alimeen **a**');
INSERT INTO `en_transliteration` VALUES (2565, 'Wamina a **l** shshayateeni man yaghoosoona lahu waya\'maloona \'amalan doona thalika wakunna lahum hafi _th_ een **a**');
INSERT INTO `en_transliteration` VALUES (2566, 'Waayyooba ith nada rabbahu annee massaniya a **l** ddurru waanta arhamu a **l** rrahimeen **a**');
INSERT INTO `en_transliteration` VALUES (2567, 'Fa **i** stajabna lahu fakashafna ma bihi min durrin waataynahu ahlahu wamithlahum ma\'ahum rahmatan min \'indina wathikra lil\'abideen **a**');
INSERT INTO `en_transliteration` VALUES (2568, 'Waisma\'eela waidreesa watha alkifli kullun mina a **l** ssabireen **a**');
INSERT INTO `en_transliteration` VALUES (2569, 'Waadkhanahum fee rahmatina innahum mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (2570, 'Watha a **l** nnooni iththahaba mughadiban fa _th_ anna an lan naqdira \'alayhi fanada fee a **l** _thth_ ulumati an la ilaha illa anta subhanaka innee kuntu mina a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (2571, 'Fa **i** stajabna lahu wanajjaynahu mina alghammi wakathalika nunjee almumineen **a**');
INSERT INTO `en_transliteration` VALUES (2572, 'Wazakariyya ith nada rabbahu rabbi la tatharnee fardan waanta khayru alwaritheen **a**');
INSERT INTO `en_transliteration` VALUES (2573, 'Fa **i** stajabna lahu wawahabna lahu yahya waaslahna lahu zawjahu innahum kanoo yusari\'oona fee alkhayrati wayad\'oonana raghaban warahaban wakanoo lana khashi\'een **a**');
INSERT INTO `en_transliteration` VALUES (2574, 'Wa **a** llatee ahsanat farjaha fanafakhna feeha min roohina waja\'anaha wa **i** bnahaayatan lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (2575, 'Inna hathihi ommatukum ommatan wahidatan waana rabbukum fao\'budoon **i**');
INSERT INTO `en_transliteration` VALUES (2576, 'Wataqatta\'oo amrahum baynahum kullun ilayna raji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2577, 'Faman ya\'mal mina a **l** ssalihati wahuwa muminun fala kufrana lisa\'yihi wainna lahu katiboon **a**');
INSERT INTO `en_transliteration` VALUES (2578, 'Waharamun \'ala qaryatin ahlaknaha annahum la yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2579, 'Hatta itha futihat yajooju wamajooju wahum min kulli hadabin yansiloon **a**');
INSERT INTO `en_transliteration` VALUES (2580, 'Wa **i** qtaraba alwa\'du alhaqqu faitha hiya shakhisatun absaru allatheena kafaroo ya waylana qad kunna fee ghaflatin min hatha bal kunna _th_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (2581, 'Innakum wama ta\'budoona min dooni Allahi hasabu jahannama antum laha waridoon **a**');
INSERT INTO `en_transliteration` VALUES (2582, 'Law kana haolai alihatan ma waradooha wakullun feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (2583, 'Lahum feeha zafeerun wahum feeha la yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2584, 'Inna allatheena sabaqat lahum minna alhusna olaika \'anha mub\'adoon **a**');
INSERT INTO `en_transliteration` VALUES (2585, 'La yasma\'oona haseesaha wahum fee ma ishtahat anfusuhum khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (2586, 'La yahzunuhumu alfaza\'u alakbaru watatalaqqahumu almalaikatu hatha yawmukumu allathee kuntum too\'adoon **a**');
INSERT INTO `en_transliteration` VALUES (2587, 'Yawma natwee a **l** ssamaa katayyi a **l** ssijlli lilkutubi kama badana awwala khalqin nu\'eeduhu wa\'dan \'alayna inna kunna fa\'ileen **a**');
INSERT INTO `en_transliteration` VALUES (2588, 'Walaqad katabna fee a **l** zzaboori min ba\'di a **l** ththikri anna alarda yarithuha \'ibadiya a **l** ssalihoon **a**');
INSERT INTO `en_transliteration` VALUES (2589, 'Inna fee hatha labalaghan liqawmin \'abideen **a**');
INSERT INTO `en_transliteration` VALUES (2590, 'Wama arsanaka illa rahmatan lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (2591, 'Qul innama yooha ilayya annama ilahukum ilahun wahidun fahal antum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (2592, 'Fain tawallaw faqul athantukum \'ala sawain wain adree aqareebun am ba\'eedun ma too\'adoon **a**');
INSERT INTO `en_transliteration` VALUES (2593, 'Innahu ya\'lamu aljahra mina alqawli waya\'lamu ma taktumoon **a**');
INSERT INTO `en_transliteration` VALUES (2594, 'Wain adree la\'allahu fitnatun lakum wamata\'un ilaheen **in**');
INSERT INTO `en_transliteration` VALUES (2595, 'Qala rabbi ohkum bi **a** lhaqqi warabbuna a **l** rrahmanu almusta\'anu \'ala ma tasifoon **a**');
INSERT INTO `en_transliteration` VALUES (2596, 'Ya ayyuha a **l** nnasu ittaqoo rabbakum inna zazalata a **l** ssa\'ati shayon \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (2597, 'Yawma tarawnaha tathhalu kullu murdi\'atin \'amma arda\'at watada\'u kullu thati hamlin hamlaha watara a **l** nnasa sukara wama hum bisukara walakinna \'athaba Allahi shadeed **un**');
INSERT INTO `en_transliteration` VALUES (2598, 'Wamina a **l** nnasi man yujadilu fee Allahi bighayri \'ilmin wayattabi\'u kulla shaytanin mareed **in**');
INSERT INTO `en_transliteration` VALUES (2599, 'Kutiba \'alayhi annahu man tawallahu faannahu yudilluhu wayahdeehi ila \'athabi a **l** ssa\'eer **i**');
INSERT INTO `en_transliteration` VALUES (2600, 'Ya ayyuha a **l** nnasu in kuntum fee raybin mina alba\'thi fainna khalaqnakum min turabin thumma min nutfatin thumma min \'alaqatin thumma min mudghatin mukhallaqatin waghayri mukhallaqatin linubayyina lakum wanuqirru fee alarhami ma nashao ila ajalin musamman thumma nukhrijukum tiflan thumma litablughoo ashuddakum waminkum man yutawaffa waminkum man yuraddu ila arthali al\'umuri likayla ya\'lama min ba\'di \'ilmin shayan watara alarda hamidatan faitha anzana \'alayha almaa ihtazzat warabat waanbatat min kulli zawjin baheej **in**');
INSERT INTO `en_transliteration` VALUES (2601, 'Thalika bianna Allaha huwa alhaqqu waannahu yuhyee almawta waannahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (2602, 'Waanna a **l** ssa\'ata atiyatun la rayba feeha waanna Allaha yab\'athu man fee alquboor **i**');
INSERT INTO `en_transliteration` VALUES (2603, 'Wamina a **l** nnasi man yujadilu fee Allahi bighayri \'ilmin wala hudan wala kitabin muneer **in**');
INSERT INTO `en_transliteration` VALUES (2604, 'Thaniya \'itfihi liyudilla \'an sabeeli Allahi lahu fee a **l** ddunya khizyun wanutheequhu yawma alqiyamati \'athaba alhareeq **i**');
INSERT INTO `en_transliteration` VALUES (2605, 'Thalika bima qaddamat yadaka waanna Allaha laysa bi _th_ allamin lil\'abeed **i**');
INSERT INTO `en_transliteration` VALUES (2606, 'Wamina a **l** nnasi man ya\'budu Allaha \'alaharfin fain asabahu khayrun itmaanna bihi wain asabathu fitnatun inqalaba \'ala wajhihi khasira a **l** ddunya wa **a** lakhirata thalika huwa alkhusranu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (2607, 'Yad\'oo min dooni Allahi ma la yadurruhu wama la yanfa\'uhu thalika huwa a **l** ddalalu alba\'eed **u**');
INSERT INTO `en_transliteration` VALUES (2608, 'Yad\'oo laman darruhu aqrabu min naf\'ihi labisa almawla walabisa al\'asheer **u**');
INSERT INTO `en_transliteration` VALUES (2609, 'Inna Allaha yudkhilu allatheena amanoo wa\'amiloo a **l** ssalihati jannatin tajree min tahtiha alanharu inna Allaha yaf\'alu ma yureed **u**');
INSERT INTO `en_transliteration` VALUES (2610, 'Man kana ya _th_ unnu an lan yansurahu Allahu fee a **l** ddunya wa **a** lakhirati falyamdud bisababin ila a **l** ssamai thumma liyaqta\' falyan _th_ ur hal yuthhibanna kayduhu ma yaghee _th_ **u**');
INSERT INTO `en_transliteration` VALUES (2611, 'Wakathalika anzanahu ayatin bayyinatin waanna Allaha yahdee man yureed **u**');
INSERT INTO `en_transliteration` VALUES (2612, 'Inna allatheena amanoo wa **a** llatheena hadoo wa **al** ssabieena wa **al** nnasara wa **a** lmajoosa wa **a** llatheena ashrakoo inna Allaha yafsilu baynahum yawma alqiyamati inna Allaha \'ala kulli shayin shaheed **un**');
INSERT INTO `en_transliteration` VALUES (2613, 'Alam tara anna Allaha yasjudu lahu man fee a **l** ssamawati waman fee alardi wa **al** shshamsu wa **a** lqamaru wa **al** nnujoomu wa **a** ljibalu wa **al** shshajaru wa **al** ddawabbu wakatheerun mina a **l** nnasi wakatheerun haqqa \'alayhi al\'athabu waman yuhini Allahu fama lahu min mukrimin inna Allaha yaf\'alu ma yasha **o**');
INSERT INTO `en_transliteration` VALUES (2614, 'Hathani khasmani ikhtasamoo fee rabbihim fa **a** llatheena kafaroo qutti\'at lahum thiyabun min narin yusabbu min fawqi ruoosihimu alhameem **u**');
INSERT INTO `en_transliteration` VALUES (2615, 'Yusharu bihi ma fee butoonihim wa **a** ljulood **u**');
INSERT INTO `en_transliteration` VALUES (2616, 'Walahum maqami\'u min hadeed **in**');
INSERT INTO `en_transliteration` VALUES (2617, 'Kullama aradoo an yakhrujoo minha min ghammin o\'eedoo feeha wathooqoo \'athaba alhareeq **i**');
INSERT INTO `en_transliteration` VALUES (2618, 'Inna Allaha yudkhilu allatheena amanoo wa\'amiloo a **l** ssalihati jannatin tajree min tahtiha alanharu yuhallawna feeha min asawira min thahabin waluluan walibasuhum feehahareer **un**');
INSERT INTO `en_transliteration` VALUES (2619, 'Wahudoo ila a **l** ttayyibi mina alqawli wahudoo ilasirati alhameed **i**');
INSERT INTO `en_transliteration` VALUES (2620, 'Inna allatheena kafaroo wayasuddoona \'an sabeeli Allahi wa **a** lmasjidi alharami allathee ja\'anahu li **l** nnasi sawaan al\'akifu feehi wa **a** lbadi waman yurid feehi biilhadin bi _th_ ulmin nuthiqhu min \'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (2621, 'Waith bawwana liibraheema makana albayti an la tushrik bee shayan watahhir baytiya li **l** ttaifeena wa **a** lqaimeena wa **al** rrukka\'i a **l** ssujood **i**');
INSERT INTO `en_transliteration` VALUES (2622, 'Waaththin fee a **l** nnasi bi **a** lhajji yatooka rijalan wa\'ala kulli damirin yateena min kulli fajjin \'ameeq **in**');
INSERT INTO `en_transliteration` VALUES (2623, 'Liyashhadoo manafi\'a lahum wayathkuroo isma Allahi fee ayyamin ma\'loomatin \'ala ma razaqahum min baheemati alan\'ami fakuloo minha waat\'imoo albaisa alfaqeer **a**');
INSERT INTO `en_transliteration` VALUES (2624, 'Thumma lyaqdoo tafathahum walyoofoo nuthoorahum walyattawwafoo bi **a** lbayti al\'ateeq **i**');
INSERT INTO `en_transliteration` VALUES (2625, 'Thalika waman yu\'a _thth_ im hurumati Allahi fahuwa khayrun lahu \'inda rabbihi waohillat lakumu alan\'amu illa ma yutla \'alaykum fa **i** jtaniboo a **l** rrijsa mina alawthani wa **i** jtaniboo qawla a **l** zzoor **i**');
INSERT INTO `en_transliteration` VALUES (2626, 'Hunafaa lillahi ghayra mushrikeena bihi waman yushrik bi **A** llahi fakaannama kharra mina a **l** ssamai fatakhtafuhu a **l** ttayru aw tahwee bihi a **l** rreehu fee makanin saheeq **in**');
INSERT INTO `en_transliteration` VALUES (2627, 'Thalika waman yu\'a _thth_ im sha\'aira Allahi fainnaha min taqwa alquloob **i**');
INSERT INTO `en_transliteration` VALUES (2628, 'Lakum feeha manafi\'u ila ajalin musamman thumma mahilluha ila albayti al\'ateeq **i**');
INSERT INTO `en_transliteration` VALUES (2629, 'Walikulli ommatin ja\'ana mansakan liyathkuroo isma Allahi \'ala ma razaqahum min baheemati alan\'ami failahukum ilahun wahidun falahu aslimoo wabashshiri almukhbiteen **a**');
INSERT INTO `en_transliteration` VALUES (2630, 'Allatheena ithathukira Allahu wajilat quloobuhum wa **al** ssabireena \'ala ma asabahum wa **a** lmuqeemee a **l** ssalati wamimma razaqnahum yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (2631, 'Wa **a** lbudna ja\'anaha lakum min sha\'airi Allahi lakum feeha khayrun fa **o** thkuroo isma Allahi \'alayhasawaffa faitha wajabat junoobuha fakuloo minha waat\'imoo alqani\'a wa **a** lmu\'tarra kathalika sakhkharnaha lakum la\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (2632, 'Lan yanala Allaha luhoomuha wala dimaoha walakin yanaluhu a **l** ttaqwa minkum kathalika sakhkharaha lakum litukabbiroo Allaha \'ala ma hadakum wabashshiri almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (2633, 'Inna Allaha yudafi\'u \'ani allatheena amanoo inna Allaha la yuhibbu kulla khawwanin kafoor **in**');
INSERT INTO `en_transliteration` VALUES (2634, 'Othina lillatheena yuqataloona biannahum _th_ ulimoo wainna Allaha \'ala nasrihim laqadeer **un**');
INSERT INTO `en_transliteration` VALUES (2635, 'Allatheena okhrijoo min diyarihim bighayri haqqin illa an yaqooloo rabbuna Allahu walawla daf\'u Allahi a **l** nnasa ba\'dahum biba\'din lahuddimat sawami\'u wabiya\'un wasalawatun wamasajidu yuthkaru feeha ismu Allahi katheeran walayansuranna Allahu man yansuruhu inna Allaha laqawiyyun \'azeez **un**');
INSERT INTO `en_transliteration` VALUES (2636, 'Allatheena in makkannahum fee alardi aqamoo a **l** ssalata waatawoo a **l** zzakata waamaroo bi **a** lma\'roofi wanahaw \'ani almunkari walillahi \'aqibatu alomoor **i**');
