INSERT INTO `en_transliteration` VALUES (2776, 'Waman khaffat mawazeenuhu faolaika allatheena khasiroo anfusahum fee jahannama khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (2777, 'Talfahu wujoohahumu a **l** nnaru wahum feeha kalihoon **a**');
INSERT INTO `en_transliteration` VALUES (2778, 'Alam takun ayatee tutla \'alaykum fakuntum biha tukaththiboon **a**');
INSERT INTO `en_transliteration` VALUES (2779, 'Qaloo rabbana ghalabat \'alayna shiqwatuna wakunna qawman dalleen **a**');
INSERT INTO `en_transliteration` VALUES (2780, 'Rabbana akhrijna minha fain \'udna fainna _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (2781, 'Qala ikhsaoo feeha wala tukallimoon **i**');
INSERT INTO `en_transliteration` VALUES (2782, 'Innahu kana fareequn min \'ibadee yaqooloona rabbanaamanna fa **i** ghfir lana wa **i** rhamna waanta khayru a **l** rrahimeen **a**');
INSERT INTO `en_transliteration` VALUES (2783, 'Fa **i** ttakhathtumoohum sikhriyyan hatta ansawkum thikree wakuntum minhum tadhakoon **a**');
INSERT INTO `en_transliteration` VALUES (2784, 'Innee jazaytuhumu alyawma bimasabaroo annahum humu alfaizoon **a**');
INSERT INTO `en_transliteration` VALUES (2785, 'Qala kam labithtum fee alardi \'adada sineen **a**');
INSERT INTO `en_transliteration` VALUES (2786, 'Qaloo labithna yawman aw ba\'da yawmin fa **i** sali al\'addeen **a**');
INSERT INTO `en_transliteration` VALUES (2787, 'Qala in labithtum illa qaleelan law annakum kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (2788, 'Afahasibtum annama khalaqnakum \'abathan waannakum ilayna la turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2789, 'Fata\'ala Allahu almaliku alhaqqu la ilaha illa huwa rabbu al\'arshi alkareem **i**');
INSERT INTO `en_transliteration` VALUES (2790, 'Waman yad\'u ma\'a Allahi ilahan akhara la burhana lahu bihi fainnamahisabuhu \'inda rabbihi innahu la yuflihu alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (2791, 'Waqul rabbi ighfir wa **i** rham waanta khayru a **l** rrahimeen **a**');
INSERT INTO `en_transliteration` VALUES (2792, 'Sooratun anzanaha wafaradnaha waanzana feehaayatin bayyinatin la\'allakum tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (2793, 'A **l** zzaniyatu wa **al** zzanee fa **i** jlidoo kulla wahidin minhuma miata jadatin wala takhuthkum bihima rafatun fee deeni Allahi in kuntum tuminoona bi **A** llahi wa **a** lyawmi alakhiri walyashhad \'athabahumataifatun mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (2794, 'A **l** zzanee la yankihu illa zaniyatan aw mushrikatan wa **al** zzaniyatu la yankihuha illa zanin aw mushrikun wahurrima thalika \'ala almumineen **a**');
INSERT INTO `en_transliteration` VALUES (2795, 'Wa **a** llatheena yarmoona almuhsanati thumma lam yatoo biarba\'ati shuhadaa fa **i** jlidoohum thamaneena jadatan wala taqbaloo lahum shahadatan abadan waolaika humu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (2796, 'Illa allatheena taboo min ba\'di thalika waaslahoo fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (2797, 'Wa **a** llatheena yarmoona azwajahum walam yakun lahum shuhadao illa anfusuhum fashahadatu ahadihim arba\'u shahadatin bi **A** llahi innahu lamina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (2798, 'Wa **a** lkhamisatu anna la\'nata Allahi \'alayhi in kana mina alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (2799, 'Wayadrao \'anha al\'athaba an tashhada arba\'a shahadatin bi **A** llahi innahu lamina alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (2800, 'Wa **a** lkhamisata anna ghadaba Allahi \'alayha in kana mina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (2801, 'Walawla fadlu Allahi \'alaykum warahmatuhu waanna Allaha tawwabun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (2802, 'Inna allatheena jaoo bi **a** lifki \'usbatun minkum la tahsaboohu sharran lakum bal huwa khayrun lakum likulli imriin minhum ma iktasaba mina alithmi wa **a** llathee tawalla kibrahu minhum lahu \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (2803, 'Lawla ith sami\'tumoohu _th_ anna almuminoona wa **a** lmuminatu bianfusihim khayran waqaloo hatha ifkun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (2804, 'Lawla jaoo \'alayhi biarba\'ati shuhadaa faith lam yatoo bi **al** shshuhadai faolaika \'inda Allahi humu alkathiboon **a**');
INSERT INTO `en_transliteration` VALUES (2805, 'Walawla fadlu Allahi \'alaykum warahmatuhu fee a **l** ddunya wa **a** lakhirati lamassakum fee ma afadtum feehi \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (2806, 'Ith talaqqawnahu biasinatikum wataqooloona biafwahikum ma laysa lakum bihi \'ilmun watahsaboonahu hayyinan wahuwa \'inda Allahi \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (2807, 'Walawla ith sami\'tumoohu qultum ma yakoonu lana an natakallama bihatha subhanaka hatha buhtanun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (2808, 'Ya\'i _th_ ukumu Allahu an ta\'oodoo limithlihi abadan in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (2809, 'Wayubayyinu Allahu lakumu alayati wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (2810, 'Inna allatheena yuhibboona an tashee\'a alfahishatu fee allatheena amanoo lahum \'athabun aleemun fee a **l** ddunya wa **a** lakhirati wa **A** llahu ya\'lamu waantum la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (2811, 'Walawla fadlu Allahi \'alaykum warahmatuhu waanna Allaha raoofun raheem **un**');
INSERT INTO `en_transliteration` VALUES (2812, 'Ya ayyuha allatheena amanoo la tattabi\'oo khutuwati a **l** shshaytani waman yattabi\' khutuwati a **l** shshaytani fainnahu yamuru bi **a** lfahshai wa **a** lmunkari walawla fadlu Allahi \'alaykum warahmatuhu ma zaka minkum min ahadin abadan walakinna Allaha yuzakkee man yashao wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (2813, 'Wala yatali oloo alfadli minkum wa **al** ssa\'ati an yutoo olee alqurba wa **a** lmasakeena wa **a** lmuhajireena fee sabeeli Allahi walya\'foo walyasfahoo ala tuhibboona an yaghfira Allahu lakum wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (2814, 'Inna allatheena yarmoona almuhsanati alghafilati almuminati lu\'inoo fee a **l** ddunya wa **a** lakhirati walahum \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (2815, 'Yawma tashhadu \'alayhim asinatuhum waaydeehim waarjuluhum bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (2816, 'Yawmaithin yuwaffeehimu Allahu deenahumu alhaqqa waya\'lamoona anna Allaha huwa alhaqqu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (2817, 'Alkhabeethatu lilkhabeetheena wa **a** lkhabeethoona lilkhabeethati wa **al** ttayyibatu li **l** ttayyibeena wa **al** ttayyiboona li **l** ttayyibati olaika mubarraoona mimma yaqooloona lahum maghfiratun warizqun kareem **un**');
INSERT INTO `en_transliteration` VALUES (2818, 'Ya ayyuha allatheena amanoo la tadkhuloo buyootan ghayra buyootikum hatta tastanisoo watusallimoo \'ala ahlihathalikum khayrun lakum la\'allakum tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (2819, 'Fain lam tajidoo feeha ahadan fala tadkhuloohahatta yuthana lakum wain qeela lakumu irji\'oo fa **i** rji\'oo huwa azka lakum wa **A** llahu bima ta\'maloona \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (2820, 'Laysa \'alaykum junahun an tadkhuloo buyootan ghayra maskoonatin feeha mata\'un lakum wa **A** llahu ya\'lamu ma tubdoona wama taktumoon **a**');
INSERT INTO `en_transliteration` VALUES (2821, 'Qul lilmumineena yaghuddoo min absarihum wayahfa _th_ oo furoojahum thalika azka lahum inna Allaha khabeerun bima yasna\'oon **a**');
INSERT INTO `en_transliteration` VALUES (2822, 'Waqul lilmuminati yaghdudna min absarihinna wayahfa _th_ na furoojahunna wala yubdeena zeenatahunna illa ma _th_ ahara minha walyadribna bikhumurihinna \'ala juyoobihinna wala yubdeena zeenatahunna illa libu\'oolatihinna aw abaihinna aw abai bu\'oolatihinna aw abnaihinna aw abnai bu\'oolatihinna aw ikhwanihinna aw banee ikhwanihinna aw banee akhawatihinna aw nisaihinna aw ma malakat aymanuhunna awi a **l** ttabi\'eena ghayri olee alirbati mina a **l** rrijali awi a **l** ttifli allatheena lam ya _th_ haroo \'ala \'awrati a **l** nnisai wala yadribna biarjulihinna liyu\'lama ma yukhfeena min zeenatihinna watooboo ila Allahi jamee\'an ayyuha almuminoona la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (2823, 'Waankihoo alayama minkum wa **al** ssaliheena min \'ibadikum waimaikum in yakoonoo fuqaraa yughnihimu Allahu min fadlihi wa **A** llahu wasi\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (2824, 'Walyasta\'fifi allatheena la yajidoona nikahan hatta yughniyahumu Allahu min fadlihi wa **a** llatheena yabtaghoona alkitaba mimma malakat aymanukum fakatiboohum in \'alimtum feehim khayran waatoohum min mali Allahi allathee atakum wala tukrihoo fatayatikum \'ala albighai in aradna tahassunan litabtaghoo \'arada alhayati a **l** ddunya waman yukrihhunna fainna Allaha min ba\'di ikrahihinna ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (2825, 'Walaqad anzana ilaykum ayatin mubayyinatin wamathalan mina allatheena khalaw min qablikum wamaw\'i _th_ atan lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (2826, 'Allahu nooru a **l** ssamawati wa **a** lardi mathalu noorihi kamishkatin feeha misbahun almisbahu fee zujajatin a **l** zzujajatu kaannaha kawkabun durriyyun yooqadu min shajaratin mubarakatin zaytoonatin la sharqiyyatin wala gharbiyyatin yakadu zaytuha yudeeo walaw lam tamsashu narun noorun \'ala noorin yahdee Allahu linoorihi man yashao wayadribu Allahu alamthala li **l** nnasi wa **A** llahu bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (2827, 'Fee buyootin athina Allahu an turfa\'a wayuthkara feeha ismuhu yusabbihu lahu feeha bi **a** lghuduwwi wa **a** lasal **i**');
INSERT INTO `en_transliteration` VALUES (2828, 'Rijalun la tulheehim tijaratun wala bay\'un \'an thikri Allahi waiqami a **l** ssalati waeetai a **l** zzakati yakhafoona yawman tataqallabu feehi alquloobu wa **a** labsar **u**');
INSERT INTO `en_transliteration` VALUES (2829, 'Liyajziyahumu Allahu ahsana ma \'amiloo wayazeedahum min fadlihi wa **A** llahu yarzuqu man yashao bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (2830, 'Wa **a** llatheena kafaroo a\'maluhum kasarabin biqee\'atin yahsabuhu a **l** _thth_ amanu maan hatta itha jaahu lam yajidhu shayan wawajada Allaha \'indahu fawaffahu hisabahu wa **A** llahu saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (2831, 'Aw ka _th_ ulumatin fee bahrin lujjiyyin yaghshahu mawjun min fawqihi mawjun min fawqihi sahabun _th_ ulumatun ba\'duha fawqa ba\'din itha akhraja yadahu lam yakad yaraha waman lam yaj\'ali Allahu lahu nooran fama lahu min noor **in**');
INSERT INTO `en_transliteration` VALUES (2832, 'Alam tara anna Allaha yusabbihu lahu man fee a **l** ssamawati wa **a** lardi wa **al** ttayru saffatin kullun qad \'alima salatahu watasbeehahu wa **A** llahu \'aleemun bima yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (2833, 'Walillahi mulku a **l** ssamawati wa **a** lardi waila Allahi almaseer **u**');
INSERT INTO `en_transliteration` VALUES (2834, 'Alam tara anna Allaha yuzjee sahaban thumma yuallifu baynahu thumma yaj\'aluhu rukaman fatara alwadqa yakhruju min khilalihi wayunazzilu mina a **l** ssamai min jibalin feeha min baradin fayuseebu bihi man yashao wayasrifuhu \'an man yashao yakadu sana barqihi yathhabu bi **a** labsar **i**');
INSERT INTO `en_transliteration` VALUES (2835, 'Yuqallibu Allahu allayla wa **al** nnahara inna fee thalika la\'ibratan liolee alabsar **i**');
INSERT INTO `en_transliteration` VALUES (2836, 'Wa **A** llahu khalaqa kulla dabbatin min main faminhum man yamshee \'ala batnihi waminhum man yamshee \'ala rijlayni waminhum man yamshee \'ala arba\'in yakhluqu Allahu ma yashao inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (2837, 'Laqad anzanaayatin mubayyinatin wa **A** llahu yahdee man yashao ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (2838, 'Wayaqooloona amanna bi **A** llahi wabi **al** rrasooli waata\'na thumma yatawalla fareequn minhum min ba\'di thalika wama olaika bi **a** lmumineen **a**');
INSERT INTO `en_transliteration` VALUES (2839, 'Waitha du\'oo ila Allahi warasoolihi liyahkuma baynahum itha fareequn minhum mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (2840, 'Wain yakun lahumu alhaqqu yatoo ilayhi muth\'ineen **a**');
INSERT INTO `en_transliteration` VALUES (2841, 'Afee quloobihim maradun ami irtaboo am yakhafoona an yaheefa Allahu \'alayhim warasooluhu bal olaika humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (2842, 'Innama kana qawla almumineena itha du\'oo ila Allahi warasoolihi liyahkuma baynahum an yaqooloo sami\'na waata\'na waolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (2843, 'Waman yuti\'i Allaha warasoolahu wayakhsha Allaha wayattaqhi faolaika humu alfaizoon **a**');
INSERT INTO `en_transliteration` VALUES (2844, 'Waaqsamoo bi **A** llahi jahda aymanihim lain amartahum layakhrujunna qul la tuqsimoo ta\'atun ma\'roofatun inna Allaha khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (2845, 'Qul atee\'oo Allaha waatee\'oo a **l** rrasoola fain tawallaw fainnama \'alayhi mahummila wa\'alaykum mahummiltum wain tutee\'oohu tahtadoo wama \'ala a **l** rrasooli illa albalaghu almubeen **a**');
INSERT INTO `en_transliteration` VALUES (2846, 'Wa\'ada Allahu allatheena amanoo minkum wa\'amiloo a **l** ssalihati layastakhlifannahum fee alardi kama istakhlafa allatheena min qablihim walayumakkinanna lahum deenahumu allathee irtada lahum walayubaddilannahum min ba\'di khawfihim amnan ya\'budoonanee la yushrikoona bee shayan waman kafara ba\'da thalika faolaika humu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (2847, 'Waaqeemoo a **l** ssalata waatoo a **l** zzakata waatee\'oo a **l** rrasoola la\'allakum turhamoon **a**');
INSERT INTO `en_transliteration` VALUES (2848, 'La tahsabanna allatheena kafaroo mu\'jizeena fee alardi wamawahumu a **l** nnaru walabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (2849, 'Ya ayyuha allatheena amanoo liyastathinkumu allatheena malakat aymanukum wa **a** llatheena lam yablughoo alhuluma minkum thalatha marratin min qabli salati alfajri waheena tada\'oona thiyabakum mina a **l** _thth_ aheerati wamin ba\'di salati al\'ishai thalathu \'awratin lakum laysa \'alaykum wala \'alayhim junahun ba\'dahunna tawwafoona \'alaykum ba\'dukum \'ala ba\'din kathalika yubayyinu Allahu lakumu alayati wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (2850, 'Waitha balagha alatfalu minkumu alhuluma falyastathinoo kama istathana allatheena min qablihim kathalika yubayyinu Allahu lakum ayatihi wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (2851, 'Wa **a** lqawa\'idu mina a **l** nnisai allatee la yarjoona nikahan falaysa \'alayhinna junahun an yada\'na thiyabahunna ghayra mutabarrijatin bizeenatin waan yasta\'fifna khayrun lahunna wa **A** llahu samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (2852, 'Laysa \'ala ala\'maharajun wala \'ala ala\'raji harajun wala \'ala almareedi harajun wala \'ala anfusikum an takuloo min buyootikum aw buyooti abaikum aw buyooti ommahatikum aw buyooti ikhwanikum aw buyooti akhawatikum aw buyooti a\'mamikum aw buyooti \'ammatikum aw buyooti akhwalikum aw buyooti khalatikum aw ma malaktum mafatihahu aw sadeeqikum laysa \'alaykum junahun an takuloo jamee\'an aw ashtatan faitha dakhatum buyootan fasallimoo \'ala anfusikum tahiyyatan min \'indi Allahi mubarakatan tayyibatan kathalika yubayyinu Allahu lakumu alayati la\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (2853, 'Innama almuminoona allatheena amanoo bi **A** llahi warasoolihi waitha kanoo ma\'ahu \'ala amrin jami\'in lam yathhaboo hatta yastathinoohu inna allatheena yastathinoonaka olaika allatheena yuminoona bi **A** llahi warasoolihi faitha istathanooka liba\'di shanihim fathan liman shita minhum wa **i** staghfir lahumu Allaha inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (2854, 'La taj\'aloo du\'aa a **l** rrasooli baynakum kadu\'ai ba\'dikum ba\'dan qad ya\'lamu Allahu allatheena yatasallaloona minkum liwathan falyahthari allatheena yukhalifoona \'an amrihi an tuseebahum fitnatun aw yuseebahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (2855, 'Ala inna lillahi ma fee a **l** ssamawati wa **a** lardi qad ya\'lamu ma antum \'alayhi wayawma yurja\'oona ilayhi fayunabbiohum bima \'amiloo wa **A** llahu bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (2856, 'Tabaraka allathee nazzala alfurqana \'ala \'abdihi liyakoona lil\'alameena natheera **n**');
INSERT INTO `en_transliteration` VALUES (2857, 'Allathee lahu mulku a **l** ssamawati wa **a** lardi walam yattakhith waladan walam yakun lahu shareekun fee almulki wakhalaqa kulla shayin faqaddarahu taqdeera **n**');
INSERT INTO `en_transliteration` VALUES (2858, 'Wa **i** ttakhathoo min doonihi alihatan la yakhluqoona shayan wahum yukhlaqoona wala yamlikoona lianfusihim darran wala naf\'an wala yamlikoona mawtan walahayatan wala nushoora **n**');
INSERT INTO `en_transliteration` VALUES (2859, 'Waqala allatheena kafaroo in hatha illa ifkun iftarahu waa\'anahu \'alayhi qawmun akharoona faqad jaoo _th_ ulman wazoora **n**');
INSERT INTO `en_transliteration` VALUES (2860, 'Waqaloo asateeru alawwaleena iktatabaha fahiya tumla \'alayhi bukratan waaseela **n**');
INSERT INTO `en_transliteration` VALUES (2861, 'Qul anzalahu allathee ya\'lamu a **l** ssirra fee a **l** ssamawati wa **a** lardi innahu kana ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (2862, 'Waqaloo mali hatha a **l** rrasooli yakulu a **l** tta\'ama wayamshee fee alaswaqi lawla onzila ilayhi malakun fayakoona ma\'ahu natheera **n**');
INSERT INTO `en_transliteration` VALUES (2863, 'Aw yulqa ilayhi kanzun aw takoonu lahu jannatun yakulu minha waqala a **l** _thth_ alimoona in tattabi\'oona illa rajulan mashoora **n**');
INSERT INTO `en_transliteration` VALUES (2864, 'On _th_ ur kayfa daraboo laka alamthala fadalloo fala yastatee\'oona sabeela **n**');
INSERT INTO `en_transliteration` VALUES (2865, 'Tabaraka allathee in shaa ja\'ala laka khayran min thalika jannatin tajree min tahtiha alanharu wayaj\'al laka qusoora **n**');
INSERT INTO `en_transliteration` VALUES (2866, 'Bal kaththaboo bi **al** ssa\'ati waa\'tadna liman kaththaba bi **al** ssa\'ati sa\'eera **n**');
INSERT INTO `en_transliteration` VALUES (2867, 'Itha raathum min makanin ba\'eedin sami\'oo laha taghayyu _th_ an wazafeera **n**');
INSERT INTO `en_transliteration` VALUES (2868, 'Waitha olqoo minha makanan dayyiqan muqarraneena da\'aw hunalika thuboora **n**');
INSERT INTO `en_transliteration` VALUES (2869, 'La tad\'oo alyawma thubooran wahidan wa **o** d\'oo thubooran katheera **n**');
INSERT INTO `en_transliteration` VALUES (2870, 'Qul athalika khayrun am jannatu alkhuldi allatee wu\'ida almuttaqoona kanat lahum jazaan wamaseera **n**');
INSERT INTO `en_transliteration` VALUES (2871, 'Lahum feeha ma yashaoona khalideena kana \'ala rabbika wa\'dan masoola **n**');
INSERT INTO `en_transliteration` VALUES (2872, 'Wayawma yahshuruhum wama ya\'budoona min dooni Allahi fayaqoolu aantum adlatum \'ibadee haolai am hum dalloo a **l** ssabeel **a**');
INSERT INTO `en_transliteration` VALUES (2873, 'Qaloo subhanaka ma kana yanbaghee lana an nattakhitha min doonika min awliyaa walakin matta\'tahum waabaahum hatta nasoo a **l** ththikra wakanoo qawman boora **n**');
INSERT INTO `en_transliteration` VALUES (2874, 'Faqad kaththabookum bima taqooloona fama tastatee\'oona sarfan wala nasran waman ya _th_ lim minkum nuthiqhu \'athaban kabeera **n**');
INSERT INTO `en_transliteration` VALUES (2875, 'Wama arsana qablaka mina almursaleena illa innahum layakuloona a **l** tta\'ama wayamshoona fee alaswaqi waja\'ana ba\'dakum liba\'din fitnatan atasbiroona wakana rabbuka baseera **n**');
INSERT INTO `en_transliteration` VALUES (2876, 'Waqala allatheena la yarjoona liqaana lawla onzila \'alayna almalaikatu aw nara rabbana laqadi istakbaroo fee anfusihim wa\'ataw \'utuwwan kabeera **n**');
