INSERT INTO `en_transliteration` VALUES (3220, 'Amman ja\'ala alarda qararan waja\'ala khilalaha anharan waja\'ala laha rawasiya waja\'ala bayna albahrayni hajizan ailahun ma\'a Allahi bal aktharuhum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3221, 'Amman yujeebu almudtarra itha da\'ahu wayakshifu a **l** ssooa wayaj\'alukum khulafaa alardi ailahun ma\'a Allahi qaleelan ma tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (3222, 'Amman yahdeekum fee _th_ ulumati albarri wa **a** lbahri waman yursilu a **l** rriyaha bushran bayna yaday rahmatihi ailahun ma\'a Allahi ta\'ala Allahu \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (3223, 'Amman yabdao alkhalqa thumma yu\'eeduhu waman yarzuqukum mina a **l** ssamai wa **a** lardi ailahun ma\'a Allahi qul hatoo burhanakum in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3224, 'Qul la ya\'lamu man fee a **l** ssamawati wa **a** lardi alghayba illa Allahu wama yash\'uroona ayyana yub\'athoon **a**');
INSERT INTO `en_transliteration` VALUES (3225, 'Bali iddaraka \'ilmuhum fee alakhirati bal hum fee shakkin minha bal hum minha \'amoon **a**');
INSERT INTO `en_transliteration` VALUES (3226, 'Waqala allatheena kafaroo aitha kunna turaban waabaona ainna lamukhrajoon **a**');
INSERT INTO `en_transliteration` VALUES (3227, 'Laqad wu\'idna hatha nahnu waabaona min qablu in hatha illa asateeru alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (3228, 'Qul seeroo fee alardi fa **o** n _th_ uroo kayfa kana \'aqibatu almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (3229, 'Wala tahzan \'alayhim wala takun fee dayqin mimma yamkuroon **a**');
INSERT INTO `en_transliteration` VALUES (3230, 'Wayaqooloona mata hatha alwa\'du in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3231, 'Qul \'asa an yakoona radifa lakum ba\'du allathee tasta\'jiloon **a**');
INSERT INTO `en_transliteration` VALUES (3232, 'Wainna rabbaka lathoo fadlin \'ala a **l** nnasi walakinna aktharahum la yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (3233, 'Wainna rabbaka laya\'lamu ma tukinnu sudooruhum wama yu\'linoon **a**');
INSERT INTO `en_transliteration` VALUES (3234, 'Wama min ghaibatin fee a **l** ssamai wa **a** lardi illa fee kitabin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (3235, 'Inna hatha alqurana yaqussu \'ala banee israeela akthara allathee hum feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (3236, 'Wainnahu lahudan warahmatun lilmumineen **a**');
INSERT INTO `en_transliteration` VALUES (3237, 'Inna rabbaka yaqdee baynahum bihukmihi wahuwa al\'azeezu al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (3238, 'Fatawakkal \'ala Allahi innaka \'ala alhaqqi almubeen **i**');
INSERT INTO `en_transliteration` VALUES (3239, 'Innaka la tusmi\'u almawta wala tusmi\'u a **l** ssumma a **l** ddu\'aa itha wallaw mudbireen **a**');
INSERT INTO `en_transliteration` VALUES (3240, 'Wama anta bihadee al\'umyi \'an dalalatihim in tusmi\'u illa man yuminu biayatina fahum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (3241, 'Waitha waqa\'a alqawlu \'alayhim akhrajna lahum dabbatan mina alardi tukallimuhum anna a **l** nnasa kanoo biayatina la yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (3242, 'Wayawma nahshuru min kulli ommatin fawjan mimman yukaththibu biayatina fahum yooza\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3243, 'Hatta itha jaoo qala akaththabtum biayatee walam tuheetoo biha \'ilman ammatha kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3244, 'Wawaqa\'a alqawlu \'alayhim bima _th_ alamoo fahum la yantiqoon **a**');
INSERT INTO `en_transliteration` VALUES (3245, 'Alam yaraw anna ja\'ana allayla liyaskunoo feehi wa **al** nnahara mubsiran inna fee thalika laayatin liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3246, 'Wayawma yunfakhu fee a **l** ssoori fafazi\'a man fee a **l** ssamawati waman fee alardi illa man shaa Allahu wakullun atawhu dakhireen **a**');
INSERT INTO `en_transliteration` VALUES (3247, 'Watara aljibala tahsabuha jamidatan wahiya tamurru marra a **l** ssahabi sun\'a Allahi allathee atqana kulla shayin innahu khabeerun bima taf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (3248, 'Man jaa bi **a** lhasanati falahu khayrun minha wahum min faza\'in yawmaithin aminoon **a**');
INSERT INTO `en_transliteration` VALUES (3249, 'Waman jaa bi **al** ssayyiati fakubbat wujoohuhum fee a **l** nnari hal tujzawna illa ma kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3250, 'Innama omirtu an a\'buda rabba hathihi albadati allathee harramaha walahu kullu shayin waomirtu an akoona mina almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (3251, 'Waan atluwa alqurana famani ihtada fainnama yahtadee linafsihi waman dalla faqul innama ana mina almunthireen **a**');
INSERT INTO `en_transliteration` VALUES (3252, 'Waquli alhamdu lillahi sayureekum ayatihi fata\'rifoonaha wama rabbuka bighafilin \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3253, 'Taseenmeem');
INSERT INTO `en_transliteration` VALUES (3254, 'Tilka ayatu alkitabi almubeen **i**');
INSERT INTO `en_transliteration` VALUES (3255, 'Natloo \'alayka min nabai moosa wafir\'awna bi **a** lhaqqi liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3256, 'Inna fir\'awna \'ala fee alardi waja\'ala ahlaha shiya\'an yastad\'ifu taifatan minhum yuthabbihu abnaahum wayastahyee nisaahum innahu kana mina almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (3257, 'Wanureedu an namunna \'ala allatheena istud\'ifoo fee alardi wanaj\'alahum aimmatan wanaj\'alahumu alwaritheen **a**');
INSERT INTO `en_transliteration` VALUES (3258, 'Wanumakkina lahum fee alardi wanuriya fir\'awna wahamana wajunoodahuma minhum ma kanoo yahtharoon **a**');
INSERT INTO `en_transliteration` VALUES (3259, 'Waawhayna ila ommi moosa an ardi\'eehi faitha khifti \'alayhi faalqeehi fee alyammi wala takhafee wala tahzanee inna raddoohu ilayki waja\'iloohu mina almursaleen **a**');
INSERT INTO `en_transliteration` VALUES (3260, 'Fa **i** ltaqatahu alu fir\'awna liyakoona lahum \'aduwwan wahazanan inna fir\'awna wahamana wajunoodahuma kanoo khatieen **a**');
INSERT INTO `en_transliteration` VALUES (3261, 'Waqalati imraatu fir\'awna qurratu \'aynin lee walaka la taqtuloohu \'asa an yanfa\'ana aw nattakhithahu waladan wahum la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (3262, 'Waasbaha fuadu ommi moosa farighan in kadat latubdee bihi lawla an rabatna \'ala qalbiha litakoona mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (3263, 'Waqalat liokhtihi qusseehi fabasurat bihi \'an junubin wahum la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (3264, 'Waharramna \'alayhi almaradi\'a min qablu faqalat hal adullukum \'ala ahli baytin yakfuloonahu lakum wahum lahu nasihoon **a**');
INSERT INTO `en_transliteration` VALUES (3265, 'Faradadnahu ila ommihi kay taqarra \'aynuha wala tahzana walita\'lama anna wa\'da Allahi haqqun walakinna aktharahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3266, 'Walamma balagha ashuddahu wa **i** stawaataynahu hukman wa\'ilman wakathalika najzee almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (3267, 'Wadakhala almadeenata \'alaheeni ghaflatin min ahliha fawajada feeha rajulayni yaqtatilani hatha min shee\'atihi wahatha min \'aduwwihi fa **i** staghathahu allathee min shee\'atihi \'ala allathee min \'aduwwihi fawakazahu moosa faqada \'alayhi qala hatha min \'amali a **l** shshaytani innahu \'aduwwun mudillun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (3268, 'Qala rabbi innee _th_ alamtu nafsee fa **i** ghfir lee faghafara lahu innahu huwa alghafooru a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (3269, 'Qala rabbi bima an\'amta \'alayya falan akoona _th_ aheeran lilmujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (3270, 'Faasbaha fee almadeenati khaifan yataraqqabu faitha allathee istansarahu bi **a** lamsi yastasrikhuhu qala lahu moosa innaka laghawiyyun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (3271, 'Falamma an arada an yabtisha bi **a** llathee huwa \'aduwwun lahuma qala ya moosa atureedu an taqtulanee kama qatata nafsan bi **a** lamsi in tureedu illa an takoona jabbaran fee alardi wama tureedu an takoona mina almusliheen **a**');
INSERT INTO `en_transliteration` VALUES (3272, 'Wajaa rajulun min aqsa almadeenati yas\'a qala ya moosa inna almalaa yatamiroona bika liyaqtulooka fa **o** khruj innee laka mina a **l** nnasiheen **a**');
INSERT INTO `en_transliteration` VALUES (3273, 'Fakharaja minha khaifan yataraqqabu qala rabbi najjinee mina alqawmi a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (3274, 'Walamma tawajjaha tilqaa madyana qala \'asa rabbee an yahdiyanee sawaa a **l** ssabeel **i**');
INSERT INTO `en_transliteration` VALUES (3275, 'Walamma warada maa madyana wajada \'alayhi ommatan mina a **l** nnasi yasqoona wawajada min doonihimu imraatayni tathoodani qala ma khatbukuma qalata la nasqee hatta yusdira a **l** rri\'ao waaboona shaykhun kabeer **un**');
INSERT INTO `en_transliteration` VALUES (3276, 'Fasaqa lahuma thumma tawalla ila a **l** _thth_ illi faqala rabbi innee lima anzata ilayya min khayrin faqeer **un**');
INSERT INTO `en_transliteration` VALUES (3277, 'Fajaathu ihdahuma tamshee \'ala istihyain qalat inna abee yad\'ooka liyajziyaka ajra ma saqayta lana falamma jaahu waqassa \'alayhi alqasasa qala la takhaf najawta mina alqawmi a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (3278, 'Qalat ihdahuma ya abati istajirhu inna khayra mani istajarta alqawiyyu alameen **u**');
INSERT INTO `en_transliteration` VALUES (3279, 'Qala innee oreedu an onkihaka ihda ibnatayya hatayni \'ala an tajuranee thamaniya hijajin fain atmamta \'ashran famin \'indika wama oreedu an ashuqqa \'alayka satajidunee in shaa Allahu mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (3280, 'Qala thalika baynee wabaynaka ayyama alajalayni qadaytu fala \'udwana \'alayya wa **A** llahu \'ala ma naqoolu wakeel **un**');
INSERT INTO `en_transliteration` VALUES (3281, 'Falamma qada moosaalajala wasara biahlihi anasa min janibi a **l** ttoori naran qala liahlihi omkuthoo innee anastu naran la\'allee ateekum minha bikhabarin aw jathwatin mina a **l** nnari la\'allakum tastaloon **a**');
INSERT INTO `en_transliteration` VALUES (3282, 'Falamma ataha noodiya min shatii alwadi alaymani fee albuq\'ati almubarakati mina a **l** shshajarati an ya moosa innee ana Allahu rabbu al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3283, 'Waan alqi \'asaka falamma raaha tahtazzu kaannaha jannun walla mudbiran walam yu\'aqqib ya moosa aqbil wala takhaf innaka mina alamineen **a**');
INSERT INTO `en_transliteration` VALUES (3284, 'Osluk yadaka fee jaybika takhruj baydaa min ghayri sooin wa **o** dmum ilayka janahaka mina a **l** rrahbi fathanika burhanani min rabbika ila fir\'awna wamalaihi innahum kanoo qawman fasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3285, 'Qala rabbi innee qatatu minhum nafsan faakhafu an yaqtuloon **i**');
INSERT INTO `en_transliteration` VALUES (3286, 'Waakhee haroonu huwa afsahu minnee lisanan faarsilhu ma\'iya ridan yusaddiqunee innee akhafu an yukaththiboon **i**');
INSERT INTO `en_transliteration` VALUES (3287, 'Qala sanashuddu \'adudaka biakheeka wanaj\'alu lakuma sultanan fala yasiloona ilaykuma biayatina antuma wamani ittaba\'akuma alghaliboon **a**');
INSERT INTO `en_transliteration` VALUES (3288, 'Falamma jaahum moosa biayatina bayyinatin qaloo ma hatha illa sihrun muftaran wama sami\'na bihatha fee abaina alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (3289, 'Waqala moosa rabbee a\'lamu biman jaa bi **a** lhuda min \'indihi waman takoonu lahu \'aqibatu a **l** ddari innahu la yuflihu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (3290, 'Waqala fir\'awnu ya ayyuha almalao ma \'alimtu lakum min ilahin ghayree faawqid lee ya hamanu \'ala a **l** tteeni fa **i** j\'al lee sarhan la\'allee attali\'u ila ilahi moosa wainnee laa _th_ unnuhu mina alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (3291, 'Wa **i** stakbara huwa wajunooduhu fee alardi bighayri alhaqqi wa _th_ annoo annahum ilayna la yurja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3292, 'Faakhathnahu wajunoodahu fanabathnahum fee alyammi fa **o** n _th_ ur kayfa kana \'aqibatu a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (3293, 'Waja\'anahum aimmatan yad\'oona ila a **l** nnari wayawma alqiyamati la yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (3294, 'Waatba\'nahum fee hathihi a **l** ddunya la\'natan wayawma alqiyamati hum mina almaqbooheen **a**');
INSERT INTO `en_transliteration` VALUES (3295, 'Walaqad atayna moosa alkitaba min ba\'di ma ahlakna alquroona aloola basaira li **l** nnasi wahudan warahmatan la\'allahum yatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (3296, 'Wama kunta bijanibi algharbiyyi ith qadayna ila moosa alamra wama kunta mina a **l** shshahideen **a**');
INSERT INTO `en_transliteration` VALUES (3297, 'Walakinna anshana quroonan fatatawala \'alayhimu al\'umuru wama kunta thawiyan fee ahli madyana tatloo \'alayhim ayatina walakinna kunna mursileen **a**');
INSERT INTO `en_transliteration` VALUES (3298, 'Wama kunta bijanibi a **l** ttoori ith nadayna walakin rahmatan min rabbika litunthira qawman ma atahum min natheerin min qablika la\'allahum yatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (3299, 'Walawla an tuseebahum museebatun bima qaddamat aydeehim fayaqooloo rabbana lawla arsata ilayna rasoolan fanattabi\'a ayatika wanakoona mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (3300, 'Falamma jaahumu alhaqqu min \'indina qaloo lawla ootiya mithla ma ootiya moosa awalam yakfuroo bima ootiya moosa min qablu qaloo sihrani ta _th_ ahara waqaloo inna bikullin kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (3301, 'Qul fatoo bikitabin min \'indi Allahi huwa ahda minhuma attabi\'hu in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3302, 'Fain lam yastajeeboo laka fa **i** \'lam annama yattabi\'oona ahwaahum waman adallu mimmani ittaba\'a hawahu bighayri hudan mina Allahi inna Allaha la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (3303, 'Walaqad wassana lahumu alqawla la\'allahum yatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (3304, 'Allatheena ataynahumu alkitaba min qablihi hum bihi yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3305, 'Waitha yutla \'alayhim qaloo amanna bihi innahu alhaqqu min rabbina inna kunna min qablihi muslimeen **a**');
INSERT INTO `en_transliteration` VALUES (3306, 'Olaika yutawna ajrahum marratayni bimasabaroo wayadraoona bi **a** lhasanati a **l** ssayyiata wamimma razaqnahum yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (3307, 'Waitha sami\'oo allaghwa a\'radoo \'anhu waqaloo lana a\'maluna walakum a\'malukum salamun \'alaykum la nabtaghee aljahileen **a**');
INSERT INTO `en_transliteration` VALUES (3308, 'Innaka la tahdee man ahbabta walakinna Allaha yahdee man yashao wahuwa a\'lamu bi **a** lmuhtadeen **a**');
INSERT INTO `en_transliteration` VALUES (3309, 'Waqaloo in nattabi\'i alhuda ma\'aka nutakhattaf min ardina awalam numakkin lahum haraman aminan yujba ilayhi thamaratu kulli shayin rizqan min ladunna walakinna aktharahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3310, 'Wakam ahlakna min qaryatin batirat ma\'eeshataha fatilka masakinuhum lam tuskan min ba\'dihim illa qaleelan wakunna nahnu alwaritheen **a**');
INSERT INTO `en_transliteration` VALUES (3311, 'Wama kana rabbuka muhlika alqurahatta yab\'atha fee ommiha rasoolan yatloo \'alayhim ayatina wama kunna muhlikee alqura illa waahluha _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (3312, 'Wama ooteetum min shayin famata\'u alhayati a **l** ddunya wazeenatuha wama \'inda Allahi khayrun waabqa afala ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (3313, 'Afaman wa\'adnahu wa\'dan hasanan fahuwa laqeehi kaman matta\'nahu mata\'a alhayati a **l** ddunya thumma huwa yawma alqiyamati mina almuhdareen **a**');
INSERT INTO `en_transliteration` VALUES (3314, 'Wayawma yunadeehim fayaqoolu ayna shurakaiya allatheena kuntum taz\'umoon **a**');
INSERT INTO `en_transliteration` VALUES (3315, 'Qala allatheena haqqa \'alayhimu alqawlu rabbana haolai allatheena aghwayna aghwaynahum kama ghawayna tabarrana ilayka ma kanoo iyyana ya\'budoon **a**');
INSERT INTO `en_transliteration` VALUES (3316, 'Waqeela od\'oo shurakaakum fada\'awhum falam yastajeeboo lahum waraawoo al\'athaba law annahum kanoo yahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (3317, 'Wayawma yunadeehim fayaqoolu matha ajabtumu almursaleen **a**');
INSERT INTO `en_transliteration` VALUES (3318, 'Fa\'amiyat \'alayhimu alanbao yawmaithin fahum la yatasaaloon **a**');
INSERT INTO `en_transliteration` VALUES (3319, 'Faamma man taba waamana wa\'amila salihan fa\'asa an yakoona mina almufliheen **a**');
INSERT INTO `en_transliteration` VALUES (3320, 'Warabbuka yakhluqu ma yashao wayakhtaru ma kana lahumu alkhiyaratu subhana Allahi wata\'ala \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (3321, 'Warabbuka ya\'lamu ma tukinnu sudooruhum wama yu\'linoon **a**');
INSERT INTO `en_transliteration` VALUES (3322, 'Wahuwa Allahu la ilaha illa huwa lahu alhamdu fee aloola wa **a** lakhirati walahu alhukmu wailayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3323, 'Qul araaytum in ja\'ala Allahu \'alaykumu allayla sarmadan ila yawmi alqiyamati man ilahun ghayru Allahi yateekum bidiyain afala tasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3324, 'Qul araaytum in ja\'ala Allahu \'alaykumu a **l** nnahara sarmadan ila yawmi alqiyamati man ilahun ghayru Allahi yateekum bilaylin taskunoona feehi afala tubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (3325, 'Wamin rahmatihi ja\'ala lakumu allayla wa **al** nnahara litaskunoo feehi walitabtaghoo min fadlihi wala\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (3326, 'Wayawma yunadeehim fayaqoolu ayna shurakaiya allatheena kuntum taz\'umoon **a**');
INSERT INTO `en_transliteration` VALUES (3327, 'Wanaza\'na min kulli ommatin shaheedan faqulna hatoo burhanakum fa\'alimoo anna alhaqqa lillahi wadalla \'anhum ma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (3328, 'Inna qaroona kana min qawmi moosa fabagha \'alayhim waataynahu mina alkunoozi ma inna mafatihahu latanooo bi **a** l\'usbati olee alquwwati ith qala lahu qawmuhu la tafrah inna Allaha la yuhibbu alfariheen **a**');
INSERT INTO `en_transliteration` VALUES (3329, 'Wa **i** btaghi feemaataka Allahu a **l** ddara alakhirata wala tansa naseebaka mina a **l** ddunya waahsin kama ahsana Allahu ilayka wala tabghi alfasada fee alardi inna Allaha la yuhibbu almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (3330, 'Qala innama ooteetuhu \'ala \'ilmin \'indee awalam ya\'lam anna Allaha qad ahlaka min qablihi mina alqurooni man huwa ashaddu minhu quwwatan waaktharu jam\'an wala yusalu \'an thunoobihimu almujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (3331, 'Fakharaja \'ala qawmihi fee zeenatihi qala allatheena yureedoona alhayata a **l** ddunya ya layta lana mithla ma ootiya qaroonu innahu lathoo ha _thth_ in \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (3332, 'Waqala allatheena ootoo al\'ilma waylakum thawabu Allahi khayrun liman amana wa\'amila salihan wala yulaqqaha illa a **l** ssabiroon **a**');
INSERT INTO `en_transliteration` VALUES (3333, 'Fakhasafna bihi wabidarihi alarda fama kana lahu min fiatin yansuroonahu min dooni Allahi wama kana mina almuntasireen **a**');
INSERT INTO `en_transliteration` VALUES (3334, 'Waasbaha allatheena tamannaw makanahu bi **a** lamsi yaqooloona waykaanna Allaha yabsutu a **l** rrizqa liman yashao min \'ibadihi wayaqdiru lawla an manna Allahu \'alayna lakhasafa bina waykaannahu la yuflihu alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (3335, 'Tilka a **l** ddaru alakhiratu naj\'aluha lillatheena la yureedoona \'uluwwan fee alardi wala fasadan wa **a** l\'aqibatu lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (3336, 'Man jaa bi **a** lhasanati falahu khayrun minha waman jaa bi **al** ssayyiati fala yujza allatheena \'amiloo a **l** ssayyiati illa ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3337, 'Inna allathee farada \'alayka alqurana laradduka ila ma\'adin qul rabbee a\'lamu man jaa bi **a** lhuda waman huwa fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (3338, 'Wama kunta tarjoo an yulqa ilayka alkitabu illa rahmatan min rabbika fala takoonanna _th_ aheeran lilkafireen **a**');
INSERT INTO `en_transliteration` VALUES (3339, 'Wala yasuddunnaka \'an ayati Allahi ba\'da ith onzilat ilayka wa **o** d\'u ila rabbika wala takoonanna mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (3340, 'Wala tad\'u ma\'a Allahi ilahan akhara la ilaha illa huwa kullu shayin halikun illa wajhahu lahu alhukmu wailayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3341, 'Aliflammeem');
INSERT INTO `en_transliteration` VALUES (3342, 'Ahasiba a **l** nnasu an yutrakoo an yaqooloo amanna wahum la yuftanoon **a**');
INSERT INTO `en_transliteration` VALUES (3343, 'Walaqad fatanna allatheena min qablihim falaya\'lamanna Allahu allatheena sadaqoo walaya\'lamanna alkathibeen **a**');
INSERT INTO `en_transliteration` VALUES (3344, 'Am hasiba allatheena ya\'maloona a **l** ssayyiati an yasbiqoona saa ma yahkumoon **a**');
INSERT INTO `en_transliteration` VALUES (3345, 'Man kana yarjoo liqaa Allahi fainna ajala Allahi laatin wahuwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (3346, 'Waman jahada fainnama yujahidu linafsihi inna Allaha laghaniyyun \'ani al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3347, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati lanukaffiranna \'anhum sayyiatihim walanajziyannahum ahsana allathee kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3348, 'Wawassayna alinsana biwalidayhi husnan wain jahadaka litushrika bee ma laysa laka bihi \'ilmun fala tuti\'huma ilayya marji\'ukum faonabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3349, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati lanudkhilannahum fee a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (3350, 'Wamina a **l** nnasi man yaqoolu amanna bi **A** llahi faitha oothiya fee Allahi ja\'ala fitnata a **l** nnasi ka\'athabi Allahi walain jaa nasrun min rabbika layaqoolunna inna kunna ma\'akum awalaysa Allahu bia\'lama bima fee sudoori al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3351, 'Walaya\'lamanna Allahu allatheena amanoo walaya\'lamanna almunafiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3352, 'Waqala allatheena kafaroo lillatheena amanoo ittabi\'oo sabeelana wanahmil khatayakum wama hum bihamileena min khatayahum min shayin innahum lakathiboon **a**');
INSERT INTO `en_transliteration` VALUES (3353, 'Walayahmilunna athqalahum waathqalan ma\'a athqalihim walayusalunna yawma alqiyamati \'amma kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (3354, 'Walaqad arsana noohan ila qawmihi falabitha feehim alfa sanatin illa khamseena \'aman faakhathahumu a **l** ttoofanu wahum _th_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (3355, 'Faanjaynahu waashaba a **l** ssafeenati waja\'anahaayatan lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3356, 'Waibraheema ith qala liqawmihi o\'budoo Allaha wa **i** ttaqoohu thalikum khayrun lakum in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3357, 'Innama ta\'budoona min dooni Allahi awthanan watakhluqoona ifkan inna allatheena ta\'budoona min dooni Allahi la yamlikoona lakum rizqan fa **i** btaghoo \'inda Allahi a **l** rrizqa wa **o** \'budoohu wa **o** shkuroo lahu ilayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3358, 'Wain tukaththiboo faqad kaththaba omamun min qablikum wama \'ala a **l** rrasooli illa albalaghu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (3359, 'Awalam yaraw kayfa yubdio Allahu alkhalqa thumma yu\'eeduhu inna thalika \'ala Allahi yaseer **un**');
INSERT INTO `en_transliteration` VALUES (3360, 'Qul seeroo fee alardi fa **o** n _th_ uroo kayfa badaa alkhalqa thumma Allahu yunshio a **l** nnashata alakhirata inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (3361, 'Yu\'aththibu man yashao wayarhamu man yashao wailayhi tuqlaboon **a**');
INSERT INTO `en_transliteration` VALUES (3362, 'Wama antum bimu\'jizeena fee alardi wala fee a **l** ssamai wama lakum min dooni Allahi min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (3363, 'Wa **a** llatheena kafaroo biayati Allahi waliqaihi olaika yaisoo min rahmatee waolaika lahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (3364, 'Fama kana jawaba qawmihi illa an qaloo oqtuloohu aw harriqoohu faanjahu Allahu mina a **l** nnari inna fee thalika laayatin liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3365, 'Waqala innama ittakhathtum min dooni Allahi awthanan mawaddata baynikum fee alhayati a **l** ddunya thumma yawma alqiyamati yakfuru ba\'dukum biba\'din wayal\'anu ba\'dukum ba\'dan wamawakumu a **l** nnaru wama lakum min nasireen **a**');
INSERT INTO `en_transliteration` VALUES (3366, 'Faamana lahu lootun waqala innee muhajirun ila rabbee innahu huwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (3367, 'Wawahabna lahu ishaqa waya\'qooba waja\'ana fee thurriyyatihi a **l** nnubuwwata wa **a** lkitaba waataynahu ajrahu fee a **l** ddunya wainnahu fee alakhirati lamina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (3368, 'Walootan ith qala liqawmihi innakum latatoona alfahishata ma sabaqakum biha min ahadin mina al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3369, 'Ainnakum latatoona a **l** rrijala wataqta\'oona a **l** ssabeela watatoona fee nadeekumu almunkara fama kana jawaba qawmihi illa an qaloo itina bi\'athabi Allahi in kunta mina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3370, 'Qala rabbi onsurnee \'ala alqawmi almufsideen **a**');
INSERT INTO `en_transliteration` VALUES (3371, 'Walamma jaat rusuluna ibraheema bi **a** lbushra qaloo inna muhlikoo ahli hathihi alqaryati inna ahlaha kanoo _th_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (3372, 'Qala inna feeha lootan qaloo nahnu a\'lamu biman feeha lanunajjiyannahu waahlahu illa imraatahu kanat mina alghabireen **a**');
INSERT INTO `en_transliteration` VALUES (3373, 'Walamma an jaat rusuluna lootan seea bihim wadaqa bihim thar\'an waqaloo la takhaf wala tahzan inna munajjooka waahlaka illa imraataka kanat mina alghabireen **a**');
INSERT INTO `en_transliteration` VALUES (3374, 'Inna munziloona \'ala ahli hathihi alqaryati rijzan mina a **l** ssamai bima kanoo yafsuqoon **a**');
INSERT INTO `en_transliteration` VALUES (3375, 'Walaqad tarakna minhaayatan bayyinatan liqawmin ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (3376, 'Waila madyana akhahum shu\'ayban faqala ya qawmi o\'budoo Allaha wa **o** rjoo alyawma alakhira wala ta\'thaw fee alardi mufsideen **a**');
INSERT INTO `en_transliteration` VALUES (3377, 'Fakaththaboohu faakhathathumu a **l** rrajfatu faasbahoo fee darihim jathimeen **a**');
INSERT INTO `en_transliteration` VALUES (3378, 'Wa\'adan wathamooda waqad tabayyana lakum min masakinihim wazayyana lahumu a **l** shshaytanu a\'malahum fasaddahum \'ani a **l** ssabeeli wakanoo mustabsireen **a**');
INSERT INTO `en_transliteration` VALUES (3379, 'Waqaroona wafir\'awna wahamana walaqad jaahum moosa bi **a** lbayyinati fa **i** stakbaroo fee alardi wama kanoo sabiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3380, 'Fakullan akhathna bithanbihi faminhum man arsana \'alayhi hasiban waminhum man akhathathu a **l** ssayhatu waminhum man khasafna bihi alarda waminhum man aghraqna wama kana Allahu liya _th_ limahum walakin kanoo anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (3381, 'Mathalu allatheena ittakhathoo min dooni Allahi awliyaa kamathali al\'ankabooti ittakhathat baytan wainna awhana albuyooti labaytu al\'ankabooti law kanoo ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3382, 'Inna Allaha ya\'lamu ma yad\'oona min doonihi min shayin wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (3383, 'Watilka alamthalu nadribuha li **l** nnasi wama ya\'qiluha illa al\'alimoon **a**');
INSERT INTO `en_transliteration` VALUES (3384, 'Khalaqa Allahu a **l** ssamawati wa **a** larda bi **a** lhaqqi inna fee thalika laayatan lilmumineen **a**');
INSERT INTO `en_transliteration` VALUES (3385, 'Otlu ma oohiya ilayka mina alkitabi waaqimi a **l** ssalata inna a **l** ssalata tanha \'ani alfahshai wa **a** lmunkari walathikru Allahi akbaru wa **A** llahu ya\'lamu ma tasna\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3386, 'Wala tujadiloo ahla alkitabi illa bi **a** llatee hiya ahsanu illa allatheena _th_ alamoo minhum waqooloo amanna bi **a** llathee onzila ilayna waonzila ilaykum wailahuna wailahukum wahidun wanahnu lahu muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (3387, 'Wakathalika anzana ilayka alkitaba fa **a** llatheena ataynahumu alkitaba yuminoona bihi wamin haolai man yuminu bihi wama yajhadu biayatina illa alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (3388, 'Wama kunta tatloo min qablihi min kitabin wala takhuttuhu biyameenika ithan la **i** rtaba almubtiloon **a**');
INSERT INTO `en_transliteration` VALUES (3389, 'Bal huwa ayatun bayyinatun fee sudoori allatheena ootoo al\'ilma wama yajhadu biayatina illa a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (3390, 'Waqaloo lawla onzila \'alayhi ayatun min rabbihi qul innama alayatu \'inda Allahi wainnama ana natheerun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (3391, 'Awalam yakfihim anna anzana \'alayka alkitaba yutla \'alayhim inna fee thalika larahmatan wathikra liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3392, 'Qul kafa bi **A** llahi baynee wabaynakum shaheedan ya\'lamu ma fee a **l** ssamawati wa **a** lardi wa **a** llatheena amanoo bi **a** lbatili wakafaroo bi **A** llahi olaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (3393, 'Wayasta\'jiloonaka bi **a** l\'athabi walawla ajalun musamman lajaahumu al\'athabu walayatiyannahum baghtatan wahum la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (3394, 'Yasta\'jiloonaka bi **a** l\'athabi wainna jahannama lamuheetatun bi **a** lkafireen **a**');
INSERT INTO `en_transliteration` VALUES (3395, 'Yawma yaghshahumu al\'athabu min fawqihim wamin tahti arjulihim wayaqoolu thooqoo ma kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3396, 'Ya \'ibadiya allatheena amanoo inna ardee wasi\'atun faiyyaya fa **o** \'budoon **i**');
INSERT INTO `en_transliteration` VALUES (3397, 'Kullu nafsin thaiqatu almawti thumma ilayna turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3398, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati lanubawwiannahum mina aljannati ghurafan tajree min tahtiha alanharu khalideena feeha ni\'ma ajru al\'amileen **a**');
INSERT INTO `en_transliteration` VALUES (3399, 'Allatheena sabaroo wa\'ala rabbihim yatawakkaloon **a**');
INSERT INTO `en_transliteration` VALUES (3400, 'Wakaayyin min dabbatin la tahmilu rizqaha Allahu yarzuquha waiyyakum wahuwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (3401, 'Walain saatahum man khalaqa a **l** ssamawati wa **a** larda wasakhkhara a **l** shshamsa wa **a** lqamara layaqoolunna Allahu faanna yufakoon **a**');
INSERT INTO `en_transliteration` VALUES (3402, 'Allahu yabsutu a **l** rrizqa liman yashao min \'ibadihi wayaqdiru lahu inna Allaha bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (3403, 'Walain saatahum man nazzala mina a **l** ssamai maan faahya bihi alarda min ba\'di mawtiha layaqoolunna Allahu quli alhamdu lillahi bal aktharuhum la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (3404, 'Wama hathihi alhayatu a **l** ddunya illa lahwun wala\'ibun wainna a **l** ddara alakhirata lahiya alhayawanu law kanoo ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3405, 'Faitha rakiboo fee alfulki da\'awoo Allaha mukhliseena lahu a **l** ddeena falamma najjahum ila albarri itha hum yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (3406, 'Liyakfuroo bimaataynahum waliyatamatta\'oo fasawfa ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3407, 'Awalam yaraw anna ja\'anaharaman aminan wayutakhattafu a **l** nnasu min hawlihim afabi **a** lbatili yuminoona wabini\'mati Allahi yakfuroon **a**');
INSERT INTO `en_transliteration` VALUES (3408, 'Waman a _th_ lamu mimmani iftara \'ala Allahi kathiban aw kaththaba bi **a** lhaqqi lamma jaahu alaysa fee jahannama mathwan lilkafireen **a**');
INSERT INTO `en_transliteration` VALUES (3409, 'Wa **a** llatheena jahadoo feena lanahdiyannahum subulana wainna Allaha lama\'a almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (3410, 'Aliflammeem');
INSERT INTO `en_transliteration` VALUES (3411, 'Ghulibati a **l** rroom **u**');
INSERT INTO `en_transliteration` VALUES (3412, 'Fee adna alardi wahum min ba\'di ghalabihim sayaghliboon **a**');
INSERT INTO `en_transliteration` VALUES (3413, 'Fee bid\'i sineena lillahi alamru min qablu wamin ba\'du wayawmaithin yafrahu almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3414, 'Binasri Allahi yansuru man yashao wahuwa al\'azeezu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (3415, 'Wa\'da Allahi la yukhlifu Allahu wa\'dahu walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3416, 'Ya\'lamoona _th_ ahiran mina alhayati a **l** ddunya wahum \'ani alakhirati hum ghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (3417, 'Awalam yatafakkaroo fee anfusihim ma khalaqa Allahu a **l** ssamawati wa **a** larda wama baynahuma illa bi **a** lhaqqi waajalin musamman wainna katheeran mina a **l** nnasi biliqai rabbihim lakafiroon **a**');
INSERT INTO `en_transliteration` VALUES (3418, 'Awalam yaseeroo fee alardi fayan _th_ uroo kayfa kana \'aqibatu allatheena min qablihim kanoo ashadda minhum quwwatan waatharoo alarda wa\'amarooha akthara mimma \'amarooha wajaathum rusuluhum bi **a** lbayyinati fama kana Allahu liya _th_ limahum walakin kanoo anfusahum ya _th_ limoon **a**');
INSERT INTO `en_transliteration` VALUES (3419, 'Thumma kana \'aqibata allatheena asaoo a **l** ssooa an kaththaboo biayati Allahi wakanoo biha yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (3420, 'Allahu yabdao alkhalqa thumma yu\'eeduhu thumma ilayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3421, 'Wayawma taqoomu a **l** ssa\'atu yublisu almujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (3422, 'Walam yakun lahum min shurakaihim shufa\'ao wakanoo bishurakaihim kafireen **a**');
INSERT INTO `en_transliteration` VALUES (3423, 'Wayawma taqoomu a **l** ssa\'atu yawmaithin yatafarraqoon **a**');
INSERT INTO `en_transliteration` VALUES (3424, 'Faamma allatheena amanoo wa\'amiloo a **l** ssalihati fahum fee rawdatin yuhbaroon **a**');
INSERT INTO `en_transliteration` VALUES (3425, 'Waamma allatheena kafaroo wakaththaboo biayatina waliqai alakhirati faolaika fee al\'athabi muhdaroon **a**');
INSERT INTO `en_transliteration` VALUES (3426, 'Fasubhana Allahi heena tumsoona waheena tusbihoon **a**');
INSERT INTO `en_transliteration` VALUES (3427, 'Walahu alhamdu fee a **l** ssamawati wa **a** lardi wa\'ashiyyan waheena tu _th_ hiroon **a**');
INSERT INTO `en_transliteration` VALUES (3428, 'Yukhriju alhayya mina almayyiti wayukhriju almayyita mina alhayyi wayuhyee alarda ba\'da mawtiha wakathalika tukhrajoon **a**');
INSERT INTO `en_transliteration` VALUES (3429, 'Wamin ayatihi an khalaqakum min turabin thumma itha antum basharun tantashiroon **a**');
INSERT INTO `en_transliteration` VALUES (3430, 'Wamin ayatihi an khalaqa lakum min anfusikum azwajan litaskunoo ilayha waja\'ala baynakum mawaddatan warahmatan inna fee thalika laayatin liqawmin yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (3431, 'Wamin ayatihi khalqu a **l** ssamawati wa **a** lardi wa **i** khtilafu asinatikum waalwanikum inna fee thalika laayatin lil\'alimeen **a**');
INSERT INTO `en_transliteration` VALUES (3432, 'Wamin ayatihi manamukum bi **a** llayli wa **al** nnahari wa **i** btighaokum min fadlihi inna fee thalika laayatin liqawmin yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3433, 'Wamin ayatihi yureekumu albarqa khawfan watama\'an wayunazzilu mina a **l** ssamai maan fayuhyee bihi alarda ba\'da mawtiha inna fee thalika laayatin liqawmin ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (3434, 'Wamin ayatihi an taqooma a **l** ssamao wa **a** lardu biamrihi thumma itha da\'akum da\'watan mina alardi itha antum takhrujoon **a**');
INSERT INTO `en_transliteration` VALUES (3435, 'Walahu man fee a **l** ssamawati wa **a** lardi kullun lahu qanitoon **a**');
INSERT INTO `en_transliteration` VALUES (3436, 'Wahuwa allathee yabdao alkhalqa thumma yu\'eeduhu wahuwa ahwanu \'alayhi walahu almathalu ala\'la fee a **l** ssamawati wa **a** lardi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (3437, 'Daraba lakum mathalan min anfusikum hal lakum mi mma malakat aymanukum min shurakaa fee ma razaqnakum faantum feehi sawaon takhafoonahum kakheefatikum anfusakum kathalika nufassilu alayati liqawmin ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (3438, 'Bali ittaba\'a allatheena _th_ alamoo ahwaahum bighayri \'ilmin faman yahdee man adalla Allahu wama lahum min nasireen **a**');
INSERT INTO `en_transliteration` VALUES (3439, 'Faaqim wajhaka li **l** ddeeni haneefan fitrata Allahi allatee fatara a **l** nnasa \'alayha la tabdeela likhalqi Allahi thalika a **l** ddeenu alqayyimu walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3440, 'Muneebeena ilayhi wa **i** ttaqoohu waaqeemoo a **l** ssalata wala takoonoo mina almushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (3441, 'Mina allatheena farraqoo deenahum wakanoo shiya\'an kullu hizbin bima ladayhim farihoon **a**');
INSERT INTO `en_transliteration` VALUES (3442, 'Waitha massa a **l** nnasa durrun da\'aw rabbahum muneebeena ilayhi thumma itha athaqahum minhu rahmatan itha fareequn minhum birabbihim yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (3443, 'Liyakfuroo bimaataynahum fatamatta\'oo fasawfa ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3444, 'Am anzana \'alayhim sultanan fahuwa yatakallamu bima kanoo bihi yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (3445, 'Wa **i** tha athaqna a **l** nnasa rahmatan farihoo biha wain tusibhum sayyiatun bima qaddamat aydeehim itha hum yaqnatoon **a**');
INSERT INTO `en_transliteration` VALUES (3446, 'Awalam yaraw anna Allaha yabsutu a **l** rrizqa liman yashao wayaqdiru inna fee thalika laayatin liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (3447, 'Faati tha alqurbahaqqahu wa **a** lmiskeena wa **i** bna a **l** ssabeeli thalika khayrun lillatheena yureedoona wajha Allahi waolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (3448, 'Wamaataytum min riban liyarbuwa fee amwali a **l** nnasi fala yarboo \'inda Allahi wamaataytum min zakatin tureedoona wajha Allahi faolaika humu almud\'ifoon **a**');
INSERT INTO `en_transliteration` VALUES (3449, 'Allahu allathee khalaqakum thumma razaqakum thumma yumeetukum thumma yuhyeekum hal min shurakaikum man yaf\'alu min thalikum min shayin subhanahu wata\'ala \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (3450, '_Th_ ahara alfasadu fee albarri wa **a** lbahri bima kasabat aydee a **l** nnasi liyutheeqahum ba\'da allathee \'amiloo la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3451, 'Qul seeroo fee alardi fa **o** n _th_ uroo kayfa kana \'aqibatu allatheena min qablu kana aktharuhum mushrikeen **a**');
INSERT INTO `en_transliteration` VALUES (3452, 'Faaqim wajhaka li **l** ddeeni alqayyimi min qabli an yatiya yawmun la maradda lahu mina Allahi yawmaithin yassadda\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3453, 'Man kafara fa\'alayhi kufruhu waman \'amila salihan falianfusihim yamhadoon **a**');
INSERT INTO `en_transliteration` VALUES (3454, 'Liyajziya allatheena amanoo wa\'amiloo a **l** ssalihati min fadlihi innahu la yuhibbu alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (3455, 'Wamin ayatihi an yursila a **l** rriyaha mubashshiratin waliyutheeqakum min rahmatihi walitajriya alfulku biamrihi walitabtaghoo min fadlihi wala\'allakum tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (3456, 'Walaqad arsana min qablika rusulan ila qawmihim fajaoohum bi **a** lbayyinati fa **i** ntaqamna mina allatheena ajramoo wakana haqqan \'alayna nasru almumineen **a**');
INSERT INTO `en_transliteration` VALUES (3457, 'Allahu allathee yursilu a **l** rriyaha fatutheeru sahaban fayabsutuhu fee a **l** ssamai kayfa yashao wayaj\'aluhu kisafan fatara alwadqa yakhruju min khilalihi faitha asaba bihi man yashao min \'ibadihi itha hum yastabshiroon **a**');
INSERT INTO `en_transliteration` VALUES (3458, 'Wain kanoo min qabli an yunazzala \'alayhim min qablihi lamubliseen **a**');
INSERT INTO `en_transliteration` VALUES (3459, 'Fa **o** n _th_ ur ilaathari rahmati Allahi kayfa yuhyee alarda ba\'da mawtiha inna thalika lamuhyee almawta wahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (3460, 'Walain arsana reehan faraawhu musfarran la _th_ alloo min ba\'dihi yakfuroon **a**');
INSERT INTO `en_transliteration` VALUES (3461, 'Fainnaka la tusmi\'u almawta wala tusmi\'u a **l** ssumma a **l** ddu\'aa itha wallaw mudbireen **a**');
INSERT INTO `en_transliteration` VALUES (3462, 'Wama anta bihadi al\'umyi \'an dalalatihim in tusmi\'u illa man yuminu biayatina fahum muslimoon **a**');
INSERT INTO `en_transliteration` VALUES (3463, 'Allahu allathee khalaqakum min da\'fin thumma ja\'ala min ba\'di da\'fin quwwatan thumma ja\'ala min ba\'di quwwatin da\'fan washaybatan yakhluqu ma yashao wahuwa al\'aleemu alqadeer **u**');
INSERT INTO `en_transliteration` VALUES (3464, 'Wayawma taqoomu a **l** ssa\'atu yuqsimu almujrimoona ma labithoo ghayra sa\'atin kathalika kanoo yufakoon **a**');
INSERT INTO `en_transliteration` VALUES (3465, 'Waqala allatheena ootoo al\'ilma wa **a** leemana laqad labithtum fee kitabi Allahi ila yawmi alba\'thi fahatha yawmu alba\'thi walakinnakum kuntum la ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3466, 'Fayawmaithin la yanfa\'u allatheena _th_ alamoo ma\'thiratuhum wala hum yusta\'taboon **a**');
INSERT INTO `en_transliteration` VALUES (3467, 'Walaqad darabna li **l** nnasi fee hatha alqurani min kulli mathalin walain jitahum biayatin layaqoolanna allatheena kafaroo in antum illa mubtiloon **a**');
INSERT INTO `en_transliteration` VALUES (3468, 'Kathalika yatba\'u Allahu \'ala quloobi allatheena la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3469, 'Fa **i** sbir inna wa\'da Allahi haqqun wala yastakhiffannaka allatheena la yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (3470, 'Aliflammeem');
INSERT INTO `en_transliteration` VALUES (3471, 'Tilka ayatu alkitabi alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (3472, 'Hudan warahmatan lilmuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (3473, 'Allatheena yuqeemoona a **l** ssalata wayutoona a **l** zzakata wahum bi **a** lakhirati hum yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (3474, 'Olaika \'ala hudan min rabbihim waolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (3475, 'Wamina a **l** nnasi man yashtaree lahwa alhadeethi liyudilla \'an sabeeli Allahi bighayri \'ilmin wayattakhithaha huzuwan olaika lahum \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (3476, 'Waitha tutla \'alayhi ayatuna walla mustakbiran kaan lam yasma\'ha kaanna fee othunayhi waqran fabashshirhu bi\'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (3477, 'Inna allatheena amanoo wa\'amiloo a **l** ssalihati lahum jannatu a **l** nna\'eem **i**');
INSERT INTO `en_transliteration` VALUES (3478, 'Khalideena feeha wa\'da Allahi haqqan wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (3479, 'Khalaqa a **l** ssamawati bighayri \'amadin tarawnaha waalqa fee alardi rawasiya an tameeda bikum wabaththa feeha min kulli dabbatin waanzana mina a **l** ssamai maan faanbatna feeha min kulli zawjin kareem **in**');
INSERT INTO `en_transliteration` VALUES (3480, 'Hatha khalqu Allahi faaroonee matha khalaqa allatheena min doonihi bali a **l** _thth_ alimoona fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (3481, 'Walaqad atayna luqmana alhikmata ani oshkur lillahi waman yashkur fainnama yashkuru linafsihi waman kafara fainna Allaha ghaniyyun hameed **un**');
INSERT INTO `en_transliteration` VALUES (3482, 'Waith qala luqmanu li **i** bnihi wahuwa ya\'i _th_ uhu ya bunayya la tushrik bi **A** llahi inna a **l** shshirka la _th_ ulmun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (3483, 'Wawassayna alinsana biwalidayhi hamalathu ommuhu wahnan \'ala wahnin wafisaluhu fee \'amayni ani oshkur lee waliwalidayka ilayya almaseer **u**');
INSERT INTO `en_transliteration` VALUES (3484, 'Wain jahadaka \'ala an tushrika bee ma laysa laka bihi \'ilmun fala tuti\'huma wasahibhuma fee a **l** ddunya ma\'roofan wa **i** ttabi\' sabeela man anaba ilayya thumma ilayya marji\'ukum faonabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3485, 'Ya bunayya innaha in taku mithqala habbatin min khardalin fatakun fee sakhratin aw fee a **l** ssamawati aw fee alardi yati biha Allahu inna Allaha lateefun khabeer **un**');
INSERT INTO `en_transliteration` VALUES (3486, 'Ya bunayya aqimi a **l** ssalata wamur bi **a** lma\'roofi wa **i** nha \'ani almunkari wa **i** sbir \'ala ma asabaka inna thalika min \'azmi alomoor **i**');
INSERT INTO `en_transliteration` VALUES (3487, 'Wala tusa\'\'ir khaddaka li **l** nnasi wala tamshi fee alardi marahan inna Allaha la yuhibbu kulla mukhtalin fakhoor **in**');
INSERT INTO `en_transliteration` VALUES (3488, 'Wa **i** qsid fee mashyika wa **o** ghdud min sawtika inna ankara alaswati lasawtu alhameer **i**');
INSERT INTO `en_transliteration` VALUES (3489, 'Alam taraw anna Allaha sakhkhara lakum ma fee a **l** ssamawati wama fee alardi waasbagha \'alaykum ni\'amahu _th_ ahiratan wabatinatan wamina a **l** nnasi man yujadilu fee Allahi bighayri \'ilmin wala hudan wala kitabin muneer **in**');
INSERT INTO `en_transliteration` VALUES (3490, 'Waitha qeela lahumu ittabi\'oo ma anzala Allahu qaloo bal nattabi\'u ma wajadna \'alayhi abaana awalaw kana a **l** shshaytanu yad\'oohum ila \'athabi a **l** ssa\'eer **i**');
INSERT INTO `en_transliteration` VALUES (3491, 'Waman yuslim wajhahu ila Allahi wahuwa muhsinun faqadi istamsaka bi **a** l\'urwati alwuthqa waila Allahi \'aqibatu alomoor **i**');
INSERT INTO `en_transliteration` VALUES (3492, 'Waman kafara fala yahzunka kufruhu ilayna marji\'uhum fanunabbiohum bima \'amiloo inna Allaha \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (3493, 'Numatti\'uhum qaleelan thumma nadtarruhum ila \'athabin ghalee _th_ **in**');
INSERT INTO `en_transliteration` VALUES (3494, 'Walain saatahum man khalaqa a **l** ssamawati wa **a** larda layaqoolunna Allahu quli alhamdu lillahi bal aktharuhum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3495, 'Lillahi ma fee a **l** ssamawati wa **a** lardi inna Allaha huwa alghaniyyu alhameed **u**');
INSERT INTO `en_transliteration` VALUES (3496, 'Walaw annama fee alardi min shajaratin aqlamun wa **a** lbahru yamudduhu min ba\'dihi sab\'atu abhurin ma nafidat kalimatu Allahi inna Allaha \'azeezun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (3497, 'Ma khalqukum wala ba\'thukum illa kanafsin wahidatin inna Allaha samee\'un baseer **un**');
INSERT INTO `en_transliteration` VALUES (3498, 'Alam tara anna Allaha yooliju allayla fee a **l** nnahari wayooliju a **l** nnahara fee allayli wasakhkhara a **l** shshamsa wa **a** lqamara kullun yajree ila ajalin musamman waanna Allaha bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (3499, 'Thalika bianna Allaha huwa alhaqqu waanna ma yad\'oona min doonihi albatilu waanna Allaha huwa al\'aliyyu alkabeer **u**');
INSERT INTO `en_transliteration` VALUES (3500, 'Alam tara anna alfulka tajree fee albahri bini\'mati Allahi liyuriyakum min ayatihi inna fee thalika laayatin likulli sabbarin shakoor **in**');
INSERT INTO `en_transliteration` VALUES (3501, 'Waitha ghashiyahum mawjun ka **al** _thth_ ulali da\'awoo Allaha mukhliseena lahu a **l** ddeena falamma najjahum ila albarri faminhum muqtasidun wama yajhadu biayatina illa kullu khattarin kafoor **in**');
INSERT INTO `en_transliteration` VALUES (3502, 'Ya ayyuha a **l** nnasu ittaqoo rabbakum wa **i** khshaw yawman la yajzee walidun \'an waladihi wala mawloodun huwa jazin \'an walidihi shayan inna wa\'da Allahi haqqun fala taghurrannakumu alhayatu a **l** ddunya wala yaghurrannakum bi **A** llahi algharoor **u**');
INSERT INTO `en_transliteration` VALUES (3503, 'Inna Allaha \'indahu \'ilmu a **l** ssa\'ati wayunazzilu alghaytha waya\'lamu ma fee alarhami wama tadree nafsun matha taksibu ghadan wama tadree nafsun biayyi ardin tamootu inna Allaha \'aleemun khabeer **un**');
INSERT INTO `en_transliteration` VALUES (3504, 'Aliflammeem');
INSERT INTO `en_transliteration` VALUES (3505, 'Tanzeelu alkitabi la rayba feehi min rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (3506, 'Am yaqooloona iftarahu bal huwa alhaqqu min rabbika litunthira qawman ma atahum min natheerin min qablika la\'allahum yahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (3507, 'Allahu allathee khalaqa a **l** ssamawati wa **a** larda wama baynahuma fee sittati ayyamin thumma istawa \'ala al\'arshi ma lakum min doonihi min waliyyin wala shafee\'in afala tatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (3508, 'Yudabbiru alamra mina a **l** ssamai ila alardi thumma ya\'ruju ilayhi fee yawmin kana miqdaruhu alfa sanatin mimma ta\'uddoon **a**');
INSERT INTO `en_transliteration` VALUES (3509, 'Thalika \'alimu alghaybi wa **al** shshahadati al\'azeezu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (3510, 'Allathee ahsana kulla shayin khalaqahu wabadaa khalqa alinsani min teen **in**');
INSERT INTO `en_transliteration` VALUES (3511, 'Thumma ja\'ala naslahu min sulalatin min main maheen **in**');
INSERT INTO `en_transliteration` VALUES (3512, 'Thumma sawwahu wanafakha feehi min roohihi waja\'ala lakumu a **l** ssam\'a wa **a** labsara wa **a** lafidata qaleelan ma tashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (3513, 'Waqaloo aithadalana fee alardi ainna lafee khalqin jadeedin bal hum biliqai rabbihim kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (3514, 'Qul yatawaffakum malaku almawti allathee wukkila bikum thumma ila rabbikum turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3515, 'Walaw tara ithi almujrimoona nakisoo ruoosihim \'inda rabbihim rabbana absarna wasami\'na faarji\'na na\'mal salihan inna mooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (3516, 'Walaw shina laatayna kulla nafsin hudaha walakin haqqa alqawlu minnee laamlaanna jahannama mina aljinnati wa **al** nnasi ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (3517, 'Fathooqoo bima naseetum liqaa yawmikum hatha inna naseenakum wathooqoo \'athaba alkhuldi bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3518, 'Innama yuminu biayatina allatheena ithathukkiroo biha kharroo sujjadan wasabbahoo bihamdi rabbihim wahum la yastakbiroon **a**');
INSERT INTO `en_transliteration` VALUES (3519, 'Tatajafa junoobuhum \'ani almadaji\'i yad\'oona rabbahum khawfan watama\'an wamimma razaqnahum yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (3520, 'Fala ta\'lamu nafsun ma okhfiya lahum min qurrati a\'yunin jazaan bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3521, 'Afaman kana muminan kaman kana fasiqan la yastawoon **a**');
INSERT INTO `en_transliteration` VALUES (3522, 'Amma allatheena amanoo wa\'amiloo a **l** ssalihati falahum jannatu almawa nuzulan bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3523, 'Waamma allatheena fasaqoo famawahumu a **l** nnaru kullama aradoo an yakhrujoo minha o\'eedoo feeha waqeela lahum thooqoo \'athaba a **l** nnari allathee kuntum bihi tukaththiboon **a**');
INSERT INTO `en_transliteration` VALUES (3524, 'Walanutheeqannahum mina al\'athabi aladna doona al\'athabi alakbari la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3525, 'Waman a _th_ lamu mimman thukkira biayati rabbihi thumma a\'rada \'anha inna mina almujrimeena muntaqimoon **a**');
INSERT INTO `en_transliteration` VALUES (3526, 'Walaqad atayna moosa alkitaba fala takun fee miryatin min liqaihi waja\'anahu hudan libanee israeel **a**');
INSERT INTO `en_transliteration` VALUES (3527, 'Waja\'ana minhum aimmatan yahdoona biamrina lammasabaroo wakanoo biayatina yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (3528, 'Inna rabbaka huwa yafsilu baynahum yawma alqiyamati feema kanoo feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (3529, 'Awalam yahdi lahum kam ahlakna min qablihim mina alqurooni yamshoona fee masakinihim inna fee thalika laayatin afala yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (3530, 'Awalam yaraw anna nasooqu almaa ila alardi aljuruzi fanukhriju bihi zar\'an takulu minhu an\'amuhum waanfusuhum afala yubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (3531, 'Wayaqooloona mata hatha alfathu in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3532, 'Qul yawma alfathi la yanfa\'u allatheena kafaroo eemanuhum wala hum yun _th_ aroon **a**');
INSERT INTO `en_transliteration` VALUES (3533, 'Faa\'rid \'anhum wa **i** nta _th_ ir innahum munta _th_ iroon **a**');
INSERT INTO `en_transliteration` VALUES (3534, 'Ya ayyuha a **l** nnabiyyu ittaqi Allaha wala tuti\'i alkafireena wa **a** lmunafiqeena inna Allaha kana \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (3535, 'Wa **i** ttabi\' ma yooha ilayka min rabbika inna Allaha kana bima ta\'maloona khabeera **n**');
INSERT INTO `en_transliteration` VALUES (3536, 'Watawakkal \'ala Allahi wakafa bi **A** llahi wakeela **n**');
INSERT INTO `en_transliteration` VALUES (3537, 'Ma ja\'ala Allahu lirajulin min qalbayni fee jawfihi wama ja\'ala azwajakumu allaee tu _th_ ahiroona minhunna ommahatikum wama ja\'ala ad\'iyaakum abnaakum thalikum qawlukum biafwahikum wa **A** llahu yaqoolu alhaqqa wahuwa yahdee a **l** ssabeel **a**');
INSERT INTO `en_transliteration` VALUES (3538, 'Od\'oohum liabaihim huwa aqsatu \'inda Allahi fain lam ta\'lamoo abaahum faikhwanukum fee a **l** ddeeni wamawaleekum walaysa \'alaykum junahun feema akhtatum bihi walakin ma ta\'ammadat quloobukum wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (3539, 'A **l** nnabiyyu awla bi **a** lmumineena min anfusihim waazwajuhu ommahatuhum waoloo alarhami ba\'duhum awla biba\'din fee kitabi Allahi mina almumineena wa **a** lmuhajireena illa an taf\'aloo ila awliyaikum ma\'roofan kana thalika fee alkitabi mastoora **n**');
INSERT INTO `en_transliteration` VALUES (3540, 'Waith akhathna mina a **l** nnabiyyeena meethaqahum waminka wamin noohin waibraheema wamoosa wa\'eesa ibni maryama waakhathna minhum meethaqan ghalee _th_ a **n**');
INSERT INTO `en_transliteration` VALUES (3541, 'Liyasala a **l** ssadiqeena \'an sidqihim waa\'adda lilkafireena \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (3542, 'Ya ayyuha allatheena amanoo othkuroo ni\'mata Allahi \'alaykum ith jaatkum junoodun faarsana \'alayhim reehan wajunoodan lam tarawha wakana Allahu bima ta\'maloona baseera **n**');
INSERT INTO `en_transliteration` VALUES (3543, 'Ith jaookum min fawqikum wamin asfala minkum waith zaghati alabsaru wabalaghati alquloobu alhanajira wata _th_ unnoona bi **A** llahi a **l** _thth_ unoona');
INSERT INTO `en_transliteration` VALUES (3544, 'Hunalika ibtuliya almuminoona wazulziloo zilzalan shadeeda **n**');
INSERT INTO `en_transliteration` VALUES (3545, 'Waith yaqoolu almunafiqoona wa **a** llatheena fee quloobihim maradun ma wa\'adana Allahu warasooluhu illa ghuroora **n**');
INSERT INTO `en_transliteration` VALUES (3546, 'Waith qalat taifatun minhum ya ahla yathriba la muqama lakum fa **i** rji\'oo wayastathinu fareequn minhumu a **l** nnabiyya yaqooloona inna buyootana \'awratun wama hiya bi\'awratin in yureedoona illa firara **n**');
INSERT INTO `en_transliteration` VALUES (3547, 'Walaw dukhilat \'alayhim min aqtariha thumma suiloo alfitnata laatawha wama talabbathoo biha illa yaseera **n**');
INSERT INTO `en_transliteration` VALUES (3548, 'Walaqad kanoo \'ahadoo Allaha min qablu la yuwalloona aladbara wakana \'ahdu Allahi masoola **n**');
INSERT INTO `en_transliteration` VALUES (3549, 'Qul lan yanfa\'akumu alfiraru in farartum mina almawti awi alqatli waithan la tumatta\'oona illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (3550, 'Qul man tha allathee ya\'simukum mina Allahi in arada bikum sooan aw arada bikum rahmatan wala yajidoona lahum min dooni Allahi waliyyan wala naseera **n**');
INSERT INTO `en_transliteration` VALUES (3551, 'Qad ya\'lamu Allahu almu\'awwiqeena minkum wa **a** lqaileena liikhwanihim halumma ilayna wala yatoona albasa illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (3552, 'Ashihhatan \'alaykum faitha jaa alkhawfu raaytahum yan _th_ uroona ilayka tadooru a\'yunuhum ka **a** llathee yughsha \'alayhi mina almawti faithathahaba alkhawfu salaqookum biasinatin hidadin ashihhatan \'ala alkhayri olaika lam yuminoo faahbata Allahu a\'malahum wakana thalika \'ala Allahi yaseera **n**');
INSERT INTO `en_transliteration` VALUES (3553, 'Yahsaboona alahzaba lam yathhaboo wain yati alahzabu yawaddoo law annahum badoona fee ala\'rabi yasaloona \'an anbaikum walaw kanoo feekum ma qataloo illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (3554, 'Laqad kana lakum fee rasooli Allahi oswatun hasanatun liman kana yarjoo Allaha wa **a** lyawma alakhira wathakara Allaha katheera **n**');
INSERT INTO `en_transliteration` VALUES (3555, 'Walamma raa almuminoona alahzaba qaloo hatha ma wa\'adana Allahu warasooluhu wasadaqa Allahu warasooluhu wama zadahum illa eemanan watasleema **n**');
INSERT INTO `en_transliteration` VALUES (3556, 'Mina almumineena rijalun sadaqoo ma \'ahadoo Allaha \'alayhi faminhum man qada nahbahu waminhum man yanta _th_ iru wama baddaloo tabdeela **n**');
INSERT INTO `en_transliteration` VALUES (3557, 'Liyajziya Allahu a **l** ssadiqeena bisidqihim wayu\'aththiba almunafiqeena in shaa aw yatooba \'alayhim inna Allaha kana ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (3558, 'Waradda Allahu allatheena kafaroo bighay _th_ ihim lam yanaloo khayran wakafa Allahu almumineena alqitala wakana Allahu qawiyyan \'azeeza **n**');
INSERT INTO `en_transliteration` VALUES (3559, 'Waanzala allatheena _th_ aharoohum min ahli alkitabi min sayaseehim waqathafa fee quloobihimu a **l** rru\'ba fareeqan taqtuloona watasiroona fareeqa **n**');
INSERT INTO `en_transliteration` VALUES (3560, 'Waawrathakum ardahum wadiyarahum waamwalahum waardan lam tataooha wakana Allahu \'ala kulli shayin qadeera **n**');
INSERT INTO `en_transliteration` VALUES (3561, 'Ya ayyuha a **l** nnabiyyu qul liazwajika in kuntunna turidna alhayata a **l** ddunya wazeenataha fata\'alayna omatti\'kunna waosarrihkunna sarahan jameela **n**');
INSERT INTO `en_transliteration` VALUES (3562, 'Wain kuntunna turidna Allaha warasoolahu wa **al** ddara alakhirata fainna Allaha a\'adda lilmuhsinati minkunna ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (3563, 'Ya nisaa a **l** nnabiyyi man yati minkunna bifahishatin mubayyinatin yuda\'af laha al\'athabu di\'fayni wakana thalika \'ala Allahi yaseera **n**');
INSERT INTO `en_transliteration` VALUES (3564, 'Waman yaqnut minkunna lillahi warasoolihi wata\'mal salihan nutiha ajraha marratayni waa\'tadna laha rizqan kareema **n**');
INSERT INTO `en_transliteration` VALUES (3565, 'Ya nisaa a **l** nnabiyyi lastunna kaahadin mina a **l** nnisai ini ittaqaytunna fala takhda\'na bi **a** lqawli fayatma\'a allathee fee qalbihi maradun waqulna qawlan ma\'roofa **n**');
INSERT INTO `en_transliteration` VALUES (3566, 'Waqarna fee buyootikunna wala tabarrajna tabarruja aljahiliyyati aloola waaqimna a **l** ssalata waateena a **l** zzakata waati\'na Allaha warasoolahu innama yureedu Allahu liyuthhiba \'ankumu a **l** rrijsa ahla albayti wayutahhirakum tatheera **n**');
INSERT INTO `en_transliteration` VALUES (3567, 'Wa **o** thkurna ma yutla fee buyootikunna min ayati Allahi wa **a** lhikmati inna Allaha kana lateefan khabeera **n**');
INSERT INTO `en_transliteration` VALUES (3568, 'Inna almuslimeena wa **a** lmuslimati wa **a** lmumineena wa **a** lmuminati wa **a** lqaniteena wa **a** lqanitati wa **al** ssadiqeena wa **al** ssadiqati wa **al** ssabireena wa **al** ssabirati wa **a** lkhashi\'eena wa **a** lkhashi\'ati wa **a** lmutasaddiqeena wa **a** lmutasaddiqati wa **al** ssaimeena wa **al** ssaimati wa **a** lhafi _th_ eena furoojahum wa **a** lhafi _th_ ati wa **al** ththakireena Allaha katheeran wa **al** ththakirati a\'adda Allahu lahum maghfiratan waajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (3569, 'Wama kana limuminin wala muminatin itha qada Allahu warasooluhu amran an yakoona lahumu alkhiyaratu min amrihim waman ya\'si Allaha warasoolahu faqad dalla dalalan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (3570, 'Waith taqoolu lillathee an\'ama Allahu \'alayhi waan\'amta \'alayhi amsik \'alayka zawjaka wa **i** ttaqi Allaha watukhfee fee nafsika ma Allahu mubdeehi watakhsha a **l** nnasa wa **A** llahu ahaqqu an takhshahu falamma qada zaydun minha wataran zawwajnakaha likay la yakoona \'ala almumineena harajun fee azwaji ad\'iyaihim itha qadaw minhunna wataran wakana amru Allahi maf\'oola **n**');
INSERT INTO `en_transliteration` VALUES (3571, 'Ma kana \'ala a **l** nnabiyyi min harajin feema farada Allahu lahu sunnata Allahi fee allatheena khalaw min qablu wakana amru Allahi qadaran maqdoora **n**');
INSERT INTO `en_transliteration` VALUES (3572, 'Allatheena yuballighoona risalati Allahi wayakhshawnahu wala yakhshawna ahadan illa Allaha wakafa bi **A** llahi haseeba **n**');
INSERT INTO `en_transliteration` VALUES (3573, 'Ma kana muhammadun aba ahadin min rijalikum walakin rasoola Allahi wakhatama a **l** nnabiyyeena wakana Allahu bikulli shayin \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (3574, 'Ya ayyuha allatheena amanoo othkuroo Allaha thikran katheera **n**');
INSERT INTO `en_transliteration` VALUES (3575, 'Wasabbihoohu bukratan waaseela **n**');
INSERT INTO `en_transliteration` VALUES (3576, 'Huwa allathee yusallee \'alaykum wamalaikatuhu liyukhrijakum mina a **l** _thth_ ulumati ila a **l** nnoori wakana bi **a** lmumineena raheema **n**');
INSERT INTO `en_transliteration` VALUES (3577, 'Tahiyyatuhum yawma yalqawnahu salamun waa\'adda lahum ajran kareema **n**');
INSERT INTO `en_transliteration` VALUES (3578, 'Ya ayyuha a **l** nnabiyyu inna arsanaka shahidan wamubashshiran wanatheera **n**');
INSERT INTO `en_transliteration` VALUES (3579, 'Wada\'iyan ila Allahi biithnihi wasirajan muneera **n**');
INSERT INTO `en_transliteration` VALUES (3580, 'Wabashshiri almumineena bianna lahum mina Allahi fadlan kabeera **n**');
INSERT INTO `en_transliteration` VALUES (3581, 'Wala tuti\'i alkafireena wa **a** lmunafiqeena wada\' athahum watawakkal \'ala Allahi wakafa bi **A** llahi wakeela **n**');
INSERT INTO `en_transliteration` VALUES (3582, 'Ya ayyuha allatheena amanoo itha nakahtumu almuminati thumma tallaqtumoohunna min qabli an tamassoohunna fama lakum \'alayhinna min \'iddatin ta\'taddoonaha famatti\'oohunna wasarrihoohunna sarahan jameela **n**');
INSERT INTO `en_transliteration` VALUES (3583, 'Ya ayyuha a **l** nnabiyyu inna ahlana laka azwajaka allatee atayta ojoorahunna wama malakat yameenuka mimma afaa Allahu \'alayka wabanati \'ammika wabanati \'ammatika wabanati khalika wabanati khalatika allatee hajarna ma\'aka wa **i** mraatan muminatan in wahabat nafsaha li **l** nnabiyyi in arada a **l** nnabiyyu an yastankihaha khalisatan laka min dooni almumineena qad \'alimna ma faradna \'alayhim fee azwajihim wama malakat aymanuhum likayla yakoona \'alayka harajun wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (3584, 'Turjee man tashao minhunna watuwee ilayka man tashao wamani ibtaghayta mimman \'azata fala junaha \'alayka thalika adna an taqarra a\'yunuhunna wala yahzanna wayardayna bimaataytahunna kulluhunna wa **A** llahu ya\'lamu ma fee quloobikum wakana Allahu \'aleeman haleema **n**');
INSERT INTO `en_transliteration` VALUES (3585, 'La yahillu laka a **l** nnisao min ba\'du wala an tabaddala bihinna min azwajin walaw a\'jabaka husnuhunna illa ma malakat yameenuka wakana Allahu \'ala kulli shayin raqeeba **n**');
INSERT INTO `en_transliteration` VALUES (3586, 'Ya ayyuha allatheena amanoo la tadkhuloo buyoota a **l** nnabiyyi illa an yuthana lakum ilata\'amin ghayra na _th_ ireena inahu walakin itha du\'eetum fa **o** dkhuloo faithata\'imtum fa **i** ntashiroo wala mustaniseena lihadeethin inna thalikum kana yuthee a **l** nnabiyya fayastahyee minkum wa **A** llahu la yastahyee mina alhaqqi waitha saatumoohunna mata\'an fa **i** saloohunna min warai hijabin thalikum atharu liquloobikum waquloobihinna wama kana lakum an tuthoo rasoola Allahi wala an tankihoo azwajahu min ba\'dihi abadan inna thalikum kana \'inda Allahi \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (3587, 'In tubdoo shayan aw tukhfoohu fainna Allaha kana bikulli shayin \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (3588, 'La junaha \'alayhinna fee abaihinna wala abnaihinna wala ikhwanihinna wala abnai ikhwanihinna wala abnai akhawatihinna wala nisaihinna wala ma malakat aymanuhunna wa **i** ttaqeena Allaha inna Allaha kana \'ala kulli shayin shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (3589, 'Inna Allaha wamalaikatahu yusalloona \'ala a **l** nnabiyyi ya ayyuha allatheena amanoo salloo \'alayhi wasallimoo tasleema **n**');
INSERT INTO `en_transliteration` VALUES (3590, 'Inna allatheena yuthoona Allaha warasoolahu la\'anahumu Allahu fee a **l** ddunya wa **a** lakhirati waa\'adda lahum \'athaban muheena **n**');
INSERT INTO `en_transliteration` VALUES (3591, 'Wa **a** llatheena yuthoona almumineena wa **a** lmuminati bighayri ma iktasaboo faqadi ihtamaloo buhtanan waithman mubeena **n**');
INSERT INTO `en_transliteration` VALUES (3592, 'Ya ayyuha a **l** nnabiyyu qul liazwajika wabanatika wanisai almumineena yudneena \'alayhinna min jalabeebihinna thalika adna an yu\'rafna fala yuthayna wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (3593, 'Lain lam yantahi almunafiqoona wa **a** llatheena fee quloobihim maradun wa **a** lmurjifoona fee almadeenati lanughriyannaka bihim thumma la yujawiroonaka feeha illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (3594, 'Mal\'ooneena aynama thuqifoo okhithoo waquttiloo taqteela **n**');
INSERT INTO `en_transliteration` VALUES (3595, 'Sunnata Allahi fee allatheena khalaw min qablu walan tajida lisunnati Allahi tabdeela **n**');
INSERT INTO `en_transliteration` VALUES (3596, 'Yasaluka a **l** nnasu \'ani a **l** ssa\'ati qul innama \'ilmuha \'inda Allahi wama yudreeka la\'alla a **l** ssa\'ata takoonu qareeba **n**');
INSERT INTO `en_transliteration` VALUES (3597, 'Inna Allaha la\'ana alkafireena waa\'adda lahum sa\'eera **n**');
INSERT INTO `en_transliteration` VALUES (3598, 'Khalideena feeha abadan la yajidoona waliyyan wala naseera **n**');
INSERT INTO `en_transliteration` VALUES (3599, 'Yawma tuqallabu wujoohuhum fee a **l** nnari yaqooloona ya laytana ata\'na Allaha waata\'na a **l** rrasoola');
INSERT INTO `en_transliteration` VALUES (3600, 'Waqaloo rabbana inna ata\'na sadatana wakubaraana faadalloona a **l** ssabeela');
INSERT INTO `en_transliteration` VALUES (3601, 'Rabbanaatihim di\'fayni mina al\'athabi wa **i** l\'anhum la\'nan kabeera **n**');
INSERT INTO `en_transliteration` VALUES (3602, 'Ya ayyuha allatheena amanoo la takoonoo ka **a** llatheena athaw moosa fabarraahu Allahu mimma qaloo wakana \'inda Allahi wajeeha **n**');
INSERT INTO `en_transliteration` VALUES (3603, 'Ya ayyuha allatheena amanoo ittaqoo Allaha waqooloo qawlan sadeeda **n**');
INSERT INTO `en_transliteration` VALUES (3604, 'Yuslih lakum a\'malakum wayaghfir lakum thunoobakum waman yuti\'i Allaha warasoolahu faqad faza fawzan \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (3605, 'Inna \'aradna alamanata \'ala a **l** ssamawati wa **a** lardi wa **a** ljibali faabayna an yahmilnaha waashfaqna minha wahamalaha alinsanu innahu kana _th_ alooman jahoola **n**');
INSERT INTO `en_transliteration` VALUES (3606, 'Liyu\'aththiba Allahu almunafiqeena wa **a** lmunafiqati wa **a** lmushrikeena wa **a** lmushrikati wayatooba Allahu \'ala almumineena wa **a** lmuminati wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (3607, 'Alhamdu lillahi allathee lahu ma fee a **l** ssamawati wama fee alardi walahu alhamdu fee alakhirati wahuwa alhakeemu alkhabeer **u**');
INSERT INTO `en_transliteration` VALUES (3608, 'Ya\'lamu ma yaliju fee alardi wama yakhruju minha wama yanzilu mina a **l** ssamai wama ya\'ruju feeha wahuwa a **l** rraheemu alghafoor **u**');
INSERT INTO `en_transliteration` VALUES (3609, 'Waqala allatheena kafaroo la tateena a **l** ssa\'atu qul bala warabbee latatiyannakum \'alimi alghaybi la ya\'zubu \'anhu mithqalu tharratin fee a **l** ssamawati wala fee alardi wala asgharu min thalika wala akbaru illa fee kitabin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (3610, 'Liyajziya allatheena amanoo wa\'amiloo a **l** ssalihati olaika lahum maghfiratun warizqun kareem **un**');
INSERT INTO `en_transliteration` VALUES (3611, 'Wa **a** llatheena sa\'aw fee ayatina mu\'ajizeena olaika lahum \'athabun min rijzin aleem **in**');
INSERT INTO `en_transliteration` VALUES (3612, 'Wayara allatheena ootoo al\'ilma allathee onzila ilayka min rabbika huwa alhaqqa wayahdee ilasirati al\'azeezi alhameed **i**');
INSERT INTO `en_transliteration` VALUES (3613, 'Waqala allatheena kafaroo hal nadullukum \'ala rajulin yunabbiokum itha muzziqtum kulla mumazzaqin innakum lafee khalqin jadeed **in**');
INSERT INTO `en_transliteration` VALUES (3614, 'Aftara \'ala Allahi kathiban am bihi jinnatun bali allatheena la yuminoona bi **a** lakhirati fee al\'athabi wa **al** ddalali alba\'eed **i**');
INSERT INTO `en_transliteration` VALUES (3615, 'Afalam yaraw ila ma bayna aydeehim wama khalfahum mina a **l** ssamai wa **a** lardi in nasha nakhsif bihimu alarda aw nusqit \'alayhim kisafan mina a **l** ssamai inna fee thalika laayatan likulli \'abdin muneeb **in**');
INSERT INTO `en_transliteration` VALUES (3616, 'Walaqad atayna dawooda minna fadlan ya jibalu awwibee ma\'ahu wa **al** ttayra waalanna lahu alhadeed **a**');
INSERT INTO `en_transliteration` VALUES (3617, 'Ani i\'mal sabighatin waqaddir fee a **l** ssardi wa **i** \'maloo salihan innee bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (3618, 'Walisulaymana a **l** rreeha ghuduwwuha shahrun warawahuha shahrun waasana lahu \'ayna alqitri wamina aljinni man ya\'malu bayna yadayhi biithni rabbihi waman yazigh minhum \'an amrina nuthiqhu min \'athabi a **l** ssa\'eer **i**');
INSERT INTO `en_transliteration` VALUES (3619, 'Ya\'maloona lahu ma yashao min mahareeba watamatheela wajifanin ka **a** ljawabi waqudoorin rasiyatin i\'maloo ala dawooda shukran waqaleelun min \'ibadiya a **l** shshakoor **u**');
INSERT INTO `en_transliteration` VALUES (3620, 'Falamma qadayna \'alayhi almawta ma dallahum \'ala mawtihi illa dabbatu alardi takulu minsaatahu falamma kharra tabayyanati aljinnu an law kanoo ya\'lamoona alghayba ma labithoo fee al\'athabi almuheen **i**');
INSERT INTO `en_transliteration` VALUES (3621, 'Laqad kana lisabain fee maskanihim ayatun jannatani \'an yameenin washimalin kuloo min rizqi rabbikum wa **o** shkuroo lahu badatun tayyibatun warabbun ghafoor **un**');
INSERT INTO `en_transliteration` VALUES (3622, 'Faa\'radoo faarsana \'alayhim sayla al\'arimi wabaddanahum bijannatayhim jannatayni thawatay okulin khamtin waathlin washayin min sidrin qaleel **in**');
INSERT INTO `en_transliteration` VALUES (3623, 'Thalika jazaynahum bima kafaroo wahal nujazee illa alkafoor **a**');
INSERT INTO `en_transliteration` VALUES (3624, 'Waja\'ana baynahum wabayna alqura allatee barakna feeha quran _th_ ahiratan waqaddarna feeha a **l** ssayra seeroo feeha layaliya waayyaman amineen **a**');
INSERT INTO `en_transliteration` VALUES (3625, 'Faqaloo rabbana ba\'id bayna asfarina wa _th_ alamoo anfusahum faja\'anahum ahadeetha wamazzaqnahum kulla mumazzaqin inna fee thalika laayatin likulli sabbarin shakoor **in**');
INSERT INTO `en_transliteration` VALUES (3626, 'Walaqad saddaqa \'alayhim ibleesu _th_ annahu fa **i** ttaba\'oohu illa fareeqan mina almumineen **a**');
INSERT INTO `en_transliteration` VALUES (3627, 'Wama kana lahu \'alayhim min sultanin illa lina\'lama man yuminu bi **a** lakhirati mimman huwa minha fee shakkin warabbuka \'ala kulli shayin hafee _th_ **un**');
INSERT INTO `en_transliteration` VALUES (3628, 'Quli od\'oo allatheena za\'amtum min dooni Allahi la yamlikoona mithqala tharratin fee a **l** ssamawati wala fee alardi wama lahum feehima min shirkin wama lahu minhum min _th_ aheer **in**');
INSERT INTO `en_transliteration` VALUES (3629, 'Wala tanfa\'u a **l** shshafa\'atu \'indahu illa liman athina lahu hatta itha fuzzi\'a \'an quloobihim qaloo matha qala rabbukum qaloo alhaqqa wahuwa al\'aliyyu alkabeer **u**');
INSERT INTO `en_transliteration` VALUES (3630, 'Qul man yarzuqukum mina a **l** ssamawati wa **a** lardi quli Allahu wainna aw iyyakum la\'ala hudan aw fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (3631, 'Qul la tusaloona \'amma ajramna wala nusalu \'amma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3632, 'Qul yajma\'u baynana rabbuna thumma yaftahu baynana bi **a** lhaqqi wahuwa alfattahu al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (3633, 'Qul arooniya allatheena alhaqtum bihi shurakaa kalla bal huwa Allahu al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (3634, 'Wama arsanaka illa kaffatan li **l** nnasi basheeran wanatheeran walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3635, 'Wayaqooloona mata hatha alwa\'du in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (3636, 'Qul lakum mee\'adu yawmin la tastakhiroona \'anhu sa\'atan wala tastaqdimoon **a**');
INSERT INTO `en_transliteration` VALUES (3637, 'Waqala allatheena kafaroo lan numina bihatha alqurani wala bi **a** llathee bayna yadayhi walaw tara ithi a **l** _thth_ alimoona mawqoofoona \'inda rabbihim yarji\'u ba\'duhum ila ba\'din alqawla yaqoolu allatheena istud\'ifoo lillatheena istakbaroo lawla antum lakunna mumineen **a**');
INSERT INTO `en_transliteration` VALUES (3638, 'Qala allatheena istakbaroo lillatheena istud\'ifoo anahnu sadadnakum \'ani alhuda ba\'da ith jaakum bal kuntum mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (3639, 'Waqala allatheena istud\'ifoo lillatheena istakbaroo bal makru allayli wa **al** nnahari ith tamuroonana an nakfura bi **A** llahi wanaj\'ala lahu andadan waasarroo a **l** nnadamata lamma raawoo al\'athaba waja\'ana alaghlala fee a\'naqi allatheena kafaroo hal yujzawna illa ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (3640, 'Wama arsana fee qaryatin min natheerin illa qala mutrafooha inna bima orsiltum bihi kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (3641, 'Waqaloo nahnu aktharu amwalan waawladan wama nahnu bimu\'aththabeen **a**');
INSERT INTO `en_transliteration` VALUES (3642, 'Qul inna rabbee yabsutu a **l** rrizqa liman yashao wayaqdiru walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (3643, 'Wama amwalukum wala awladukum bi **a** llatee tuqarribukum \'indana zulfa illa man amana wa\'amila salihan faolaika lahum jazao a **l** ddi\'fi bima \'amiloo wahum fee alghurufati aminoon **a**');
INSERT INTO `en_transliteration` VALUES (3644, 'Wa **a** llatheena yas\'awna fee ayatina mu\'ajizeena olaika fee al\'athabi muhdaroon **a**');
INSERT INTO `en_transliteration` VALUES (3645, 'Qul inna rabbee yabsutu a **l** rrizqa liman yashao min \'ibadihi wayaqdiru lahu wama anfaqtum min shayin fahuwa yukhlifuhu wahuwa khayru a **l** rraziqeen **a**');
INSERT INTO `en_transliteration` VALUES (3646, 'Wayawma yahshuruhum jamee\'an thumma yaqoolu lilmalaikati ahaolai iyyakum kanoo ya\'budoon **a**');
INSERT INTO `en_transliteration` VALUES (3647, 'Qaloo subhanaka anta waliyyuna min doonihim bal kanoo ya\'budoona aljinna aktharuhum bihim muminoon **a**');
INSERT INTO `en_transliteration` VALUES (3648, 'Fa **a** lyawma la yamliku ba\'dukum liba\'din naf\'an waladarran wanaqoolu lillatheena _th_ alamoo thooqoo \'athaba a **l** nnari allatee kuntum biha tukaththiboon **a**');
INSERT INTO `en_transliteration` VALUES (3649, 'Waitha tutla \'alayhim ayatuna bayyinatin qaloo ma hatha illa rajulun yureedu an yasuddakum \'amma kana ya\'budu abaokum waqaloo ma hatha illa ifkun muftaran waqala allatheena kafaroo lilhaqqi lamma jaahum in hatha illa sihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (3650, 'Wamaataynahum min kutubin yadrusoonaha wama arsana ilayhim qablaka min natheer **in**');
INSERT INTO `en_transliteration` VALUES (3651, 'Wakaththaba allatheena min qablihim wama balaghoo mi\'shara maataynahum fakaththaboo rusulee fakayfa kana nakeer **i**');
INSERT INTO `en_transliteration` VALUES (3652, 'Qul innama a\'i _th_ ukum biwahidatin an taqoomoo lillahi mathna wafurada thumma tatafakkaroo ma bisahibikum min jinnatin in huwa illa natheerun lakum bayna yaday \'athabin shadeed **in**');
INSERT INTO `en_transliteration` VALUES (3653, 'Qul ma saatukum min ajrin fahuwa lakum in ajriya illa \'ala Allahi wahuwa \'ala kulli shayin shaheed **un**');
INSERT INTO `en_transliteration` VALUES (3654, 'Qul inna rabbee yaqthifu bi **a** lhaqqi \'allamu alghuyoob **i**');
INSERT INTO `en_transliteration` VALUES (3655, 'Qul jaa alhaqqu wama yubdio albatilu wama yu\'eed **u**');
INSERT INTO `en_transliteration` VALUES (3656, 'Qul in dalatu fainnama adillu \'ala nafsee waini ihtadaytu fabima yoohee ilayya rabbee innahu samee\'un qareeb **un**');
INSERT INTO `en_transliteration` VALUES (3657, 'Walaw tara ith fazi\'oo fala fawta waokhithoo min makanin qareeb **in**');
INSERT INTO `en_transliteration` VALUES (3658, 'Waqaloo amanna bihi waanna lahumu a **l** ttanawushu min makanin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (3659, 'Waqad kafaroo bihi min qablu wayaqthifoona bi **a** lghaybi min makanin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (3660, 'Waheela baynahum wabayna ma yashtahoona kama fu\'ila biashya\'ihim min qablu innahum kanoo fee shakkin mureeb **in**');
INSERT INTO `en_transliteration` VALUES (3661, 'Alhamdu lillahi fatiri a **l** ssamawati wa **a** lardi ja\'ili almalaikati rusulan olee ajnihatin mathna wathulatha waruba\'a yazeedu fee alkhalqi ma yashao inna Allaha \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (3662, 'Ma yaftahi Allahu li **l** nnasi min rahmatin fala mumsika laha wama yumsik fala mursila lahu min ba\'dihi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (3663, 'Ya ayyuha a **l** nnasu othkuroo ni\'mata Allahi \'alaykum hal min khaliqin ghayru Allahi yarzuqukum mina a **l** ssamai wa **a** lardi la ilaha illa huwa faanna tufakoon **a**');
INSERT INTO `en_transliteration` VALUES (3664, 'Wain yukaththibooka faqad kuththibat rusulun min qablika waila Allahi turja\'u alomoor **u**');
