INSERT INTO `en_transliteration` VALUES (4007, 'Wa **al** shshayateena kulla bannain waghawwas **in**');
INSERT INTO `en_transliteration` VALUES (4008, 'Waakhareena muqarraneena fee alasfad **i**');
INSERT INTO `en_transliteration` VALUES (4009, 'Hatha \'ataona fa **o** mnun aw amsik bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (4010, 'Wainna lahu \'indana lazulfa wahusna maab **in**');
INSERT INTO `en_transliteration` VALUES (4011, 'Wa **o** thkur \'abdana ayyooba ith nada rabbahu annee massaniya a **l** shshaytanu binusbin wa\'athab **in**');
INSERT INTO `en_transliteration` VALUES (4012, 'Orkud birijlika hatha mughtasalun baridun washarab **un**');
INSERT INTO `en_transliteration` VALUES (4013, 'Wawahabna lahu ahlahu wamithlahum ma\'ahum rahmatan minna wathikra liolee alalbab **i**');
INSERT INTO `en_transliteration` VALUES (4014, 'Wakhuth biyadika dighthan fa **i** drib bihi wala tahnath inna wajadnahu sabiran ni\'ma al\'abdu innahu awwab **un**');
INSERT INTO `en_transliteration` VALUES (4015, 'Wa **o** thkur \'ibadana ibraheema waishaqa waya\'qooba olee alaydee wa **a** labsar **i**');
INSERT INTO `en_transliteration` VALUES (4016, 'Inna akhlasnahum bikhalisatin thikra a **l** ddar **i**');
INSERT INTO `en_transliteration` VALUES (4017, 'Wainnahum \'indana lamina almustafayna alakhyar **i**');
INSERT INTO `en_transliteration` VALUES (4018, 'Wa **o** thkur isma\'eela wailyasa\'a watha alkifli wakullun mina alakhyar **i**');
INSERT INTO `en_transliteration` VALUES (4019, 'Hathathikrun wainna lilmuttaqeena lahusna maab **in**');
INSERT INTO `en_transliteration` VALUES (4020, 'Jannati \'adnin mufattahatan lahumu alabwab **u**');
INSERT INTO `en_transliteration` VALUES (4021, 'Muttakieena feeha yad\'oona feeha bifakihatin katheeratin washarab **in**');
INSERT INTO `en_transliteration` VALUES (4022, 'Wa\'indahum qasiratu a **l** ttarfi atrab **un**');
INSERT INTO `en_transliteration` VALUES (4023, 'Hatha ma too\'adoona liyawmi alhisab **i**');
INSERT INTO `en_transliteration` VALUES (4024, 'Inna hatha larizquna ma lahu min nafad **in**');
INSERT INTO `en_transliteration` VALUES (4025, 'Hatha wainna li **l** ttagheena lasharra maab **in**');
INSERT INTO `en_transliteration` VALUES (4026, 'Jahannama yaslawnaha fabisa almihad **u**');
INSERT INTO `en_transliteration` VALUES (4027, 'Hatha falyathooqoohu hameemun waghassaq **un**');
INSERT INTO `en_transliteration` VALUES (4028, 'Waakharu min shaklihi azwaj **un**');
INSERT INTO `en_transliteration` VALUES (4029, 'Hatha fawjun muqtahimun ma\'akum la marhaban bihim innahum saloo a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4030, 'Qaloo bal antum la marhaban bikum antum qaddamtumoohu lana fabisa alqarar **u**');
INSERT INTO `en_transliteration` VALUES (4031, 'Qaloo rabbana man qaddama lana hatha fazidhu \'athaban di\'fan fee a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4032, 'Waqaloo ma lana la nara rijalan kunna na\'udduhum mina alashrar **i**');
INSERT INTO `en_transliteration` VALUES (4033, 'Attakhathnahum sikhriyyan am zaghat \'anhumu alabsar **u**');
INSERT INTO `en_transliteration` VALUES (4034, 'Inna thalika lahaqqun takhasumu ahli a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4035, 'Qul innama ana munthirun wama min ilahin illa Allahu alwahidu alqahhar **u**');
INSERT INTO `en_transliteration` VALUES (4036, 'Rabbu a **l** ssamawati wa **a** lardi wama baynahuma al\'azeezu alghaffar **u**');
INSERT INTO `en_transliteration` VALUES (4037, 'Qul huwa nabaon \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (4038, 'Antum \'anhu mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (4039, 'Ma kana liya min \'ilmin bi **a** lmalai ala\'la ith yakhtasimoon **a**');
INSERT INTO `en_transliteration` VALUES (4040, 'In yooha ilayya illa annama ana natheerun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (4041, 'Ith qala rabbuka lilmalaikati innee khaliqun basharan min teen **in**');
INSERT INTO `en_transliteration` VALUES (4042, 'Faitha sawwaytuhu wanafakhtu feehi min roohee faqa\'oo lahu sajideen **a**');
INSERT INTO `en_transliteration` VALUES (4043, 'Fasajada almalaikatu kulluhum ajma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4044, 'Illa ibleesa istakbara wakana mina alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (4045, 'Qala ya ibleesu ma mana\'aka an tasjuda lima khalaqtu biyadayya astakbarta am kunta mina al\'aleen **a**');
INSERT INTO `en_transliteration` VALUES (4046, 'Qala ana khayrun minhu khalaqtanee min narin wakhalaqtahu min teen **in**');
INSERT INTO `en_transliteration` VALUES (4047, 'Qala fa **o** khruj minha fainnaka rajeem **un**');
INSERT INTO `en_transliteration` VALUES (4048, 'Wainna \'alayka la\'natee ila yawmi a **l** ddeen **i**');
INSERT INTO `en_transliteration` VALUES (4049, 'Qala rabbi faan _th_ irnee ila yawmi yub\'athoon **a**');
INSERT INTO `en_transliteration` VALUES (4050, 'Qala fainnaka mina almun _th_ areen **a**');
INSERT INTO `en_transliteration` VALUES (4051, 'Ila yawmi alwaqti alma\'loom **i**');
INSERT INTO `en_transliteration` VALUES (4052, 'Qala fabi\'izzatika laoghwiyannahum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (4053, 'Illa \'ibadaka minhumu almukhlaseen **a**');
INSERT INTO `en_transliteration` VALUES (4054, 'Qala fa **a** lhaqqu wa **a** lhaqqa aqool **u**');
INSERT INTO `en_transliteration` VALUES (4055, 'Laamlaanna jahannama minka wamimman tabi\'aka minhum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (4056, 'Qul ma asalukum \'alayhi min ajrin wama ana mina almutakallifeen **a**');
INSERT INTO `en_transliteration` VALUES (4057, 'In huwa illathikrun lil\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4058, 'Walata\'lamunna nabaahu ba\'da heen **in**');
INSERT INTO `en_transliteration` VALUES (4059, 'Tanzeelu alkitabi mina Allahi al\'azeezi alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (4060, 'Inna anzana ilayka alkitaba bi **a** lhaqqi fa **o** \'budi Allaha mukhlisan lahu a **l** ddeen **a**');
INSERT INTO `en_transliteration` VALUES (4061, 'Ala lillahi a **l** ddeenu alkhalisu wa **a** llatheena ittakhathoo min doonihi awliyaa ma na\'buduhum illa liyuqarriboona ila Allahi zulfa inna Allaha yahkumu baynahum fee ma hum feehi yakhtalifoona inna Allaha la yahdee man huwa kathibun kaffar **un**');
INSERT INTO `en_transliteration` VALUES (4062, 'Law arada Allahu an yattakhitha waladan la **i** stafa mimma yakhluqu ma yashao subhanahu huwa Allahu alwahidu alqahhar **u**');
INSERT INTO `en_transliteration` VALUES (4063, 'Khalaqa a **l** ssamawati wa **a** larda bi **a** lhaqqi yukawwiru allayla \'ala a **l** nnahari wayukawwiru a **l** nnahara \'ala allayli wasakhkhara a **l** shshamsa wa **a** lqamara kullun yajree liajalin musamman ala huwa al\'azeezu alghaffar **u**');
INSERT INTO `en_transliteration` VALUES (4064, 'Khalaqakum min nafsin wahidatin thumma ja\'ala minha zawjaha waanzala lakum mina alan\'ami thamaniyata azwajin yakhluqukum fee butooni ommahatikum khalqan min ba\'di khalqin fee _th_ ulumatin thalathin thalikumu Allahu rabbukum lahu almulku la ilaha illa huwa faanna tusrafoon **a**');
INSERT INTO `en_transliteration` VALUES (4065, 'In takfuroo fainna Allaha ghaniyyun \'ankum wala yarda li\'ibadihi alkufra wain tashkuroo yardahu lakum wala taziru waziratun wizra okhra thumma ila rabbikum marji\'ukum fayunabbiokum bima kuntum ta\'maloona innahu \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (4066, 'Waitha massa alinsana durrun da\'a rabbahu muneeban ilayhi thumma itha khawwalahu ni\'matan minhu nasiya ma kana yad\'oo ilayhi min qablu waja\'ala lillahi andadan liyudilla \'an sabeelihi qul tamatta\' bikufrika qaleelan innaka min ashabi a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4067, 'Amman huwa qanitun anaa allayli sajidan waqaiman yahtharu alakhirata wayarjoo rahmata rabbihi qul hal yastawee allatheena ya\'lamoona wa **a** llatheena la ya\'lamoona innama yatathakkaru oloo alalbab **i**');
INSERT INTO `en_transliteration` VALUES (4068, 'Qul ya \'ibadi allatheena amanoo ittaqoo rabbakum lillatheena ahsanoo fee hathihi a **l** ddunyahasanatun waardu Allahi wasi\'atun innama yuwaffa a **l** ssabiroona ajrahum bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (4069, 'Qul innee omirtu an a\'buda Allaha mukhlisan lahu a **l** ddeen **a**');
INSERT INTO `en_transliteration` VALUES (4070, 'Waomirtu lian akoona awwala almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (4071, 'Qul innee akhafu in \'asaytu rabbee \'athaba yawmin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (4072, 'Quli Allaha a\'budu mukhlisan lahu deenee');
INSERT INTO `en_transliteration` VALUES (4073, 'Fa **o** \'budoo ma shitum min doonihi qul inna alkhasireena allatheena khasiroo anfusahum waahleehim yawma alqiyamati alathalika huwa alkhusranu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (4074, 'Lahum min fawqihim _th_ ulalun mina a **l** nnari wamin tahtihim _th_ ulalun thalika yukhawwifu Allahu bihi \'ibadahu ya \'ibadi fa **i** ttaqoon **i**');
INSERT INTO `en_transliteration` VALUES (4075, 'Wa **a** llatheena ijtanaboo a **l** ttaghoota an ya\'budooha waanaboo ila Allahi lahumu albushra fabashshir \'ibad **i**');
INSERT INTO `en_transliteration` VALUES (4076, 'Allatheena yastami\'oona alqawla fayattabi\'oona ahsanahu olaika allatheena hadahumu Allahu waolaika hum oloo alalbab **i**');
INSERT INTO `en_transliteration` VALUES (4077, 'Afaman haqqa \'alayhi kalimatu al\'athabi afaanta tunqithu man fee a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4078, 'Lakini allatheena ittaqaw rabbahum lahum ghurafun min fawqiha ghurafun mabniyyatun tajree min tahtiha alanharu wa\'da Allahi la yukhlifu Allahu almee\'ad **a**');
INSERT INTO `en_transliteration` VALUES (4079, 'Alam tara anna Allaha anzala mina a **l** ssamai maan fasalakahu yanabee\'a fee alardi thumma yukhriju bihi zar\'an mukhtalifan alwanuhu thumma yaheeju fatarahu musfarran thumma yaj\'aluhu hutaman inna fee thalika lathikra liolee alalbab **i**');
INSERT INTO `en_transliteration` VALUES (4080, 'Afaman sharaha Allahu sadrahu lilislami fahuwa \'ala noorin min rabbihi fawaylun lilqasiyati quloobuhum min thikri Allahi olaika fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (4081, 'Allahu nazzala ahsana alhadeethi kitaban mutashabihan mathaniya taqsha\'irru minhu juloodu allatheena yakhshawna rabbahum thumma taleenu julooduhum waquloobuhum ilathikri Allahi thalika huda Allahi yahdee bihi man yashao waman yudlili Allahu fama lahu min had **in**');
INSERT INTO `en_transliteration` VALUES (4082, 'Afaman yattaqee biwajhihi sooa al\'athabi yawma alqiyamati waqeela li **l** _thth_ alimeena thooqoo ma kuntum taksiboon **a**');
INSERT INTO `en_transliteration` VALUES (4083, 'Kaththaba allatheena min qablihim faatahumu al\'athabu min haythu la yash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (4084, 'Faathaqahumu Allahu alkhizya fee alhayati a **l** ddunya wala\'athabu alakhirati akbaru law kanoo ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4085, 'Walaqad darabna li **l** nnasi fee hatha alqurani min kulli mathalin la\'allahum yatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (4086, 'Quranan \'arabiyyan ghayra thee \'iwajin la\'allahum yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (4087, 'Daraba Allahu mathalan rajulan feehi shurakao mutashakisoona warajulan salaman lirajulin hal yastawiyani mathalan alhamdu lillahi bal aktharuhum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4088, 'Innaka mayyitun wainnahum mayyitoon **a**');
INSERT INTO `en_transliteration` VALUES (4089, 'Thumma innakum yawma alqiyamati \'inda rabbikum takhtasimoon **a**');
INSERT INTO `en_transliteration` VALUES (4090, 'Faman a _th_ lamu mimman kathaba \'ala Allahi wakaththaba bi **al** ssidqi ith jaahu alaysa fee jahannama mathwan lilkafireen **a**');
INSERT INTO `en_transliteration` VALUES (4091, 'Wa **a** llathee jaa bi **al** ssidqi wasaddaqa bihi olaika humu almuttaqoon **a**');
INSERT INTO `en_transliteration` VALUES (4092, 'Lahum ma yashaoona \'inda rabbihim thalika jazao almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (4093, 'Liyukaffira Allahu \'anhum aswaa allathee \'amiloo wayajziyahum ajrahum biahsani allathee kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4094, 'Alaysa Allahu bikafin \'abdahu wayukhawwifoonaka bi **a** llatheena min doonihi waman yudlili Allahu fama lahu min had **in**');
INSERT INTO `en_transliteration` VALUES (4095, 'Waman yahdi Allahu fama lahu min mudillin alaysa Allahu bi\'azeezin thee intiqam **in**');
INSERT INTO `en_transliteration` VALUES (4096, 'Walain saatahum man khalaqa a **l** ssamawati wa **a** larda layaqoolunna Allahu qul afaraaytum ma tad\'oona min dooni Allahi in aradaniya Allahu bidurrin hal hunna kashifatu durrihi aw aradanee birahmatin hal hunna mumsikatu rahmatihi qul hasbiya Allahu \'alayhi yatawakkalu almutawakkiloon **a**');
INSERT INTO `en_transliteration` VALUES (4097, 'Qul ya qawmi i\'maloo \'ala makanatikum innee \'amilun fasawfa ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4098, 'Man yateehi \'athabun yukhzeehi wayahillu \'alayhi \'athabun muqeem **un**');
INSERT INTO `en_transliteration` VALUES (4099, 'Inna anzana \'alayka alkitaba li **l** nnasi bi **a** lhaqqi famani ihtada falinafsihi waman dalla fainnama yadillu \'alayha wama anta \'alayhim biwakeel **in**');
INSERT INTO `en_transliteration` VALUES (4100, 'Allahu yatawaffa alanfusa heena mawtiha wa **a** llatee lam tamut fee manamiha fayumsiku allatee qada \'alayha almawta wayursilu alokhra ila ajalin musamman inna fee thalika laayatin liqawmin yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (4101, 'Ami ittakhathoo min dooni Allahi shufa\'aa qul awalaw kanoo la yamlikoona shayan wala ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (4102, 'Qul lillahi a **l** shshafa\'atu jamee\'an lahu mulku a **l** ssamawati wa **a** lardi thumma ilayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4103, 'Waithathukira Allahu wahdahu ishmaazzat quloobu allatheena la yuminoona bi **a** lakhirati waithathukira allatheena min doonihi itha hum yastabshiroon **a**');
INSERT INTO `en_transliteration` VALUES (4104, 'Quli allahumma fatira a **l** ssamawati wa **a** lardi \'alima alghaybi wa **al** shshahadati anta tahkumu bayna \'ibadika fee ma kanoo feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (4105, 'Walaw anna lillatheena _th_ alamoo ma fee alardi jamee\'an wamithlahu ma\'ahu la **i** ftadaw bihi min sooi al\'athabi yawma alqiyamati wabada lahum mina Allahi ma lam yakoonoo yahtasiboon **a**');
INSERT INTO `en_transliteration` VALUES (4106, 'Wabada lahum sayyiatu ma kasaboo wahaqa bihim ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (4107, 'Faitha massa alinsana durrun da\'ana thumma itha khawwanahu ni\'matan minna qala innama ooteetuhu \'ala \'ilmin bal hiya fitnatun walakinna aktharahum la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4108, 'Qad qalaha allatheena min qablihim fama aghna \'anhum ma kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (4109, 'Faasabahum sayyiatu ma kasaboo wa **a** llatheena _th_ alamoo min haolai sayuseebuhum sayyiatu ma kasaboo wama hum bimu\'jizeen **a**');
INSERT INTO `en_transliteration` VALUES (4110, 'Awalam ya\'lamoo anna Allaha yabsutu a **l** rrizqa liman yashao wayaqdiru inna fee thalika laayatin liqawmin yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (4111, 'Qul ya \'ibadiya allatheena asrafoo \'ala anfusihim la taqnatoo min rahmati Allahi inna Allaha yaghfiru a **l** ththunooba jamee\'an innahu huwa alghafooru a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (4112, 'Waaneeboo ila rabbikum waaslimoo lahu min qabli an yatiyakumu al\'athabu thumma la tunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (4113, 'Wa **i** ttabi\'oo ahsana ma onzila ilaykum min rabbikum min qabli an yatiyakumu al\'athabu baghtatan waantum la tash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (4114, 'An taqoola nafsun yahasrata \'ala ma farrattu fee janbi Allahi wain kuntu lamina a **l** ssakhireen **a**');
INSERT INTO `en_transliteration` VALUES (4115, 'Aw taqoola law anna Allaha hadanee lakuntu mina almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (4116, 'Aw taqoola heena tara al\'athaba law anna lee karratan faakoona mina almuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (4117, 'Bala qad jaatka ayatee fakaththabta biha wa **i** stakbarta wakunta mina alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (4118, 'Wayawma alqiyamati tara allatheena kathaboo \'ala Allahi wujoohuhum muswaddatun alaysa fee jahannama mathwan lilmutakabbireen **a**');
INSERT INTO `en_transliteration` VALUES (4119, 'Wayunajjee Allahu allatheena ittaqaw bimafazatihim la yamassuhumu a **l** ssooo wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (4120, 'Allahu khaliqu kulli shayin wahuwa \'ala kulli shayin wakeel **un**');
INSERT INTO `en_transliteration` VALUES (4121, 'Lahu maqaleedu a **l** ssamawati wa **a** lardi wa **a** llatheena kafaroo biayati Allahi olaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (4122, 'Qul afaghayra Allahi tamuroonnee a\'budu ayyuha aljahiloon **a**');
INSERT INTO `en_transliteration` VALUES (4123, 'Walaqad oohiya ilayka waila allatheena min qablika lain ashrakta layahbatanna \'amaluka walatakoonanna mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (4124, 'Bali Allaha fa **o** \'bud wakun mina a **l** shshakireen **a**');
INSERT INTO `en_transliteration` VALUES (4125, 'Wama qadaroo Allaha haqqa qadrihi wa **a** lardu jamee\'an qabdatuhu yawma alqiyamati wa **al** ssamawatu matwiyyatun biyameenihi subhanahu wata\'ala \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (4126, 'Wanufikha fee a **l** ssoori fasa\'iqa man fee a **l** ssamawati waman fee alardi illa man shaa Allahu thumma nufikha feehi okhra faitha hum qiyamun yan _th_ uroon **a**');
INSERT INTO `en_transliteration` VALUES (4127, 'Waashraqati alardu binoori rabbiha wawudi\'a alkitabu wajeea bi **al** nnabiyyeena wa **al** shshuhadai waqudiya baynahum bi **a** lhaqqi wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4128, 'Wawuffiyat kullu nafsin ma \'amilat wahuwa a\'lamu bima yaf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (4129, 'Waseeqa allatheena kafaroo ila jahannama zumaran hatta itha jaooha futihat abwabuha waqala lahum khazanatuha alam yatikum rusulun minkum yatloona \'alaykum ayati rabbikum wayunthiroonakum liqaa yawmikum hatha qaloo bala walakin haqqat kalimatu al\'athabi \'ala alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (4130, 'Qeela odkhuloo abwaba jahannama khalideena feeha fabisa mathwa almutakabbireen **a**');
INSERT INTO `en_transliteration` VALUES (4131, 'Waseeqa allatheena ittaqaw rabbahum ila aljannati zumaran hatta itha jaooha wafutihat abwabuha waqala lahum khazanatuha salamun \'alaykum tibtum fa **o** dkhulooha khalideen **a**');
INSERT INTO `en_transliteration` VALUES (4132, 'Waqaloo alhamdu lillahi allathee sadaqana wa\'dahu waawrathana alarda natabawwao mina aljannati haythu nashao fani\'ma ajru al\'amileen **a**');
INSERT INTO `en_transliteration` VALUES (4133, 'Watara almalaikata haffeena min hawli al\'arshi yusabbihoona bihamdi rabbihim waqudiya baynahum bi **a** lhaqqi waqeela alhamdu lillahi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4134, 'Hameem');
INSERT INTO `en_transliteration` VALUES (4135, 'Tanzeelu alkitabi mina Allahi al\'azeezi al\'aleem **i**');
INSERT INTO `en_transliteration` VALUES (4136, 'Ghafiri a **l** ththanbi waqabili a **l** ttawbi shadeedi al\'iqabi thee a **l** ttawli la ilaha illa huwa ilayhi almaseer **u**');
INSERT INTO `en_transliteration` VALUES (4137, 'Ma yujadilu fee ayati Allahi illa allatheena kafaroo fala yaghrurka taqallubuhum fee albilad **i**');
INSERT INTO `en_transliteration` VALUES (4138, 'Kaththabat qablahum qawmu noohin wa **a** lahzabu min ba\'dihim wahammat kullu ommatin birasoolihim liyakhuthoohu wajadaloo bi **a** lbatili liyudhidoo bihi alhaqqa faakhathtuhum fakayfa kana \'iqab **i**');
INSERT INTO `en_transliteration` VALUES (4139, 'Wakathalika haqqat kalimatu rabbika \'ala allatheena kafaroo annahum ashabu a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4140, 'Allatheena yahmiloona al\'arsha waman hawlahu yusabbihoona bihamdi rabbihim wayuminoona bihi wayastaghfiroona lillatheena amanoo rabbana wasi\'ta kulla shayin rahmatan wa\'ilman fa **i** ghfir lillatheena taboo wa **i** ttaba\'oo sabeelaka waqihim \'athaba aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (4141, 'Rabbana waadkhilhum jannati \'adnin allatee wa\'adtahum waman salaha min abaihim waazwajihim wathurriyyatihim innaka anta al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (4142, 'Waqihimu a **l** ssayyiati waman taqi a **l** ssayyiati yawmaithin faqad rahimtahu wathalika huwa alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (4143, 'Inna allatheena kafaroo yunadawna lamaqtu Allahi akbaru min maqtikum anfusakum ith tud\'awna ila aleemani fatakfuroon **a**');
INSERT INTO `en_transliteration` VALUES (4144, 'Qaloo rabbana amattana ithnatayni waahyaytana ithnatayni fa **i** \'tarafna bithunoobina fahal ila khuroojin min sabeel **in**');
INSERT INTO `en_transliteration` VALUES (4145, 'Thalikum biannahu itha du\'iya Allahu wahdahu kafartum wain yushrak bihi tuminoo fa **a** lhukmu lillahi al\'aliyyi alkabeer **i**');
INSERT INTO `en_transliteration` VALUES (4146, 'Huwa allathee yureekum ayatihi wayunazzilu lakum mina a **l** ssamai rizqan wama yatathakkaru illa man yuneeb **u**');
INSERT INTO `en_transliteration` VALUES (4147, 'Fa **o** d\'oo Allaha mukhliseena lahu a **l** ddeena walaw kariha alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (4148, 'Rafee\'u a **l** ddarajati thoo al\'arshi yulqee a **l** rrooha min amrihi \'ala man yashao min \'ibadihi liyunthira yawma a **l** ttalaq **i**');
INSERT INTO `en_transliteration` VALUES (4149, 'Yawma hum barizoona la yakhfa \'ala Allahi minhum shayon limani almulku alyawma lillahi alwahidi alqahhar **i**');
INSERT INTO `en_transliteration` VALUES (4150, 'Alyawma tujza kullu nafsin bima kasabat la _th_ ulma alyawma inna Allaha saree\'u alhisab **i**');
INSERT INTO `en_transliteration` VALUES (4151, 'Waanthirhum yawma alazifati ithi alquloobu lada alhanajiri ka _th_ imeena ma li **l** _thth_ alimeena min hameemin wala shafee\'in yuta\' **u**');
INSERT INTO `en_transliteration` VALUES (4152, 'Ya\'lamu khainata ala\'yuni wama tukhfee a **l** ssudoor **u**');
INSERT INTO `en_transliteration` VALUES (4153, 'Wa **A** llahu yaqdee bi **a** lhaqqi wa **a** llatheena yad\'oona min doonihi la yaqdoona bishayin inna Allaha huwa a **l** ssamee\'u albaseer **u**');
INSERT INTO `en_transliteration` VALUES (4154, 'Awa lam yaseeroo fee alardi fayan _th_ uroo kayfa kana \'aqibatu allatheena kanoo min qablihim kanoo hum ashadda minhum quwwatan waatharan fee alardi faakhathahumu Allahu bithunoobihim wama kana lahum mina Allahi min waq **in**');
INSERT INTO `en_transliteration` VALUES (4155, 'Thalika biannahum kanat tateehim rusuluhum bi **a** lbayyinati fakafaroo faakhathahumu Allahu innahu qawiyyun shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (4156, 'Walaqad arsana moosa biayatina wasultanin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (4157, 'Ila fir\'awna wahamana waqaroona faqaloo sahirun kaththab **un**');
INSERT INTO `en_transliteration` VALUES (4158, 'Falamma jaahum bi **a** lhaqqi min \'indina qaloo oqtuloo abnaa allatheena amanoo ma\'ahu wa **i** stahyoo nisaahum wama kaydu alkafireena illa fee dalal **in**');
INSERT INTO `en_transliteration` VALUES (4159, 'Waqala fir\'awnu tharoonee aqtul moosa walyad\'u rabbahu innee akhafu an yubaddila deenakum aw an yu _th_ hira fee alardi alfasad **a**');
INSERT INTO `en_transliteration` VALUES (4160, 'Waqala moosa innee \'uthtu birabbee warabbikum min kulli mutakabbirin la yuminu biyawmi alhisab **i**');
INSERT INTO `en_transliteration` VALUES (4161, 'Waqala rajulun muminun min ali fir\'awna yaktumu eemanahu ataqtuloona rajulan an yaqoola rabbiyya Allahu waqad jaakum bi **a** lbayyinati min rabbikum wain yaku kathiban fa\'alayhi kathibuhu wain yaku sadiqan yusibkum ba\'du allathee ya\'idukum inna Allaha la yahdee man huwa musrifun kaththab **un**');
INSERT INTO `en_transliteration` VALUES (4162, 'Ya qawmi lakumu almulku alyawma _th_ ahireena fee alardi faman yansuruna min basi Allahi in jaana qala fir\'awnu ma oreekum illa ma ara wama ahdeekum illa sabeela a **l** rrashad **i**');
INSERT INTO `en_transliteration` VALUES (4163, 'Waqala allathee amana ya qawmi innee akhafu \'alaykum mithla yawmi alahzab **i**');
INSERT INTO `en_transliteration` VALUES (4164, 'Mithla dabi qawmi noohin wa\'adin wathamooda wa **a** llatheena min ba\'dihim wama Allahu yureedu _th_ ulman lil\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (4165, 'Waya qawmi innee akhafu \'alaykum yawma a **l** ttanad **i**');
INSERT INTO `en_transliteration` VALUES (4166, 'Yawma tuwalloona mudbireena ma lakum mina Allahi min \'asimin waman yudlili Allahu fama lahu min had **in**');
INSERT INTO `en_transliteration` VALUES (4167, 'Walaqad jaakum yoosufu min qablu bi **a** lbayyinati fama ziltum fee shakkin mimma jaakum bihi hatta itha halaka qultum lan yab\'atha Allahu min ba\'dihi rasoolan kathalika yudillu Allahu man huwa musrifun murtab **un**');
INSERT INTO `en_transliteration` VALUES (4168, 'Allatheena yujadiloona fee ayati Allahi bighayri sultanin atahum kabura maqtan \'inda Allahi wa\'inda allatheena amanoo kathalika yatba\'u Allahu \'ala kulli qalbi mutakabbirin jabbar **in**');
INSERT INTO `en_transliteration` VALUES (4169, 'Waqala fir\'awnu ya hamanu ibni lee sarhan la\'allee ablughu alasbab **a**');
INSERT INTO `en_transliteration` VALUES (4170, 'Asbaba a **l** ssamawati faattali\'a ila ilahi moosa wainnee laa _th_ unnuhu kathiban wakathalika zuyyina lifir\'awna sooo \'amalihi wasudda \'ani a **l** ssabeeli wama kaydu fir\'awna illa fee tabab **in**');
INSERT INTO `en_transliteration` VALUES (4171, 'Waqala allathee amana ya qawmi ittabi\'ooni ahdikum sabeela a **l** rrashad **i**');
INSERT INTO `en_transliteration` VALUES (4172, 'Ya qawmi innama hathihi alhayatu a **l** ddunya mata\'un wainna alakhirata hiya daru alqarar **i**');
INSERT INTO `en_transliteration` VALUES (4173, 'Man \'amila sayyiatan fala yujza illa mithlaha waman \'amila salihan min thakarin aw ontha wahuwa muminun faolaika yadkhuloona aljannata yurzaqoona feeha bighayri hisab **in**');
INSERT INTO `en_transliteration` VALUES (4174, 'Waya qawmi ma lee ad\'ookum ila a **l** nnajati watad\'oonanee ila a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4175, 'Tad\'oonanee liakfura bi **A** llahi waoshrika bihi ma laysa lee bihi \'ilmun waana ad\'ookum ila al\'azeezi alghaffar **i**');
INSERT INTO `en_transliteration` VALUES (4176, 'La jarama annama tad\'oonanee ilayhi laysa lahu da\'watun fee a **l** ddunya wala fee alakhirati waanna maraddana ila Allahi waanna almusrifeena hum ashabu a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4177, 'Fasatathkuroona ma aqoolu lakum waofawwidu amree ila Allahi inna Allaha baseerun bi **a** l\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (4178, 'Fawaqahu Allahu sayyiati ma makaroo wahaqa biali fir\'awna sooo al\'athab **i**');
INSERT INTO `en_transliteration` VALUES (4179, 'A **l** nnaru yu\'radoona \'alayha ghuduwwan wa\'ashiyyan wayawma taqoomu a **l** ssa\'atu adkhiloo ala fir\'awna ashadda al\'athab **i**');
INSERT INTO `en_transliteration` VALUES (4180, 'Waith yatahajjoona fee a **l** nnari fayaqoolu a **l** ddu\'afao lillatheena istakbaroo inna kunna lakum taba\'an fahal antum mughnoona \'anna naseeban mina a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (4181, 'Qala allatheena istakbaroo inna kullun feeha inna Allaha qad hakama bayna al\'ibad **i**');
INSERT INTO `en_transliteration` VALUES (4182, 'Waqala allatheena fee a **l** nnari likhazanati jahannama od\'oo rabbakum yukhaffif \'anna yawman mina al\'athab **i**');
INSERT INTO `en_transliteration` VALUES (4183, 'Qaloo awalam taku tateekum rusulukum bi **a** lbayyinati qaloo bala qaloo fa **o** d\'oo wama du\'ao alkafireena illa fee dalal **in**');
INSERT INTO `en_transliteration` VALUES (4184, 'Inna lanansuru rusulana wa **a** llatheena amanoo fee alhayati a **l** ddunya wayawma yaqoomu alashhad **u**');
INSERT INTO `en_transliteration` VALUES (4185, 'Yawma la yanfa\'u a **l** _thth_ alimeena ma\'thiratuhum walahumu alla\'natu walahum sooo a **l** ddar **i**');
INSERT INTO `en_transliteration` VALUES (4186, 'Walaqad atayna moosa alhuda waawrathna banee israeela alkitab **a**');
INSERT INTO `en_transliteration` VALUES (4187, 'Hudan wathikra liolee alalbab **i**');
INSERT INTO `en_transliteration` VALUES (4188, 'Fa **i** sbir inna wa\'da Allahi haqqun wa **i** staghfir lithanbika wasabbih bihamdi rabbika bi **a** l\'ashiyyi wa **a** libkar **i**');
INSERT INTO `en_transliteration` VALUES (4189, 'Inna allatheena yujadiloona fee ayati Allahi bighayri sultanin atahum in fee sudoorihim illa kibrun ma hum bibaligheehi fa **i** sta\'ith bi **A** llahi innahu huwa a **l** ssamee\'u albaseer **u**');
INSERT INTO `en_transliteration` VALUES (4190, 'Lakhalqu a **l** ssamawati wa **a** lardi akbaru min khalqi a **l** nnasi walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4191, 'Wama yastawee ala\'ma wa **a** lbaseeru wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati wala almuseeo qaleelan ma tatathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (4192, 'Inna a **l** ssa\'ata laatiyatun la rayba feeha walakinna akthara a **l** nnasi la yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (4193, 'Waqala rabbukumu od\'oonee astajib lakum inna allatheena yastakbiroona \'an \'ibadatee sayadkhuloona jahannama dakhireen **a**');
INSERT INTO `en_transliteration` VALUES (4194, 'Allahu allathee ja\'ala lakumu allayla litaskunoo feehi wa **al** nnahara mubsiran inna Allaha lathoo fadlin \'ala a **l** nnasi walakinna akthara a **l** nnasi la yashkuroon **a**');
INSERT INTO `en_transliteration` VALUES (4195, 'Thalikumu Allahu rabbukum khaliqu kulli shayin la ilaha illa huwa faanna tufakoon **a**');
INSERT INTO `en_transliteration` VALUES (4196, 'Kathalika yufaku allatheena kanoo biayati Allahi yajhadoon **a**');
INSERT INTO `en_transliteration` VALUES (4197, 'Allahu allathee ja\'ala lakumu alarda qararan wa **al** ssamaa binaan wasawwarakum faahsana suwarakum warazaqakum mina a **l** ttayyibati thalikumu Allahu rabbukum fatabaraka Allahu rabbu al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4198, 'Huwa alhayyu la ilaha illa huwa fa **o** d\'oohu mukhliseena lahu a **l** ddeena alhamdu lillahi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4199, 'Qul innee nuheetu an a\'buda allatheena tad\'oona min dooni Allahi lamma jaaniya albayyinatu min rabbee waomirtu an oslima lirabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4200, 'Huwa allathee khalaqakum min turabin thumma min nutfatin thumma min \'alaqatin thumma yukhrijukum tiflan thumma litablughoo ashuddakum thumma litakoonoo shuyookhan waminkum man yutawaffa min qablu walitablughoo ajalan musamman wala\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (4201, 'Huwa allathee yuhyee wayumeetu faitha qada amran fainnama yaqoolu lahu kun fayakoon **u**');
INSERT INTO `en_transliteration` VALUES (4202, 'Alam tara ila allatheena yujadiloona fee ayati Allahi anna yusrafoon **a**');
INSERT INTO `en_transliteration` VALUES (4203, 'Allatheena kaththaboo bi **a** lkitabi wabima arsana bihi rusulana fasawfa ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4204, 'Ithi alaghlalu fee a\'naqihim wa **al** ssalasilu yushaboon **a**');
INSERT INTO `en_transliteration` VALUES (4205, 'Fee alhameemi thumma fee a **l** nnari yusjaroon **a**');
INSERT INTO `en_transliteration` VALUES (4206, 'Thumma qeela lahum ayna ma kuntum tushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (4207, 'Min dooni Allahi qaloo dalloo \'anna bal lam nakun nad\'oo min qablu shayan kathalika yudillu Allahu alkafireen **a**');
INSERT INTO `en_transliteration` VALUES (4208, 'Thalikum bima kuntum tafrahoona fee alardi bighayri alhaqqi wabima kuntum tamrahoon **a**');
INSERT INTO `en_transliteration` VALUES (4209, 'Odkhuloo abwaba jahannama khalideena feeha fabisa mathwa almutakabbireen **a**');
INSERT INTO `en_transliteration` VALUES (4210, 'Fa **i** sbir inna wa\'da Allahi haqqun faimma nuriyannaka ba\'da allathee na\'iduhum aw natawaffayannaka failayna yurja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4211, 'Walaqad arsana rusulan min qablika minhum man qasasna \'alayka waminhum man lam naqsus \'alayka wama kana lirasoolin an yatiya biayatin illa biithni Allahi faitha jaa amru Allahi qudiya bi **a** lhaqqi wakhasira hunalika almubtiloon **a**');
INSERT INTO `en_transliteration` VALUES (4212, 'Allahu allathee ja\'ala lakumu alan\'ama litarkaboo minha waminha takuloon **a**');
INSERT INTO `en_transliteration` VALUES (4213, 'Walakum feeha manafi\'u walitablughoo \'alayhahajatan fee sudoorikum wa\'alayha wa\'ala alfulki tuhmaloon **a**');
INSERT INTO `en_transliteration` VALUES (4214, 'Wayureekum ayatihi faayya ayati Allahi tunkiroon **a**');
INSERT INTO `en_transliteration` VALUES (4215, 'Afalam yaseeroo fee alardi fayan _th_ uroo kayfa kana \'aqibatu allatheena min qablihim kanoo akthara minhum waashadda quwwatan waatharan fee alardi fama aghna \'anhum ma kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (4216, 'Falamma jaathum rusuluhum bi **a** lbayyinati farihoo bima \'indahum mina al\'ilmi wahaqa bihim ma kanoo bihi yastahzioon **a**');
