INSERT INTO `en_transliteration` VALUES (4218, 'Falam yaku yanfa\'uhum eemanuhum lamma raaw basana sunnata Allahi allatee qad khalat fee \'ibadihi wakhasira hunalika alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (4219, 'Hameem');
INSERT INTO `en_transliteration` VALUES (4220, 'Tanzeelun mina a **l** rrahmani a **l** rraheem **i**');
INSERT INTO `en_transliteration` VALUES (4221, 'Kitabun fussilat ayatuhu quranan \'arabiyyan liqawmin ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4222, 'Basheeran wanatheeran faa\'rada aktharuhum fahum la yasma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4223, 'Waqaloo quloobuna fee akinnatin mimma tad\'oona ilayhi wafee athanina waqrun wamin baynina wabaynika hijabun fa **i** \'mal innana \'amiloon **a**');
