INSERT INTO `en_transliteration` VALUES (4225, 'Allatheena la yutoona a **l** zzakata wahum bi **a** lakhirati hum kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (4226, 'Inna allatheena amanoo wa\'amiloo a **l** ssalihati lahum ajrun ghayru mamnoon **in**');
INSERT INTO `en_transliteration` VALUES (4227, 'Qul ainnakum latakfuroona bi **a** llathee khalaqa alarda fee yawmayni wataj\'aloona lahu andadan thalika rabbu al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4228, 'Waja\'ala feeha rawasiya min fawqiha wabaraka feeha waqaddara feeha aqwataha fee arba\'ati ayyamin sawaan li **l** ssaileen **a**');
INSERT INTO `en_transliteration` VALUES (4229, 'Thumma istawa ila a **l** ssamai wahiya dukhanun faqala laha walilardi itiyataw\'an aw karhan qalata ataynatai\'een **a**');
INSERT INTO `en_transliteration` VALUES (4230, 'Faqadahunna sab\'a samawatin fee yawmayni waawha fee kulli samain amraha wazayyanna a **l** ssamaa a **l** ddunya bimasabeeha wahif _th_ an thalika taqdeeru al\'azeezi al\'aleem **i**');
INSERT INTO `en_transliteration` VALUES (4231, 'Fain a\'radoo fuqul anthartukum sa\'iqatan mithla sa\'iqati \'adin wathamood **a**');
INSERT INTO `en_transliteration` VALUES (4232, 'Ith jaathumu a **l** rrusulu min bayni aydeehim wamin khalfihim alla ta\'budoo illa Allaha qaloo law shaa rabbuna laanzala malaikatan fainna bima orsiltum bihi kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (4233, 'Faamma \'adun fa **i** stakbaroo fee alardi bighayri alhaqqi waqaloo man ashaddu minna quwwatan awalam yaraw anna Allaha allathee khalaqahum huwa ashaddu minhum quwwatan wakanoo biayatina yajhadoon **a**');
INSERT INTO `en_transliteration` VALUES (4234, 'Faarsana \'alayhim reehan sarsaran fee ayyamin nahisatin linutheeqahum \'athaba alkhizyi fee alhayati a **l** ddunya wala\'athabu alakhirati akhza wahum la yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (4235, 'Waamma thamoodu fahadaynahum fa **i** stahabboo al\'ama \'ala alhuda faakhathathum sa\'iqatu al\'athabi alhooni bima kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (4236, 'Wanajjayna allatheena amanoo wakanoo yattaqoon **a**');
INSERT INTO `en_transliteration` VALUES (4237, 'Wayawma yuhsharu a\'dao Allahi ila a **l** nnari fahum yooza\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4238, 'Hatta itha ma jaooha shahida \'alayhim sam\'uhum waabsaruhum wajulooduhum bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4239, 'Waqaloo lijuloodihim lima shahidtum \'alayna qaloo antaqana Allahu allathee antaqa kulla shayin wahuwa khalaqakum awwala marratin wailayhi turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4240, 'Wama kuntum tastatiroona an yashhada \'alaykum sam\'ukum wala absarukum wala juloodukum walakin _th_ anantum anna Allaha la ya\'lamu katheeran mimma ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4241, 'Wathalikum _th_ annukumu allathee _th_ anantum birabbikum ardakum faasbahtum mina alkhasireen **a**');
INSERT INTO `en_transliteration` VALUES (4242, 'Fain yasbiroo fa **al** nnaru mathwan lahum wain yasta\'tiboo fama hum mina almu\'tabeen **a**');
INSERT INTO `en_transliteration` VALUES (4243, 'Waqayyadna lahum quranaa fazayyanoo lahum ma bayna aydeehim wama khalfahum wahaqqa \'alayhimu alqawlu fee omamin qad khalat min qablihim mina aljinni wa **a** linsi innahum kanoo khasireen **a**');
INSERT INTO `en_transliteration` VALUES (4244, 'Waqala allatheena kafaroo la tasma\'oo lihatha alqurani wa **i** lghaw feehi la\'allakum taghliboon **a**');
INSERT INTO `en_transliteration` VALUES (4245, 'Falanutheeqanna allatheena kafaroo \'athaban shadeedan walanajziyannahum aswaa allathee kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4246, 'Thalika jazao a\'dai Allahi a **l** nnaru lahum feeha daru alkhuldi jazaan bima kanoo biayatina yajhadoon **a**');
INSERT INTO `en_transliteration` VALUES (4247, 'Waqala allatheena kafaroo rabbana arina allathayni adallana mina aljinni wa **a** linsi naj\'alhuma tahta aqdamina liyakoona mina alasfaleen **a**');
INSERT INTO `en_transliteration` VALUES (4248, 'Inna allatheena qaloo rabbuna Allahu thumma istaqamoo tatanazzalu \'alayhimu almalaikatu alla takhafoo wala tahzanoo waabshiroo bi **a** ljannati allatee kuntum too\'adoon **a**');
INSERT INTO `en_transliteration` VALUES (4249, 'Nahnu awliyaokum fee alhayati a **l** ddunya wafee alakhirati walakum feeha ma tashtahee anfusukum walakum feeha ma tadda\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4250, 'Nuzulan min ghafoorin raheem **in**');
INSERT INTO `en_transliteration` VALUES (4251, 'Waman ahsanu qawlan mimman da\'a ila Allahi wa\'amila salihan waqala innanee mina almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (4252, 'Wala tastawee alhasanatu wala a **l** ssayyiatu idfa\' bi **a** llatee hiya ahsanu faitha allathee baynaka wabaynahu \'adawatun kaannahu waliyyun hameem **un**');
INSERT INTO `en_transliteration` VALUES (4253, 'Wama yulaqqaha illa allatheena sabaroo wama yulaqqaha illathoo ha _thth_ in \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (4254, 'Waimma yanzaghannaka mina a **l** shshaytani nazghun fa **i** sta\'ith bi **A** llahi innahu huwa a **l** ssamee\'u al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (4255, 'Wamin ayatihi allaylu wa **al** nnaharu wa **al** shshamsu wa **a** lqamaru la tasjudoo li **l** shshamsi wala lilqamari wa **o** sjudoo lillahi alathee khalaqahunna in kuntum iyyahu ta\'budoon **a**');
INSERT INTO `en_transliteration` VALUES (4256, 'Faini istakbaroo fa **a** llatheena \'inda rabbika yusabbihoona lahu bi **a** llayli wa **al** nnahari wahum la yasamoon **a**');
INSERT INTO `en_transliteration` VALUES (4257, 'Wamin ayatihi annaka tara alarda khashi\'atan faitha anzana \'alayha almaa ihtazzat warabat inna allathee ahyaha lamuhyee almawta innahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (4258, 'Inna allatheena yulhidoona fee ayatina la yakhfawna \'alayna afaman yulqa fee a **l** nnari khayrun am man yatee aminan yawma alqiyamati i\'maloo ma shitum innahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (4259, 'Inna allatheena kafaroo bi **al** ththikri lamma jaahum wainnahu lakitabun \'azeez **un**');
INSERT INTO `en_transliteration` VALUES (4260, 'La yateehi albatilu min bayni yadayhi wala min khalfihi tanzeelun min hakeemin hameed **in**');
INSERT INTO `en_transliteration` VALUES (4261, 'Ma yuqalu laka illa ma qad qeela li **l** rrusuli min qablika inna rabbaka lathoo maghfiratin wathoo \'iqabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (4262, 'Walaw ja\'anahu quranan a\'jamiyyan laqaloo lawla fussilat ayatuhu aa\'jamiyyun wa\'arabiyyun qul huwa lillatheena amanoo hudan washifaon wa **a** llatheena la yuminoona fee athanihim waqrun wahuwa \'alayhim \'aman olaika yunadawna min makanin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (4263, 'Walaqad atayna moosa alkitaba fa **i** khtulifa feehi walawla kalimatun sabaqat min rabbika laqudiya baynahum wainnahum lafee shakkin minhu mureeb **in**');
INSERT INTO `en_transliteration` VALUES (4264, 'Man \'amila salihan falinafsihi waman asaa fa\'alayha wama rabbuka bi _th_ allamin lil\'abeed **i**');
INSERT INTO `en_transliteration` VALUES (4265, 'Ilayhi yuraddu \'ilmu a **l** ssa\'ati wama takhruju min thamaratin min akmamiha wama tahmilu min ontha wala tada\'u illa bi\'ilmihi wayawma yunadeehim ayna shurakaee qaloo athannaka ma minna min shaheed **in**');
INSERT INTO `en_transliteration` VALUES (4266, 'Wadalla \'anhum ma kanoo yad\'oona min qablu wa _th_ annoo ma lahum min mahees **in**');
INSERT INTO `en_transliteration` VALUES (4267, 'La yasamu alinsanu min du\'ai alkhayri wain massahu a **l** shsharru fayaoosun qanoot **un**');
INSERT INTO `en_transliteration` VALUES (4268, 'Walain athaqnahu rahmatan minna min ba\'di darraa massathu layaqoolanna hatha lee wama a _th_ unnu a **l** ssa\'ata qaimatan walain ruji\'tu ila rabbee inna lee \'indahu lalhusna falanunabbianna allatheena kafaroo bima \'amiloo walanutheeqannahum min \'athabin ghalee _th_ **in**');
INSERT INTO `en_transliteration` VALUES (4269, 'Waitha an\'amna \'ala alinsani a\'rada wanaa bijanibihi waitha massahu a **l** shsharru fathoo du\'ain \'areed **in**');
INSERT INTO `en_transliteration` VALUES (4270, 'Qul araaytum in kana min \'indi Allahi thumma kafartum bihi man adallu mimman huwa fee shiqaqin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (4271, 'Sanureehim ayatina fee alafaqi wafee anfusihim hatta yatabayyana lahum annahu alhaqqu awalam yakfi birabbika annahu \'ala kulli shayin shaheed **un**');
INSERT INTO `en_transliteration` VALUES (4272, 'Ala innahum fee miryatin min liqai rabbihim ala innahu bikulli shayin muheet **un**');
INSERT INTO `en_transliteration` VALUES (4273, 'Hameem');
INSERT INTO `en_transliteration` VALUES (4274, '\'aynseenqaf');
INSERT INTO `en_transliteration` VALUES (4275, 'Kathalika yoohee ilayka waila allatheena min qablika Allahu al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (4276, 'Lahu ma fee a **l** ssamawati wama fee alardi wahuwa al\'aliyyu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (4277, 'Takadu a **l** ssamawatu yatafattarna min fawqihinna wa **a** lmalaikatu yusabbihoona bihamdi rabbihim wayastaghfiroona liman fee alardi ala inna Allaha huwa alghafooru a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (4278, 'Wa **a** llatheena ittakhathoo min doonihi awliyaa Allahu hafee _th_ un \'alayhim wama anta \'alayhim biwakeel **in**');
INSERT INTO `en_transliteration` VALUES (4279, 'Wakathalika awhayna ilayka quranan \'arabiyyan litunthira omma alqura waman hawlaha watunthira yawma aljam\'i la rayba feehi fareequn fee aljannati wafareequn fee a **l** ssa\'eer **i**');
INSERT INTO `en_transliteration` VALUES (4280, 'Walaw shaa Allahu laja\'alahum ommatan wahidatan walakin yudkhilu man yashao fee rahmatihi wa **al** _thth_ alimoona ma lahum min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (4281, 'Ami ittakhathoo min doonihi awliyaa fa **A** llahu huwa alwaliyyu wahuwa yuhyee almawta wahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (4282, 'Wama ikhtalaftum feehi min shayin fahukmuhu ila Allahi thalikumu Allahu rabbee \'alayhi tawakkatu wailayhi oneeb **u**');
INSERT INTO `en_transliteration` VALUES (4283, 'Fatiru a **l** ssamawati wa **a** lardi ja\'ala lakum min anfusikum azwajan wamina alan\'ami azwajan yathraokum feehi laysa kamithlihi shayon wahuwa a **l** ssamee\'u albaseer **u**');
INSERT INTO `en_transliteration` VALUES (4284, 'Lahu maqaleedu a **l** ssamawati wa **a** lardi yabsutu a **l** rrizqa liman yashao wayaqdiru innahu bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (4285, 'Shara\'a lakum mina a **l** ddeeni ma wassa bihi noohan wa **a** llathee awhayna ilayka wama wassayna bihi ibraheema wamoosa wa\'eesa an aqeemoo a **l** ddeena wala tatafarraqoo feehi kabura \'ala almushrikeena ma tad\'oohum ilayhi Allahu yajtabee ilayhi man yashao wayahdee ilayhi man yuneeb **u**');
INSERT INTO `en_transliteration` VALUES (4286, 'Wama tafarraqoo illa min ba\'di ma jaahumu al\'ilmu baghyan baynahum walawla kalimatun sabaqat min rabbika ila ajalin musamman laqudiya baynahum wainna allatheena oorithoo alkitaba min ba\'dihim lafee shakkin minhu mureeb **in**');
INSERT INTO `en_transliteration` VALUES (4287, 'Falithalika fa **o** d\'u wa **i** staqim kama omirta wala tattabi\' ahwaahum waqul amantu bima anzala Allahu min kitabin waomirtu lia\'dila baynakum Allahu rabbuna warabbukum lana a\'maluna walakum a\'malukum lahujjata baynana wabaynakumu Allahu yajma\'u baynana wailayhi almaseer **u**');
INSERT INTO `en_transliteration` VALUES (4288, 'Wa **a** llatheena yuhajjoona fee Allahi min ba\'di ma istujeeba lahu hujjatuhum dahidatun \'inda rabbihim wa\'alayhim ghadabun walahum \'athabun shadeed **un**');
INSERT INTO `en_transliteration` VALUES (4289, 'Allahu allathee anzala alkitaba bi **a** lhaqqi wa **a** lmeezani wama yudreeka la\'alla a **l** ssa\'ata qareeb **un**');
INSERT INTO `en_transliteration` VALUES (4290, 'Yasta\'jilu biha allatheena la yuminoona biha wa **a** llatheena amanoo mushfiqoona minha waya\'lamoona annaha alhaqqu ala inna allatheena yumaroona fee a **l** ssa\'ati lafee dalalin ba\'eed **in**');
INSERT INTO `en_transliteration` VALUES (4291, 'Allahu lateefun bi\'ibadihi yarzuqu man yashao wahuwa alqawiyyu al\'azeez **u**');
INSERT INTO `en_transliteration` VALUES (4292, 'Man kana yureedu hartha alakhirati nazid lahu fee harthihi waman kana yureedu hartha a **l** ddunya nutihi minha wama lahu fee alakhirati min naseeb **in**');
INSERT INTO `en_transliteration` VALUES (4293, 'Am lahum shurakao shara\'oo lahum mina a **l** ddeeni ma lam yathan bihi Allahu walawla kalimatu alfasli laqudiya baynahum wainna a **l** _thth_ alimeena lahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (4294, 'Tara a **l** _thth_ alimeena mushfiqeena mimma kasaboo wahuwa waqi\'un bihim wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati fee rawdati aljannati lahum ma yashaoona \'inda rabbihim thalika huwa alfadlu alkabeer **u**');
INSERT INTO `en_transliteration` VALUES (4295, 'Thalika allathee yubashshiru Allahu \'ibadahu allatheena amanoo wa\'amiloo a **l** ssalihati qul la asalukum \'alayhi ajran illa almawaddata fee alqurba waman yaqtarif hasanatan nazid lahu feehahusnan inna Allaha ghafoorun shakoor **un**');
INSERT INTO `en_transliteration` VALUES (4296, 'Am yaqooloona iftara \'ala Allahi kathiban fain yashai Allahu yakhtim \'ala qalbika wayamhu Allahu albatila wayuhiqqu alhaqqa bikalimatihi innahu \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (4297, 'Wahuwa allathee yaqbalu a **l** ttawbata \'an \'ibadihi waya\'foo \'ani a **l** ssayyiati waya\'lamu ma taf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (4298, 'Wayastajeebu allatheena amanoo wa\'amiloo a **l** ssalihati wayazeeduhum min fadlihi wa **a** lkafiroona lahum \'athabun shadeed **un**');
INSERT INTO `en_transliteration` VALUES (4299, 'Walaw basata Allahu a **l** rrizqa li\'ibadihi labaghaw fee alardi walakin yunazzilu biqadarin ma yashao innahu bi\'ibadihi khabeerun baseer **un**');
INSERT INTO `en_transliteration` VALUES (4300, 'Wahuwa allathee yunazzilu alghaytha min ba\'di ma qanatoo wayanshuru rahmatahu wahuwa alwaliyyu alhameed **u**');
INSERT INTO `en_transliteration` VALUES (4301, 'Wamin ayatihi khalqu a **l** ssamawati wa **a** lardi wama baththa feehima min dabbatin wahuwa \'ala jam\'ihim itha yashao qadeer **un**');
INSERT INTO `en_transliteration` VALUES (4302, 'Wama asabakum min museebatin fabima kasabat aydeekum waya\'foo \'an katheer **in**');
INSERT INTO `en_transliteration` VALUES (4303, 'Wama antum bimu\'jizeena fee alardi wama lakum min dooni Allahi min waliyyin wala naseer **in**');
INSERT INTO `en_transliteration` VALUES (4304, 'Wamin ayatihi aljawari fee albahri ka **a** la\'lam **i**');
INSERT INTO `en_transliteration` VALUES (4305, 'In yasha yuskini a **l** rreeha faya _th_ lana rawakida \'ala _th_ ahrihi inna fee thalika laayatin likulli sabbarin shakoor **in**');
INSERT INTO `en_transliteration` VALUES (4306, 'Aw yoobiqhunna bima kasaboo waya\'fu \'an katheer **in**');
INSERT INTO `en_transliteration` VALUES (4307, 'Waya\'lama allatheena yujadiloona fee ayatina ma lahum min mahees **in**');
INSERT INTO `en_transliteration` VALUES (4308, 'Fama ooteetum min shayin famata\'u alhayati a **l** ddunya wama \'inda Allahi khayrun waabqa lillatheena amanoo wa\'ala rabbihim yatawakkaloon **a**');
INSERT INTO `en_transliteration` VALUES (4309, 'Wa **a** llatheena yajtaniboona kabaira alithmi wa **a** lfawahisha waitha ma ghadiboo hum yaghfiroon **a**');
INSERT INTO `en_transliteration` VALUES (4310, 'Wa **a** llatheena istajaboo lirabbihim waaqamoo a **l** ssalata waamruhum shoora baynahum wamimma razaqnahum yunfiqoon **a**');
INSERT INTO `en_transliteration` VALUES (4311, 'Wa **a** llatheena itha asabahumu albaghyu hum yantasiroon **a**');
INSERT INTO `en_transliteration` VALUES (4312, 'Wajazao sayyiatin sayyiatun mithluha faman \'afa waaslaha faajruhu \'ala Allahi innahu la yuhibbu a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (4313, 'Walamani intasara ba\'da _th_ ulmihi faolaika ma \'alayhim min sabeel **in**');
INSERT INTO `en_transliteration` VALUES (4314, 'Innama a **l** ssabeelu \'ala allatheena ya _th_ limoona a **l** nnasa wayabghoona fee alardi bighayri alhaqqi olaika lahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (4315, 'Walaman sabara waghafara inna thalika lamin \'azmi alomoor **i**');
INSERT INTO `en_transliteration` VALUES (4316, 'Waman yudlili Allahu fama lahu min waliyyin min ba\'dihi watara a **l** _thth_ alimeena lamma raawoo al\'athaba yaqooloona hal ila maraddin min sabeel **in**');
INSERT INTO `en_transliteration` VALUES (4317, 'Watarahum yu\'radoona \'alayha khashi\'eena mina a **l** ththulli yan _th_ uroona min tarfin khafiyyin waqala allatheena amanoo inna alkhasireena allatheena khasiroo anfusahum waahleehim yawma alqiyamati ala inna a **l** _thth_ alimeena fee \'athabin muqeem **in**');
INSERT INTO `en_transliteration` VALUES (4318, 'Wama kana lahum min awliyaa yansuroonahum min dooni Allahi waman yudlili Allahu fama lahu min sabeel **in**');
INSERT INTO `en_transliteration` VALUES (4319, 'Istajeeboo lirabbikum min qabli an yatiya yawmun la maradda lahu mina Allahi ma lakum min maljain yawmaithin wama lakum min nakeer **in**');
INSERT INTO `en_transliteration` VALUES (4320, 'Fain a\'radoo fama arsanaka \'alayhim hafee _th_ an in \'alayka illa albalaghu wainna itha athaqna alinsana minna rahmatan fariha biha wain tusibhum sayyiatun bima qaddamat aydeehim fainna alinsana kafoor **un**');
INSERT INTO `en_transliteration` VALUES (4321, 'Lillahi mulku a **l** ssamawati wa **a** lardi yakhluqu ma yashao yahabu liman yashao inathan wayahabu liman yashao a **l** ththukoor **a**');
INSERT INTO `en_transliteration` VALUES (4322, 'Aw yuzawwijuhum thukranan wainathan wayaj\'alu man yashao \'aqeeman innahu \'aleemun qadeer **un**');
INSERT INTO `en_transliteration` VALUES (4323, 'Wama kana libasharin an yukallimahu Allahu illa wahyan aw min warai hijabin aw yursila rasoolan fayoohiya biithnihi ma yashao innahu \'aliyyun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (4324, 'Wakathalika awhayna ilayka roohan min amrina ma kunta tadree ma alkitabu wala aleemanu walakin ja\'anahu nooran nahdee bihi man nashao min \'ibadina wainnaka latahdee ilasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (4325, 'Sirati Allahi allathee lahu ma fee a **l** ssamawati wama fee alardi ala ila Allahi taseeru alomoor **u**');
INSERT INTO `en_transliteration` VALUES (4326, 'Hameem');
INSERT INTO `en_transliteration` VALUES (4327, 'Wa **a** lkitabi almubeen **i**');
INSERT INTO `en_transliteration` VALUES (4328, 'Inna ja\'anahu quranan \'arabiyyan la\'allakum ta\'qiloona');
INSERT INTO `en_transliteration` VALUES (4329, 'Wainnahu fee ommi alkitabi ladayna la\'aliyyun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (4330, 'Afanadribu \'ankumu a **l** ththikra safhan an kuntum qawman musrifeen **a**');
INSERT INTO `en_transliteration` VALUES (4331, 'Wakam arsana min nabiyyin fee alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (4332, 'Wama yateehim min nabiyyin illa kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (4333, 'Faahlakna ashadda minhum batshan wamada mathalu alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (4334, 'Walain saatahum man khalaqa a **l** ssamawati wa **a** larda layaqoolunna khalaqahunna al\'azeezu al\'aleem **u**');
INSERT INTO `en_transliteration` VALUES (4335, 'Allathee ja\'ala lakumu alarda mahdan waja\'ala lakum feeha subulan la\'allakum tahtadoon **a**');
INSERT INTO `en_transliteration` VALUES (4336, 'Wa **a** llathee nazzala mina a **l** ssamai maan biqadarin faansharna bihi badatan maytan kathalika tukhrajoon **a**');
INSERT INTO `en_transliteration` VALUES (4337, 'Wa **a** llathee khalaqa alazwaja kullaha waja\'ala lakum mina alfulki wa **a** lan\'ami ma tarkaboon **a**');
INSERT INTO `en_transliteration` VALUES (4338, 'Litastawoo \'ala _th_ uhoorihi thumma tathkuroo ni\'mata rabbikum itha istawaytum \'alayhi wataqooloo subhana allathee sakhkhara lana hatha wama kunna lahu muqrineen **a**');
INSERT INTO `en_transliteration` VALUES (4339, 'Wainna ila rabbina lamunqaliboon **a**');
INSERT INTO `en_transliteration` VALUES (4340, 'Waja\'aloo lahu min \'ibadihi juzan inna alinsana lakafoorun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (4341, 'Ami ittakhatha mimma yakhluqu banatin waasfakum bi **a** lbaneen **a**');
INSERT INTO `en_transliteration` VALUES (4342, 'Waitha bushshira ahaduhum bimadaraba li **l** rrahmani mathalan _th_ alla wajhuhu muswaddan wahuwa ka _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (4343, 'Awaman yunashshao fee alhilyati wahuwa fee alkhisami ghayru mubeen **in**');
INSERT INTO `en_transliteration` VALUES (4344, 'Waja\'aloo almalaikata allatheena hum \'ibadu a **l** rrahmani inathan ashahidoo khalqahum satuktabu shahadatuhum wayusaloon **a**');
INSERT INTO `en_transliteration` VALUES (4345, 'Waqaloo law shaa a **l** rrahmanu ma \'abadnahum ma lahum bithalika min \'ilmin in hum illa yakhrusoon **a**');
INSERT INTO `en_transliteration` VALUES (4346, 'Am ataynahum kitaban min qablihi fahum bihi mustamsikoon **a**');
INSERT INTO `en_transliteration` VALUES (4347, 'Bal qaloo inna wajadnaabaana \'ala ommatin wainna \'alaatharihim muhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (4348, 'Wakathalika ma arsana min qablika fee qaryatin min natheerin illa qala mutrafooha inna wajadnaabaana \'ala ommatin wainna \'alaatharihim muqtadoon **a**');
INSERT INTO `en_transliteration` VALUES (4349, 'Qala awalaw jitukum biahda mimma wajadtum \'alayhi abaakum qaloo inna bima orsiltum bihi kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (4350, 'Fa **i** ntaqamna minhum fa **o** n _th_ ur kayfa kana \'aqibatu almukaththibeen **a**');
INSERT INTO `en_transliteration` VALUES (4351, 'Waith qala ibraheemu liabeehi waqawmihi innanee baraon mimma ta\'budoon **a**');
INSERT INTO `en_transliteration` VALUES (4352, 'Illa allathee fataranee fainnahu sayahdeen **i**');
INSERT INTO `en_transliteration` VALUES (4353, 'Waja\'alaha kalimatan baqiyatan fee \'aqibihi la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4354, 'Bal matta\'tu haolai waabaahum hatta jaahumu alhaqqu warasoolun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (4355, 'Walamma jaahumu alhaqqu qaloo hatha sihrun wainna bihi kafiroon **a**');
INSERT INTO `en_transliteration` VALUES (4356, 'Waqaloo lawla nuzzila hatha alquranu \'ala rajulin mina alqaryatayni \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (4357, 'Ahum yaqsimoona rahmata rabbika nahnu qasamna baynahum ma\'eeshatahum fee alhayati a **l** ddunya warafa\'na ba\'dahum fawqa ba\'din darajatin liyattakhitha ba\'duhum ba\'dan sukhriyyan warahmatu rabbika khayrun mimma yajma\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4358, 'Walawla an yakoona a **l** nnasu ommatan wahidatan laja\'ana liman yakfuru bi **al** rrahmani libuyootihim suqufan min fiddatin wama\'arija \'alayha ya _th_ haroon **a**');
INSERT INTO `en_transliteration` VALUES (4359, 'Walibuyootihim abwaban wasururan \'alayha yattakioon **a**');
INSERT INTO `en_transliteration` VALUES (4360, 'Wazukhrufan wain kullu thalika lamma mata\'u alhayati a **l** ddunya wa **a** lakhiratu \'inda rabbika lilmuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (4361, 'Waman ya\'shu \'an thikri a **l** rrahmani nuqayyid lahu shaytanan fahuwa lahu qareen **un**');
INSERT INTO `en_transliteration` VALUES (4362, 'Wainnahum layasuddoonahum \'ani a **l** ssabeeli wayahsaboona annahum muhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (4363, 'Hatta itha jaana qala ya layta baynee wabaynaka bu\'da almashriqayni fabisa alqareen **u**');
INSERT INTO `en_transliteration` VALUES (4364, 'Walan yanfa\'akumu alyawma ith _th_ alamtum annakum fee al\'athabi mushtarikoon **a**');
INSERT INTO `en_transliteration` VALUES (4365, 'Afaanta tusmi\'u a **l** ssumma aw tahdee al\'umya waman kana fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (4366, 'Faimma nathhabanna bika fainna minhum muntaqimoon **a**');
INSERT INTO `en_transliteration` VALUES (4367, 'Aw nuriyannaka allathee wa\'adnahum fainna \'alayhim muqtadiroon **a**');
INSERT INTO `en_transliteration` VALUES (4368, 'Fa **i** stamsik bi **a** llathee oohiya ilayka innaka \'alasiratin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (4369, 'Wainnahu lathikrun laka waliqawmika wasawfa tusaloon **a**');
INSERT INTO `en_transliteration` VALUES (4370, 'Wa **i** sal man arsana min qablika min rusulina aja\'ana min dooni a **l** rrahmani alihatan yu\'badoon **a**');
INSERT INTO `en_transliteration` VALUES (4371, 'Walaqad arsana moosa biayatina ila fir\'awna wamalaihi faqala innee rasoolu rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4372, 'Falamma jaahum biayatina itha hum minha yadhakoon **a**');
INSERT INTO `en_transliteration` VALUES (4373, 'Wama nureehim min ayatin illa hiya akbaru min okhtiha waakhathnahum bi **a** l\'athabi la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4374, 'Waqaloo ya ayyuha a **l** sahiru od\'u lana rabbaka bima \'ahida \'indaka innana lamuhtadoon **a**');
INSERT INTO `en_transliteration` VALUES (4375, 'Falamma kashafna \'anhumu al\'athaba itha hum yankuthoon **a**');
INSERT INTO `en_transliteration` VALUES (4376, 'Wanada fir\'awnu fee qawmihi qala ya qawmi alaysa lee mulku misra wahathihi alanharu tajree min tahtee afala tubsiroon **a**');
INSERT INTO `en_transliteration` VALUES (4377, 'Am ana khayrun min hatha allathee huwa maheenun wala yakadu yubeen **u**');
INSERT INTO `en_transliteration` VALUES (4378, 'Falawla olqiya \'alayhi aswiratun min thahabin aw jaa ma\'ahu almalaikatu muqtarineen **a**');
