INSERT INTO `en_transliteration` VALUES (4398, 'Lakum feeha fakihatun katheeratun minha takuloon **a**');
INSERT INTO `en_transliteration` VALUES (4399, 'Inna almujrimeena fee \'athabi jahannama khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (4400, 'La yufattaru \'anhum wahum feehi mublisoon **a**');
INSERT INTO `en_transliteration` VALUES (4401, 'Wama _th_ alamnahum walakin kanoo humu a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (4402, 'Wanadaw ya maliku liyaqdi \'alayna rabbuka qala innakum makithoon **a**');
