INSERT INTO `en_transliteration` VALUES (4427, 'Anna lahumu a **l** ththikra waqad jaahum rasoolun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (4428, 'Thumma tawallaw \'anhu waqaloo mu\'allamun majnoon **un**');
INSERT INTO `en_transliteration` VALUES (4429, 'Inna kashifoo al\'athabi qaleelan innakum \'aidoon **a**');
