INSERT INTO `en_transliteration` VALUES (4432, 'An addoo ilayya \'ibada Allahi innee lakum rasoolun ameen **un**');
INSERT INTO `en_transliteration` VALUES (4433, 'Waan la ta\'loo \'ala Allahi innee ateekum bisultanin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (4434, 'Wainnee \'uthtu birabbee warabbikum an tarjumoon **i**');
INSERT INTO `en_transliteration` VALUES (4435, 'Wain lam tuminoo lee fa **i** \'taziloon **i**');
INSERT INTO `en_transliteration` VALUES (4436, 'Fada\'a rabbahu anna haolai qawmun mujrimoon **a**');
INSERT INTO `en_transliteration` VALUES (4437, 'Faasri bi\'ibadee laylan innakum muttaba\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4438, 'Wa **o** truki albahra rahwan innahum jundun mughraqoon **a**');
INSERT INTO `en_transliteration` VALUES (4439, 'Kam tarakoo min jannatin wa\'uyoon **in**');
INSERT INTO `en_transliteration` VALUES (4440, 'Wazuroo\'in wamaqamin kareem **in**');
INSERT INTO `en_transliteration` VALUES (4441, 'Wana\'matin kanoo feeha fakiheen **a**');
