INSERT INTO `en_transliteration` VALUES (4445, 'Min fir\'awna innahu kana \'aliyan mina almusrifeen **a**');
INSERT INTO `en_transliteration` VALUES (4446, 'Walaqadi ikhtarnahum \'ala \'ilmin \'ala al\'alameen **a**');
