INSERT INTO `en_transliteration` VALUES (4450, 'Fatoo biabaina in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (4451, 'Ahum khayrun am qawmu tubba\'in wa **a** llatheena min qablihim ahlaknahum innahum kanoo mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (4452, 'Wama khalaqna a **l** ssamawati wa **a** larda wama baynahuma la\'ibeen **a**');
