INSERT INTO `en_transliteration` VALUES (4454, 'Inna yawma alfasli meeqatuhum ajma\'een **a**');
INSERT INTO `en_transliteration` VALUES (4455, 'Yawma la yughnee mawlan \'an mawlan shayan wala hum yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (4456, 'Illa man rahima Allahu innahu huwa al\'azeezu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (4457, 'Inna shajarata a **l** zzaqqoom **i**');
INSERT INTO `en_transliteration` VALUES (4458, 'Ta\'amu alatheem **i**');
INSERT INTO `en_transliteration` VALUES (4459, 'Ka **a** lmuhli yaghlee fee albutoon **i**');
INSERT INTO `en_transliteration` VALUES (4460, 'Kaghalyi alhameem **i**');
INSERT INTO `en_transliteration` VALUES (4461, 'Khuthoohu fa **i** \'tiloohu ila sawai aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (4462, 'Thumma subboo fawqa rasihi min \'athabi alhameem **i**');
