INSERT INTO `en_transliteration` VALUES (4478, 'Wa **i** khtilafi allayli wa **al** nnahari wama anzala Allahu mina a **l** ssamai min rizqin faahya bihi alarda ba\'da mawtiha watasreefi a **l** rriyahi ayatun liqawmin ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (4479, 'Tilka ayatu Allahi natlooha \'alayka bi **a** lhaqqi fabiayyi hadeethin ba\'da Allahi waayatihi yuminoon **a**');
INSERT INTO `en_transliteration` VALUES (4480, 'Waylun likulli affakin atheem **in**');
INSERT INTO `en_transliteration` VALUES (4481, 'Yasma\'u ayati Allahi tutla \'alayhi thumma yusirru mustakbiran kaan lam yasma\'ha fabashshirhu bi\'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (4482, 'Waitha \'alima min ayatina shayan ittakhathaha huzuwan olaika lahum \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (4483, 'Min waraihim jahannamu wala yughnee \'anhum ma kasaboo shayan wala ma ittakhathoo min dooni Allahi awliyaa walahum \'athabun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (4484, 'Hatha hudan wa **a** llatheena kafaroo biayati rabbihim lahum \'athabun min rijzin aleem **in**');
