INSERT INTO `en_transliteration` VALUES (4486, 'Wasakhkhara lakum ma fee a **l** ssamawati wama fee alardi jamee\'an minhu inna fee thalika laayatin liqawmin yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (4487, 'Qul lillatheena amanoo yaghfiroo lillatheena la yarjoona ayyama Allahi liyajziya qawman bima kanoo yaksiboon **a**');
INSERT INTO `en_transliteration` VALUES (4488, 'Man \'amila salihan falinafsihi waman asaa fa\'alayha thumma ila rabbikum turja\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4489, 'Walaqad atayna banee israeela alkitaba wa **a** lhukma wa **al** nnubuwwata warazaqnahum mina a **l** ttayyibati wafaddanahum \'ala al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4490, 'Waataynahum bayyinatin mina alamri fama ikhtalafoo illa min ba\'di ma jaahumu al\'ilmu baghyan baynahum inna rabbaka yaqdee baynahum yawma alqiyamati feema kanoo feehi yakhtalifoon **a**');
INSERT INTO `en_transliteration` VALUES (4491, 'Thumma ja\'anaka \'ala sharee\'atin mina alamri fa **i** ttabi\'ha wala tattabi\' ahwaa allatheena la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4492, 'Innahum lan yughnoo \'anka mina Allahi shayan wainna a **l** _thth_ alimeena ba\'duhum awliyao ba\'din wa **A** llahu waliyyu almuttaqeen **a**');
INSERT INTO `en_transliteration` VALUES (4493, 'Hatha basairu li **l** nnasi wahudan warahmatun liqawmin yooqinoon **a**');
INSERT INTO `en_transliteration` VALUES (4494, 'Am hasiba allatheena ijtarahoo a **l** ssayyiati an naj\'alahum ka **a** llatheena amanoo wa\'amiloo a **l** ssalihati sawaan mahyahum wamamatuhum saa ma yahkumoon **a**');
INSERT INTO `en_transliteration` VALUES (4495, 'Wakhalaqa Allahu a **l** ssamawati wa **a** larda bi **a** lhaqqi walitujza kullu nafsin bima kasabat wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4496, 'Afaraayta mani ittakhatha ilahahu hawahu waadallahu Allahu \'ala \'ilmin wakhatama \'ala sam\'ihi waqalbihi waja\'ala \'ala basarihi ghishawatan faman yahdeehi min ba\'di Allahi afala tathakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (4497, 'Waqaloo ma hiya illahayatuna a **l** ddunya namootu wanahya wama yuhlikuna illa a **l** ddahru wama lahum bithalika min \'ilmin in hum illa ya _th_ unnoon **a**');
INSERT INTO `en_transliteration` VALUES (4498, 'Waitha tutla \'alayhim ayatuna bayyinatin ma kana hujjatahum illa an qaloo itoo biabaina in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (4499, 'Quli Allahu yuhyeekum thumma yumeetukum thumma yajma\'ukum ila yawmi alqiyamati la rayba feehi walakinna akthara a **l** nnasi la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4500, 'Walillahi mulku a **l** ssamawati wa **a** lardi wayawma taqoomu a **l** ssa\'atu yawmaithin yakhsaru almubtiloon **a**');
INSERT INTO `en_transliteration` VALUES (4501, 'Watara kulla ommatin jathiyatan kullu ommatin tud\'a ila kitabiha alyawma tujzawna ma kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4502, 'Hatha kitabuna yantiqu \'alaykum bi **a** lhaqqi inna kunna nastansikhu ma kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4503, 'Faamma allatheena amanoo wa\'amiloo a **l** ssalihati fayudkhiluhum rabbuhum fee rahmatihi thalika huwa alfawzu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (4504, 'Waamma allatheena kafaroo afalam takun ayatee tutla \'alaykum fa **i** stakbartum wakuntum qawman mujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (4505, 'Waitha qeela inna wa\'da Allahi haqqun wa **al** ssa\'atu la rayba feeha qultum ma nadree ma a **l** ssa\'atu in na _th_ unnu illa _th_ annan wama nahnu bimustayqineen **a**');
INSERT INTO `en_transliteration` VALUES (4506, 'Wabada lahum sayyiatu ma \'amiloo wahaqa bihim ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (4507, 'Waqeela alyawma nansakum kama naseetum liqaa yawmikum hatha wamawakumu a **l** nnaru wama lakum min nasireen **a**');
INSERT INTO `en_transliteration` VALUES (4508, 'Thalikum biannakumu ittakhathtum ayati Allahi huzuwan wagharratkumu alhayatu a **l** ddunya fa **a** lyawma la yukhrajoona minha wala hum yusta\'taboon **a**');
INSERT INTO `en_transliteration` VALUES (4509, 'Falillahi alhamdu rabbi a **l** ssamawati warabbi alardi rabbi al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (4510, 'Walahu alkibriyao fee a **l** ssamawati wa **a** lardi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (4511, 'Hameem');
INSERT INTO `en_transliteration` VALUES (4512, 'Tanzeelu alkitabi mina Allahi al\'azeezi alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (4513, 'Ma khalaqna a **l** ssamawati wa **a** larda wama baynahuma illa bi **a** lhaqqi waajalin musamman wa **a** llatheena kafaroo \'amma onthiroo mu\'ridoon **a**');
INSERT INTO `en_transliteration` VALUES (4514, 'Qul araaytum ma tad\'oona min dooni Allahi aroonee matha khalaqoo mina alardi am lahum shirkun fee a **l** ssamawati eetoonee bikitabin min qabli hatha aw atharatin min \'ilmin in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (4515, 'Waman adallu mimman yad\'oo min dooni Allahi man la yastajeebu lahu ila yawmi alqiyamati wahum \'an du\'aihim ghafiloon **a**');
INSERT INTO `en_transliteration` VALUES (4516, 'Waithahushira a **l** nnasu kanoo lahum a\'daan wakanoo bi\'ibadatihim kafireen **a**');
INSERT INTO `en_transliteration` VALUES (4517, 'Waitha tutla \'alayhim ayatuna bayyinatin qala allatheena kafaroo lilhaqqi lamma jaahum hatha sihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (4518, 'Am yaqooloona iftarahu qul ini iftaraytuhu fala tamlikoona lee mina Allahi shayan huwa a\'lamu bima tufeedoona feehi kafa bihi shaheedan baynee wabaynakum wahuwa alghafooru a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (4519, 'Qul ma kuntu bid\'an mina a **l** rrusuli wama adree ma yuf\'alu bee wala bikum in attabi\'u illa ma yooha ilayya wama ana illa natheerun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (4520, 'Qul araaytum in kana min \'indi Allahi wakafartum bihi washahida shahidun min banee israeela \'ala mithlihi faamana wa **i** stakbartum inna Allaha la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (4521, 'Waqala allatheena kafaroo lillatheena amanoo law kana khayran ma sabaqoona ilayhi waith lam yahtadoo bihi fasayaqooloona hatha ifkun qadeem **un**');
INSERT INTO `en_transliteration` VALUES (4522, 'Wamin qablihi kitabu moosa imaman warahmatan wahatha kitabun musaddiqun lisanan \'arabiyyan liyunthira allatheena _th_ alamoo wabushra lilmuhsineen **a**');
INSERT INTO `en_transliteration` VALUES (4523, 'Inna allatheena qaloo rabbuna Allahu thumma istaqamoo fala khawfun \'alayhim wala hum yahzanoon **a**');
INSERT INTO `en_transliteration` VALUES (4524, 'Olaika ashabu aljannati khalideena feeha jazaan bima kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (4525, 'Wawassayna alinsana biwalidayhi ihsanan hamalathu ommuhu kurhan wawada\'athu kurhan wahamluhu wafisaluhu thalathoona shahran hatta itha balagha ashuddahu wabalagha arba\'eena sanatan qala rabbi awzi\'nee an ashkura ni\'mataka allatee an\'amta \'alayya wa\'ala walidayya waan a\'mala salihan tardahu waaslih lee fee thurriyyatee innee tubtu ilayka wainnee mina almuslimeen **a**');
INSERT INTO `en_transliteration` VALUES (4526, 'Olaika allatheena nataqabbalu \'anhum ahsana ma \'amiloo wanatajawazu \'an sayyiatihim fee ashabi aljannati wa\'da a **l** ssidqi allathee kanoo yoo\'adoon **a**');
INSERT INTO `en_transliteration` VALUES (4527, 'Wa **a** llathee qala liwalidayhi offin lakuma ata\'idaninee an okhraja waqad khalati alquroonu min qablee wahuma yastagheethani Allaha waylaka amin inna wa\'da Allahi haqqun fayaqoolu ma hatha illa asateeru alawwaleen **a**');
INSERT INTO `en_transliteration` VALUES (4528, 'Olaika allatheena haqqa \'alayhimu alqawlu fee umamin qad khalat min qablihim mina aljinni wa **a** linsi innahum kanoo khasireen **a**');
INSERT INTO `en_transliteration` VALUES (4529, 'Walikullin darajatun mimma \'amiloo waliyuwaffiyahum a\'malahum wahum la yu _th_ lamoon **a**');
INSERT INTO `en_transliteration` VALUES (4530, 'Wayawma yu\'radu allatheena kafaroo \'ala a **l** nnari athhabtum tayyibatikum fee hayatikumu a **l** ddunya wa **i** stamta\'tum biha fa **a** lyawma tujzawna \'athaba alhooni bima kuntum tastakbiroona fee alardi bighayri alhaqqi wabima kuntum tafsuqoon **a**');
INSERT INTO `en_transliteration` VALUES (4531, 'Wa **o** thkur akha \'adin ith anthara qawmahu bi **a** lahqafi waqad khalati a **l** nnuthuru min bayni yadayhi wamin khalfihi alla ta\'budoo illa Allaha innee akhafu \'alaykum \'athaba yawmin \'a _th_ eem **in**');
INSERT INTO `en_transliteration` VALUES (4532, 'Qaloo ajitana litafikana \'an alihatina fatina bima ta\'iduna in kunta mina a **l** ssadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (4533, 'Qala innama al\'ilmu \'inda Allahi waoballighukum ma orsiltu bihi walakinnee arakum qawman tajhaloon **a**');
INSERT INTO `en_transliteration` VALUES (4534, 'Falamma raawhu \'aridan mustaqbila awdiyatihim qaloo hatha \'aridun mumtiruna bal huwa ma ista\'jatum bihi reehun feeha \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (4535, 'Tudammiru kulla shayin biamri rabbiha faasbahoo la yura illa masakinuhum kathalika najzee alqawma almujrimeen **a**');
INSERT INTO `en_transliteration` VALUES (4536, 'Walaqad makkannahum feema in makkannakum feehi waja\'ana lahum sam\'an waabsaran waafidatan fama aghna \'anhum sam\'uhum wala absaruhum wala afidatuhum min shayin ith kanoo yajhadoona biayati Allahi wahaqa bihim ma kanoo bihi yastahzioon **a**');
INSERT INTO `en_transliteration` VALUES (4537, 'Walaqad ahlakna mahawlakum mina alqura wasarrafna alayati la\'allahum yarji\'oon **a**');
INSERT INTO `en_transliteration` VALUES (4538, 'Falawla nasarahumu allatheena ittakhathoo min dooni Allahi qurbanan alihatan bal dalloo \'anhum wathalika ifkuhum wama kanoo yaftaroon **a**');
INSERT INTO `en_transliteration` VALUES (4539, 'Waithsarafna ilayka nafaran mina aljinni yastami\'oona alqurana falammahadaroohu qaloo ansitoo falamma qudiya wallaw ila qawmihim munthireen **a**');
INSERT INTO `en_transliteration` VALUES (4540, 'Qaloo ya qawmana inna sami\'na kitaban onzila min ba\'di moosa musaddiqan lima bayna yadayhi yahdee ila alhaqqi wailatareeqin mustaqeem **in**');
INSERT INTO `en_transliteration` VALUES (4541, 'Ya qawmana ajeeboo da\'iya Allahi waaminoo bihi yaghfir lakum min thunoobikum wayujirkum min \'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (4542, 'Waman la yujib da\'iya Allahi falaysa bimu\'jizin fee alardi walaysa lahu min doonihi awliyaa olaika fee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (4543, 'Awalam yaraw anna Allaha allathee khalaqa a **l** ssamawati wa **a** larda walam ya\'ya bikhalqihinna biqadirin \'ala an yuhyiya almawta bala innahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (4544, 'Wayawma yu\'radu allatheena kafaroo \'ala a **l** nnari alaysa hatha bi **a** lhaqqi qaloo bala warabbina qala fathooqoo al\'athaba bima kuntum takfuroon **a**');
INSERT INTO `en_transliteration` VALUES (4545, 'Fa **i** sbir kamasabara oloo al\'azmi mina a **l** rrusuli wala tasta\'jil lahum kaannahum yawma yarawna ma yoo\'adoona lam yalbathoo illa sa\'atan min naharin balaghun fahal yuhlaku illa alqawmu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (4546, 'Allatheena kafaroo wasaddoo \'an sabeeli Allahi adalla a\'malahum');
INSERT INTO `en_transliteration` VALUES (4547, 'Wa **a** llatheena amanoo wa\'amiloo a **l** ssalihati waamanoo bima nuzzila \'ala muhammadin wahuwa alhaqqu min rabbihim kaffara \'anhum sayyiatihim waaslaha balahum');
INSERT INTO `en_transliteration` VALUES (4548, 'Thalika bianna allatheena kafaroo ittaba\'oo albatila waanna allatheena amanoo ittaba\'oo alhaqqa min rabbihim kathalika yadribu Allahu li **l** nnasi amthalahum');
INSERT INTO `en_transliteration` VALUES (4549, 'Faitha laqeetumu allatheena kafaroo fadarba a **l** rriqabi hatta itha athkhantumoohum fashuddoo alwathaqa faimma mannan ba\'du waimma fidaan hatta tada\'a alharbu awzarahathalika walaw yashao Allahu la **i** ntasara minhum walakin liyabluwa ba\'dakum biba\'din wa **a** llatheena qutiloo fee sabeeli Allahi falan yudilla a\'malahum');
INSERT INTO `en_transliteration` VALUES (4550, 'Sayahdeehim wayuslihu balahum');
INSERT INTO `en_transliteration` VALUES (4551, 'Wayudkhiluhumu aljannata \'arrafaha lahum');
INSERT INTO `en_transliteration` VALUES (4552, 'Ya ayyuha allatheena amanoo in tansuroo Allaha yansurkum wayuthabbit aqdamakum');
INSERT INTO `en_transliteration` VALUES (4553, 'Wa **a** llatheena kafaroo fata\'san lahum waadalla a\'malahum');
INSERT INTO `en_transliteration` VALUES (4554, 'Thalika biannahum karihoo ma anzala Allahu faahbata a\'malahum');
INSERT INTO `en_transliteration` VALUES (4555, 'Afalam yaseeroo fee alardi fayan _th_ uroo kayfa kana \'aqibatu allatheena min qablihim dammara Allahu \'alayhim walilkafireena amthaluha');
INSERT INTO `en_transliteration` VALUES (4556, 'Thalika bianna Allaha mawla allatheena amanoo waanna alkafireena la mawla lahum');
INSERT INTO `en_transliteration` VALUES (4557, 'Inna Allaha yudkhilu allatheena amanoo wa\'amiloo a **l** ssalihati jannatin tajree min tahtiha alanharu wa **a** llatheena kafaroo yatamatta\'oona wayakuloona kama takulu alan\'amu wa **al** nnaru mathwan lahum');
INSERT INTO `en_transliteration` VALUES (4558, 'Wakaayyin min qaryatin hiya ashaddu quwwatan min qaryatika allatee akhrajatka ahlaknahum fala nasira lahum');
INSERT INTO `en_transliteration` VALUES (4559, 'Afaman kana \'ala bayyinatin min rabbihi kaman zuyyina lahu sooo \'amalihi wa **i** ttaba\'oo ahwaahum');
INSERT INTO `en_transliteration` VALUES (4560, 'Mathalu aljannati allatee wu\'ida almuttaqoona feeha anharun min main ghayri asinin waanharun min labanin lam yataghayyar ta\'muhu waanharun min khamrin laththatin li **l** shsharibeena waanharun min \'asalin musaffan walahum feeha min kulli a **l** ththamarati wamaghfiratun min rabbihim kaman huwa khalidun fee a **l** nnari wasuqoo maan hameeman faqatta\'a am\'aahum');
INSERT INTO `en_transliteration` VALUES (4561, 'Waminhum man yastami\'u ilayka hatta itha kharajoo min \'indika qaloo lillatheena ootoo al\'ilma matha qala anifan olaika allatheena taba\'a Allahu \'ala quloobihim wa **i** ttaba\'oo ahwaahum');
INSERT INTO `en_transliteration` VALUES (4562, 'Wa **a** llatheena ihtadaw zadahum hudan waatahum taqwahum');
INSERT INTO `en_transliteration` VALUES (4563, 'Fahal yan _th_ uroona illa a **l** ssa\'ata an tatiyahum baghtatan faqad jaa ashratuha faanna lahum itha jaathum thikrahum');
INSERT INTO `en_transliteration` VALUES (4564, 'Fa **i** \'lam annahu la ilaha illa Allahu wa **i** staghfir lithanbika walilmumineena wa **a** lmuminati wa **A** llahu ya\'lamu mutaqallabakum wamathwakum');
INSERT INTO `en_transliteration` VALUES (4565, 'Wayaqoolu allatheena amanoo lawla nuzzilat sooratun faitha onzilat sooratun muhkamatun wathukira feeha alqitalu raayta allatheena fee quloobihim maradun yan _th_ uroona ilayka na _th_ ara almaghshiyyi \'alayhi mina almawti faawla lahum');
INSERT INTO `en_transliteration` VALUES (4566, 'Ta\'atun waqawlun ma\'roofun faitha \'azama alamru falaw sadaqoo Allaha lakana khayran lahum');
INSERT INTO `en_transliteration` VALUES (4567, 'Fahal \'asaytum in tawallaytum an tufsidoo fee alardi watuqatti\'oo arhamakum');
INSERT INTO `en_transliteration` VALUES (4568, 'Olaika allatheena la\'anahumu Allahu faasammahum waa\'ma absarahum');
INSERT INTO `en_transliteration` VALUES (4569, 'Afala yatadabbaroona alqurana am \'ala quloobin aqfaluha');
INSERT INTO `en_transliteration` VALUES (4570, 'Inna allatheena irtaddoo \'ala adbarihim min ba\'di ma tabayyana lahumu alhuda a **l** shshaytanu sawwala lahum waamla lahum');
INSERT INTO `en_transliteration` VALUES (4571, 'Thalika biannahum qaloo lillatheena karihoo ma nazzala Allahu sanutee\'ukum fee ba\'di alamri wa **A** llahu ya\'lamu israrahum');
INSERT INTO `en_transliteration` VALUES (4572, 'Fakayfa itha tawaffathumu almalaikatu yadriboona wujoohahum waadbarahum');
INSERT INTO `en_transliteration` VALUES (4573, 'Thalika biannahumu ittaba\'oo ma askhata Allaha wakarihoo ridwanahu faahbata a\'malahum');
INSERT INTO `en_transliteration` VALUES (4574, 'Am hasiba allatheena fee quloobihim maradun an lan yukhrija Allahu adghanahum');
INSERT INTO `en_transliteration` VALUES (4575, 'Walaw nashao laaraynakahum fala\'araftahum biseemahum walata\'rifannahum fee lahni alqawli wa **A** llahu ya\'lamu a\'malakum');
INSERT INTO `en_transliteration` VALUES (4576, 'Walanabluwannakum hatta na\'lama almujahideena minkum wa **al** ssabireena wanabluwa akhbarakum');
INSERT INTO `en_transliteration` VALUES (4577, 'Inna allatheena kafaroo wasaddoo \'an sabeeli Allahi washaqqoo a **l** rrasoola min ba\'di ma tabayyana lahumu alhuda lan yaduroo Allaha shayan wasayuhbitu a\'malahum');
INSERT INTO `en_transliteration` VALUES (4578, 'Ya ayyuha allatheena amanoo atee\'oo Allaha waatee\'oo a **l** rrasoola wala tubtiloo a\'malakum');
INSERT INTO `en_transliteration` VALUES (4579, 'Inna allatheena kafaroo wasaddoo \'an sabeeli Allahi thumma matoo wahum kuffarun falan yaghfira Allahu lahum');
INSERT INTO `en_transliteration` VALUES (4580, 'Fala tahinoo watad\'oo ila a **l** ssalmi waantumu ala\'lawna wa **A** llahu ma\'akum walan yatirakum a\'malakum');
INSERT INTO `en_transliteration` VALUES (4581, 'Innama alhayatu a **l** ddunya la\'ibun walahwun wain tuminoo watattaqoo yutikum ojoorakum wala yasalkum amwalakum');
INSERT INTO `en_transliteration` VALUES (4582, 'In yasalkumooha fayuhfikum tabkhaloo wayukhrij adghanakum');
INSERT INTO `en_transliteration` VALUES (4583, 'Haantum haolai tud\'awna litunfiqoo fee sabeeli Allahi faminkum man yabkhalu waman yabkhal fainnama yabkhalu \'an nafsihi wa **A** llahu alghaniyyu waantumu alfuqarao wain tatawallaw yastabdil qawman ghayrakum thumma la yakoonoo amthalakum');
INSERT INTO `en_transliteration` VALUES (4584, 'Inna fatahna laka fathan mubeena **n**');
INSERT INTO `en_transliteration` VALUES (4585, 'Liyaghfira laka Allahu ma taqaddama min thanbika wama taakhkhara wayutimma ni\'matahu \'alayka wayahdiyaka siratan mustaqeema **n**');
INSERT INTO `en_transliteration` VALUES (4586, 'Wayansuraka Allahu nasran \'azeeza **n**');
INSERT INTO `en_transliteration` VALUES (4587, 'Huwa allathee anzala a **l** ssakeenata fee quloobi almumineena liyazdadoo eemanan ma\'a eemanihim walillahi junoodu a **l** ssamawati wa **a** lardi wakana Allahu \'aleeman hakeema **n**');
INSERT INTO `en_transliteration` VALUES (4588, 'Liyudkhila almumineena wa **a** lmuminati jannatin tajree min tahtiha alanharu khalideena feeha wayukaffira \'anhum sayyiatihim wakana thalika \'inda Allahi fawzan \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (4589, 'Wayu\'aththiba almunafiqeena wa **a** lmunafiqati wa **a** lmushrikeena wa **a** lmushrikati a **l** _thth_ anneena bi **A** llahi _th_ anna a **l** ssawi \'alayhim dairatu a **l** ssawi waghadiba Allahu \'alayhim wala\'anahum waa\'adda lahum jahannama wasaat maseera **n**');
INSERT INTO `en_transliteration` VALUES (4590, 'Walillahi junoodu a **l** ssamawati wa **a** lardi wakana Allahu \'azeezan hakeema **n**');
INSERT INTO `en_transliteration` VALUES (4591, 'Inna arsanaka shahidan wamubashshiran wanatheera **n**');
INSERT INTO `en_transliteration` VALUES (4592, 'Lituminoo bi **A** llahi warasoolihi watu\'azziroohu watuwaqqiroohu watusabbihoohu bukratan waaseela **n**');
INSERT INTO `en_transliteration` VALUES (4593, 'Inna allatheena yubayi\'oonaka innama yubayi\'oona Allaha yadu Allahi fawqa aydeehim faman nakatha fainnama yankuthu \'ala nafsihi waman awfa bima \'ahada \'alayhu Allaha fasayuteehi ajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (4594, 'Sayaqoolu laka almukhallafoona mina ala\'rabi shaghalatna amwaluna waahloona fa **i** staghfir lana yaqooloona biasinatihim ma laysa fee quloobihim qul faman yamliku lakum mina Allahi shayan in arada bikum darran aw arada bikum naf\'an bal kana Allahu bima ta\'maloona khabeera **n**');
INSERT INTO `en_transliteration` VALUES (4595, 'Bal _th_ anantum an lan yanqaliba a **l** rrasoolu wa **a** lmuminoona ila ahleehim abadan wazuyyina thalika fee quloobikum wa _th_ anantum _th_ anna a **l** ssawi wakuntum qawman boora **n**');
INSERT INTO `en_transliteration` VALUES (4596, 'Waman lam yumin bi **A** llahi warasoolihi fainna a\'tadna lilkafireena sa\'eera **n**');
INSERT INTO `en_transliteration` VALUES (4597, 'Walillahi mulku a **l** ssamawati wa **a** lardi yaghfiru liman yashao wayu\'aththibu man yashao wakana Allahu ghafooran raheema **n**');
INSERT INTO `en_transliteration` VALUES (4598, 'Sayaqoolu almukhallafoona itha intalaqtum ila maghanima litakhuthoohatharoona nattabi\'kum yureedoona an yubaddiloo kalama Allahi qul lan tattabi\'oona kathalikum qala Allahu min qablu fasayaqooloona bal tahsudoonana bal kanoo la yafqahoona illa qaleela **n**');
INSERT INTO `en_transliteration` VALUES (4599, 'Qul lilmukhallafeena mina ala\'rabi satud\'awna ila qawmin olee basin shadeedin tuqatiloonahum aw yuslimoona fain tutee\'oo yutikumu Allahu ajran hasanan wain tatawallaw kama tawallaytum min qablu yu\'aththibkum \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (4600, 'Laysa \'ala ala\'maharajun wala \'ala ala\'raji harajun wala \'ala almareedi harajun waman yuti\'i Allaha warasoolahu yudkhilhu jannatin tajree min tahtiha alanharu waman yatawalla yu\'aththibhu \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (4601, 'Laqad radiya Allahu \'ani almumineena ith yubayi\'oonaka tahta a **l** shshajarati fa\'alima ma fee quloobihim faanzala a **l** sakeenata \'alayhim waathabahum fathan qareeba **n**');
INSERT INTO `en_transliteration` VALUES (4602, 'Wamaghanima katheeratan yakhuthoonaha wakana Allahu \'azeezan hakeema **n**');
INSERT INTO `en_transliteration` VALUES (4603, 'Wa\'adakumu Allahu maghanima katheeratan takhuthoonaha fa\'ajjala lakum hathihi wakaffa aydiya a **l** nnasi \'ankum walitakoona ayatan lilmumineena wayahdiyakum siratan mustaqeema **n**');
INSERT INTO `en_transliteration` VALUES (4604, 'Waokhra lam taqdiroo \'alayha qad ahata Allahu biha wakana Allahu \'ala kulli shayin qadeera **n**');
INSERT INTO `en_transliteration` VALUES (4605, 'Walaw qatalakumu allatheena kafaroo lawallawoo aladbara thumma la yajidoona waliyyan wala naseera **n**');
INSERT INTO `en_transliteration` VALUES (4606, 'Sunnata Allahi allatee qad khalat min qablu walan tajida lisunnati Allahi tabdeela **n**');
INSERT INTO `en_transliteration` VALUES (4607, 'Wahuwa allathee kaffa aydiyahum \'ankum waaydiyakum \'anhum bibatni makkata min ba\'di an a _th_ farakum \'alayhim wakana Allahu bima ta\'maloona baseera **n**');
INSERT INTO `en_transliteration` VALUES (4608, 'Humu allatheena kafaroo wasaddookum \'ani almasjidi alharami wa **a** lhadya ma\'koofan an yablugha mahillahu walawla rijalun muminoona wanisaon muminatun lam ta\'lamoohum an tataoohum fatuseebakum minhum ma\'arratun bighayri \'ilmin liyudkhila Allahu fee rahmatihi man yashao law tazayyaloo la\'aththabna allatheena kafaroo minhum \'athaban aleema **n**');
INSERT INTO `en_transliteration` VALUES (4609, 'Ith ja\'ala allatheena kafaroo fee quloobihimu alhamiyyata hamiyyata aljahiliyyati faanzala Allahu sakeenatahu \'ala rasoolihi wa\'ala almumineena waazamahum kalimata a **l** ttaqwa wakanoo ahaqqa biha waahlaha wakana Allahu bikulli shayin \'aleema **n**');
INSERT INTO `en_transliteration` VALUES (4610, 'Laqad sadaqa Allahu rasoolahu a **l** rruya bi **a** lhaqqi latadkhulunna almasjida alharama in shaa Allahu amineena muhalliqeena ruoosakum wamuqassireena la takhafoona fa\'alima ma lam ta\'lamoo faja\'ala min dooni thalika fathan qareeba **n**');
INSERT INTO `en_transliteration` VALUES (4611, 'Huwa allathee arsala rasoolahu bi **a** lhuda wadeeni alhaqqi liyu _th_ hirahu \'ala a **l** ddeeni kullihi wakafa bi **A** llahi shaheeda **n**');
INSERT INTO `en_transliteration` VALUES (4612, 'Muhammadun rasoolu Allahi wa **a** llatheena ma\'ahu ashiddao \'ala alkuffari ruhamao baynahum tarahum rukka\'an sujjadan yabtaghoona fadlan mina Allahi waridwanan seemahum fee wujoohihim min athari a **l** ssujoodi thalika mathaluhum fee a **l** ttawrati wamathaluhum fee alinjeeli kazar\'in akhraja shatahu faazarahu fa **i** staghla _th_ a fa **i** stawa \'ala sooqihi yu\'jibu a **l** zzurra\'a liyaghee _th_ a bihimu alkuffara wa\'ada Allahu allatheena amanoo wa\'amiloo a **l** ssalihati minhum maghfiratan waajran \'a _th_ eema **n**');
INSERT INTO `en_transliteration` VALUES (4613, 'Ya ayyuha allatheena amanoo la tuqaddimoo bayna yadayi Allahi warasoolihi wa **i** ttaqoo Allaha inna Allaha samee\'un \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (4614, 'Ya ayyuha allatheena amanoo la tarfa\'oo aswatakum fawqa sawti a **l** nnabiyyi wala tajharoo lahu bi **a** lqawli kajahri ba\'dikum liba\'din an tahbata a\'malukum waantum la tash\'uroon **a**');
INSERT INTO `en_transliteration` VALUES (4615, 'Inna allatheena yaghuddoona aswatahum \'inda rasooli Allahi olaika allatheena imtahana Allahu quloobahum li **l** ttaqwa lahum maghfiratun waajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (4616, 'Inna allatheena yunadoonaka min warai alhujurati aktharuhum la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (4617, 'Walaw annahum sabaroo hatta takhruja ilayhim lakana khayran lahum wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (4618, 'Ya ayyuha allatheena amanoo in jaakum fasiqun binabain fatabayyanoo an tuseeboo qawman bijahalatin fatusbihoo \'ala ma fa\'atum nadimeen **a**');
INSERT INTO `en_transliteration` VALUES (4619, 'Wa **i** \'lamoo anna feekum rasoola Allahi law yutee\'ukum fee katheerin mina alamri la\'anittum walakinna Allaha habbaba ilaykumu aleemana wazayyanahu fee quloobikum wakarraha ilaykumu alkufra wa **a** lfusooqa wa **a** l\'isyana olaika humu a **l** rrashidoon **a**');
INSERT INTO `en_transliteration` VALUES (4620, 'Fadlan mina Allahi wani\'matan wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (4621, 'Wain taifatani mina almumineena iqtataloo faaslihoo baynahuma fain baghat ihdahuma \'ala alokhra faqatiloo allatee tabghee hatta tafeea ila amri Allahi fain faat faaslihoo baynahuma bi **a** l\'adli waaqsitoo inna Allaha yuhibbu almuqsiteen **a**');
INSERT INTO `en_transliteration` VALUES (4622, 'Innama almuminoona ikhwatun faaslihoo bayna akhawaykum wa **i** ttaqoo Allaha la\'allakum turhamoon **a**');
INSERT INTO `en_transliteration` VALUES (4623, 'Ya ayyuha allatheena amanoo la yaskhar qawmun min qawmin \'asa an yakoonoo khayran minhum wala nisaon min nisain \'asa an yakunna khayran minhunna wala talmizoo anfusakum wala tanabazoo bi **a** lalqabi bisa alismu alfusooqu ba\'da aleemani waman lam yatub faolaika humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (4624, 'Ya ayyuha allatheena amanoo ijtaniboo katheeran mina a **l** _thth_ anni inna ba\'da a **l** _thth_ anni ithmun wala tajassasoo wala yaghtab ba\'dukum ba\'dan ayuhibbu ahadukum an yakula lahma akheehi maytan fakarihtumoohu wa **i** ttaqoo Allaha inna Allaha tawwabun raheem **un**');
INSERT INTO `en_transliteration` VALUES (4625, 'Ya ayyuha a **l** nnasu inna khalaqnakum min thakarin waontha waja\'anakum shu\'ooban waqabaila lita\'arafoo inna akramakum \'inda Allahi atqakum inna Allaha \'aleemun khabeer **un**');
INSERT INTO `en_transliteration` VALUES (4626, 'Qalati ala\'rabu amanna qul lam tuminoo walakin qooloo aslamna walamma yadkhuli aleemanu fee quloobikum wain tutee\'oo Allaha warasoolahu la yalitkum min a\'malikum shayan inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (4627, 'Innama almuminoona allatheena amanoo bi **A** llahi warasoolihi thumma lam yartaboo wajahadoo biamwalihim waanfusihim fee sabeeli Allahi olaika humu a **l** ssadiqoon **a**');
INSERT INTO `en_transliteration` VALUES (4628, 'Qul atu\'allimoona Allaha bideenikum wa **A** llahu ya\'lamu ma fee a **l** ssamawati wama fee alardi wa **A** llahu bikulli shayin \'aleem **un**');
