INSERT INTO `en_transliteration` VALUES (5076, 'Sabbaha lillahi ma fee a **l** ssamawati wa **a** lardi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5077, 'Lahu mulku a **l** ssamawati wa **a** lardi yuhyee wayumeetu wahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (5078, 'Huwa alawwalu wa **a** lakhiru wa **al** _thth_ ahiru wa **a** lbatinu wahuwa bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (5079, 'Huwa allathee khalaqa a **l** ssamawati wa **a** larda fee sittati ayyamin thumma istawa \'ala al\'arshi ya\'lamu ma yaliju fee alardi wama yakhruju minha wama yanzilu mina a **l** ssamai wama ya\'ruju feeha wahuwa ma\'akum ayna ma kuntum wa **A** llahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (5080, 'Lahu mulku a **l** ssamawati wa **a** lardi waila Allahi turja\'u alomoor **u**');
INSERT INTO `en_transliteration` VALUES (5081, 'Yooliju allayla fee a **l** nnahari wayooliju a **l** nnahara fee allayi wahuwa \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (5082, 'Aminoo bi **A** llahi warasoolihi waanfiqoo mimma ja\'alakum mustakhlafeena feehi fa **a** llatheena amanoo minkum waanfaqoo lahum ajrun kabeer **un**');
INSERT INTO `en_transliteration` VALUES (5083, 'Wama lakum la tuminoona bi **A** llahi wa **al** rrasoolu yad\'ookum lituminoo birabbikum waqad akhatha meethaqakum in kuntum mumineen **a**');
INSERT INTO `en_transliteration` VALUES (5084, 'Huwa allathee yunazzilu \'ala \'abdihi ayatin bayyinatin liyukhrijakum mina a **l** _thth_ ulumati ila a **l** nnoori wainna Allaha bikum laraoofun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5085, 'Wama lakum alla tunfiqoo fee sabeeli Allahi walillahi meerathu a **l** ssamawati wa **a** lardi la yastawee minkum man anfaqa min qabli alfathi waqatala olaika a\' _th_ amu darajatan mina allatheena anfaqoo min ba\'du waqataloo wakullan wa\'ada Allahu alhusna wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (5086, 'Man tha allathee yuqridu Allaha qardan hasanan fayuda\'ifahu lahu walahu ajrun kareem **un**');
INSERT INTO `en_transliteration` VALUES (5087, 'Yawma tara almumineena wa **a** lmuminati yas\'a nooruhum bayna aydeehim wabiaymanihim bushrakumu alyawma jannatun tajree min tahtiha alanharu khalideena feehathalika huwa alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (5088, 'Yawma yaqoolu almunafiqoona wa **a** lmunafiqatu lillatheena amanoo on _th_ uroona naqtabis min noorikum qeela irji\'oo waraakum fa **i** ltamisoo nooran faduriba baynahum bisoorin lahu babun batinuhu feehi a **l** rrahmatu wa _th_ ahiruhu min qibalihi al\'athab **u**');
INSERT INTO `en_transliteration` VALUES (5089, 'Yunadoonahum alam nakun ma\'akum qaloo bala walakinnakum fatantum anfusakum watarabbastum wa **i** rtabtum wagharratkumu alamaniyyu hatta jaa amru Allahi wagharrakum bi **A** llahi algharoor **u**');
INSERT INTO `en_transliteration` VALUES (5090, 'Fa **a** lyawma la yukhathu minkum fidyatun wala mina allatheena kafaroo mawakumu a **l** nnaru hiya mawlakum wabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (5091, 'Alam yani lillatheena amanoo an takhsha\'a quloobuhum lithikri Allahi wama nazala mina alhaqqi wala yakoonoo ka **a** llatheena ootoo alkitaba min qablu fatala \'alayhimu alamadu faqasat quloobuhum wakatheerun minhum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (5092, 'I\'lamoo anna Allaha yuhyee alarda ba\'da mawtiha qad bayyanna lakumu alayati la\'allakum ta\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (5093, 'Inna almussaddiqeena wa **a** lmussaddiqati waaqradoo Allaha qardan hasanan yuda\'afu lahum walahum ajrun kareem **un**');
INSERT INTO `en_transliteration` VALUES (5094, 'Wa **a** llatheena amanoo bi **A** llahi warusulihi olaika humu a **l** ssiddeeqoona wa **al** shshuhadao \'inda rabbihim lahum ajruhum wanooruhum wa **a** llatheena kafaroo wakaththaboo biayatina olaika ashabu aljaheem **i**');
INSERT INTO `en_transliteration` VALUES (5095, 'I\'lamoo annama alhayatu a **l** ddunya la\'ibun walahwun wazeenatun watafakhurun baynakum watakathurun fee alamwali wa **a** lawladi kamathali ghaythin a\'jaba alkuffara nabatuhu thumma yaheeju fatarahu musfarran thumma yakoonu hutaman wafee alakhirati \'athabun shadeedun wamaghfiratun mina Allahi waridwanun wama alhayatu a **l** ddunya illa mata\'u alghuroor **i**');
INSERT INTO `en_transliteration` VALUES (5096, 'Sabiqoo ila maghfiratin min rabbikum wajannatin \'arduha ka\'ardi a **l** ssamai wa **a** lardi o\'iddat lillatheena amanoo bi **A** llahi warusulihi thalika fadlu Allahi yuteehi man yashao wa **A** llahu thoo alfadli al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (5097, 'Ma asaba min museebatin fee alardi wala fee anfusikum illa fee kitabin min qabli an nabraaha inna thalika \'ala Allahi yaseer **un**');
INSERT INTO `en_transliteration` VALUES (5098, 'Likayla tasaw \'ala ma fatakum wala tafrahoo bimaatakum wa **A** llahu la yuhibbu kulla mukhtalin fakhoor **in**');
INSERT INTO `en_transliteration` VALUES (5099, 'Allatheena yabkhaloona wayamuroona a **l** nnasa bi **a** lbukhli waman yatawalla fainna Allaha huwa alghaniyyu alhameed **u**');
INSERT INTO `en_transliteration` VALUES (5100, 'Laqad arsana rusulana bi **a** lbayyinati waanzana ma\'ahumu alkitaba wa **a** lmeezana liyaqooma a **l** nnasu bi **a** lqisti waanzana alhadeeda feehi basun shadeedun wamanafi\'u li **l** nnasi waliya\'lama Allahu man yansuruhu warusulahu bi **a** lghaybi inna Allaha qawiyyun \'azeez **un**');
INSERT INTO `en_transliteration` VALUES (5101, 'Walaqad arsana noohan waibraheema waja\'ana fee thurriyyatihima a **l** nnubuwwata wa **a** lkitaba faminhum muhtadin wakatheerun minhum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (5102, 'Thumma qaffayna \'alaatharihim birusulina waqaffayna bi\'eesa ibni maryama waataynahu alinjeela waja\'ana fee quloobi allatheena ittaba\'oohu rafatan warahmatan warahbaniyyatan ibtada\'ooha ma katabnaha \'alayhim illa ibtighaa ridwani Allahi fama ra\'awhahaqqa ri\'ayatiha faatayna allatheena amanoo minhum ajrahum wakatheerun minhum fasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (5103, 'Ya ayyuha allatheena amanoo ittaqoo Allaha waaminoo birasoolihi yutikum kiflayni min rahmatihi wayaj\'al lakum nooran tamshoona bihi wayaghfir lakum wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5104, 'Lialla ya\'lama ahlu alkitabi alla yaqdiroona \'ala shayin min fadli Allahi waanna alfadla biyadi Allahi yuteehi man yashao wa **A** llahu thoo alfadli al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (5105, 'Qad sami\'a Allahu qawla allatee tujadiluka fee zawjiha watashtakee ila Allahi wa **A** llahu yasma\'u tahawurakuma inna Allaha samee\'un baseer **un**');
INSERT INTO `en_transliteration` VALUES (5106, 'Allatheena yu _th_ ahiroona minkum min nisaihim ma hunna ommahatihim in ommahatuhum illa allaee waladnahum wainnahum layaqooloona munkaran mina alqawli wazooran wainna Allaha la\'afuwwun ghafoor **un**');
INSERT INTO `en_transliteration` VALUES (5107, 'Wa **a** llatheena yu _th_ ahiroona min nisaihim thumma ya\'oodoona lima qaloo fatahreeru raqabatin min qabli an yatamassathalikum too\'a _th_ oona bihi wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (5108, 'Faman lam yajid fasiyamu shahrayni mutatabi\'ayni min qabli an yatamassa faman lam yastati\' fait\'amu sitteena miskeenan thalika lituminoo bi **A** llahi warasoolihi watilka hudoodu Allahi walilkafireena \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (5109, 'Inna allatheena yuhaddoona Allaha warasoolahu kubitoo kama kubita allatheena min qablihim waqad anzanaayatin bayyinatin walilkafireena \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (5110, 'Yawma yab\'athuhumu Allahu jamee\'an fayunabbiohum bima \'amiloo ahsahu Allahu wanasoohu wa **A** llahu \'ala kulli shayin shaheed **un**');
INSERT INTO `en_transliteration` VALUES (5111, 'Alam tara anna Allaha ya\'lamu ma fee a **l** ssamawati wama fee alardi ma yakoonu min najwa thalathatin illa huwa rabi\'uhum wala khamsatin illa huwa sadisuhum wala adna min thalika wala akthara illa huwa ma\'ahum ayna ma kanoo thumma yunabbiohum bima \'amiloo yawma alqiyamati inna Allaha bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (5112, 'Alam tara ila allatheena nuhoo \'ani a **l** nnajwa thumma ya\'oodoona lima nuhoo \'anhu wayatanajawna bi **a** lithmi wa **a** l\'udwani wama\'siyati a **l** rrasooli waitha jaooka hayyawka bima lam yuhayyika bihi Allahu wayaqooloona fee anfusihim lawla yu\'aththibuna Allahu bima naqoolu hasbuhum jahannamu yaslawnaha fabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (5113, 'Ya ayyuha allatheena amanoo itha tanajaytum fala tatanajaw bi **a** lithmi wa **a** l\'udwani wama\'siyati a **l** rrasooli watanajaw bi **a** lbirri wa **al** ttaqwa wa **i** ttaqoo Allaha allathee ilayhi tuhsharoon **a**');
INSERT INTO `en_transliteration` VALUES (5114, 'Innama a **l** nnajwa mina a **l** shshaytani liyahzuna allatheena amanoo walaysa bidarrihim shayan illa biithni Allahi wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (5115, 'Ya ayyuha allatheena amanoo itha qeela lakum tafassahoo fee almajalisi fa **i** fsahoo yafsahi Allahu lakum waitha qeela onshuzoo fa **o** nshuzoo yarfa\'i Allahu allatheena amanoo minkum wa **a** llatheena ootoo al\'ilma darajatin wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (5116, 'Ya ayyuha allatheena amanoo itha najaytumu a **l** rrasoola faqaddimoo bayna yaday najwakum sadaqatan thalika khayrun lakum waatharu fain lam tajidoo fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5117, 'Aashfaqtum an tuqaddimoo bayna yaday najwakum sadaqatin faith lam taf\'aloo wataba Allahu \'alaykum faaqeemoo a **l** ssalata waatoo a **l** zzakata waatee\'oo Allaha warasoolahu wa **A** llahu khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5118, 'Alam tara ila allatheena tawallaw qawman ghadiba Allahu \'alayhim ma hum minkum wala minhum wayahlifoona \'ala alkathibi wahum ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (5119, 'a\'adda Allahu lahum \'athaban shadeedan innahum saa ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5120, 'Ittakhathoo aymanahum junnatan fasaddoo \'an sabeeli Allahi falahum \'athabun muheen **un**');
INSERT INTO `en_transliteration` VALUES (5121, 'Lan tughniya \'anhum amwaluhum wala awladuhum mina Allahi shayan olaika ashabu a **l** nnari hum feeha khalidoon **a**');
INSERT INTO `en_transliteration` VALUES (5122, 'Yawma yab\'athuhumu Allahu jamee\'an fayahlifoona lahu kama yahlifoona lakum wayahsaboona annahum \'ala shayin ala innahum humu alkathiboon **a**');
INSERT INTO `en_transliteration` VALUES (5123, 'Istahwatha \'alayhimu a **l** shshaytanu faansahum thikra Allahi olaika hizbu a **l** shshaytani ala inna hizba a **l** shshaytani humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (5124, 'Inna allatheena yuhaddoona Allaha warasoolahu olaika fee alathalleen **a**');
INSERT INTO `en_transliteration` VALUES (5125, 'Kataba Allahu laaghlibanna ana warusulee inna Allaha qawiyyun \'azeez **un**');
INSERT INTO `en_transliteration` VALUES (5126, 'La tajidu qawman yuminoona bi **A** llahi wa **a** lyawmi alakhiri yuwaddoona man hadda Allaha warasoolahu walaw kanoo abaahum aw abnaahum aw ikhwanahum aw \'asheeratahum olaika kataba fee quloobihimu aleemana waayyadahum biroohin minhu wayudkhiluhum jannatin tajree min tahtiha alanharu khalideena feeha radiya Allahu \'anhum waradoo \'anhu olaika hizbu Allahi ala inna hizba Allahi humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (5127, 'Sabbaha lillahi ma fee a **l** ssamawati wama fee alardi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5128, 'Huwa allathee akhraja allatheena kafaroo min ahli alkitabi min diyarihim liawwali alhashri ma _th_ anantum an yakhrujoo wa _th_ annoo annahum mani\'atuhum husoonuhum mina Allahi faatahumu Allahu min haythu lam yahtasiboo waqathafa fee quloobihimu a **l** rru\'ba yukhriboona buyootahum biaydeehim waaydee almumineena fa **i** \'tabiroo ya olee alabsar **i**');
INSERT INTO `en_transliteration` VALUES (5129, 'Walawla an kataba Allahu \'alayhimu aljalaa la\'aththabahum fee a **l** ddunya walahum fee alakhirati \'athabu a **l** nnar **i**');
INSERT INTO `en_transliteration` VALUES (5130, 'Thalika biannahum shaqqoo Allaha warasoolahu waman yushaqqi Allaha fainna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (5131, 'Ma qata\'tum min leenatin aw taraktumooha qaimatan \'ala osooliha fabiithni Allahi waliyukhziya alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (5132, 'Wama afaa Allahu \'ala rasoolihi minhum fama awjaftum \'alayhi min khaylin wala rikabin walakinna Allaha yusallitu rusulahu \'ala man yashao wa **A** llahu \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (5133, 'Ma afaa Allahu \'ala rasoolihi min ahli alqura falillahi wali **l** rrasooli walithee alqurba wa **a** lyatama wa **a** lmasakeeni wa **i** bni a **l** ssabeeli kay la yakoona doolatan bayna alaghniyai minkum wamaatakumu a **l** rrasoolu fakhuthoohu wama nahakum \'anhu fa **i** ntahoo wa **i** ttaqoo Allaha inna Allaha shadeedu al\'iqab **i**');
INSERT INTO `en_transliteration` VALUES (5134, 'Lilfuqarai almuhajireena allatheena okhrijoo min diyarihim waamwalihim yabtaghoona fadlan mina Allahi waridwanan wayansuroona Allaha warasoolahu olaika humu a **l** ssadiqoon **a**');
INSERT INTO `en_transliteration` VALUES (5135, 'Wa **a** llatheena tabawwaoo a **l** ddara wa **a** leemana min qablihim yuhibboona man hajara ilayhim wala yajidoona fee sudoorihim hajatan mimma ootoo wayuthiroona \'ala anfusihim walaw kana bihim khasasatun waman yooqa shuhha nafsihi faolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (5136, 'Wa **a** llatheena jaoo min ba\'dihim yaqooloona rabbana ighfir lana waliikhwanina allatheena sabaqoona bi **a** leemani wala taj\'al fee quloobina ghillan lillatheena amanoo rabbana innaka raoofun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5137, 'Alam tara ila allatheena nafaqoo yaqooloona liikhwanihimu allatheena kafaroo min ahli alkitabi lain okhrijtum lanakhrujanna ma\'akum wala nutee\'u feekum ahadan abadan wain qootiltum lanansurannakum wa **A** llahu yashhadu innahum lakathiboon **a**');
INSERT INTO `en_transliteration` VALUES (5138, 'Lain okhrijoo la yakhrujoona ma\'ahum walain qootiloo la yansuroonahum walain nasaroohum layuwallunna aladbara thumma la yunsaroon **a**');
INSERT INTO `en_transliteration` VALUES (5139, 'Laantum ashaddu rahbatan fee sudoorihim mina Allahi thalika biannahum qawmun la yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (5140, 'La yuqatiloonakum jamee\'an illa fee quran muhassanatin aw min warai judurin basuhum baynahum shadeedun tahsabuhum jamee\'an waquloobuhum shattathalika biannahum qawmun la ya\'qiloon **a**');
INSERT INTO `en_transliteration` VALUES (5141, 'Kamathali allatheena min qablihim qareeban thaqoo wabala amrihim walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (5142, 'Kamathali a **l** shshaytani ith qala lilinsani okfur falamma kafara qala innee bareeon minka innee akhafu Allaha rabba al\'alameen **a**');
INSERT INTO `en_transliteration` VALUES (5143, 'Fakana \'aqibatahuma annahuma fee a **l** nnari khalidayni feeha wathalika jazao a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (5144, 'Ya ayyuha allatheena amanoo ittaqoo Allaha watan _th_ ur nafsun ma qaddamat lighadin wa **i** ttaqoo Allaha inna Allaha khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5145, 'Wala takoonoo ka **a** llatheena nasoo Allaha faansahum anfusahum olaika humu alfasiqoon **a**');
INSERT INTO `en_transliteration` VALUES (5146, 'La yastawee ashabu a **l** nnari waashabu aljannati ashabu aljannati humu alfaizoon **a**');
INSERT INTO `en_transliteration` VALUES (5147, 'Law anzana hatha alqurana \'ala jabalin laraaytahu khashi\'an mutasaddi\'an min khashyati Allahi watilka alamthalu nadribuha li **l** nnasi la\'allahum yatafakkaroon **a**');
INSERT INTO `en_transliteration` VALUES (5148, 'Huwa Allahu allathee la ilaha illa huwa \'alimu alghaybi wa **al** shshahadati huwa a **l** rrahmanu a **l** rraheem **u**');
INSERT INTO `en_transliteration` VALUES (5149, 'Huwa Allahu allathee la ilaha illa huwa almaliku alquddoosu a **l** ssalamu almuminu almuhayminu al\'azeezu aljabbaru almutakabbiru subhana Allahi \'amma yushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (5150, 'Huwa Allahu alkhaliqu albario almusawwiru lahu alasmao alhusna yusabbihu lahu ma fee a **l** ssamawati wa **a** lardi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5151, 'Ya ayyuha allatheena amanoo la tattakhithoo \'aduwwee wa\'aduwwakum awliyaa tulqoona ilayhim bi **a** lmawaddati waqad kafaroo bima jaakum mina alhaqqi yukhrijoona a **l** rrasoola waiyyakum an tuminoo bi **A** llahi rabbikum in kuntum kharajtum jihadan fee sabeelee wa **i** btighaa mardatee tusirroona ilayhim bi **a** lmawaddati waana a\'lamu bima akhfaytum wama a\'lantum waman yaf\'alhu minkum faqad dalla sawaa a **l** ssabeel **i**');
INSERT INTO `en_transliteration` VALUES (5152, 'In yathqafookum yakoonoo lakam a\'daan wayabsutoo ilaykum aydiyahum waasinatahum bi **al** ssooi wawaddoo law takfuroon **a**');
INSERT INTO `en_transliteration` VALUES (5153, 'Lan tanfa\'akum arhamukum wala awladukum yawma alqiyamati yafsilu baynakum wa **A** llahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (5154, 'Qad kanat lakum oswatun hasanatun fee ibraheema wa **a** llatheena ma\'ahu ith qaloo liqawmihim inna buraao minkum wamimma ta\'budoona min dooni Allahi kafarna bikum wabada baynana wabaynakumu al\'adawatu wa **a** lbaghdao abadan hatta tuminoo bi **A** llahi wahdahu illa qawla ibraheema liabeehi laastaghfiranna laka wama amliku laka mina Allahi min shayin rabbana \'alayka tawakkana wailayka anabna wailayka almaseer **u**');
INSERT INTO `en_transliteration` VALUES (5155, 'Rabbana la taj\'ana fitnatan lillatheena kafaroo wa **i** ghfir lana rabbana innaka anta al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5156, 'Laqad kana lakum feehim oswatun hasanatun liman kana yarjoo Allaha wa **a** lyawma alakhira waman yatawalla fainna Allaha huwa alghanniyyu alhameed **u**');
INSERT INTO `en_transliteration` VALUES (5157, '\'asa Allahu an yaj\'ala baynakum wabayna allatheena \'adaytum minhum mawaddatan wa **A** llahu qadeerun wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5158, 'La yanhakumu Allahu \'ani allatheena lam yuqatilookum fee a **l** ddeeni walam yukhrijookum min diyarikum an tabarroohum watuqsitoo ilayhim inna Allaha yuhibbu almuqsiteen **a**');
INSERT INTO `en_transliteration` VALUES (5159, 'Innama yanhakumu Allahu \'ani allatheena qatalookum fee a **l** ddeeni waakhrajookum min diyarikum wa _th_ aharoo \'ala ikhrajikum an tawallawhum waman yatawallahum faolaika humu a **l** _thth_ alimoon **a**');
INSERT INTO `en_transliteration` VALUES (5160, 'Ya ayyuha allatheena amanoo itha jaakumu almuminatu muhajiratin fa **i** mtahinoohunna Allahu a\'lamu bieemanihinna fain \'alimtumoohunna muminatin fala tarji\'oohunna ila alkuffari la hunna hillun lahum wala hum yahilloona lahunna waatoohum ma anfaqoo wala junaha \'alaykum an tankihoohunna ithaataytumoohunna ojoorahunna wala tumsikoo bi\'isami alkawafiri wa **i** saloo ma anfaqtum walyasaloo ma anfaqoo thalikum hukmu Allahi yahkumu baynakum wa **A** llahu \'aleemun hakeem **un**');
INSERT INTO `en_transliteration` VALUES (5161, 'Wain fatakum shayon min azwajikum ila alkuffari fa\'aqabtum faatoo allatheena thahabat azwajuhum mithla ma anfaqoo wa **i** ttaqoo Allaha allathee antum bihi muminoon **a**');
INSERT INTO `en_transliteration` VALUES (5162, 'Ya ayyuha a **l** nnabiyyu itha jaaka almuminatu yubayi\'naka \'ala an la yushrikna bi **A** llahi shayan wala yasriqna wala yazneena wala yaqtulna awladahunna wala yateena bibuhtanin yaftareenahu bayna aydeehinna waarjulihinna wala ya\'seenaka fee ma\'roofin fabayi\'hunna wa **i** staghfir lahunna Allaha inna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5163, 'Ya ayyuha allatheena amanoo la tatawallaw qawman ghadiba Allahu \'alayhim qad yaisoo mina alakhirati kama yaisa alkuffaru min ashabi alquboor **i**');
INSERT INTO `en_transliteration` VALUES (5164, 'Sabbaha lillahi ma fee a **l** ssamawati wama fee alardi wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5165, 'Ya ayyuha allatheena amanoo lima taqooloona ma la taf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (5166, 'Kabura maqtan \'inda Allahi an taqooloo ma la taf\'aloon **a**');
INSERT INTO `en_transliteration` VALUES (5167, 'Inna Allaha yuhibbu allatheena yuqatiloona fee sabeelihi saffan kaannahum bunyanun marsoos **un**');
INSERT INTO `en_transliteration` VALUES (5168, 'Waith qala moosa liqawmihi ya qawmi lima tuthoonanee waqad ta\'lamoona annee rasoolu Allahi ilaykum falamma zaghoo azagha Allahu quloobahum wa **A** llahu la yahdee alqawma alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (5169, 'Waith qala \'eesa ibnu maryama ya banee israeela innee rasoolu Allahi ilaykum musaddiqan lima bayna yadayya mina a **l** ttawrati wamubashshiran birasoolin yatee min ba\'dee ismuhu ahmadu falamma jaahum bi **a** lbayyinati qaloo hatha sihrun mubeen **un**');
INSERT INTO `en_transliteration` VALUES (5170, 'Waman a _th_ lamu mimmani iftara \'ala Allahi alkathiba wahuwa yud\'a ila alislami wa **A** llahu la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (5171, 'Yureedoona liyutfioo noora Allahi biafwahihim wa **A** llahu mutimmu noorihi walaw kariha alkafiroon **a**');
INSERT INTO `en_transliteration` VALUES (5172, 'Huwa allathee arsala rasoolahu bi **a** lhuda wadeeni alhaqqi liyu _th_ hirahu \'ala a **l** ddeeni kullihi walaw kariha almushrikoon **a**');
INSERT INTO `en_transliteration` VALUES (5173, 'Ya ayyuha allatheena amanoo hal adullukum \'ala tijaratin tunjeekum min \'athabin aleem **in**');
INSERT INTO `en_transliteration` VALUES (5174, 'Tuminoona bi **A** llahi warasoolihi watujahidoona fee sabeeli Allahi biamwalikum waanfusikum thalikum khayrun lakum in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (5175, 'Yaghfir lakum thunoobakum wayudkhilkum jannatin tajree min tahtiha alanharu wamasakina tayyibatan fee jannati \'adnin thalika alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (5176, 'Waokhra tuhibboonaha nasrun mina Allahi wafathun qarreebun wabashshiri almumineen **a**');
INSERT INTO `en_transliteration` VALUES (5177, 'Ya ayyuha allatheena amanoo koonoo ansara Allahi kama qala \'eesa ibnu maryama lilhawariyyeena man ansaree ila Allahi qala alhawariyyoona nahnu ansaru Allahi faamanat taifatun min banee israeela wakafarat taifatun faayyadna allatheena amanoo \'ala \'aduwwihim faasbahoo _th_ ahireen **a**');
INSERT INTO `en_transliteration` VALUES (5178, 'Yusabbihu lillahi ma fee a **l** ssamawati wama fee alardi almaliki alquddoosi al\'azeezi alhakeem **i**');
INSERT INTO `en_transliteration` VALUES (5179, 'Huwa allathee ba\'atha fee alommiyyeena rasoolan minhum yatloo \'alayhim ayatihi wayuzakkeehim wayu\'allimuhumu alkitaba wa **a** lhikmata wain kanoo min qablu lafee dalalin mubeen **in**');
INSERT INTO `en_transliteration` VALUES (5180, 'Waakhareena minhum lamma yalhaqoo bihim wahuwa al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5181, 'Thalika fadlu Allahi yuteehi man yashao wa **A** llahu thoo alfadli al\'a _th_ eem **i**');
INSERT INTO `en_transliteration` VALUES (5182, 'Mathalu allatheena hummiloo a **l** ttawrata thumma lam yahmilooha kamathali alhimari yahmilu asfaran bisa mathalu alqawmi allatheena kaththaboo biayati Allahi wa **A** llahu la yahdee alqawma a **l** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (5183, 'Qul ya ayyuha allatheena hadoo in za\'amtum annakum awliyao lillahi min dooni a **l** nnasi fatamannawoo almawta in kuntum sadiqeen **a**');
INSERT INTO `en_transliteration` VALUES (5184, 'Wala yatamannawnahu abadan bima qaddamat aydeehim wa **A** llahu \'aleemun bi **al** _thth_ alimeen **a**');
INSERT INTO `en_transliteration` VALUES (5185, 'Qul inna almawta allathee tafirroona minhu fainnahu mulaqeekum thumma turaddoona ila \'alimi alghaybi wa **al** shshahadati fayunabbiokum bima kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5186, 'Ya ayyuha allatheena amanoo itha noodiya li **l** ssalati min yawmi aljumu\'ati fa **i** s\'aw ilathikri Allahi watharoo albay\'a thalikum khayrun lakum in kuntum ta\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (5187, 'Faitha qudiyati a **l** ssalatu fa **i** ntashiroo fee alardi wa **i** btaghoo min fadli Allahi wa **o** thkuroo Allaha katheeran la\'allakum tuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (5188, 'Waitha raaw tijaratan aw lahwan infaddoo ilayha watarakooka qaiman qul ma \'inda Allahi khayrun mina allahwi wamina a **l** ttijarati wa **A** llahu khayru a **l** rraziqeen **a**');
INSERT INTO `en_transliteration` VALUES (5189, 'Itha jaaka almunafiqoona qaloo nashhadu innaka larasoolu Allahi wa **A** llahu ya\'lamu innaka larasooluhu wa **A** llahu yashhadu inna almunafiqeena lakathiboon **a**');
INSERT INTO `en_transliteration` VALUES (5190, 'Ittakhathoo aymanahum junnatan fasaddoo \'an sabeeli Allahi innahum saa ma kanoo ya\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5191, 'Thalika biannahum amanoo thumma kafaroo fatubi\'a \'ala quloobihim fahum la yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (5192, 'Waitha raaytahum tu\'jibuka ajsamuhum wain yaqooloo tasma\' liqawlihim kaannahum khushubun musannadatun yahsaboona kulla sayhatin \'alayhim humu al\'aduwwu fa **i** htharhum qatalahumu Allahu anna yufakoon **a**');
INSERT INTO `en_transliteration` VALUES (5193, 'Waitha qeela lahum ta\'alaw yastaghfir lakum rasoolu Allahi lawwaw ruoosahum waraaytahum yasuddoona wahum mustakbiroon **a**');
INSERT INTO `en_transliteration` VALUES (5194, 'Sawaon \'alayhim astaghfarta lahum am lam tastaghfir lahum lan yaghfira Allahu lahum inna Allaha la yahdee alqawma alfasiqeen **a**');
INSERT INTO `en_transliteration` VALUES (5195, 'Humu allatheena yaqooloona la tunfiqoo \'ala man \'inda rasooli Allahi hatta yanfaddoo walillahi khazainu a **l** ssamawati wa **a** lardi walakinna almunafiqeena la yafqahoon **a**');
INSERT INTO `en_transliteration` VALUES (5196, 'Yaqooloona lain raja\'na ila almadeenati layukhrijanna ala\'azzu minha alathalla walillahi al\'izzatu walirasoolihi walilmumineena walakinna almunafiqeena la ya\'lamoon **a**');
INSERT INTO `en_transliteration` VALUES (5197, 'Ya ayyuha allatheena amanoo la tulhikum amwalukum wala awladukum \'an thikri Allahi waman yaf\'al thalika faolaika humu alkhasiroon **a**');
INSERT INTO `en_transliteration` VALUES (5198, 'Waanfiqoo min ma razaqnakum min qabli an yatiya ahadakumu almawtu fayaqoola rabbi lawla akhkhartanee ila ajalin qareebin faassaddaqa waakun mina a **l** ssaliheen **a**');
INSERT INTO `en_transliteration` VALUES (5199, 'Walan yuakhkhira Allahu nafsan itha jaa ajaluha wa **A** llahu khabeerun bima ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5200, 'Yusabbihu lillahi ma fee a **l** ssamawati wama fee alardi lahu almulku walahu alhamdu wahuwa \'ala kulli shayin qadeer **un**');
INSERT INTO `en_transliteration` VALUES (5201, 'Huwa allathee khalaqakum faminkum kafirun waminkum muminun wa **A** llahu bima ta\'maloona baseer **un**');
INSERT INTO `en_transliteration` VALUES (5202, 'Khalaqa a **l** ssamawati wa **a** larda bi **a** lhaqqi wasawwarakum faahsana suwarakum wailayhi almaseer **u**');
INSERT INTO `en_transliteration` VALUES (5203, 'Ya\'lamu ma fee a **l** ssamawati wa **a** lardi waya\'lamu ma tusirroona wama tu\'linoona wa **A** llahu \'aleemun bithati a **l** ssudoor **i**');
INSERT INTO `en_transliteration` VALUES (5204, 'Alam yatikum nabao allatheena kafaroo min qablu fathaqoo wabala amrihim walahum \'athabun aleem **un**');
INSERT INTO `en_transliteration` VALUES (5205, 'Thalika biannahu kanat tateehim rusuluhum bi **a** lbayyinati faqaloo abasharun yahdoonana fakafaroo watawallaw wa **i** staghna Allahu wa **A** llahu ghaniyyun hameed **un**');
INSERT INTO `en_transliteration` VALUES (5206, 'Za\'ama allatheena kafaroo an lan yub\'athoo qul bala warabbee latub\'athunna thumma latunabbaonna bima \'amiltum wathalika \'ala Allahi yaseer **un**');
INSERT INTO `en_transliteration` VALUES (5207, 'Faaminoo bi **A** llahi warasoolihi wa **al** nnoori allathee anzana wa **A** llahu bima ta\'maloona khabeer **un**');
INSERT INTO `en_transliteration` VALUES (5208, 'Yawma yajma\'ukum liyawmi aljam\'i thalika yawmu a **l** ttaghabuni waman yumin bi **A** llahi waya\'mal salihan yukaffir \'anhu sayyiatihi wayudkhilhu jannatin tajree min tahtiha alanharu khalideena feeha abadan thalika alfawzu al\'a _th_ eem **u**');
INSERT INTO `en_transliteration` VALUES (5209, 'Wa **a** llatheena kafaroo wakaththaboo biayatina olaika ashabu a **l** nnari khalideena feeha wabisa almaseer **u**');
INSERT INTO `en_transliteration` VALUES (5210, 'Ma asaba min museebatin illa biithni Allahi waman yumin bi **A** llahi yahdi qalbahu wa **A** llahu bikulli shayin \'aleem **un**');
INSERT INTO `en_transliteration` VALUES (5211, 'Waatee\'oo Allaha waatee\'oo a **l** rrasoola fain tawallaytum fainnama \'ala rasoolina albalaghu almubeen **u**');
INSERT INTO `en_transliteration` VALUES (5212, 'Allahu la ilaha illa huwa wa\'ala Allahi falyatawakkali almuminoon **a**');
INSERT INTO `en_transliteration` VALUES (5213, 'Ya ayyuha allatheena amanoo inna min azwajikum waawladikum \'aduwwan lakum fa **i** htharoohum wain ta\'foo watasfahoo wataghfiroo fainna Allaha ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5214, 'Innama amwalukum waawladukum fitnatun wa **A** llahu \'indahu ajrun \'a _th_ eem **un**');
INSERT INTO `en_transliteration` VALUES (5215, 'Fa **i** ttaqoo Allaha ma istata\'tum wa **i** sma\'oo waatee\'oo waanfiqoo khayran lianfusikum waman yooqa shuhha nafsihi faolaika humu almuflihoon **a**');
INSERT INTO `en_transliteration` VALUES (5216, 'In tuqridoo Allaha qardan hasanan yuda\'ifhu lakum wayaghfir lakum wa **A** llahu shakoorun haleem **un**');
INSERT INTO `en_transliteration` VALUES (5217, '\'alimu alghaybi wa **al** shshahadati al\'azeezu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5218, 'Ya ayyuha a **l** nnabiyyu ithatallaqtumu a **l** nnisaa fatalliqoohunna li\'iddatihinna waahsoo al\'iddata wa **i** ttaqoo Allaha rabbakum la tukhrijoohunna min buyootihinna wala yakhrujna illa an yateena bifahishatin mubayyinatin watilka hudoodu Allahi waman yata\'adda hudooda Allahi faqad _th_ alama nafsahu la tadree la\'alla Allaha yuhdithu ba\'da thalika amra **n**');
INSERT INTO `en_transliteration` VALUES (5219, 'Faitha balaghna ajalahunna faamsikoohunna bima\'roofin aw fariqoohunna bima\'roofin waashhidoo thaway \'adlin minkum waaqeemoo a **l** shshahadata lillahi thalikum yoo\'a _th_ u bihi man kana yuminu bi **A** llahi wa **a** lyawmi alakhiri waman yattaqi Allaha yaj\'al lahu makhraja **n**');
INSERT INTO `en_transliteration` VALUES (5220, 'Wayarzuqhu min haythu la yahtasibu waman yatawakkal \'ala Allahi fahuwa hasbuhu inna Allaha balighu amrihi qad ja\'ala Allahu likulli shayin qadra **n**');
INSERT INTO `en_transliteration` VALUES (5221, 'Wa **a** llaee yaisna mina almaheedi min nisaikum ini irtabtum fa\'iddatuhunna thalathatu ashhurin wa **a** llaee lam yahidna waolatu alahmali ajaluhunna an yada\'na hamlahunna waman yattaqi Allaha yaj\'al lahu min amrihi yusra **n**');
INSERT INTO `en_transliteration` VALUES (5222, 'Thalika amru Allahi anzalahu ilaykum waman yattaqi Allaha yukaffir \'anhu sayyiatihi wayu\' _th_ im lahu ajra **n**');
INSERT INTO `en_transliteration` VALUES (5223, 'Askinoohunna min haythu sakantum min wujdikum wala tudarroohunna litudayyiqoo \'alayhinna wain kunna olati hamlin faanfiqoo \'alayhinna hatta yada\'na hamlahunna fain arda\'na lakum faatoohunna ojoorahunna watamiroo baynakum bima\'roofin wain ta\'asartum fasaturdi\'u lahu okhra');
INSERT INTO `en_transliteration` VALUES (5224, 'Liyunfiq thoo sa\'atin min sa\'atihi waman qudira \'alayhi rizquhu falyunfiq mimmaatahu Allahu la yukallifu Allahu nafsan illa maataha sayaj\'alu Allahu ba\'da \'usrin yusra **n**');
INSERT INTO `en_transliteration` VALUES (5225, 'Wakaayyin min qaryatin \'atat \'an amri rabbiha warusulihi fahasabnahahisaban shadeedan wa\'aththabnaha \'athaban nukra **n**');
INSERT INTO `en_transliteration` VALUES (5226, 'Fathaqat wabala amriha wakana \'aqibatu amriha khusra **n**');
INSERT INTO `en_transliteration` VALUES (5227, 'a\'adda Allahu lahum \'athaban shadeedan fa **i** ttaqoo Allaha ya olee alalbabi allatheena amanoo qad anzala Allahu ilaykum thikra **n**');
INSERT INTO `en_transliteration` VALUES (5228, 'Rasoolan yatloo \'alaykum ayati Allahi mubayyinatin liyukhrija allatheena amanoo wa\'amiloo a **l** ssalihati mina a **l** _thth_ ulumati ila a **l** nnoori waman yumin bi **A** llahi waya\'mal salihan yudkhilhu jannatin tajree min tahtiha alanharu khalideena feeha abadan qad ahsana Allahu lahu rizqa **n**');
INSERT INTO `en_transliteration` VALUES (5229, 'Allahu allathee khalaqa sab\'a samawatin wamina alardi mithlahunna yatanazzalu alamru baynahunna lita\'lamoo anna Allaha \'ala kulli shayin qadeerun waanna Allaha qad ahata bikulli shayin \'ilma **n**');
INSERT INTO `en_transliteration` VALUES (5230, 'Ya ayyuha a **l** nnabiyyu lima tuharrimu ma ahalla Allahu laka tabtaghee mardata azwajika wa **A** llahu ghafoorun raheem **un**');
INSERT INTO `en_transliteration` VALUES (5231, 'Qad farada Allahu lakum tahillata aymanikum wa **A** llahu mawlakum wahuwa al\'aleemu alhakeem **u**');
INSERT INTO `en_transliteration` VALUES (5232, 'Waith asarra a **l** nnabiyyu ila ba\'di azwajihi hadeethan falamma nabbaat bihi waa _th_ harahu Allahu \'alayhi \'arrafa ba\'dahu waa\'rada \'an ba\'din falamma nabbaaha bihi qalat man anbaaka hatha qala nabbaaniya al\'aleemu alkhabeer **u**');
INSERT INTO `en_transliteration` VALUES (5233, 'In tatooba ila Allahi faqad saghat quloobukuma wain ta _th_ ahara \'alayhi fainna Allaha huwa mawlahu wajibreelu wasalihu almumineena wa **a** lmalaikatu ba\'da thalika _th_ aheer **un**');
INSERT INTO `en_transliteration` VALUES (5234, '\'asa rabbuhu in tallaqakunna an yubdilahu azwajan khayran minkunna muslimatin muminatin qanitatin taibatin \'abidatin saihatin thayyibatin waabkara **n**');
INSERT INTO `en_transliteration` VALUES (5235, 'Ya ayyuha allatheena amanoo qoo anfusakum waahleekum naran waqooduha a **l** nnasu wa **a** lhijaratu \'alayha malaikatun ghila _th_ un shidadun la ya\'soona Allaha ma amarahum wayaf\'aloona ma yumaroon **a**');
INSERT INTO `en_transliteration` VALUES (5236, 'Ya ayyuha allatheena kafaroo la ta\'tathiroo alyawma innama tujzawna ma kuntum ta\'maloon **a**');
INSERT INTO `en_transliteration` VALUES (5237, 'Ya ayyuha allatheena amanoo tooboo ila Allahi tawbatan nasoohan \'asa rabbukum an yukaffira \'ankum sayyiatikum wayudkhilakum jannatin tajree min tahtiha alanharu yawma la yukhzee Allahu a **l** nnabiyya wa **a** llatheena amanoo ma\'ahu nooruhum yas\'a bayna aydeehim wabiaymanihim yaqooloona rabbana atmim lana noorana wa **i** ghfir lana innaka \'ala kulli shayin qadeer **un**');
