INSERT INTO `tr_transliteration` VALUES (1, 'bismi-llâhi-rraḥmâni-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (2, 'elḥamdü lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3, 'erraḥmâni-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (4, 'mâliki yevmi-ddîn.');
INSERT INTO `tr_transliteration` VALUES (5, 'iyyâke na\\`büdü veiyyâke neste\\`în.');
INSERT INTO `tr_transliteration` VALUES (6, 'ihdine-ṣṣirâṭa-lmüsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (7, 'ṣirâṭa-lleẕîne en\\`amte \\`aleyhim gayri-lmagḍûbi \\`aleyhim vele-ḍḍâllîn.');
INSERT INTO `tr_transliteration` VALUES (8, 'elif-lâm-mîm.');
INSERT INTO `tr_transliteration` VALUES (9, 'ẕâlike-lkitâbü lâ raybe fîh. hüdel lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (10, 'elleẕîne yü\'minûne bilgaybi veyüḳîmûne-ṣṣalâte vemimmâ razaḳnâhüm yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (11, 'velleẕîne yü\'minûne bimâ ünzile ileyke vemâ ünzile min ḳablik. vebil\'âḫirati hüm yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (12, 'ülâike \\`alâ hüdem mir rabbihim veülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (13, 'inne-lleẕîne keferû sevâün \\`aleyhim eenẕertehüm em lem tünẕirhüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (14, 'ḫateme-llâhü \\`alâ ḳulûbihim ve\\`alâ sem\\`ihim. ve\\`alâ ebṣârihim gişâveh. velehüm \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (15, 'vemine-nnâsi mey yeḳûlü âmennâ billâhi vebilyevmi-l\'âḫiri vemâ hüm bimü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (16, 'yüḫâdi\\`ûne-llâhe velleẕîne âmenû. vemâ yaḫde\\`ûne illâ enfüsehüm vemâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (17, 'fî ḳulûbihim meraḍun fezâdehümü-llâhü meraḍâ. velehüm \\`aẕâbün elîmüm bimâ kânû yekẕibûn.');
INSERT INTO `tr_transliteration` VALUES (18, 'veiẕâ ḳîle lehüm lâ tüfsidû fi-l\'arḍi ḳâlû innemâ naḥnü muṣliḥûn.');
INSERT INTO `tr_transliteration` VALUES (19, 'elâ innehüm hümü-lmüfsidûne velâkil lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (20, 'veiẕâ ḳîle lehüm âminû kemâ âmene-nnâsü ḳâlû enü\'minü kemâ âmene-ssüfehâ\'. elâ innehüm hümü-ssüfehâü velâkil lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (21, 'veiẕâ leḳu-lleẕîne âmenû ḳâlû âmennâ. veiẕâ ḫalev ilâ şeyâṭînihim ḳâlû innâ me\\`aküm innemâ naḥnü müstehziûn.');
INSERT INTO `tr_transliteration` VALUES (22, 'allâhü yestehziü bihim veyemüddühüm fî ṭugyânihim ya\\`mehûn.');
INSERT INTO `tr_transliteration` VALUES (23, 'ülâike-lleẕîne-şteravu-ḍḍalâlete bilhüdâ. femâ rabiḥat ticâratühüm vemâ kânû mühtedîn.');
INSERT INTO `tr_transliteration` VALUES (24, 'meŝelühüm kemeŝeli-lleẕi-stevḳade nârâ. felemmâ eḍâet mâ ḥavlehû ẕehebe-llâhü binûrihim veterakehüm fî żulümâtil lâ yübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (25, 'ṣummüm bükmün \\`umyün fehüm lâ yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (26, 'ev keṣayyibim mine-ssemâi fîhi żulümâtüv vera\\`düv veberḳ. yec\\`alûne eṣâbi\\`ahüm fî âẕânihim mine-ṣṣavâ\\`iḳi ḥaẕera-lmevt. vellâhü müḥîṭum bilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (27, 'yekâdü-lberḳu yaḫṭafü ebṣârahüm. küllemâ eḍâe lehüm meşev fîhi veiẕâ ażleme \\`aleyhim ḳâmû. velev şâe-llâhü leẕehebe bisem\\`ihim veebṣârihim. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (28, 'yâ eyyühe-nnâsu-\\`büdû rabbekümü-lleẕî ḫaleḳaküm velleẕîne min ḳabliküm le\\`alleküm tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (29, 'elleẕî ce\\`ale lekümü-l\'arḍa firâşev vessemâe binaâ. veenzele mine-ssemâi mâen feaḫrace bihî mine-ŝŝemerâti rizḳal leküm. felâ tec\\`alû lillâhi endâdev veentüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (30, 'vein küntüm fî raybim mimmâ nezzelnâ \\`alâ \\`abdinâ fe\'tû bisûratim mim miŝlih. ved\\`û şühedâeküm min dûni-llâhi in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (31, 'feil lem tef\\`alû velen tef\\`alû fetteḳu-nnâra-lletî veḳûdühe-nnâsü velḥicârah. ü\\`iddet lilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (32, 'vebeşşiri-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti enne lehüm cennâtin tecrî min taḥtihe-l\'enhâr. küllemâ ruziḳû minhâ min ŝemeratir rizḳan ḳâlû hâẕe-lleẕî ruziḳnâ min ḳablü veütû bihî müteşâbihâ. velehüm fîhâ ezvâcüm müṭahherâtüv vehüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (33, 'inne-llâhe lâ yestaḥyî ey yaḍribe meŝelem mâ be\\`ûḍaten femâ fevḳahâ. feemme-lleẕîne âmenû feya\\`lemûne ennehü-lḥaḳḳu mir rabbihim. veemme-lleẕîne keferû feyeḳûlûne mâẕâ erâde-llâhü bihâẕâ meŝelâ. yüḍillü bihî keŝîrav veyehdî bihî keŝîrâ. vemâ yüḍillü bihî ille-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (34, 'elleẕîne yenḳuḍûne \\`ahde-llâhi mim ba\\`di mîŝâḳih. veyaḳṭa\\`ûne mâ emera-llâhü bihî ey yûṣale veyüfsidûne fi-l\'arḍ. ülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (35, 'keyfe tekfürûne billâhi veküntüm emvâten feaḥyâküm. ŝümme yümîtüküm ŝümme yuḥyîküm ŝümme ileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (36, 'hüve-lleẕî ḫaleḳa leküm mâ fi-l\'arḍi cemî\\`an ŝümme-stevâ ile-ssemâi fesevvâhünne seb\\`a semâvât. vehüve bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (37, 'veiẕ ḳâle rabbüke lilmelâiketi innî câ\\`ilün fi-l\'arḍi ḫalîfeh. ḳâlû etec\\`alü fîhâ mey yüfsidü fîhâ veyesfikü-ddimâ\'. venaḥnü nüsebbiḥu biḥamdike venüḳaddisü lek. ḳâle innî a\\`lemü mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (38, 've\\`alleme âdeme-l\'esmâe küllehâ ŝümme \\`araḍahüm \\`ale-lmelâiketi feḳâle embiûnî biesmâi hâülâi in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (39, 'ḳâlû sübḥâneke lâ \\`ilme lenâ illâ mâ \\`allemtenâ. inneke ente-l\\`alîmü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (40, 'ḳâle yâ âdemü embi\'hüm biesmâihim. felemmâ embeehüm biesmâihim ḳâle elem eḳul leküm innî a\\`lemü gaybe-ssemâvâti vel\'arḍi vea\\`lemü mâ tübdûne vemâ küntüm tektümûn.');
INSERT INTO `tr_transliteration` VALUES (41, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. ebâ vestekbera vekâne mine-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (42, 'veḳulnâ yâ âdemü-skün ente vezevcüke-lcennete vekülâ minhâ ragaden ḥayŝü şi\'tümâ. velâ taḳrabâ hâẕihi-şşecerate fetekûnâ mine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (43, 'feezellehüme-şşeyṭânü \\`anhâ feaḫracehümâ mimmâ kânâ fîh. veḳulne-hbiṭû ba\\`ḍuküm liba\\`ḍin \\`adüvv. veleküm fi-l\'arḍi müsteḳarruv vemetâ\\`un ilâ ḥîn.');
INSERT INTO `tr_transliteration` VALUES (44, 'feteleḳḳâ âdemü mir rabbihî kelimâtin fetâbe \\`aleyh. innehû hüve-ttevvâbü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (45, 'ḳulne-hbiṭû minhâ cemî\\`â. feimmâ ye\'tiyenneküm minnî hüden femen tebi\\`a hüdâye felâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (46, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (47, 'yâ benî isrâîle-ẕkürû ni\\`metiye-lletî en\\`amtü \\`aleyküm veevfû bi\\`ahdî ûfi bi\\`ahdiküm veiyyâye ferhebûn.');
INSERT INTO `tr_transliteration` VALUES (48, 'veâminû bimâ enzeltü müṣaddiḳal limâ me\\`aküm velâ tekûnû evvele kâfirim bih. velâ teşterû biâyâtî ŝemenen ḳalîlâ. veiyyâye fetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (49, 'velâ telbisü-lḥaḳḳa bilbâṭili vetektümü-lḥaḳḳa veentüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (50, 'veeḳîmu-ṣṣalâte veâtü-zzekâte verke\\`û me\\`a-rrâki\\`în.');
INSERT INTO `tr_transliteration` VALUES (51, 'ete\'mürûne-nnâse bilbirri vetensevne enfüseküm veentüm tetlûne-lkitâb. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (52, 'veste\\`înû biṣṣabri veṣṣalâh. veinnehâ lekebîratün illâ \\`ale-lḫâşi\\`în.');
INSERT INTO `tr_transliteration` VALUES (53, 'elleẕîne yeżunnûne ennehüm mülâḳû rabbihim veennehüm ileyhi râci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (54, 'yâ benî isrâîle-ẕkürû ni\\`metiye-lletî en\\`amtü \\`aleyküm veennî feḍḍaltüküm \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (55, 'vetteḳû yevmel lâ teczî nefsün \\`an nefsin şey\'ev velâ yuḳbelü minhâ şefâ\\`atüv velâ yü\'ḫaẕü minhâ \\`adlüv velâ hüm yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (56, 'veiẕ necceynâküm min âli fir\\`avne yesûmûneküm sûe-l\\`aẕâbi yüẕebbiḥûne ebnâeküm veyestaḥyûne nisâeküm. vefî ẕâliküm belâüm mir rabbiküm \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (57, 'veiẕ feraḳnâ bikümü-lbaḥra feenceynâküm veagraḳnâ âle fir\\`avne veentüm tenżurûn.');
INSERT INTO `tr_transliteration` VALUES (58, 'veiẕ vâ\\`adnâ mûsâ erbe\\`îne leyleten ŝümme-tteḫaẕtümü-l\\`icle mim ba\\`dihî veentüm żâlimûn.');
INSERT INTO `tr_transliteration` VALUES (59, 'ŝümme \\`afevnâ \\`anküm mim ba\\`di ẕâlike le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (60, 'veiẕ âteynâ mûse-lkitâbe velfürḳâne le\\`alleküm tehtedûn.');
INSERT INTO `tr_transliteration` VALUES (61, 'veiẕ ḳâle mûsâ liḳavmihî yâ ḳavmi inneküm żalemtüm enfüseküm bittiḫâẕikümü-l\\`icle fetûbû ilâ bâriiküm faḳtülû enfüseküm. ẕâliküm ḫayrul leküm \\`inde bâriiküm. fetâbe \\`aleyküm. innehû hüve-ttevvâbü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (62, 'veiẕ ḳultüm yâ mûsâ len nü\'mine leke ḥattâ nera-llâhe cehraten feeḫaẕetkümu-ṣṣâ\\`iḳatü veentüm tenżurûn.');
INSERT INTO `tr_transliteration` VALUES (63, 'ŝümme be\\`aŝnâküm mim ba\\`di mevtiküm le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (64, 'veżallelnâ \\`aleykümü-lgamâme veenzelnâ \\`aleykümü-lmenne vesselvâ. külû min ṭayyibâti mâ razaḳnâküm. vemâ żalemûnâ velâkin kânû enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (65, 'veiẕ ḳulne-dḫulû hâẕihi-lḳaryete fekülû minhâ ḥayŝü şi\'tüm ragadev vedḫulü-lbâbe süccedev veḳûlû ḥiṭṭatün nagfir leküm ḫaṭâyâküm. vesenezîdü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (66, 'febeddele-lleẕîne żalemû ḳavlen gayra-lleẕî ḳîle lehüm feenzelnâ \\`ale-lleẕîne żalemû riczem mine-ssemâi bimâ kânû yefsüḳûn.');
INSERT INTO `tr_transliteration` VALUES (67, 'veiẕi-stesḳâ mûsâ liḳavmihî feḳulne-ḍrib bi\\`aṣâke-lḥacer. fenfecerat minhü-ŝnetâ \\`aşrate \\`aynâ. ḳad \\`alime küllü ünâsim meşrabehüm. külû veşrabû mir rizḳi-llâhi velâ ta\\`ŝev fi-l\'arḍi müfsidîn.');
INSERT INTO `tr_transliteration` VALUES (68, 'veiẕ ḳultüm yâ mûsâ len naṣbira \\`alâ ṭa\\`âmiv vâḥidin fed\\`u lenâ rabbeke yuḫric lenâ mimmâ tümbitü-l\'arḍu mim baḳlihâ veḳiŝŝâihâ vefûmihâ ve\\`adesihâ vebeṣalihâ. ḳâle etestebdilûne-lleẕî hüve ednâ billeẕî hüve ḫayr. ihbiṭû miṣran feinne leküm mâ seeltüm. veḍuribet \\`aleyhimü-ẕẕilletü velmeskenetü vebâû bigaḍabim mine-llâh. ẕâlike biennehüm kânû yekfürûne biâyâti-llâhi veyaḳtülûne-nnebiyyîne bigayri-lḥaḳḳ. ẕâlike bimâ \\`aṣav vekânû ya\\`tedûn.');
INSERT INTO `tr_transliteration` VALUES (69, 'inne-lleẕîne âmenû velleẕîne hâdû venneṣârâ veṣṣâbi\'îne men âmene billâhi velyevmi-l\'âḫiri ve\\`amile ṣâliḥan felehüm ecruhüm \\`inde rabbihim. velâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (70, 'veiẕ eḫaẕnâ mîŝâḳaküm verafa\\`nâ fevḳakümu-ṭṭûr. ḫuẕû mâ âteynâküm biḳuvvetiv veẕkürû mâ fîhi le\\`alleküm tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (71, 'ŝümme tevelleytüm mim ba\\`di ẕâlik. felevlâ faḍlü-llâhi \\`aleyküm veraḥmetühû leküntüm mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (72, 'veleḳad \\`alimtümü-lleẕîne-\\`tedev minküm fi-ssebti feḳulnâ lehüm kûnû ḳiradeten ḫâsiîn.');
INSERT INTO `tr_transliteration` VALUES (73, 'fece\\`alnâhâ nekâlel limâ beyne yedeyhâ vemâ ḫalfehâ vemev\\`iżatel lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (74, 'veiẕ ḳâle mûsâ liḳavmihî inne-llâhe ye\'müruküm en teẕbeḥû beḳarah. ḳâlû etetteḫiẕünâ hüzüvâ. ḳâle e\\`ûẕü billâhi en ekûne mine-lcâhilîn.');
INSERT INTO `tr_transliteration` VALUES (75, 'ḳâlü-d\\`u lenâ rabbeke yübeyyil lenâ mâ hî. ḳâle innehû yeḳûlü innehâ beḳaratül lâ fâriḍuv velâ bikr. \\`avânüm beyne ẕâlik. fef\\`alû mâ tü\'merûn.');
INSERT INTO `tr_transliteration` VALUES (76, 'ḳâlü-d\\`u lenâ rabbeke yübeyyil lenâ mâ levnühâ. ḳâle innehû yeḳûlü innehâ beḳaratün ṣafrâü fâḳi\\`ul levnühâ tesürru-nnâżirîn.');
INSERT INTO `tr_transliteration` VALUES (77, 'ḳâlü-d\\`u lenâ rabbeke yübeyyil lenâ mâ hiye inne-lbeḳara teşâbehe \\`aleynâ. veinnâ in şâe-llâhü lemühtedûn.');
INSERT INTO `tr_transliteration` VALUES (78, 'ḳâle innehû yeḳûlü innehâ beḳaratül lâ ẕelûlün tüŝîru-l\'arḍa velâ tesḳi-lḥarŝ. müsellemetül lâ şiyete fîhâ. ḳâlü-l\'âne ci\'te bilḥaḳḳ. feẕebeḥûhâ vemâ kâdû yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (79, 'veiẕ ḳateltüm nefsen feddâra\'tüm fîhâ. vellâhü muḫricüm mâ küntüm tektümûn.');
INSERT INTO `tr_transliteration` VALUES (80, 'feḳulne-ḍribûhü biba\\`ḍihâ. keẕâlike yuḥyi-llâhü-lmevtâ veyürîküm âyâtihî le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (81, 'ŝümme ḳaset ḳulûbüküm mim ba\\`di ẕâlike fehiye kelḥicârati ev eşeddü ḳasveh. veinne mine-lḥicârati lemâ yetefecceru minhü-l\'enhâr. veinne minhâ lemâ yeşşeḳḳaḳu feyaḫrucü minhü-lmâ\'. veinne minhâ lemâ yehbiṭu min ḫaşyeti-llâh. veme-llâhü bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (82, 'efetaṭme\\`ûne ey yü\'minû leküm veḳad kâne ferîḳum minhüm yesme\\`ûne kelâme-llâhi ŝümme yüḥarrifûnehû mim ba\\`di mâ \\`aḳalûhü vehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (83, 'veiẕâ leḳu-lleẕîne âmenû ḳâlû âmennâ. veiẕâ ḫalâ ba\\`ḍuhüm ilâ ba\\`ḍin ḳâlû etüḥaddiŝûnehüm bimâ feteḥa-llâhü \\`aleyküm liyüḥâccûküm bihî \\`inde rabbiküm. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (84, 'evelâ ya\\`lemûne enne-llâhe ya\\`lemü mâ yüsirrûne vemâ yü\\`linûn.');
INSERT INTO `tr_transliteration` VALUES (85, 'veminhüm ümmiyyûne lâ ya\\`lemûne-lkitâbe illâ emâniyye vein hüm illâ yeżunnûn.');
INSERT INTO `tr_transliteration` VALUES (86, 'feveylül lilleẕîne yektübûne-lkitâbe bieydîhim ŝümme yeḳûlûne hâẕâ min \\`indi-llâhi liyeşterû bihî ŝemenen ḳalîlâ. feveylül lehüm mimmâ ketebet eydîhim veveylül lehüm mimmâ yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (87, 'veḳâlû len temessene-nnâru illâ eyyâmem ma\\`dûdeh. ḳul etteḫaẕtüm \\`inde-llâhi \\`ahden feley yuḫlife-llâhü \\`ahdehû em teḳûlûne \\`ale-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (88, 'belâ men kesebe seyyietev veeḥâṭat bihî ḫaṭîetühû feülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (89, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (90, 'veiẕ eḫaẕnâ mîŝâḳa benî isrâîle lâ ta\\`büdûne ille-llâhe vebilvâlideyni iḥsânev veẕi-lḳurbâ velyetâmâ velmesâkîni veḳûlû linnâsi ḥusnev veeḳîmu-ṣṣalâte veâtü-zzekâh. ŝümme tevelleytüm illâ ḳalîlem minküm veentüm mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (91, 'veiẕ eḫaẕnâ mîŝâḳaküm lâ tesfikûne dimâeküm velâ tuḫricûne enfüseküm min diyâriküm ŝümme aḳrartüm veentüm teşhedûn.');
INSERT INTO `tr_transliteration` VALUES (92, 'ŝümme entüm hâülâi taḳtülûne enfüseküm vetuḫricûne ferîḳam minküm min diyârihim. teżâherûne \\`aleyhim bil\'iŝmi vel\\`udvân. veiy ye\'tûküm üsârâ tüfâdûhüm vehüve müḥarramün \\`aleyküm iḫrâcühüm. efetü\'minûne biba\\`ḍi-lkitâbi vetekfürûne biba\\`ḍ. femâ cezâü mey yef\\`alü ẕâlike minküm illâ ḫizyün fi-lḥayâti-ddünyâ. veyevme-lḳiyâmeti yüraddûne ilâ eşeddi-l\\`aẕâb. veme-llâhü bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (93, 'ülâike-lleẕîne-şteravu-lḥayâte-ddünyâ bil\'âḫirati. felâ yüḫaffefü \\`anhümü-l\\`aẕâbü velâ hüm yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (94, 'veleḳad âteynâ mûse-lkitâbe veḳaffeynâ mim ba\\`dihî birrusüli veâteynâ \\`îse-bne meryeme-lbeyyinâti veeyyednâhü birûḥi-lḳudüs. efeküllemâ câeküm rasûlüm bimâ lâ tehvâ enfüsükümü-stekbertüm. feferîḳan keẕẕebtüm veferîḳan taḳtülûn.');
INSERT INTO `tr_transliteration` VALUES (95, 'veḳâlû ḳulûbünâ gulf. bel le\\`anehümü-llâhü biküfrihim feḳalîlem mâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (96, 'velemmâ câehüm kitâbüm min \\`indi-llâhi müṣaddiḳul limâ me\\`ahüm vekânû min ḳablü yesteftiḥûne \\`ale-lleẕîne keferû. felemmâ câehüm mâ \\`arafû keferû bih. fela\\`netü-llâhi \\`ale-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (97, 'bi\'seme-şterav bihî enfüsehüm ey yekfürû bimâ enzele-llâhü bagyen ey yünezzile-llâhü min faḍlihî \\`alâ mey yeşâü min \\`ibâdih. febâû bigaḍabin \\`alâ gaḍab. velilkâfirîne \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (98, 'veiẕâ ḳîle lehüm âminû bimâ enzele-llâhü ḳâlû nü\'minü bimâ ünzile \\`aleynâ veyekfürûne bimâ verâehû vehüve-lḥaḳḳu müṣaddiḳal limâ me\\`ahüm. ḳul felime taḳtülûne embiyâe-llâhi min ḳablü in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (99, 'veleḳad câeküm mûsâ bilbeyyinâti ŝümme-tteḫaẕtümü-l\\`icle mim ba\\`dihî veentüm żâlimûn.');
INSERT INTO `tr_transliteration` VALUES (100, 'veiẕ eḫaẕnâ mîŝâḳaküm verafa\\`nâ fevḳakümu-ṭṭûr. ḫuẕû mâ âteynâküm biḳuvvetiv vesme\\`û. ḳâlû semi\\`nâ ve\\`aṣaynâ veüşribû fî ḳulûbihimü-l\\`icle biküfrihim. ḳul bi\'semâ ye\'müruküm bihî îmânüküm in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (101, 'ḳul in kânet lekümü-ddâru-l\'âḫiratü \\`inde-llâhi ḫâliṣatem min dûni-nnâsi fetemennevu-lmevte in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (102, 'veley yetemennevhü ebedem bimâ ḳaddemet eydîhim. vellâhü \\`alîmüm biżżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (103, 'veletecidennehüm aḥraṣa-nnâsi \\`alâ ḥayâh. vemine-lleẕîne eşrakû yeveddü eḥadühüm lev yü\\`ammeru elfe seneh. vemâ hüve bimüzaḥziḥihî mine-l\\`aẕâbi ey yü\\`ammer. vellâhü beṣîrum bimâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (104, 'ḳul men kâne \\`adüvvel licibrîle feinnehû nezzelehû \\`alâ ḳalbike biiẕni-llâhi müṣaddiḳal limâ beyne yedeyhi vehüdev vebüşrâ lilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (105, 'men kâne \\`adüvvel lillâhi vemelâiketihî verusülihî vecibrîle vemîkâle feinne-llâhe \\`adüvvül lilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (106, 'veleḳad enzelnâ ileyke âyâtim beyyinât. vemâ yekfüru bihâ ille-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (107, 'eveküllemâ \\`âhedû \\`ahden nebeẕehû ferîḳum minhüm. bel ekŝeruhüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (108, 'velemmâ câehüm rasûlüm min \\`indi-llâhi müṣaddiḳul limâ me\\`ahüm nebeẕe ferîḳum mine-lleẕîne ûtü-lkitâb. kitâbe-llâhi verâe żuhûrihim keennehüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (109, 'vettebe\\`û mâ tetlü-şşeyâṭînü \\`alâ mülki süleymân. vemâ kefera süleymânü velâkinne-şşeyâṭîne keferû yü\\`allimûne-nnâse-ssiḥr. vemâ ünzile \\`ale-lmelekeyni bibâbile hârûte vemârût. vemâ yü\\`allimâni min eḥadin ḥattâ yeḳûlâ innemâ naḥnü fitnetün felâ tekfür. feyete\\`allemûne minhümâ mâ yüferriḳûne bihî beyne-lmer\'i vezevcih. vemâ hüm biḍârrîne bihî min eḥadin illâ biiẕni-llâh. veyete\\`allemûne mâ yeḍurruhüm velâ yenfe\\`uhüm. veleḳad \\`alimû lemeni-şterâhü mâ lehû fi-l\'âḫirati min ḫalâḳ. velebi\'se mâ şerav bihî enfüsehüm. lev kânû ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (110, 'velev ennehüm âmenû vetteḳav lemeŝûbetüm min \\`indi-llâhi ḫayr. lev kânû ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (111, 'yâ eyyühe-lleẕîne âmenû lâ teḳûlû râ\\`inâ veḳûlü-nẕürnâ vesme\\`û. velilkâfirîne \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (112, 'mâ yeveddü-lleẕîne keferû min ehli-lkitâbi vele-lmüşrikîne ey yünezzele \\`aleyküm min ḫayrim mir rabbiküm. vellâhü yaḫteṣṣu biraḥmetihî mey yeşâ\'. vellâhü ẕü-lfaḍli-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (113, 'mâ nensaḫ min âyetin ev nünsihâ ne\'ti biḫayrim minhâ ev miŝlihâ. elem ta\\`lem enne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (114, 'elem ta\\`lem enne-llâhe lehû mülkü-ssemâvâti vel\'arḍ. vemâ leküm min dûni-llâhi miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (115, 'em türîdûne en tes\'elû rasûleküm kemâ süile mûsâ min ḳabl. vemey yetebeddeli-lküfra bil\'îmâni feḳad ḍalle sevâe-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (116, 'vedde keŝîrum min ehli-lkitâbi lev yeruddûneküm mim ba\\`di îmâniküm küffârâ. ḥasedem min \\`indi enfüsihim mim ba\\`di mâ tebeyyene lehümü-lḥaḳḳ. fa\\`fû vaṣfeḥû ḥattâ ye\'tiye-llâhü biemrih. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (117, 'veeḳîmu-ṣṣalâte veâtü-zzekâh. vemâ tüḳaddimû lienfüsiküm min ḫayrin tecidûhü \\`inde-llâh. inne-llâhe bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (118, 'veḳâlû ley yedḫule-lcennete illâ men kâne hûden ev neṣârâ. tilke emâniyyühüm. ḳul hâtû bürhâneküm in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (119, 'belâ men esleme vechehû lillâhi vehüve muḥsinün felehû ecruhû \\`inde rabbih. velâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (120, 'veḳâleti-lyehûdü leyseti-nneṣârâ \\`alâ şey\'. veḳâleti-nneṣârâ leyseti-lyehûdü \\`alâ şey\'iv vehüm yetlûne-lkitâb. keẕâlike ḳâle-lleẕîne lâ ya\\`lemûne miŝle ḳavlihim. fellâhü yaḥkümü beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (121, 'vemen ażlemü mimmem mene\\`a mesâcide-llâhi ey yüẕkera fîhe-smühû vese\\`â fî ḫarâbihâ. ülâike mâ kâne lehüm ey yedḫulûhâ illâ ḫâifîn. lehüm fi-ddünyâ ḫizyüv velehüm fi-l\'âḫirati \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (122, 'velillâhi-lmeşriḳu velmagribü feeynemâ tüvellû feŝemme vechü-llâh. inne-llâhe vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (123, 'veḳâlü-tteḫaẕe-llâhü veleden sübḥâneh. bel lehû mâ fi-ssemâvâti vel\'arḍ. küllül lehû ḳânitûn.');
INSERT INTO `tr_transliteration` VALUES (124, 'bedî\\`u-ssemâvâti vel\'arḍ. veiẕâ ḳaḍâ emran feinnemâ yeḳûlü lehû kün feyekûn.');
INSERT INTO `tr_transliteration` VALUES (125, 'veḳâle-lleẕîne lâ ya\\`lemûne levlâ yükellimüne-llâhü ev te\'tînâ âyeh. keẕâlike ḳâle-lleẕîne min ḳablihim miŝle ḳavlihim. teşâbehet ḳulûbühüm. ḳad beyyenne-l\'âyâti liḳavmiy yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (126, 'innâ erselnâke bilḥaḳḳi beşîrav veneẕîrav velâ tüs\'elü \\`an aṣḥâbi-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (127, 'velen terḍâ \\`anke-lyehûdü vele-nneṣârâ ḥattâ tettebi\\`a milletehüm. ḳul inne hüde-llâhi hüve-lhüdâ. veleini-tteba\\`te ehvâehüm ba\\`de-lleẕî câeke mine-l\\`ilmi mâ leke mine-llâhi miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (128, 'elleẕîne âteynâhümü-lkitâbe yetlûnehû ḥaḳḳa tilâvetih. ülâike yü\'minûne bih. vemey yekfür bihî feülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (129, 'yâ benî isrâîle-ẕkürû ni\\`metiye-lletî en\\`amtü \\`aleyküm veennî feḍḍaltüküm \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (130, 'vetteḳû yevmel lâ teczî nefsün \\`an nefsin şey\'ev velâ yuḳbelü minhâ \\`adlüv velâ tenfe\\`uhâ şefâ\\`atüv velâ hüm yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (131, 'veiẕi-btelâ ibrâhîme rabbühû bikelimâtin feetemmehünn. ḳâle innî câ\\`ilüke linnâsi imâmâ. ḳâle vemin ẕürriyyetî. ḳâle lâ yenâlü \\`ahdi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (132, 'veiẕ ce\\`alne-lbeyte meŝâbetel linnâsi veemnâ. vetteḫiẕû mim meḳâmi ibrâhîme müṣallâ. ve\\`ahidnâ ilâ ibrâhîme veismâ\\`île en ṭahhirâ beytiye liṭṭâifîne vel\\`âkifîne verrukke\\`i-ssücûd.');
INSERT INTO `tr_transliteration` VALUES (133, 'veiẕ ḳâle ibrâhîmü rabbi-c\\`al hâẕâ beleden âminev verzuḳ ehlehû mine-ŝŝemerâti men âmene minhüm billâhi velyevmi-l\'âḫir. ḳâle vemen kefera feümetti\\`uhû ḳalîlen ŝümme aḍṭarruhû ilâ \\`aẕâbi-nnâr. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (134, 'veiẕ yerfe\\`u ibrâhîmü-lḳavâ\\`ide mine-lbeyti veismâ\\`îl. rabbenâ teḳabbel minnâ. inneke ente-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (135, 'rabbenâ vec\\`alnâ müslimeyni leke vemin ẕürriyyetinâ ümmetem müslimetel lek. veerinâ menâsikenâ vetüb \\`aleynâ. inneke ente-ttevvâbü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (136, 'rabbenâ veb\\`aŝ fîhim rasûlem minhüm yetlû \\`aleyhim âyâtike veyü\\`allimühümü-lkitâbe velḥikmete veyüzekkîhim. inneke ente-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (137, 'vemey yergabü \\`am milleti ibrâhîme illâ men sefihe nefseh. veleḳadi-ṣṭafeynâhü fi-ddünyâ. veinnehû fi-l\'âḫirati lemine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (138, 'iẕ ḳâle lehû rabbühû eslim ḳâle eslemtü lirabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (139, 'veveṣṣâ bihâ ibrâhîmü benîhi veya\\`ḳûb. yâ benîye inne-llâhe-ṣṭafâ lekümü-ddîne felâ temûtünne illâ veentüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (140, 'em küntüm şühedâe iẕ ḥaḍara ya\\`ḳûbe-lmevtü iẕ ḳâle libenîhi mâ ta\\`büdûne mim ba\\`dî. ḳâlû na\\`büdü ilâheke veilâhe âbâike ibrâhîme veismâ\\`île veisḥâḳa ilâhev vâḥidâ. venaḥnü lehû müslimûn.');
INSERT INTO `tr_transliteration` VALUES (141, 'tilke ümmetün ḳad ḫalet. lehâ mâ kesebet veleküm mâ kesebtüm. velâ tüs\'elûne \\`ammâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (142, 'veḳâlû kûnû hûden ev neṣârâ tehtedû. ḳul bel millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (143, 'ḳûlû âmennâ billâhi vemâ ünzile ileynâ vemâ ünzile ilâ ibrâhîme veismâ\\`île veisḥâḳa veya\\`ḳûbe vel\'esbâṭi vemâ ûtiye mûsâ ve\\`îsâ vemâ ûtiye-nnebiyyûne mir rabbihim. lâ nüferriḳu beyne eḥadim minhüm. venaḥnü lehû müslimûn.');
INSERT INTO `tr_transliteration` VALUES (144, 'fein âmenû bimiŝli mâ âmentüm bihî feḳadi-htedev. vein tevellev feinnemâ hüm fî şiḳâḳ. feseyekfîkehümü-llâh. vehüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (145, 'ṣibgate-llâh. vemen aḥsenü mine-llâhi ṣibgah. venaḥnü lehû \\`âbidûn.');
INSERT INTO `tr_transliteration` VALUES (146, 'ḳul etüḥâccûnenâ fi-llâhi vehüve rabbünâ verabbüküm. velenâ a\\`mâlünâ veleküm a\\`mâlüküm. venaḥnü lehû muḫliṣûn.');
INSERT INTO `tr_transliteration` VALUES (147, 'em teḳûlûne inne ibrâhîme veismâ\\`île veisḥâḳa veya\\`ḳûbe vel\'esbâṭa kânû hûden ev neṣârâ. ḳul eentüm a\\`lemü emi-llâh. vemen ażlemü mimmen keteme şehâdeten \\`indehû mine-llâh. veme-llâhü bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (148, 'tilke ümmetün ḳad ḫalet. lehâ mâ kesebet veleküm mâ kesebtüm. velâ tüs\'elûne \\`ammâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (149, 'seyeḳûlü-ssüfehâü mine-nnâsi mâ vellâhüm \\`an ḳibletihimü-lletî kânû \\`aleyhâ. ḳul lillâhi-lmeşriḳu velmagrib. yehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (150, 'vekeẕâlike ce\\`alnâküm ümmetev veseṭal litekûnû şühedâe \\`ale-nnâsi veyekûne-rrasûlü \\`aleyküm şehîdâ. vemâ ce\\`alne-lḳiblete-lletî künte \\`aleyhâ illâ lina\\`leme mey yettebi\\`u-rrasûle mimmey yenḳalibü \\`alâ \\`aḳibeyh. vein kânet lekebîraten illâ \\`ale-lleẕîne hede-llâh. vemâ kâne-llâhü liyüḍî\\`a îmâneküm. inne-llâhe binnâsi leraûfür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (151, 'ḳad nerâ teḳallübe vechike fi-ssemâ\'. felenüvelliyenneke ḳibleten terḍâhâ. fevelli vecheke şaṭra-lmescidi-lḥarâm. veḥayŝü mâ küntüm fevellû vucûheküm şaṭrah. veinne-lleẕîne ûtü-lkitâbe leya\\`lemûne ennehü-lḥaḳḳu mir rabbihim. veme-llâhü bigâfilin \\`ammâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (152, 'velein eteyte-lleẕîne ûtü-lkitâbe bikülli âyetim mâ tebi\\`û ḳibletek. vemâ ente bitâbi\\`in ḳibletehüm. vemâ ba\\`ḍuhüm bitâbi\\`in ḳiblete ba\\`ḍ. veleini-tteba\\`te ehvâehüm mim ba\\`di mâ câeke mine-l\\`ilmi inneke iẕel lemine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (153, 'elleẕîne âteynâhümü-lkitâbe ya\\`rifûnehû kemâ ya\\`rifûne ebnâehüm. veinne ferîḳam minhüm leyektümûne-lḥaḳḳa vehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (154, 'elḥaḳḳu mir rabbike felâ tekûnenne mine-lmümterîn.');
INSERT INTO `tr_transliteration` VALUES (155, 'velikülliv vichetün hüve müvellîhâ festebiḳu-lḫayrât. eyne mâ tekûnû ye\'ti bikümü-llâhü cemî\\`â. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (156, 'vemin ḥayŝü ḫaracte fevelli vecheke şaṭra-lmescidi-lḥarâm. veinnehû lelḥaḳḳu mir rabbik. veme-llâhü bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (157, 'vemin ḥayŝü ḫaracte fevelli vecheke şaṭra-lmescidi-lḥarâm. veḥayŝü mâ küntüm fevellû vucûheküm şaṭrahû liellâ yekûne linnâsi \\`aleyküm ḥucceh. ille-lleẕîne żalemû minhüm felâ taḫşevhüm vaḫşevnî veliütimme ni\\`metî \\`aleyküm vele\\`alleküm tehtedûn.');
INSERT INTO `tr_transliteration` VALUES (158, 'kemâ erselnâ fîküm rasûlem minküm yetlû \\`aleyküm âyâtinâ veyüzekkîküm veyü\\`allimükümü-lkitâbe velḥikmete veyü\\`allimüküm mâ lem tekûnû ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (159, 'feẕkürûnî eẕkürküm veşkürû lî velâ tekfürûn.');
INSERT INTO `tr_transliteration` VALUES (160, 'yâ eyyühe-lleẕîne âmenü-ste\\`înû biṣṣabri veṣṣalâh. inne-llâhe me\\`a-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (161, 'velâ teḳûlû limey yuḳtelü fî sebîli-llâhi emvâh. bel aḥyâüv velâkil lâ teş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (162, 'veleneblüvenneküm bişey\'im mine-lḫavfi velcû\\`i venaḳṣim mine-l\'emvâli vel\'enfüsi veŝŝemerât. vebeşşiri-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (163, 'elleẕîne iẕâ eṣâbethüm müṣîbetün ḳâlû innâ lillâhi veinnâ ileyhi râci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (164, 'ülâike \\`aleyhim ṣalevâtüm mir rabbihim veraḥmetüv veülâike hümü-lmühtedûn.');
INSERT INTO `tr_transliteration` VALUES (165, 'inne-ṣṣafâ velmervete min şe\\`âiri-llâh. femen ḥacce-lbeyte evi-\\`temera felâ cünâḥa \\`aleyhi ey yeṭṭavvefe bihimâ. vemen teṭavve\\`a ḫayran feinne-llâhe şâkirun \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (166, 'inne-lleẕîne yektümûne mâ enzelnâ mine-lbeyyinâti velhüdâ mim ba\\`di mâ beyyennâhü linnâsi fi-lkitâbi ülâike yel\\`anühümü-llâhü veyel\\`anühümü-llâ\\`inûn.');
INSERT INTO `tr_transliteration` VALUES (167, 'ille-lleẕîne tâbû veaṣleḥû vebeyyenû feülâike etûbü \\`aleyhim. veene-ttevvâbü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (168, 'inne-lleẕîne keferû vemâtû vehüm küffârun ülâike \\`aleyhim la\\`netü-llâhi velmelâiketi vennâsi ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (169, 'ḫâlidîne fîhâ. lâ yüḫaffefü \\`anhümü-l\\`aẕâbü velâ hüm yünżarûn.');
INSERT INTO `tr_transliteration` VALUES (170, 'veilâhüküm ilâhüv vâḥid. lâ ilâhe illâ hüve-rraḥmânü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (171, 'inne fî ḫalḳi-ssemâvâti vel\'arḍi vaḫtilâfi-lleyli vennehâri velfülki-lletî tecrî fi-lbaḥri bimâ yenfe\\`u-nnâse vemâ enzele-llâhü mine-ssemâi mim mâin feaḥyâ bihi-l\'arḍa ba\\`de mevtihâ vebeŝŝe fîhâ min külli dâbbeh. vetaṣrîfi-rriyâḥi vesseḥâbi-lmüseḫḫari beyne-ssemâi vel\'arḍi leâyâtil liḳavmiy ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (172, 'vemine-nnâsi mey yetteḫiẕü min dûni-llâhi endâdey yüḥibbûnehüm keḥubbi-llâh. velleẕîne âmenû eşeddü ḥubbel lillâh. velev yera-lleẕîne żalemû iẕ yeravne-l\\`aẕâbe enne-lḳuvvete lillâhi cemî\\`av veenne-llâhe şedîdü-l\\`aẕâb.');
INSERT INTO `tr_transliteration` VALUES (173, 'iẕ teberrae-lleẕîne-ttübi\\`û mine-lleẕîne-ttebe\\`û veraevü-l\\`aẕâbe veteḳaṭṭa\\`at bihimü-l\'esbâb.');
INSERT INTO `tr_transliteration` VALUES (174, 'veḳâle-lleẕîne-ttebe\\`û lev enne lenâ kerraten feneteberrae minhüm kemâ teberraû minnâ. keẕâlike yürîhimü-llâhü a\\`mâlehüm ḥaserâtin \\`aleyhim. vemâ hüm biḫâricîne mine-nnâr.');
INSERT INTO `tr_transliteration` VALUES (175, 'yâ eyyühe-nnâsü külû mimmâ fi-l\'arḍi ḥalâlen ṭayyibâ. velâ tettebi\\`û ḫuṭuvâti-şşeyṭân. innehû leküm \\`adüvvüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (176, 'innemâ ye\'müruküm bissûi velfaḥşâi veen teḳûlû \\`ale-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (177, 'veiẕâ ḳîle lehümü-ttebi\\`û mâ enzele-llâhü ḳâlû bel nettebi\\`u mâ elfeynâ \\`aleyhi âbâenâ. evelev kâne âbâühüm lâ ya\\`ḳilûne şey\'ev velâ yehtedûn.');
INSERT INTO `tr_transliteration` VALUES (178, 'vemeŝelü-lleẕîne keferû kemeŝeli-lleẕî yen\\`iḳu bimâ lâ yesme\\`u illâ dü\\`âev venidââ. ṣummüm bükmün \\`umyün fehüm lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (179, 'yâ eyyühe-lleẕîne âmenû külû min ṭayyibâti mâ razaḳnâküm veşkürû lillâhi in küntüm iyyâhü ta\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (180, 'innemâ ḥarrame \\`aleykümü-lmeytete veddeme velaḥme-lḫinzîri vemâ ühille bihî ligayri-llâh. femeni-ḍṭurra gayra bâgiv velâ \\`âdin felâ iŝme \\`aleyh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (181, 'inne-lleẕîne yektümûne mâ enzele-llâhü mine-lkitâbi veyeşterûne bihî ŝemenen ḳalîlen ülâike mâ ye\'külûne fî büṭûnihim ille-nnâra velâ yükellimühümü-llâhü yevme-lḳiyâmeti velâ yüzekkîhim. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (182, 'ülâike-lleẕîne-şteravu-ḍḍalâlete bilhüdâ vel\\`aẕâbe bilmagfirah. femâ aṣberahüm \\`ale-nnâr.');
INSERT INTO `tr_transliteration` VALUES (183, 'ẕâlike bienne-llâhe nezzele-lkitâbe bilḥaḳḳ. veinne-lleẕîne-ḫtelefû fi-lkitâbi lefî şiḳâḳum be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (184, 'leyse-lbirra en tüvellû vucûheküm ḳibele-lmeşriḳi velmagribi velâkinne-lbirra men âmene billâhi velyevmi-l\'âḫiri velmelâiketi velkitâbi vennebiyyîn. veâte-lmâle \\`alâ ḥubbihî ẕevi-lḳurbâ velyetâmâ velmesâkîne vebne-ssebîli vessâilîne vefi-rriḳâb. veeḳâme-ṣṣalâte veâte-zzekâh. velmûfûne bi\\`ahdihim iẕâ \\`âhedû. veṣṣâbirîne fi-lbe\'sâi veḍḍarrâi veḥîne-lbe\'s. ülâike-lleẕîne ṣadeḳû. veülâike hümü-lmütteḳûn.');
INSERT INTO `tr_transliteration` VALUES (185, 'yâ eyyühe-lleẕîne âmenû kütibe \\`aleykümü-lḳiṣâṣu fi-lḳatlâ. elḥurru bilḥurri vel\\`abdü bil\\`abdi vel\'ünŝâ bil\'ünŝâ. femen \\`ufiye lehû min eḫîhi şey\'ün fettibâ\\`um bilma\\`rûfi veedâün ileyhi biiḥsân. ẕâlike taḫfîfüm mir rabbiküm veraḥmetün. femeni-\\`tedâ ba\\`de ẕâlike felehû \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (186, 'veleküm fi-lḳiṣâṣi ḥayâtüy yâ ûli-l\'elbâbi le\\`alleküm tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (187, 'kütibe \\`aleyküm iẕâ ḥaḍara eḥadekümü-lmevtü in terake ḫayran. elveṣiyyetü lilvâlideyni vel\'aḳrabîne bilma\\`rûf. ḥaḳḳan \\`ale-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (188, 'femem beddelehû ba\\`de mâ semi\\`ahû feinnemâ iŝmühû \\`ale-lleẕîne yübeddilûneh. inne-llâhe semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (189, 'femen ḫâfe mim mûṣin cenefen ev iŝmen feaṣleḥa beynehüm felâ iŝme \\`aleyh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (190, 'yâ eyyühe-lleẕîne âmenû kütibe \\`aleykümu-ṣṣiyâmü kemâ kütibe \\`ale-lleẕîne min ḳabliküm le\\`alleküm tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (191, 'eyyâmem ma\\`dûdât. femen kâne minküm merîḍan ev \\`alâ seferin fe\\`iddetüm min eyyâmin üḫar. ve\\`ale-lleẕîne yüṭîḳûnehû fidyetün ṭa\\`âmü miskîn. femen teṭavve\\`a ḫayran fehüve ḫayrul leh. veen teṣûmû ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (192, 'şehru rameḍane-lleẕî ünzile fîhi-lḳur\'ânü hüdel linnâsi vebeyyinâtim mine-lhüdâ velfürḳân. femen şehide minkümü-şşehra felyeṣumh. vemen kâne merîḍan ev \\`alâ seferin fe\\`iddetüm min eyyâmin üḫar. yürîdü-llâhü bikümü-lyüsra velâ yürîdü bikümü-l\\`usr. velitükmilü-l\\`iddete velitükebbirü-llâhe \\`alâ mâ hedâküm vele\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (193, 'veiẕâ seeleke \\`ibâdî \\`annî feinnî ḳarîb. ücîbü da\\`vete-ddâ\\`i iẕâ de\\`âni felyestecîbû lî velyü\'minû bî le\\`allehüm yerşüdûn.');
INSERT INTO `tr_transliteration` VALUES (194, 'üḥille leküm leylete-ṣṣiyâmi-rrafeŝü ilâ nisâiküm. hünne libâsül leküm veentüm libâsül lehünn. \\`alime-llâhü enneküm küntüm taḫtânûne enfüseküm fetâbe \\`aleyküm ve\\`afâ \\`anküm. fel\'âne bâşirûhünne vebtegû mâ ketebe-llâhü leküm. vekülû veşrabû ḥattâ yetebeyyene lekümü-lḫayṭu-l\'ebyeḍu mine-lḫayṭi-l\'esvedi mine-lfecr. ŝümme etimmu-ṣṣiyâme ile-lleyl. velâ tübâşirûhünne veentüm \\`âkifûne fi-lmesâcid. tilke ḥudûdü-llâhi felâ taḳrabûhâ. keẕâlike yübeyyinü-llâhü âyâtihî linnâsi le\\`allehüm yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (195, 'velâ te\'külû emvâleküm beyneküm bilbâṭili vetüdlû bihâ ile-lḥukkâmi lite\'külû ferîḳam min emvâli-nnâsi bil\'iŝmi veentüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (196, 'yes\'elûneke \\`ani-l\'ehilleh. ḳul hiye mevâḳîtü linnâsi velḥacc. veleyse-lbirru bien te\'tü-lbüyûte min żuhûrihâ velâkinne-lbirra meni-tteḳâ. ve\'tü-lbüyûte min ebvâbihâ. vetteḳu-llâhe le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (197, 'veḳâtilû fî sebîli-llâhi-lleẕîne yüḳâtilûneküm velâ ta\\`tedû. inne-llâhe lâ yüḥibbü-lmü\\`tedîn.');
INSERT INTO `tr_transliteration` VALUES (198, 'vaḳtülûhüm ḥayŝü ŝeḳiftümûhüm veaḫricûhüm min ḥayŝü aḫracûküm velfitnetü eşeddü mine-lḳatl. velâ tüḳâtilûhüm \\`inde-lmescidi-lḥarâmi ḥattâ yüḳâtilûküm fîh. fein ḳâtelûküm faḳtülûhüm. keẕâlike cezâü-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (199, 'feini-ntehev feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (200, 'veḳâtilûhüm ḥattâ lâ tekûne fitnetüv veyekûne-ddînü lillâh. feini-ntehev felâ \\`udvâne illâ \\`ale-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (201, 'eşşehru-lḥarâmü bişşehri-lḥarâmi velḥurumâtü ḳiṣâṣ. femeni-\\`tedâ \\`aleyküm fa\\`tedû \\`aleyhi bimiŝli me-\\`tedâ \\`aleyküm. vetteḳu-llâhe va\\`lemû enne-llâhe me\\`a-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (202, 'veenfiḳû fî sebîli-llâhi velâ tülḳû bieydîküm ile-ttehlükeh. veaḥsinû. inne-llâhe yüḥibbü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (203, 'veetimmü-lḥacce vel\\`umrate lillâh. fein uḥṣirtüm feme-steysera mine-lhedy. velâ taḥliḳû ruûseküm ḥattâ yeblüga-lhedyü meḥilleh. femen kâne minküm merîḍan ev bihî eẕem mir ra\'sihî fefidyetüm min ṣiyâmin ev ṣadeḳatin ev nüsük. feiẕâ emintüm. femen temette\\`a bil\\`umrati ile-lḥacci feme-steysera mine-lhedy. femel lem yecid feṣiyâmü ŝelâŝeti eyyâmin fi-lḥacci veseb\\`atin iẕâ raca\\`tüm. tilke \\`aşeratün kâmileh. ẕâlike limel lem yekün ehlühû ḥâḍiri-lmescidi-lḥarâm. vetteḳu-llâhe va\\`lemû enne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (204, 'elḥaccü eşhürum ma\\`lûmâh. femen feraḍa fîhinne-lḥacce felâ rafeŝe velâ füsûḳa velâ cidâle fi-lḥacc. vemâ tef\\`alû min ḫayriy ya\\`lemhü-llâh. vetezevvedû feinne ḫayra-zzâdi-ttaḳvâ. vetteḳûni yâ ûli-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (205, 'leyse \\`aleyküm cünâḥun en tebtegû faḍlem mir rabbiküm. feiẕâ efaḍtüm min \\`arafâtin feẕkürü-llâhe \\`inde-lmeş\\`ari-lḥarâm. veẕkürûhü kemâ hedâküm. vein küntüm min ḳablihî lemine-ḍḍâllîn.');
INSERT INTO `tr_transliteration` VALUES (206, 'ŝümme efîḍû min ḥayŝü efâḍa-nnâsü vestagfirü-llâh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (207, 'feiẕâ ḳaḍaytüm menâsikeküm feẕkürü-llâhe keẕikriküm âbâeküm ev eşedde ẕikrâ. femine-nnâsi mey yeḳûlü rabbenâ âtinâ fi-ddünyâ vemâ lehû fi-l\'âḫirati min ḫalâḳ.');
INSERT INTO `tr_transliteration` VALUES (208, 'veminhüm mey yeḳûlü rabbenâ âtinâ fi-ddünyâ ḥasenetev vefi-l\'âḫirati ḥasenetev veḳinâ \\`aẕâbe-nnâr.');
INSERT INTO `tr_transliteration` VALUES (209, 'ülâike lehüm neṣîbüm mimmâ kesebû. vellâhü serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (210, 'veẕkürü-llâhe fî eyyâmim ma\\`dûdât. femen te\\`accele fî yevmeyni felâ iŝme \\`aleyh. vemen teeḫḫara felâ iŝme \\`aleyhi limeni-tteḳâ. vetteḳu-llâhe va\\`lemû enneküm ileyhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (211, 'vemine-nnâsi mey yü\\`cibüke ḳavlühû fi-lḥayâti-ddünyâ veyüşhidü-llâhe \\`alâ mâ fî ḳalbihî vehüve eleddü-lḫiṣâm.');
INSERT INTO `tr_transliteration` VALUES (212, 'veiẕâ tevellâ se\\`â fi-l\'arḍi liyüfside fîhâ veyühlike-lḥarŝe vennesl. vellâhü lâ yüḥibbü-lfesâd.');
INSERT INTO `tr_transliteration` VALUES (213, 'veiẕâ ḳîle lehü-tteḳi-llâhe eḫaẕethü-l\\`izzetü bil\'iŝmi feḥasbühû cehennem. velebi\'se-lmihâd.');
INSERT INTO `tr_transliteration` VALUES (214, 'vemine-nnâsi mey yeşrî nefsehü-btigâe merḍâti-llâh. vellâhü raûfüm bil\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (215, 'yâ eyyühe-lleẕîne âmenü-dḫulû fi-ssilmi kâffeten. velâ tettebi\\`û ḫuṭuvâti-şşeyṭân. innehû leküm \\`adüvvüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (216, 'fein zeleltüm mim ba\\`di mâ câetkümü-lbeyyinâtü fa\\`lemû enne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (217, 'hel yenżurûne illâ ey ye\'tiyehümü-llâhü fî żulelim mine-lgamâmi velmelâiketü veḳuḍiye-l\'emr. veile-llâhi türce\\`u-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (218, 'sel benî isrâîle kem âteynâhüm min âyetim beyyineh. vemey yübeddil ni\\`mete-llâhi mim ba\\`di mâ câethü feinne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (219, 'züyyine lilleẕîne keferü-lḥayâtü-ddünyâ veyesḫarûne mine-lleẕîne âmenû. velleẕîne-tteḳav fevḳahüm yevme-lḳiyâmeh. vellâhü yerzüḳu mey yeşâü bigayri ḥisâb.');
INSERT INTO `tr_transliteration` VALUES (220, 'kâne-nnâsü ümmetev vâḥideten febe\\`aŝe-llâhü-nnebiyyîne mübeşşirîne vemünẕirîn. veenzele me\\`ahümü-lkitâbe bilḥaḳḳi liyaḥküme beyne-nnâsi fîme-ḫtelefû fîh. veme-ḫtelefe fîhi ille-lleẕîne ûtûhü mim ba\\`di mâ câethümü-lbeyyinâtü bagyem beynehüm. fehede-llâhü-lleẕîne âmenû lime-ḫtelefû fîhi mine-lḥaḳḳi biiẕnih. vellâhü yehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (221, 'em ḥasibtüm en tedḫulü-lcennete velemmâ ye\'tiküm meŝelü-lleẕîne ḫalev min ḳabliküm. messethümü-lbe\'sâü veḍḍarrâü vezülzilû ḥattâ yeḳûle-rrasûlü velleẕîne âmenû me\\`ahû metâ naṣru-llâh. elâ inne naṣra-llâhi ḳarîb.');
INSERT INTO `tr_transliteration` VALUES (222, 'yes\'elûneke mâẕâ yünfiḳûn. ḳul mâ enfaḳtüm min ḫayrin felilvâlideyni vel\'aḳrabîne velyetâmâ velmesâkîni vebni-ssebîl. vemâ tef\\`alû min ḫayrin feinne-llâhe bihî \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (223, 'kütibe \\`aleykümü-lḳitâlü vehüve kürhül leküm. ve\\`asâ en tekrahû şey\'ev vehüve ḫayrul leküm. ve\\`asâ en tüḥibbû şey\'ev vehüve şerrul leküm. vellâhü ya\\`lemü veentüm lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (224, 'yes\'elûneke \\`ani-şşehri-lḥarâmi ḳitâlin fîh. ḳul ḳitâlün fîhi kebîr. veṣaddün \\`an sebîli-llâhi veküfrum bihî velmescidi-lḥarâmi veiḫrâcü ehlihî minhü ekberu \\`inde-llâh. velfitnetü ekberu mine-lḳatl. velâ yezâlûne yüḳâtilûneküm ḥattâ yeruddûküm \\`an dîniküm ini-steṭâ\\`û. vemey yertedid minküm \\`an dînihî feyemüt vehüve kâfirun feülâike ḥabiṭat a\\`mâlühüm fi-ddünyâ vel\'âḫirah. veülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (225, 'inne-lleẕîne âmenû velleẕîne hâcerû vecâhedû fî sebîli-llâhi ülâike yercûne raḥmete-llâh. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (226, 'yes\'elûneke \\`ani-lḫamri velmeysir. ḳul fîhimâ iŝmün kebîruv vemenâfi\\`u linnâs. veiŝmühümâ ekberu min nef\\`ihimâ. veyes\'elûneke mâẕâ yünfiḳûn. ḳuli-l\\`afv. keẕâlike yübeyyinü-llâhü lekümü-l\'âyâti le\\`alleküm tetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (227, 'fi-ddünyâ vel\'âḫirah. veyes\'elûneke \\`ani-lyetâmâ. ḳul iṣlâḥul lehüm ḫayr. vein tüḫâliṭûhüm feiḫvânüküm. vellâhü ya\\`lemü-lmüfside mine-lmuṣliḥ. velev şâe-llâhü lea\\`neteküm. inne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (228, 'velâ tenkiḥü-lmüşrikâti ḥattâ yü\'minn. veleemetüm mü\'minetün ḫayrum mim müşriketiv velev a\\`cebetküm. velâ tünkiḥü-lmüşrikîne ḥattâ yü\'minû. vele\\`abdüm mü\'minün ḫayrum mim müşrikiv velev a\\`cebeküm. ülâike yed\\`ûne ile-nnâr. vellâhü yed\\`û ile-lcenneti velmagfirati biiẕnih. veyübeyyinü âyâtihî linnâsi le\\`allehüm yeteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (229, 'veyes\'elûneke \\`ani-lmeḥîḍ. ḳul hüve eẕen fa\\`tezilü-nnisâe fi-lmeḥîḍi velâ taḳrabûhünne ḥattâ yaṭhürn. feiẕâ teṭahherne fe\'tûhünne min ḥayŝü emerakümü-llâh. inne-llâhe yüḥibbü-ttevvâbîne veyüḥibbü-lmüteṭahhirîn.');
INSERT INTO `tr_transliteration` VALUES (230, 'nisâüküm ḥarŝül leküm. fe\'tû ḥarŝeküm ennâ şi\'tüm. veḳaddimû lienfüsiküm. vetteḳu-llâhe va\\`lemû enneküm mülâḳûh. vebeşşiri-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (231, 'velâ tec\\`alü-llâhe \\`urḍatel lieymâniküm en teberrû vetetteḳû vetuṣliḥû beyne-nnâs. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (232, 'lâ yüâḫiẕükümü-llâhü billagvi fî eymâniküm velâkiy yüâḫiẕüküm bimâ kesebet ḳulûbüküm. vellâhü gafûrun ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (233, 'lilleẕîne yü\'lûne min nisâihim terabbüṣu erbe\\`ati eşhür. fein fâû feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (234, 'vein \\`azemu-ṭṭalâḳa feinne-llâhe semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (235, 'velmüṭalleḳâtü yeterabbaṣne bienfüsihinne ŝelâŝete ḳurû\'. velâ yeḥillü lehünne ey yektümne mâ ḫaleḳa-llâhü fî erḥâmihinne in künne yü\'minne billâhi velyevmi-l\'âḫir. vebü\\`ûletühünne eḥaḳḳu biraddihinne fî ẕâlike in erâdû iṣlâḥâ. velehünne miŝlü-lleẕî \\`aleyhinne bilma\\`rûf. velirricâli \\`aleyhinne deraceh. vellâhü \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (236, 'eṭṭalâḳu merratân. feimsâküm bima\\`rûfin ev tesrîḥum biiḥsân. velâ yeḥillü leküm en te\'ḫuẕû mimmâ âteytümûhünne şey\'en illâ ey yeḫâfâ ellâ yüḳîmâ ḥudûde-llâh. fein ḫiftüm ellâ yüḳîmâ ḥudûde-llâhi felâ cünâḥa \\`aleyhimâ fîme-ftedet bih. tilke ḥudûdü-llâhi felâ ta\\`tedûhâ. vemey yete\\`adde ḥudûde-llâhi feülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (237, 'fein ṭalleḳahâ felâ teḥillü lehû mim ba\\`dü ḥattâ tenkiḥa zevcen gayrah. fein ṭalleḳahâ felâ cünâḥa \\`aleyhimâ ey yeterâce\\`â in żannâ ey yüḳîmâ ḥudûde-llâh. vetilke ḥudûdü-llâhi yübeyyinühâ liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (238, 'veiẕâ ṭallaḳtümü-nnisâe febelagne ecelehünne feemsikûhünne bima\\`rûfin ev serriḥûhünne bima\\`rûf. velâ tümsikûhünne ḍirâral lita\\`tedû. vemey yef\\`al ẕâlike feḳad żaleme nefseh. velâ tetteḫiẕû âyâti-llâhi hüzüvâ. veẕkürû ni\\`mete-llâhi \\`aleyküm vemâ enzele \\`aleyküm mine-lkitâbi velḥikmeti ye\\`iżuküm bih. vetteḳu-llâhe va\\`lemû enne-llâhe bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (239, 'veiẕâ ṭallaḳtümü-nnisâe febelagne ecelehünne felâ ta\\`ḍulûhünne ey yenkiḥne ezvâcehünne iẕâ terâḍav beynehüm bilma\\`rûf. ẕâlike yû\\`ażu bihî men kâne minküm yü\'minü billâhi velyevmi-l\'âḫir. ẕâliküm ezkâ leküm veaṭher. vellâhü ya\\`lemü veentüm lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (240, 'velvâlidâtü yürḍi\\`ne evlâdehünne ḥavleyni kâmileyni limen erâde ey yütimme-rraḍâ\\`ah. ve\\`ale-lmevlûdi lehû rizḳuhünne vekisvetühünne bilma\\`rûf. lâ tükellefü nefsün illâ vus\\`ahâ. lâ tüḍârra vâlidetüm biveledihâ velâ mevlûdül lehû biveledihî ve\\`ale-lvâriŝi miŝlü ẕâlik. fein erâdâ fiṣâlen \\`an terâḍim minhümâ veteşâvurin felâ cünâḥa \\`aleyhimâ. vein erattüm en testerḍi\\`û evlâdeküm felâ cünâḥa \\`aleyküm iẕâ sellemtüm mâ âteytüm bilma\\`rûf. vetteḳu-llâhe va\\`lemû enne-llâhe bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (241, 'velleẕîne yüteveffevne minküm veyeẕerûne ezvâcey yeterabbaṣne bienfüsihinne erbe\\`ate eşhüriv ve\\`aşrâ. feiẕâ belagne ecelehünne felâ cünâḥa \\`aleyküm fîmâ fe\\`alne fî enfüsihinne bilma\\`rûf. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (242, 'velâ cünâḥa \\`aleyküm fîmâ \\`arraḍtüm bihî min ḫiṭbeti-nnisâi ev eknentüm fî enfüsiküm. \\`alime-llâhü enneküm seteẕkürûnehünne velâkil lâ tüvâ\\`idûhünne sirran illâ en teḳûlû ḳavlem ma\\`rûfâ. velâ ta\\`zimû \\`uḳdete-nnikâḥi ḥattâ yeblüga-lkitâbü eceleh. va\\`lemû enne-llâhe ya\\`lemü mâ fî enfüsiküm faḥẕerûh. va\\`lemû enne-llâhe gafûrun ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (243, 'lâ cünâḥa \\`aleyküm in ṭallaḳtümü-nnisâe mâ lem temessûhünne ev tefriḍû lehünne ferîḍah. vemetti\\`ûhünn. \\`ale-lmûsi\\`i ḳaderuhû ve\\`ale-lmuḳtiri ḳaderuhü. metâ\\`am bilma\\`rûf. ḥaḳḳan \\`ale-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (244, 'vein ṭallaḳtümûhünne min ḳabli en temessûhünne veḳad feraḍtüm lehünne ferîḍaten feniṣfü mâ feraḍtüm illâ ey ya\\`fûne ev ya\\`füve-lleẕî biyedihî \\`uḳdetü-nnikâḥ. veen ta\\`fû aḳrabü littaḳvâ. velâ tensevu-lfaḍle beyneküm. inne-llâhe bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (245, 'ḥâfiżû \\`ale-ṣṣalâvâti veṣṣalâti-lvusṭâ veḳûmû lillâhi ḳânitîn.');
INSERT INTO `tr_transliteration` VALUES (246, 'fein ḫiftüm fericâlen ev rukbânâ. feiẕâ emintüm feẕkürü-llâhe kemâ \\`allemeküm mâ lem tekûnû ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (247, 'velleẕîne yüteveffevne minküm veyeẕerûne ezvâcâ. veṣiyyetel liezvâcihim metâ\\`an ile-lḥavli gayra iḫrâc. fein ḫaracne felâ cünâḥa \\`aleyküm fî mâ fe\\`alne fî enfüsihinne mim ma\\`rûf. vellâhü \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (248, 'velilmüṭalleḳâti metâ\\`um bilma\\`rûf. ḥaḳḳan \\`ale-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (249, 'keẕâlike yübeyyinü-llâhü leküm âyâtihî le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (250, 'elem tera ile-lleẕîne ḫaracû min diyârihim vehüm ülûfün ḥaẕera-lmevt. feḳâle lehümü-llâhü mûtû ŝümme aḥyâhüm. inne-llâhe leẕû faḍlin \\`ale-nnâsi velâkinne ekŝera-nnâsi lâ yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (251, 'veḳâtilû fî sebîli-llâhi va\\`lemû enne-llâhe semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (252, 'men ẕe-lleẕî yuḳriḍu-llâhe ḳarḍan ḥasenen feyüḍâ\\`ifehû lehû aḍ\\`âfen keŝîrah. vellâhü yaḳbiḍu veyebsüṭ. veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (253, 'elem tera ile-lmelei mim benî isrâîle mim ba\\`di mûsâ. iẕ ḳâlû linebiyyil lehümü-b\\`aŝ lenâ meliken nüḳâtil fî sebîli-llâh. ḳâle hel \\`aseytüm in kütibe \\`aleykümü-lḳitâlü ellâ tüḳâtilû. ḳâlû vemâ lenâ ellâ nüḳâtile fî sebîli-llâhi veḳad uḫricnâ min diyârinâ veebnâinâ. felemmâ kütibe \\`aleyhimü-lḳitâlü tevellev illâ ḳalîlem minhüm. vellâhü \\`alîmüm biżżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (254, 'veḳâle lehüm nebiyyühüm inne-llâhe ḳad be\\`aŝe leküm ṭâlûte melikâ. ḳâlû ennâ yekûnü lehü-lmülkü \\`aleynâ venaḥnü eḥaḳḳu bilmülki minhü velem yü\'te se\\`atem mine-lmâl. ḳâle inne-llâhe-ṣṭafâhü \\`aleyküm vezâdehû besṭaten fi-l\\`ilmi velcism. vellâhü yü\'tî mülkehû mey yeşâ\'. vellâhü vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (255, 'veḳâle lehüm nebiyyühüm inne âyete mülkihî ey ye\'tiyekümü-ttâbûtü fîhi sekînetüm mir rabbiküm vebeḳiyyetüm mimmâ terake âlü mûsâ veâlü hârûne taḥmilühü-lmelâikeh. inne fî ẕâlike leâyetel leküm in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (256, 'felemmâ feṣale ṭâlûtü bilcünûdi ḳâle inne-llâhe mübtelîküm bineher. femen şeribe minhü feleyse minnî. vemel lem yaṭ\\`amhü feinnehû minnî illâ meni-gterafe gurfetem biyedih. feşeribû minhü illâ ḳalîlem minhüm. felemmâ câvezehû hüve velleẕîne âmenû me\\`ahû ḳâlû lâ ṭâḳate lene-lyevme bicâlûte vecünûdih. ḳâle-lleẕîne yeżunnûne ennehüm mülâḳu-llâhi kem min fietin ḳalîletin galebet fieten keŝîratem biiẕni-llâh. vellâhü me\\`a-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (257, 'velemmâ berazû licâlûte vecünûdihî ḳâlû rabbenâ efrig \\`aleynâ ṣabrav veŝebbit aḳdâmenâ venṣurnâ \\`ale-lḳavmi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (258, 'fehezemûhüm biiẕni-llâhi veḳatele dâvûdü câlûte veâtâhü-llâhü-lmülke velḥikmete ve\\`allemehû mimmâ yeşâ\'. velevlâ def\\`u-llâhi-nnâse ba\\`ḍahüm biba\\`ḍil lefesedeti-l\'arḍu velâkinne-llâhe ẕû faḍlin \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (259, 'tilke âyâtü-llâhi netlûhâ \\`aleyke bilḥaḳḳ. veinneke lemine-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (260, 'tilke-rrusülü feḍḍalnâ ba\\`ḍahüm \\`alâ ba\\`ḍ. minhüm men kelleme-llâhü verafe\\`a ba\\`ḍahüm deracât. veâteynâ \\`îse-bne meryeme-lbeyyinâti veeyyednâhü birûḥi-lḳudüs. velev şâe-llâhü me-ḳtetele-lleẕîne mim ba\\`dihim mim ba\\`di mâ câethümü-lbeyyinâtü velâkini-ḫtelefû feminhüm men âmene veminhüm men kefer. velev şâe-llâhü me-ḳtetelû velâkinne-llâhe yef\\`alü mâ yürîd.');
INSERT INTO `tr_transliteration` VALUES (261, 'yâ eyyühe-lleẕîne âmenû enfiḳû mimmâ razaḳnâküm min ḳabli ey ye\'tiye yevmül lâ bey\\`un fîhi velâ ḫulletüv velâ şefâ\\`ah. velkâfirûne hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (262, 'allâhü lâ ilâhe illâ hû. elḥayyü-lḳayyûm. lâ te\'ḫuẕühû sinetüv velâ nevm. lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. men ẕe-lleẕî yeşfe\\`u \\`indehû illâ biiẕnih. ya\\`lemü mâ beyne eydîhim vemâ ḫalfehüm. velâ yüḥîṭûne bişey\'im min \\`ilmihî illâ bimâ şâ\'. vesi\\`a kürsiyyühü-ssemâvâti vel\'arḍ. velâ yeûdühû ḥifżuhümâ. vehüve-l\\`aliyyü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (263, 'lâ ikrâhe fi-ddîni ḳat tebeyyene-rruşdü mine-lgayy. femey yekfür biṭṭâgûti veyü\'mim billâhi feḳadi-stemseke bil\\`urveti-lvuŝḳâ. le-nfiṣâme lehâ. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (264, 'allâhü veliyyü-lleẕîne âmenû yuḫricühüm mine-żżulümâti ile-nnûr. velleẕîne keferû evliyâühümu-ṭṭâgûtü yuḫricûnehüm mine-nnûri ile-żżulümât. ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (265, 'elem tera ile-lleẕî ḥâcce ibrâhîme fî rabbihî en âtâhü-llâhü-lmülk. iẕ ḳâle ibrâhîmü rabbiye-lleẕî yuḥyî veyümîtü ḳâle ene uḥyî veümît. ḳâle ibrâhîmü feinne-llâhe ye\'tî bişşemsi mine-lmeşriḳi fe\'ti bihâ mine-lmagribi febühite-lleẕî kefer. vellâhü lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (266, 'ev kelleẕî merra \\`alâ ḳaryetiv vehiye ḫâviyetün \\`alâ \\`urûşihâ. ḳâle ennâ yuḥyî hâẕihi-llâhü ba\\`de mevtihâ. feemâtehü-llâhü miete \\`âmin ŝümme be\\`aŝeh. ḳâle kem lebiŝt. ḳâle lebiŝtü yevmen ev ba\\`ḍa yevm. ḳâle bel lebiŝte miete \\`âmin fenżur ilâ ṭa\\`âmike veşerâbike lem yetesenneh. venżur ilâ ḥimârike velinec\\`aleke âyetel linnâsi venżur ile-l\\`iżâmi keyfe nünşizühâ ŝümme neksûhâ laḥmâ. felemmâ tebeyyene lehû ḳâle a\\`lemü enne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (267, 'veiẕ ḳâle ibrâhîmü rabbi erinî keyfe tuḥyi-lmevtâ. ḳâle evelem tü\'min. ḳâle belâ velâkil liyaṭmeinne ḳalbî. ḳâle feḫuẕ erbe\\`atem mine-ṭṭayri feṣurhünne ileyke ŝümme-c\\`al \\`alâ külli cebelim minhünne cüz\'en ŝümme-d\\`uhünne ye\'tîneke sa\\`yâ. va\\`lem enne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (268, 'meŝelü-lleẕîne yünfiḳûne emvâlehüm fî sebîli-llâhi kemeŝeli ḥabbetin embetet seb\\`a senâbile fî külli sümbületim mietü ḥabbeh. vellâhü yüḍâ\\`ifü limey yeşâ\'. vellâhü vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (269, 'elleẕîne yünfiḳûne emvâlehüm fî sebîli-llâhi ŝümme lâ yütbi\\`ûne mâ enfeḳû mennev velâ eẕel lehüm ecruhüm \\`inde rabbihim. velâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (270, 'ḳavlüm ma\\`rûfüv vemagfiratün ḫayrum min ṣadeḳatiy yetbe\\`uhâ eẕâ. vellâhü ganiyyün ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (271, 'yâ eyyühe-lleẕîne âmenû lâ tübṭilû ṣadeḳâtiküm bilmenni vel\'eẕâ kelleẕî yünfiḳu mâlehû riâe-nnâsi velâ yü\'minü billâhi velyevmi-l\'âḫir. femeŝelühû kemeŝeli ṣafvânin \\`aleyhi türâbün feeṣâbehû vâbilün feterakehû ṣaldâ. lâ yaḳdirûne \\`alâ şey\'im mimmâ kesebû. vellâhü lâ yehdi-lḳavme-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (272, 'vemeŝelü-lleẕîne yünfiḳûne emvâlehümü-btigâe merḍâti-llâhi veteŝbîtem min enfüsihim kemeŝeli cennetim birabvetin eṣâbehâ vâbilün feâtet ükülehâ ḍi\\`feyn. feil lem yüṣibhâ vâbilün feṭall. vellâhü bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (273, 'eyeveddü eḥadüküm en tekûne lehû cennetüm min neḫîliv vea\\`nâbin tecrî min taḥtihe-l\'enhâru lehû fîhâ min külli-ŝŝemerâti veeṣâbehü-lkiberu velehû ẕürriyyetün ḍu\\`afâ\'. feeṣâbehâ i\\`ṣârun fîhi nârun faḥteraḳat. keẕâlike yübeyyinü-llâhü lekümü-l\'âyâti le\\`alleküm tetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (274, 'yâ eyyühe-lleẕîne âmenû enfiḳû min ṭayyibâti mâ kesebtüm vemimmâ aḫracnâ leküm mine-l\'arḍ. velâ teyemmemü-lḫabîŝe minhü tünfiḳûne velestüm biâḫiẕîhi illâ en tugmiḍû fîh. va\\`lemû enne-llâhe ganiyyün ḥamîd.');
INSERT INTO `tr_transliteration` VALUES (275, 'eşşeyṭânü ye\\`idükümü-lfaḳra veye\'müruküm bilfaḥşâ\'. vellâhü ye\\`idüküm magfiratem minhü vefaḍlâ. vellâhü vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (276, 'yü\'ti-lḥikmete mey yeşâ\'. vemey yü\'te-lḥikmete feḳad ûtiye ḫayran keŝîâ. vemâ yeẕẕekkeru illâ ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (277, 'vemâ enfaḳtüm min nefeḳatin ev neẕertüm min neẕrin feinne-llâhe ya\\`lemüh. vemâ liżżâlimîne min enṣâr.');
INSERT INTO `tr_transliteration` VALUES (278, 'in tübdu-ṣṣadeḳâti feni\\`immâ hî. vein tuḫfûhâ vetü\'tûhe-lfüḳarâe fehüve ḫayrul leküm. veyükeffiru \\`anküm min seyyiâtiküm. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (279, 'leyse \\`aleyke hüdâhüm velâkinne-llâhe yehdî mey yeşâ\'. vemâ tünfiḳû min ḫayrin felienfüsiküm. vemâ tünfiḳûne ille-btigâe vechi-llâh. vemâ tünfiḳû min ḫayriy yüveffe ileyküm veentüm lâ tużlemûn.');
INSERT INTO `tr_transliteration` VALUES (280, 'lilfüḳarâi-lleẕîne uḥṣirû fî sebîli-llâhi lâ yesteṭî\\`ûne ḍarben fi-l\'arḍ. yaḥsebühümü-lcâhilü agniyâe mine-tte\\`affüf. ta\\`rifühüm bisîmâhüm. lâ yes\'elûne-nnâse ilḥâfâ. vemâ tünfiḳû min ḫayrin feinne-llâhe bihî \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (281, 'elleẕîne yünfiḳûne emvâlehüm billeyli vennehâri sirrav ve\\`alâniyeten felehüm ecruhüm \\`inde rabbihim. velâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (282, 'elleẕîne ye\'külûne-rribâ lâ yeḳûmûne illâ kemâ yeḳûmü-lleẕî yeteḫabbeṭuhü-şşeyṭânü mine-lmess. ẕâlike biennehüm ḳâlû inneme-lbey\\`u miŝlü-rribâ. veeḥalle-llâhü-lbey\\`a veḥarrame-rribâ. femen câehû mev\\`iżatüm mir rabbihî fentehâ felehû mâ selef. veemruhû ile-llâh. vemen \\`âde feülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (283, 'yemḥaḳu-llâhü-rribâ veyürbi-ṣṣadeḳâti. vellâhü lâ yüḥibbü külle keffârin eŝîm.');
INSERT INTO `tr_transliteration` VALUES (284, 'inne-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti veeḳâmu-ṣṣalâte veâtevu-zzekâte lehüm ecruhüm \\`inde rabbihim. velâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (285, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veẕerû mâ beḳiye mine-rribâ in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (286, 'feil lem tef\\`alû fe\'ẕenû biḥarbim mine-llâhi verasûlih. vein tübtüm feleküm ruûsü emvâliküm. lâ tażlimûne velâ tużlemûn.');
INSERT INTO `tr_transliteration` VALUES (287, 'vein kâne ẕû \\`usratin feneżiratün ilâ meyserah. veen teṣaddeḳû ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (288, 'vetteḳû yevmen türce\\`ûne fîhi ile-llâhi ŝümme tüveffâ küllü nefsim mâ kesebet vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (289, 'yâ eyyühe-lleẕîne âmenû iẕâ tedâyentüm bideynin ilâ ecelim müsemmen fektübûh. velyektüb beyneküm kâtibüm bil\\`adl. velâ ye\'be kâtibün ey yektübe kemâ \\`allemehü-llâhü felyektüb. velyümlili-lleẕî \\`aleyhi-lḥaḳḳu velyetteḳi-llâhe rabbehû velâ yebḫas minhü şey\'â. fein kâne-lleẕî \\`aleyhi-lḥaḳḳu sefîhen ev ḍa\\`îfen ev lâ yesteṭî\\`u ey yümille hüve felyümlil veliyyühû bil\\`adl. vesteşhidû şehîdeyni mir ricâliküm. feil lem yekûnâ racüleyni feracülüv vemraetâni mimmen terḍavne mine-şşühedâi en teḍille iḥdâhümâ fetüẕekkira iḥdâhüme-l\'uḫrâ. velâ ye\'be-şşühedâü iẕâ mâ dü\\`û. velâ tes\'emû en tektübûhü ṣagîran ev kebîran ilâ ecelih. ẕâliküm aḳseṭu \\`inde-llâhi veaḳvemü lişşehâdeti veednâ ellâ tertâbû illâ en tekûne ticâraten ḥâḍiraten tüdîrûnehâ beyneküm feleyse \\`aleyküm cünâḥun ellâ tektübûhâ. veeşhidû iẕâ tebâya\\`tüm. velâ yüḍârra kâtibüv velâ şehîd. vein tef\\`alû feinnehû füsûḳum biküm. vetteḳu-llâh. veyü\\`allimükümü-llâh. vellâhü bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (290, 'vein küntüm \\`alâ seferiv velem tecidû kâtiben ferihânüm maḳbûḍah. fein emine ba\\`ḍuküm ba\\`ḍan felyüeddi-lleẕi-\'tümine emânetehû velyetteḳi-llâhe rabbeh. velâ tektümü-şşehâdet. vemey yektümhâ feinnehû âŝimün ḳalbüh. vellâhü bimâ ta\\`melûne \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (291, 'lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vein tübdû mâ fî enfüsiküm ev tuḫfûhü yüḥâsibküm bihi-llâh. feyagfiru limey yeşâü veyü\\`aẕẕibü mey yeşâ\'. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (292, 'âmene-rrasûlü bimâ ünzile ileyhi mir rabbihî velmü\'minûn. küllün âmene billâhi vemelâiketihî vekütübihî verusülih. lâ nüferriḳu beyne eḥadim mir rusülih. veḳâlû semi\\`nâ veeṭa\\`nâ gufrâneke rabbenâ veileyke-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (293, 'lâ yükellifü-llâhü nefsen illâ vus\\`ahâ. lehâ mâ kesebet ve\\`aleyhâ me-ktesebet. rabbenâ lâ tüâḫiẕnâ in nesînâ ev aḫṭa\'nâ. rabbenâ velâ taḥmil \\`aleynâ iṣran kemâ ḥameltehû \\`ale-lleẕîne min ḳablinâ. rabbenâ velâ tüḥammilnâ mâ lâ ṭâḳate lenâ bih. va\\`fü \\`annâ. vagfir lenâ. verḥamnâ. ente mevlânâ fenṣurnâ \\`ale-lḳavmi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (294, 'elif-lâm-mîm.');
INSERT INTO `tr_transliteration` VALUES (295, 'allâhü lâ ilâhe illâ hüve-lḥayyü-lḳayyûm.');
INSERT INTO `tr_transliteration` VALUES (296, 'nezzele \\`aleyke-lkitâbe bilḥaḳḳi müṣaddiḳal limâ beyne yedeyhi veenzele-ttevrâte vel\'incîl.');
INSERT INTO `tr_transliteration` VALUES (297, 'min ḳablü hüdel linnâsi veenzele-lfürḳân. inne-lleẕîne keferû biâyâti-llâhi lehüm \\`aẕâbün şedîd. vellâhü \\`azîzün ẕü-ntiḳâm.');
INSERT INTO `tr_transliteration` VALUES (298, 'inne-llâhe lâ yaḫfâ \\`aleyhi şey\'ün fi-l\'arḍi velâ fi-ssemâ\'.');
INSERT INTO `tr_transliteration` VALUES (299, 'hüve-lleẕî yüṣavviruküm fi-l\'erḥâmi keyfe yeşâ\'. lâ ilâhe illâ hüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (300, 'hüve-lleẕî enzele \\`aleyke-lkitâbe minhü âyâtüm muḥkemâtün hünne ümmü-lkitâbi veüḫaru müteşâbihâh. feemme-lleẕîne fî ḳulûbihim zeygun feyettebi\\`ûne mâ teşâbehe minhü-btigâe-lfitneti vebtigâe te\'vîlih. vemâ ya\\`lemü te\'vîlehû ille-llâh. verrâsiḫûne fi-l\\`ilmi yeḳûlûne âmennâ bihî küllüm min \\`indi rabbinâ. vemâ yeẕẕekkeru illâ ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (301, 'rabbenâ lâ tüzig ḳulûbenâ ba\\`de iẕ hedeytenâ veheb lenâ mil ledünke raḥmeh. inneke ente-lvehhâb.');
INSERT INTO `tr_transliteration` VALUES (302, 'rabbenâ inneke câmi\\`u-nnâsi liyevmil lâ raybe fîh. inne-llâhe lâ yuḫlifü-lmî\\`âd.');
INSERT INTO `tr_transliteration` VALUES (303, 'inne-lleẕîne keferû len tugniye \\`anhüm emvâlühüm velâ evlâdühüm mine-llâhi şey\'â. veülâike hüm veḳûdü-nnâr.');
INSERT INTO `tr_transliteration` VALUES (304, 'kede\'bi âli fir\\`avne velleẕîne min ḳablihim. keẕẕebû biâyâtinâ. feeḫaẕehümü-llâhü biẕünûbihim. vellâhü şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (305, 'ḳul lilleẕîne keferû setuglebûne vetuḥşerûne ilâ cehennem. vebi\'se-lmihâd.');
INSERT INTO `tr_transliteration` VALUES (306, 'ḳad kâne leküm âyetün fî fieteyni-lteḳatâ. fietün tüḳâtilü fî sebîli-llâhi veuḫrâ kâfiratüy yeravnehüm miŝleyhim ra\'ye-l\\`ayn. vellâhü yü\'eyyidü binaṣrihî mey yeşâ\'. inne fî ẕâlike le\\`ibratel liüli-l\'ebṣâr.');
INSERT INTO `tr_transliteration` VALUES (307, 'züyyine linnâsi ḥubbü-şşehevâti mine-nnisâi velbenîne velḳanâṭîri-lmüḳanṭarati mine-ẕẕehebi velfiḍḍati velḫayli-lmüsevvemeti vel\'en\\`âmi velḥarŝ. ẕâlike metâ\\`u-lḥayâti-ddünyâ. vellâhü \\`indehû ḥusnü-lmeâb.');
INSERT INTO `tr_transliteration` VALUES (308, 'ḳul eünebbiüküm biḫayrim min ẕâliküm. lilleẕîne-tteḳav \\`inde rabbihim cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ veezvâcüm müṭahherâtüv veriḍvânüm mine-llâh. vellâhü beṣîrum bil\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (309, 'elleẕîne yeḳûlûne rabbenâ innenâ âmennâ fagfir lenâ ẕünûbenâ veḳinâ \\`aẕâbe-nnâr.');
INSERT INTO `tr_transliteration` VALUES (310, 'eṣṣâbirîne veṣṣâdiḳîne velḳânitîne velmünfiḳîne velmüstagfirîne bil\'esḥâr.');
INSERT INTO `tr_transliteration` VALUES (311, 'şehide-llâhü ennehû lâ ilâhe illâ hüve velmelâiketü veülü-l\\`ilmi ḳâimem bilḳisṭ. lâ ilâhe illâ hüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (312, 'inne-ddîne \\`inde-llâhi-l\'islâm. veme-ḫtelefe-lleẕîne ûtü-lkitâbe illâ mim ba\\`di mâ câehümü-l\\`ilmü bagyem beynehüm. vemey yekfür biâyâti-llâhi feinne-llâhe serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (313, 'fein ḥâccûke feḳul eslemtü vechiye lillâhi vemeni-ttebe\\`an. veḳul lilleẕîne ûtü-lkitâbe vel\'ümmiyyîne eeslemtüm. fein eslemû feḳadi-htedev. vein tevellev feinnemâ \\`aleyke-lbelâg. vellâhü beṣîrum bil\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (314, 'inne-lleẕîne yekfürûne biâyâti-llâhi veyaḳtülûne-nnebiyyîne bigayri ḥaḳḳiv veyaḳtülûne-lleẕîne ye\'mürûne bilḳisṭi mine-nnâsi febeşşirhüm bi\\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (315, 'ülâike-lleẕîne ḥabiṭat a\\`mâlühüm fi-ddünyâ vel\'âḫirah. vemâ lehüm min nâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (316, 'elem tera ile-lleẕîne ûtû neṣîbem mine-lkitâbi yüd\\`avne ilâ kitâbi-llâhi liyaḥküme beynehüm ŝümme yetevellâ ferîḳum minhüm vehüm mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (317, 'ẕâlike biennehüm ḳâlû len temessene-nnâru illâ eyyâmem ma\\`dûdât. vegarrahüm fî dînihim mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (318, 'fekeyfe iẕâ cema\\`nâhüm liyevmil lâ raybe fîhi vevuffiyet küllü nefsim mâ kesebet vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (319, 'ḳuli-llâhümme mâlike-lmülki tü\'ti-lmülke men teşâü vetenzi\\`u-lmülke mimmen teşâ\'. vetü\\`izzü men teşâü vetüẕillü men teşâ\'. biyedike-lḫayr. inneke \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (320, 'tûlicü-lleyle fi-nnehâri vetûlicü-nnehâra fi-lleyl. vetuḫricü-lḥayye mine-lmeyyiti vetuḫricü-lmeyyite mine-lḥayy. veterzüḳu men teşâü bigayri ḥisâb.');
INSERT INTO `tr_transliteration` VALUES (321, 'lâ yetteḫiẕi-lmü\'minûne-lkâfirîne evliyâe min dûni-lmü\'minîn. vemey yef\\`al ẕâlike feleyse mine-llâhi fî şey\'in illâ en tetteḳû minhüm tüḳâh. veyüḥaẕẕirukümü-llâhü nefseh. veile-llâhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (322, 'ḳul in tuḫfû mâ fî ṣudûriküm ev tübdûhü ya\\`lemhü-llâh. veya\\`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍ. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (323, 'yevme tecidü küllü nefsim mâ \\`amilet min ḫayrim muḥḍarâ. vemâ \\`amilet min sû\'. teveddü lev enne beynehâ vebeynehû emedem be\\`îdâ. veyüḥaẕẕirukümü-llâhü nefseh. vellâhü raûfüm bil\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (324, 'ḳul in küntüm tüḥibbûne-llâhe fettebi\\`ûnî yuḥbibkümü-llâhü veyagfir leküm ẕünûbeküm. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (325, 'ḳul eṭî\\`ü-llâhe verrasûl. fein tevellev feinne-llâhe lâ yüḥibbü-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (326, 'inne-llâhe-ṣṭafâ âdeme venûḥav veâle ibrâhîme veâle \\`imrâne \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (327, 'ẕürriyyetem ba\\`ḍuhâ mim ba\\`ḍ. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (328, 'iẕ ḳâleti-mraetü \\`imrâne rabbi innî neẕertü leke mâ fî baṭnî müḥarraran feteḳabbel minnî. inneke ente-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (329, 'felemmâ veḍa\\`athâ ḳâlet rabbi innî veḍa\\`tühâ ünŝâ. vellâhü a\\`lemü bimâ veḍa\\`at. veleyse-ẕẕekeru kel\'ünŝâ. veinnî semmeytühâ meryeme veinnî ü\\`îẕühâ bike veẕürriyyetehâ mine-şşeyṭâni-rracîm.');
INSERT INTO `tr_transliteration` VALUES (330, 'feteḳabbelehâ rabbühâ biḳabûlin ḥaseniv veembetehâ nebâten ḥasenev vekeffelehâ zekeriyyâ. küllemâ deḫale \\`aleyhâ zekeriyye-lmiḥrâbe vecede \\`indehâ rizḳan. ḳâle yâ meryemü ennâ leki hâẕâ. ḳâlet hüve min \\`indi-llâh. inne-llâhe yerzüḳu mey yeşâü bigayri ḥisâb.');
INSERT INTO `tr_transliteration` VALUES (331, 'hünâlike de\\`â zekeriyyâ rabbeh. ḳâle rabbi heb lî mil ledünke ẕürriyyeten ṭayyibeh. inneke semî\\`u-ddü\\`â\'.');
INSERT INTO `tr_transliteration` VALUES (332, 'fenâdethü-lmelâiketü vehüve ḳâimüy yüṣallî fi-lmiḥrâbi enne-llâhe yübeşşiruke biyaḥyâ müṣaddiḳam bikelimetim mine-llâhi veseyyidev veḥaṣûrav venebiyyem mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (333, 'ḳâle rabbi ennâ yekûnü lî gulâmüv veḳad beleganiye-lkiberu vemraetî \\`âḳir. ḳâle keẕâlike-llâhü yef\\`alü mâ yeşâ\'.');
INSERT INTO `tr_transliteration` VALUES (334, 'ḳâle rabbi-c\\`al lî âyeh. ḳâle âyetüke ellâ tükellime-nnâse ŝelâŝete eyyâmin illâ ramzâ. veẕkür rabbeke keŝîrav vesebbiḥ bil\\`aşiyyi vel\'ibkâr.');
INSERT INTO `tr_transliteration` VALUES (335, 'veiẕ ḳâleti-lmelâiketü yâ meryemü inne-llâhe-ṣṭafâki veṭahheraki vaṣṭafâki \\`alâ nisâi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (336, 'yâ meryemu-ḳnütî lirabbiki vescüdî verke\\`î me\\`a-rrâki\\`în.');
INSERT INTO `tr_transliteration` VALUES (337, 'ẕâlike min embâi-lgaybi nûḥîhi ileyk. vemâ künte ledeyhim iẕ yülḳûne aḳlâmehüm eyyühüm yekfülü meryem. vemâ künte ledeyhim iẕ yaḫteṣimûn.');
INSERT INTO `tr_transliteration` VALUES (338, 'iẕ ḳâleti-lmelâiketü yâ meryemü inne-llâhe yübeşşiruki bikelimetim minh. ismühü-lmesîḥu \\`îse-bnü meryeme vecîhen fi-ddünyâ vel\'âḫirati vemine-lmüḳarrabîn.');
INSERT INTO `tr_transliteration` VALUES (339, 'veyükellimü-nnâse fi-lmehdi vekehlev vemine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (340, 'ḳâlet rabbi ennâ yekûnü lî veledüv velem yemsesnî beşer. ḳâle keẕâliki-llâhü yaḫlüḳu mâ yeşâ\'. iẕâ ḳaḍâ emran feinnemâ yeḳûlü lehû kün feyekûn.');
INSERT INTO `tr_transliteration` VALUES (341, 'veyü\\`allimühü-lkitâbe velḥikmete vettevrâte vel\'incîl.');
INSERT INTO `tr_transliteration` VALUES (342, 'verasûlen ilâ benî isrâîle ennî ḳad ci\'tüküm biâyetim mir rabbiküm ennî aḫlüḳu leküm mine-ṭṭîni kehey\'eti-ṭṭayri feenfüḫu fîhi feyekûnü ṭayram biiẕni-llâh. veübriü-l\'ekmehe vel\'ebraṣa veuḥyi-lmevtâ biiẕni-llâh. veünebbiüküm bimâ te\'külûne vemâ teddeḫirûne fî büyûtiküm. inne fî ẕâlike leâyetel leküm in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (343, 'vemüṣaddiḳal limâ beyne yedeyye mine-ttevrâti veliüḥille leküm ba\\`ḍa-lleẕî ḥurrime \\`aleyküm veci\'tüküm biâyetim mir rabbiküm fetteḳu-llâhe veeṭî\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (344, 'inne-llâhe rabbî verabbüküm fa\\`büdûh. hâẕâ ṣirâṭum müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (345, 'felemmâ eḥasse \\`îsâ minhümü-lküfra ḳâle men enṣârî ile-llâh. ḳâle-lḥavâriyyûne naḥnü enṣâru-llâh. âmennâ billâh. veşhed biennâ müslimûn.');
INSERT INTO `tr_transliteration` VALUES (346, 'rabbenâ âmennâ bimâ enzelte vetteba\\`ne-rrasûle fektübnâ me\\`a-şşâhidîn.');
INSERT INTO `tr_transliteration` VALUES (347, 'vemekerû vemekera-llâh. vellâhü ḫayru-lmâkirîn.');
INSERT INTO `tr_transliteration` VALUES (348, 'iẕ ḳâle-llâhü yâ \\`îsâ innî müteveffîke verâfi\\`uke ileyye vemüṭahhiruke mine-lleẕîne keferû vecâ\\`ilü-lleẕîne-ttebe\\`ûke fevḳa-lleẕîne keferû ilâ yevmi-lḳiyâmeh. ŝümme ileyye merci\\`uküm feaḥkümü beyneküm fîmâ küntüm fîhi taḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (349, 'feemme-lleẕîne keferû feü\\`aẕẕibühüm \\`aẕâben şedîden fi-ddünyâ vel\'âḫirah. vemâ lehüm min nâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (350, 'veemme-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti feyüveffîhim ücûrahüm. vellâhü lâ yüḥibbu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (351, 'ẕâlike netlûhü \\`aleyke mine-l\'âyâti veẕẕikri-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (352, 'inne meŝele \\`îsâ \\`inde-llâhi kemeŝeli âdem. ḫaleḳahû min türâbin ŝümme ḳâle lehû kün feyekûn.');
INSERT INTO `tr_transliteration` VALUES (353, 'elḥaḳḳu mir rabbike felâ teküm mine-lmümterîn.');
INSERT INTO `tr_transliteration` VALUES (354, 'femen ḥâccâke fîhi mim ba\\`di mâ câeke mine-l\\`ilmi feḳul te\\`âlev ned\\`u ebnâenâ veebnâeküm venisâenâ venisâeküm veenfüsenâ veenfüseküm ŝümme nebtehil fenec\\`al la\\`nete-llâhi \\`ale-lkâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (355, 'inne hâẕâ lehüve-lḳaṣaṣu-lḥaḳḳ. vemâ min ilâhin ille-llâh. veinne-llâhe lehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (356, 'fein tevellev feinne-llâhe \\`alîmüm bilmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (357, 'ḳul yâ ehle-lkitâbi te\\`âlev ilâ kelimetin sevâim beynenâ vebeyneküm ellâ na\\`büde ille-llâhe velâ nüşrike bihî şey\'ev velâ yetteḫiẕe ba\\`ḍunâ ba\\`ḍan erbâbem min dûni-llâh. fein tevellev feḳûlü-şhedû biennâ müslimûn.');
INSERT INTO `tr_transliteration` VALUES (358, 'yâ ehle-lkitâbi lime tüḥâccûne fî ibrâhîme vemâ ünzileti-ttevrâtü vel\'incîlü illâ mim ba\\`dih. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (359, 'hâentüm hâülâi ḥâcectüm fîmâ leküm bihî \\`ilmün felime tüḥâccûne fîmâ leyse leküm bihî \\`ilm. vellâhü ya\\`lemü veentüm lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (360, 'mâ kâne ibrâhîmü yehûdiyyev velâ naṣrâniyyev velâkin kâne ḥanîfem müslimâ. vemâ kâne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (361, 'inne evle-nnâsi biibrâhîme lelleẕîne-ttebe\\`ûhü vehâẕe-nnebiyyü velleẕîne âmenû. vellâhü veliyyü-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (362, 'veddeṭ ṭâifetüm min ehli-lkitâbi lev yüḍillûneküm. vemâ yüḍillûne illâ enfüsehüm vemâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (363, 'yâ ehle-lkitâbi lime tekfürûne biâyâti-llâhi veentüm teşhedûn.');
INSERT INTO `tr_transliteration` VALUES (364, 'yâ ehle-lkitâbi lime telbisûne-lḥaḳḳa bilbâṭili vetektümûne-lḥaḳḳa veentüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (365, 'veḳâleṭ ṭâifetüm min ehli-lkitâbi âminû billeẕî ünzile \\`ale-lleẕîne âmenû veche-nnehâri vekfürû âḫirahû le\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (366, 'velâ tü\'minû illâ limen tebi\\`a dîneküm. ḳul inne-lhüdâ hüde-llâhi ey yü\'tâ eḥadüm miŝle mâ ûtîtüm ev yüḥâccûküm \\`inde rabbiküm. ḳul inne-lfaḍle biyedi-llâh. yü\'tîhi mey yeşâ\'. vellâhü vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (367, 'yaḫteṣṣu biraḥmetihî mey yeşâ\'. vellâhü ẕü-lfaḍli-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (368, 'vemin ehli-lkitâbi men in te\'menhü biḳinṭâriy yü\'eddihî ileyk. veminhüm men in te\'menhü bidînâril lâ yü\'eddihî ileyke illâ mâ dümte \\`aleyhi ḳâimâ. ẕâlike biennehüm ḳâlû leyse \\`aleynâ fi-l\'ümmiyyîne sebîl. veyeḳûlûne \\`ale-llâhi-lkeẕibe vehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (369, 'belâ men evfâ bi\\`ahdihî vetteḳâ feinne-llâhe yüḥibbü-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (370, 'inne-lleẕîne yeşterûne bi\\`ahdi-llâhi veeymânihim ŝemenen ḳalîlen ülâike lâ ḫalâḳa lehüm fi-l\'âḫirati velâ yükellimühümü-llâhü velâ yenżuru ileyhim yevme-lḳiyâmeti velâ yüzekkîhim. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (371, 'veinne minhüm leferîḳay yelvûne elsinetehüm bilkitâbi litaḥsebûhü mine-lkitâbi vemâ hüve mine-lkitâb. veyeḳûlûne hüve min \\`indi-llâhi vemâ hüve min \\`indi-llâh. veyeḳûlûne \\`ale-llâhi-lkeẕibe vehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (372, 'mâ kâne libeşerin ey yü\'tiyehü-llâhü-lkitâbe velḥukme vennübüvvete ŝümme yeḳûle linnâsi kûnû \\`ibâdel lî min dûni-llâhi velâkin kûnû rabbâniyyîne bimâ küntüm tü\\`allimûne-lkitâbe vebimâ küntüm tedrusûn.');
INSERT INTO `tr_transliteration` VALUES (373, 'velâ ye\'müraküm en tetteḫiẕü-lmelâikete vennebiyyîne erbâbâ. eye\'müruküm bilküfri ba\\`de iẕ entüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (374, 'veiẕ eḫaẕe-llâhü mîŝâḳa-nnebiyyîne lemâ âteytüküm min kitâbiv veḥikmetin ŝümme câeküm rasûlüm müṣaddiḳul limâ me\\`aküm letü\'minünne bihî veletenṣurunneh. ḳâle eaḳrartüm veeḫaẕtüm \\`alâ ẕâliküm iṣrî. ḳâlû aḳrarnâ. ḳâle feşhedû veenâ me\\`aküm mine-şşâhidîn.');
INSERT INTO `tr_transliteration` VALUES (375, 'femen tevellâ ba\\`de ẕâlike feülâike hümü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (376, 'efegayra dîni-llâhi yebgûne velehû esleme men fi-ssemâvâti vel\'arḍi ṭav\\`av vekerhev veileyhi yürce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (377, 'ḳul âmennâ billâhi vemâ ünzile \\`aleynâ vemâ ünzile \\`alâ ibrâhîme veismâ\\`île veisḥâḳa veya\\`ḳûbe vel\'esbâṭi vemâ ûtiye mûsâ ve\\`îsâ vennebiyyûne mir rabbihim. lâ nüferriḳu beyne eḥadim minhüm. venaḥnü lehû müslimûn.');
INSERT INTO `tr_transliteration` VALUES (378, 'vemey yebtegi gayra-l\'islâmi dînen feley yuḳbele minh. vehüve fi-l\'âḫirati mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (379, 'keyfe yehdi-llâhü ḳavmen keferû ba\\`de îmânihim veşehidû enne-rrasûle ḥaḳḳuv vecâehümü-lbeyyinât. vellâhü lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (380, 'ülâike cezâühüm enne \\`aleyhim la\\`nete-llâhi velmelâiketi vennâsi ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (381, 'ḫâlidîne fîhâ. lâ yüḫaffefü \\`anhümü-l\\`aẕâbü velâ hüm yünżarûn.');
INSERT INTO `tr_transliteration` VALUES (382, 'ille-lleẕîne tâbû mim ba\\`di ẕâlike veaṣleḥû feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (383, 'inne-lleẕîne keferû ba\\`de îmânihim ŝümme-zdâdû küfral len tuḳbele tevbetühüm. veülâike hümu-ḍḍâllûn.');
INSERT INTO `tr_transliteration` VALUES (384, 'inne-lleẕîne keferû vemâtû vehüm küffârun feley yuḳbele min eḥadihim mil\'ü-l\'arḍi ẕehebev velevi-ftedâ bihî. ülâike lehüm \\`aẕâbün elîmüv vemâ lehüm min nâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (385, 'len tenâlü-lbirra ḥattâ tünfiḳû mimmâ tüḥibbûn. vemâ tünfiḳû min şey\'in feinne-llâhe bihî \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (386, 'küllu-ṭṭa\\`âmi kâne ḥillel libenî isrâîle illâ mâ ḥarrame isrâîlü \\`alâ nefsihî min ḳabli en tünezzele-ttevrâh. ḳul fe\'tû bittevrâti fetlûhâ in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (387, 'femeni-fterâ \\`ale-llâhi-lkeẕibe mim ba\\`di ẕâlike feülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (388, 'ḳul ṣadeḳa-llâhü fettebi\\`û millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (389, 'inne evvele beytiv vuḍi\\`a linnâsi lelleẕî bibekkete mübârakev vehüdel lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (390, 'fîhi âyâtüm beyyinâtüm meḳâmü ibrâhîm. vemen deḫalehû kâne âminâ. velillâhi \\`ale-nnâsi ḥiccü-lbeyti meni-steṭâ\\`a ileyhi sebîlâ. vemen kefera feinne-llâhe ganiyyün \\`ani-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (391, 'ḳul yâ ehle-lkitâbi lime tekfürûne biâyâti-llâh. vellâhü şehîdün \\`alâ mâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (392, 'ḳul yâ ehle-lkitâbi lime teṣuddûne \\`an sebîli-llâhi men âmene tebgûnehâ \\`ivecev veentüm şühedâ\'. veme-llâhü bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (393, 'yâ eyyühe-lleẕîne âmenû in tüṭî\\`û ferîḳam mine-lleẕîne ûtü-lkitâbe yeruddûküm ba\\`de îmâniküm kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (394, 'vekeyfe tekfürûne veentüm tütlâ \\`aleyküm âyâtü-llâhi vefîküm rasûlüh. vemey ya\\`teṣim billâhi feḳad hüdiye ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (395, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe ḥaḳḳa tüḳâtihî velâ temûtünne illâ veentüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (396, 'va\\`teṣimû biḥabli-llâhi cemî\\`av velâ teferraḳû. veẕkürû ni\\`mete-llâhi \\`aleyküm iẕ küntüm a\\`dâen feellefe beyne ḳulûbiküm feaṣbaḥtüm bini\\`metihî iḫvânâ. veküntüm \\`alâ şefâ ḥufratim mine-nnâri feenḳaẕeküm minhâ. keẕâlike yübeyyinü-llâhü leküm âyâtihî le\\`alleküm tehtedûn.');
INSERT INTO `tr_transliteration` VALUES (397, 'velteküm minküm ümmetüy yed\\`ûne ile-lḫayri veye\'mürûne bilma\\`rûfi veyenhevne \\`ani-lmünker. veülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (398, 'velâ tekûnû kelleẕîne teferraḳû vaḫtelefû mim ba\\`di mâ câehümü-lbeyyinât. veülâike lehüm \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (399, 'yevme tebyeḍḍu vucûhüv vetesveddü vucûhün. feemme-lleẕîne-sveddet vucûhühüm. ekefertüm ba\\`de îmâniküm feẕûḳu-l\\`aẕâbe bimâ küntüm tekfürûn.');
INSERT INTO `tr_transliteration` VALUES (400, 'veemme-lleẕîne-byeḍḍat vucûhühüm fefî raḥmeti-llâh. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (401, 'tilke âyâtü-llâhi netlûhâ \\`aleyke bilḥaḳḳ. veme-llâhü yürîdü żulmel lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (402, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. veile-llâhi türce\\`u-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (403, 'küntüm ḫayra ümmetin uḫricet linnâsi te\'mürûne bilma\\`rûfi vetenhevne \\`ani-lmünkeri vetü\'minûne billâh. velev âmene ehlü-lkitâbi lekâne ḫayral lehüm. minhümü-lmü\'minûne veekŝeruhümü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (404, 'ley yeḍurrûküm illâ eẕâ. veiy yüḳâtilûküm yüvellûkümü-l\'edbâr. ŝümme lâ yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (405, 'ḍuribet \\`aleyhimü-ẕẕilletü eyne mâ ŝüḳifû illâ biḥablim mine-llâhi veḥablim mine-nnâsi vebâû bigaḍabim mine-llâhi veḍuribet \\`aleyhimü-lmeskeneh. ẕâlike biennehüm kânû yekfürûne biâyâti-llâhi veyaḳtülûne-l\'embiyâe bigayri ḥaḳḳ. ẕâlike bimâ \\`aṣav vekânû ya\\`tedûn.');
INSERT INTO `tr_transliteration` VALUES (406, 'leysû sevââ. min ehli-lkitâbi ümmetün ḳâimetüy yetlûne âyâti-llâhi ânâe-lleyli vehüm yescüdûn.');
INSERT INTO `tr_transliteration` VALUES (407, 'yü\'minûne billâhi velyevmi-l\'âḫiri veye\'mürûne bilma\\`rûfi veyenhevne \\`ani-lmünkeri veyüsâri\\`ûne fi-lḫayrât. veülâike mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (408, 'vemâ yef\\`alû min ḫayrin feley yükferûh. vellâhü \\`alîmüm bilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (409, 'inne-lleẕîne keferû len tugniye \\`anhüm emvâlühüm velâ evlâdühüm mine-llâhi şey\'â. veülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (410, 'meŝelü mâ yünfiḳûne fî hâẕihi-lḥayâti-ddünyâ kemeŝeli rîḥin fîhâ ṣirrun eṣâbet ḥarŝe ḳavmin żalemû enfüsehüm feehleketh. vemâ żalemehümü-llâhü velâkin enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (411, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕû biṭânetem min dûniküm lâ ye\'lûneküm ḫabâlâ. veddû mâ \\`anittüm. ḳad bedeti-lbagḍâü min efvâhihim. vemâ tuḫfî ṣudûruhüm ekber. ḳad beyyennâ lekümü-l\'âyâti in küntüm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (412, 'hâentüm ülâi tüḥibbûnehüm velâ yüḥibbûneküm vetü\'minûne bilkitâbi küllih. veiẕâ leḳûküm ḳâlû âmennâ. veiẕâ ḫalev \\`aḍḍû \\`aleykümü-l\'enâmile mine-lgayż. ḳul mûtû bigayżiküm. inne-llâhe \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (413, 'in temsesküm ḥasenetün tesü\'hüm. vein tüṣibküm seyyietüy yefraḥû bihâ. vein taṣbirû vetetteḳû lâ yeḍurruküm keydühüm şey\'â. inne-llâhe bimâ ya\\`melûne müḥîṭ.');
INSERT INTO `tr_transliteration` VALUES (414, 'veiẕ gadevte min ehlike tübevviü-lmü\'minîne meḳâ\\`ide lilḳitâl. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (415, 'iẕ hemmeṭ ṭâifetâni minküm en tefşelâ vellâhü veliyyühümâ. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (416, 'veleḳad neṣarakümü-llâhü bibedriv veentüm eẕilleh. fetteḳu-llâhe le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (417, 'iẕ teḳûlü lilmü\'minîne eley yekfiyeküm ey yümiddeküm rabbüküm biŝelâŝeti âlâfim mine-lmelâiketi münzelîn.');
INSERT INTO `tr_transliteration` VALUES (418, 'belâ in taṣbirû vetetteḳû veye\'tûküm min fevrihim hâẕâ yümdidküm rabbüküm biḫamseti âlâfim mine-lmelâiketi müsevvimîn.');
INSERT INTO `tr_transliteration` VALUES (419, 'vemâ ce\\`alehü-llâhü illâ büşrâ leküm velitaṭmeinne ḳulûbüküm bih. veme-nnaṣru illâ min \\`indi-llâhi-l\\`azîzi-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (420, 'liyaḳṭa\\`a ṭarafem mine-lleẕîne keferû ev yekbitehüm feyenḳalibû ḫâibîn.');
INSERT INTO `tr_transliteration` VALUES (421, 'leyse leke mine-l\'emri şey\'ün ev yetûbe \\`aleyhim ev yü\\`aẕẕibehüm feinnehüm żâlimûn.');
INSERT INTO `tr_transliteration` VALUES (422, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. yagfiru limey yeşâü veyü\\`aẕẕibü mey yeşâ\'. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (423, 'yâ eyyühe-lleẕîne âmenû lâ te\'külü-rribâ aḍ\\`âfem müḍâ\\`afeh. vetteḳu-llâhe le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (424, 'vetteḳu-nnâra-lletî ü\\`iddet lilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (425, 'veeṭî\\`ü-llâhe verrasûle le\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (426, 'vesâri\\`û ilâ magfiratim mir rabbiküm vecennetin \\`arḍuhe-ssemâvâtü vel\'arḍu ü\\`iddet lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (427, 'elleẕîne yünfiḳûne fi-sserrâi veḍḍarrâi velkâżimîne-lgayża vel\\`âfîne \\`ani-nnâs. vellâhü yüḥibbü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (428, 'velleẕîne iẕâ fe\\`alû fâḥişeten ev żalemû enfüsehüm ẕekerü-llâhe festagferû liẕünûbihim. vemey yagfiru-ẕẕünûbe ille-llâh. velem yüṣirrû \\`alâ mâ fe\\`alû vehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (429, 'ülâike cezâühüm magfiratüm mir rabbihim vecennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veni\\`me ecru-l\\`âmilîn.');
INSERT INTO `tr_transliteration` VALUES (430, 'ḳad ḫalet min ḳabliküm sünenün fesîrû fi-l\'arḍi fenżurû keyfe kâne \\`âḳibetü-lmükeẕẕibîn.');
INSERT INTO `tr_transliteration` VALUES (431, 'hâẕâ beyânül linnâsi vehüdev vemev\\`iżatül lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (432, 'velâ tehinû velâ taḥzenû veentümü-l\'a\\`levne in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (433, 'iy yemsesküm ḳarḥun feḳad messe-lḳavme ḳarḥum miŝlüh. vetilke-l\'eyyâmü nüdâvilühâ beyne-nnâs. veliya\\`leme-llâhü-lleẕîne âmenû veyetteḫiẕe minküm şühedâ\'. vellâhü lâ yüḥibbu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (434, 'veliyümeḥḥiṣa-llâhü-lleẕîne âmenû veyemḥaḳa-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (435, 'em ḥasibtüm en tedḫulü-lcennete velemmâ ya\\`lemi-llâhü-lleẕîne câhedû minküm veya\\`leme-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (436, 'veleḳad küntüm temennevne-lmevte min ḳabli en telḳavh. feḳad raeytümûhü veentüm tenżurûn.');
INSERT INTO `tr_transliteration` VALUES (437, 'vemâ müḥammedün illâ rasûl. ḳad ḫalet min ḳablihi-rrusül. efeim mâte ev ḳutile-nḳalebtüm \\`alâ a\\`ḳâbiküm. vemey yenḳalib \\`alâ \\`aḳibeyhi feley yeḍurra-llâhe şey\'â. veseyeczi-llâhü-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (438, 'vemâ kâne linefsin en temûte illâ biiẕni-llâhi kitâbem müeccelâ. vemey yürid ŝevâbe-ddünyâ nü\'tihî minhâ. vemey yürid ŝevâbe-l\'âḫirati nü\'tihî minhâ. veseneczi-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (439, 'vekeeyyim min nebiyyin ḳâtele me\\`ahû ribbiyyûne keŝîr. femâ vehenû limâ eṣâbehüm fî sebîli-llâhi vemâ ḍa\\`ufû veme-stekânû. vellâhü yüḥibbu-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (440, 'vemâ kâne ḳavlehüm illâ en ḳâlû rabbene-gfir lenâ ẕünûbenâ veisrâfenâ fî emrinâ veŝebbit aḳdâmenâ venṣurnâ \\`ale-lḳavmi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (441, 'feâtâhümü-llâhü ŝevâbe-ddünyâ veḥusne ŝevâbi-l\'âḫirah. vellâhü yüḥibbü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (442, 'yâ eyyühe-lleẕîne âmenû in tüṭî\\`ü-lleẕîne keferû yeruddûküm \\`alâ a\\`ḳâbiküm fetenḳalibû ḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (443, 'beli-llâhü mevlâküm. vehüve ḫayru-nnâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (444, 'senülḳî fî ḳulûbi-lleẕîne keferü-rru\\`be bimâ eşrakû billâhi mâ lem yünezzil bihî sülṭânâ. veme\'vâhümü-nnâr. vebi\'se meŝve-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (445, 'veleḳad ṣadeḳakümü-llâhü va\\`dehû iẕ teḥussûnehüm biiẕnih. ḥattâ iẕâ feşiltüm vetenâza\\`tüm fi-l\'emri ve\\`aṣaytüm mim ba\\`di mâ erâküm mâ tüḥibbûn. minküm mey yürîdü-ddünyâ veminküm mey yürîdü-l\'âḫirah. ŝümme ṣarafeküm \\`anhüm liyebteliyeküm. veleḳad \\`afâ \\`anküm. vellâhü ẕû faḍlin \\`ale-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (446, 'iẕ tuṣ\\`idûne velâ telvûne \\`alâ eḥadiv verrasûlü yed\\`ûküm fî uḫrâküm feeŝâbeküm gammem bigammil likeylâ taḥzenû \\`alâ mâ fâteküm velâ mâ eṣâbeküm. vellâhü ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (447, 'ŝümme enzele \\`aleyküm mim ba\\`di-lgammi emeneten nü\\`âsey yagşâ ṭâifetem minküm veṭâifetün ḳad ehemmethüm enfüsühüm yeżunnûne billâhi gayra-lḥaḳḳi żanne-lcâhiliyyeh. yeḳûlûne hel lenâ min-l\'emri min şey\'. ḳul inne-l\'emra küllehû lillâh. yuḫfûne fî enfüsihim mâ lâ yübdûne lek. yeḳûlûne lev kâne lenâ mine-l\'emri şey\'üm mâ ḳutilnâ hâhünâ. ḳul lev küntüm fî büyûtiküm leberaze-lleẕîne kütibe \\`aleyhimü-lḳatlü ilâ meḍâci\\`ihim. veliyebteliye-llâhü mâ fî ṣudûriküm veliyümeḥḥiṣa mâ fî ḳulûbiküm. vellâhü \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (448, 'inne-lleẕîne tevellev minküm yevme-lteḳe-lcem\\`âni inneme-stezellehümü-şşeyṭânü biba\\`ḍi mâ kesebû. veleḳad \\`afe-llâhü \\`anhüm. inne-llâhe gafûrun ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (449, 'yâ eyyühe-lleẕîne âmenû lâ tekûnû kelleẕîne keferû veḳâlû liiḫvânihim iẕâ ḍarabû fi-l\'arḍi ev kânû guzzel lev kânû \\`indenâ mâ mâtû vemâ ḳutilû. liyec\\`ale-llâhü ẕâlike ḥasraten fî ḳulûbihim. vellâhü yuḥyî veyümît. vellâhü bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (450, 'velein ḳutiltüm fî sebîli-llâhi ev müttüm lemagfiratüm mine-llâhi veraḥmetün ḫayrum mimmâ yecme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (451, 'veleim müttüm ev ḳutiltüm leile-llâhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (452, 'febimâ raḥmetim mine-llâhi linte lehüm. velev künte feżżan galîża-lḳalbi lenfeḍḍû min ḥavlik. fa\\`fü \\`anhüm vestagfir lehüm veşâvirhüm fi-l\'emr. feiẕâ \\`azemte fetevekkel \\`ale-llâh. inne-llâhe yüḥibbü-lmütevekkilîn.');
INSERT INTO `tr_transliteration` VALUES (453, 'iy yenṣurkümü-llâhü felâ gâlibe leküm. veiy yaḫẕülküm femen ẕe-lleẕî yenṣuruküm mim ba\\`dih. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (454, 'vemâ kâne linebiyyin ey yegull. vemey yaglül ye\'ti bimâ galle yevme-lḳiyâmeh. ŝümme tüveffâ küllü nefsim mâ kesebet vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (455, 'efemeni-ttebe\\`a riḍvâne-llâhi kemem bâe biseḫaṭim mine-llâhi veme\'vâhü cehennem. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (456, 'hüm deracâtün \\`inde-llâh. vellâhü beṣîrum bimâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (457, 'leḳad menne-llâhü \\`ale-lmü\'minîne iẕ be\\`aŝe fîhim rasûlem min enfüsihim yetlû \\`aleyhim âyâtihî veyüzekkîhim veyü\\`allimühümü-lkitâbe velḥikmeh. vein kânû min ḳablü lefî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (458, 'evelemmâ eṣâbetküm müṣîbetün ḳad eṣabtüm miŝleyhâ ḳultüm ennâ hâẕâ. ḳul hüve min \\`indi enfüsiküm. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (459, 'vemâ eṣâbeküm yevme-lteḳe-lcem\\`âni febiiẕni-llâhi veliya\\`leme-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (460, 'veliya\\`leme-lleẕîne nâfeḳû. veḳîle lehüm te\\`âlev ḳâtilû fî sebîli-llâhi evi-dfe\\`û. ḳâlû lev na\\`lemü ḳitâlel letteba\\`nâküm. hüm lilküfri yevmeiẕin aḳrabü minhüm lil\'îmân. yeḳûlûne biefvâhihim mâ leyse fî ḳulûbihim. vellâhü a\\`lemü bimâ yektümûn.');
INSERT INTO `tr_transliteration` VALUES (461, 'elleẕîne ḳâlû liiḫvânihim veḳa\\`adû lev eṭâ\\`ûnâ mâ ḳutilû. ḳul fedraû \\`an enfüsikümü-lmevte in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (462, 'velâ taḥsebenne-lleẕîne ḳutilû fî sebîli-llâhi emvâtâ. bel aḥyâün \\`inde rabbihim yürzeḳûn.');
INSERT INTO `tr_transliteration` VALUES (463, 'feriḥîne bimâ âtâhümü-llâhü min faḍlihî veyestebşirûne billeẕîne lem yelḥaḳû bihim min ḫalfihim ellâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (464, 'yestebşirûne bini\\`metim mine-llâhi vefaḍliv veenne-llâhe lâ yüḍî\\`u ecra-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (465, 'elleẕîne-stecâbû lillâhi verrasûli mim ba\\`di mâ eṣâbehümü-lḳarḥ. lilleẕîne aḥsenû minhüm vetteḳav ecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (466, 'elleẕîne ḳâle lehümü-nnâsü inne-nnâse ḳad ceme\\`û leküm faḫşevhüm fezâdehüm îmânâ. veḳâlû ḥasbüne-llâhü veni\\`me-lvekîl.');
INSERT INTO `tr_transliteration` VALUES (467, 'fenḳalebû bini\\`metim mine-llâhi vefaḍlil lem yemseshüm sûüv vettebe\\`û riḍvâne-llâh. vellâhü ẕû faḍlin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (468, 'innemâ ẕâlikümü-şşeyṭânü yüḫavvifü evliyâeh. felâ teḫâfûhüm veḫâfûni in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (469, 'velâ yaḥzünke-lleẕîne yüsâri\\`ûne fi-lküfr. innehüm ley yeḍurrü-llâhe şey\'â. yürîdü-llâhü ellâ yec\\`ale lehüm ḥażżan fi-l\'âḫirah. velehüm \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (470, 'inne-lleẕîne-şteravu-lküfra bil\'îmâni ley yeḍurrü-llâhe şey\'en. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (471, 'velâ yaḥsebenne-lleẕîne keferû ennemâ nümlî lehüm ḫayrul lienfüsihim. innemâ nümlî lehüm liyezdâdû iŝmâ. velehüm \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (472, 'mâ kâne-llâhü liyeẕera-lmü\'minîne \\`alâ mâ entüm \\`aleyhi ḥattâ yemîze-lḫabîŝe mine-ṭṭayyib. vemâ kâne-llâhü liyuṭli\\`aküm \\`ale-lgaybi velâkinne-llâhe yectebî mir rusülihî mey yeşâü feâminû billâhi verusülih. vein tü\'minû vetetteḳû feleküm ecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (473, 'velâ yaḥsebenne-lleẕîne yebḫalûne bimâ âtâhümü-llâhü min faḍlihî hüve ḫayral lehüm. bel hüve şerrul lehüm. seyüṭavveḳûne mâ beḫilû bihî yevme-lḳiyâmeh. velillâhi mîrâŝü-ssemâvâti vel\'arḍ. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (474, 'leḳad semi\\`a-llâhü ḳavle-lleẕîne ḳâlû inne-llâhe feḳîruv venaḥnü agniyâ\'. senektübü mâ ḳâlû veḳatlehümü-l\'embiyâe bigayri ḥaḳḳiv veneḳûlü ẕûḳû \\`aẕâbe-lḥarîḳ.');
INSERT INTO `tr_transliteration` VALUES (475, 'ẕâlike bimâ ḳaddemet eydîküm veenne-llâhe leyse biżallâmil lil\\`abîd.');
INSERT INTO `tr_transliteration` VALUES (476, 'elleẕîne ḳâlû inne-llâhe \\`ahide ileynâ ellâ nü\'mine lirasûlin ḥattâ ye\'tiyenâ biḳurbânin te\'külühü-nnâr. ḳul ḳad câeküm rusülüm min ḳablî bilbeyyinâti vebilleẕî ḳultüm felime ḳateltümûhüm in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (477, 'fein keẕẕebûke feḳad küẕẕibe rusülüm min ḳablike câû bilbeyyinâti vezzübüri velkitâbi-lmünîr.');
INSERT INTO `tr_transliteration` VALUES (478, 'küllü nefsin ẕâiḳatü-lmevt. veinnemâ tüveffevne ücûraküm yevme-lḳiyâmeh. femen zuḥziḥa \\`ani-nnâri veüdḫile-lcennete feḳad fâz. veme-lḥayâtü-ddünyâ illâ metâ\\`u-lgurûr.');
INSERT INTO `tr_transliteration` VALUES (479, 'letüblevunne fî emvâliküm veenfüsiküm veletesme\\`unne mine-lleẕîne ûtü-lkitâbe min ḳabliküm vemine-lleẕîne eşrakû eẕen keŝîrâ. vein taṣbirû vetetteḳû feinne ẕâlike min \\`azmi-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (480, 'veiẕ eḫaẕe-llâhü mîŝâḳa-lleẕîne ûtü-lkitâbe letübeyyinünnehû linnâsi velâ tektümûneh. fenebeẕûhü verâe żuhûrihim veşterav bihî ŝemenen ḳalîlâ. febi\'se mâ yeşterûn.');
INSERT INTO `tr_transliteration` VALUES (481, 'lâ taḥsebenne-lleẕîne yefraḥûne bimâ etev veyüḥibbûne ey yuḥmedû bimâ lem yef\\`alû felâ taḥsebennehüm bimefâzetim mine-l\\`aẕâb. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (482, 'velillâhi mülkü-ssemâvâti vel\'arḍ. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (483, 'inne fî ḫalḳi-ssemâvâti vel\'arḍi vaḫtilâfi-lleyli vennehâri leâyâtil liüli-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (484, 'elleẕîne yeẕkürûne-llâhe ḳiyâmev veḳu\\`ûdev ve\\`alâ cünûbihim veyetefekkerûne fî ḫalḳi-ssemâvâti vel\'arḍ. rabbenâ mâ ḫalaḳte hâẕâ bâṭilâ. sübḥâneke feḳinâ \\`aẕâbe-nnâr.');
INSERT INTO `tr_transliteration` VALUES (485, 'rabbenâ inneke men tüdḫili-nnâra feḳad aḫzeyteh. vemâ liżżâlimîne min enṣâr.');
INSERT INTO `tr_transliteration` VALUES (486, 'rabbenâ innenâ semi\\`nâ münâdiyey yünâdî lil\'îmâni en âminû birabbiküm feâmennâ. rabbenâ fagfir lenâ ẕünûbenâ vekeffir \\`annâ seyyiâtinâ veteveffenâ me\\`a-l\'ebrâr.');
INSERT INTO `tr_transliteration` VALUES (487, 'rabbenâ veâtinâ mâ ve\\`attenâ \\`alâ rusülike velâ tuḫzinâ yevme-lḳiyâmeh. inneke lâ tuḫlifü-lmî\\`âd.');
INSERT INTO `tr_transliteration` VALUES (488, 'festecâbe lehüm rabbühüm ennî lâ üḍî\\`u \\`amele \\`âmilim minküm min ẕekerin ev ünŝâ. ba\\`ḍuküm mim ba\\`ḍ. felleẕîne hâcerû veuḫricû min diyârihim veûẕû fî sebîlî veḳâtelû veḳutilû leükeffiranne \\`anhüm seyyiâtihim veleüdḫilennehüm cennâtin tecrî min taḥtihe-l\'enhâr. ŝevâbem min \\`indi-llâh. vellâhü \\`indehû ḥusnü-ŝŝevâb.');
INSERT INTO `tr_transliteration` VALUES (489, 'lâ yegurranneke teḳallübü-lleẕîne keferû fi-lbilâd.');
INSERT INTO `tr_transliteration` VALUES (490, 'metâ\\`un ḳalîlün ŝümme me\'vâhüm cehennem. vebi\'se-lmihâd.');
INSERT INTO `tr_transliteration` VALUES (491, 'lâkini-lleẕîne-tteḳav rabbehüm lehüm cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ nüzülem min \\`indi-llâh. vemâ \\`inde-llâhi ḫayrul lil\'ebrâr.');
INSERT INTO `tr_transliteration` VALUES (492, 'veinne min ehli-lkitâbi lemey yü\'minü billâhi vemâ ünzile ileyküm vemâ ünzile ileyhim ḫâşi\\`îne lillâhi lâ yeşterûne biâyâti-llâhi ŝemenen ḳalîlâ. ülâike lehüm ecruhüm \\`inde rabbihim. inne-llâhe serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (493, 'yâ eyyühe-lleẕîne âmenu-ṣbirû veṣâbirû verâbiṭû vetteḳu-llâhe le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (494, 'yâ eyyühe-nnâsü-tteḳû rabbekümü-lleẕî ḫaleḳaküm min nefsiv vâḥidetiv veḫaleḳa minhâ zevcehâ vebeŝŝe minhümâ ricâlen keŝîrav venisââ. vetteḳu-llâhe-lleẕî tesâelûne bihî vel\'erḥâm. inne-llâhe kâne \\`aleyküm raḳîbâ.');
INSERT INTO `tr_transliteration` VALUES (495, 'veâtü-lyetâmâ emvâlehüm velâ tetebeddelü-lḫabîŝe biṭṭayyib. velâ te\'külû emvâlehüm ilâ emvâliküm. innehû kâne ḥûben kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (496, 'vein ḫiftüm ellâ tuḳsiṭû fi-lyetâmâ fenkiḥû mâ ṭâbe leküm mine-nnisâi meŝnâ veŝülâŝe verubâ\\`. fein ḫiftüm ellâ ta\\`dilû fevâḥideten ev mâ meleket eymânüküm. ẕâlike ednâ ellâ te\\`ûlû.');
INSERT INTO `tr_transliteration` VALUES (497, 'veâtü-nnisâe ṣadüḳâtihinne niḥleh. fein ṭibne leküm \\`an şey\'im minhü nefsen fekülûhü henîem merîâ.');
INSERT INTO `tr_transliteration` VALUES (498, 'velâ tü\'tü-ssüfehâe emvâlekümü-lletî ce\\`ale-llâhü leküm ḳiyâmev verzüḳûhüm fîhâ veksûhüm veḳûlû lehüm ḳavlem ma\\`rûfâ.');
INSERT INTO `tr_transliteration` VALUES (499, 'vebtelü-lyetâmâ ḥattâ iẕâ belegü-nnikâḥ. fein ânestüm minhüm ruşden fedfe\\`û ileyhim emvâlehüm. velâ te\'külûhâ isrâfev vebidâran ey yekberû. vemen kâne ganiyyen felyesta\\`fif. vemen kâne feḳîran felye\'kül bilma\\`rûf. feiẕâ defa\\`tüm ileyhim emvâlehüm feeşhidû \\`aleyhim. vekefâ billâhi ḥasîbâ.');
INSERT INTO `tr_transliteration` VALUES (500, 'lirricâli neṣîbüm mimmâ terake-lvâlidâni vel\'aḳrabûn. velinnisâi neṣîbüm mimmâ terake-lvâlidâni vel\'aḳrabûne mimmâ ḳalle minhü ev keŝür. neṣîbem mefrûḍâ.');
INSERT INTO `tr_transliteration` VALUES (501, 'veiẕâ ḥaḍara-lḳismete ülü-lḳurbâ velyetâmâ velmesâkînü ferzüḳûhüm minhü veḳûlû lehüm ḳavlem ma\\`rûfâ.');
INSERT INTO `tr_transliteration` VALUES (502, 'velyaḫşe-lleẕîne lev terakû min ḫalfihim ẕürriyyeten ḍi\\`âfen ḫâfû \\`aleyhim. felyetteḳu-llâhe velyeḳûlû ḳavlen sedîdâ.');
INSERT INTO `tr_transliteration` VALUES (503, 'inne-lleẕîne ye\'külûne emvâle-lyetâmâ żulmen innemâ ye\'külûne fî büṭûnihim nârâ. veseyaṣlevne se\\`îrâ.');
INSERT INTO `tr_transliteration` VALUES (504, 'yûṣîkümü-llâhü fî evlâdiküm liẕẕekeri miŝlü ḥażżi-l\'ünŝeyeyn. fein künne nisâen fevḳa-ŝneteyni felehünne ŝülüŝâ mâ terak. vein kânet vâḥideten felehe-nniṣf. veliebeveyhi likülli vâḥidim minhüme-ssüdüsü mimmâ terake in kâne lehû veled. feil lem yekül lehû veledüv veveriŝehû ebevâhü feliümmihi-ŝŝülüŝ. fein kâne lehû iḫvetün feliümmihi-ssüdüsü mim ba\\`di veṣiyyetiy yûṣî bihâ ev deyn. âbâüküm veebnâüküm. lâ tedrûne eyyühüm aḳrabü leküm nef\\`â. ferîḍatem mine-llâh. inne-llâhe kâne \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (505, 'veleküm niṣfü mâ terake ezvâcüküm il lem yekül lehünne veled. fein kâne lehünne veledün felekümü-rrubü\\`u mimmâ terakne mim ba\\`di veṣiyyetiy yûṣîne bihâ ev deyn. velehünne-rrubü\\`u mimmâ teraktüm il lem yekül leküm veled. fein kâne leküm veledün felehünne-ŝŝümünü mimmâ teraktüm mim ba\\`di veṣiyyetin tûṣûne bihâ ev deyn. vein kâne racülüy yûraŝü kelâleten evi-mraetüv velehû eḫun ev uḫtün felikülli vâḥidim minhüme-ssüdüs. fein kânû ekŝera min ẕâlike fehüm şürakâü fi-ŝŝülüŝi mim ba\\`di veṣiyyetiy yûṣâ bihâ ev deynin gayra müḍârr. veṣiyyetem mine-llâh. vellâhü \\`alîmün ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (506, 'tilke ḥudûdü-llâh. vemey yüṭi\\`i-llâhe verasûlehû yüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veẕâlike-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (507, 'vemey ya\\`ṣi-llâhe verasûlehû veyete\\`addâ ḥudûdehû yüdḫilhü nâran ḫâliden fîhâ. velehû \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (508, 'vellâtî ye\'tîne-lfâḥişete min nisâiküm festeşhidû \\`aleyhinne erbe\\`atem minküm. fein şehidû feemsikûhünne fi-lbüyûti ḥattâ yeteveffâhünne-lmevtü ev yec\\`ale-llâhü lehünne sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (509, 'velleẕâni ye\'tiyânihâ minküm feâẕûhümâ. fein tâbâ veaṣleḥâ fea\\`riḍû \\`anhümâ. inne-llâhe kâne tevvâber raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (510, 'inneme-ttevbetü \\`ale-llâhi lilleẕîne ya\\`melûne-ssûe bicehâletin ŝümme yetûbûne min ḳarîbin feülâike yetûbü-llâhü \\`aleyhim. vekâne-llâhü \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (511, 'veleyseti-ttevbetü lilleẕîne ya\\`melûne-sseyyiât. ḥattâ iẕâ ḥaḍara eḥadehümü-lmevtü ḳâle innî tübtü-l\'âne vele-lleẕîne yemûtûne vehüm küffâr. ülâike a\\`tednâ lehüm \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (512, 'yâ eyyühe-lleẕîne âmenû lâ yeḥillü leküm en teriŝü-nnisâe kerhâ. velâ ta\\`ḍulûhünne liteẕhebû biba\\`ḍi mâ âteytümûhünne illâ ey ye\'tîne bifâḥişetim mübeyyineh. ve\\`âşirûhünne bilma\\`rûf. fein kerihtümûhünne fe\\`asâ en tekrahû şey\'ev veyec\\`ale-llâhü fîhi ḫayran keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (513, 'vein erattümü-stibdâle zevcim mekâne zevciv veâteytüm iḥdâhünne ḳinṭâran felâ te\'ḫuẕû minhü şey\'â. ete\'ḫuẕûnehû bühtânev veiŝmem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (514, 'vekeyfe te\'ḫuẕûnehû veḳad efḍâ ba\\`ḍuküm ilâ ba\\`ḍiv veeḫaẕne minküm mîŝâḳan galîżâ.');
INSERT INTO `tr_transliteration` VALUES (515, 'velâ tenkiḥû mâ nekeḥa âbâüküm mine-nnisâi illâ mâ ḳad selef. innehû kâne fâḥişetev vemaḳtâ. vesâe sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (516, 'ḥurrimet \\`aleyküm ümmehâtüküm vebenâtüküm veeḫavâtüküm ve\\`ammâtüküm veḫâlâtüküm vebenâtü-l\'eḫi vebenâtü-l\'uḫti veümmehâtükümü-llâtî erḍa\\`neküm veeḫavâtüküm mine-rraḍâ\\`ati veümmehâtü nisâiküm verabâibükümü-llâtî fî ḥucûriküm min nisâikümü-llâtî deḫaltüm bihinn. feil lem tekûnû deḫaltüm bihinne felâ cünâḥa \\`aleyküm. veḥalâilü ebnâikümü-lleẕîne min aṣlâbiküm veen tecme\\`û beyne-l\'uḫteyni illâ mâ ḳad selef. inne-llâhe kâne gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (517, 'velmuḥṣanâtü mine-nnisâi illâ mâ meleket eymânüküm. kitâbe-llâhi \\`aleyküm. veüḥille leküm mâ verâe ẕâliküm en tebtegû biemvâliküm muḥṣinîne gayra müsâfiḥîn. feme-stemta\\`tüm bihî minhünne feâtûhünne ücûrahünne ferîḍah. velâ cünâḥa \\`aleyküm fîmâ terâḍaytüm bihî mim ba\\`di-lferîḍah. inne-llâhe kâne \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (518, 'vemel lem yesteṭi\\` minküm ṭavlen ey yenkiḥa-lmuḥsenâti-lmü\'minâti femim mâ meleket eymânüküm min feteyâtikümü-lmü\'minât. vellâhü a\\`lemü biîmâniküm. ba\\`ḍuküm mim ba\\`ḍ. fenkiḥûhünne biiẕni ehlihinne veâtûhünne ücûrahünne bilma\\`rûfi muḥṣanâtin gayra mesâfiḥâtiv velâ mütteḫiẕâti aḫdân. feiẕâ uḥṣinne fein eteyne bifâḥişetin fe\\`aleyhinne niṣfü mâ \\`ale-lmuḥsenâti mine-l\\`aẕâb. ẕâlike limen ḫaşiye-l\\`anete minküm. veen taṣbirû ḫayrul leküm. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (519, 'yürîdü-llâhü liyübeyyine leküm veyehdiyeküm sünene-lleẕîne min ḳabliküm veyetûbe \\`aleyküm. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (520, 'vellâhü yürîdü ey yetûbe \\`aleyküm veyürîdü-lleẕîne yettebi\\`ûne-şşehevâti en temîlû meylen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (521, 'yürîdü-llâhü ey yüḫaffife \\`anküm. veḫuliḳa-l\'insânü ḍa\\`îfâ.');
INSERT INTO `tr_transliteration` VALUES (522, 'yâ eyyühe-lleẕîne âmenû lâ te\'külû emvâleküm beyneküm bilbâṭili illâ en tekûne ticâraten \\`an terâḍim minküm velâ taḳtülû enfüseküm. inne-llâhe kâne biküm raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (523, 'vemey yef\\`al ẕâlike \\`udvânev veżulmen fesevfe nuṣlîhi nârâ. vekâne ẕâlike \\`ale-llâhi yesîrâ.');
INSERT INTO `tr_transliteration` VALUES (524, 'in tectenibû kebâira mâ tünhevne \\`anhü nükeffir \\`anküm seyyiâtiküm venüdḫilküm müdḫalen kerîmâ.');
INSERT INTO `tr_transliteration` VALUES (525, 'velâ tetemennev mâ feḍḍale-llâhü bihî ba\\`ḍaküm \\`alâ ba\\`ḍ. lirricâli neṣîbüm mimme-ktesebû velinnisâi neṣîbüm mimme-ktesebn. ves\'elü-llâhe min faḍlih. inne-llâhe kâne bikülli şey\'in \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (526, 'veliküllin ce\\`alnâ mevâliye mimmâ terake-lvâlidâni vel\'aḳrabûn. velleẕîne \\`aḳadet eymânüküm feâtûhüm neṣîbehüm. inne-llâhe kâne \\`alâ külli şey\'in şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (527, 'erricâlü ḳavvâmûne \\`ale-nnisâi bimâ feḍḍale-llâhü ba\\`ḍahüm \\`alâ ba\\`ḍiv vebimâ enfeḳû min emvâlihim. feṣṣâliḥâtü ḳânitâtün ḥâfiżâtül lilgaybi bimâ ḥafiża-llâh. vellâtî teḫâfûne nüşûzehünne fe\\`iżûhünne vehcürûhünne fi-lmeḍâci\\`i vaḍribûhünn. fein eṭa\\`neküm felâ tebgû \\`aleyhinne sebîlâ. inne-llâhe kâne \\`aliyyen kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (528, 'vein ḫiftüm şiḳâḳa beynihimâ feb\\`aŝû ḥakemem min ehlihî veḥakemem min ehlihâ. iy yürîdâ iṣlâḥay yüveffiḳi-llâhü beynehümâ. inne-llâhe kâne \\`alîmen ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (529, 'va\\`büdü-llâhe velâ tüşrikû bihî şey\'ev vebilvâlideyni iḥsânev vebiẕi-lḳurbâ velyetâmâ velmesâkîni velcâri ẕi-lḳurbâ velcâri-lcünübi veṣṣâḥibi bilcembi vebni-ssebîli vemâ meleket eymânüküm. inne-llâhe lâ yüḥibbü men kâne muḫtâlen feḫûrâ.');
INSERT INTO `tr_transliteration` VALUES (530, 'elleẕîne yebḫalûne veye\'mürûne-nnâse bilbuḫli veyektümûne mâ âtâhümü-llâhü min faḍlih. vea\\`tednâ lilkâfirîne \\`aẕâbem mühînâ.');
INSERT INTO `tr_transliteration` VALUES (531, 'velleẕîne yünfiḳûne emvâlehüm riâe-nnâsi velâ yü\'minûne billâhi velâ bilyevmi-l\'âḫir. vemey yeküni-şşeyṭânü lehû ḳarînen fesâe ḳarînâ.');
INSERT INTO `tr_transliteration` VALUES (532, 'vemâẕâ \\`aleyhim lev âmenû billâhi velyevmi-l\'âḫiri veenfeḳû mimmâ razeḳahümü-llâh. vekâne-llâhü bihim \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (533, 'inne-llâhe lâ yażlimü miŝḳâle ẕerrah. vein tekü ḥasenetey yüḍâ\\`ifhâ veyü\'ti mil ledünhü ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (534, 'fekeyfe iẕâ ci\'nâ min külli ümmetim bişehîdiv veci\'nâ bike \\`alâ hâülâi şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (535, 'yevmeiẕiy yeveddü-lleẕîne keferû ve\\`aṣavu-rrasûle lev tüsevvâ bihimü-l\'arḍ. velâ yektümûne-llâhe ḥadîŝâ.');
INSERT INTO `tr_transliteration` VALUES (536, 'yâ eyyühe-lleẕîne âmenû lâ taḳrabu-ṣṣalâte veentüm sükârâ ḥattâ ta\\`lemû mâ teḳûlûne velâ cünüben illâ \\`âbirî sebîlin ḥattâ tagtesilû. vein küntüm merḍâ ev \\`alâ seferin ev câe eḥadüm minküm mine-lgâiṭi ev lemestümü-nnisâe felem tecidû mâen feteyemmemû ṣa\\`îden ṭayyiben femseḥû bivucûhiküm veeydîküm. inne-llâhe kâne \\`afüvven gafûrâ.');
INSERT INTO `tr_transliteration` VALUES (537, 'elem tera ile-lleẕîne ûtû neṣîbem mine-lkitâbi yeşterûne-ḍḍalâlete veyürîdûne en teḍillü-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (538, 'vellâhü a\\`lemü bia\\`dâiküm. vekefâ billâhi veliyyâ. vekefâ billâhi neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (539, 'mine-lleẕîne hâdû yüḥarrifûne-lkelime \\`am mevâḍi\\`ihî veyeḳûlûne semi\\`nâ ve\\`aṣaynâ vesma\\` gayra müsme\\`iv verâ\\`inâ leyyem bielsinetihim veṭa\\`nen fi-ddîn. velev ennehüm ḳâlû semi\\`nâ veeṭa\\`nâ vesma\\` venżurnâ lekâne ḫayral lehüm veaḳveme velâkil le\\`anehümü-llâhü biküfrihim felâ yü\'minûne illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (540, 'yâ eyyühe-lleẕîne ûtü-lkitâbe âminû bimâ nezzelnâ müṣaddiḳal limâ me\\`aküm min ḳabli en naṭmise vucûhen feneruddehâ \\`alâ edbârihâ ev nel\\`anehüm kemâ le\\`annâ aṣḥâbe-ssebt. vekâne emru-llâhi mef\\`ûlâ.');
INSERT INTO `tr_transliteration` VALUES (541, 'inne-llâhe lâ yagfiru ey yüşrake bihî veyagfiru mâ dûne ẕâlike limey yeşâ\'. vemey yüşrik billâhi feḳadi-fterâ iŝmen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (542, 'elem tera ile-lleẕîne yüzekkûne enfüsehüm. beli-llâhü yüzekkî mey yeşâü velâ yużlemûne fetîlâ.');
INSERT INTO `tr_transliteration` VALUES (543, 'ünżur keyfe yefterûne \\`ale-llâhi-lkeẕib. vekefâ bihî iŝmem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (544, 'elem tera ile-lleẕîne ûtû neṣîbem mine-lkitâbi yü\'minûne bilcibti veṭṭâgûti veyeḳûlûne lilleẕîne keferû hâülâi ehdâ mine-lleẕîne âmenû sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (545, 'ülâike-lleẕîne le\\`anehümü-llâh. vemey yel\\`ani-llâhü felen tecide lehû neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (546, 'em lehüm neṣîbüm mine-lmülki feiẕel lâ yü\'tûne-nnâse neḳîrâ.');
INSERT INTO `tr_transliteration` VALUES (547, 'em yaḥsüdûne-nnâse \\`alâ mâ âtâhümü-llâhü min faḍlih. feḳad âteynâ âle ibrâhîme-lkitâbe velḥikmete veâteynâhüm mülken \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (548, 'feminhüm men âmene bihî veminhüm men ṣadde \\`anh. vekefâ bicehenneme se\\`îrâ.');
INSERT INTO `tr_transliteration` VALUES (549, 'inne-lleẕîne keferû biâyâtinâ sevfe nuṣlîhim nârâ. küllemâ neḍicet cülûdühüm beddelnâhüm cülûden gayrahâ liyeẕûḳu-l\\`aẕâb. inne-llâhe kâne \\`azîzen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (550, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti senüdḫilühüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. lehüm fîhâ ezvâcüm müṭahherah. venüdḫilühüm żillen żalîlâ.');
INSERT INTO `tr_transliteration` VALUES (551, 'inne-llâhe ye\'müruküm en tü\'eddü-l\'emenâti ilâ ehlihâ veiẕâ ḥakemtüm beyne-nnâsi en taḥkümû bil\\`adl. inne-llâhe ni\\`immâ ye\\`iżuküm bih. inne-llâhe kâne semî\\`am beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (552, 'yâ eyyühe-lleẕîne âmenû eṭî\\`ü-llâhe veeṭî\\`ü-rrasûle veüli-l\'emri minküm. fein tenâza\\`tüm fî şey\'in feruddûhü ile-llâhi verrasûli in küntüm tü\'minûne billâhi velyevmi-l\'âḫir. ẕâlike ḫayruv veaḥsenü te\'vîlâ.');
INSERT INTO `tr_transliteration` VALUES (553, 'elem tera ile-lleẕîne yez\\`umûne ennehüm âmenû bimâ ünzile ileyke vemâ ünzile min ḳablike yürîdûne ey yeteḥâkemû ile-ṭṭâgûti veḳad ümirû ey yekfürû bih. veyürîdü-şşeyṭânü ey yüḍillehüm ḍalâlem be\\`îdâ.');
INSERT INTO `tr_transliteration` VALUES (554, 'veiẕâ ḳîle lehüm te\\`âlev ilâ mâ enzele-llâhü veile-rrasûli raeyte-lmünâfiḳîne yeṣuddûne \\`anke ṣudûdâ.');
INSERT INTO `tr_transliteration` VALUES (555, 'fekeyfe iẕâ eṣâbethüm müṣîbetüm bimâ ḳaddemet eydîhim ŝümme câûke yaḥlifûne billâhi in eradnâ illâ iḥsânev vetevfîḳâ.');
INSERT INTO `tr_transliteration` VALUES (556, 'ülâike-lleẕîne ya\\`lemü-llâhü mâ fî ḳulûbihim fea\\`riḍ \\`anhüm ve\\`iżhüm veḳul lehüm fî enfüsihim ḳavlem belîgâ.');
INSERT INTO `tr_transliteration` VALUES (557, 'vemâ erselnâ mir rasûlin illâ liyüṭâ\\`a biiẕni-llâh. velev ennehüm iż żalemû enfüsehüm câûke festagferü-llâhe vestagfera lehümü-rrasûlü levecedü-llâhe tevvâber raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (558, 'felâ verabbike lâ yü\'minûne ḥattâ yüḥakkimûke fîmâ şecera beynehüm ŝümme lâ yecidû fî enfüsihim ḥaracem mimmâ ḳaḍayte veyüsellimû teslîmâ.');
INSERT INTO `tr_transliteration` VALUES (559, 'velev ennâ ketebnâ \\`aleyhim eni-ḳtülû enfüseküm evi-ḫrucû min diyâriküm mâ fe\\`alûhü illâ ḳalîlüm minhüm. velev ennehüm fe\\`alû mâ yû\\`ażûne bihî lekâne ḫayral lehüm veeşedde teŝbîtâ.');
INSERT INTO `tr_transliteration` VALUES (560, 'veiẕel leâteynâhüm mil ledünnâ ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (561, 'velehedeynâhüm ṣirâṭam müsteḳîmâ.');
INSERT INTO `tr_transliteration` VALUES (562, 'vemey yüṭi\\`i-llâhe verrasûle feülâike me\\`a-lleẕîne en\\`ame-llâhü \\`aleyhim mine-nnebiyyîne veṣṣiddîḳîne veşşühedâi veṣṣâliḥîn. veḥasüne ülâike rafîḳâ.');
INSERT INTO `tr_transliteration` VALUES (563, 'ẕâlike-lfaḍlü mine-llâh. vekefâ billâhi \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (564, 'yâ eyyühe-lleẕîne âmenû ḫuẕû ḥiẕraküm fenfirû ŝübâtin evi-nfirû cemî\\`â.');
INSERT INTO `tr_transliteration` VALUES (565, 'veinne minküm lemel leyübeṭṭienn. fein eṣâbetküm müṣîbetün ḳâle ḳad en\\`ame-llâhü \\`aleyye iẕ lem eküm me\\`ahüm şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (566, 'velein eṣâbeküm faḍlüm mine-llâhi leyeḳûlenne keel lem teküm beyneküm vebeynehû meveddetüy yâ leytenî küntü me\\`ahüm feefûze fevzen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (567, 'felyüḳâtil fî sebîli-llâhi-lleẕîne yeşrûne-lḥayâte-ddünyâ bil\'âḫirah. vemey yüḳâtil fî sebîli-llâhi feyuḳtel ev yaglib fesevfe nü\'tîhi ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (568, 'vemâ leküm lâ tüḳâtilûne fî sebîli-llâhi velmüstaḍ\\`afîne mine-rricâli vennisâi velvildâni-lleẕîne yeḳûlûne rabbenâ aḫricnâ min hâẕihi-lḳaryeti-żżâlimi ehlühâ. vec\\`al lenâ mil ledünke veliyyâ. vec\\`al lenâ mil ledünke neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (569, 'elleẕîne âmenû yüḳâtilûne fî sebîli-llâh. velleẕîne keferû yüḳâtilûne fî sebîli-ṭṭâgûti feḳâtilû evliyâe-şşeyṭân. inne keyde-şşeyṭâni kâne ḍa\\`îfâ.');
INSERT INTO `tr_transliteration` VALUES (570, 'elem tera ile-lleẕîne ḳîle lehüm küffû eydiyeküm veeḳîmu-ṣṣalâte veâtü-zzekâh. felemmâ kütibe \\`aleyhimü-lḳitâlü iẕâ ferîḳum minhüm yaḫşevne-nnâse keḫaşyeti-llâhi ev eşedde ḫaşyeh. veḳâlû rabbenâ lime ketebte \\`aleyne-lḳitâl. levlâ eḫḫartenâ ilâ ecelin ḳarîb. ḳul metâ\\`u-ddünyâ ḳalîl. vel\'âḫiratü ḫayrul limeni-tteḳâ velâ tużlemûne fetîlâ.');
INSERT INTO `tr_transliteration` VALUES (571, 'eyne mâ tekûnû yüdrikkümü-lmevtü velev küntüm fî bürûcim müşeyyedeh. vein tüṣibhüm ḥasenetüy yeḳûlû hâẕihî min \\`indi-llâh. vein tüṣibhüm seyyietüy yeḳûlû hâẕihî min \\`indik. ḳul küllüm min \\`indi-llâh. femâ lihâülâi-lḳavmi lâ yekâdûne yefḳahûne ḥadîŝâ.');
INSERT INTO `tr_transliteration` VALUES (572, 'mâ eṣâbeke min ḥasenetin femine-llâh. vemâ eṣâbeke min seyyietin femin nefsik. veerselnâke linnâsi rasûlâ. vekefâ billâhi şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (573, 'mey yüṭi\\`i-rrasûle feḳad eṭâ\\`a-llâh. vemen tevellâ femâ erselnâke \\`aleyhim ḥafîżâ.');
INSERT INTO `tr_transliteration` VALUES (574, 'veyeḳûlûne ṭâ\\`ah. feiẕâ berazû min \\`indike beyyete ṭâifetüm minhüm gayra-lleẕî teḳûl. vellâhü yektübü mâ yübeyyitûn. fea\\`riḍ \\`anhüm vetevekkel \\`ale-llâh. vekefâ billâhi vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (575, 'efelâ yetedebberûne-lḳur\'ân. velev kâne min \\`indi gayri-llâhi levecedû fîhi-ḫtilâfen keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (576, 'veiẕâ câehüm emrum mine-l\'emni evi-lḫavfi eẕâ\\`û bih. velev raddûhü ile-rrasûli veilâ üli-l\'emri minhüm le\\`alimehü-lleẕîne yestembiṭûnehû minhüm. velevlâ faḍlü-llâhi \\`aleyküm veraḥmetühû letteba\\`tümü-şşeyṭâne illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (577, 'feḳâtil fî sebîli-llâh. lâ tükellefü illâ nefseke veḥarriḍi-lmü\'minîn. \\`asa-llâhü ey yeküffe be\'se-lleẕîne keferû. vellâhü eşeddü be\'sev veeşeddü tenkîlâ.');
INSERT INTO `tr_transliteration` VALUES (578, 'mey yeşfa\\` şefâ\\`aten ḥasenetey yekül lehû neṣîbüm minhâ. vemey yeşfa\\` şefâ\\`aten seyyietey yekül lehû kiflüm minhâ. vekâne-llâhü \\`alâ külli şey\'im müḳîtâ.');
INSERT INTO `tr_transliteration` VALUES (579, 'veiẕâ ḥuyyîtüm biteḥiyyetin feḥayyû biaḥsene minhâ ev ruddûhâ. inne-llâhe kâne \\`alâ külli şey\'in ḥasîbâ.');
INSERT INTO `tr_transliteration` VALUES (580, 'allâhü lâ ilâhe illâ hû. leyecme\\`anneküm ilâ yevmi-lḳiyâmeti lâ raybe fîh. vemen aṣdeḳu mine-llâhi ḥadîŝâ.');
INSERT INTO `tr_transliteration` VALUES (581, 'femâ leküm fi-lmünâfiḳîne fieteyni vellâhü erkesehüm bimâ kesebû. etürîdûne en tehdû men eḍalle-llâh. vemey yuḍlili-llâhü felen tecide lehû sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (582, 'veddû lev tekfürûne kemâ keferû fetekûnûne sevâen felâ tetteḫiẕû minhüm evliyâe ḥattâ yühâcirû fî sebîli-llâh. fein tevellev feḫuẕûhüm vaḳtülûhüm ḥayŝü vecettümûhüm. velâ tetteḫiẕû minhüm veliyyev velâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (583, 'ille-lleẕîne yeṣilûne ilâ ḳavmim beyneküm vebeynehüm mîŝâḳun ev câûküm ḥaṣirat ṣudûruhüm ey yüḳâtilûküm ev yüḳâtilû ḳavmehüm. velev şâe-llâhü leselleṭahüm \\`aleyküm feleḳâtelûküm. feini-\\`tezelûküm felem yüḳâtilûküm veelḳav ileykümü-sseleme femâ ce\\`ale-llâhü leküm \\`aleyhim sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (584, 'setecidûne âḫarîne yürîdûne ey ye\'menûküm veye\'menû ḳavmehüm. küllemâ ruddû ile-lfitneti ürkisû fîhâ. feil lem ya\\`tezilûküm veyülḳû ileykümü-sseleme veyeküffû eydiyehüm feḫuẕûhüm vaḳtülûhüm ḥayŝü ŝeḳiftümûhüm. veülâiküm ce\\`alnâ leküm \\`aleyhim sülṭânem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (585, 'vemâ kâne limü\'minin ey yaḳtüle mü\'minen illâ ḫaṭaâ. vemen ḳatele mü\'minen ḫaṭaen fetaḥrîru raḳabetim mü\'minetiv vediyetüm müsellemetün ilâ ehlihî illâ ey yeṣṣaddeḳû. fein kâne min ḳavmin \\`adüvvil leküm vehüve mü\'minün fetaḥrîru raḳabetim mü\'mineh. vein kâne min ḳavmim beyneküm vebeynehüm mîŝâḳun fediyetüm müsellemetün ilâ ehlihî vetaḥrîru raḳabetim mü\'mineh. femel lem yecid feṣiyâmü şehrayni mütetâbi\\`ayn. tevbetem mine-llâh. vekâne-llâhü \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (586, 'vemey yaḳtül mü\'minem müte\\`ammiden fecezâühû cehennemü ḫâliden fîhâ vegaḍibe-llâhü \\`aleyhi vele\\`anehû vee\\`adde lehû \\`aẕâben \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (587, 'yâ eyyühe-lleẕîne âmenû iẕâ ḍarabtüm fî sebîli-llâhi fetebeyyenû velâ teḳûlû limen elḳâ ileykümü-sselâme leste mü\'minâ. tebtegûne \\`araḍa-lḥayâti-ddünyâ. fe\\`inde-llâhi megânimü keŝîrah. keẕâlike küntüm min ḳablü femenne-llâhü \\`aleyküm fetebeyyenû. inne-llâhe kâne bimâ ta\\`melûne ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (588, 'lâ yestevi-lḳâ\\`idûne mine-lmü\'minîne gayru üli-ḍḍarari velmücâhidûne fî sebîli-llâhi biemvâlihim veenfüsihim. feḍḍale-llâhü-lmücâhidîne biemvâlihim veenfüsihim \\`ale-lḳâ\\`idîne deraceh. veküllev ve\\`ade-llâhü-lḥusnâ. vefeḍḍale-llâhü-lmücâhidîne \\`ale-lḳâ\\`idîne ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (589, 'deracâtim minhü vemagfiratev veraḥmeh. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (590, 'inne-lleẕîne teveffâhümü-lmelâiketü żâlimî enfüsihim ḳâlû fîme küntüm. ḳâlû künnâ müstaḍ\\`afîne fi-l\'arḍ. ḳâlû elem tekün arḍu-llâhi vâsi\\`aten fetühâcirû fîhâ. feülâike me\'vâhüm cehennem. vesâet meṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (591, 'ille-lmüstaḍ\\`afîne mine-rricâli vennisâi velvildâni lâ yesteṭî\\`ûne ḥîletev velâ yehtedûne sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (592, 'feülâike \\`asa-llâhü ey ya\\`füve \\`anhüm. vekâne-llâhü \\`afüvven gafûrâ.');
INSERT INTO `tr_transliteration` VALUES (593, 'vemey yühâcir fî sebîli-llâhi yecid fi-l\'arḍi mürâgamen keŝîrav vese\\`ah. vemey yaḫruc mim beytihî mühâciran ile-llâhi verasûlihî ŝümme yüdrikhü-lmevtü feḳad veḳa\\`a ecruhû \\`ale-llâh. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (594, 'veiẕâ ḍarabtüm fi-l\'arḍi feleyse \\`aleyküm cünâḥun en taḳṣurû mine-ṣṣalâh. in ḫiftüm ey yeftinekümü-lleẕîne keferû. inne-lkâfirîne kânû leküm \\`adüvvem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (595, 'veiẕâ künte fîhim feeḳamte lehümu-ṣṣalâte felteḳum ṭâifetüm minhüm me\\`ake velye\'ḫuẕû esliḥatehüm. feiẕâ secedû felyekûnû miv verâiküm. velte\'ti ṭâifetün uḫrâ lem yüṣallû felyüṣallû me\\`ake velye\'ḫuẕû ḥiẕrahüm veesliḥatehüm. vedde-lleẕîne keferû lev tagfülûne \\`an esliḥatiküm veemti\\`atiküm feyemîlûne \\`aleyküm meyletev vâḥideten. velâ cünâḥa \\`aleyküm in kâne biküm eẕem mim meṭarin ev küntüm merḍâ en teḍa\\`û esliḥateküm. veḫuẕû ḥiẕraküm. inne-llâhe e\\`adde lilkâfirîne \\`aẕâbem mühînâ.');
INSERT INTO `tr_transliteration` VALUES (596, 'feiẕâ ḳaḍaytümu-ṣṣalâte feẕkürü-llâhe ḳiyâmev veḳu\\`ûdev ve\\`alâ cünûbiküm. feiẕe-ṭme\'nentüm feeḳîmu-ṣṣalâh. inne-ṣṣalâte kânet \\`ale-lmü\'minîne kitâbem mevḳûtâ.');
INSERT INTO `tr_transliteration` VALUES (597, 'velâ tehinû fi-btigâi-lḳavm. in tekûnû te\'lemûne feinnehüm ye\'lemûne kemâ te\'lemûn. vetercûne mine-llâhi mâ lâ yercûn. vekâne-llâhü \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (598, 'innâ enzelnâ ileyke-lkitâbe bilḥaḳḳi litaḥküme beyne-nnâsi bimâ erâke-llâh. velâ tekül lilḫâinîne ḫaṣîmâ.');
INSERT INTO `tr_transliteration` VALUES (599, 'vestagfiri-llâh. inne-llâhe kâne gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (600, 'velâ tücâdil \\`ani-lleẕîne yaḫtânûne enfüsehüm. inne-llâhe lâ yüḥibbü men kâne ḫavvânen eŝîmâ.');
INSERT INTO `tr_transliteration` VALUES (601, 'yestaḫfûne mine-nnâsi velâ yestaḫfûne mine-llâhi vehüve me\\`ahüm iẕ yübeyyitûne mâ lâ yerḍâ mine-lḳavl. vekâne-llâhü bimâ ya\\`melûne müḥîṭâ.');
INSERT INTO `tr_transliteration` VALUES (602, 'hâentüm hâülâi câdeltüm \\`anhüm fi-lḥayâti-ddünyâ femey yücâdilü-llâhe \\`anhüm yevme-lḳiyâmeti em mey yekûnü \\`aleyhim vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (603, 'vemey ya\\`mel sûen ev yażlim nefsehû ŝümme yestagfiri-llâhe yecidi-llâhe gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (604, 'vemey yeksib iŝmen feinnemâ yeksibühû \\`alâ nefsih. vekâne-llâhü \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (605, 'vemey yeksib ḫaṭîeten ev iŝmen ŝümme yermi bihî berîen feḳadi-ḥtemele bühtânev veiŝmem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (606, 'velevlâ faḍlü-llâhi \\`aleyke veraḥmetühû lehemmeṭ ṭâifetüm minhüm ey yüḍillûk. vemâ yüḍillûne illâ enfüsehüm vemâ yeḍurrûneke min şey\'. veenzele-llâhü \\`aleyke-lkitâbe velḥikmete ve\\`allemeke mâ lem tekün ta\\`lem. vekâne faḍlü-llâhi \\`aleyke \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (607, 'lâ ḫayra fî keŝîrim min necvâhüm illâ men emera biṣadeḳatin ev ma\\`rûfin ev iṣlâḥim beyne-nnâs. vemey yef\\`al ẕâlike-btigâe merḍâti-llâhi fesevfe nü\'tîhi ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (608, 'vemey yüşâḳiḳi-rrasûle mim ba\\`di mâ tebeyyene lehü-lhüdâ veyettebi\\` gayra sebîli-lmü\'minîne nüvellihî mâ tevellâ venuṣlihî cehennem. vesâet meṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (609, 'inne-llâhe lâ yagfiru ey yüşrake bihî veyagfiru mâ dûne ẕâlike limey yeşâ\'. vemey yüşrik billâhi feḳad ḍalle ḍalâlem be\\`îdâ.');
INSERT INTO `tr_transliteration` VALUES (610, 'iy yed\\`ûne min dûnihî illâ inâŝâ. veiy yed\\`ûne illâ şeyṭânem merîdâ.');
INSERT INTO `tr_transliteration` VALUES (611, 'le\\`anehü-llâh. veḳâle leetteḫiẕenne min \\`ibâdike neṣîbem mefrûḍâ.');
INSERT INTO `tr_transliteration` VALUES (612, 'veleüḍillennehüm veleümenniyennehüm veleâmürannehüm feleyübettikünne âẕâne-l\'en\\`âmi veleâmürannehüm feleyügayyirunne ḫalḳa-llâh. vemey yetteḫiẕi-şşeyṭâne veliyyem min dûni-llâhi feḳad ḫasira ḫusrânem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (613, 'ye\\`idühüm veyümennîhim. vemâ ye\\`idühümü-şşeyṭânü illâ gurûrâ.');
INSERT INTO `tr_transliteration` VALUES (614, 'ülâike me\'vâhüm cehennemü velâ yecidûne \\`anhâ meḥîṣâ.');
INSERT INTO `tr_transliteration` VALUES (615, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti senüdḫilühüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. va\\`de-llâhi ḥaḳḳâ. vemen aṣdeḳu mine-llâhi ḳîlâ.');
INSERT INTO `tr_transliteration` VALUES (616, 'leyse biemâniyyiküm velâ emâniyyi ehli-lkitâb. mey ya\\`mel sûey yücze bihî velâ yecid lehû min dûni-llâhi veliyyev velâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (617, 'vemey ya\\`mel mine-ṣṣâliḥâti min ẕekerin ev ünŝâ vehüve mü\'minün feülâike yedḫulûne-lcennete velâ yużlemûne neḳîrâ.');
INSERT INTO `tr_transliteration` VALUES (618, 'vemen aḥsenü dînem mimmen esleme vechehû lillâhi vehüve muḥsinüv vettebe\\`a millete ibrâhîme ḥanîfâ. vetteḫaẕe-llâhü ibrâhîme ḫalîlâ.');
INSERT INTO `tr_transliteration` VALUES (619, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekâne-llâhü bikülli şey\'im müḥîṭâ.');
INSERT INTO `tr_transliteration` VALUES (620, 'veyesteftûneke fi-nnisâ\'. ḳuli-llâhü yüftîküm fîhinne vemâ yütlâ \\`aleyküm fi-lkitâbi fî yetâme-nnisâi-llâtî lâ tü\'tûnehünne mâ kütibe lehünne vetergabûne en tenkiḥûhünne velmüstaḍ\\`afîne mine-lvildâni veen teḳûmû lilyetâmâ bilḳisṭ. vemâ tef\\`alû min ḫayrin feinne-llâhe kâne bihî \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (621, 'veini-mraetün ḫâfet mim ba\\`lihâ nüşûzen ev i\\`râḍan felâ cünâḥa \\`aleyhimâ ey yuṣliḥâ beynehümâ ṣulḥâ. veṣṣulḥu ḫayr. veuḥḍirati-l\'enfüsü-şşüḥḥ. vein tuḥsinû vetetteḳû feinne-llâhe kâne bimâ ta\\`melûne ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (622, 'velen testeṭî\\`û en ta\\`dilû beyne-nnisâi velev ḥaraṣtüm felâ temîlû külle-lmeyli feteẕerûhâ kelmü\\`alleḳah. vein tuṣliḥû vetetteḳû feinne-llâhe kâne gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (623, 'veiy yeteferraḳâ yugni-llâhü küllem min se\\`atih. vekâne-llâhü vâsi\\`an ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (624, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. veleḳad veṣṣayne-lleẕîne ûtü-lkitâbe min ḳabliküm veiyyâküm eni-tteḳu-llâh. vein tekfürû feinne lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekâne-llâhü ganiyyen ḥamîdâ.');
INSERT INTO `tr_transliteration` VALUES (625, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekefâ billâhi vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (626, 'iy yeşe\' yüẕhibküm eyyühe-nnâsü veye\'ti biâḫarîn. vekâne-llâhü \\`alâ ẕâlike ḳadîrâ.');
INSERT INTO `tr_transliteration` VALUES (627, 'men kâne yürîdü ŝevâbe-ddünyâ fe\\`inde-llâhi ŝevâbü-ddünyâ vel\'âḫirah. vekâne-llâhü semî\\`am beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (628, 'yâ eyyühe-lleẕîne âmenû kûnû ḳavvâmîne bilḳisṭi şühedâe lillâhi velev \\`alâ enfüsiküm evi-lvâlideyni vel\'aḳrabîn. iy yekün ganiyyen ev feḳîran fellâhü evlâ bihimâ felâ tettebi\\`ü-lhevâ en ta\\`dilû. vein telvû ev tü\\`riḍû feinne-llâhe kâne bimâ ta\\`melûne ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (629, 'yâ eyyühe-lleẕîne âmenû âminû billâhi verasûlihî velkitâbi-lleẕî nezzele \\`alâ rasûlihî velkitâbi-lleẕî enzele min ḳabl. vemey yekfür billâhi vemelâiketihî vekütübihî verusülihî velyevmi-l\'âḫiri feḳad ḍalle ḍalâlem be\\`îdâ.');
INSERT INTO `tr_transliteration` VALUES (630, 'inne-lleẕîne âmenû ŝümme keferû ŝümme âmenû ŝümme keferû ŝümme-zdâdû küfral lem yeküni-llâhü liyagfira lehüm velâ liyehdiyehüm sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (631, 'beşşiri-lmünâfiḳîne bienne lehüm \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (632, 'elleẕîne yetteḫiẕûne-lkâfirîne evliyâe min dûni-lmü\'minîn. eyebtegûne \\`indehümü-l\\`izzete feinne-l\\`izzete lillâhi cemî\\`â.');
INSERT INTO `tr_transliteration` VALUES (633, 'veḳad nezzele \\`aleyküm fi-lkitâbi en iẕâ semi\\`tüm âyâti-llâhi yükferu bihâ veyüstehzeü bihâ felâ taḳ\\`udû me\\`ahüm ḥattâ yeḫûḍû fî ḥadîŝin gayrihi. inneküm iẕem miŝlühüm. inne-llâhe câmi\\`u-lmünâfiḳîne velkâfirîne fî cehenneme cemî\\`â.');
INSERT INTO `tr_transliteration` VALUES (634, 'elleẕîne yeterabbeṣûne biküm. fein kâne leküm fetḥum mine-llâhi ḳâlû elem neküm me\\`aküm. vein kâne lilkâfirîne neṣîbün ḳâlû elem nestaḥviẕ \\`aleyküm venemna\\`küm mine-lmü\'minîn. fellâhü yaḥkümü beyneküm yevme-lḳiyâmeh. veley yec\\`ale-llâhü lilkâfirîne \\`ale-lmü\'minîne sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (635, 'inne-lmünâfiḳîne yüḫâdi\\`ûne-llâhe vehüve ḫâdi\\`uhüm. veiẕâ ḳâmû ile-ṣṣalâti ḳâmû küsâlâ yürâûne-nnâse velâ yeẕkürûne-llâhe illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (636, 'müẕebẕebîne beyne ẕâlik. lâ ilâ hâülâi velâ ilâ hâülâ\'. vemey yuḍlili-llâhü felen tecide lehû sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (637, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕü-lkâfirîne evliyâe min dûni-lmü\'minîn. etürîdûne en tec\\`alû lillâhi \\`aleyküm sülṭânem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (638, 'inne-lmünâfiḳîne fi-dderki-l\'esfeli mine-nnâr. velen tecide lehüm neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (639, 'ille-lleẕîne tâbû veaṣleḥû va\\`teṣamû billâhi veaḫleṣû dînehüm lillâhi feülâike me\\`a-lmü\'minîn. vesevfe yü\'ti-llâhü-lmü\'minîne ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (640, 'mâ yef\\`alü-llâhü bi\\`aẕâbiküm in şekertüm veâmentüm. vekâne-llâhü şâkiran \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (641, 'lâ yüḥibbü-llâhü-lcehra bissûi mine-lḳavli illâ men żulim. vekâne-llâhü semî\\`an \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (642, 'in tübdû ḫayran ev tuḫfûhü ev ta\\`fû \\`an sûin feinne-llâhe kâne \\`afüvven ḳadîrâ.');
INSERT INTO `tr_transliteration` VALUES (643, 'inne-lleẕîne yekfürûne billâhi verusülihî veyürîdûne ey yüferriḳû beyne-llâhi verusülihî veyeḳûlûne nü\'minü biba\\`ḍiv venekfüru biba\\`ḍiv veyürîdûne ey yetteḫiẕû beyne ẕâlike sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (644, 'ülâike hümü-lkâfirûne ḥaḳḳâ. vea\\`tednâ lilkâfirîne \\`aẕâbem mühînâ.');
INSERT INTO `tr_transliteration` VALUES (645, 'velleẕîne âmenû billâhi verusülihî velem yüferriḳû beyne eḥadim minhüm ülâike sevfe yü\'tîhim ücûrahüm. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (646, 'yes\'elüke ehlü-lkitâbi en tünezzile \\`aleyhim kitâbem mine-ssemâi feḳad seelû mûsâ ekbera min ẕâlike feḳâlû erine-llâhe cehraten feeḫaẕethümu-ṣṣâ\\`iḳatü biżulmihim. ŝümme-tteḫaẕü-l\\`icle mim ba\\`di mâ câethümü-lbeyyinâtü fe\\`afevnâ \\`an ẕâlik. veâteynâ mûsâ sülṭânem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (647, 'verafa\\`nâ fevḳahümu-ṭṭûra bimîŝâḳihim veḳulnâ lehümü-dḫulü-lbâbe süccedev veḳulnâ lehüm lâ ta\\`dû fi-ssebti veeḫaẕnâ minhüm mîŝâḳan galîżâ.');
INSERT INTO `tr_transliteration` VALUES (648, 'febimâ naḳḍihim mîŝâḳahüm veküfrihim biâyâti-llâhi veḳatlihimü-l\'embiyâe bigayri ḥaḳḳiv veḳavlihim ḳulûbünâ gulf. bel ṭabe\\`a-llâhü \\`aleyhâ biküfrihim felâ yü\'minûne illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (649, 'vebiküfrihim veḳavlihim \\`alâ meryeme bühtânen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (650, 'veḳavlihim innâ ḳatelne-lmesîḥa \\`îse-bne meryeme rasûle-llâh. vemâ ḳatelûhü vemâ ṣalebûhü velâkin şübbihe lehüm. veinne-lleẕîne-ḫtelefû fîhi lefî şekkim minh. mâ lehüm bihî min \\`ilmin ille-ttibâ\\`a-żżann. vemâ ḳatelûhü yeḳînâ.');
INSERT INTO `tr_transliteration` VALUES (651, 'ber rafe\\`ahü-llâhü ileyh. vekâne-llâhü \\`azîzen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (652, 'veim min ehli-lkitâbi illâ leyü\'minenne bihî ḳable mevtih. veyevme-lḳiyâmeti yekûnü \\`aleyhim şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (653, 'febiżulmim mine-lleẕîne hâdû ḥarramnâ \\`aleyhim ṭayyibâtin üḥillet lehüm vebiṣaddihim \\`an sebîli-llâhi keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (654, 'veaḫẕihimü-rribâ veḳad nühû \\`anhü veeklihim emvâle-nnâsi bilbâṭil. vea\\`tednâ lilkâfirîne minhüm \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (655, 'lâkini-rrâsiḫûne fi-l\\`ilmi minhüm velmü\'minûne yü\'minûne bimâ ünzile ileyke vemâ ünzile min ḳablike velmüḳîmîne-ṣṣalâte velmü\'tûne-zzekâte velmü\'minûne billâhi velyevmi-l\'âḫir. ülâike senü\'tîhim ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (656, 'innâ evḥaynâ ileyke kemâ evḥaynâ ilâ nûḥiv vennebiyyîne mim ba\\`dih. veevḥaynâ ilâ ibrâhîme veismâ\\`île veisḥâḳa veya\\`ḳûbe vel\'esbâṭi ve\\`îsâ veeyyûbe veyûnüse vehârûne vesüleymân. veâteynâ dâvûde zebûrâ.');
INSERT INTO `tr_transliteration` VALUES (657, 'verusülen ḳad ḳaṣaṣnâhüm \\`aleyke min ḳablü verusülel lem naḳṣuṣhüm \\`aleyk. vekelleme-llâhü mûsâ teklîmâ.');
INSERT INTO `tr_transliteration` VALUES (658, 'rusülem mübeşşirîne vemünẕirîne liellâ yekûne linnâsi \\`ale-llâhi ḥuccetüm ba\\`de-rrusül. vekâne-llâhü \\`azîzen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (659, 'lâkini-llâhü yeşhedü bimâ enzele ileyke enzelehû bi\\`ilmih. velmelâiketü yeşhedûn. vekefâ billâhi şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (660, 'inne-lleẕîne keferû veṣaddû \\`an sebîli-llâhi ḳad ḍallû ḍalâlem be\\`îdâ.');
INSERT INTO `tr_transliteration` VALUES (661, 'inne-lleẕîne keferû veżalemû lem yeküni-llâhü liyagfira lehüm velâ liyehdiyehüm ṭarîḳâ.');
INSERT INTO `tr_transliteration` VALUES (662, 'illâ ṭarîḳa cehenneme ḫâlidîne fîhâ ebedâ. vekâne ẕâlike \\`ale-llâhi yesîrâ.');
INSERT INTO `tr_transliteration` VALUES (663, 'yâ eyyühe-nnâsü ḳad câekümü-rrasûlü bilḥaḳḳi mir rabbiküm feâminû ḫayral leküm. vein tekfürû feinne lillâhi mâ fi-ssemâvâti vel\'arḍ. vekâne-llâhü \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (664, 'yâ ehle-lkitâbi lâ taglû fî dîniküm velâ teḳûlû \\`ale-llâhi ille-lḥaḳḳ. inneme-lmesîḥu \\`îse-bnü meryeme rasûlü-llâhi vekelimetüh. elḳâhâ ilâ meryeme verûḥum minh. feâminû billâhi verusülih. velâ teḳûlû ŝelâŝeh. intehû ḫayral leküm. inneme-llâhü ilâhüv vâḥid. sübḥâneh ey yekûne lehû veled. lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekefâ billâhi vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (665, 'ley yestenkife-lmesîḥu ey yekûne \\`abdel lillâhi vele-lmelâiketü-lmüḳarrabûn. vemey yestenkif \\`an \\`ibâdetihî veyestekbir feseyaḥşüruhüm ileyhi cemî\\`â.');
INSERT INTO `tr_transliteration` VALUES (666, 'feemme-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti feyüveffîhim ücûrahüm veyezîdühüm min faḍlih. veemme-lleẕîne-stenkefû vestekberû feyü\\`aẕẕibühüm \\`aẕâben elîmev velâ yecidûne lehüm min dûni-llâhi veliyyev velâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (667, 'yâ eyyühe-nnâsü ḳad câeküm bürhânüm mir rabbiküm veenzelnâ ileyküm nûram mübînâ.');
INSERT INTO `tr_transliteration` VALUES (668, 'feemme-lleẕîne âmenû billâhi va\\`teṣamû bihî feseyüdḫilühüm fî raḥmetim minhü vefaḍliv veyehdîhim ileyhi ṣirâṭam müsteḳîmâ.');
INSERT INTO `tr_transliteration` VALUES (669, 'yesteftûnek. ḳuli-llâhü yüftîküm fi-lkelâleh. ini-mruün heleke leyse lehû veledüv velehû uḫtün felehâ niṣfü mâ terak. vehüve yeriŝühâ il lem yekül lehâ veledün. fein kânete-ŝneteyni felehüme-ŝŝülüŝâni mimmâ terak. vein kânû iḫveter ricâlev venisâen feliẕẕekeri miŝlü ḥażżi-l\'ünŝeyeyn. yübeyyinü-llâhü leküm en teḍillû. vellâhü bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (670, 'yâ eyyühe-lleẕîne âmenû evfû bil\\`uḳûd. üḥillet leküm behîmetü-l\'en\\`âmi illâ mâ yütlâ \\`aleyküm gayra müḥilli-ṣṣaydi veentüm ḥurum. inne-llâhe yaḥkümü mâ yürîd.');
INSERT INTO `tr_transliteration` VALUES (671, 'yâ eyyühe-lleẕîne âmenû lâ tüḥillû şe\\`âira-llâhi vele-şşehra-lḥarâme vele-lhedye vele-lḳalâide velâ âmmîne-lbeyte-lḥarâme yebtegûne faḍlem mir rabbihim veriḍvânâ. veiẕâ ḥaleltüm faṣṭâdû. velâ yecrimenneküm şeneânü ḳavmin en ṣaddûküm \\`ani-lmescidi-lḥarâmi en ta\\`tedû. vete\\`âvenû \\`ale-lbirri vettaḳvâ. velâ te\\`âvenû \\`ale-l\'iŝmi vel\\`udvân. vetteḳu-llâh. inne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (672, 'ḥurrimet \\`aleykümü-lmeytetü veddemü velaḥmü-lḫinzîri vemâ ühille ligayri-llâhi bihî velmünḫaniḳatü velmevḳûẕetü velmüteraddiyetü venneṭîḥatü vemâ ekele-ssebü\\`u illâ mâ ẕekkeytüm vemâ ẕübiḥa \\`ale-nnüṣubi veen testaḳsimû bil\'ezlâm. ẕâliküm fisḳ. elyevme yeise-lleẕîne keferû min dîniküm felâ taḫşevhüm vaḫşevn. elyevme ekmeltü leküm dîneküm veetmemtü \\`aleyküm ni\\`metî veraḍîtü lekümü-l\'islâme dînâ. femeni-ḍṭurra fî maḫmeṣatin gayra mütecânifil liiŝmin feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (673, 'yes\'elûneke mâẕâ üḥille lehüm. ḳul üḥille lekümu-ṭṭayyibâtü vemâ \\`allemtüm mine-lcevâriḥi mükellibîne tü\\`allimûnehünne mimmâ \\`allemekümü-llâh. fekülû mimmâ emsekne \\`aleyküm veẕkürü-sme-llâhi \\`aleyh. vetteḳu-llâh. inne-llâhe serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (674, 'elyevme üḥille lekümu-ṭṭayyibât. veṭa\\`âmü-lleẕîne ûtü-lkitâbe ḥillül leküm. veṭa\\`âmüküm ḥillül lehüm. velmuḥṣanâtü mine-lmü\'minâti velmuḥṣanâtü mine-lleẕîne ûtü-lkitâbe min ḳabliküm iẕâ âteytümûhünne ücûrahünne muḥṣinîne gayra müsâfiḥîne velâ mütteḫiẕî aḫdân. vemey yekfür bil\'îmâni feḳad ḥabiṭa \\`amelüh. vehüve fi-l\'âḫirati mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (675, 'yâ eyyühe-lleẕîne âmenû iẕâ ḳumtüm ile-ṣṣalâti fagsilû vucûheküm veeydiyeküm ile-lmerâfiḳi vemseḥû biruûsiküm veercüleküm ile-lka\\`beyn. vein küntüm cünüben feṭṭahherû. vein küntüm merḍâ ev \\`alâ seferin ev câe eḥadüm minküm mine-lgâiṭi ev lemestümü-nnisâe felem tecidû mâen feteyemmemû ṣa\\`îden ṭayyiben femseḥû bivucûhiküm veeydîküm minh. mâ yürîdü-llâhü liyec\\`ale \\`aleyküm min ḥaraciv velâkiy yürîdü liyüṭahhiraküm veliyütimme ni\\`metehû \\`aleyküm le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (676, 'veẕkürû ni\\`mete-llâhi \\`aleyküm vemîŝâḳahü-lleẕî vâŝeḳaküm bihî iẕ ḳultüm semi\\`nâ veeṭa\\`nâ. vetteḳu-llâh. inne-llâhe \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (677, 'yâ eyyühe-lleẕîne âmenû kûnû ḳavvâmîne lillâhi şühedâe bilḳisṭ. velâ yecrimenneküm şeneânü ḳavmin \\`alâ ellâ ta\\`dilû. i\\`dilû. hüve aḳrabü littaḳvâ. vetteḳu-llâh. inne-llâhe ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (678, 've\\`ade-llâhü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lehüm magfiratüv veecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (679, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (680, 'yâ eyyühe-lleẕîne âmenü-ẕkürû ni\\`mete-llâhi \\`aleyküm iẕ hemme ḳavmün ey yebsüṭû ileyküm eydiyehüm fekeffe eydiyehüm \\`anküm. vetteḳu-llâh. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (681, 'veleḳad eḫaẕe-llâhü mîŝâḳa benî isrâîl. vebe\\`aŝnâ minhümü-ŝney \\`aşera neḳîbâ. veḳâle-llâhü innî me\\`aküm. lein eḳamtümu-ṣṣalâte veâteytümü-zzekâte veâmentüm birusülî ve\\`azzertümûhüm veaḳraḍtümü-llâhe ḳarḍan ḥasenel leükeffiranne \\`anküm seyyiâtiküm veleüdḫilenneküm cennâtin tecrî min taḥtihe-l\'enhâr. femen kefera ba\\`de ẕâlike minküm feḳad ḍalle sevâe-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (682, 'febimâ naḳḍihim mîŝâḳahüm le\\`annâhüm vece\\`alnâ ḳulûbehüm ḳâsiyeh. yüḥarrifûne-lkelime \\`am mevâḍi\\`ihî venesû ḥażżam mimmâ ẕükkirû bih. velâ tezâlü teṭṭali\\`u \\`alâ ḫâinetim minhüm illâ ḳalîlem minhüm fa\\`fü \\`anhüm vaṣfaḥ. inne-llâhe yüḥibbü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (683, 'vemine-lleẕîne ḳâlû innâ neṣârâ eḫaẕnâ mîŝâḳahüm fenesû ḥażżam mimmâ ẕükkirû bih. feagraynâ beynehümü-l\\`adâvete velbagḍâe ilâ yevmi-lḳiyâmeh. vesevfe yünebbiühümü-llâhü bimâ kânû yaṣne\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (684, 'yâ ehle-lkitâbi ḳad câeküm rasûlünâ yübeyyinü leküm keŝîram mimmâ küntüm tuḫfûne mine-lkitâbi veya\\`fû \\`an keŝîr. ḳad câeküm mine-llâhi nûruv vekitâbüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (685, 'yehdî bihi-llâhü meni-ttebe\\`a riḍvânehû sübüle-sselâmi veyuḫricühüm mine-żżulümâti ile-nnûri biiẕnihî veyehdîhim ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (686, 'leḳad kefera-lleẕîne ḳâlû inne-llâhe hüve-lmesîḥu-bnü meryem. ḳul femey yemlikü mine-llâhi şey\'en in erâde ey yühlike-lmesîḥa-bne meryeme veümmehû vemen fi-l\'arḍi cemî\\`â. velillâhi mülkü-ssemâvâti vel\'arḍi vemâ beynehümâ. yaḫlüḳu mâ yeşâ\'. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (687, 'veḳâleti-lyehûdü venneṣârâ naḥnü ebnâü-llâhi veeḥibbâüh. ḳul felime yü\\`aẕẕibüküm biẕünûbiküm. bel entüm beşerum mimmen ḫaleḳ. yagfiru limey yeşâü veyü\\`aẕẕibü mey yeşâ\'. velillâhi mülkü-ssemâvâti vel\'arḍi vemâ beynehümâ. veileyhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (688, 'yâ ehle-lkitâbi ḳad câeküm rasûlünâ yübeyyinü leküm \\`alâ fetratim mine-rrusüli en teḳûlû mâ câenâ mim beşîriv velâ neẕîr. feḳad câeküm beşîruv veneẕîr. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (689, 'veiẕ ḳâle mûsâ liḳavmihî yâ ḳavmi-ẕkürû ni\\`mete-llâhi \\`aleyküm iẕ ce\\`ale fîküm embiyâe vece\\`aleküm mülûkâ. veâtâküm mâ lem yü\'ti eḥadem mine-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (690, 'yâ ḳavmi-dḫulü-l\'arḍa-lmüḳaddesete-lletî ketebe-llâhü leküm velâ terteddû \\`alâ edbâriküm fetenḳalibû ḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (691, 'ḳâlû yâ mûsâ inne fîhâ ḳavmen cebbârîn. veinnâ len nedḫulehâ ḥattâ yaḫrucû minhâ. feiy yaḫrucû minhâ feinnâ dâḫilûn.');
INSERT INTO `tr_transliteration` VALUES (692, 'ḳâle racülâni mine-lleẕîne yeḫâfûne en\\`ame-llâhü \\`aleyhime-dḫulû \\`aleyhimü-lbâb. feiẕâ deḫaltümûhü feinneküm gâlibûne ve\\`ale-llâhi fetevekkelû in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (693, 'ḳâlû yâ mûsâ innâ len nedḫulehâ ebedem mâ dâmû fîhâ feẕheb ente verabbüke feḳâtilâ innâ hâhünâ ḳâ\\`idûn.');
INSERT INTO `tr_transliteration` VALUES (694, 'ḳâle rabbi innî lâ emlikü illâ nefsî veeḫî fefruḳ beynenâ vebeyne-lḳavmi-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (695, 'ḳâle feinnehâ müḥarrametün \\`aleyhim erbe\\`îne seneh. yetîhûne fi-l\'arḍi felâ te\'se \\`ale-lḳavmi-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (696, 'vetlü \\`aleyhim nebee-bney âdeme bilḥaḳḳ. iẕ ḳarrabâ ḳurbânen fetüḳubbile min eḥadihimâ velem yüteḳabbel mine-l\'âḫar. ḳâle leaḳtülennek. ḳâle innemâ yeteḳabbelü-llâhü mine-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (697, 'leim besaṭte ileyye yedeke litaḳtülenî mâ ene bibâsiṭiy yediye ileyke liaḳtülek. innî eḫâfü-llâhe rabbe-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (698, 'innî ürîdü en tebûe biiŝmî veiŝmike fetekûne min aṣḥâbi-nnâr. veẕâlike cezâu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (699, 'feṭavve\\`at lehû nefsühû ḳatle eḫîhi feḳatelehû feaṣbeḥa mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (700, 'febe\\`aŝe-llâhü gurâbey yebḥaŝü fi-l\'arḍi liyüriyehû keyfe yüvârî sev\'ete eḫîh. ḳâle yâ veyletâ e\\`aceztü en ekûne miŝle hâẕe-lgurâbi feüvâriye sev\'ete eḫî. feaṣbeḥa mine-nnâdimîn.');
INSERT INTO `tr_transliteration` VALUES (701, 'min ecli ẕâlik. ketebnâ \\`alâ benî isrâîle ennehû men ḳatele nefsem bigayri nefsin ev fesâdin fi-l\'arḍi fekeennemâ ḳatele-nnâse cemî\\`â. vemen aḥyâhâ fekeennemâ aḥye-nnâse cemî\\`â. veleḳad câethüm rusülünâ bilbeyyinât. ŝümme inne keŝîram minhüm ba\\`de ẕâlike fi-l\'arḍi lemüsrifûn.');
INSERT INTO `tr_transliteration` VALUES (702, 'innemâ cezâü-lleẕîne yüḥâribûne-llâhe verasûlehû veyes\\`avne fi-l\'arḍi fesâden ey yüḳattelû ev yüṣallebû ev tüḳaṭṭa\\`a eydîhim veercülühüm min ḫilâfin ev yünfev mine-l\'arḍ. ẕâlike lehüm ḫizyün fi-ddünyâ velehüm fi-l\'âḫirati \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (703, 'ille-lleẕîne tâbû min ḳabli en taḳdirû \\`aleyhim. fa\\`lemû enne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (704, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe vebtegû ileyhi-lvesîlete vecâhidû fî sebîlihî le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (705, 'inne-lleẕîne keferû lev enne lehüm mâ fi-l\'arḍi cemî\\`av vemiŝlehû me\\`ahû liyeftedû bihî min \\`aẕâbi yevmi-lḳiyâmeti mâ tüḳubbile minhüm. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (706, 'yürîdûne ey yaḫrucû mine-nnâri vemâ hüm biḫâricîne minhâ. velehüm \\`aẕâbüm müḳîm.');
INSERT INTO `tr_transliteration` VALUES (707, 'vessâriḳu vessâriḳatü faḳṭa\\`û eydiyehümâ cezâem bimâ kesebâ nekâlem mine-llâh. vellâhü \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (708, 'femen tâbe mim ba\\`di żulmihî veaṣleḥa feinne-llâhe yetûbü \\`aleyh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (709, 'elem ta\\`lem enne-llâhe lehû mülkü-ssemâvâti vel\'arḍi yü\\`aẕẕibü mey yeşâü veyagfiru limey yeşâ\'. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (710, 'yâ eyyühe-rrasûlü lâ yaḥzünke-lleẕîne yüsâri\\`ûne fi-lküfri mine-lleẕîne ḳâlû âmennâ biefvâhihim velem tü\'min ḳulûbühüm. vemine-lleẕîne hâdû semmâ\\`ûne lilkeẕibi semmâ\\`ûne liḳavmin âḫarîne lem ye\'tûk. yüḥarrifûne-lkelime mim ba\\`di mevâḍi\\`ih. yeḳûlûne in ûtîtüm hâẕâ feḫuẕûhü veil lem tü\'tevhü faḥẕerû. vemey yüridi-llâhü fitnetehû felen temlike lehû mine-llâhi şey\'â. ülâike-lleẕîne lem yüridi-llâhü ey yüṭahhira ḳulûbehüm. lehüm fi-ddünyâ ḫizyüv velehüm fi-l\'âḫirati \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (711, 'semmâ\\`ûne lilkeẕibi ekkâlûne lissuḥt. fein câûke faḥküm beynehüm ev a\\`riḍ \\`anhüm. vein tü\\`riḍ \\`anhüm feley yeḍurrûke şey\'â. vein ḥakemte faḥküm beynehüm bilḳisṭ. inne-llâhe yüḥibbü-lmuḳsiṭîn.');
INSERT INTO `tr_transliteration` VALUES (712, 'vekeyfe yüḥakkimûneke ve\\`indehümü-ttevrâtü fîhâ ḥukmü-llâhi ŝümme yetevellevne mim ba\\`di ẕâlik. vemâ ülâike bilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (713, 'innâ enzelne-ttevrâte fîhâ hüdev venûr. yaḥkümü bihe-nnebiyyûne-lleẕîne eslemû lilleẕîne hâdû verrabbâniyyûne vel\'aḥbâru bime-stuḥfiżû min kitâbi-llâhi vekânû \\`aleyhi şühedâ\'. felâ taḫşevu-nnâse vaḫşevni velâ teşterû biâyâtî ŝemenen ḳalîlâ. vemel lem yaḥküm bimâ enzele-llâhü feülâike hümü-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (714, 'veketebnâ \\`aleyhim fîhâ enne-nnefse binnefsi vel\\`ayne bil\\`ayni vel\'enfe bil\'enfi vel\'üẕüne bil\'üẕüni vessinne bissinni velcürûḥa ḳiṣâṣ. femen teṣaddeḳa bihî fehüve keffâratül leh. vemel lem yaḥküm bimâ enzele-llâhü feülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (715, 'veḳaffeynâ \\`alâ âŝârihim bi\\`îse-bni meryeme müṣaddiḳal limâ beyne yedeyhi mine-ttevrât. veâteynâhü-l\'incîle fîhi hüdev venûruv vemüṣaddiḳal limâ beyne yedeyhi mine-ttevrâti vehüdev vemev\\`iżatel lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (716, 'velyaḥküm ehlü-l\'incîli bimâ enzele-llâhü fîh. vemel lem yaḥküm bimâ enzele-llâhü feülâike hümü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (717, 'veenzelnâ ileyke-lkitâbe bilḥaḳḳi müṣaddiḳal limâ beyne yedeyhi mine-lkitâbi vemüheyminen \\`aleyhi faḥküm beynehüm bimâ enzele-llâhü velâ tettebi\\` ehvâehüm \\`ammâ câeke mine-lḥaḳḳ. liküllin ce\\`alnâ minküm şir\\`atev veminhâcâ. velev şâe-llâhü lece\\`aleküm ümmetev vâḥidetev velâkil liyeblüveküm fî mâ âtâküm festebiḳu-lḫayrât. ile-llâhi merci\\`uküm cemî\\`an feyünebbiüküm bimâ küntüm fîhi taḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (718, 'veeni-ḥküm beynehüm bimâ enzele-llâhü velâ tettebi\\` ehvâehüm vaḥẕerhüm ey yeftinûke \\`am ba\\`ḍi mâ enzele-llâhü ileyk. fein tevellev fa\\`lem ennemâ yürîdü-llâhü ey yüṣîbehüm biba\\`ḍi ẕünûbihim. veinne keŝîram mine-nnâsi lefâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (719, 'efeḥukme-lcâhiliyyeti yebgûne. vemen aḥsenü mine-llâhi ḥukmel liḳavmiy yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (720, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕü-lyehûde venneṣârâ evliyâ\'. ba\\`ḍuhüm evliyâü ba\\`ḍ. vemey yetevellehüm minküm feinnehû minhüm. inne-llâhe lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (721, 'fetere-lleẕîne fî ḳulûbihim meraḍuy yüsâri\\`ûne fîhim yeḳûlûne naḫşâ en tüṣîbenâ dâirah. fe\\`ase-llâhü ey ye\'tiye bilfetḥi ev emrim min \\`indihî feyuṣbiḥû \\`alâ mâ eserrû fî enfüsihim nâdimîn.');
INSERT INTO `tr_transliteration` VALUES (722, 'veyeḳûlü-lleẕîne âmenû ehâülâi-lleẕîne aḳsemû billâhi cehde eymânihim innehüm leme\\`aküm. ḥabiṭat a\\`mâlühüm feaṣbeḥû ḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (723, 'yâ eyyühe-lleẕîne âmenû mey yertedde minküm \\`an dînihî fesevfe ye\'ti-llâhü biḳavmiy yüḥibbühüm veyüḥibbûnehû eẕilletin \\`ale-lmü\'minîne e\\`izzetin \\`ale-lkâfirîn. yücâhidûne fî sebîli-llâhi velâ yeḫâfûne levmete lâim. ẕâlike faḍlü-llâhi yü\'tîhi mey yeşâ\'. vellâhü vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (724, 'innemâ veliyyükümü-llâhü verasûlühû velleẕîne âmenü-lleẕîne yüḳîmûne-ṣṣalâte veyü\'tûne-zzekâte vehüm râki\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (725, 'vemey yetevelle-llâhe verasûlehû velleẕîne âmenû feinne ḥizbe-llâhi hümü-lgâlibûn.');
INSERT INTO `tr_transliteration` VALUES (726, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕü-lleẕîne-tteḫaẕû dîneküm hüzüvev vele\\`ibem mine-lleẕîne ûtü-lkitâbe min ḳabliküm velküffâra evliyâ\'. vetteḳu-llâhe in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (727, 'veiẕâ nâdeytüm ile-ṣṣalâti-tteḫaẕûhâ hüzüvev vele\\`ibâ. ẕâlike biennehüm ḳavmül lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (728, 'ḳul yâ ehle-lkitâbi hel tenḳimûne minnâ illâ en âmennâ billâhi vemâ ünzile ileynâ vemâ ünzile min ḳablü veenne ekŝeraküm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (729, 'ḳul hel ünebbiüküm bişerrim min ẕâlike meŝûbeten \\`inde-llâh. mel le\\`anehü-llâhü vegaḍibe \\`aleyhi vece\\`ale minhümü-lḳiradete velḫanâzîra ve\\`abede-ṭṭâgût. ülâike şerrum mekânev veeḍallü \\`an sevâi-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (730, 'veiẕâ câûküm ḳâlû âmennâ veḳad deḫalû bilküfri vehüm ḳad ḫaracû bih. vellâhü a\\`lemü bimâ kânû yektümûn.');
INSERT INTO `tr_transliteration` VALUES (731, 'veterâ keŝîram minhüm yüsâri\\`ûne fi-l\'iŝmi vel\\`udvâni veeklihimü-ssuḥt. lebi\'se mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (732, 'levlâ yenhâhümü-rrabbâniyyûne vel\'aḥbâru \\`an ḳavlihimü-l\'iŝme veeklihimü-ssuḥt. lebi\'se mâ kânû yaṣne\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (733, 'veḳâleti-lyehûdü yedü-llâhi maglûleh. gullet eydîhim velü\\`inû bimâ ḳâlû. bel yedâhü mebsûṭatâni yünfiḳu keyfe yeşâ\'. veleyezîdenne keŝîram minhüm mâ ünzile ileyke mir rabbike ṭugyânev veküfrâ. veelḳaynâ beynehümü-l\\`adâvete velbagḍâe ilâ yevmi-lḳiyâmeh. küllemâ evḳadû nâral lilḥarbi aṭfeehe-llâhü veyes\\`avne fi-l\'arḍi fesâdâ. vellâhü lâ yüḥibbü-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (734, 'velev enne ehle-lkitâbi âmenû vetteḳav lekeffernâ \\`anhüm seyyiâtihim veleedḫalnâhüm cennâti-nne\\`îm.');
INSERT INTO `tr_transliteration` VALUES (735, 'velev ennehüm eḳâmü-ttevrâte vel\'incîle vemâ ünzile ileyhim mir rabbihim leekelû min fevḳihim vemin taḥti ercülihim. minhüm ümmetüm muḳteṣideh. vekeŝîrum minhüm sâe mâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (736, 'yâ eyyühe-rrasûlü bellig mâ ünzile ileyke mir rabbik. veil lem tef\\`al femâ bellagte risâleteh. vellâhü ya\\`ṣimüke mine-nnâs. inne-llâhe lâ yehdi-lḳavme-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (737, 'ḳul yâ ehle-lkitâbi lestüm \\`alâ şey\'in ḥattâ tüḳîmü-ttevrâte vel\'incîle vemâ ünzile ileyküm mir rabbiküm. veleyezîdenne keŝîram minhüm mâ ünzile ileyke mir rabbike ṭugyânev veküfrâ. felâ te\'se \\`ale-lḳavmi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (738, 'inne-lleẕîne âmenû velleẕîne hâdû veṣṣâbiûne venneṣârâ men âmene billâhi velyevmi-l\'âḫiri ve\\`amile ṣâliḥan felâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (739, 'leḳad eḫaẕnâ mîŝâḳa benî isrâîle veerselnâ ileyhim rusülâ. küllemâ câehüm rasûlüm bimâ lâ tehvâ enfüsühüm ferîḳan keẕẕebû veferîḳay yaḳtülûn.');
INSERT INTO `tr_transliteration` VALUES (740, 'veḥasibû ellâ tekûne fitnetün fe\\`amû veṣammû ŝümme tâbe-llâhü \\`aleyhim ŝümme \\`amû veṣammû keŝîrum minhüm. vellâhü beṣîrum bimâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (741, 'leḳad kefera-lleẕîne ḳâlû inne-llâhe hüve-lmesîḥu-bnü meryem. veḳâle-lmesîḥu yâ benî isrâîle-\\`büdü-llâhe rabbî verabbeküm. innehû mey yüşrik billâhi feḳad ḥarrame-llâhü \\`aleyhi-lcennete veme\'vâhü-nnâr. vemâ liżżâlimîne min enṣâr.');
INSERT INTO `tr_transliteration` VALUES (742, 'leḳad kefera-lleẕîne ḳâlû inne-llâhe ŝâliŝü ŝelâŝeh. vemâ min ilâhin illâ ilâhüv vâḥid. veil lem yentehû \\`ammâ yeḳûlûne leyemessenne-lleẕîne keferû minhüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (743, 'efelâ yetûbûne ile-llâhi veyestagfirûneh. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (744, 'me-lmesîḥu-bnü meryeme illâ rasûl. ḳad ḫalet min ḳablihi-rrusül. veümmühû ṣiddîḳah. kânâ ye\'külâni-ṭṭa\\`âm. ünżur keyfe nübeyyinü lehümü-l\'âyâti ŝümme-nżur ennâ yü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (745, 'ḳul eta\\`büdûne min dûni-llâhi mâ lâ yemlikü leküm ḍarrav velâ nef\\`â. vellâhü hüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (746, 'ḳul yâ ehle-lkitâbi lâ taglû fî dîniküm gayra-lḥaḳḳi velâ tettebi\\`û ehvâe ḳavmin ḳad ḍallû min ḳablü veeḍallû keŝîrav veḍallû \\`an sevâi-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (747, 'lü\\`ine-lleẕîne keferû mim benî isrâîle \\`alâ lisâni dâvûde ve\\`îse-bni meryem. ẕâlike bimâ \\`aṣav vekânû ya\\`tedûn.');
INSERT INTO `tr_transliteration` VALUES (748, 'kânû lâ yetenâhevne \\`am münkerin fe\\`alûhü. lebi\'se mâ kânû yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (749, 'terâ keŝîram minhüm yetevellevne-lleẕîne keferû. lebi\'se mâ ḳaddemet lehüm enfüsühüm en seḫiṭa-llâhü \\`aleyhim vefi-l\\`aẕâbi hüm ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (750, 'velev kânû yü\'minûne billâhi vennebiyyi vemâ ünzile ileyhi me-tteḫaẕûhüm evliyâe velâkinne keŝîram minhüm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (751, 'letecidenne eşedde-nnâsi \\`adâvetel lilleẕîne âmenü-lyehûde velleẕîne eşrakû. veletecidenne aḳrabehüm meveddetel lilleẕîne âmenü-lleẕîne ḳâlû innâ neṣârâ. ẕâlike bienne minhüm ḳissîsîne veruhbânev veennehüm lâ yestekbirûn.');
INSERT INTO `tr_transliteration` VALUES (752, 'veiẕâ semi\\`û mâ ünzile ile-rrasûli terâ a\\`yünehüm tefîḍu mine-ddem\\`i mimmâ \\`arafû mine-lḥaḳḳ. yeḳûlûne rabbenâ âmennâ fektübnâ me\\`a-şşâhidîn.');
INSERT INTO `tr_transliteration` VALUES (753, 'vemâ lenâ lâ nü\'minü billâhi vemâ câenâ mine-lḥaḳḳi venaṭme\\`u ey yüdḫilenâ rabbünâ me\\`a-lḳavmi-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (754, 'feeŝâbehümü-llâhü bimâ ḳâlû cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veẕâlike cezâü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (755, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (756, 'yâ eyyühe-lleẕîne âmenû lâ tüḥarrimû ṭayyibâti mâ eḥalle-llâhü leküm velâ ta\\`tedû. inne-llâhe lâ yüḥibbü-lmü\\`tedîn.');
INSERT INTO `tr_transliteration` VALUES (757, 'vekülû mimmâ razeḳakümü-llâhü ḥalâlen ṭayyibâ. vetteḳu-llâhe-lleẕî entüm bihî mü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (758, 'lâ yüâḫiẕükümü-llâhü billagvi fî eymâniküm velâkiy yüâḫiẕüküm bimâ \\`aḳḳattümü-l\'eymân. fekeffâratühû iṭ\\`âmü \\`aşerati mesâkîne min evseṭi mâ tuṭ\\`imûne ehlîküm ev kisvetühüm ev taḥrîru raḳabeh. femel lem yecid feṣiyâmü ŝelâŝeti eyyâm. ẕâlike keffâratü eymâniküm iẕâ ḥaleftüm. vaḥfeżû eymâneküm. keẕâlike yübeyyinü-llâhü leküm âyâtihî le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (759, 'yâ eyyühe-lleẕîne âmenû inneme-lḫamru velmeysiru vel\'enṣâbü vel\'ezlâmü ricsüm min \\`ameli-şşeyṭâni fectenibûhü le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (760, 'innemâ yürîdü-şşeyṭânü ey yûḳi\\`a beynekümü-l\\`adâvete velbagḍâe fi-lḫamri velmeysiri veyeṣuddeküm \\`an ẕikri-llâhi ve\\`ani-ṣṣalâh. fehel entüm müntehûn.');
INSERT INTO `tr_transliteration` VALUES (761, 'veeṭî\\`ü-llâhe veeṭî\\`ü-rrasûle vaḥẕerû. fein tevelleytüm fa\\`lemû ennemâ \\`alâ rasûline-lbelâgu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (762, 'leyse \\`ale-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti cünâḥun fîmâ ṭa\\`imû iẕâ me-tteḳav veâmenû ve\\`amilu-ṣṣâliḥâti ŝümme-tteḳav veâmenû ŝümme-tteḳav veaḥsenû. vellâhü yüḥibbü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (763, 'yâ eyyühe-lleẕîne âmenû leyeblüvennekümü-llâhü bişey\'im mine-ṣṣaydi tenâlühû eydîküm verimâḥuküm liya\\`leme-llâhü mey yeḫâfühû bilgayb. femeni-\\`tedâ ba\\`de ẕâlike felehû \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (764, 'yâ eyyühe-lleẕîne âmenû lâ taḳtülu-ṣṣayde veentüm ḥurum. vemen ḳatelehû minküm müte\\`ammiden fecezâüm miŝlü mâ ḳatele mine-nne\\`ami yaḥkümü bihî ẕevâ \\`adlim minküm hedyem bâliga-lka\\`beti ev keffâratün ṭa\\`âmü mesâkîne ev \\`adlü ẕâlike ṣiyâmel liyeẕûḳa vebâle emrih. \\`afe-llâhü \\`ammâ selef. vemen \\`âde feyenteḳimü-llâhü minh. vellâhü \\`azîzün ẕü-ntiḳâm.');
INSERT INTO `tr_transliteration` VALUES (765, 'üḥille leküm ṣaydü-lbaḥri veṭa\\`âmühû metâ\\`al leküm velissâyyârah. veḥurrime \\`aleyküm ṣaydü-lberri mâ dümtüm ḥurumâ. vetteḳu-llâhe-lleẕî ileyhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (766, 'ce\\`ale-llâhü-lka\\`bete-lbeyte-lḥarâme ḳiyâmel linnâsi veşşehra-lḥarâme velhedye velḳalâid. ẕâlike lita\\`lemû enne-llâhe ya\\`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍi veenne-llâhe bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (767, 'i\\`lemû enne-llâhe şedîdü-l\\`iḳâbi veenne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (768, 'mâ \\`ale-rrasûli ille-lbelâg. vellâhü ya\\`lemü mâ tübdûne vemâ tektümûn.');
INSERT INTO `tr_transliteration` VALUES (769, 'ḳul lâ yestevi-lḫabîŝü veṭṭayyibü velev a\\`cebeke keŝratü-lḫabîŝ. fetteḳu-llâhe yâ ûli-l\'elbâbi le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (770, 'yâ eyyühe-lleẕîne âmenû lâ tes\'elû \\`an eşyâe in tübde leküm tesü\'küm. vein tes\'elû \\`anhâ ḥîne yünezzelü-lḳur\'ânü tübde leküm. \\`afe-llâhü \\`anhâ. vellâhü gafûrun ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (771, 'ḳad seelehâ ḳavmüm min ḳabliküm ŝümme aṣbeḥû bihâ kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (772, 'mâ ce\\`ale-llâhü mim beḥîrativ velâ sâibetiv velâ veṣîletiv velâ ḥâmiv velâkinne-lleẕîne keferû yefterûne \\`ale-llâhi-lkeẕib. veekŝeruhüm lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (773, 'veiẕâ ḳîle lehüm te\\`âlev ilâ mâ enzele-llâhü veile-rrasûli ḳâlû ḥasbünâ mâ vecednâ \\`aleyhi âbâenâ. evelev kâne âbâühüm lâ ya\\`lemûne şey\'ev velâ yehtedûn.');
INSERT INTO `tr_transliteration` VALUES (774, 'yâ eyyühe-lleẕîne âmenû \\`aleyküm enfüseküm. lâ yeḍurruküm men ḍalle iẕe-htedeytüm. ile-llâhi merci\\`uküm cemî\\`an feyünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (775, 'yâ eyyühe-lleẕîne âmenû şehâdetü beyniküm iẕâ ḥaḍara eḥadekümü-lmevtü ḥîne-lveṣiyyeti-ŝnâni ẕevâ \\`adlim minküm ev âḫarâni min gayriküm in entüm ḍarabtüm fi-l\'arḍi feeṣâbetküm müṣîbtü-lmevt. taḥbisûnehümâ mim ba\\`di-ṣṣalâti feyuḳsimâni billâhi ini-rtebtüm lâ neşterî bihî ŝemenev velev kâne ẕâ ḳurbâ velâ nektümü şehâdete-llâhi innâ iẕel lemine-l\'âŝimîn.');
INSERT INTO `tr_transliteration` VALUES (776, 'fein \\`uŝira \\`alâ ennehüme-steḥaḳḳâ iŝmen feâḫarâni yeḳûmâni meḳâmehümâ mine-lleẕîne-steḥaḳḳa \\`aleyhimü-l\'evleyâni feyuḳsimâni billâhi leşehâdetünâ eḥaḳḳu min şehâdetihimâ veme-\\`tedeynâ. innâ iẕel lemine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (777, 'ẕâlike ednâ ey ye\'tû bişşehâdeti \\`alâ vechihâ ev yeḫâfû en türadde eymânüm ba\\`de eymânihim. vetteḳu-llâhe vesme\\`û. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (778, 'yevme yecme\\`u-llâhü-rrusüle feyeḳûlü mâẕâ ücibtüm. ḳâlû lâ \\`ilme lenâ. inneke ente \\`allâmü-lguyûb.');
INSERT INTO `tr_transliteration` VALUES (779, 'iẕ ḳâle-llâhü yâ \\`îse-bne meryeme-ẕkür ni\\`metî \\`aleyke ve\\`alâ vâlidetik. iẕ eyyettüke birûḥi-lḳudüsi tükellimü-nnâse fi-lmehdi vekehlâ. veiẕ \\`allemtüke-lkitâbe velḥikmete vettevrâte vel\'incîl. veiẕ taḫlüḳu mine-ṭṭîni kehey\'eti-ṭṭayri biiẕnî fetenfüḫu fîhâ fetekûnü ṭayram biiẕnî vetübriü-l\'ekmehe vel\'ebraṣa biiẕnî. veiẕ tuḫricü-lmevtâ biiẕnî. veiẕ kefeftü benî isrâîle \\`anke iẕ ci\'tehüm bilbeyyinâti feḳâle-lleẕîne keferû minhüm in hâẕâ illâ siḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (780, 'veiẕ evḥaytü ile-lḥavâriyyîne en âminû bî vebirasûlî. ḳâlû âmennâ veşhed biennenâ müslimûn.');
INSERT INTO `tr_transliteration` VALUES (781, 'iẕ ḳâle-lḥavâriyyûne yâ \\`îse-bne meryeme hel yesteṭî\\`u rabbüke ey yünezzile \\`aleynâ mâidetem mine-ssemâ\'. ḳâle-tteḳu-llâhe in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (782, 'ḳâlû nürîdü en ne\'küle minhâ vetaṭmeinne ḳulûbünâ vena\\`leme en ḳad ṣadaḳtenâ venekûne \\`aleyhâ mine-şşâhidîn.');
INSERT INTO `tr_transliteration` VALUES (783, 'ḳâle \\`îse-bnü meryeme-llâhümme rabbenâ enzil \\`aleynâ mâidetem mine-ssemâi tekûnü lenâ \\`îdel lievvelinâ veâḫirinâ veâyetem mink. verzuḳnâ veente ḫayru-rrâziḳîn.');
INSERT INTO `tr_transliteration` VALUES (784, 'ḳâle-llâhü innî münezzilühâ \\`aleyküm. femey yekfür ba\\`dü minküm feinnî ü\\`aẕẕibühû \\`aẕâbel lâ ü\\`aẕẕibühû eḥadem mine-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (785, 'veiẕ ḳâle-llâhü yâ \\`îse-bne meryeme eente ḳulte linnâsi-tteḫiẕûnî veümmiye ilâheyni min dûni-llâh. ḳâle sübḥâneke mâ yekûnü lî en eḳûle mâ leyse lî biḥaḳḳ. in küntü ḳultühû feḳad \\`alimteh. ta\\`lemü mâ fî nefsî velâ a\\`lemü mâ fî nefsik. inneke ente \\`allâmü-lguyûb.');
INSERT INTO `tr_transliteration` VALUES (786, 'mâ ḳultü lehüm illâ mâ emertenî bihî eni-\\`büdü-llâhe rabbî verabbeküm. veküntü \\`aleyhim şehîdem mâ dümtü fîhim. felemmâ teveffeytenî künte ente-rraḳîbe \\`aleyhim. veente \\`alâ külli şey\'in şehîd.');
INSERT INTO `tr_transliteration` VALUES (787, 'in tü\\`aẕẕibhüm feinnehüm \\`ibâdük. vein tagfir lehüm feinneke ente-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (788, 'ḳâle-llâhü hâẕâ yevmü yenfe\\`u-ṣṣâdiḳîne ṣidḳuhüm. lehüm cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. raḍiye-llâhü \\`anhüm veraḍû \\`anh. ẕâlike-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (789, 'lillâhi mülkü-ssemâvâti vel\'arḍi vemâ fîhinn. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (790, 'elḥamdü lillâhi-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa vece\\`ale-żżulümâti vennûr. ŝümme-lleẕîne keferû birabbihim ya\\`dilûn.');
INSERT INTO `tr_transliteration` VALUES (791, 'hüve-lleẕî ḫaleḳaküm min ṭînin ŝümme ḳaḍâ ecelâ. veecelüm müsemmen \\`indehû ŝümme entüm temterûn.');
INSERT INTO `tr_transliteration` VALUES (792, 'vehüve-llâhü fi-ssemâvâti vefi-l\'arḍ. ya\\`lemü sirraküm vecehraküm veya\\`lemü mâ teksibûn.');
INSERT INTO `tr_transliteration` VALUES (793, 'vemâ te\'tîhim min âyetim min âyâti rabbihim illâ kânû \\`anhâ mü\\`riḍîn.');
INSERT INTO `tr_transliteration` VALUES (794, 'feḳad keẕẕebû bilḥaḳḳi lemmâ câehüm. fesevfe ye\'tîhim embâü mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (795, 'elem yerav kem ehleknâ min ḳablihim min ḳarnim mekkennâhüm fi-l\'arḍi mâ lem nümekkil leküm veerselne-ssemâe \\`aleyhim midrârâ. vece\\`alne-l\'enhâra tecrî min taḥtihim feehleknâhüm biẕünûbihim veenşe\'nâ mim ba\\`dihim ḳarnen âḫarîn.');
INSERT INTO `tr_transliteration` VALUES (796, 'velev nezzelnâ \\`aleyke kitâben fî ḳirṭâsin felemesûhü bieydîhim leḳâle-lleẕîne keferû in hâẕâ illâ siḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (797, 'veḳâlû levlâ ünzile \\`aleyhi melek. velev enzelnâ melekel leḳuḍiye-l\'emru ŝümme lâ yünżarûn.');
INSERT INTO `tr_transliteration` VALUES (798, 'velev ce\\`alnâhü melekel lece\\`alnâhü racülev velelebesnâ \\`aleyhim mâ yelbisûn.');
INSERT INTO `tr_transliteration` VALUES (799, 'veleḳadi-stühzie birusülim min ḳablike feḥâḳa billeẕîne seḫirû minhüm mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (800, 'ḳul sîrû fi-l\'arḍi ŝümme-nżurû keyfe kâne \\`âḳibetü-lmükeẕẕibîn.');
INSERT INTO `tr_transliteration` VALUES (801, 'ḳul limem mâ fi-ssemâvâti vel\'arḍ. ḳul lillâh. ketebe \\`alâ nefsihi-rraḥmeh. leyecme\\`anneküm ilâ yevmi-lḳiyâmeti lâ raybe fîh. elleẕîne ḫasirû enfüsehüm fehüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (802, 'velehû mâ sekene fi-lleyli vennehâr. vehüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (803, 'ḳul egayra-llâhi etteḫiẕü veliyyen fâṭiri-ssemâvâti vel\'arḍi vehüve yuṭ\\`imü velâ yuṭ\\`am. ḳul innî ümirtü en ekûne evvele men esleme velâ tekûnenne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (804, 'ḳul innî eḫâfü in \\`aṣaytü rabbî \\`aẕâbe yevmin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (805, 'mey yuṣraf \\`anhü yevmeiẕin feḳad raḥimeh. veẕâlike-lfevzü-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (806, 'veiy yemseske-llâhü biḍurrin felâ kâşife lehû illâ hû. veiy yemseske biḫayrin fehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (807, 'vehüve-lḳâhiru fevḳa \\`ibâdih. vehüve-lḥakîmü-lḫabîr.');
INSERT INTO `tr_transliteration` VALUES (808, 'ḳul eyyü şey\'in ekberu şehâdeten. ḳuli-llâhü şehîdüm beynî vebeyneküm veûḥiye ileyye hâẕe-lḳur\'ânü liünẕiraküm bihî vemem belag. einneküm leteşhedûne enne me\\`a-llâhi âliheten uḫrâ. ḳul lâ eşhed. ḳul innemâ hüve ilâhüv vâḥidüv veinnenî berîüm mimmâ tüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (809, 'elleẕîne âteynâhümü-lkitâbe ya\\`rifûnehû kemâ ya\\`rifûne ebnâehüm. elleẕîne ḫasirû enfüsehüm fehüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (810, 'vemen ażlemü mimmeni-fterâ \\`ale-llâhi keẕiben ev keẕẕebe biâyâtih. innehû lâ yüfliḥu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (811, 'veyevme naḥşüruhüm cemî\\`an ŝümme neḳûlü lilleẕîne eşrakû eyne şürakâükümü-lleẕîne küntüm tez\\`umûn.');
INSERT INTO `tr_transliteration` VALUES (812, 'ŝümme lem tekün fitnetühüm illâ en ḳâlû vellâhi rabbinâ mâ künnâ müşrikîn.');
INSERT INTO `tr_transliteration` VALUES (813, 'ünżur keyfe keẕebû \\`alâ enfüsihim veḍalle \\`anhüm mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (814, 'veminhüm mey yestemi\\`u ileyk. vece\\`alnâ \\`alâ ḳulûbihim ekinneten ey yefḳahûhü vefî âẕânihim vaḳrâ. veiy yerav külle âyetil lâ yü\'minû bihâ. ḥattâ iẕâ câûke yücâdilûneke yeḳûlü-lleẕîne keferû in hâẕâ illâ esâṭîru-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (815, 'vehüm yenhevne \\`anhü veyen\'evne \\`anh. veiy yühlikûne illâ enfüsehüm vemâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (816, 'velev terâ iẕ vuḳifû \\`ale-nnâri feḳâlû yâ leytenâ nüraddü velâ nükeẕẕibe biâyâti rabbinâ venekûne mine-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (817, 'bel bedâ lehüm mâ kânû yuḫfûne min ḳabl. velev ruddû le\\`âdû limâ nühû \\`anhü veinnehüm lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (818, 'veḳâlû in hiye illâ ḥayâtüne-ddünyâ vemâ naḥnü bimeb\\`ûŝîn.');
INSERT INTO `tr_transliteration` VALUES (819, 'velev terâ iẕ vuḳifû \\`alâ rabbihim. ḳâle eleyse hâẕâ bilḥaḳḳ. ḳâlû belâ verabbinâ. ḳâle feẕûḳu-l\\`aẕâbe bimâ küntüm tekfürûn.');
INSERT INTO `tr_transliteration` VALUES (820, 'ḳad ḫasira-lleẕîne keẕẕebû biliḳâi-llâh. ḥattâ iẕâ câethümü-ssâ\\`atü bagteten ḳâlû yâ ḥasratenâ \\`alâ mâ ferraṭnâ fîhâ vehüm yaḥmilûne evzârahüm \\`alâ żuhûrihim. elâ sâe mâ yezirûn.');
INSERT INTO `tr_transliteration` VALUES (821, 'veme-lḥayâtü-ddünyâ illâ le\\`ibüv velehv. veleddâru-l\'âḫiratü ḫayrul lilleẕîne yetteḳûn. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (822, 'ḳad na\\`lemü innehû leyaḥzünüke-lleẕî yeḳûlûne feinnehüm lâ yükeẕẕibûneke velâkinne-żżâlimîne biâyâti-llâhi yecḥadûn.');
INSERT INTO `tr_transliteration` VALUES (823, 'veleḳad küẕẕibet rusülüm min ḳablike feṣaberû \\`alâ mâ küẕẕibû veûẕû ḥattâ etâhüm naṣrunâ. velâ mübeddile likelimâti-llâh. veleḳad câeke min nebei-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (824, 'vein kâne kebüra \\`aleyke i\\`râḍuhüm feini-steṭa\\`te en tebtegiye nefeḳan fi-l\'arḍi ev süllemen fi-ssemâi fete\'tiyehüm biâyeh. velev şâe-llâhü leceme\\`ahüm \\`ale-lhüdâ felâ tekûnenne mine-lcâhilîn.');
INSERT INTO `tr_transliteration` VALUES (825, 'innemâ yestecîbü-lleẕîne yesme\\`ûn. velmevtâ yeb\\`aŝühümü-llâhü ŝümme ileyhi yürce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (826, 'veḳâlû levlâ nüzzile \\`aleyhi âyetüm mir rabbih. ḳul inne-llâhe ḳâdirun \\`alâ ey yünezzile âyetev velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (827, 'vemâ min dâbbetin fi-l\'arḍi velâ ṭâiriy yeṭîru bicenâḥayhi illâ ümemün emŝâlüküm. mâ ferraṭnâ fi-lkitâbi min şey\'in ŝümme ilâ rabbihim yuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (828, 'velleẕîne keẕẕebû biâyâtinâ ṣummüv vebükmün fi-żżulümât. mey yeşei-llâhü yuḍlilh. vemey yeşe\' yec\\`alhü \\`alâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (829, 'ḳul era\'eyteküm in etâküm \\`aẕâbü-llâhi ev etetkümü-ssâ\\`atü egayra-llâhi ted\\`ûn. in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (830, 'bel iyyâhü ted\\`ûne feyekşifü mâ ted\\`ûne ileyhi in şâe vetensevne mâ tüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (831, 'veleḳad erselnâ ilâ ümemim min ḳablike feeḫaẕnâhüm bilbe\'sâi veḍḍarrâi le\\`allehüm yeteḍarra\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (832, 'felevlâ iẕ câehüm be\'sünâ teḍarra\\`û velâkin ḳaset ḳulûbühüm vezeyyene lehümü-şşeyṭânü mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (833, 'felemmâ nesû mâ ẕükkirû bihî fetaḥnâ \\`aleyhim ebvâbe külli şey\'. ḥattâ iẕâ feriḥû bimâ ûtû eḫaẕnâhüm bagteten feiẕâ hüm müblisûn.');
INSERT INTO `tr_transliteration` VALUES (834, 'feḳuṭi\\`a dâbiru-lḳavmi-lleẕîne żalemû. velḥamdü lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (835, 'ḳul era\'eytüm in eḫaẕe-llâhü sem\\`aküm veebṣâraküm veḫateme \\`alâ ḳulûbiküm men ilâhün gayru-llâhi ye\'tîküm bih. ünżur keyfe nüṣarrifü-l\'âyâti ŝümme hüm yaṣdifûn.');
INSERT INTO `tr_transliteration` VALUES (836, 'ḳul era\'eyteküm in etâküm \\`aẕâbü-llâhi bagteten ev cehraten hel yühlekü ille-lḳavmu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (837, 'vemâ nürsilü-lmürselîne illâ mübeşşirîne vemünẕirîn. femen âmene veaṣleḥa felâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (838, 'velleẕîne keẕẕebû biâyâtinâ yemessühümü-l\\`aẕâbü bimâ kânû yefsüḳûn.');
INSERT INTO `tr_transliteration` VALUES (839, 'ḳul lâ eḳûlü leküm \\`indî ḫazâinü-llâhi velâ a\\`lemü-lgaybe velâ eḳûlü leküm innî melek. in ettebi\\`u illâ mâ yûḥâ ileyy. ḳul hel yestevi-l\'a\\`mâ velbeṣîr. efelâ tetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (840, 'veenẕir bihi-lleẕîne yeḫâfûne ey yuḥşerû ilâ rabbihim leyse lehüm min dûnihî veliyyüv velâ şefî\\`ul le\\`allehüm yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (841, 'velâ taṭrudi-lleẕîne yed\\`ûne rabbehüm bilgadâti vel\\`aşiyyi yürîdûne vecheh. mâ \\`aleyke min ḥisâbihim min şey\'iv vemâ min ḥisâbike \\`aleyhim min şey\'in fetaṭrudehüm fetekûne mine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (842, 'vekeẕâlike fetennâ ba\\`ḍahüm biba\\`ḍil liyeḳûlû ehâülâi menne-llâhü \\`aleyhim mim beyninâ. eleyse-llâhü bia\\`leme bişşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (843, 'veiẕâ câeke-lleẕîne yü\'minûne biâyâtinâ feḳul selâmün \\`aleyküm ketebe rabbüküm \\`alâ nefsihi-rraḥmete ennehû men \\`amile minküm sûem bicehâletin ŝümme tâbe mim ba\\`dihî veaṣleḥa feennehû gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (844, 'vekeẕâlike nüfeṣṣilü-l\'âyâti velitestebîne sebîlü-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (845, 'ḳul innî nühîtü en a\\`büde-lleẕîne ted\\`ûne min dûni-llâh. ḳul lâ ettebi\\`u ehvâeküm ḳad ḍaleltü iẕev vemâ ene mine-lmühtedîn.');
INSERT INTO `tr_transliteration` VALUES (846, 'ḳul innî \\`alâ beyyinetim mir rabbî vekeẕẕebtüm bih. mâ \\`indî mâ testa\\`cilûne bih. ini-lḥukmü illâ lillâh. yeḳuṣṣu-lḥaḳḳa vehüve ḫayru-lfâṣilîn.');
INSERT INTO `tr_transliteration` VALUES (847, 'ḳul lev enne \\`indî mâ testa\\`cilûne bihî leḳuḍiye-l\'emru beynî vebeyneküm. vellâhü a\\`lemü biżżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (848, 've\\`indehû mefâtiḥu-lgaybi lâ ya\\`lemühâ illâ hû. veya\\`lemü mâ fi-lberri velbaḥr. vemâ tesḳuṭu miv veraḳatin illâ ya\\`lemühâ velâ ḥabbetin fî żulümâti-l\'arḍi velâ raṭbiv velâ yâbisin illâ fî kitâbim mübîn.');
INSERT INTO `tr_transliteration` VALUES (849, 'vehüve-lleẕî yeteveffâküm billeyli veya\\`lemü mâ ceraḥtüm binnehâri ŝümme yeb\\`aŝüküm fîhi liyuḳḍâ ecelüm müsemmâ. ŝümme ileyhi merci\\`uküm ŝümme yünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (850, 'vehüve-lḳâhiru fevḳa \\`ibâdihî veyürsilü \\`aleyküm ḥafeżah. ḥattâ iẕâ câe eḥadekümü-lmevtü teveffethü rusülünâ vehüm lâ yüferriṭûn.');
INSERT INTO `tr_transliteration` VALUES (851, 'ŝümme ruddû ile-llâhi mevlâhümü-lḥaḳḳ. elâ lehü-lḥukmü vehüve esra\\`u-lḥâsibîn.');
INSERT INTO `tr_transliteration` VALUES (852, 'ḳul mey yüneccîküm min żulümâti-lberri velbaḥri ted\\`ûnehû teḍarru\\`av veḫufyeh. lein encânâ min hâẕihî lenekûnenne mine-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (853, 'ḳuli-llâhü yüneccîküm minhâ vemin külli kerbin ŝümme entüm tüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (854, 'ḳul hüve-lḳâdiru \\`alâ ey yeb\\`aŝe \\`aleyküm \\`aẕâbem min fevḳiküm ev min taḥti ercüliküm ev yelbiseküm şiye\\`av veyüẕîḳa ba\\`ḍaküm be\'se ba\\`ḍ. ünżur keyfe nüṣarrifü-l\'âyâti le\\`allehüm yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (855, 'vekeẕẕebe bihî ḳavmüke vehüve-lḥaḳḳ. ḳul lestü \\`aleyküm bivekîl.');
INSERT INTO `tr_transliteration` VALUES (856, 'likülli nebeim müsteḳarr. vesevfe ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (857, 'veiẕâ raeyte-lleẕîne yeḫûḍûne fî âyâtinâ fea\\`riḍ \\`anhüm ḥattâ yeḫûḍû fî ḥadîŝin gayrih. veimmâ yünsiyenneke-şşeyṭânü felâ taḳ\\`ud ba\\`de-ẕẕikrâ me\\`a-lḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (858, 'vemâ \\`ale-lleẕîne yetteḳûne min ḥisâbihim min şey\'iv velâkin ẕikrâ le\\`allehüm yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (859, 'veẕeri-lleẕîne-tteḫaẕû dînehüm le\\`ibev velehvev vegarrathümü-lḥayâtü-ddünyâ veẕekkir bihî en tübsele nefsüm bimâ kesebet. leyse lehâ min dûni-llâhi veliyyüv velâ şefî\\`. vein ta\\`dil külle \\`adlil lâ yü\'ḫaẕ minhâ. ülâike-lleẕîne übsilû bimâ kesebû. lehüm şerâbüm min ḥamîmiv ve\\`aẕâbün elîmüm bimâ kânû yekfürûn.');
INSERT INTO `tr_transliteration` VALUES (860, 'ḳul ened\\`û min dûni-llâhi mâ lâ yenfe\\`unâ velâ yeḍurrunâ venüraddü \\`alâ a\\`ḳâbinâ ba\\`de iẕ hedâne-llâhü kelleẕi-stehvethü-şşeyâṭînü fi-l\'arḍi ḥayrân. lehû aṣḥâbüy yed\\`ûnehû ile-lhüde-\'tinâ. ḳul inne hüde-llâhi hüve-lhüdâ. veümirnâ linüslime lirabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (861, 'veen eḳîmu-ṣṣalâte vetteḳûh. vehüve-lleẕî ileyhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (862, 'vehüve-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳ. veyevme yeḳûlü kün feyekûn. ḳavlühü-lḥaḳḳ. velehü-lmülkü yevme yünfeḫu fi-ṣṣûr. \\`âlimü-lgaybi veşşehâdeh. vehüve-lḥakîmü-lḫabîr.');
INSERT INTO `tr_transliteration` VALUES (863, 'veiẕ ḳâle ibrâhîmü liebîhi âzera etetteḫiẕü aṣnâmen âliheten. innî erâke veḳavmeke fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (864, 'vekeẕâlike nürî ibrâhîme melekûte-ssemâvâti vel\'arḍi veliyekûne mine-lmûḳinîn.');
INSERT INTO `tr_transliteration` VALUES (865, 'felemmâ cenne \\`aleyhi-lleylü raâ kevkebâ. ḳâle hâẕâ rabbî. felemmâ efele ḳâle lâ üḥibbü-l\'âfilîn.');
INSERT INTO `tr_transliteration` VALUES (866, 'felemmâ rae-lḳamera bâzigan ḳâle hâẕâ rabbî. felemmâ efele ḳâle leil lem yehdinî rabbî leekûnenne mine-lḳavmi-ḍḍâllîn.');
INSERT INTO `tr_transliteration` VALUES (867, 'felemmâ rae-şşemse bâzigaten ḳâle hâẕâ rabbî hâẕâ ekber. felemmâ efelet ḳâle yâ ḳavmi innî berîüm mimmâ tüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (868, 'innî veccehtü vechiye lilleẕî feṭara-ssemâvâti vel\'arḍa ḥanîfev vemâ ene mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (869, 'veḥâccehû ḳavmüh. ḳâle etüḥâccûnnî fi-llâhi veḳad hedân. velâ eḫâfü mâ tüşrikûne bihî illâ ey yeşâe rabbî şey\'â. vesi\\`a rabbî külle şey\'in \\`ilmâ. efelâ teteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (870, 'vekeyfe eḫâfü mâ eşraktüm velâ teḫâfûne enneküm eşraktüm billâhi mâ lem yünezzil bihî \\`aleyküm sülṭânâ. feeyyü-lferîḳayni eḥaḳḳu bil\'emn. in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (871, 'elleẕîne âmenû velem yelbisû îmânehüm biżulmin ülâike lehümü-l\'emnü vehüm mühtedûn.');
INSERT INTO `tr_transliteration` VALUES (872, 'vetilke ḥuccetünâ âteynâhâ ibrâhîme \\`alâ ḳavmih. nerfe\\`u deracâtim men neşâ\'. inne rabbeke ḥakîmün \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (873, 'vevehebnâ lehû isḥâḳa veya\\`ḳûb. küllen hedeynâ. venûḥan hedeynâ min ḳablü vemin ẕürriyyetihî dâvûde vesüleymâne veeyyûbe veyûsüfe vemûsâ vehârûn. vekeẕâlike neczi-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (874, 'vezekeriyyâ veyaḥyâ ve\\`îsâ veilyâs. küllüm mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (875, 'veismâ\\`île velyese\\`a veyûnüse velûṭâ. veküllen feḍḍalnâ \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (876, 'vemin âbâihim veẕürriyyâtihim veiḫvânihim. vectebeynâhüm vehedeynâhüm ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (877, 'ẕâlike hüde-llâhi yehdî bihî mey yeşâü min \\`ibâdih. velev eşrakû leḥabiṭa \\`anhüm mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (878, 'ülâike-lleẕîne âteynâhümü-lkitâbe velḥukme vennübüvveh. feiy yekfür bihâ hâülâi feḳad vekkelnâ bihâ ḳavmel leysû bihâ bikâfirîn.');
INSERT INTO `tr_transliteration` VALUES (879, 'ülâike-lleẕîne hede-llâhü febihüdâhümu-ḳtedih. ḳul lâ es\'elüküm \\`aleyhi ecrâ. in hüve illâ ẕikrâ lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (880, 'vemâ ḳaderü-llâhe ḥaḳḳa ḳadrihî iẕ ḳâlû mâ enzele-llâhü \\`alâ beşerim min şey\'. ḳul men enzele-lkitâbe-lleẕî câe bihî mûsâ nûrav vehüdel linnâsi tec\\`alûnehû ḳarâṭîse tübdûnehâ vetuḫfûne keŝîrâ. ve\\`ullimtüm mâ lem ta\\`lemû entüm velâ âbâüküm. ḳuli-llâhü ŝümme ẕerhüm fî ḫavḍihim yel\\`abûn.');
INSERT INTO `tr_transliteration` VALUES (881, 'vehâẕâ kitâbün enzelnâhü mübâraküm müṣaddiḳu-lleẕî beyne yedeyhi velitünẕira ümme-lḳurâ vemen ḥavlehâ. velleẕîne yü\'minûne bil\'âḫirati yü\'minûne bihî vehüm \\`alâ ṣalâtihim yüḥâfiżûn.');
INSERT INTO `tr_transliteration` VALUES (882, 'vemen ażlemü mimmeni-fterâ \\`ale-llâhi keẕiben ev ḳâle ûḥiye ileyye velem yûḥa ileyhi şey\'üv vemen ḳâle seünzilü miŝle mâ enzele-llâh. velev terâ iẕi-żżâlimûne fî gamerâti-lmevti velmelâiketü bâsiṭû eydîhim. aḫricû enfüseküm. elyevme tüczevne \\`aẕâbe-lhûni bimâ küntüm teḳûlûne \\`ale-llâhi gayra-lḥaḳḳi veküntüm \\`an âyâtihî testekbirûn.');
INSERT INTO `tr_transliteration` VALUES (883, 'veleḳad ci\'tümûnâ fürâdâ kemâ ḫalaḳnâküm evvele merrativ veteraktüm mâ ḫavvelnâküm verâe żuhûriküm. vemâ nerâ me\\`aküm şüfe\\`âekümü-lleẕîne za\\`amtüm ennehüm fîküm şürakâ\'. leḳat teḳaṭṭa\\`a beyneküm veḍalle \\`anküm mâ küntüm tez\\`umûn.');
INSERT INTO `tr_transliteration` VALUES (884, 'inne-llâhe fâliḳu-lḥabbi vennevâ. yuḫricü-lḥayye mine-lmeyyiti vemuḫricü-lmeyyiti mine-lḥayy. ẕâlikümü-llâhü feennâ tü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (885, 'fâliḳu-l\'iṣbâḥ. vece\\`ale-lleyle sekenev veşşemse velḳamera ḥusbânâ. ẕâlike taḳdîru-l\\`azîzi-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (886, 'vehüve-lleẕî ce\\`ale lekümü-nnücûme litehtedû bihâ fî żulümâti-lberri velbaḥr. ḳad feṣṣalne-l\'âyâti liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (887, 'vehüve-lleẕî enşeeküm min nefsiv vâḥidetin femüsteḳarruv vemüstevda\\`. ḳad feṣṣalne-l\'âyâti liḳavmiy yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (888, 'vehüve-lleẕî enzele mine-ssemâi mââ. feaḫracnâ bihî nebâte külli şey\'in feaḫracnâ minhü ḫaḍiran nuḫricü minhü ḥabbem müterâkibâ. vemine-nnaḫli min ṭal\\`ihâ ḳinvânün dâniyetüv vecennâtim min a\\`nâbiv vezzeytûne verrummâne müştebihev vegayra müteşâbih.-nżurû ilâ ŝemerihî iẕâ eŝmera veyen\\`ih. inne fî ẕâliküm leâyâtil liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (889, 'vece\\`alû lillâhi şürakâe-lcinne veḫaleḳahüm veḫaraḳû lehû benîne vebenâtim bigayri \\`ilm. sübḥânehû vete\\`âlâ \\`ammâ yeṣifûn.');
INSERT INTO `tr_transliteration` VALUES (890, 'bedî\\`u-ssemâvâti vel\'arḍ. ennâ yekûnü lehû veledüv velem tekül lehû ṣâḥibeh. veḫaleḳa külle şey\'. vehüve bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (891, 'ẕâlikümü-llâhü rabbüküm. lâ ilâhe illâ hû. ḫâliḳu külli şey\'in fa\\`büdûh. vehüve \\`alâ külli şey\'iv vekîl.');
INSERT INTO `tr_transliteration` VALUES (892, 'lâ tüdrikühü-l\'ebṣâr. vehüve yüdrikü-l\'ebṣâr. vehüve-lleṭîfü-lḫabîr.');
INSERT INTO `tr_transliteration` VALUES (893, 'ḳad câeküm beṣâiru mir rabbiküm. femen ebṣara felinefsih. vemen \\`amiye fe\\`aleyhâ. vemâ ene \\`aleyküm biḥafîż.');
INSERT INTO `tr_transliteration` VALUES (894, 'vekeẕâlike nüṣarrifü-l\'âyâti veliyeḳûlû deraste velinübeyyinehû liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (895, 'ittebi\\` mâ ûḥiye ileyke mir rabbik. lâ ilâhe illâ hû. vea\\`riḍ \\`ani-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (896, 'velev şâe-llâhü mâ eşrakû. vemâ ce\\`alnâke \\`aleyhim ḥafîżâ. vemâ ente \\`aleyhim bivekîl.');
INSERT INTO `tr_transliteration` VALUES (897, 'velâ tesübbü-lleẕîne yed\\`ûne min dûni-llâhi feyesübbü-llâhe \\`advem bigayri \\`ilm. keẕâlike zeyyennâ likülli ümmetin \\`amelehüm ŝümme ilâ rabbihim merci\\`uhüm feyünebbiühüm bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (898, 'veaḳsemû billâhi cehde eymânihim lein câethüm âyetüh leyü\'minünne bihâ. ḳul inneme-l\'âyâtü \\`inde-llâhi vemâ yüş\\`iruküm ennehâ iẕâ câet lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (899, 'venüḳallibü ef\'idetehüm veebṣârahüm kemâ lem yü\'minû bihî evvele merrativ veneẕeruhüm fî ṭugyânihim ya\\`mehûn.');
INSERT INTO `tr_transliteration` VALUES (900, 'velev ennenâ nezzelnâ ileyhimü-lmelâikete vekellemehümü-lmevtâ veḥaşernâ \\`aleyhim külle şey\'in ḳubülem mâ kânû liyü\'minû illâ ey yeşâe-llâhü velâkinne ekŝerahüm yechelûn.');
INSERT INTO `tr_transliteration` VALUES (901, 'vekeẕâlike ce\\`alnâ likülli nebiyyin \\`adüvven şeyâṭîne-l\'insi velcinni yûḥî ba\\`ḍuhüm ilâ ba\\`ḍin zuḫrufe-lḳavli gurûrâ. velev şâe rabbüke mâ fe\\`alûhü feẕerhüm vemâ yefterûn.');
INSERT INTO `tr_transliteration` VALUES (902, 'velitaṣgâ ileyhi ef\'idetü-lleẕîne lâ yü\'minûne bil\'âḫirati veliyerḍavhü veliyaḳterifû mâ hüm muḳterifûn.');
INSERT INTO `tr_transliteration` VALUES (903, 'efegayra-llâhi ebtegî ḥakemev vehüve-lleẕî enzele ileykümü-lkitâbe müfeṣṣalâ. velleẕîne âteynâhümü-lkitâbe ya\\`lemûne ennehû münezzelüm mir rabbike bilḥaḳḳi felâ tekûnenne mine-lmümterîn.');
INSERT INTO `tr_transliteration` VALUES (904, 'vetemmet kelimetü rabbike ṣidḳav ve\\`adlâ. lâ mübeddile likelimâtih. vehüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (905, 'vein tüṭi\\` ekŝera men fi-l\'arḍi yüḍillûke \\`an sebîli-llâh. iy yettebi\\`ûne ille-żżanne vein hüm illâ yaḫruṣûn.');
INSERT INTO `tr_transliteration` VALUES (906, 'inne rabbeke hüve a\\`lemü mey yeḍillü \\`an sebîlih. vehüve a\\`lemü bilmühtedîn.');
INSERT INTO `tr_transliteration` VALUES (907, 'fekülû mimmâ ẕükira-smü-llâhi \\`aleyhi in küntüm biâyâtihî mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (908, 'vemâ leküm ellâ te\'külû mimmâ ẕükira-smü-llâhi \\`aleyhi veḳad feṣṣale leküm mâ ḥarrame \\`aleyküm illâ me-ḍṭurirtüm ileyh. veinne keŝîral leyüḍillûne biehvâihim bigayri \\`ilm. inne rabbeke hüve a\\`lemü bilmü\\`tedîn.');
INSERT INTO `tr_transliteration` VALUES (909, 'veẕerû żâhira-l\'iŝmi vebâṭineh. inne-lleẕîne yeksibûne-l\'iŝme seyüczevne bimâ kânû yaḳterifûn.');
INSERT INTO `tr_transliteration` VALUES (910, 'velâ te\'külû mimmâ lem yüẕkeri-smü-llâhi \\`aleyhi veinnehû lefisḳ. veinne-şşeyâṭîne leyûḥûne ilâ evliyâihim liyücâdilûküm. vein eṭa\\`tümûhüm inneküm lemüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (911, 'evemen kâne meyten feaḥyeynâhü vece\\`alnâ lehû nûray yemşî bihî fi-nnâsi kemem meŝelühû fi-żżulümâti leyse biḫâricim minhâ. keẕâlike züyyine lilkâfirîne mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (912, 'vekeẕâlike ce\\`alnâ fî külli ḳaryetin ekâbira mücrimîhâ liyemkürû fîhâ. vemâ yemkürûne illâ bienfüsihim vemâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (913, 'veiẕâ câethüm âyetün ḳâlû len nü\'mine ḥattâ nü\'tâ miŝle mâ ûtiye rusülü-llâh. allâhü a\\`lemü ḥayŝü yec\\`alü risâleteh. seyüṣîbü-lleẕîne ecramû ṣagârun \\`inde-llâhi ve\\`aẕâbün şedîdüm bimâ kânû yemkürûn.');
INSERT INTO `tr_transliteration` VALUES (914, 'femey yüridi-llâhü ey yehdiyehû yeşraḥ ṣadrahû lil\'islâm. vemey yürid ey yüḍillehû yec\\`al ṣadrahû ḍayyiḳan ḥaracen keennemâ yeṣṣa\\`\\`adü fi-ssemâ\'. keẕâlike yec\\`alü-llâhü-rricse \\`ale-lleẕîne lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (915, 'vehâẕâ ṣirâṭu rabbike müsteḳîmâ. ḳad feṣṣalne-l\'âyâti liḳavmiy yeẕẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (916, 'lehüm dâru-sselâmi \\`inde rabbihim vehüve veliyyühüm bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (917, 'veyevme yaḥşüruhüm cemî\\`â. yâ ma\\`şera-lcinni ḳadi-stekŝertüm mine-l\'ins. veḳâle evliyâühüm mine-l\'insi rabbene-stemte\\`a ba\\`ḍunâ biba\\`ḍiv vebelagnâ ecelene-lleẕî eccelte lenâ. ḳâle-nnâru meŝvâküm ḫâlidîne fîhâ illâ mâ şâe-llâh. inne rabbeke ḥakîmün \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (918, 'vekeẕâlike nüvellî ba\\`ḍa-żżâlimîne ba\\`ḍam bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (919, 'yâ ma\\`şera-lcinni vel\'insi elem ye\'tiküm rusülüm minküm yeḳuṣṣûne \\`aleyküm âyâtî veyünẕirûneküm liḳâe yevmiküm hâẕâ. ḳâlû şehidnâ \\`alâ enfüsinâ vegarrathümü-lḥayâtü-ddünyâ veşehidû \\`alâ enfüsihim ennehüm kânû kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (920, 'ẕâlike el lem yekür rabbüke mühlike-lḳurâ biżulmiv veehlühâ gâfilûn.');
INSERT INTO `tr_transliteration` VALUES (921, 'veliküllin deracâtüm mimmâ \\`amilû. vemâ rabbüke bigâfilin \\`ammâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (922, 'verabbüke-lganiyyü ẕü-rraḥmeh. iy yeşe\' yüẕhibküm veyestaḫlif mim ba\\`diküm mâ yeşâü kemâ enşeeküm min ẕürriyyeti ḳavmin âḫarîn.');
INSERT INTO `tr_transliteration` VALUES (923, 'inne mâ tû\\`adûne leâtiv vemâ entüm bimü\\`cizîn.');
INSERT INTO `tr_transliteration` VALUES (924, 'ḳul yâ ḳavmi-\\`melû \\`alâ mekânetiküm innî \\`âmil. fesevfe ta\\`lemûne men tekûnü lehû \\`âḳibetü-ddâr. innehû lâ yüfliḥu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (925, 'vece\\`alû lillâhi mimmâ ẕerae mine-lḥarŝi vel\'en\\`âmi neṣîben feḳâlû hâẕâ lillâhi biza\\`mihim vehâẕâ lişürakâinâ. femâ kâne lişürakâihim felâ yeṣilü ile-llâh. vemâ kâne lillâhi fehüve yeṣilü ilâ şürakâihim. sâe mâ yaḥkümûn.');
INSERT INTO `tr_transliteration` VALUES (926, 'vekeẕâlike zeyyene likeŝîrim mine-lmüşrikîne ḳatle evlâdihim şürakâühüm liyürdûhüm veliyelbisû \\`aleyhim dînehüm. velev şâe-llâhü mâ fe\\`alûhü feẕerhüm vemâ yefterûn.');
INSERT INTO `tr_transliteration` VALUES (927, 'veḳâlû hâẕihî en\\`âmüv veḥarŝün ḥicr. lâ yaṭ\\`amühâ illâ men neşâü biza\\`mihim veen\\`âmün ḥurrimet żuhûruhâ veen\\`âmül lâ yeẕkürûne-sme-llâhi \\`aleyhe-ftirâen \\`aleyh. seyeczîhim bimâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (928, 'veḳâlû mâ fî büṭûni hâẕihi-l\'en\\`âmi ḫâliṣatül liẕükûrinâ vemüḥarramün \\`alâ ezvâcinâ. veiy yeküm meyteten fehüm fîhi şürakâ\'. seyeczîhim vaṣfehüm. innehû ḥakîmün \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (929, 'ḳad ḫasira-lleẕîne ḳatelû evlâdehüm sefehem bigayri \\`ilmiv veḥarramû mâ razeḳahümü-llâhü-ftirâen \\`ale-llâh. ḳad ḍallû vemâ kânû mühtedîn.');
INSERT INTO `tr_transliteration` VALUES (930, 'vehüve-lleẕî enşee cennâtim ma\\`rûşâtiv vegayra ma\\`rûşâtiv vennaḫle vezzer\\`a muḫtelifen ükülühû vezzeytûne verrummâne müteşâbihev vegayra müteşâbih. külû min ŝemerihî iẕâ eŝmera veâtû ḥaḳḳahû yevme ḥaṣâdihi. velâ tüsrifû. innehû lâ yüḥibbü-lmüsrifîn.');
INSERT INTO `tr_transliteration` VALUES (931, 'vemine-l\'en\\`âmi ḥamûletev veferşâ. külû mimmâ razeḳakümü-llâhü velâ tettebi\\`û ḫuṭuvâti-şşeyṭân. innehû leküm \\`adüvvüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (932, 'ŝemâniyete ezvâc. mine-ḍḍa\'ni-ŝneyni vemine-lma\\`zi-ŝneyn. ḳul âẕẕekerayni ḥarrame emi-l\'ünŝeyeyni emme-ştemelet \\`aleyhi erḥâmü-l\'ünŝeyeyn. nebbiûnî bi\\`ilmin in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (933, 'vemine-l\'ibili-ŝneyni vemine-lbeḳari-ŝneyn. ḳul âẕẕekerayni ḥarrame emi-l\'ünŝeyeyni emme-ştemelet \\`aleyhi erḥâmü-l\'ünŝeyeyn. em küntüm şühedâe iẕ veṣṣâkümü-llâhü bihâẕâ. femen ażlemü mimmeni-fterâ \\`ale-llâhi keẕibel liyüḍille-nnâse bigayri \\`ilm. inne-llâhe lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (934, 'ḳul lâ ecidü fî mâ ûḥiye ileyye müḥarramen \\`alâ ṭâ\\`imiy yaṭ\\`amühû illâ ey yekûne meyteten ev demem mesfûḥan ev laḥme ḫinzîrin feinnehû ricsün ev fisḳan ühille ligayri-llâhi bih. femeni-ḍṭurra gayra bâgiv velâ \\`âdin feinne rabbeke gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (935, 've\\`ale-lleẕîne hâdû ḥarramnâ külle ẕî żufür. vemine-lbeḳari velganemi ḥarramnâ \\`aleyhim şüḥûmehümâ illâ mâ ḥamelet żuhûruhümâ evi-lḥavâyâ ev me-ḫteleṭa bi\\`ażm. ẕâlike cezeynâhüm bibagyihim. veinnâ leṣâdiḳûn.');
INSERT INTO `tr_transliteration` VALUES (936, 'fein keẕẕebûke feḳur rabbüküm ẕû raḥmetiv vâsi\\`ah. velâ yüraddü be\'sühû \\`ani-lḳavmi-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (937, 'seyeḳûlü-lleẕîne eşrakû lev şâe-llâhü mâ eşraknâ velâ âbâünâ velâ ḥarramnâ min şey\'. keẕâlike keẕẕebe-lleẕîne min ḳablihim ḥattâ ẕâḳû be\'senâ. ḳul hel \\`indeküm min \\`ilmin fetuḫricûhü lenâ. in tettebi\\`ûne ille-żżanne vein entüm illâ taḫruṣûn.');
INSERT INTO `tr_transliteration` VALUES (938, 'ḳul felillâhi-lḥuccetü-lbâligah. felev şâe lehedâküm ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (939, 'ḳul helümme şühedâekümü-lleẕîne yeşhedûne enne-llâhe ḥarrame hâẕâ. fein şehidû felâ teşhed me\\`ahüm. velâ tettebi\\` ehvâe-lleẕîne keẕẕebû biâyâtinâ velleẕîne lâ yü\'minûne bil\'âḫirati vehüm birabbihim ya\\`dilûn.');
INSERT INTO `tr_transliteration` VALUES (940, 'ḳul te\\`âlev etlü mâ ḥarrame rabbüküm \\`aleyküm ellâ tüşrikû bihî şey\'â. vebilvâlideyni iḥsânâ. velâ taḳtülû evlâdeküm min imlâḳ. naḥnü nerzüḳuküm veiyyâhüm. velâ taḳrabü-lfevâḥişe mâ żahera minhâ vemâ beṭan. velâ taḳtülü-nnefse-lletî ḥarrame-llâhü illâ bilḥaḳḳ. ẕâliküm veṣṣâküm bihî le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (941, 'velâ taḳrabû mâle-lyetîmi illâ billetî hiye aḥsenü ḥattâ yeblüga eşüddeh. veevfü-lkeyle velmîzâne bilḳisṭ. lâ nükellifü nefsen illâ vus\\`ahâ veiẕâ ḳultüm fa\\`dilû velev kâne ẕâ ḳurbâ. vebi\\`ahdi-llâhi evfû. ẕâliküm veṣṣâküm bihî le\\`alleküm teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (942, 'veenne hâẕâ ṣirâṭî müsteḳîmen fettebi\\`ûh. velâ tettebi\\`ü-ssübüle feteferraḳa biküm \\`an sebîlih. ẕâliküm veṣṣâküm bihî le\\`alleküm tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (943, 'ŝümme âteynâ mûse-lkitâbe temâmen \\`ale-lleẕî aḥsene vetefṣîlel likülli şey\'iv vehüdev veraḥmetel le\\`allehüm biliḳâi rabbihim yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (944, 'vehâẕâ kitâbün enzelnâhü mübârakün fettebi\\`ûhü vetteḳû le\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (945, 'en teḳûlû innemâ ünzile-lkitâbü \\`alâ ṭâifeteyni min ḳablinâ. vein künnâ \\`an dirâsetihim legâfilîn.');
INSERT INTO `tr_transliteration` VALUES (946, 'ev teḳûlû lev ennâ ünzile \\`aleyne-lkitâbü lekünnâ ehdâ minhüm. feḳad câeküm beyyinetüm mir rabbiküm vehüdev veraḥmeh. femen ażlemü mimmen keẕẕebe biâyâti-llâhi veṣadefe \\`anhâ. seneczi-lleẕîne yaṣdifûne \\`an âyâtinâ sûe-l\\`aẕâbi bimâ kânû yaṣdifûn.');
INSERT INTO `tr_transliteration` VALUES (947, 'hel yenżurûne illâ en te\'tiyehümü-lmelâiketü ev ye\'tiye rabbüke ev ye\'tiye ba\\`ḍu âyâti rabbik. yevme ye\'tî ba\\`ḍu âyâti rabbike lâ yenfe\\`u nefsen îmânühâ lem tekün âmenet min ḳablü ev kesebet fî îmânihâ ḫayrâ. ḳuli-nteżirû innâ münteżirûn.');
INSERT INTO `tr_transliteration` VALUES (948, 'inne-lleẕîne ferraḳû dînehüm vekânû şiye\\`al leste minhüm fî şey\'. innemâ emruhüm ile-llâhi ŝümme yünebbiühüm bimâ kânû yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (949, 'men câe bilḥaseneti felehû \\`aşru emŝâlihâ. vemen câe bisseyyieti felâ yüczâ illâ miŝlehâ vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (950, 'ḳul innenî hedânî rabbî ilâ ṣirâṭim müsteḳîm. dînen ḳiyemem millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (951, 'ḳul inne ṣalâtî venüsükî vemaḥyâye vememâtî lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (952, 'lâ şerîke leh. vebiẕâlike ümirtü veenâ evvelü-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (953, 'ḳul egayra-llâhi ebgî rabbev vehüve rabbü külli şey\'. velâ teksibü küllü nefsin illâ \\`aleyhâ. velâ teziru vâziratüv vizra uḫrâ. ŝümme ilâ rabbiküm merci\\`uküm feyünebbiüküm bimâ küntüm fîhi taḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (954, 'vehüve-lleẕî ce\\`aleküm ḫalâife-l\'arḍi verafe\\`a ba\\`ḍaküm fevḳa ba\\`ḍin deracâtil liyeblüveküm fî mâ âtâküm. inne rabbeke serî\\`u-l\\`iḳâb. veinnehû legafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (955, 'elif-lâm-mîm-ṣâd.');
INSERT INTO `tr_transliteration` VALUES (956, 'kitâbün ünzile ileyke felâ yekün fî ṣadrike ḥaracüm minhü litünẕira bihî veẕikrâ lilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (957, 'ittebi\\`û mâ ünzile ileyküm mir rabbiküm velâ tettebi\\`û min dûnihî evliyâ\'. ḳalîlem mâ teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (958, 'vekem min ḳaryetin ehleknâhâ fecâehâ be\'sünâ beyâten ev hüm ḳâilûn.');
INSERT INTO `tr_transliteration` VALUES (959, 'femâ kâne da\\`vâhüm iẕ câehüm be\'sünâ illâ en ḳâlû innâ künnâ żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (960, 'felenes\'elenne-lleẕîne ürsile ileyhim velenes\'elenne-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (961, 'feleneḳuṣṣanne \\`aleyhim bi\\`ilmiv vemâ künnâ gâibîn.');
INSERT INTO `tr_transliteration` VALUES (962, 'velveznü yevmeiẕin-lḥaḳḳ. femen ŝeḳulet mevâzînühû feülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (963, 'vemen ḫaffet mevâzînühû feülâike-lleẕîne ḫasirû enfüsehüm bimâ kânû biâyâtinâ yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (964, 'veleḳad mekkennâküm fi-l\'arḍi vece\\`alnâ leküm fîhâ me\\`âyiş. ḳalîlem mâ teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (965, 'veleḳad ḫalaḳnâküm ŝümme ṣavvernâküm ŝümme ḳulnâ lilmelâiketi-scüdû liâdem. fesecedû illâ iblîs. lem yeküm mine-ssâcidîn.');
INSERT INTO `tr_transliteration` VALUES (966, 'ḳâle mâ mene\\`ake ellâ tescüde iẕ emertük. ḳâle ene ḫayrum minh. ḫalaḳtenî min nâriv veḫalaḳtehû min ṭîn.');
INSERT INTO `tr_transliteration` VALUES (967, 'ḳâle fehbiṭ minhâ femâ yekûnü leke en tetekebbera fîhâ faḫruc inneke mine-ṣṣâgirîn.');
INSERT INTO `tr_transliteration` VALUES (968, 'ḳâle enżirnî ilâ yevmi yüb\\`aŝûn.');
INSERT INTO `tr_transliteration` VALUES (969, 'ḳâle inneke mine-lmünżarîn.');
INSERT INTO `tr_transliteration` VALUES (970, 'ḳâle febimâ agveytenî leaḳ\\`udenne lehüm ṣirâṭake-lmüsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (971, 'ŝümme leâtiyennehüm mim beyni eydîhim vemin ḫalfihim ve\\`an eymânihim ve\\`an şemâilihim. velâ tecidü ekŝerahüm şâkirîn.');
INSERT INTO `tr_transliteration` VALUES (972, 'ḳâle-ḫruc minhâ meẕ\'ûmem medḥûrâ. lemen tebi\\`ake minhüm leemleenne cehenneme minküm ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (973, 'veyâ âdemü-skün ente vezevcüke-lcennete fekülâ min ḥayŝü şi\'tümâ velâ taḳrabâ hâẕihi-şşecerate fetekûnâ mine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (974, 'fevesvese lehüme-şşeyṭânü liyübdiye lehümâ mâ vûriye \\`anhümâ min sev\'âtihimâ veḳâle mâ nehâkümâ rabbükümâ \\`an hâẕihi-şşecerati illâ en tekûnâ melekeyni ev tekûnâ mine-lḫâlidîn.');
INSERT INTO `tr_transliteration` VALUES (975, 'veḳâsemehümâ innî lekümâ lemine-nnâṣiḥîn.');
INSERT INTO `tr_transliteration` VALUES (976, 'fedellâhümâ bigurûr. felemmâ ẕâḳe-şşecerate bedet lehümâ sev\'âtühümâ veṭafiḳâ yaḫṣifâni \\`aleyhimâ miv veraḳi-lcenneh. venâdâhümâ rabbühümâ elem enhekümâ \\`an tilküme-şşecerati veeḳul lekümâ inne-şşeyṭâne lekümâ \\`adüvvüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (977, 'ḳâlâ rabbenâ żalemnâ enfüsenâ veil lem tagfir lenâ veterḥamnâ lenekûnenne mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (978, 'ḳâle-hbiṭû ba\\`ḍuküm liba\\`ḍin \\`adüvv. veleküm fi-l\'arḍi müsteḳarruv vemetâ\\`un ilâ ḥîn.');
INSERT INTO `tr_transliteration` VALUES (979, 'ḳâle fîhâ taḥyevne vefîhâ temûtûne veminhâ tuḫracûn.');
INSERT INTO `tr_transliteration` VALUES (980, 'yâ benî âdeme ḳad enzelnâ \\`aleyküm libâsey yüvârî sev\'âtiküm verîşâ. velibâsü-ttaḳvâ ẕâlike ḫayr. ẕâlike min âyâti-llâhi le\\`allehüm yeẕẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (981, 'yâ benî âdeme lâ yeftinennekümü-şşeyṭânü kemâ aḫrace ebeveyküm mine-lcenneti yenzi\\`u \\`anhümâ libâsehümâ liyüriyehümâ sev\'âtihimâ. innehû yerâküm hüve veḳabîlühû min ḥayŝü lâ teravnehüm. innâ ce\\`alne-şşeyâṭîne evliyâe lilleẕîne lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (982, 'veiẕâ fe\\`alû fâḥişeten ḳâlû vecednâ \\`aleyhâ âbâenâ vellâhü emeranâ bihâ. ḳul inne-llâhe lâ ye\'müru bilfaḥşâ\'. eteḳûlûne \\`ale-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (983, 'ḳul emera rabbî bilḳisṭ. veeḳîmû vucûheküm \\`inde külli mescidiv ved\\`ûhü muḫliṣîne lehü-ddîne. kemâ bedeeküm te\\`ûdûn.');
INSERT INTO `tr_transliteration` VALUES (984, 'ferîḳan hedâ veferîḳan ḥaḳḳa \\`aleyhimu-ḍḍalâleh. innehümü-tteḫaẕü-şşeyâṭîne evliyâe min dûni-llâhi veyaḥsebûne ennehüm mühtedûn.');
INSERT INTO `tr_transliteration` VALUES (985, 'yâ benî âdeme ḫuẕû zîneteküm \\`inde külli mescidiv vekülû veşrabû velâ tüsrifû. innehû lâ yüḥibbü-lmüsrifîn.');
INSERT INTO `tr_transliteration` VALUES (986, 'ḳul men ḥarrame zînete-llâhi-lletî aḫrace li\\`ibâdihî veṭṭayyibâti mine-rrizḳ. ḳul hiye lilleẕîne âmenû fi-lḥayâti-ddünyâ ḫâliṣatey yevme-lḳiyâmeh. keẕâlike nüfeṣṣilü-l\'âyâti liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (987, 'ḳul innemâ ḥarrame rabbiye-lfevâḥişe mâ żahera minhâ vemâ beṭane vel\'iŝme velbagye bigayri-lḥaḳḳi veen tüşrikû billâhi mâ lem yünezzil bihî sülṭânev veen teḳûlû \\`ale-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (988, 'velikülli ümmetin ecel. feiẕâ câe ecelühüm lâ yeste\'ḫirûne sâ\\`atev velâ yestaḳdimûn.');
INSERT INTO `tr_transliteration` VALUES (989, 'yâ benî âdeme immâ ye\'tiyenneküm rusülüm minküm yeḳuṣṣûne \\`aleyküm âyâtî femeni-tteḳâ veaṣleḥa felâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (990, 'velleẕîne keẕẕebû biâyâtinâ vestekberû \\`anhâ ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (991, 'femen ażlemü mimmeni-fterâ \\`ale-llâhi keẕiben ev keẕẕebe biâyâtih. ülâike yenâlühüm neṣîbühüm mine-lkitâb. ḥattâ iẕâ câethüm rusülünâ yeteveffevnehüm ḳâlû eyne mâ küntüm ted\\`ûne min dûni-llâh. ḳâlû ḍallû \\`annâ veşehidû \\`alâ enfüsihim ennehüm kânû kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (992, 'ḳâle-dḫulû fî ümemin ḳad ḫalet min ḳabliküm mine-lcinni vel\'insi fi-nnâr. küllemâ deḫalet ümmetül le\\`anet uḫtehâ. ḥattâ iẕe-ddârakû fîhâ cemî\\`an ḳâlet uḫrâhüm liûlâhüm rabbenâ hâülâi eḍallûnâ feâtihim \\`aẕâben ḍi\\`fem mine-nnâr. ḳâle liküllin ḍi\\`füv velâkil lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (993, 'veḳâlet ûlâhüm liuḫrâhüm femâ kâne leküm \\`aleynâ min faḍlin feẕûḳu-l\\`aẕâbe bimâ küntüm teksibûn.');
INSERT INTO `tr_transliteration` VALUES (994, 'inne-lleẕîne keẕẕebû biâyâtinâ vestekberû \\`anhâ lâ tüfetteḥu lehüm ebvâbü-ssemâi velâ yedḫulûne-lcennete ḥattâ yelice-lcemelü fî semmi-lḫiyâṭ. vekeẕâlike neczi-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (995, 'lehüm min cehenneme mihâdüv vemin fevḳihim gavâş. vekeẕâlike neczi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (996, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lâ nükellifü nefsen illâ vus\\`ahâ. ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (997, 'veneza\\`nâ mâ fî ṣudûrihim min gillin tecrî min taḥtihimü-l\'enhâr. veḳâlü-lḥamdü lillâhi-lleẕî hedânâ lihâẕâ vemâ künnâ linehtediye levlâ en hedâne-llâh. leḳad câet rusülü rabbinâ bilḥaḳḳ. venûdû en tilkümü-lcennetü ûriŝtümûhâ bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (998, 'venâdâ aṣḥâbü-lcenneti aṣḥâbe-nnâri en ḳad vecednâ mâ ve\\`adenâ rabbünâ ḥaḳḳan fehel vecettüm mâ ve\\`ade rabbüküm ḥaḳḳâ. ḳâlû ne\\`am. feeẕẕene müeẕẕinüm beynehüm el la\\`netü-llâhi \\`ale-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (999, 'elleẕîne yeṣuddûne \\`an sebîli-llâhi veyebgûnehâ \\`ivecâ. vehüm bil\'âḫirati kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1000, 'vebeynehümâ ḥicâb. ve\\`ale-l\'a\\`râfi ricâlüy ya\\`rifûne küllem bisîmâhüm. venâdev aṣḥâbe-lcenneti en selâmün \\`aleyküm lem yedḫulûhâ vehüm yaṭme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1001, 'veiẕâ ṣurifet ebṣâruhüm tilḳâe aṣḥâbi-nnâri ḳâlû rabbenâ lâ tec\\`alnâ me\\`a-lḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1002, 'venâdâ aṣḥâbü-l\'a\\`râfi ricâley ya\\`rifûnehüm bisîmâhüm ḳâlû mâ agnâ \\`anküm cem\\`uküm vemâ küntüm testekbirûn.');
INSERT INTO `tr_transliteration` VALUES (1003, 'ehâülâi-lleẕîne aḳsemtüm lâ yenâlühümü-llâhü biraḥmeh. üdḫulü-lcennete lâ ḫavfün \\`aleyküm velâ entüm taḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (1004, 'venâdâ aṣḥâbü-nnâri aṣḥâbe-lcenneti en efîḍû \\`aleynâ mine-lmâi ev mimmâ razeḳakümü-llâh. ḳâlû inne-llâhe ḥarramehümâ \\`ale-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1005, 'elleẕîne-tteḫaẕû dînehüm lehvev vele\\`ibev vegarrathümü-lḥayâtü-ddünyâ. felyevme nensâhüm kemâ nesû liḳâe yevmihim hâẕâ vemâ kânû biâyâtinâ yecḥadûn.');
INSERT INTO `tr_transliteration` VALUES (1006, 'veleḳad ci\'nâhüm bikitâbin feṣṣalnâhü \\`alâ \\`ilmin hüdev veraḥmetel liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1007, 'hel yenżurûne illâ te\'vîleh. yevme ye\'tî te\'vîlühû yeḳûlü-lleẕîne nesûhü min ḳablü ḳad câet rusülü rabbinâ bilḥaḳḳ. fehel lenâ min şüfe\\`âe feyeşfe\\`û lenâ ev nüraddü fena\\`mele gayra-lleẕî künnâ na\\`mel. ḳad ḫasirû enfüsehüm veḍalle \\`anhüm mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (1008, 'inne rabbekümü-llâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmin ŝümme-stevâ \\`ale-l\\`arşi yugşi-lleyle-nnehâra yaṭlübühû ḥaŝîŝev veşşemse velḳamera vennücûme müseḫḫarâtim biemrih. elâ lehü-lḫalḳu vel\'emr. tebârake-llâhü rabbü-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1009, 'üd\\`û rabbeküm teḍarru\\`av veḫufyeh. innehû lâ yüḥibbü-lmü\\`tedîn.');
INSERT INTO `tr_transliteration` VALUES (1010, 'velâ tüfsidû fi-l\'arḍi ba\\`de iṣlâḥihâ ved\\`ûhü ḫavfev veṭame\\`â. inne raḥmete-llâhi ḳarîbüm mine-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1011, 'vehüve-lleẕî yürsilü-rriyâḥa büşram beyne yedey raḥmetih. ḥattâ iẕâ eḳallet seḥâben ŝiḳâlen suḳnâhü libeledim meyyitin feenzelnâ bihi-lmâe feaḫracnâ bihî min külli-ŝŝemerât. keẕâlike nuḫricü-lmevtâ le\\`alleküm teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1012, 'velbeledu-ṭṭayyibü yaḫrucü nebâtühû biiẕni rabbih. velleẕî ḫabüŝe lâ yaḫrucü illâ nekidâ. keẕâlike nüṣarrifü-l\'âyâti liḳavmiy yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (1013, 'leḳad erselnâ nûḥan ilâ ḳavmihî feḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. innî eḫâfü \\`aleyküm \\`aẕâbe yevmin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1014, 'ḳâle-lmeleü min ḳavmihî innâ lenerâke fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1015, 'ḳâle yâ ḳavmi leyse bî ḍalâletüv velâkinnî rasûlüm mir rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1016, 'übelliguküm risâlâti rabbî veenṣaḥu leküm vea\\`lemü mine-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1017, 'eve\\`acibtüm en câeküm ẕikrum mir rabbiküm \\`alâ racülim minküm liyünẕiraküm velitetteḳû vele\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (1018, 'fekeẕẕebûhü feenceynâhü velleẕîne me\\`ahû fi-lfülki veagraḳne-lleẕîne keẕẕebû biâyâtinâ. innehüm kânû ḳavmen \\`amîn.');
INSERT INTO `tr_transliteration` VALUES (1019, 'veilâ \\`âdin eḫâhüm hûdâ. ḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. efelâ tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1020, 'ḳâle-lmeleü-lleẕîne keferû min ḳavmihî innâ lenerâke fî sefâhetiv veinnâ leneżunnüke mine-lkâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (1021, 'ḳâle yâ ḳavmi leyse bî sefâhetüv velâkinnî rasûlüm mir rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1022, 'übelliguküm risâlâti rabbî veenâ leküm nâṣiḥun emîn.');
INSERT INTO `tr_transliteration` VALUES (1023, 'eve\\`acibtüm en câeküm ẕikrum mir rabbiküm \\`alâ racülim minküm liyünẕiraküm. veẕkürû iẕ ce\\`aleküm ḫulefâe mim ba\\`di ḳavmi nûḥiv vezâdeküm fi-lḫalḳi baṣṭah. feẕkürû âlâe-llâhi le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (1024, 'ḳâlû eci\'tenâ lina\\`büde-llâhe vaḥdehû veneẕera mâ kâne ya\\`büdü âbâünâ. fe\'tinâ bimâ te\\`idünâ in künte mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1025, 'ḳâle ḳad veḳa\\`a \\`aleyküm mir rabbiküm ricsüv vegaḍab. etücâdilûnenî fî esmâin semmeytümûhâ entüm veâbâüküm mâ nezzele-llâhü bihâ min sülṭân. fenteżirû innî me\\`aküm mine-lmünteżirîn.');
INSERT INTO `tr_transliteration` VALUES (1026, 'feenceynâhü velleẕîne me\\`ahû biraḥmetim minnâ veḳaṭa\\`nâ dâbira-lleẕîne keẕẕebû biâyâtinâ vemâ kânû mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1027, 'veilâ ŝemûde eḫâhüm ṣâliḥâ. ḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. ḳad câetküm beyyinetüm mir rabbiküm. hâẕihî nâḳatü-llâhi leküm âyeten feẕerûhâ te\'kül fî arḍi-llâhi velâ temessûhâ bisûin feye\'ḫuẕeküm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1028, 'veẕkürû iẕ ce\\`aleküm ḫulefâe mim ba\\`di \\`âdiv vebevveeküm fi-l\'arḍi tetteḫiẕûne min sühûlihâ ḳuṣûrav vetenḥitûne-lcibâle büyûtâ. feẕkürû âlâe-llâhi velâ ta\\`ŝev fi-l\'arḍi müfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1029, 'ḳâle-lmeleü-lleẕîne-stekberû min ḳavmihî lilleẕîne-stuḍ\\`ifû limen âmene minhüm eta\\`lemûne enne ṣâliḥam mürselüm mir rabbih. ḳâlû innâ bimâ ürsile bihî mü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1030, 'ḳâle-lleẕîne-stekberû innâ billeẕî âmentüm bihî kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1031, 'fe\\`aḳarü-nnâḳate ve\\`atev \\`an emri rabbihim veḳâlû yâ ṣâliḥu-\'tinâ bimâ te\\`idünâ in künte mine-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (1032, 'feeḫaẕethümü-rracfetü feaṣbeḥû fî dârihim câŝimîn.');
INSERT INTO `tr_transliteration` VALUES (1033, 'fetevellâ \\`anhüm veḳâle yâ ḳavmi leḳad eblagtüküm risâlete rabbî veneṣaḥtü leküm velâkil lâ tüḥibbûne-nnâṣiḥîn.');
INSERT INTO `tr_transliteration` VALUES (1034, 'velûṭan iẕ ḳâle liḳavmihî ete\'tûne-lfâḥişete mâ sebeḳaküm bihâ min eḥadim mine-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1035, 'inneküm lete\'tûne-rricâle şehvetem min dûni-nnisâ\'. bel entüm ḳavmüm müsrifûn.');
INSERT INTO `tr_transliteration` VALUES (1036, 'vemâ kâne cevâbe ḳavmihî illâ en ḳâlû aḫricûhüm min ḳaryetiküm. innehüm ünâsüy yeteṭahherûn.');
INSERT INTO `tr_transliteration` VALUES (1037, 'feenceynâhü veehlehû ille-mraeteh. kânet mine-lgâbirîn.');
INSERT INTO `tr_transliteration` VALUES (1038, 'veemṭarnâ \\`aleyhim meṭarâ. fenżur keyfe kâne \\`âḳibetü-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1039, 'veilâ medyene eḫâhüm şu\\`aybâ. ḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. ḳad câetküm beyyinetüm mir rabbiküm feevfü-lkeyle velmîzâne velâ tebḫasü-nnâse eşyâehüm velâ tüfsidû fi-l\'arḍi ba\\`de iṣlâḥihâ. ẕâliküm ḫayrul leküm in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1040, 'velâ taḳ\\`udû bikülli ṣirâṭin tû\\`idûne veteṣuddûne \\`an sebîli-llâhi men âmene bihî vetebgûnehâ \\`ivecâ. veẕkürû iẕ küntüm ḳalîlen fekeŝŝeraküm. venżurû keyfe kâne \\`âḳibetü-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1041, 'vein kâne ṭâifetüm minküm âmenû billeẕî ürsiltü bihî veṭâifetül lem yü\'minû faṣbirû ḥattâ yaḥküme-llâhü beynenâ. vehüve ḫayru-lḥâkimîn.');
INSERT INTO `tr_transliteration` VALUES (1042, 'ḳâle-lmeleü-lleẕîne-stekberû min ḳavmihî lenuḫricenneke yâ şu\\`aybü velleẕîne âmenû me\\`ake min ḳaryetinâ ev lete\\`ûdünne fî milletinâ. ḳâle evelev künnâ kârihîn.');
INSERT INTO `tr_transliteration` VALUES (1043, 'ḳadi-fteraynâ \\`ale-llâhi keẕiben in \\`udnâ fî milletiküm ba\\`de iẕ neccâne-llâhü minhâ. vemâ yekûnü lenâ en ne\\`ûde fîhâ illâ ey yeşâe-llâhü rabbünâ. vesi\\`a rabbünâ külle şey\'in \\`ilmâ. \\`ale-llâhi tevekkelnâ. rabbene-ftaḥ beynenâ vebeyne ḳavminâ bilḥaḳḳi veente ḫayru-lfâtiḥîn.');
INSERT INTO `tr_transliteration` VALUES (1044, 'veḳâle-lmeleü-lleẕîne keferû min ḳavmihî leini-tteba\\`tüm şu\\`ayben inneküm iẕel leḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (1045, 'feeḫaẕethümü-rracfetü feaṣbeḥû fî dârihim câŝimîn.');
INSERT INTO `tr_transliteration` VALUES (1046, 'elleẕîne keẕẕebû şu\\`ayben keel lem yagnev fîhâ. elleẕîne keẕẕebû şu\\`ayben kânû hümü-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (1047, 'fetevellâ \\`anhüm veḳâle yâ ḳavmi leḳad eblagtüküm risâlâti rabbî veneṣaḥtü leküm. fekeyfe âsâ \\`alâ ḳavmin kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1048, 'vemâ erselnâ fî ḳaryetim min nebiyyin illâ eḫaẕnâ ehlehâ bilbe\'sâi veḍḍarrâi le\\`allehüm yeḍḍarra\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1049, 'ŝümme beddelnâ mekâne-sseyyieti-lḥasenete ḥattâ \\`afev veḳâlû ḳad messe âbâene-ḍḍarrâü vesserrâü feeḫaẕnâhüm bagtetev vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (1050, 'velev enne ehle-lḳurâ âmenû vetteḳav lefetaḥnâ \\`aleyhim berakâtim mine-ssemâi vel\'arḍi velâkin keẕẕebû feeḫaẕnâhüm bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (1051, 'efeemine ehlü-lḳurâ ey ye\'tiyehüm be\'sünâ beyâtev vehüm nâimûn.');
INSERT INTO `tr_transliteration` VALUES (1052, 'eveemine ehlü-lḳurâ ey ye\'tiyehüm be\'sünâ ḍuḥav vehüm yel\\`abûn.');
INSERT INTO `tr_transliteration` VALUES (1053, 'efeeminû mekra-llâh. felâ ye\'menü mekra-llâhi ille-lḳavmü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (1054, 'evelem yehdi lilleẕîne yeriŝûne-l\'arḍa mim ba\\`di ehlihâ el lev neşâü eṣabnâhüm biẕünûbihim. venaṭbe\\`u \\`alâ ḳulûbihim fehüm lâ yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1055, 'tilke-lḳurâ neḳuṣṣu \\`aleyke min embâihâ. veleḳad câethüm rusülühüm bilbeyyinât. femâ kânû liyü\'minû bimâ keẕẕebû min ḳabl. keẕâlike yaṭbe\\`u-llâhü \\`alâ ḳulûbi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1056, 'vemâ vecednâ liekŝerihim min \\`ahd. veiv vecednâ ekŝerahüm lefâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1057, 'ŝümme be\\`aŝnâ mim ba\\`dihim mûsâ biâyâtinâ ilâ fir\\`avne vemeleihî feżalemû bihâ. fenżur keyfe kâne \\`âḳibetü-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1058, 'veḳâle mûsâ yâ fir\\`avnü innî rasûlüm mir rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1059, 'ḥaḳîḳun \\`alâ el lâ eḳûle \\`ale-llâhi ille-lḥaḳḳ. ḳad ci\'tüküm bibeyyinetim mir rabbiküm feersil me\\`iye benî isrâîl.');
INSERT INTO `tr_transliteration` VALUES (1060, 'ḳâle in künte ci\'te biâyetin fe\'ti bihâ in künte mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1061, 'feelḳâ \\`aṣâhü feiẕâ hiye ŝü\\`bânüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (1062, 'veneza\\`a yedehû feiẕâ hiye beyḍâü linnâżirîn.');
INSERT INTO `tr_transliteration` VALUES (1063, 'ḳâle-lmeleü min ḳavmi fir\\`avne inne hâẕâ lesâḥirun \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1064, 'yürîdü ey yuḫriceküm min arḍiküm. femâẕâ te\'mürûn.');
INSERT INTO `tr_transliteration` VALUES (1065, 'ḳâlû ercih veeḫâhü veersil fi-lmedâini ḥâşirîn.');
INSERT INTO `tr_transliteration` VALUES (1066, 'ye\'tûke bikülli sâḥirin \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1067, 'vecâe-sseḥaratü fir\\`avne ḳâlû inne lenâ leecran in künnâ naḥnü-lgâlibîn.');
INSERT INTO `tr_transliteration` VALUES (1068, 'ḳâle ne\\`am veinneküm lemine-lmüḳarrabîn.');
INSERT INTO `tr_transliteration` VALUES (1069, 'ḳâlû yâ mûsâ immâ en tülḳiye veimmâ en nekûne naḥnü-lmülḳîn.');
INSERT INTO `tr_transliteration` VALUES (1070, 'ḳâle elḳû. felemmâ elḳav seḥarû a\\`yüne-nnâsi vesterhebûhüm vecâû bisiḥrin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1071, 'veevḥaynâ ilâ mûsâ en elḳi \\`aṣâk. feiẕâ hiye telḳafü mâ ye\'fikûn.');
INSERT INTO `tr_transliteration` VALUES (1072, 'feveḳa\\`a-lḥaḳḳu vebeṭale mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1073, 'fegulibû hünâlike venḳalebû ṣâgirîn.');
INSERT INTO `tr_transliteration` VALUES (1074, 'veülḳiye-sseḥaratü sâcidîn.');
INSERT INTO `tr_transliteration` VALUES (1075, 'ḳâlû âmennâ birabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1076, 'rabbi mûsâ vehârûn.');
INSERT INTO `tr_transliteration` VALUES (1077, 'ḳâle fir\\`avnü âmentüm bihî ḳable en âẕene leküm. inne hâẕâ lemekrum mekertümûhü fi-lmedîneti lituḫricû minhâ ehlehâ. fesevfe ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1078, 'leüḳaṭṭi\\`anne eydiyeküm veercüleküm min ḫilâfin ŝümme leüṣallibenneküm ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (1079, 'ḳâlû innâ ilâ rabbinâ münḳalibûn.');
INSERT INTO `tr_transliteration` VALUES (1080, 'vemâ tenḳimü minnâ illâ en âmennâ biâyâti rabbinâ lemmâ câetnâ. rabbenâ efrig \\`aleynâ ṣabrav veteveffenâ müslimîn.');
INSERT INTO `tr_transliteration` VALUES (1081, 'veḳâle-lmeleü min ḳavmi fir\\`avne eteẕeru mûsâ veḳavmehû liyüfsidû fi-l\'arḍi veyeẕerake veâlihetek. ḳâle senüḳattilü ebnâehüm venestaḥyî nisâehüm. veinnâ fevḳahüm ḳâhirûn.');
INSERT INTO `tr_transliteration` VALUES (1082, 'ḳâle mûsâ liḳavmihi-ste\\`înû billâhi vaṣbirû. inne-l\'arḍa lillâh. yûriŝühâ mey yeşâü min \\`ibâdih. vel\\`âḳibetü lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1083, 'ḳâlû ûẕîne min ḳabli en te\'tiyenâ vemim ba\\`di mâ ci\'tenâ. ḳâle \\`asâ rabbüküm ey yühlike \\`adüvveküm veyestaḫlifeküm fi-l\'arḍi feyenżura keyfe ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1084, 'veleḳad eḫaẕnâ âle fir\\`avne bissinîne venaḳṣim mine-ŝŝemerâti le\\`allehüm yeẕẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1085, 'feiẕâ câethümü-lḥasenetü ḳâlû lenâ hâẕih. vein tüṣibhüm seyyietüy yeṭṭayyerû bimûsâ vemem me\\`ah. elâ innemâ ṭâiruhüm \\`inde-llâhi velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1086, 'veḳâlû mehmâ te\'tinâ bihî min âyetil litesḥaranâ bihâ femâ naḥnü leke bimü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1087, 'feerselnâ \\`aleyhimu-ṭṭûfâne velcerâde velḳummele veḍḍafâdi\\`a veddeme âyâtim müfeṣṣalâtin festekberû vekânû ḳavmem mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1088, 'velemmâ veḳa\\`a \\`aleyhimü-rriczü ḳâlû yâ mûse-d\\`u lenâ rabbeke bimâ \\`ahide \\`indek. lein keşefte \\`anne-rricze lenü\'minenne leke velenürsilenne me\\`ake benî isrâîl.');
INSERT INTO `tr_transliteration` VALUES (1089, 'felemmâ keşefnâ \\`anhümü-rricze ilâ ecelin hüm bâligûhü iẕâ hüm yenküŝûn.');
INSERT INTO `tr_transliteration` VALUES (1090, 'fenteḳamnâ minhüm feagraḳnâhüm fi-lyemmi biennehüm keẕẕebû biâyâtinâ vekânû \\`anhâ gâfilîn.');
INSERT INTO `tr_transliteration` VALUES (1091, 'veevraŝne-lḳavme-lleẕîne kânû yüstaḍ\\`afûne meşâriḳa-l\'arḍi vemegâribehe-lletî bâraknâ fîhâ. vetemmet kelimetü rabbike-lḥusnâ \\`alâ benî isrâîle bimâ ṣaberû. vedemmernâ mâ kâne yaṣne\\`u fir\\`avnü veḳavmühû vemâ kânû ya\\`rişûn.');
INSERT INTO `tr_transliteration` VALUES (1092, 'vecâveznâ bibenî isrâîle-lbaḥra feetev \\`alâ ḳavmiy ya\\`küfûne \\`alâ aṣnâmil lehüm. ḳâlû yâ mûse-c\\`al lenâ ilâhen kemâ lehüm âliheh. ḳâle inneküm ḳavmün techelûn.');
INSERT INTO `tr_transliteration` VALUES (1093, 'inne hâülâi mütebberum mâ hüm fîhi vebâṭilüm mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1094, 'ḳâle egayra-llâhi ebgîküm ilâhev vehüve feḍḍaleküm \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1095, 'veiẕ enceynâküm min âli fir\\`avne yesûmûneküm sûe-l\\`aẕâb. yüḳattilûne ebnâeküm veyestaḥyûne nisâeküm. vefî ẕâliküm belâüm mir rabbiküm \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1096, 'vevâ\\`adnâ mûsâ ŝelâŝîne leyletev veetmemnâhâ bi\\`aşrin fetemme mîḳâtü rabbihî erbe\\`îne leyleh. veḳâle mûsâ lieḫîhi hârûne-ḫlüfnî fî ḳavmî veaṣliḥ velâ tettebi\\` sebîle-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1097, 'velemmâ câe mûsâ limîḳâtinâ vekellemehû rabbühû ḳâle rabbi erinî enżur ileyk. ḳâle len terânî velâkini-nżur ile-lcebeli feini-steḳarra mekânehû fesevfe terânî. felemmâ tecellâ rabbühû lilcebeli ce\\`alehû dekkev veḫarra mûsâ ṣa\\`iḳâ. felemmâ efâḳa ḳâle sübḥâneke tübtü ileyke veenâ evvelü-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1098, 'ḳâle yâ mûsâ inni-ṣṭafeytüke \\`ale-nnâsi birisâletî vebikelâmî. feḫuẕ mâ âteytüke veküm mine-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (1099, 'veketebnâ lehû fi-l\'elvâḥi min külli şey\'im mev\\`iżatev vetefṣîlel likülli şey\'. feḫuẕhâ biḳuvvetiv ve\'mür ḳavmeke ye\'ḫuẕû biaḥsenihâ. seürîküm dâra-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1100, 'seaṣrifü \\`an âyâtiye-lleẕîne yetekebberûne fi-l\'arḍi bigayri-lḥaḳḳ. veiy yerav külle âyetil lâ yü\'minû bihâ. veiy yerav sebîle-rruşdi lâ yetteḫiẕûhü sebîlâ. veiy yerav sebîle-lgayyi yetteḫiẕûhü sebîlâ. ẕâlike biennehüm keẕẕebû biâyâtinâ vekânû \\`anhâ gâfilîn.');
INSERT INTO `tr_transliteration` VALUES (1101, 'velleẕîne keẕẕebû biâyâtinâ veliḳâi-l\'âḫirati ḥabiṭat a\\`mâlühüm. hel yüczevne illâ mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1102, 'vetteḫaẕe ḳavmü mûsâ mim ba\\`dihî min ḥuliyyihim \\`iclen cesedel lehû ḫuvâr. elem yerav ennehû lâ yükellimühüm velâ yehdîhim sebîlâ. itteḫaẕûhü vekânû żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1103, 'velemmâ süḳiṭa fî eydîhim veraev ennehüm ḳad ḍallû ḳâlû leil lem yerḥamnâ rabbünâ veyagfir lenâ lenekûnenne mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (1104, 'velemmâ race\\`a mûsâ ilâ ḳavmihî gaḍbâne esifen ḳâle bi\'semâ ḫaleftümûnî mim ba\\`dî. e\\`aciltüm emra rabbiküm. veelḳa-l\'elvâḥa veeḫaẕe bira\'si eḫîhi yecürruhû ileyh. ḳâle-bne ümme inne-lḳavme-staḍ\\`afûnî vekâdû yaḳtülûnenî. felâ tüşmit biye-l\'a\\`dâe velâ tec\\`alnî me\\`a-lḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1105, 'ḳâle rabbi-gfir lî velieḫî veedḫilnâ fî raḥmetik. veente erḥamü-rrâḥimîn.');
INSERT INTO `tr_transliteration` VALUES (1106, 'inne-lleẕîne-tteḫaẕü-l\\`icle seyenâlühüm gaḍabüm mir rabbihim veẕilletün fi-lḥayâti-ddünyâ. vekeẕâlike neczi-lmüfterîn.');
INSERT INTO `tr_transliteration` VALUES (1107, 'velleẕîne \\`amilü-sseyyiâti ŝümme tâbû mim ba\\`dihâ veâmenû. inne rabbeke mim ba\\`dihâ legafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1108, 'velemmâ sekete \\`am mûse-lgaḍabü eḫaẕe-l\'elvâḥ. vefî nüsḫatihâ hüdev veraḥmetül lilleẕîne hüm lirabbihim yerhebûn.');
INSERT INTO `tr_transliteration` VALUES (1109, 'vaḫtâra mûsâ ḳavmehû seb\\`îne racülel limîḳâtinâ. felemmâ eḫaẕethümü-rracfetü ḳâle rabbi lev şi\'te ehlektehüm min ḳablü veiyyây. etühlikünâ bimâ fe\\`ale-ssüfehâü minnâ. in hiye illâ fitnetük. tüḍillü bihâ men teşâü vetehdî men teşâ\'. ente veliyyünâ fagfir lenâ verḥamnâ veente ḫayru-lgâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1110, 'vektüb lenâ fî hâẕihi-ddünyâ ḥasenetev vefi-l\'âḫirati innâ hüdnâ ileyk. ḳâle \\`aẕâbî üṣîbü bihî men eşâ\'. veraḥmetî vesi\\`at külle şey\'. feseektübühâ lilleẕîne yetteḳûne veyü\'tûne-zzekâte velleẕîne hüm biâyâtinâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1111, 'elleẕîne yettebi\\`ûne-rrasûle-nnebiyye-l\'ümmiyye-lleẕî yecidûnehû mektûben \\`indehüm fi-ttevrâti vel\'incîl. ye\'müruhüm bilma\\`rûfi veyenhâhüm \\`ani-lmünkeri veyüḥillü lehümu-ṭṭayyibâti veyüḥarrimü \\`aleyhimü-lḫabâiŝe veyeḍa\\`u \\`anhüm iṣrahüm vel\'aglâle-lletî kânet \\`aleyhim. felleẕîne âmenû bihî ve\\`azzerûhü veneṣarûhü vettebe\\`ü-nnûra-lleẕî ünzile me\\`ahû ülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (1112, 'ḳul yâ eyyühe-nnâsü innî rasûlü-llâhi ileyküm cemî\\`an-lleẕî lehû mülkü-ssemâvâti vel\'arḍ. lâ ilâhe illâ hüve yuḥyî veyümît. feâminû billâhi verasûlihi-nnebiyyi-l\'ümmiyyi-lleẕî yü\'minü billâhi vekelimâtihî vettebi\\`ûhü le\\`alleküm tehtedûn.');
INSERT INTO `tr_transliteration` VALUES (1113, 'vemin ḳavmi mûsâ ümmetüy yehdûne bilḥaḳḳi vebihî ya\\`dilûn.');
INSERT INTO `tr_transliteration` VALUES (1114, 'veḳaṭṭa\\`nâhümü-ŝnetey \\`aşrate esbâṭan ümemâ. veevḥaynâ ilâ mûsâ iẕi-stesḳâhü ḳavmüh eni-ḍrib bi\\`aṣâke-lḥacer. fembeceset minhü-ŝnetâ \\`aşrate \\`aynâ. ḳad \\`alime küllü ünâsim meşrabehüm. veżallelnâ \\`aleyhimü-lgamâme veenzelnâ \\`aleyhimü-lmenne vesselvâ. külû min ṭayyibâti mâ razaḳnâküm. vemâ żalemûnâ velâkin kânû enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (1115, 'veiẕ ḳîle lehümü-skünû hâẕihi-lḳaryete vekülû minhâ ḥayŝü şi\'tüm veḳûlû ḥiṭṭatüv vedḫulü-lbâbe sücceden nagfir leküm ḫatiâtiküm. senezîdü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1116, 'febeddele-lleẕîne żalemû minhüm ḳavlen gayra-lleẕî ḳîle lehüm feerselnâ \\`aleyhim riczem mine-ssemâi bimâ kânû yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (1117, 'ves\'elhüm \\`ani-lḳaryeti-lletî kânet ḥâḍirate-lbaḥr. iẕ ya\\`dûne fi-ssebti iẕ te\'tîhim ḥîtânühüm yevme sebtihim şürra\\`av veyevme lâ yesbitûne lâ te\'tîhim. keẕâlike neblûhüm bimâ kânû yefsüḳûn.');
INSERT INTO `tr_transliteration` VALUES (1118, 'veiẕ ḳâlet ümmetüm minhüm lime te\\`iżûne ḳavmen-llâhü mühlikühüm ev mü\\`aẕẕibühüm \\`aẕâben şedîdâ. ḳâlû ma\\`ẕiraten ilâ rabbiküm vele\\`allehüm yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1119, 'felemmâ nesû mâ ẕükkirû bihî enceyne-lleẕîne yenhevne \\`ani-ssûi veeḫaẕne-lleẕîne żalemû bi\\`aẕâbim beîsim bimâ kânû yefsüḳûn.');
INSERT INTO `tr_transliteration` VALUES (1120, 'felemmâ \\`atev \\`am mâ nühû \\`anhü ḳulnâ lehüm kûnû ḳiradeten ḫâsiîn.');
INSERT INTO `tr_transliteration` VALUES (1121, 'veiẕ teeẕẕene rabbüke leyeb\\`aŝenne \\`aleyhim ilâ yevmi-lḳiyâmeti mey yesûmühüm sûe-l\\`aẕâb. inne rabbeke leserî\\`u-l\\`iḳâb. veinnehû legafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1122, 'veḳaṭṭa\\`nâhüm fi-l\'arḍi ümemâ. minhümu-ṣṣâliḥûne veminhüm dûne ẕâlik. vebelevnâhüm bilḥasenâti vesseyyiâti le\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1123, 'feḫalefe mim ba\\`dihim ḫalfüv veriŝü-lkitâbe ye\'ḫuẕûne \\`araḍa hâẕe-l\'ednâ veyeḳûlûne seyugferu lenâ. veiy ye\'tihim \\`araḍum miŝlühû ye\'ḫuẕûh. elem yü\'ḫaẕ \\`aleyhim mîŝâḳu-lkitâbi el lâ yeḳûlû \\`ale-llâhi ille-lḥaḳḳa vederasû mâ fîh. veddâru-l\'âḫiratü ḫayrul lilleẕîne yetteḳûn. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1124, 'velleẕîne yümessikûne bilkitâbi veeḳâmu-ṣṣalâh. innâ lâ nüḍî\\`u ecra-lmuṣliḥîn.');
INSERT INTO `tr_transliteration` VALUES (1125, 'veiẕ netaḳne-lcebele fevḳahüm keennehû żulletüv veżannû ennehû vâḳi\\`um bihim. ḫuẕû mâ âteynâküm biḳuvvetiv veẕkürû mâ fîhi le\\`alleküm tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1126, 'veiẕ eḫaẕe rabbüke mim benî âdeme min żuhûrihim ẕürriyyetehüm veeşhedehüm \\`alâ enfüsihim. elestü birabbiküm. ḳâlû belâ. şehidnâ. en teḳûlû yevme-lḳiyâmeti innâ künnâ \\`an hâẕâ gâfilîn.');
INSERT INTO `tr_transliteration` VALUES (1127, 'ev teḳûlû innemâ eşrake âbâünâ min ḳablü vekünnâ ẕürriyyetem mim ba\\`dihim. efetühlikünâ bimâ fe\\`ale-lmübṭilûn.');
INSERT INTO `tr_transliteration` VALUES (1128, 'vekeẕâlike nüfeṣṣilü-l\'âyâti vele\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1129, 'vetlü \\`aleyhim nebee-lleẕî âteynâhü âyâtinâ fenseleḫa minhâ feetbe\\`ahü-şşeyṭânü fekâne mine-lgâvîn.');
INSERT INTO `tr_transliteration` VALUES (1130, 'velev şi\'nâ lerafa\\`nâhü bihâ velâkinnehû aḫlede ile-l\'arḍi vettebe\\`a hevâh. femeŝelühû kemeŝeli-lkelb. in taḥmil \\`aleyhi yelheŝ ev tetrukhü yelheŝ. ẕâlike meŝelü-lḳavmi-lleẕîne keẕẕebû biâyâtinâ. faḳṣuṣi-lḳaṣaṣa le\\`allehüm yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1131, 'sâe meŝelen-lḳavmü-lleẕîne keẕẕebû biâyâtinâ veenfüsehüm kânû yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (1132, 'mey yehdi-llâhü fehüve-lmühtedî. vemey yuḍlil feülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (1133, 'veleḳad ẕera\'nâ licehenneme keŝîram mine-lcinni vel\'ins. lehüm ḳulûbül lâ yefḳahûne bihâ. velehüm a\\`yünül lâ yübṣirûne bihâ. velehüm âẕânül lâ yesme\\`ûne bihâ. ülâike kel\'en\\`âmi bel hüm eḍall. ülâike hümü-lgâfilûn.');
INSERT INTO `tr_transliteration` VALUES (1134, 'velillâhi-l\'esmâü-lḥusnâ fed\\`ûhü bihâ. veẕerü-lleẕîne yülḥidûne fî esmâih. seyüczevne mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1135, 'vemimmen ḫalaḳnâ ümmetüy yehdûne bilḥaḳḳi vebihî ya\\`dilûn.');
INSERT INTO `tr_transliteration` VALUES (1136, 'velleẕîne keẕẕebû biâyâtinâ senestedricühüm min ḥayŝü lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1137, 'veümlî lehüm. inne keydî metîn.');
INSERT INTO `tr_transliteration` VALUES (1138, 'evelem yetefekkerû mâ biṣâḥibihim min cinneh. in hüve illâ neẕîrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (1139, 'evelem yenżurû fî melekûti-ssemâvâti vel\'arḍi vemâ ḫaleḳa-llâhü min şey\'iv veen \\`asâ ey yekûne ḳadi-ḳterabe ecelühüm. febieyyi ḥadîŝim ba\\`dehû yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1140, 'mey yuḍlili-llâhü felâ hâdiye leh. veyeẕeruhüm fî ṭugyânihim ya\\`mehûn.');
INSERT INTO `tr_transliteration` VALUES (1141, 'yes\'elûneke \\`ani-ssâ\\`ati eyyâne mürsâhâ. ḳul innemâ \\`ilmühâ \\`inde rabbî. lâ yücellîhâ livaḳtihâ illâ hû. ŝeḳulet fi-ssemâvâti vel\'arḍ. lâ te\'tîküm illâ bagteten. yes\'elûneke keenneke ḥafiyyün \\`anhâ. ḳul innemâ \\`ilmühâ \\`inde-llâhi velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1142, 'ḳul lâ emlikü linefsî nef\\`av velâ ḍarran illâ mâ şâe-llâh. velev küntü a\\`lemü-lgaybe lestekŝertü mine-lḫayr. vemâ messeniye-ssûü in ene illâ neẕîruv vebeşîrul liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1143, 'hüve-lleẕî ḫaleḳaküm min nefsiv vâḥidetiv vece\\`ale minhâ zevcehâ liyesküne ileyhâ. felemmâ tegaşşâhâ ḥamelet ḥamlen ḫafîfen femerrat bih. felemmâ eŝḳalet de\\`ave-llâhe rabbehümâ lein âteytenâ ṣâliḥal lenekûnenne mine-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (1144, 'felemmâ âtâhümâ ṣâliḥan ce\\`alâ lehû şürakâe fîmâ âtâhümâ. fete\\`âle-llâhü \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (1145, 'eyüşrikûne mâ lâ yaḫlüḳu şey\'ev vehüm yuḫleḳûn.');
INSERT INTO `tr_transliteration` VALUES (1146, 'velâ yesteṭî\\`ûne lehüm naṣrav velâ enfüsehüm yenṣurûn.');
INSERT INTO `tr_transliteration` VALUES (1147, 'vein ted\\`ûhüm ile-lhüdâ lâ yettebi\\`ûküm. sevâün \\`aleyküm ede\\`avtümûhüm em entüm ṣâmitûn.');
INSERT INTO `tr_transliteration` VALUES (1148, 'inne-lleẕîne ted\\`ûne min dûni-llâhi \\`ibâdün emŝâlüküm fed\\`ûhüm felyestecîbû leküm in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1149, 'elehüm ercülüy yemşûne bihâ. em lehüm eydiy yebṭişûne bihâ. em lehüm a\\`yünüy yübṣirûne bihâ. em lehüm âẕânüy yesme\\`ûne bihâ. ḳuli-d\\`û şürakâeküm ŝümme kîdûni felâ tünżirûn.');
INSERT INTO `tr_transliteration` VALUES (1150, 'inne veliyyiye-llâhü-lleẕî nezzele-lkitâb. vehüve yetevelle-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (1151, 'velleẕîne ted\\`ûne min dûnihî lâ yesteṭî\\`ûne naṣraküm velâ enfüsehüm yenṣurûn.');
INSERT INTO `tr_transliteration` VALUES (1152, 'vein ted\\`ûhüm ile-lhüdâ lâ yesme\\`û. veterâhüm yenżurûne ileyke vehüm lâ yübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (1153, 'ḫuẕi-l\\`afve ve\'mür bil\\`urfi vea\\`riḍ \\`ani-lcâhilîn.');
INSERT INTO `tr_transliteration` VALUES (1154, 'veimmâ yenzeganneke mine-şşeyṭâni nezgun feste\\`iẕ billâh. innehû semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1155, 'inne-lleẕîne-tteḳav iẕâ messehüm ṭâifüm mine-şşeyṭâni teẕekkerû feiẕâ hüm mübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (1156, 'veiḫvânühüm yemüddûnehüm fi-lgayyi ŝümme lâ yuḳṣirûn.');
INSERT INTO `tr_transliteration` VALUES (1157, 'veiẕâ lem te\'tihim biâyetin ḳâlû levle-ctebeytehâ. ḳul innemâ ettebi\\`u mâ yûḥâ ileyye mir rabbî. hâẕâ beṣâiru mir rabbiküm vehüdev veraḥmetül liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1158, 'veiẕâ ḳurie-lḳur\'ânü festemi\\`û lehû veenṣitû le\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (1159, 'veẕkür rabbeke fî nefsike teḍarru\\`av veḫîfetev vedûne-lcehri mine-lḳavli bilgudüvvi vel\'eṣâli velâ teküm mine-lgâfilîn.');
INSERT INTO `tr_transliteration` VALUES (1160, 'inne-lleẕîne \\`inde rabbike lâ yestekbirûne \\`an \\`ibâdetihî veyüsebbiḥûnehû velehû yescüdûn.');
INSERT INTO `tr_transliteration` VALUES (1161, 'yes\'elûneke \\`ani-l\'enfâl. ḳuli-l\'enfâlü lillâhi verrasûl. fetteḳu-llâhe veaṣliḥû ẕâte beyniküm. veeṭî\\`ü-llâhe verasûlehû in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1162, 'inneme-lmü\'minûne-lleẕîne iẕâ ẕükira-llâhü vecilet ḳulûbühüm veiẕâ tüliyet \\`aleyhim âyâtühû zâdethüm îmânev ve\\`alâ rabbihim yetevekkelûn.');
INSERT INTO `tr_transliteration` VALUES (1163, 'elleẕîne yüḳîmûne-ṣṣalâte vemimmâ razaḳnâhüm yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (1164, 'ülâike hümü-lmü\'minûne ḥaḳḳâ. lehüm deracâtün \\`inde rabbihim vemagfiratüv verizḳun kerîm.');
INSERT INTO `tr_transliteration` VALUES (1165, 'kemâ aḫraceke rabbüke mim beytike bilḥaḳḳ. veinne ferîḳam mine-lmü\'minîne lekârihûn.');
INSERT INTO `tr_transliteration` VALUES (1166, 'yücâdilûneke fi-lḥaḳḳi ba\\`de mâ tebeyyene keennemâ yüsâḳûne ile-lmevti vehüm yenżurûn.');
INSERT INTO `tr_transliteration` VALUES (1167, 'veiẕ ye\\`idükümü-llâhü iḥde-ṭṭâifeteyni ennehâ leküm veteveddûne enne gayra ẕâti-şşevketi tekûnü leküm veyürîdü-llâhü ey yüḥiḳḳa-lḥaḳḳa bikelimâtihî veyaḳṭa\\`a dâbira-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1168, 'liyüḥiḳḳa-lḥaḳḳa veyübṭile-lbâṭile velev kerihe-lmücrimûn.');
INSERT INTO `tr_transliteration` VALUES (1169, 'iẕ testegîŝûne rabbeküm festecâbe leküm ennî mümiddüküm bielfim mine-lmelâiketi mürdifîn.');
INSERT INTO `tr_transliteration` VALUES (1170, 'vemâ ce\\`alehü-llâhü illâ büşrâ velitaṭmeinne bihî ḳulûbüküm. veme-nnaṣru illâ min \\`indi-llâh. inne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1171, 'iẕ yügaşşîkümü-nnü\\`âse emenetem minhü veyünezzilü \\`aleyküm mine-ssemâi mâel liyüṭahhiraküm bihî veyüẕhibe \\`anküm ricze-şşeyṭâni veliyerbiṭa \\`alâ ḳulûbiküm veyüŝebbite bihi-l\'aḳdâm.');
INSERT INTO `tr_transliteration` VALUES (1172, 'iẕ yûḥî rabbüke ile-lmelâiketi ennî me\\`aküm feŝebbitü-lleẕîne âmenû. seülḳî fî ḳulûbi-lleẕîne keferü-rru\\`be faḍribû fevḳa-l\'a\\`nâḳi vaḍribû minhüm külle benân.');
INSERT INTO `tr_transliteration` VALUES (1173, 'ẕâlike biennehüm şâḳḳu-llâhe verasûleh. vemey yüşâḳiḳi-llâhe verasûlehû feinne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (1174, 'ẕâliküm feẕûḳûhü veenne lilkâfirîne \\`aẕâbe-nnâr.');
INSERT INTO `tr_transliteration` VALUES (1175, 'yâ eyyühe-lleẕîne âmenû iẕâ leḳîtümü-lleẕîne keferû zaḥfen felâ tüvellûhümü-l\'edbâr.');
INSERT INTO `tr_transliteration` VALUES (1176, 'vemey yüvellihim yevmeiẕin dübürahû illâ müteḥarrifel liḳitâlin ev müteḥayyizen ilâ fietin feḳad bâe bigaḍabim mine-llâhi veme\'vâhü cehennem. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (1177, 'felem taḳtülûhüm velâkinne-llâhe ḳatelehüm. vemâ rameyte iẕ rameyte velâkinne-llâhe ramâ. veliyübliye-lmü\'minîne minhü belâen ḥasenâ. inne-llâhe semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1178, 'ẕâliküm veenne-llâhe mûhinü keydi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1179, 'in testeftiḥû feḳad câekümü-lfetḥ. vein tentehû fehüve ḫayrul leküm. vein te\\`ûdû ne\\`ud. velen tugniye \\`anküm fietüküm şey\'ev velev keŝürat veenne-llâhe me\\`a-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1180, 'yâ eyyühe-lleẕîne âmenû eṭî\\`ü-llâhe verasûlehû velâ tevellev \\`anhü veentüm tesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1181, 'velâ tekûnû kelleẕîne ḳâlû semi\\`nâ vehüm lâ yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1182, 'inne şerra-ddevâbbi \\`inde-llâhi-ṣṣummü-lbükmü-lleẕîne lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1183, 'velev \\`alime-llâhü fîhim ḫayral leesme\\`ahüm. velev esme\\`ahüm letevellev vehüm mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (1184, 'yâ eyyühe-lleẕîne âmenü-stecîbû lillâhi velirrasûli iẕâ de\\`âküm limâ yuḥyîküm. va\\`lemû enne-llâhe yeḥûlü beyne-lmer\'i veḳalbihî veennehû ileyhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (1185, 'vetteḳû fitnetel lâ tüṣîbenne-lleẕîne żalemû minküm ḫâṣṣah. va\\`lemû enne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (1186, 'veẕkürû iẕ entüm ḳalîlüm müstaḍ\\`afûne fi-l\'arḍi teḫâfûne ey yeteḫaṭṭafekümü-nnâsü feâvâküm veeyyedeküm binaṣrihî verazeḳaküm mine-ṭṭayyibâti le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (1187, 'yâ eyyühe-lleẕîne âmenû lâ teḫûnü-llâhe verrasûle veteḫûnû emânâtiküm veentüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1188, 'va\\`lemû ennemâ emvâlüküm veevlâdüküm fitnetüv veenne-llâhe \\`indehû ecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1189, 'yâ eyyühe-lleẕîne âmenû in tetteḳu-llâhe yec\\`al leküm fürḳânev veyükeffir \\`anküm seyyiâtiküm veyagfir leküm. vellâhü ẕü-lfaḍli-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1190, 'veiẕ yemküru bike-lleẕîne keferû liyüŝbitûke ev yaḳtülûke ev yuḫricûk. veyemkürûne veyemküru-llâh. vellâhü ḫayru-lmâkirîn.');
INSERT INTO `tr_transliteration` VALUES (1191, 'veiẕâ tütlâ \\`aleyhim âyâtünâ ḳâlû ḳad semi\\`nâ lev neşâü leḳulnâ miŝle hâẕâ in hâẕâ illâ esâṭîru-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (1192, 'veiẕ ḳâlü-llâhümme in kâne hâẕâ hüve-lḥaḳḳa min \\`indike feemṭir \\`aleynâ ḥicâratem mine-ssemâi evi-\'tinâ bi\\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (1193, 'vemâ kâne-llâhü liyü\\`aẕẕibehüm veente fîhim. vemâ kâne-llâhü mü\\`aẕẕibehüm vehüm yestagfirûn.');
INSERT INTO `tr_transliteration` VALUES (1194, 'vemâ lehüm ellâ yü\\`aẕẕibehümü-llâhü vehüm yeṣuddûne \\`ani-lmescidi-lḥarâmi vemâ kânû evliyâh. in evliyâühû ille-lmütteḳûne velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1195, 'vemâ kâne ṣalâtühüm \\`inde-lbeyti illâ mükâev vetaṣdiyeh. feẕûḳu-l\\`aẕâbe bimâ küntüm tekfürûn.');
INSERT INTO `tr_transliteration` VALUES (1196, 'inne-lleẕîne keferû yünfiḳûne emvâlehüm liyeṣuddû \\`an sebîli-llâh. feseyünfiḳûnehâ ŝümme tekûnü \\`aleyhim ḥasraten ŝümme yuglebûn. velleẕîne keferû ilâ cehenneme yuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (1197, 'liyemîze-llâhü-lḫabîŝe mine-ṭṭayyibi veyec\\`ale-lḫabîŝe ba\\`ḍahû \\`alâ ba\\`ḍin feyerkümehû cemî\\`an feyec\\`alehû fî cehennem. ülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (1198, 'ḳul lilleẕîne keferû iy yentehû yugfer lehüm mâ ḳad selef. veiy ye\\`ûdû feḳad meḍat sünnetü-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (1199, 'veḳâtilûhüm ḥattâ lâ tekûne fitnetüv veyekûne-ddînü küllühû lillâh. feini-ntehev feinne-llâhe bimâ ya\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (1200, 'vein tevellev fa\\`lemû enne-llâhe mevlâküm. ni\\`me-lmevlâ veni\\`me-nneṣîr.');
INSERT INTO `tr_transliteration` VALUES (1201, 'va\\`lemû ennemâ ganimtüm min şey\'in feenne lillâhi ḫumüsehû velirrasûli veliẕi-lḳurbâ velyetâmâ velmesâkîni vebni-ssebîli in küntüm âmentüm billâhi vemâ enzelnâ \\`alâ \\`abdinâ yevme-lfürḳâni yevme-lteḳe-lcem\\`ân. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (1202, 'iẕ entüm bil\\`udveti-ddünyâ vehüm bil\\`udveti-lḳuṣvâ verrakbü esfele minküm. velev tevâ\\`attüm laḫteleftüm fi-lmî\\`âdi velâkil liyaḳdiye-llâhü emran kâne mef\\`ûlel liyehlike men heleke \\`am beyyinetiv veyaḥyâ men ḥayye \\`am beyyineh. veinne-llâhe lesemî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1203, 'iẕ yürîkehümü-llâhü fî menâmike ḳalîlâ. velev erâkehüm keŝîral lefeşiltüm veletenâza\\`tüm fi-l\'emri velâkinne-llâhe sellem. innehû \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (1204, 'veiẕ yürîkümûhüm iẕi-tteḳaytüm fî a\\`yüniküm ḳalîlev veyüḳallilüküm fî a\\`yünihim liyaḳdiye-llâhü emran kâne mef\\`ûlâ. veile-llâhi türce\\`u-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (1205, 'yâ eyyühe-lleẕîne âmenû iẕâ leḳîtüm fieten feŝbütû veẕkürü-llâhe keŝîral le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (1206, 'veeṭî\\`ü-llâhe verasûlehû velâ tenâza\\`û fetefşelû veteẕhebe rîḥuküm vaṣbirû. inne-llâhe me\\`a-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (1207, 'velâ tekûnû kelleẕîne ḫaracû min diyârihim beṭarav veriâe-nnâsi veyeṣuddûne \\`an sebîli-llâh. vellâhü bimâ ya\\`melûne müḥîṭ.');
INSERT INTO `tr_transliteration` VALUES (1208, 'veiẕ zeyyene lehümü-şşeyṭânü a\\`mâlehüm veḳâle lâ gâlibe lekümü-lyevme mine-nnâsi veinnî cârul leküm. felemmâ terâeti-lfietâni nekeṣa \\`alâ \\`aḳibeyhi veḳâle innî berîüm minküm innî erâ mâ lâ teravne innî eḫâfü-llâh. vellâhü şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (1209, 'iẕ yeḳûlü-lmünâfiḳûne velleẕîne fî ḳulûbihim meraḍun garra hâülâi dînühüm. vemey yetevekkel \\`ale-llâhi feinne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1210, 'velev terâ iẕ yeteveffe-lleẕîne keferü-lmelâiketü yaḍribûne vucûhehüm veedbârahüm. veẕûḳû \\`aẕâbe-lḥarîḳ.');
INSERT INTO `tr_transliteration` VALUES (1211, 'ẕâlike bimâ ḳaddemet eydîküm veenne-llâhe leyse biżallâmil lil\\`abîd.');
INSERT INTO `tr_transliteration` VALUES (1212, 'kede\'bi âli fir\\`avne velleẕîne min ḳablihim. keferû biâyâti-llâhi feeḫaẕehümü-llâhü biẕünûbihim. inne-llâhe ḳaviyyün şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (1213, 'ẕâlike bienne-llâhe lem yekü mügayyiran ni\\`meten en\\`amehâ \\`alâ ḳavmin ḥattâ yügayyirû mâ bienfüsihim veenne-llâhe semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1214, 'kede\'bi âli fir\\`avne velleẕîne min ḳablihim. keẕẕebû biâyâti rabbihim. feehleknâhüm biẕünûbihim veagraḳnâ âle fir\\`avn. veküllün kânû żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1215, 'inne şerra-ddevâbbi \\`inde-llâhi-lleẕîne keferû fehüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1216, 'elleẕîne \\`âhette minhüm ŝümme yenḳuḍûne \\`ahdehüm fî külli merrativ vehüm lâ yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1217, 'feimmâ teŝḳafennehüm fi-lḥarbi feşerrid bihim men ḫalfehüm le\\`allehüm yeẕẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1218, 'veimmâ teḫâfenne min ḳavmin ḫiyâneten fembiẕ ileyhim \\`alâ sevâ\'. inne-llâhe lâ yüḥibbü-lḫâinîn.');
INSERT INTO `tr_transliteration` VALUES (1219, 'velâ yaḥsebenne-lleẕîne keferû sebeḳû. innehüm lâ yü\\`cizûn.');
INSERT INTO `tr_transliteration` VALUES (1220, 'vee\\`iddû lehüm me-steṭa\\`tüm min ḳuvvetiv vemir ribâṭi-lḫayli türhibûne bihî \\`adüvve-llâhi ve\\`adüvveküm veâḫarîne min dûnihim. lâ ta\\`lemûnehüm. allâhü ya\\`lemühüm. vemâ tünfiḳû min şey\'in fî sebîli-llâhi yüveffe ileyküm veentüm lâ tużlemûn.');
INSERT INTO `tr_transliteration` VALUES (1221, 'vein ceneḥû lisselmi fecnaḥ lehâ vetevekkel \\`ale-llâh. innehû hüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1222, 'veiy yürîdû ey yaḫde\\`ûke feinne ḥasbeke-llâh. hüve-lleẕî eyyedeke binaṣrihî vebilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1223, 'veellefe beyne ḳulûbihim. lev enfaḳte mâ fi-l\'arḍi cemî\\`am mâ ellefte beyne ḳulûbihim velâkinne-llâhe ellefe beynehüm. innehû \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1224, 'yâ eyyühe-nnebiyyü ḥasbüke-llâhü vemeni-ttebe\\`ake mine-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1225, 'yâ eyyühe-nnebiyyü ḥarriḍi-lmü\'minîne \\`ale-lḳitâl. iy yeküm minküm \\`işrûne ṣâbirûne yaglibû mieteyn. veiy yeküm minküm mietüy yaglibû elfem mine-lleẕîne keferû biennehüm ḳavmül lâ yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (1226, 'el\'âne ḫaffefe-llâhü \\`anküm ve\\`alime enne fîküm ḍa\\`fâ. feiy yeküm minküm mietün ṣâbiratüy yaglibû mieteyn. veiy yeküm minküm elfüy yaglibû elfeyni biiẕni-llâh. vellâhü me\\`a-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (1227, 'mâ kâne linebiyyin ey yekûne lehû esrâ ḥattâ yüŝḫine fi-l\'arḍ. türîdûne \\`araḍa-ddünyâ. vellâhü yürîdü-l\'âḫirah. vellâhü \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1228, 'levlâ kitâbüm mine-llâhi sebeḳa lemesseküm fîmâ eḫaẕtüm \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1229, 'fekülû mimmâ ganimtüm ḥalâlen ṭayyibâ. vetteḳu-llâh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1230, 'yâ eyyühe-nnebiyyü ḳul limen fî eydîküm mine-l\'esrâ iy ya\\`lemi-llâhü fî ḳulûbiküm ḫayray yü\'tiküm ḫayram mimmâ üḫiẕe minküm veyagfir leküm. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1231, 'veiy yürîdû ḫiyâneteke feḳad ḫânü-llâhe min ḳablü feemkene minhüm. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1232, 'inne-lleẕîne âmenû vehâcerû vecâhedû biemvâlihim veenfüsihim fî sebîli-llâhi velleẕîne evev veneṣarû ülâike ba\\`ḍuhüm evliyâü ba\\`ḍ. velleẕîne âmenû velem yühâcirû mâ leküm miv velâyetihim min şey\'in ḥattâ yühâcirû. veini-stenṣarûküm fi-ddîni fe\\`aleykümü-nnaṣru illâ \\`alâ ḳavmim beyneküm vebeynehüm mîŝâḳun. vellâhü bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (1233, 'velleẕîne keferû ba\\`ḍuhüm evliyâü ba\\`ḍ. illâ tef\\`alûhü tekün fitnetün fi-l\'arḍi vefesâdün kebîr.');
INSERT INTO `tr_transliteration` VALUES (1234, 'velleẕîne âmenû vehâcerû vecâhedû fî sebîli-llâhi velleẕîne evev veneṣarû ülâike hümü-lmü\'minûne ḥaḳḳâ. lehüm magfiratüv verizḳun kerîm.');
INSERT INTO `tr_transliteration` VALUES (1235, 'velleẕîne âmenû mim ba\\`dü vehâcerû vecâhedû me\\`aküm feülâike minküm. veülü-l\'erḥâmi ba\\`ḍuhüm evlâ biba\\`ḍin fî kitâbi-llâh. inne-llâhe bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1236, 'berâetüm mine-llâhi verasûlihî ile-lleẕîne \\`âhettüm mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (1237, 'fesîḥû fi-l\'arḍi erbe\\`ate eşhüriv va\\`lemû enneküm gayru mü\\`cizi-llâhi veenne-llâhe muḫzi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1238, 'veeẕânüm mine-llâhi verasûlihî ile-nnâsi yevme-lḥacci-l\'ekberi enne-llâhe berîüm mine-lmüşrikîne verasûlüh. fein tübtüm fehüve ḫayrul leküm. vein tevelleytüm fa\\`lemû enneküm gayru mü\\`cizi-llâh. vebeşşiri-lleẕîne keferû bi\\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (1239, 'ille-lleẕîne \\`âhettüm mine-lmüşrikîne ŝümme lem yenḳuṣûküm şey\'ev velem yüżahirû \\`aleyküm eḥaden feetimmû ileyhim \\`ahdehüm ilâ müddetihim. inne-llâhe yüḥibbü-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1240, 'feiẕe-nseleḫa-l\'eşhüru-lḥurumü faḳtülü-lmüşrikîne ḥayŝü vecettümûhüm veḫuẕûhüm vaḥṣurûhüm vaḳ\\`udû lehüm külle merṣad. fein tâbû veeḳâmu-ṣṣalâte veâtevu-zzekâte feḫallû sebîlehüm. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1241, 'vein eḥadüm mine-lmüşrikîne-stecârake feecirhü ḥattâ yesme\\`a kelâme-llâhi ŝümme eblighü me\'meneh. ẕâlike biennehüm ḳavmül lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1242, 'keyfe yekûnü lilmüşrikîne \\`ahdün \\`inde-llâhi ve\\`inde rasûlihî ille-lleẕîne \\`âhettüm \\`inde-lmescidi-lḥarâm. feme-steḳâmû leküm festeḳîmû lehüm. inne-llâhe yüḥibbü-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1243, 'keyfe veiy yażherû \\`aleyküm lâ yerḳubû fîküm illev velâ ẕimmeh. yürḍûneküm biefvâhihim vete\'bâ ḳulûbühüm. veekŝeruhüm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (1244, 'işterav biâyâti-llâhi ŝemenen ḳalîlen feṣaddû \\`an sebîlih. innehüm sâe mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1245, 'lâ yerḳubûne fî mü\'minin illev velâ ẕimmeh. veülâike hümü-lmü\\`tedûn.');
INSERT INTO `tr_transliteration` VALUES (1246, 'fein tâbû veeḳâmu-ṣṣalâte veâtevu-zzekâte feiḫvânüküm fi-ddîn. venüfeṣṣilü-l\'âyâti liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1247, 'vein nekeŝû eymânehüm mim ba\\`di \\`ahdihim veṭa\\`anû fî dîniküm feḳâtilû eimmete-lküfri innehüm lâ eymâne lehüm le\\`allehüm yentehûn.');
INSERT INTO `tr_transliteration` VALUES (1248, 'elâ tüḳâtilûne ḳavmen nekeŝû eymânehüm vehemmû biiḫrâci-rrasûli vehüm bedeûküm evvele merrah. etaḫşevnehüm. fellâhü eḥaḳḳu en taḫşevhü in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1249, 'ḳâtilûhüm yü\\`aẕẕibhümü-llâhü bieydîküm veyuḫzihim veyenṣurküm \\`aleyhim veyeşfi ṣudûra ḳavmim mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1250, 'veyüẕhib gayża ḳulûbihim. veyetûbü-llâhü \\`alâ mey yeşâ\'. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1251, 'em ḥasibtüm en tütrakû velemmâ ya\\`lemi-llâhü-lleẕîne câhedû minküm velem yetteḫiẕû min dûni-llâhi velâ rasûlihî vele-lmü\'minîne velîceh. vellâhü ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1252, 'mâ kâne lilmüşrikîne ey ya\\`mürû mesâcide-llâhi şâhidîne \\`alâ enfüsihim bilküfr. ülâike ḥabiṭat a\\`mâlühüm. vefi-nnâri hüm ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (1253, 'innemâ ya\\`müru mesâcide-llâhi men âmene billâhi velyevmi-l\'âḫiri veeḳâme-ṣṣalâte veâte-zzekâte velem yaḫşe ille-llâhe fe\\`asâ ülâike ey yekûnû mine-lmühtedîn.');
INSERT INTO `tr_transliteration` VALUES (1254, 'ece\\`altüm siḳâyete-lḥâcci ve\\`imârate-lmescidi-lḥarâmi kemen âmene billâhi velyevmi-l\'âḫiri vecâhede fî sebîli-llâh. lâ yestevûne \\`inde-llâh. vellâhü lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1255, 'elleẕîne âmenû vehâcerû vecâhedû fî sebîli-llâhi biemvâlihim veenfüsihim a\\`żamü deraceten \\`inde-llâh. veülâike hümü-lfâizûn.');
INSERT INTO `tr_transliteration` VALUES (1256, 'yübeşşiruhüm rabbühüm biraḥmetim minhü veriḍvâniv vecennâtil lehüm fîhâ ne\\`îmüm müḳîm.');
INSERT INTO `tr_transliteration` VALUES (1257, 'ḫâlidîne fîhâ ebedâ. inne-llâhe \\`indehû ecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1258, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕû âbâeküm veiḫvâneküm evliyâe ini-steḥabbü-lküfra \\`ale-l\'îmân. vemey yetevellehüm minküm feülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (1259, 'ḳul in kâne âbâüküm veebnâüküm veiḫvânüküm veezvâcüküm ve\\`aşîratüküm veemvâlün-ḳteraftümûhâ veticâratün taḫşevne kesâdehâ vemesâkinü terḍavnehâ eḥabbe ileyküm mine-llâhi verasûlihî vecihâdin fî sebîlihî feterabbeṣû ḥattâ ye\'tiye-llâhü biemrih. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1260, 'leḳad neṣarakümü-llâhü fî mevâṭine keŝîrativ veyevme ḥuneynin iẕ a\\`cebetküm keŝratüküm felem tugni \\`anküm şey\'ev veḍâḳat \\`aleykümü-l\'arḍu bimâ raḥubet ŝümme velleytüm müdbirîn.');
INSERT INTO `tr_transliteration` VALUES (1261, 'ŝümme enzele-llâhü sekînetehû \\`alâ rasûlihî ve\\`ale-lmü\'minîne veenzele cünûdel lem teravhâ ve\\`aẕẕebe-lleẕîne keferû. veẕâlike cezâü-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1262, 'ŝümme yetûbü-llâhü mim ba\\`di ẕâlike \\`alâ mey yeşâ\'. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1263, 'yâ eyyühe-lleẕîne âmenû inneme-lmüşrikûne necesün felâ yaḳrabü-lmescide-lḥarâme ba\\`de \\`âmihim hâẕâ. vein ḫiftüm \\`ayleten fesevfe yugnîkümü-llâhü min faḍlihî in şâ\'. inne-llâhe \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1264, 'ḳâtilü-lleẕîne lâ yü\'minûne billâhi velâ bilyevmi-l\'âḫiri velâ yüḥarrimûne mâ ḥarrame-llâhü verasûlühû velâ yedînûne dîne-lḥaḳḳi mine-lleẕîne ûtü-lkitâbe ḥattâ yü\\`ṭü-lcizyete \\`ay yediv vehüm ṣâgirûn.');
INSERT INTO `tr_transliteration` VALUES (1265, 'veḳâleti-lyehûdü \\`uzeyrun-bnü-llâhi veḳâleti-nneṣâra-lmesîḥu-bnü-llâh. ẕâlike ḳavlühüm biefvâhihim. yüḍâhiûne ḳavle-lleẕîne keferû min ḳabl. ḳâtelehümü-llâh. ennâ yü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (1266, 'itteḫaẕû aḥbârahüm veruhbânehüm erbâbem min dûni-llâhi velmesîḥa-bne meryem. vemâ ümirû illâ liya\\`büdû ilâhev vâḥidâ. lâ ilâhe illâ hû. sübḥânehû \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (1267, 'yürîdûne ey yuṭfiû nûra-llâhi biefvâhihim veye\'be-llâhü illâ ey yütimme nûrahû velev kerihe-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1268, 'hüve-lleẕî ersele rasûlehû bilhüdâ vedîni-lḥaḳḳi liyużhirahû \\`ale-ddîni küllihî velev kerihe-lmüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (1269, 'yâ eyyühe-lleẕîne âmenû inne keŝîram mine-l\'aḥbâri verruhbâni leye\'külûne emvâle-nnâsi bilbâṭili veyeṣuddûne \\`an sebîli-llâh. velleẕîne yeknizûne-ẕẕehebe velfiḍḍate velâ yünfiḳûnehâ fî sebîli-llâhi febeşşirhüm bi\\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (1270, 'yevme yuḥmâ \\`aleyhâ fî nâri cehenneme fetükvâ bihâ cibâhühüm vecünûbühüm veżuhûruhüm. hâẕâ mâ keneztüm lienfüsiküm feẕûḳû mâ küntüm teknizûn.');
INSERT INTO `tr_transliteration` VALUES (1271, 'inne \\`iddete-şşühûri \\`inde-llâhi-ŝnâ \\`aşera şehran fî kitâbi-llâhi yevme ḫaleḳa-ssemâvâti vel\'arḍa minhâ erbe\\`atün ḥurum. ẕâlike-ddînü-lḳayyimü felâ tażlimû fîhinne enfüseküm veḳâtilü-lmüşrikîne kâffeten kemâ yüḳâtilûneküm kâffeh. va\\`lemû enne-llâhe me\\`a-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1272, 'inneme-nnesîü ziyâdetün fi-lküfri yüḍallü bihi-lleẕîne keferû yüḥillûnehû \\`âmev veyüḥarrimûnehû \\`âmel liyüvâṭiû \\`iddete mâ ḥarrame-llâhü feyüḥillû mâ ḥarrame-llâh. züyyine lehüm sûü a\\`mâlihim. vellâhü lâ yehdi-lḳavme-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1273, 'yâ eyyühe-lleẕîne âmenû mâ leküm iẕâ ḳîle lekümü-nfirû fî sebîli-llâhi-ŝŝâḳaltüm ile-l\'arḍ. eraḍîtüm bilḥayâti-ddünyâ mine-l\'âḫirah. femâ metâ\\`u-lḥayâti-ddünyâ fi-l\'âḫirati illâ ḳalîl.');
INSERT INTO `tr_transliteration` VALUES (1274, 'illâ tenfirû yü\\`aẕẕibküm \\`aẕâben elîmev veyestebdil ḳavmen gayraküm velâ teḍurrûhü şey\'â. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (1275, 'illâ tenṣurûhü feḳad neṣarahü-llâhü iẕ aḫracehü-lleẕîne keferû ŝâniye-ŝneyni iẕ hümâ fi-lgâri iẕ yeḳûlü liṣâḥibihî lâ taḥzen inne-llâhe me\\`anâ. feenzele-llâhü sekînetehû \\`aleyhi veeyyedehû bicünûdil lem teravhâ vece\\`ale kelimete-lleẕîne keferü-ssüflâ. vekelimetü-llâhi hiye-l\\`ulyâ. vellâhü \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1276, 'infirû ḫifâfev veŝiḳâlev vecâhidû biemvâliküm veenfüsiküm fî sebîli-llâh. ẕâliküm ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1277, 'lev kâne \\`araḍan ḳarîbev veseferan ḳâṣidel lettebe\\`ûke velâkim be\\`udet \\`aleyhimü-şşüḳḳah. veseyaḥlifûne billâhi levi-steṭa\\`nâ leḫaracnâ me\\`aküm. yühlikûne enfüsehüm. vellâhü ya\\`lemü innehüm lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (1278, '\\`afe-llâhü \\`ank. lime eẕinte lehüm ḥattâ yetebeyyene leke-lleẕîne ṣadeḳû veta\\`leme-lkâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (1279, 'lâ yeste\'ẕinüke-lleẕîne yü\'minûne billâhi velyevmi-l\'âḫiri ey yücâhidû biemvâlihim veenfüsihim. vellâhü \\`alîmüm bilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1280, 'innemâ yeste\'ẕinüke-lleẕîne lâ yü\'minûne billâhi velyevmi-l\'âḫiri vertâbet ḳulûbühüm fehüm fî raybihim yeteraddedûn.');
INSERT INTO `tr_transliteration` VALUES (1281, 'velev erâdü-lḫurûce lee\\`addû lehû \\`uddetev velâkin kerihe-llâhü-mbi\\`âŝehüm feŝebbeṭahüm veḳîle-ḳ\\`udû me\\`a-lḳâ\\`idîn.');
INSERT INTO `tr_transliteration` VALUES (1282, 'lev ḫaracû fîküm mâ zâdûküm illâ ḫabâlev veleevḍa\\`û ḫilâleküm yebgûnekümü-lfitneh. vefîküm semmâ\\`ûne lehüm. vellâhü \\`alîmüm biżżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1283, 'leḳadi-btegavu-lfitnete min ḳablü veḳallebû leke-l\'ümûra ḥattâ câe-lḥaḳḳu veżahera emru-llâhi vehüm kârihûn.');
INSERT INTO `tr_transliteration` VALUES (1284, 'veminhüm mey yeḳûlü-\'ẕel lî velâ teftinnî. elâ fi-lfitneti seḳaṭû. veinne cehenneme lemüḥîṭatüm bilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1285, 'in tüṣibke ḥasenetün tesü\'hüm. vein tüṣibke müṣîbetüy yeḳûlû ḳad eḫaẕnâ emranâ min ḳablü veyetevellev vehüm feriḥûn.');
INSERT INTO `tr_transliteration` VALUES (1286, 'ḳul ley yüṣîbenâ illâ mâ ketebe-llâhü lenâ. hüve mevlânâ. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1287, 'ḳul hel terabbeṣûne binâ illâ iḥde-lḥusneyeyn. venaḥnü neterabbeṣu biküm ey yüṣîbekümü-llâhü bi\\`aẕâbim min \\`indihî ev bieydînâ. feterabbeṣû innâ me\\`aküm müterabbiṣûn.');
INSERT INTO `tr_transliteration` VALUES (1288, 'ḳul enfiḳû ṭav\\`an ev kerhel ley yüteḳabbele minküm. inneküm küntüm ḳavmen fâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1289, 'vemâ mene\\`ahüm en tuḳbele minhüm nefeḳâtühüm illâ ennehüm keferû billâhi vebirasûlihî velâ ye\'tûne-ṣṣalâte illâ vehüm küsâlâ velâ yünfiḳûne illâ vehüm kârihûn.');
INSERT INTO `tr_transliteration` VALUES (1290, 'felâ tü\\`cibke emvâlühüm velâ evlâdühüm. innemâ yürîdü-llâhü liyü\\`aẕẕibehüm bihâ fi-lḥayâti-ddünyâ vetezheḳa enfüsühüm vehüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1291, 'veyaḥlifûne billâhi innehüm leminküm. vemâ hüm minküm velâkinnehüm ḳavmüy yefraḳûn.');
INSERT INTO `tr_transliteration` VALUES (1292, 'lev yecidûne melceen ev megârâtin ev müddeḫalel levellev ileyhi vehüm yecmeḥûn.');
INSERT INTO `tr_transliteration` VALUES (1293, 'veminhüm mey yelmizüke fi-ṣṣadeḳât. fein ü\\`ṭû minhâ raḍû veil lem yü\\`ṭav minhâ iẕâ hüm yesḫaṭûn.');
INSERT INTO `tr_transliteration` VALUES (1294, 'velev ennehüm raḍû mâ âtâhümü-llâhü verasûlühû veḳâlû ḥasbüne-llâhü seyü\'tîne-llâhü min faḍlihî verasûlühû innâ ile-llâhi râgibûn.');
INSERT INTO `tr_transliteration` VALUES (1295, 'inneme-ṣṣadeḳâtü lilfüḳarâi velmesâkîni vel\\`âmilîne \\`aleyhâ velmüellefeti ḳulûbühüm vefi-rriḳâbi velgârimîne vefî sebîli-llâhi vebni-ssebîl. ferîḍatem mine-llâh. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1296, 'veminhümü-lleẕîne yü\'ẕûne-nnebiyye veyeḳûlûne hüve üẕün. ḳul üẕünü ḫayril leküm yü\'minü billâhi veyü\'minü lilmü\'minîne veraḥmetül lilleẕîne âmenû minküm. velleẕîne yü\'ẕûne rasûle-llâhi lehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1297, 'yaḥlifûne billâhi leküm liyürḍûküm. vellâhü verasûlühû eḥaḳḳu ey yürḍûhü in kânû mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1298, 'elem ya\\`lemû ennehû mey yüḥâdidi-llâhe verasûlehû feenne lehû nâra cehenneme ḫâliden fîhâ. ẕâlike-lḫizyü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1299, 'yaḥẕeru-lmünâfiḳûne en tünezzele \\`aleyhim sûratün tünebbiühüm bimâ fî ḳulûbihim. ḳuli-stehziû. inne-llâhe muḫricüm mâ taḥẕerûn.');
INSERT INTO `tr_transliteration` VALUES (1300, 'velein seeltehüm leyeḳûlünne innemâ künnâ neḫûḍu venel\\`ab. ḳul ebillâhi veâyâtihî verasûlihî küntüm testehziûn.');
INSERT INTO `tr_transliteration` VALUES (1301, 'lâ ta\\`teẕirû ḳad kefertüm ba\\`de îmâniküm. in na\\`fü \\`an ṭâifetim minküm nü\\`aẕẕib ṭâifetem biennehüm kânû mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1302, 'elmünâfiḳûne velmünâfiḳâtü ba\\`ḍuhüm mim ba\\`ḍ. ye\'mürûne bilmünkeri veyenhevne \\`ani-lma\\`rûfi veyaḳbiḍûne eydiyehüm. nesü-llâhe fenesiyehüm. inne-lmünâfiḳîne hümü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (1303, 've\\`ade-llâhü-lmünâfiḳîne velmünâfiḳâti velküffâra nâra cehenneme ḫâlidîne fîhâ. hiye ḥasbühüm. vele\\`anehümü-llâh. velehüm \\`aẕâbüm müḳîm.');
INSERT INTO `tr_transliteration` VALUES (1304, 'kelleẕîne min ḳabliküm kânû eşedde minküm ḳuvvetev veekŝera emvâlev veevlâdâ. festemte\\`û biḫalâḳihim festemta\\`tüm biḫalâḳiküm keme-stemte\\`a-lleẕîne min ḳabliküm biḫalâḳihim veḫuḍtüm kelleẕî ḫâḍû. ülâike ḥabiṭat a\\`mâlühüm fi-ddünyâ vel\'âḫirah. veülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (1305, 'elem ye\'tihim nebeü-lleẕîne min ḳablihim ḳavmi nûḥiv ve\\`âdiv veŝemûde veḳavmi ibrâhîme veaṣḥâbi medyene velmü\'tefikât. etethüm rusülühüm bilbeyyinât. femâ kâne-llâhü liyażlimehüm velâkin kânû enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (1306, 'velmü\'minûne velmü\'minâtü ba\\`ḍuhüm evliyâü ba\\`ḍ. ye\'mürûne bilma\\`rûfi veyenhevne \\`ani-lmünkeri veyüḳîmûne-ṣṣalâte veyü\'tûne-zzekâte veyüṭî\\`ûne-llâhe verasûleh. ülâike seyerḥamühümü-llâh. inne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1307, 've\\`ade-llâhü-lmü\'minîne velmü\'minâti cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ vemesâkine ṭayyibeten fî cennâti \\`adn. veriḍvânüm mine-llâhi ekber. ẕâlike hüve-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1308, 'yâ eyyühe-nnebiyyü câhidi-lküffâra velmünâfiḳîne vagluż \\`aleyhim. veme\'vâhüm cehennem. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (1309, 'yaḥlifûne billâhi mâ ḳâlû. veleḳad ḳâlû kelimete-lküfri vekeferû ba\\`de islâmihim vehemmû bimâ lem yenâlû. vemâ neḳamû illâ en agnâhümü-llâhü verasûlühû min faḍlih. feiy yetûbû yekü ḫayral lehüm. veiy yetevellev yü\\`aẕẕibhümü-llâhü \\`aẕâben elîmen fi-ddünyâ vel\'âḫirah. vemâ lehüm fi-l\'arḍi miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (1310, 'veminhüm men \\`âhede-llâhe lein âtânâ min faḍlihî leneṣṣaddeḳanne velenekûnenne mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (1311, 'felemmâ âtâhüm min faḍlihî beḫilû bihî vetevellev vehüm mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (1312, 'fea\\`ḳabehüm nifâḳan fî ḳulûbihim ilâ yevmi yelḳavnehû bimâ aḫlefü-llâhe mâ ve\\`adûhü vebimâ kânû yekẕibûn.');
INSERT INTO `tr_transliteration` VALUES (1313, 'elem ya\\`lemû enne-llâhe ya\\`lemü sirrahüm venecvâhüm veenne-llâhe \\`allâmü-lguyûb.');
INSERT INTO `tr_transliteration` VALUES (1314, 'elleẕîne yelmizûne-lmüṭṭavvi\\`îne mine-lmü\'minîne fi-ṣṣadeḳâti velleẕîne lâ yecidûne illâ cühdehüm feyesḫarûne minhüm. seḫira-llâhü minhüm. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1315, 'istagfir lehüm ev lâ testagfir lehüm. in testagfir lehüm seb\\`îne merraten feley yagfira-llâhü lehüm. ẕâlike biennehüm keferû billâhi verasûlih. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1316, 'feriḥa-lmüḫallefûne bimaḳ\\`adihim ḫilâfe rasûli-llâhi vekerihû ey yücâhidû biemvâlihim veenfüsihim fî sebîli-llâhi veḳâlû lâ tenfirû fi-lḥarr. ḳul nâru cehenneme eşeddü ḥarrâ. lev kânû yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (1317, 'felyaḍḥakû ḳalîlev velyebkû keŝîrâ. cezâem bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (1318, 'feir race\\`ake-llâhü ilâ ṭâifetim minhüm feste\'ẕenûke lilḫurûci feḳul len taḫrucû me\\`iye ebedev velen tüḳâtilû me\\`iye \\`adüvvâ. inneküm raḍîtüm bilḳu\\`ûdi evvele merratin faḳ\\`udû me\\`a-lḫâlifîn.');
INSERT INTO `tr_transliteration` VALUES (1319, 'velâ tüṣalli \\`alâ eḥadim minhüm mâte ebedev velâ teḳum \\`alâ ḳabrih. innehüm keferû billâhi verasûlihî vemâtû vehüm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (1320, 'velâ tü\\`cibke emvâlühüm veevlâdühüm. innemâ yürîdü-llâhü ey yü\\`aẕẕibehüm bihâ fi-ddünyâ vetezheḳa enfüsühüm vehüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1321, 'veiẕâ ünzilet sûratün en âminû billâhi vecâhidû me\\`a rasûlihi-ste\'ẕeneke ülu-ṭṭavli minhüm veḳâlû ẕernâ neküm me\\`a-lḳâ\\`idîn.');
INSERT INTO `tr_transliteration` VALUES (1322, 'raḍû biey yekûnû me\\`a-lḫavâlifi veṭubi\\`a \\`alâ ḳulûbihim fehüm lâ yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (1323, 'lâkini-rrasûlü velleẕîne âmenû me\\`ahû câhedû biemvâlihim veenfüsihim. veülâike lehümü-lḫayrât. veülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (1324, 'e\\`adde-llâhü lehüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. ẕâlike-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1325, 'vecâe-lmü\\`aẕẕirûne mine-l\'a\\`râbi liyü\'ẕene lehüm veḳa\\`ade-lleẕîne keẕebü-llâhe verasûleh. seyüṣîbü-lleẕîne keferû minhüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1326, 'leyse \\`ale-ḍḍu\\`afâi velâ \\`ale-lmerḍâ velâ \\`ale-lleẕîne lâ yecidûne mâ yünfiḳûne ḥaracün iẕâ neṣaḥû lillâhi verasûlih. mâ \\`ale-lmuḥsinîne min sebîlin. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1327, 'velâ \\`ale-lleẕîne iẕâ mâ etevke litaḥmilehüm ḳulte lâ ecidü mâ aḥmilüküm \\`aleyh. tevellev vea\\`yünühüm tefîḍu mine-ddem\\`i ḥazenen ellâ yecidû mâ yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (1328, 'inneme-ssebîlü \\`ale-lleẕîne yeste\'ẕinûneke vehüm agniyâ\'. raḍû biey yekûnû me\\`a-lḫavâlifi veṭabe\\`a-llâhü \\`alâ ḳulûbihim fehüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1329, 'ya\\`teẕirûne ileyküm iẕâ raca\\`tüm ileyhim. ḳul lâ ta\\`teẕirû len nü\'mine leküm ḳad nebbeene-llâhü min aḫbâriküm. veseyere-llâhü \\`ameleküm verasûlühû ŝümme türaddûne ilâ \\`âlimi-lgaybi veşşehâdeti feyünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1330, 'seyaḥlifûne billâhi leküm iẕe-nḳalebtüm ileyhim litü\\`riḍû \\`anhüm. fea\\`riḍû \\`anhüm. innehüm rics. veme\'vâhüm cehennem. cezâem bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (1331, 'yaḥlifûne leküm literḍav \\`anhüm. fein terḍav \\`anhüm feinne-llâhe lâ yerḍâ \\`ani-lḳavmi-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1332, 'el\'a\\`râbü eşeddü küfrav venifâḳav veecderu ellâ ya\\`lemû ḥudûde mâ enzele-llâhü \\`alâ rasûlih. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1333, 'vemine-l\'a\\`râbi mey yetteḫiẕü mâ yünfiḳu magramev veyeterabbeṣu bikümü-ddevâir. \\`aleyhim dâiratü-ssev\'. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1334, 'vemine-l\'a\\`râbi mey yü\'minü billâhi velyevmi-l\'âḫiri veyetteḫiẕü mâ yünfiḳu ḳurubâtin \\`inde-llâhi veṣalevâti-rrasûl. elâ innehâ ḳurbetül lehüm. seyüdḫilühümü-llâhü fî raḥmetih. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1335, 'vessâbiḳûne-l\'evvelûne mine-lmühâcirîne vel\'enṣâri velleẕîne-ttebe\\`ûhüm biiḥsânir raḍiye-llâhü \\`anhüm veraḍû \\`anhü vee\\`adde lehüm cennâtin tecrî taḥtehe-l\'enhâru ḫâlidîne fîhâ ebedâ. ẕâlike-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1336, 'vemimmen ḥavleküm mine-l\'a\\`râbi münâfiḳûn. vemin ehli-lmedîneti meradû \\`ale-nnifâḳi lâ ta\\`lemühüm. naḥnü na\\`lemühüm. senü\\`aẕẕibühüm merrateyni ŝümme yüraddûne ilâ \\`aẕâbin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1337, 'veâḫarûne-\\`terafû biẕünûbihim ḫaleṭû \\`amelen ṣâliḥav veâḫara seyyiâ. \\`asa-llâhü ey yetûbe \\`aleyhim. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1338, 'ḫuẕ min emvâlihim ṣadeḳaten tüṭahhiruhüm vetüzekkîhim bihâ veṣalli \\`aleyhim. inne ṣalâteke sekenül lehüm. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1339, 'elem ya\\`lemû enne-llâhe hüve yaḳbelü-ttevbete \\`an \\`ibâdihî veye\'ḫuẕu-ṣṣadeḳâti veenne-llâhe hüve-ttevvâbü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (1340, 'veḳuli-\\`melû feseyere-llâhü \\`ameleküm verasûlühû velmü\'minûn. vesetüraddûne ilâ \\`âlimi-lgaybi veşşehâdeti feyünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1341, 'veâḫarûne mürcevne liemri-llâhi immâ yü\\`aẕẕibühüm veimmâ yetûbü \\`aleyhim. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1342, 'velleẕîne-tteḫaẕû mesciden ḍirârav veküfrav vetefrîḳam beyne-lmü\'minîne veirṣâdel limen ḥârabe-llâhe verasûlehû min ḳabl. veleyaḥlifünne in eradnâ ille-lḥusnâ. vellâhü yeşhedü innehüm lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (1343, 'lâ teḳum fîhi ebedâ. lemescidün üssise \\`ale-ttaḳvâ min evveli yevmin eḥaḳḳu en teḳûme fîh. fîhi ricâlüy yüḥibbûne ey yeteṭahherû. vellâhü yüḥibbü-lmüṭṭahhirîn.');
INSERT INTO `tr_transliteration` VALUES (1344, 'efemen essese bünyânehû \\`alâ taḳvâ mine-llâhi veriḍvânin ḫayrun em men essese bünyânehû \\`alâ şefâ cürufin hârin fenhâra bihî fî nâri cehennem. vellâhü lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1345, 'lâ yezâlü bünyânühümü-lleẕî benev rîbeten fî ḳulûbihim illâ en teḳaṭṭa\\`a ḳulûbühüm. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1346, 'inne-llâhe-şterâ mine-lmü\'minîne enfüsehüm veemvâlehüm bienne lehümü-lcenneh. yüḳâtilûne fî sebîli-llâhi feyaḳtülûne veyuḳtelûne va\\`den \\`aleyhi ḥaḳḳan fi-ttevrâti vel\'incîli velḳur\'ân. vemen evfâ bi\\`ahdihî mine-llâhi festebşirû bibey\\`ikümü-lleẕî bâya\\`tüm bih. veẕâlike hüve-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1347, 'ettâibûne-l\\`âbidûne-lḥâmidûne-ssâiḥûne-rrâki\\`ûne-ssâcidûne-l\'âmirûne bilma\\`rûfi vennâhûne \\`ani-lmünkeri velḥâfiżûne liḥudûdi-llâh. vebeşşiri-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1348, 'mâ kâne linnebiyyi velleẕîne âmenû ey yestagfirû lilmüşrikîne velev kânû ülî ḳurbâ mim ba\\`di mâ tebeyyene lehüm ennehüm aṣḥâbü-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (1349, 'vemâ kâne-stigfâru ibrâhîme liebîhi illâ \\`am mev\\`idetiv ve\\`adehâ iyyâh. felemmâ tebeyyene lehû ennehû \\`adüvvül lillâhi teberrae minh. inne ibrâhîme leevvâhün ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (1350, 'vemâ kâne-llâhü liyüḍille ḳavmem ba\\`de iẕ hedâhüm ḥattâ yübeyyine lehüm mâ yetteḳûn. inne-llâhe bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1351, 'inne-llâhe lehû mülkü-ssemâvâti vel\'arḍ. yuḥyî veyümît. vemâ leküm min dûni-llâhi miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (1352, 'leḳat tâbe-llâhü \\`ale-nnebiyyi velmühâcirîne vel\'enṣâri-lleẕîne-ttebe\\`ûhü fî sâ\\`ati-l\\`usrati mim ba\\`di mâ kâde yezîgu ḳulûbü ferîḳim minhüm ŝümme tâbe \\`aleyhim. innehû bihim raûfür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1353, 've\\`ale-ŝŝelâŝeti-lleẕîne ḫullifû. ḥattâ iẕâ ḍâḳat \\`aleyhimü-l\'arḍu bimâ raḥubet veḍâḳat \\`aleyhim enfüsühüm veżannû el lâ melcee mine-llâhi illâ ileyh. ŝümme tâbe \\`aleyhim liyetûbû. inne-llâhe hüve-ttevvâbü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (1354, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe vekûnû me\\`a-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1355, 'mâ kâne liehli-lmedîneti vemen ḥavlehüm mine-l\'a\\`râbi ey yeteḫallefû \\`ar rasûli-llâhi velâ yergabû bienfüsihim \\`an nefsih. ẕâlike biennehüm lâ yüṣîbühüm żameüv velâ neṣabüv velâ maḫmeṣatün fî sebîli-llâhi velâ yeṭaûne mevṭiey yegîżu-lküffâra velâ yenâlûne min \\`adüvvin neylen illâ kütibe lehüm bihî \\`amelün ṣâliḥ. inne-llâhe lâ yüḍî\\`u ecra-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1356, 'velâ yünfiḳûne nefeḳaten ṣagîratev velâ kebîratev velâ yaḳṭa\\`ûne vâdiyen illâ kütibe lehüm liyecziyehümü-llâhü aḥsene mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1357, 'vemâ kâne-lmü\'minûne liyenfirû kâffeh. felevlâ nefera min külli firḳatim minhüm ṭâifetül liyetefeḳḳahû fi-ddîni veliyünẕirû ḳavmehüm iẕâ race\\`û ileyhim le\\`allehüm yaḥẕerûn.');
INSERT INTO `tr_transliteration` VALUES (1358, 'yâ eyyühe-lleẕîne âmenû ḳâtilü-lleẕîne yelûneküm mine-lküffâri velyecidû fîküm gilżah. va\\`lemû enne-llâhe me\\`a-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1359, 'veiẕâ mâ ünzilet sûratün feminhüm mey yeḳûlü eyyüküm zâdethü hâẕihî îmânâ. feemme-lleẕîne âmenû fezâdethüm îmânev vehüm yestebşirûn.');
INSERT INTO `tr_transliteration` VALUES (1360, 'veemme-lleẕîne fi ḳulûbihim meraḍun fezâdethüm ricsen ilâ ricsihim vemâtû vehüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1361, 'evelâ yeravne ennehüm yüftenûne fî külli \\`âmim merraten ev merrateyni ŝümme lâ yetûbûne velâ hüm yeẕẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1362, 'veiẕâ mâ ünzilet sûratün neżara ba\\`ḍuhüm ilâ ba\\`ḍ. hel yerâküm min eḥadin ŝümme-nṣarafû. ṣarafe-llâhü ḳulûbehüm biennehüm ḳavmül lâ yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (1363, 'leḳad câeküm rasûlüm min enfüsiküm \\`azîz. \\`aleyhi mâ \\`anittüm ḥarîṣun \\`aleyküm bilmü\'minîne raûfür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1364, 'fein tevellev feḳul ḥasbiye-llâh. lâ ilâhe illâ hû. \\`aleyhi tevekkeltü vehüve rabbü-l\\`arşi-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1365, 'elif-lâm-râ. tilke âyâtü-lkitâbi-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1366, 'ekâne linnâsi \\`aceben en evḥaynâ ilâ racülim minhüm en enẕiri-nnâse vebeşşiri-lleẕîne âmenû enne lehüm ḳademe ṣidḳin \\`inde rabbihim. ḳâle-lkâfirûne inne hâẕâ lesâḥirum mübîn.');
INSERT INTO `tr_transliteration` VALUES (1367, 'inne rabbekümü-llâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmin ŝümme-stevâ \\`ale-l\\`arşi yüdebbiru-l\'emr. mâ min şefî\\`in illâ mim ba\\`di iẕnih. ẕâlikümü-llâhü rabbüküm fa\\`büdûh. efelâ teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1368, 'ileyhi merci\\`uküm cemî\\`â. va\\`de-llâhi ḥaḳḳâ. innehû yebdeü-lḫalḳa ŝümme yü\\`îdühû liyecziye-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti bilḳisṭ. velleẕîne keferû lehüm şerâbüm min ḥamîmiv ve\\`aẕâbün elîmüm bimâ kânû yekfürûn.');
INSERT INTO `tr_transliteration` VALUES (1369, 'hüve-lleẕî ce\\`ale-şşemse ḍiyâev velḳamera nûrav veḳadderahû menâzile lita\\`lemû \\`adede-ssinîne velḥisâb. mâ ḫaleḳa-llâhü ẕâlike illâ bilḥaḳḳ. yüfeṣṣilü-l\'âyâti liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1370, 'inne fi-ḫtilâfi-lleyli vennehâri vemâ ḫaleḳa-llâhü fi-ssemâvâti vel\'arḍi leâyâtil liḳavmiy yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1371, 'inne-lleẕîne lâ yercûne liḳâenâ veraḍû bilḥayâti-ddünyâ vaṭmeennû bihâ velleẕîne hüm \\`an âyâtinâ gâfilûn.');
INSERT INTO `tr_transliteration` VALUES (1372, 'ülâike me\'vâhümü-nnâru bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (1373, 'inne-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti yehdîhim rabbühüm biîmânihim. tecrî min taḥtihimü-l\'enhâru fî cennâti-nne\\`îm.');
INSERT INTO `tr_transliteration` VALUES (1374, 'da\\`vâhüm fîhâ sübḥâneke-llâhümme veteḥiyyetühüm fîhâ selâm. veâḫiru da\\`vâhüm eni-lḥamdü lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1375, 'velev yü\\`accilü-llâhü linnâsi-şşerra-sti\\`câlehüm bilḫayri leḳuḍiye ileyhim ecelühüm. feneẕeru-lleẕîne lâ yercûne liḳâenâ fî ṭugyânihim ya\\`mehûn.');
INSERT INTO `tr_transliteration` VALUES (1376, 'veiẕâ messe-l\'insâne-ḍḍurru de\\`ânâ licembihî ev ḳâ\\`iden ev ḳâimen. felemmâ keşefnâ \\`anhü ḍurrahû merra keel lem yed\\`unâ ilâ ḍurrim messeh. keẕâlike züyyine lilmüsrifîne mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1377, 'veleḳad ehlekne-lḳurûne min ḳabliküm lemmâ żalemû vecâethüm rusülühüm bilbeyyinâti vemâ kânû liyü\'minû. keẕâlike neczi-lḳavme-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1378, 'ŝümme ce\\`alnâküm ḫalâife fi-l\'arḍi mim ba\\`dihim linenżura keyfe ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1379, 'veiẕâ tütlâ \\`aleyhim âyâtünâ beyyinâtin ḳâle-lleẕîne lâ yercûne liḳâene-\'ti biḳur\'ânin gayri hâẕâ ev beddilh. ḳul mâ yekûnü lî en übeddilehû min tilḳâi nefsî. in ettebi\\`u illâ mâ yûḥâ ileyy. innî eḫâfü in \\`aṣaytü rabbî \\`aẕâbe yevmin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1380, 'ḳul lev şâe-llâhü mâ televtühû \\`aleyküm velâ edrâküm bih. feḳad lebiŝtü fîküm \\`umüram min ḳablih. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1381, 'femen ażlemü mimmeni-fterâ \\`ale-llâhi keẕiben ev keẕẕebe biâyâtih. innehû lâ yüfliḥu-lmücrimûn.');
INSERT INTO `tr_transliteration` VALUES (1382, 'veya\\`büdûne min dûni-llâhi mâ lâ yeḍurruhüm velâ yenfe\\`uhüm veyeḳûlûne hâülâi şüfe\\`âünâ \\`inde-llâh. ḳul etünebbiûne-llâhe bimâ lâ ya\\`lemü fi-ssemâvâti velâ fi-l\'arḍ. sübḥânehû vete\\`âlâ \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (1383, 'vemâ kâne-nnâsü illâ ümmetev vâḥideten faḫtelefû. velevlâ kelimetün sebeḳat mir rabbike leḳuḍiye beynehüm fîmâ fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (1384, 'veyeḳûlûne levlâ ünzile \\`aleyhi âyetüm mir rabbih. feḳul inneme-lgaybü lillâhi fenteżirû. innî me\\`aküm mine-lmünteżirîn.');
INSERT INTO `tr_transliteration` VALUES (1385, 'veiẕâ eẕaḳne-nnâse raḥmetem mim ba\\`di ḍarrâe messethüm iẕâ lehüm mekrun fî âyâtinâ. ḳuli-llâhü esra\\`u mekrâ. inne rusülenâ yektübûne mâ temkürûn.');
INSERT INTO `tr_transliteration` VALUES (1386, 'hüve-lleẕî yüseyyiruküm fi-lberri velbaḥr. ḥattâ iẕâ küntüm fi-lfülk. vecerayne bihim birîḥin ṭayyibetiv veferiḥû bihâ câethâ rîḥun \\`âṣifüv vecâehümü-lmevcü min külli mekâniv veżannû ennehüm üḥîṭa bihim de\\`avu-llâhe muḫliṣîne lehü-ddîn. lein enceytenâ min hâẕihî lenekûnenne mine-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (1387, 'felemmâ encâhüm iẕâ hüm yebgûne fi-l\'arḍi bigayri-lḥaḳḳ. yâ eyyühe-nnâsü innemâ bagyüküm \\`alâ enfüsiküm metâ\\`a-lḥayâti-ddünyâ ŝümme ileynâ merci\\`uküm fenünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1388, 'innemâ meŝelü-lḥayâti-ddünyâ kemâin enzelnâhü mine-ssemâi faḫteleṭa bihî nebâtü-l\'arḍi mimmâ ye\'külü-nnâsü vel\'en\\`âm. ḥattâ iẕâ eḫaẕeti-l\'arḍu zuḫrufehâ vezzeyyenet veżanne ehlühâ ennehüm ḳâdirûne \\`aleyhâ etâhâ emrunâ leylen ev nehâran fece\\`alnâhâ ḥaṣîden keel lem tagne bil\'ems. keẕâlike nüfeṣṣilü-l\'âyâti liḳavmiy yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1389, 'vellâhü yed\\`û ilâ dâri-sselâm. veyehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (1390, 'lilleẕîne aḥsenü-lḥusnâ veziyâdeh. velâ yerheḳu vucûhehüm ḳateruv velâ ẕilleh. ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (1391, 'velleẕîne kesebü-sseyyiâti cezâü seyyietim bimiŝlihâ veterheḳuhüm ẕilleh. mâ lehüm mine-llâhi min \\`âṣim. keennemâ ugşiyet vucûhühüm ḳiṭa\\`am mine-lleyli mużlimâ. ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (1392, 'veyevme naḥşüruhüm cemî\\`an ŝümme neḳûlü lilleẕîne eşrakû mekâneküm entüm veşürakâüküm. fezeyyelnâ beynehüm veḳâle şürakâühüm mâ küntüm iyyânâ ta\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (1393, 'fekefâ billâhi şehîdem beynenâ vebeyneküm in künnâ \\`an \\`ibâdetiküm legâfilîn.');
INSERT INTO `tr_transliteration` VALUES (1394, 'hünâlike teblû küllü nefsim mâ eslefet veruddû ile-llâhi mevlâhümü-lḥaḳḳi veḍalle \\`anhüm mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (1395, 'ḳul mey yerzüḳuküm mine-ssemâi vel\'arḍi emmey yemlikü-ssem\\`a vel\'ebṣâra vemey yuḫricü-lḥayye mine-lmeyyiti veyuḫricü-lmeyyite mine-lḥayyi vemey yüdebbiru-l\'emr. feseyeḳûlûne-llâh. feḳul efelâ tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1396, 'feẕâlikümü-llâhü rabbükümü-lḥaḳḳ. femâẕâ ba\\`de-lḥaḳḳi ille-ḍḍalâl. feennâ tuṣrafûn.');
INSERT INTO `tr_transliteration` VALUES (1397, 'keẕâlike ḥaḳḳat kelimetü rabbike \\`ale-lleẕîne feseḳû ennehüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1398, 'ḳul hel min şürakâiküm mey yebdeü-lḫalḳa ŝümme yü\\`îdüh. ḳuli-llâhü yebdeü-lḫalḳa ŝümme yü\\`îdühû feennâ tü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (1399, 'ḳul hel min şürakâiküm mey yehdî ile-lḥaḳḳ. ḳuli-llâhü yehdî lilḥaḳḳ. efemey yehdî ile-lḥaḳḳi eḥaḳḳu ey yüttebe\\`a emmel lâ yehiddî illâ ey yühdâ. femâ leküm. keyfe taḥkümûn.');
INSERT INTO `tr_transliteration` VALUES (1400, 'vemâ yettebi\\`u ekŝeruhüm illâ żannâ. inne-żżanne lâ yugnî mine-lḥaḳḳi şey\'â. inne-llâhe \\`alîmüm bimâ yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (1401, 'vemâ kâne hâẕe-lḳur\'ânü ey yüfterâ min dûni-llâhi velâkin taṣdîḳa-lleẕî beyne yedeyhi vetefṣîle-lkitâbi lâ raybe fîhi mir rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1402, 'em yeḳûlûne-fterâh. ḳul fe\'tû bisûratim miŝlihî ved\\`û meni-steṭa\\`tüm min dûni-llâhi in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1403, 'bel keẕẕebû bimâ lem yüḥîṭû bi\\`ilmihî velemmâ ye\'tihim te\'vîlüh. keẕâlike keẕẕebe-lleẕîne min ḳablihim fenżur keyfe kâne \\`âḳibetu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1404, 'veminhüm mey yü\'minü bihî veminhüm mel lâ yü\'minü bih. verabbüke a\\`lemü bilmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1405, 'vein keẕẕebûke feḳul lî \\`amelî veleküm \\`amelüküm. entüm berîûne mimmâ a\\`melü veenâ berîüm mimmâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1406, 'veminhüm mey yestemi\\`ûne ileyk. efeente tüsmi\\`u-ṣṣumme velev kânû lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1407, 'veminhüm mey yenżuru ileyk. efeente tehdi-l\\`umye velev kânû lâ yübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (1408, 'inne-llâhe lâ yażlimü-nnâse şey\'ev velâkinne-nnâse enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (1409, 'veyevme yaḥşüruhüm keel lem yelbeŝû illâ sâ\\`atem mine-nnehâri yete\\`ârafûne beynehüm. ḳad ḫasira-lleẕîne keẕẕebû biliḳâi-llâhi vemâ kânû mühtedîn.');
INSERT INTO `tr_transliteration` VALUES (1410, 'veimmâ nüriyenneke ba\\`ḍa-lleẕî ne\\`idühüm ev neteveffeyenneke feileynâ merci\\`uhüm ŝümme-llâhü şehîdün \\`alâ mâ yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (1411, 'velikülli ümmetir rasûl. feiẕâ câe rasûlühüm ḳuḍiye beynehüm bilḳisṭi vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (1412, 'veyeḳûlûne metâ hâẕe-lva\\`dü in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1413, 'ḳul lâ emlikü linefsî ḍarrav velâ nef\\`an illâ mâ şâe-llâh. likülli ümmetin ecelün. iẕâ câe ecelühüm felâ yeste\'ḫirûne sâ\\`atev velâ yestaḳdimûn.');
INSERT INTO `tr_transliteration` VALUES (1414, 'ḳul era\'eytüm in etâküm \\`aẕâbühû beyâten ev nehâram mâẕâ yesta\\`cilü minhü-lmücrimûn.');
INSERT INTO `tr_transliteration` VALUES (1415, 'eŝümme iẕâ mâ veḳa\\`a âmentüm bih. âl\'âne veḳad küntüm bihî testa\\`cilûn.');
INSERT INTO `tr_transliteration` VALUES (1416, 'ŝümme ḳîle lilleẕîne żalemû ẕûḳû \\`aẕâbe-lḫuld. hel tüczevne illâ bimâ küntüm teksibûn.');
INSERT INTO `tr_transliteration` VALUES (1417, 'veyestembiûneke eḥaḳḳun hû. ḳul î verabbî innehû leḥaḳḳuv vemâ entüm bimü\\`cizîn.');
INSERT INTO `tr_transliteration` VALUES (1418, 'velev enne likülli nefsin żalemet mâ fi-l\'arḍi leftedet bih. veeserrü-nnedâmete lemmâ raevu-l\\`aẕâb. veḳuḍiye beynehüm bilḳisṭi vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (1419, 'elâ inne lillâhi mâ fi-ssemâvâti vel\'arḍ. elâ inne va\\`de-llâhi ḥaḳḳuv velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1420, 'hüve yuḥyî veyümîtü veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1421, 'yâ eyyühe-nnâsü ḳad câetküm mev\\`iżatüm mir rabbiküm veşifâül limâ fi-ṣṣudûri vehüdev veraḥmetül lilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1422, 'ḳul bifaḍli-llâhi vebiraḥmetihî febiẕâlike felyefraḥû. hüve ḫayrum mimmâ yecme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1423, 'ḳul era\'eytüm mâ enzele-llâhü leküm mir rizḳin fece\\`altüm minhü ḥarâmev veḥalâlâ. ḳul ellâhü eẕine leküm em \\`ale-llâhi tefterûn.');
INSERT INTO `tr_transliteration` VALUES (1424, 'vemâ żannü-lleẕîne yefterûne \\`ale-llâhi-lkeẕibe yevme-lḳiyâmeh. inne-llâhe leẕû faḍlin \\`ale-nnâsi velâkinne ekŝerahüm lâ yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (1425, 'vemâ tekûnü fî şe\'niv vemâ tetlû minhü min ḳur\'âniv velâ ta\\`melûne min \\`amelin illâ künnâ \\`aleyküm şühûden iẕ tüfîḍûne fîh. vemâ ya\\`zübü \\`ar rabbike mim miŝḳâli ẕerratin fi-l\'arḍi velâ fi-ssemâi velâ aṣgara min ẕâlike velâ ekbera illâ fî kitâbim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1426, 'elâ inne evliyâe-llâhi lâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (1427, 'elleẕîne âmenû vekânû yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1428, 'lehümü-lbüşrâ fi-lḥayâti-ddünyâ vefi-l\'âḫirah. lâ tebdîle likelimâti-llâh. ẕâlike hüve-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1429, 'velâ yaḥzünke ḳavlühüm. inne-l\\`izzete lillâhi cemî\\`â. hüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1430, 'elâ inne lillâhi men fi-ssemâvâti vemen fi-l\'arḍ. vemâ yettebi\\`u-lleẕîne yed\\`ûne min dûni-llâhi şürakâ\'. iy yettebi\\`ûne ille-żżanne vein hüm illâ yaḫruṣûn.');
INSERT INTO `tr_transliteration` VALUES (1431, 'hüve-lleẕî ce\\`ale lekümü-lleyle liteskünû fîhi vennehâra mübṣirâ. inne fî ẕâlike leâyâtil liḳavmiy yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1432, 'ḳâlü-tteḫaẕe-llâhü veleden sübḥâneh. hüve-lganiyy. lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. in \\`indeküm min sülṭânim bihâẕâ. eteḳûlûne \\`ale-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1433, 'ḳul inne-lleẕîne yefterûne \\`ale-llâhi-lkeẕibe lâ yüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (1434, 'metâ\\`un fi-ddünyâ ŝümme ileynâ merci\\`uhüm ŝümme nüẕîḳuhümü-l\\`aẕâbe-şşedîde bimâ kânû yekfürûn.');
INSERT INTO `tr_transliteration` VALUES (1435, 'vetlü \\`aleyhim nebee nûḥ. iẕ ḳâle liḳavmihî yâ ḳavmi in kâne kebüra \\`aleyküm meḳâmî veteẕkîrî biâyâti-llâhi fe\\`ale-llâhi tevekkeltü feecmi\\`û emraküm veşürakâeküm ŝümme lâ yekün emruküm \\`aleyküm gummeten ŝümme-ḳḍû ileyye velâ tünżirûn.');
INSERT INTO `tr_transliteration` VALUES (1436, 'fein tevelleytüm femâ seeltüküm min ecr. in ecriye illâ \\`ale-llâhi veümirtü en ekûne mine-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (1437, 'fekeẕẕebûhü fenecceynâhü vemem me\\`ahû fi-lfülki vece\\`alnâhüm ḫalâife veagraḳne-lleẕîne keẕẕebû biâyâtinâ. fenżur keyfe kâne \\`âḳibetü-lmünẕerîn.');
INSERT INTO `tr_transliteration` VALUES (1438, 'ŝümme be\\`aŝnâ mim ba\\`dihî rusülen ilâ ḳavmihim fecâûhüm bilbeyyinâti femâ kânû liyü\'minû bimâ keẕẕebû bihî min ḳabl. keẕâlike naṭbe\\`u \\`alâ ḳulûbi-lmü\\`tedîn.');
INSERT INTO `tr_transliteration` VALUES (1439, 'ŝümme be\\`aŝnâ mim ba\\`dihim mûsâ vehârûne ilâ fir\\`avne vemeleihî biâyâtinâ festekberû vekânû ḳavmem mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1440, 'felemmâ câehümü-lḥaḳḳu min \\`indinâ ḳâlû inne hâẕâ lesiḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (1441, 'ḳâle mûsâ eteḳûlûne lilḥaḳḳi lemmâ câeküm. esiḥrun hâẕâ. velâ yüfliḥu-ssâḥirûn.');
INSERT INTO `tr_transliteration` VALUES (1442, 'ḳâlû eci\'tenâ litelfitenâ \\`ammâ vecednâ \\`aleyhi âbâenâ vetekûne leküme-lkibriyâü fi-l\'arḍ. vemâ naḥnü lekümâ bimü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1443, 'veḳâle fir\\`avnü-\'tûnî bikülli sâḥirin \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1444, 'felemmâ câe-sseḥaratü ḳâle lehüm mûsâ elḳû mâ entüm mülḳûn.');
INSERT INTO `tr_transliteration` VALUES (1445, 'felemmâ elḳav ḳâle mûsâ mâ ci\'tüm bihi-ssiḥr. inne-llâhe seyübṭilüh. inne-llâhe lâ yuṣliḥu \\`amele-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1446, 'veyüḥiḳḳu-llâhü-lḥaḳḳa bikelimâtihî velev kerihe-lmücrimûn.');
INSERT INTO `tr_transliteration` VALUES (1447, 'femâ âmene limûsâ illâ ẕürriyyetüm min ḳavmihî \\`alâ ḫavfim min fir\\`avne vemeleihim ey yeftinehüm. veinne fir\\`avne le\\`âlin fi-l\'arḍ. veinnehû lemine-lmüsrifîn.');
INSERT INTO `tr_transliteration` VALUES (1448, 'veḳâle mûsâ yâ ḳavmi in küntüm âmentüm billâhi fe\\`aleyhi tevekkelû in küntüm müslimîn.');
INSERT INTO `tr_transliteration` VALUES (1449, 'feḳâlû \\`ale-llâhi tevekkelnâ. rabbenâ lâ tec\\`alnâ fitnetel lilḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1450, 'veneccinâ biraḥmetike mine-lḳavmi-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1451, 'veevḥaynâ ilâ mûsâ veeḫîhi en tebevveâ liḳavmikümâ bimiṣra büyûtev vec\\`alû büyûteküm ḳibletev veeḳîmu-ṣṣalâh. vebeşşiri-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1452, 'veḳâle mûsâ rabbenâ inneke âteyte fir\\`avne vemeleehû zînetev veemvâlen fi-lḥayâti-ddünyâ rabbenâ liyüḍillû \\`an sebîlik. rabbene-ṭmis \\`alâ emvâlihim veşdüd \\`alâ ḳulûbihim felâ yü\'minû ḥattâ yeravu-l\\`aẕâbe-l\'elîm.');
INSERT INTO `tr_transliteration` VALUES (1453, 'ḳâle ḳad ücîbet da\\`vetükümâ festeḳîmâ velâ tettebi\\`ânni sebîle-lleẕîne lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1454, 'vecâveznâ bibenî isrâîle-lbaḥra feetbe\\`ahüm fir\\`avnü vecünûdühû bagyev ve\\`advâ. ḥattâ iẕâ edrakehü-lgaraḳu ḳâle âmentü ennehû lâ ilâhe ille-lleẕî âmenet bihî benû isrâîle veenâ mine-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (1455, 'âl\'âne veḳad \\`aṣayte ḳablü vekünte mine-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1456, 'felyevme nüneccîke bibedenike litekûne limen ḫalfeke âyeh. veinne keŝîram mine-nnâsi \\`an âyâtinâ legâfilûn.');
INSERT INTO `tr_transliteration` VALUES (1457, 'veleḳad bevve\'nâ benî isrâîle mübevvee ṣidḳiv verazaḳnâhüm mine-ṭṭayyibât. feme-ḫtelefû ḥattâ câehümü-l\\`ilm. inne rabbeke yaḳḍî beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (1458, 'fein künte fî şekkim mimmâ enzelnâ ileyke fes\'eli-lleẕîne yaḳraûne-lkitâbe min ḳablik. leḳad câeke-lḥaḳḳu mir rabbike felâ tekûnenne mine-lmümterîn.');
INSERT INTO `tr_transliteration` VALUES (1459, 'velâ tekûnenne mine-lleẕîne keẕẕebû biâyâti-llâhi fetekûne mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (1460, 'inne-lleẕîne ḥaḳḳat \\`aleyhim kelimetü rabbike lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1461, 'velev câethüm küllü âyetin ḥattâ yeravu-l\\`aẕâbe-l\'elîm.');
INSERT INTO `tr_transliteration` VALUES (1462, 'felevlâ kânet ḳaryetün âmenet fenefe\\`ahâ îmânühâ illâ ḳavme yûnüs. lemmâ âmenû keşefnâ \\`anhüm \\`aẕâbe-lḫizyi fi-lḥayâti-ddünyâ vemetta\\`nâhüm ilâ ḥîn.');
INSERT INTO `tr_transliteration` VALUES (1463, 'velev şâe rabbüke leâmene men fi-l\'arḍi küllühüm cemî\\`â. efeente tükrihü-nnâse ḥattâ yekûnû mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1464, 'vemâ kâne linefsin en tü\'mine illâ biiẕni-llâh. veyec\\`alü-rricse \\`ale-lleẕîne lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1465, 'ḳuli-nżurû mâẕâ fi-ssemâvâti vel\'arḍ. vemâ tugni-l\'âyâtü vennüẕüru \\`an ḳavmil lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1466, 'fehel yenteżirûne illâ miŝle eyyâmi-lleẕîne ḫalev min ḳablihim. ḳul fenteżirû innî me\\`aküm mine-lmünteżirîn.');
INSERT INTO `tr_transliteration` VALUES (1467, 'ŝümme nüneccî rusülenâ velleẕîne âmenû keẕâlik. ḥaḳḳan \\`aleynâ nünci-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1468, 'ḳul yâ eyyühe-nnâsü in küntüm fî şekkim min dînî felâ a\\`büdü-lleẕîne ta\\`büdûne min dûni-llâhi velâkin a\\`büdü-llâhe-lleẕî yeteveffâküm. veümirtü en ekûne mine-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1469, 'veen eḳim vecheke lildîni ḥanîfâ. velâ tekûnenne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (1470, 'velâ ted\\`u min dûni-llâhi mâ lâ yenfe\\`uke velâ yeḍurruk. fein fe\\`alte feinneke iẕem mine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1471, 'veiy yemseske-llâhü biḍurrin felâ kâşife lehû illâ hû. veiy yüridke biḫayrin felâ râdde lifaḍlih. yüṣîbü bihî mey yeşâü min \\`ibâdih. vehüve-lgafûru-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (1472, 'ḳul yâ eyyühe-nnâsü ḳad câekümü-lḥaḳḳu mir rabbiküm. femeni-htedâ feinnemâ yehtedî linefsih. vemen ḍalle feinnemâ yeḍillü \\`aleyhâ. vemâ ene \\`aleyküm bivekîl.');
INSERT INTO `tr_transliteration` VALUES (1473, 'vettebi\\` mâ yûḥâ ileyke vaṣbir ḥattâ yaḥküme-llâh. vehüve ḫayru-lḥâkimîn.');
INSERT INTO `tr_transliteration` VALUES (1474, 'elif-lâm-râ. kitâbün uḥkimet âyâtühû ŝümme füṣṣilet mil ledün ḥakîmin ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (1475, 'ellâ ta\\`büdû ille-llâh. innenî leküm minhü neẕîruv vebeşîr.');
INSERT INTO `tr_transliteration` VALUES (1476, 'veeni-stagfirû rabbeküm ŝümme tûbû ileyhi yümetti\\`küm metâ\\`an ḥasenen ilâ ecelim müsemmev veyü\'ti külle ẕî faḍlin faḍleh. vein tevellev feinnî eḫâfü \\`aleyküm \\`aẕâbe yevmin kebîr.');
INSERT INTO `tr_transliteration` VALUES (1477, 'ile-llâhi merci\\`uküm. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (1478, 'elâ innehüm yeŝnûne ṣudûrahüm liyestaḫfû minh. elâ ḥîne yestagşûne ŝiyâbehüm ya\\`lemü mâ yüsirrûne vemâ yü\\`linûne. innehû \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (1479, 'vemâ min dâbbetin fi-l\'arḍi illâ \\`ale-llâhi rizḳuhâ veya\\`lemü müsteḳarrahâ vemüstevde\\`ahâ. küllün fî kitâbim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1480, 'vehüve-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmiv vekâne \\`arşühû \\`ale-lmâi liyeblüveküm eyyüküm aḥsenü \\`amelâ. velein ḳulte inneküm meb\\`ûŝûne mim ba\\`di-lmevti leyeḳûlenne-lleẕîne keferû in hâẕâ illâ siḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (1481, 'velein eḫḫarnâ \\`anhümü-l\\`aẕâbe ilâ ümmetim ma\\`dûdetil leyeḳûlünne mâ yaḥbisüh. elâ yevme ye\'tîhim leyse maṣrûfen \\`anhüm veḥâḳa bihim mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (1482, 'velein eẕaḳne-l\'insâne minnâ raḥmeten ŝümme neza\\`nâhâ minh. innehû leyeûsün kefûr.');
INSERT INTO `tr_transliteration` VALUES (1483, 'velein eẕaḳnâhü na\\`mâe ba\\`de ḍarrâe messethü leyeḳûlenne ẕehebe-sseyyiâtü \\`annî. innehû leferiḥun feḫûr.');
INSERT INTO `tr_transliteration` VALUES (1484, 'ille-lleẕîne ṣaberû ve\\`amilu-ṣṣâliḥâti. ülâike lehüm magfiratüv veecrun kebîr.');
INSERT INTO `tr_transliteration` VALUES (1485, 'fele\\`alleke târiküm ba\\`ḍa mâ yûḥâ ileyke veḍâiḳum bihî ṣadruke ey yeḳûlû levlâ ünzile \\`aleyhi kenzün ev câe me\\`ahû melek. innemâ ente neẕîr. vellâhü \\`alâ külli şey\'iv vekîl.');
INSERT INTO `tr_transliteration` VALUES (1486, 'em yeḳûlûne-fterâh. ḳul fe\'tû bi\\`aşri süverim miŝlihî müfterayâtiv ved\\`û meni-steṭa\\`tüm min dûni-llâhi in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1487, 'feillem yestecîbû leküm fa\\`lemû ennemâ ünzile bi\\`ilmi-llâhi veel lâ ilâhe illâ hû. fehel entüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (1488, 'men kâne yürîdü-lḥayâte-ddünyâ vezînetehâ nüveffi ileyhim a\\`mâlehüm fîhâ vehüm fîhâ lâ yübḫasûn.');
INSERT INTO `tr_transliteration` VALUES (1489, 'ülâike-lleẕîne leyse lehüm fi-l\'âḫirati ille-nnâr. veḥabiṭa mâ ṣane\\`û fîhâ vebâṭilüm mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1490, 'efemen kâne \\`alâ beyyinetim mir rabbihî veyetlûhü şâhidüm minhü vemin ḳablihî kitâbü mûsâ imâmev veraḥmeh. ülâike yü\'minûne bih. vemey yekfür bihî mine-l\'aḥzâbi felnâru mev\\`idüh. felâ tekü fî miryetim minhü innehü-lḥaḳḳu mir rabbike velâkinne ekŝera-nnâsi lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1491, 'vemen ażlemü mimmeni-fterâ \\`ale-llâhi keẕibâ. ülâike yü\\`raḍûne \\`alâ rabbihim veyeḳûlü-l\'eşhâdü hâülâi-lleẕîne keẕebû \\`alâ rabbihim. elâ la\\`netü-llâhi \\`ale-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1492, 'elleẕîne yeṣuddûne \\`an sebîli-llâhi veyebgûnehâ \\`ivecâ. vehüm bil\'âḫirati hüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1493, 'ülâike lem yekûnû mü\\`cizîne fi-l\'arḍi vemâ kâne lehüm min dûni-llâhi min evliyâ\'. yüḍâ\\`afü lehümü-l\\`aẕâb. mâ kânû yesteṭî\\`ûne-ssem\\`a vemâ kânû yübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (1494, 'ülâike-lleẕîne ḫasirû enfüsehüm veḍalle \\`anhüm mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (1495, 'lâ cerame ennehüm fi-l\'âḫirati hümü-l\'aḫserûn.');
INSERT INTO `tr_transliteration` VALUES (1496, 'inne-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti veaḫbetû ilâ rabbihim ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (1497, 'meŝelü-lferîḳayni kel\'a\\`mâ vel\'eṣammi velbeṣîri vessemî\\`. hel yesteviyâni meŝelâ. efelâ teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1498, 'veleḳad erselnâ nûḥan ilâ ḳavmih. innî leküm neẕîrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (1499, 'el lâ ta\\`büdû ille-llâh. innî eḫâfü \\`aleyküm \\`aẕâbe yevmin elîm.');
INSERT INTO `tr_transliteration` VALUES (1500, 'feḳâle-lmeleü-lleẕîne keferû min ḳavmihî mâ nerâke illâ beşeram miŝlenâ vemâ nerâke-ttebe\\`ake ille-lleẕîne hüm erâẕilünâ bâdiye-rra\'y. vemâ nerâ leküm \\`aleynâ min faḍlim bel neżunnüküm kâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (1501, 'ḳâle yâ ḳavmi era\'eytüm in küntü \\`alâ beyyinetim mir rabbî veâtânî raḥmetem min \\`indihî fe\\`ummiyet \\`aleyküm. enülzimükümûhâ veentüm lehâ kârihûn.');
INSERT INTO `tr_transliteration` VALUES (1502, 'veyâ ḳavmi lâ es\'elüküm \\`aleyhi mâlâ. in ecriye illâ \\`ale-llâhi vemâ ene biṭâridi-lleẕîne âmenû. innehüm mülâḳû rabbihim velâkinnî erâküm ḳavmen techelûn.');
INSERT INTO `tr_transliteration` VALUES (1503, 'veyâ ḳavmi mey yenṣurunî mine-llâhi in ṭarattühüm. efelâ teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1504, 'velâ eḳûlü leküm \\`indî ḫazâinü-llâhi velâ a\\`lemü-lgaybe velâ eḳûlü innî meleküv velâ eḳûlü lilleẕîne tezderî a\\`yünüküm ley yü\'tiyehümü-llâhü ḫayrâ. allâhü a\\`lemü bimâ fî enfüsihim. innî iẕel lemine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1505, 'ḳâlû yâ nûḥu ḳad câdeltenâ feekŝerte cidâlenâ fe\'tinâ bimâ te\\`idünâ in künte mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1506, 'ḳâle innemâ ye\'tîküm bihi-llâhü in şâe vemâ entüm bimü\\`cizîn.');
INSERT INTO `tr_transliteration` VALUES (1507, 'velâ yenfe\\`uküm nuṣḥî in erattü en enṣaḥa leküm in kâne-llâhü yürîdü ey yugviyeküm. hüve rabbüküm veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1508, 'em yeḳûlûne-fterâh. ḳul ini-fteraytühû fe\\`aleyye icrâmî veenâ berîüm mimmâ tücrimûn.');
INSERT INTO `tr_transliteration` VALUES (1509, 'veûḥiye ilâ nûḥin ennehû ley yü\'mine min ḳavmike illâ men ḳad âmene felâ tebteis bimâ kânû yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (1510, 'vaṣne\\`i-lfülke bia\\`yüninâ vevaḥyinâ velâ tüḫâṭibnî fi-lleẕîne żalemû. innehüm mugraḳûn.');
INSERT INTO `tr_transliteration` VALUES (1511, 'veyaṣne\\`u-lfülke veküllemâ merra \\`aleyhi meleüm min ḳavmihî seḫirû minh. ḳâle in tesḫarû minnâ feinnâ nesḫaru minküm kemâ tesḫarûn.');
INSERT INTO `tr_transliteration` VALUES (1512, 'fesevfe ta\\`lemûne mey ye\'tîhi \\`aẕâbüy yuḫzîhi veyeḥillü \\`aleyhi \\`aẕâbüm müḳîm.');
INSERT INTO `tr_transliteration` VALUES (1513, 'ḥattâ iẕâ câe emrunâ vefâra-ttennûru ḳulne-ḥmil fîhâ min küllin zevceyni-ŝneyni veehleke illâ men sebeḳa \\`aleyhi-lḳavlü vemen âmen. vemâ âmene me\\`ahû illâ ḳalîl.');
INSERT INTO `tr_transliteration` VALUES (1514, 'veḳâle-rkebû fîhâ bismi-llâhi mecrâhâ vemürsâhâ. inne rabbî legafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1515, 'vehiye tecrî bihim fî mevcin kelcibâli venâdâ nûḥun-bnehû vekâne fî ma\\`ziliy yâ büneyye-rkem me\\`anâ velâ teküm me\\`a-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (1516, 'ḳâle seâvî ilâ cebeliy ya\\`ṣimünî mine-lmâ\'. ḳâle lâ \\`âṣime-lyevme min emri-llâhi illâ mer raḥim. veḥâle beynehüme-lmevcü fekâne mine-lmugraḳîn.');
INSERT INTO `tr_transliteration` VALUES (1517, 'veḳîle yâ arḍu-ble\\`î mâeki veyâ semâü aḳli\\`î vegîḍa-lmâü veḳuḍiye-l\'emru vestevet \\`ale-lcûdiyyi veḳîle bü\\`del lilḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1518, 'venâdâ nûḥur rabbehû feḳâle rabbi inne-bnî min ehlî veinne va\\`deke-lḥaḳḳu veente aḥkemü-lḥâkimîn.');
INSERT INTO `tr_transliteration` VALUES (1519, 'ḳâle yâ nûḥu innehû leyse min ehlik. innehû \\`amelün gayru ṣâliḥ. felâ tes\'elni mâ leyse leke bihî \\`ilm. innî e\\`iżuke en tekûne mine-lcâhilîn.');
INSERT INTO `tr_transliteration` VALUES (1520, 'ḳâle rabbi innî e\\`ûẕü bike en es\'eleke mâ leyse lî bihî \\`ilm. veillâ tagfir lî veterḥamnî eküm mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (1521, 'ḳîle yâ nûḥu-hbiṭ biselâmim minnâ veberakâtin \\`aleyke ve\\`alâ ümemim mimmem me\\`ak. veümemün senümetti\\`uhüm ŝümme yemessühüm minnâ \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1522, 'tilke min embâi-lgaybi nûḥîhâ ileyk. mâ künte ta\\`lemühâ ente velâ ḳavmüke min ḳabli hâẕâ. faṣbir. inne-l\\`âḳibete lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (1523, 'veilâ \\`âdin eḫâhüm hûdâ. ḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. in entüm illâ müfterûn.');
INSERT INTO `tr_transliteration` VALUES (1524, 'yâ ḳavmi lâ es\'elüküm \\`aleyhi ecrâ. in ecriye illâ \\`ale-lleẕî feṭaranî. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1525, 'veyâ ḳavmi-stagfirû rabbeküm ŝümme tûbû ileyhi yürsili-ssemâe \\`aleyküm midrârav veyezidküm ḳuvveten ilâ ḳuvvetiküm velâ tetevellev mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1526, 'ḳâlû yâ hûdü mâ ci\'tenâ bibeyyinetiv vemâ naḥnü bitârikî âlihetinâ \\`an ḳavlike vemâ naḥnü leke bimü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1527, 'in neḳûlü ille-\\`terâke ba\\`ḍu âlihetinâ bisû\'. ḳâle innî üşhidü-llâhe veşhedû ennî berîüm mimmâ tüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (1528, 'min dûnihî fekîdûnî cemî\\`an ŝümme lâ tünżirûn.');
INSERT INTO `tr_transliteration` VALUES (1529, 'innî tevekkeltü \\`ale-llâhi rabbî verabbiküm. mâ min dâbbetin illâ hüve âḫiẕüm binâṣiyetihâ. inne rabbî \\`alâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (1530, 'fein tevellev feḳad eblagtüküm mâ ürsiltü bihî ileyküm. veyestaḫlifü rabbî ḳavmen gayraküm. velâ teḍurrûnehû şey\'â. inne rabbî \\`alâ külli şey\'in ḥafîż.');
INSERT INTO `tr_transliteration` VALUES (1531, 'velemmâ câe emrunâ necceynâ hûdev velleẕîne âmenû me\\`ahû biraḥmetim minnâ. venecceynâhüm min \\`aẕâbin galîż.');
INSERT INTO `tr_transliteration` VALUES (1532, 'vetilke \\`âdün ceḥadû biâyâti rabbihim ve\\`aṣav rusülehû vettebe\\`û emra külli cebbârin \\`anîd.');
INSERT INTO `tr_transliteration` VALUES (1533, 'veütbi\\`û fî hâẕihi-ddünyâ la\\`netev veyevme-lḳiyâmeh. elâ inne \\`âden keferû rabbehüm. elâ bü\\`del li\\`âdin ḳavmi hûd.');
INSERT INTO `tr_transliteration` VALUES (1534, 'veilâ ŝemûde eḫâhüm ṣâliḥâ. ḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. hüve enşeeküm mine-l\'arḍi vesta\\`meraküm fîhâ festagfirûhü ŝümme tûbû ileyh. inne rabbî ḳarîbüm mücîb.');
INSERT INTO `tr_transliteration` VALUES (1535, 'ḳâlû yâ ṣâliḥu ḳad künte fînâ mercüvven ḳable hâẕâ etenhânâ en na\\`büde mâ ya\\`büdü âbâünâ veinnenâ lefî şekkim mimmâ ted\\`ûnâ ileyhi mürîbün.');
INSERT INTO `tr_transliteration` VALUES (1536, 'ḳâle yâ ḳavmi era\'eytüm in küntü \\`alâ beyyinetim mir rabbî veâtânî minhü raḥmeten femey yenṣurunî mine-llâhi in \\`aṣaytühû femâ tezîdûnenî gayra taḫsîr.');
INSERT INTO `tr_transliteration` VALUES (1537, 'veyâ ḳavmi hâẕihî nâḳatü-llâhi leküm âyeten feẕerûhâ te\'kül fî arḍi-llâhi velâ temessûhâ bisûin feye\'ḫuẕeküm \\`aẕâbün ḳarîb.');
INSERT INTO `tr_transliteration` VALUES (1538, 'fe\\`aḳarûhâ feḳâle temette\\`û fî dâriküm ŝelâŝete eyyâm. ẕâlike va\\`dün gayru mekẕûb.');
INSERT INTO `tr_transliteration` VALUES (1539, 'felemmâ câe emrunâ necceynâ ṣâliḥav velleẕîne âmenû me\\`ahû biraḥmetim minnâ vemin ḫizyi yevmiiẕ. inne rabbeke hüve-lḳaviyyü-l\\`azîz.');
INSERT INTO `tr_transliteration` VALUES (1540, 'veeḫaẕe-lleẕîne żalemu-ṣṣayḥatü feaṣbeḥû fî diyârihim câŝimîn.');
INSERT INTO `tr_transliteration` VALUES (1541, 'keel lem yagnev fîhâ. elâ inne ŝemûde keferû rabbehüm. elâ bü\\`del liŝemûd.');
INSERT INTO `tr_transliteration` VALUES (1542, 'veleḳad câet rusülünâ ibrâhîme bilbüşrâ ḳâlû selâmâ. ḳâle selâmün femâ lebiŝe en câe bi\\`iclin ḥanîẕ.');
INSERT INTO `tr_transliteration` VALUES (1543, 'felemmâ raâ eydiyehüm lâ teṣilü ileyhi nekirahüm veevcese minhüm ḫîfeh. ḳâlû lâ teḫaf innâ ürsilnâ ilâ ḳavmi lûṭ.');
INSERT INTO `tr_transliteration` VALUES (1544, 'vemraetühû ḳâimetün feḍaḥiket febeşşernâhâ biisḥâḳa vemiv verâi isḥâḳa ya\\`ḳûb.');
INSERT INTO `tr_transliteration` VALUES (1545, 'ḳâlet yâ veyletâ eelidü veenâ \\`acûzüv vehâẕâ ba\\`lî şeyḫâ. inne hâẕâ leşey\'ün \\`acîb.');
INSERT INTO `tr_transliteration` VALUES (1546, 'ḳâlû eta\\`cebîne min emri-llâhi raḥmetü-llâhi veberakâtühû \\`aleyküm ehle-lbeyt. innehû ḥamîdüm mecîd.');
INSERT INTO `tr_transliteration` VALUES (1547, 'felemmâ ẕehebe \\`an ibrâhîme-rrav\\`u vecâethü-lbüşrâ yücâdilünâ fî ḳavmi lûṭ.');
INSERT INTO `tr_transliteration` VALUES (1548, 'inne ibrâhîme leḥalîmün evvâhüm münîb.');
INSERT INTO `tr_transliteration` VALUES (1549, 'yâ ibrâhîmü a\\`riḍ \\`an hâẕâ. innehû ḳad câe emru rabbik. veinnehüm âtîhim \\`aẕâbün gayru merdûd.');
INSERT INTO `tr_transliteration` VALUES (1550, 'velemmâ câet rusülünâ lûṭan sîe bihim veḍâḳa bihim ẕer\\`av veḳâle hâẕâ yevmün \\`aṣîb.');
INSERT INTO `tr_transliteration` VALUES (1551, 'vecâehû ḳavmühû yühra\\`ûne ileyhi vemin ḳablü kânû ya\\`melûne-sseyyiât. ḳâle yâ ḳavmi hâülâi benâtî hünne aṭheru leküm fetteḳu-llâhe velâ tuḫzûni fî ḍayfî. eleyse minküm racülür raşîd.');
INSERT INTO `tr_transliteration` VALUES (1552, 'ḳâlû leḳad \\`alimte mâ lenâ fî benâtike min ḥaḳḳ. veinneke leta\\`lemü mâ nürîd.');
INSERT INTO `tr_transliteration` VALUES (1553, 'ḳâle lev enne lî biküm ḳuvveten ev âvî ilâ ruknin şedîd.');
INSERT INTO `tr_transliteration` VALUES (1554, 'ḳâlû yâ lûṭu innâ rusülü rabbike ley yeṣilû ileyke feesri biehlike biḳiṭ\\`im mine-lleyli velâ yeltefit minküm eḥadün ille-mraetek. innehû müṣîbühâ mâ eṣâbehüm. inne mev\\`idehümu-ṣṣubḥ. eleyse-ṣṣubḥu biḳarîb.');
INSERT INTO `tr_transliteration` VALUES (1555, 'felemmâ câe emrunâ ce\\`alnâ \\`âliyehâ sâfilehâ veemṭarnâ \\`aleyhâ ḥicâratem min siccîlim menḍûd.');
INSERT INTO `tr_transliteration` VALUES (1556, 'müsevvemeten \\`inde rabbik. vemâ hiye mine-żżâlimîne bibe\\`îd.');
INSERT INTO `tr_transliteration` VALUES (1557, 'veilâ medyene eḫâhüm şu\\`aybâ. ḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. velâ tenḳuṣü-lmikyâle velmîzâne innî erâküm biḫayriv veinnî eḫâfü \\`aleyküm \\`aẕâbe yevmim müḥîṭ.');
INSERT INTO `tr_transliteration` VALUES (1558, 'veyâ ḳavmi evfü-lmikyâle velmîzâne bilḳisṭi velâ tebḫasü-nnâse eşyâehüm velâ ta\\`ŝev fi-l\'arḍi müfsidîn.');
INSERT INTO `tr_transliteration` VALUES (1559, 'beḳiyyetü-llâhi ḫayrul leküm in küntüm mü\'minîn. vemâ ene \\`aleyküm biḥafîż.');
INSERT INTO `tr_transliteration` VALUES (1560, 'ḳâlû yâ şu\\`aybü eṣalâtüke te\'müruke en netruke mâ ya\\`büdü âbâünâ ev en nef\\`ale fî emvâlinâ mâ neşâ\'. inneke leente-lḥalîmü-rraşîd.');
INSERT INTO `tr_transliteration` VALUES (1561, 'ḳâle yâ ḳavmi era\'eytüm in küntü \\`alâ beyyinetim mir rabbî verazeḳanî minhü rizḳan ḥasenâ. vemâ ürîdü en üḫâlifeküm ilâ mâ enhâküm \\`anh. in ürîdü ille-l\'iṣlâḥa me-steṭa\\`t. vemâ tevfîḳî illâ billâh. \\`aleyhi tevekkeltü veileyhi ünîb.');
INSERT INTO `tr_transliteration` VALUES (1562, 'veyâ ḳavmi lâ yecrimenneküm şiḳâḳî ey yüṣîbeküm miŝlü mâ eṣâbe ḳavme nûḥin ev ḳavme hûdin ev ḳavme ṣâliḥ. vemâ ḳavmü lûṭim minküm bibe\\`îd.');
INSERT INTO `tr_transliteration` VALUES (1563, 'vestagfirû rabbeküm ŝümme tûbû ileyh. inne rabbî raḥîmüv vedûd.');
INSERT INTO `tr_transliteration` VALUES (1564, 'ḳâlû yâ şu\\`aybü mâ nefḳahû keŝîram mimmâ teḳûlü veinnâ lenerâke fînâ ḍa\\`îfâ. velevlâ rahṭuke leracemnâk. vemâ ente \\`aleynâ bi\\`azîz.');
INSERT INTO `tr_transliteration` VALUES (1565, 'ḳâle yâ ḳavmi erahṭî e\\`azzü \\`aleyküm mine-llâh. vetteḫaẕtümûhü verâeküm żihriyyâ. inne rabbî bimâ ta\\`melûne müḥîṭ.');
INSERT INTO `tr_transliteration` VALUES (1566, 'veyâ ḳavmi-\\`melû \\`alâ mekânetiküm innî \\`âmil. sevfe ta\\`lemûne mey ye\'tîhi \\`aẕâbüy yuḫzîhi vemen hüve kâẕib. verteḳibû innî me\\`aküm raḳîb.');
INSERT INTO `tr_transliteration` VALUES (1567, 'velemmâ câe emrunâ necceynâ şu\\`aybev velleẕîne âmenû me\\`ahû biraḥmetim minnâ veeḫaẕeti-lleẕîne żalemu-ṣṣayḥatü feaṣbeḥû fî diyârihim câŝimîn.');
INSERT INTO `tr_transliteration` VALUES (1568, 'keel lem yagnev fîhâ. elâ bü\\`del limedyene kemâ be\\`idet ŝemûd.');
INSERT INTO `tr_transliteration` VALUES (1569, 'veleḳad erselnâ mûsâ biâyâtinâ vesülṭânim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1570, 'ilâ fir\\`avne vemeleihî fettebe\\`û emra fir\\`avn. vemâ emru fir\\`avne biraşîd.');
INSERT INTO `tr_transliteration` VALUES (1571, 'yaḳdümü ḳavmehû yevme-lḳiyâmeti feevradehümü-nnâr. vebi\'se-lvirdü-lmevrûd.');
INSERT INTO `tr_transliteration` VALUES (1572, 'veütbi\\`û fî hâẕihî la\\`netev veyevme-lḳiyâmeh. bi\'se-rrifdü-lmerfûd.');
INSERT INTO `tr_transliteration` VALUES (1573, 'ẕâlike min embâi-lḳurâ neḳuṣṣuhû \\`aleyke minhâ ḳâimüv veḥaṣîd.');
INSERT INTO `tr_transliteration` VALUES (1574, 'vemâ żalemnâhüm velâkin żalemû enfüsehüm femâ agnet \\`anhüm âlihetühümü-lletî yed\\`ûne min dûni-llâhi min şey\'il lemmâ câe emru rabbik. vemâ zâdûhüm gayra tetbîb.');
INSERT INTO `tr_transliteration` VALUES (1575, 'vekeẕâlike aḫẕü rabbike iẕâ eḫaẕe-lḳurâ vehiye żâlimeh. inne aḫẕehû elîmün şedîd.');
INSERT INTO `tr_transliteration` VALUES (1576, 'inne fî ẕâlike leâyetel limen ḫâfe \\`aẕâbe-l\'âḫirah. ẕâlike yevmüm mecmû\\`ul lehü-nnâsü veẕâlike yevmüm meşhûd.');
INSERT INTO `tr_transliteration` VALUES (1577, 'vemâ nü\'eḫḫiruhû illâ liecelim ma\\`dûd.');
INSERT INTO `tr_transliteration` VALUES (1578, 'yevme ye\'ti lâ tekellemü nefsün illâ biiẕnih. feminhüm şeḳiyyüv vese\\`îd.');
INSERT INTO `tr_transliteration` VALUES (1579, 'feemme-lleẕîne şeḳû fefi-nnâri lehüm fîhâ zefîruv veşehîḳ.');
INSERT INTO `tr_transliteration` VALUES (1580, 'ḫâlidîne fîhâ mâ dâmeti-ssemâvâtü vel\'arḍu illâ mâ şâe rabbük. inne rabbeke fa\\`\\`âlül limâ yürîd.');
INSERT INTO `tr_transliteration` VALUES (1581, 'veemme-lleẕîne sü\\`idû fefi-lcenneti ḫâlidîne fîhâ mâ dâmeti-ssemâvâtü vel\'arḍu illâ mâ şâe rabbük. \\`aṭâen gayra mecẕûẕ.');
INSERT INTO `tr_transliteration` VALUES (1582, 'felâ tekü fî miryetim mimmâ ya\\`büdü hâülâ\'. mâ ya\\`büdûne illâ kemâ ya\\`büdü âbâühüm min ḳabl. veinnâ lemüveffûhüm neṣîbehüm gayra menḳûṣ.');
INSERT INTO `tr_transliteration` VALUES (1583, 'veleḳad âteynâ mûse-lkitâbe faḫtülife fîh. velevlâ kelimetün sebeḳat mir rabbike leḳuḍiye beynehüm. veinnehüm lefî şekkim minhü mürîbün.');
INSERT INTO `tr_transliteration` VALUES (1584, 'veinne küllel lemmâ leyüveffiyennehüm rabbüke a\\`mâlehüm. innehû bimâ ya\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (1585, 'festeḳim kemâ ümirte vemen tâbe me\\`ake velâ taṭgav. innehû bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (1586, 'velâ terkenû ile-lleẕîne żalemû fetemessekümü-nnâru vemâ leküm min dûni-llâhi min evliyâe ŝümme lâ tünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (1587, 'veeḳimi-ṣṣalâte ṭarafeyi-nnehâri vezülefem mine-lleyl. inne-lḥasenâti yüẕhibne-sseyyiât. ẕâlike ẕikrâ liẕẕâkirîn.');
INSERT INTO `tr_transliteration` VALUES (1588, 'vaṣbir feinne-llâhe lâ yüḍî\\`u ecra-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1589, 'felevlâ kâne mine-lḳurûni min ḳabliküm ülû beḳiyyetiy yenhevne \\`ani-lfesâdi fi-l\'arḍi illâ ḳalîlem mimmen enceynâ minhüm. vettebe\\`a-lleẕîne żalemû mâ ütrifû fîhi vekânû mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1590, 'vemâ kâne rabbüke liyühlike-lḳurâ biżulmiv veehlühâ muṣliḥûn.');
INSERT INTO `tr_transliteration` VALUES (1591, 'velev şâe rabbüke lece\\`ale-nnâse ümmetev vâḥidetev velâ yezâlûne muḫtelifîn.');
INSERT INTO `tr_transliteration` VALUES (1592, 'illâ mer raḥime rabbük. veliẕâlike ḫaleḳahüm. vetemmet kelimetü rabbike leemleenne cehenneme mine-lcinneti vennâsi ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (1593, 'veküllen neḳuṣṣu \\`aleyke min embâi-rrusüli mâ nüŝebbitü bihî füâdek. vecâeke fî hâẕihi-lḥaḳḳu vemev\\`iżatüv veẕikrâ lilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1594, 'veḳul lilleẕîne lâ yü\'minûne-\\`melû \\`alâ mekânetiküm. innâ \\`âmilûn.');
INSERT INTO `tr_transliteration` VALUES (1595, 'venteżirû. innâ münteżirûn.');
INSERT INTO `tr_transliteration` VALUES (1596, 'velillâhi gaybü-ssemâvâti vel\'arḍi veileyhi yürce\\`u-l\'emru küllühû fa\\`büdhü vetevekkel \\`aleyh. vemâ rabbüke bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1597, 'elif-lâm-râ. tilke âyâtü-lkitâbi-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (1598, 'innâ enzelnâhü ḳur\'ânen \\`arabiyyel le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1599, 'naḥnü neḳuṣṣu \\`aleyke aḥsene-lḳaṣaṣi bimâ evḥaynâ ileyke hâẕe-lḳur\'ân. vein künte min ḳablihî lemine-lgâfilîn.');
INSERT INTO `tr_transliteration` VALUES (1600, 'iẕ ḳâle yûsüfü liebîhi yâ ebeti innî raeytü eḥade \\`aşera kevkebev veşşemse velḳamera raeytühüm lî sâcidîn.');
INSERT INTO `tr_transliteration` VALUES (1601, 'ḳâle yâ büneyye lâ taḳṣuṣ ru\'yâke \\`alâ iḫvetike feyekîdû leke keydâ. inne-şşeyṭâne lil\'insâni \\`adüvvüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (1602, 'vekeẕâlike yectebîke rabbüke veyü\\`allimüke min te\'vîli-l\'eḥâdîŝi veyütimmü ni\\`metehû \\`aleyke ve\\`alâ âli ya\\`ḳûbe kemâ etemmehâ \\`alâ ebeveyke min ḳablü ibrâhîme veisḥâḳ. inne rabbeke \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1603, 'leḳad kâne fî yûsüfe veiḫvetihî âyâtül lissâilîn.');
INSERT INTO `tr_transliteration` VALUES (1604, 'iẕ ḳâlû leyûsüfü veeḫûhü eḥabbü ilâ ebînâ minnâ venaḥnü \\`uṣbeh. inne ebânâ lefî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1605, 'uḳtülû yûsüfe evi-ṭraḥûhü arḍay yaḫlü leküm vechü ebîküm vetekûnû mim ba\\`dihî ḳavmen ṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (1606, 'ḳâle ḳâilüm minhüm lâ taḳtülû yûsüfe veelḳûhü fî gayâbeti-lcübbi yelteḳiṭhü ba\\`ḍu-sseyyârati in küntüm fâ\\`ilîn.');
INSERT INTO `tr_transliteration` VALUES (1607, 'ḳâlû yâ ebânâ mâ leke lâ te\'mennâ \\`alâ yûsüfe veinnâ lehû lenâṣiḥûn.');
INSERT INTO `tr_transliteration` VALUES (1608, 'ersilhü me\\`anâ gadey yerta\\` veyel\\`ab veinnâ lehû leḥâfiżûn.');
INSERT INTO `tr_transliteration` VALUES (1609, 'ḳâle innî leyaḥzününî en teẕhebû bihî veeḫâfü ey ye\'külehü-ẕẕi\'bü veentüm \\`anhü gâfilûn.');
INSERT INTO `tr_transliteration` VALUES (1610, 'ḳâlû lein ekelehü-ẕẕi\'bü venaḥnü \\`uṣbetün innâ iẕel leḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (1611, 'felemmâ ẕehebû bihî veecme\\`û ey yec\\`alûhü fî gayâbeti-lcübb. veevḥaynâ ileyhi letünebbiennehüm biemrihim hâẕâ vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (1612, 'vecâû ebâhüm \\`işâey yebkûn.');
INSERT INTO `tr_transliteration` VALUES (1613, 'ḳâlû yâ ebânâ innâ ẕehebnâ nestebiḳu veteraknâ yûsüfe \\`inde metâ\\`inâ feekelehü-ẕẕi\'b. vemâ ente bimü\'minil lenâ velev künnâ ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1614, 'vecâû \\`alâ ḳamîṣihî bidemin kâẕib. ḳâle bel sevvelet leküm enfüsüküm emrâ. feṣabrun cemîl. vellâhü-lmüste\\`ânü \\`alâ mâ teṣifûn.');
INSERT INTO `tr_transliteration` VALUES (1615, 'vecâet seyyâratün feerselû vâridehüm feedlâ delveh. ḳâle yâ büşrâ hâẕâ gulâm. veeserrûhü biḍâ\\`ah. vellâhü \\`alîmüm bimâ ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1616, 'veşeravhü biŝemenim baḫsin derâhime ma\\`dûdeh. vekânû fîhi mine-zzâhidîn.');
INSERT INTO `tr_transliteration` VALUES (1617, 'veḳâle-lleẕi-şterâhü mim miṣra limraetihî ekrimî meŝvâhü \\`asâ ey yenfe\\`anâ ev netteḫiẕehû veledâ. vekeẕâlike mekkennâ liyûsüfe fi-l\'arḍ. velinü\\`allimehû min te\'vîli-l\'eḥâdîŝ. vellâhü gâlibün \\`alâ emrihî velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1618, 'velemmâ belega eşüddeh âteynâhü ḥukmev ve\\`ilmâ. vekeẕâlike neczi-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1619, 'verâvedethü-lletî hüve fî beytihâ \\`an nefsihî vegalleḳati-l\'ebvâbe veḳâlet heyte lek. ḳâle me\\`âẕe-llâhi innehû rabbî aḥsene meŝvây. innehû lâ yüfliḥu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (1620, 'veleḳad hemmet bih. vehemme bihâ. levlâ er raâ bürhâne rabbih. keẕâlike linaṣrife \\`anhü-ssûe velfaḥşâ\'. innehû min \\`ibâdine-lmuḫleṣîn.');
INSERT INTO `tr_transliteration` VALUES (1621, 'vestebeḳe-lbâbe veḳaddet ḳamîṣahû min dübüriv veelfeyâ seyyidehâ lede-lbâb. ḳâlet mâ cezâü men erâde biehlike sûen illâ ey yüscene ev \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1622, 'ḳâle hiye râvedetnî \\`an nefsî veşehide şâhidüm min ehlihâ. in kâne ḳamîṣuhû ḳudde min ḳubülin feṣadeḳat vehüve mine-lkâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (1623, 'vein kâne ḳamîṣuhû ḳudde min dübürin fekeẕebet vehüve mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1624, 'felemmâ raâ ḳamîṣahû ḳudde min dübürin ḳâle innehû min keydikünn. inne keydekünne \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1625, 'yûsüfü a\\`riḍ \\`an hâẕâ vestagfirî liẕembik. inneki künti mine-lḫâṭiîn.');
INSERT INTO `tr_transliteration` VALUES (1626, 'veḳâle nisvetün fi-lmedîneti-mraetü-l\\`azîzi türâvidü fetâhâ \\`an nefsih. ḳad şegafehâ ḥubbâ. innâ lenerâhâ fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1627, 'felemmâ semi\\`at bimekrihinne erselet ileyhinne vea\\`tedet lehünne müttekeev veâtet külle vâḥidetim minhünne sikkînev veḳâleti-ḫruc \\`aleyhinn. felemmâ raeynâhü ekbernehû veḳaṭṭa\\`ne eydiyehünne veḳulne ḥâşe lillâhi mâ hâẕâ beşerâ. in hâẕâ illâ melekün kerîm.');
INSERT INTO `tr_transliteration` VALUES (1628, 'ḳâlet feẕâlikünne-lleẕî lümtünnenî fîh. veleḳad râvettühû \\`an nefsihî festa\\`ṣam. veleil lem yef\\`al mâ âmüruhû leyüscenenne veleyekûnem mine-ṣṣâgirîn.');
INSERT INTO `tr_transliteration` VALUES (1629, 'ḳâle rabbi-ssicnü eḥabbü ileyye mimmâ yed\\`ûnenî ileyh. veillâ taṣrif \\`annî keydehünne aṣbü ileyhinne veeküm mine-lcâhilîn.');
INSERT INTO `tr_transliteration` VALUES (1630, 'festecâbe lehû rabbühû feṣarafe \\`anhü keydehünn. innehû hüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1631, 'ŝümme bedâ lehüm mim ba\\`di mâ raevu-l\'âyâti leyescününnehû ḥattâ ḥîn.');
INSERT INTO `tr_transliteration` VALUES (1632, 'vedeḫale me\\`ahü-ssicne feteyân. ḳâle eḥadühümâ innî erânî a\\`ṣiru ḫamrâ. veḳâle-l\'âḫaru innî erânî aḥmilü fevḳa ra\'sî ḫubzen te\'külu-ṭṭayru minh. nebbi\'nâ bite\'vîlih. innâ nerâke mine-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1633, 'ḳâle lâ ye\'tîkümâ ṭa\\`âmün türzeḳânihî illâ nebbe\'tükümâ bite\'vîlihî ḳable ey ye\'tiyekümâ. ẕâlikümâ mimmâ \\`allemenî rabbî. innî teraktü millete ḳavmil lâ yü\'minûne billâhi vehüm bil\'âḫirati hüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1634, 'vetteba\\`tü millete âbâî ibrâhîme veisḥâḳa veya\\`ḳûb. mâ kâne lenâ en nüşrike billâhi min şey\'. ẕâlike min faḍli-llâhi \\`aleynâ ve\\`ale-nnâsi velâkinne ekŝera-nnâsi lâ yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (1635, 'yâ ṣâḥibeyi-ssicni eerbâbüm müteferriḳûne ḫayrun emi-llâhü-lvâḥidü-lḳahhâr.');
INSERT INTO `tr_transliteration` VALUES (1636, 'mâ ta\\`büdûne min dûnihî illâ esmâen semmeytümûhâ entüm veâbâüküm mâ enzele-llâhü bihâ min sülṭân. ini-lḥukmü illâ lillâh. emera ellâ ta\\`büdû illâ iyyâh. ẕâlike-ddînü-lḳayyimü velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1637, 'yâ ṣâḥibeyi-ssicni emmâ eḥadükümâ feyesḳî rabbehû ḫamrâ. veemme-l\'âḫaru feyuṣlebü fete\'külu-ṭṭayru mir ra\'sih. ḳuḍiye-l\'emru-lleẕî fîhi testeftiyân.');
INSERT INTO `tr_transliteration` VALUES (1638, 'veḳâle lilleẕî żanne ennehû nâcim minhüme-ẕkürnî \\`inde rabbik. feensâhü-şşeyṭânü ẕikra rabbihî felebiŝe fi-ssicni biḍ\\`a sinîn.');
INSERT INTO `tr_transliteration` VALUES (1639, 'veḳâle-lmelikü innî erâ seb\\`a beḳarâtin simâniy ye\'külühünne seb\\`un \\`icâfüv veseb\\`a sümbülâtin ḫuḍriv veüḫarâ yâbisât. yâ eyyühe-lmeleü eftûnî fî ru\'yâye in küntüm lirru\'yâ ta\\`bürûn.');
INSERT INTO `tr_transliteration` VALUES (1640, 'ḳâlû aḍgâŝü aḥlâm. vemâ naḥnü bite\'vîli-l\'aḥlâmi bi\\`âlimîn.');
INSERT INTO `tr_transliteration` VALUES (1641, 'veḳâle-lleẕî necâ minhümâ veddekera ba\\`de ümmetin ene ünebbiüküm bite\'vîlihî feersilûn.');
INSERT INTO `tr_transliteration` VALUES (1642, 'yûsüfü eyyühe-ṣṣiddîḳu eftinâ fî seb\\`i beḳarâtin simâniy ye\'külühünne seb\\`un \\`icâfüv veseb\\`i sümbülâtin ḫuḍriv veüḫarâ yâbisâtil le\\`allî erci\\`u ile-nnâsi le\\`allehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1643, 'ḳâle tezra\\`ûne seb\\`a sinîne deebâ. femâ ḥaṣattüm feẕerûhü fî sümbülihî illâ ḳalîlem mimmâ te\'külûn.');
INSERT INTO `tr_transliteration` VALUES (1644, 'ŝümme ye\'tî mim ba\\`di ẕâlike seb\\`un şidâdüy ye\'külne mâ ḳaddemtüm lehünne illâ ḳalîlem mimmâ tuḥṣinûn.');
INSERT INTO `tr_transliteration` VALUES (1645, 'ŝümme ye\'tî mim ba\\`di ẕâlike \\`âmün fîhi yügâŝü-nnâsü vefîhi ya\\`ṣirûn.');
INSERT INTO `tr_transliteration` VALUES (1646, 'veḳâle-lmelikü-\'tûnî bih. felemmâ câehü-rrasûlü ḳâle-rci\\` ilâ rabbike fes\'elhü mâ bâlü-nnisveti-llâtî ḳaṭṭa\\`ne eydiyehünn. inne rabbî bikeydihinne \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1647, 'ḳâle mâ ḫaṭbükünne iẕ râvettünne yûsüfe \\`an nefsih. ḳulne ḥâşe lillâhi mâ \\`alimnâ \\`aleyhi min sû\'. ḳâleti-mraetü-l\\`azîzi-l\'âne ḥaṣḥaṣa-lḥaḳḳ. ene râvettühû \\`an nefsihî veinnehû lemine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1648, 'ẕâlike liya\\`leme ennî lem eḫunhü bilgaybi veenne-llâhe lâ yehdî keyde-lḫâinîn.');
INSERT INTO `tr_transliteration` VALUES (1649, 'vemâ überriü nefsî. inne-nnefse leemmâratüm bissûi illâ mâ raḥime rabbî. inne rabbî gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1650, 'veḳâle-lmelikü-\'tûnî bihî estaḫliṣhü linefsî. felemmâ kellemehû ḳâle inneke-lyevme ledeynâ mekînün emîn.');
INSERT INTO `tr_transliteration` VALUES (1651, 'ḳâle-c\\`alnî \\`alâ ḫazâini-l\'arḍ. innî ḥafîżun \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1652, 'vekeẕâlike mekkennâ liyûsüfe fi-l\'arḍ. yetebevveü minhâ ḥayŝü yeşâ\'. nüṣîbü biraḥmetinâ men neşâü velâ nüḍî\\`u ecra-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1653, 'veleecru-l\'âḫirati ḫayrul lilleẕîne âmenû vekânû yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (1654, 'vecâe iḫvetü yûsüfe fedeḫalû \\`aleyhi fe\\`arafehüm vehüm lehû münkirûn.');
INSERT INTO `tr_transliteration` VALUES (1655, 'velemmâ cehhezehüm bicehâzihim ḳâle-\'tûnî bieḫil leküm min ebîküm. elâ teravne ennî ûfi-lkeyle veenâ ḫayru-lmünzilîn.');
INSERT INTO `tr_transliteration` VALUES (1656, 'feil lem te\'tûnî bihî felâ keyle leküm \\`indî velâ taḳrabûn.');
INSERT INTO `tr_transliteration` VALUES (1657, 'ḳâlû senürâvidü \\`anhü ebâhü veinnâ lefâ\\`ilûn.');
INSERT INTO `tr_transliteration` VALUES (1658, 'veḳâle lifityânihi-c\\`alû biḍâ\\`atehüm fî riḥâlihim le\\`allehüm ya\\`rifûnehâ iẕe-nḳalebû ilâ ehlihim le\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1659, 'felemmâ race\\`û ilâ ebîhim ḳâlû yâ ebânâ müni\\`a minne-lkeylü feersil me\\`anâ eḫânâ nektel veinnâ lehû leḥâfiżûn.');
INSERT INTO `tr_transliteration` VALUES (1660, 'ḳâle hel âmenüküm \\`aleyhi illâ kemâ emintüküm \\`alâ eḫîhi min ḳabl. fellâhü ḫayrun ḥâfiżâ. vehüve erḥamü-rrâḥimîn.');
INSERT INTO `tr_transliteration` VALUES (1661, 'velemmâ feteḥû metâ\\`ahüm vecedû biḍâ\\`atehüm ruddet ileyhim. ḳâlû yâ ebânâ mâ nebgî. hâẕihî biḍâ\\`atünâ ruddet ileynâ. venemîru ehlenâ venaḥfeżu eḫânâ venezdâdü keyle be\\`îr. ẕâlike keylüy yesîr.');
INSERT INTO `tr_transliteration` VALUES (1662, 'ḳâle len ürsilehû me\\`aküm ḥattâ tü\'tûni mevŝiḳam mine-llâhi lete\'tünnenî bihî illâ ey yüḥâṭa biküm. felemmâ âtevhü mevŝiḳahüm ḳâle-llâhü \\`alâ mâ neḳûlü vekîl.');
INSERT INTO `tr_transliteration` VALUES (1663, 'veḳâle yâ benîye lâ tedḫulû mim bâbiv vâḥidiv vedḫulû min ebvâbim müteferriḳah. vemâ ugnî \\`anküm mine-llâhi min şey\'. ini-lḥukmü illâ lillâh. \\`aleyhi tevekkelt. ve\\`aleyhi felyetevekkeli-lmütevekkilûn.');
INSERT INTO `tr_transliteration` VALUES (1664, 'velemmâ deḫalû min ḥayŝü emerahüm ebûhüm. mâ kâne yugnî \\`anhüm mine-llâhi min şey\'in illâ ḥâceten fî nefsi ya\\`ḳûbe ḳaḍâhâ. veinnehû leẕû \\`ilmil limâ \\`allemnâhü velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1665, 'velemmâ deḫalû \\`alâ yûsüfe âvâ ileyhi eḫâhü ḳâle innî ene eḫûke felâ tebteis bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1666, 'felemmâ cehhezehüm bicehâzihim ce\\`ale-ssiḳâyete fî raḥli eḫîhi ŝümme eẕẕene müeẕẕinün eyyetühe-l\\`îru inneküm lesâriḳûn.');
INSERT INTO `tr_transliteration` VALUES (1667, 'ḳâlû veaḳbelû \\`aleyhim mâẕâ tefḳidûn.');
INSERT INTO `tr_transliteration` VALUES (1668, 'ḳâlû nefḳidü ṣuvâ\\`a-lmeliki velimen câe bihî ḥimlü be\\`îriv veenâ bihî za\\`îm.');
INSERT INTO `tr_transliteration` VALUES (1669, 'ḳâlû tellâhi leḳad \\`alimtüm mâ ci\'nâ linüfside fi-l\'arḍi vemâ künnâ sâriḳîn.');
INSERT INTO `tr_transliteration` VALUES (1670, 'ḳâlû femâ cezâühû in küntüm kâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (1671, 'ḳâlû cezâühû mev vucide fî raḥlihî fehüve cezâüh. keẕâlike neczi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1672, 'febedee biev\\`iyetihim ḳable vi\\`âi eḫîhi ŝümme-staḫracehâ miv vi\\`âi eḫîh. keẕâlike kidnâ liyûsüf. mâ kâne liye\'ḫuẕe eḫâhü fî dîni-lmeliki illâ ey yeşâe-llâh. nerfe\\`u deracâtim men neşâ\'. vefevḳa külli ẕî \\`ilmin \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (1673, 'ḳâlû iy yesriḳ feḳad seraḳa eḫul lehû min ḳabl. feeserrahâ yûsüfü fî nefsihî velem yübdihâ lehüm ḳâle entüm şerrum mekânâ. vellâhü a\\`lemü bimâ teṣifûn.');
INSERT INTO `tr_transliteration` VALUES (1674, 'ḳâlû yâ eyyühe-l\\`azîzü inne lehû eben şeyḫan kebîran feḫuẕ eḥadenâ mekâneh. innâ nerâke mine-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1675, 'ḳâle me\\`âẕe-llâhi en ne\'ḫuẕe illâ mev vecednâ metâ\\`anâ \\`indehû innâ iẕel leżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (1676, 'felemme-stey\'esû minhü ḫaleṣû neciyyâ. ḳâle kebîruhüm elem ta\\`lemû enne ebâküm ḳad eḫaẕe \\`aleyküm mevŝiḳam mine-llâhi vemin ḳablü mâ ferraṭtüm fî yûsüf. felen ebraḥa-l\'arḍa ḥattâ ye\'ẕene lî ebî ev yaḥküme-llâhü lî. vehüve ḫayru-lḥâkimîn.');
INSERT INTO `tr_transliteration` VALUES (1677, 'irci\\`û ilâ ebîküm feḳûlû yâ ebânâ inne-bneke seraḳ. vemâ şehidnâ illâ bimâ \\`alimnâ vemâ künnâ lilgaybi ḥâfiżîn.');
INSERT INTO `tr_transliteration` VALUES (1678, 'ves\'eli-lḳaryete-lletî künnâ fîhâ vel\\`îra-lletî aḳbelnâ fîhâ. veinnâ leṣâdiḳûn.');
INSERT INTO `tr_transliteration` VALUES (1679, 'ḳâle bel sevvelet leküm enfüsüküm emrâ. feṣabrun cemîl. \\`asa-llâhü ey ye\'tiyenî bihim cemî\\`â. innehû hüve-l\\`alîmü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1680, 'vetevellâ \\`anhüm veḳâle yâ esefâ \\`alâ yûsüfe vebyeḍḍat \\`aynâhü mine-lḥuzni fehüve keżîm.');
INSERT INTO `tr_transliteration` VALUES (1681, 'ḳâlû tellâhi tefteü teẕküru yûsüfe ḥattâ tekûne ḥaraḍan ev tekûne mine-lhâlikîn.');
INSERT INTO `tr_transliteration` VALUES (1682, 'ḳâle innemâ eşkû beŝŝî veḥuznî ile-llâhi vea\\`lemü mine-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1683, 'yâ benîye-ẕhebû feteḥassesû miy yûsüfe veeḫîhi velâ tey\'esû mir ravḥi-llâh. innehû lâ yey\'esü mir ravḥi-llâhi ille-lḳavmü-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1684, 'felemmâ deḫalû \\`aleyhi ḳâlû yâ eyyühe-l\\`azîzü messenâ veehlene-ḍḍurru veci\'nâ bibiḍâ\\`atim müzcâtin feevfi lene-lkeyle veteṣaddaḳ \\`aleynâ. inne-llâhe yeczi-lmüteṣaddiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1685, 'ḳâle hel \\`alimtüm mâ fe\\`altüm biyûsüfe veeḫîhi iẕ entüm câhilûn.');
INSERT INTO `tr_transliteration` VALUES (1686, 'ḳâlû einneke leente yûsüf. ḳâle ene yûsüfü vehâẕâ eḫî. ḳad menne-llâhü \\`aleynâ. innehû mey yetteḳi veyaṣbir feinne-llâhe lâ yüḍî\\`u ecra-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (1687, 'ḳâlû tellâhi leḳad âŝerake-llâhü \\`aleynâ vein künnâ leḫâṭiîn.');
INSERT INTO `tr_transliteration` VALUES (1688, 'ḳâle lâ teŝrîbe \\`aleykümü-lyevm. yagfiru-llâhü leküm. vehüve erḥamü-rrâḥimîn.');
INSERT INTO `tr_transliteration` VALUES (1689, 'iẕhebû biḳamîṣî hâẕâ feelḳûhü \\`alâ vechi ebî ye\'ti beṣîrâ. ve\'tûnî biehliküm ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (1690, 'velemmâ feṣaleti-l\\`îru ḳâle ebûhüm innî leecidü rîḥa yûsüfe levlâ en tüfennidûn.');
INSERT INTO `tr_transliteration` VALUES (1691, 'ḳâlû tellâhi inneke lefî ḍalâlike-lḳadîm.');
INSERT INTO `tr_transliteration` VALUES (1692, 'felemmâ en câe-lbeşîru elḳâhü \\`alâ vechihî fertedde beṣîrâ. ḳâle elem eḳul leküm innî a\\`lemü mine-llâhi mâ lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1693, 'ḳâlû yâ ebâne-stagfir lenâ ẕünûbenâ innâ künnâ ḫâṭiîn.');
INSERT INTO `tr_transliteration` VALUES (1694, 'ḳâle sevfe estagfiru leküm rabbî. innehû hüve-lgafûru-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (1695, 'felemmâ deḫalû \\`alâ yûsüfe âvâ ileyhi ebeveyhi veḳâle-dḫulû miṣra in şâe-llâhü âminîn.');
INSERT INTO `tr_transliteration` VALUES (1696, 'verafe\\`a ebeveyhi \\`ale-l\\`arşi veḫarrû lehû süccedâ. veḳâle yâ ebeti hâẕâ te\'vîlü ru\'yâye min ḳabl. ḳad ce\\`alehâ rabbî ḥaḳḳâ. veḳad aḥsene bî iẕ aḫracenî mine-ssicni vecâe biküm mine-lbedvi mim ba\\`di en nezega-şşeyṭânü beynî vebeyne iḫvetî. inne rabbî leṭîfül limâ yeşâ\'. innehû hüve-l\\`alîmü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1697, 'rabbi ḳad âteytenî mine-lmülki ve\\`allemtenî min te\'vîli-l\'eḥâdîŝ. fâṭira-ssemâvâti vel\'arḍi ente veliyyî fi-ddünyâ vel\'âḫirah. teveffenî müslimev veelḥiḳnî biṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (1698, 'ẕâlike min embâi-lgaybi nûḥîhi ileyk. vemâ künte ledeyhim iẕ ecme\\`û emrahüm vehüm yemkürûn.');
INSERT INTO `tr_transliteration` VALUES (1699, 'vemâ ekŝeru-nnâsi velev ḥaraṣte bimü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (1700, 'vemâ tes\'elühüm \\`aleyhi min ecr. in hüve illâ ẕikrul lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (1701, 'vekeeyyim min âyetin fi-ssemâvâti vel\'arḍi yemürrûne \\`aleyhâ vehüm \\`anhâ mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (1702, 'vemâ yü\'minü ekŝeruhüm billâhi illâ vehüm müşrikûn.');
INSERT INTO `tr_transliteration` VALUES (1703, 'efeeminû en te\'tiyehüm gâşiyetüm min \\`aẕâbi-llâhi ev te\'tiyehümü-ssâ\\`atü bagtetev vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (1704, 'ḳul hâẕihî sebîlî ed\\`û ile-llâhi \\`alâ beṣîratin ene vemeni-ttebe\\`anî. vesübḥâne-llâhi vemâ ene mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (1705, 'vemâ erselnâ min ḳablike illâ ricâlen nûḥî ileyhim min ehli-lḳurâ. efelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne \\`âḳibetü-lleẕîne min ḳablihim. veledâru-l\'âḫirati ḫayrul lilleẕîne-tteḳav. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1706, 'ḥattâ iẕe-stey\'ese-rrusülü veżannû ennehüm ḳad küẕibû câehüm naṣrunâ fenücciye men neşâ\'. velâ yüraddü be\'sünâ \\`ani-lḳavmi-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1707, 'leḳad kâne fî ḳaṣaṣihim \\`ibratül liüli-l\'elbâb. mâ kâne ḥadîŝey yüfterâ velâkin taṣdîḳa-lleẕî beyne yedeyhi vetefṣîle külli şey\'iv vehüdev veraḥmetel liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1708, 'elif-lâm-mîm-râ. tilke âyâtü-lkitâb. velleẕî ünzile ileyke mir rabbike-lḥaḳḳu velâkinne ekŝera-nnâsi lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1709, 'allâhü-lleẕî rafe\\`a-ssemâvâti bigayri \\`amedin teravnehâ ŝümme-stevâ \\`ale-l\\`arşi veseḫḫara-şşemse velḳamer. küllüy yecrî liecelim müsemmâ. yüdebbiru-l\'emra yüfeṣṣilü-l\'âyâti le\\`alleküm biliḳâi rabbiküm tûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (1710, 'vehüve-lleẕî medde-l\'arḍa vece\\`ale fîhâ ravâsiye veenhârâ. vemin külli-ŝŝemerâti ce\\`ale fîhâ zevceyni-ŝneyni yugşi-lleyle-nnehâr. inne fî ẕâlike leâyâtil liḳavmiy yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1711, 'vefi-l\'arḍi ḳiṭa\\`um mütecâvirâtüv vecennâtüm min a\\`nâbiv vezer\\`uv veneḫîlün ṣinvânüv vegayru ṣinvâniy yüsḳâ bimâiv vâḥid. venüfeḍḍilü ba\\`ḍahâ \\`alâ ba\\`ḍin fi-l\'ükül. inne fî ẕâlike leâyâtil liḳavmiy ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1712, 'vein ta\\`ceb fe\\`acebün ḳavlühüm eiẕâ künnâ türâben einnâ lefî ḫalḳin cedîd. ülâike-lleẕîne keferû birabbihim. veülâike-l\'aglâlü fî a\\`nâḳihim. veülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (1713, 'veyesta\\`cilûneke bisseyyieti ḳable-lḥaseneti veḳad ḫalet min ḳablihimü-lmeŝülât. veinne rabbeke leẕû magfiratil linnâsi \\`alâ żulmihim. veinne rabbeke leşedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (1714, 'veyeḳûlü-lleẕîne keferû levlâ ünzile \\`aleyhi âyetüm mir rabbih. innemâ ente münẕiruv velikülli ḳavmin hâd.');
INSERT INTO `tr_transliteration` VALUES (1715, 'allâhü ya\\`lemü mâ taḥmilü küllü ünŝâ vemâ tegîḍu-l\'erḥâmü vemâ tezdâd. veküllü şey\'in \\`indehû bimiḳdâr.');
INSERT INTO `tr_transliteration` VALUES (1716, '\\`âlimü-lgaybi veşşehâdeti-lkebîru-lmüte\\`âl.');
INSERT INTO `tr_transliteration` VALUES (1717, 'sevâüm minküm men eserra-lḳavle vemen cehera bihî vemen hüve müstaḫfim billeyli vesâribüm binnehâr.');
INSERT INTO `tr_transliteration` VALUES (1718, 'lehû mü\\`aḳḳibâtüm mim beyni yedeyhi vemin ḫalfihî yaḥfeżûnehû min emri-llâh. inne-llâhe lâ yügayyiru mâ biḳavmin ḥattâ yügayyirû mâ bienfüsihim. veiẕâ erâde-llâhü biḳavmin sûen felâ meradde leh. vemâ lehüm min dûnihî miv vâl.');
INSERT INTO `tr_transliteration` VALUES (1719, 'hüve-lleẕî yürîkümü-lberḳa ḫavfev veṭame\\`av veyünşiü-sseḥâbe-ŝŝiḳâl.');
INSERT INTO `tr_transliteration` VALUES (1720, 'veyüsebbiḥu-rra\\`dü biḥamdihî velmelâiketü min ḫîfetih. veyürsilu-ṣṣavâ\\`iḳa feyüṣîbü bihâ mey yeşâü vehüm yücâdilûne fi-llâh. vehüve şedîdü-lmiḥâl.');
INSERT INTO `tr_transliteration` VALUES (1721, 'lehû da\\`vetü-lḥaḳḳ. velleẕîne yed\\`ûne min dûnihî lâ yestecîbûne lehüm bişey\'in illâ kebâsiṭi keffeyhi ile-lmâi liyeblüga fâhü vemâ hüve bibâligih. vemâ dü\\`âü-lkâfirîne illâ fî ḍalâl.');
INSERT INTO `tr_transliteration` VALUES (1722, 'velillâhi yescüdü men fi-ssemâvâti vel\'arḍi ṭav\\`av vekerhev veżilâlühüm bilgudüvvi vel\'eṣâl.');
INSERT INTO `tr_transliteration` VALUES (1723, 'ḳul mer rabbü-ssemâvâti vel\'arḍ. ḳuli-llâh. ḳul efetteḫaẕtüm min dûnihî evliyâe lâ yemlikûne lienfüsihim nef\\`av velâ ḍarrâ. ḳul hel yestevi-l\'a\\`mâ velbeṣîru em hel testevi-żżulümâtü vennûr. em ce\\`alû lillâhi şürakâe ḫaleḳû keḫalḳihî feteşâbehe-lḫalḳu \\`aleyhim. ḳuli-llâhü ḫâliḳu külli şey\'iv vehüve-lvâḥidü-lḳahhâr.');
INSERT INTO `tr_transliteration` VALUES (1724, 'enzele mine-ssemâi mâen fesâlet evdiyetüm biḳaderihâ faḥtemele-sseylü zebeder râbiyâ. vemimmâ yûḳidûne \\`aleyhi fi-nnâri-btigâe ḥilyetin ev metâ\\`in zebedüm miŝlüh. keẕâlike yaḍribü-llâhü-lḥaḳḳa velbâṭil. feemme-zzebedü feyeẕhebü cüfââ. veemmâ mâ yenfe\\`u-nnâse feyemküŝü fi-l\'arḍ. keẕâlike yaḍribü-llâhü-l\'emŝâl.');
INSERT INTO `tr_transliteration` VALUES (1725, 'lilleẕîne-stecâbû lirabbihimü-lḥusnâ. velleẕîne lem yestecîbû lehû lev enne lehüm mâ fi-l\'arḍi cemî\\`av vemiŝlehû me\\`ahû leftedev bih. ülâike lehüm sûü-lḥisâbi veme\'vâhüm cehennem. vebi\'se-lmihâd.');
INSERT INTO `tr_transliteration` VALUES (1726, 'efemey ya\\`lemü ennemâ ünzile ileyke mir rabbike-lḥaḳḳu kemen hüve a\\`mâ. innemâ yeteẕekkeru ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (1727, 'elleẕîne yûfûne bi\\`ahdi-llâhi velâ yenḳuḍûne-lmîŝâḳa.');
INSERT INTO `tr_transliteration` VALUES (1728, 'velleẕîne yeṣilûne mâ emera-llâhü bihî ey yûṣale veyaḫşevne rabbehüm veyeḫâfûne sûe-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (1729, 'velleẕîne ṣaberü-btigâe vechi rabbihim veeḳâmu-ṣṣalâte veenfeḳû mimmâ razaḳnâhüm sirrav ve\\`alâniyetev veyedraûne bilḥaseneti-sseyyiete ülâike lehüm \\`uḳbe-ddâr.');
INSERT INTO `tr_transliteration` VALUES (1730, 'cennâtü \\`adniy yedḫulûnehâ vemen ṣaleḥa min âbâihim veezvâcihim veẕürriyyâtihim velmelâiketü yedḫulûne \\`aleyhim min külli bâb.');
INSERT INTO `tr_transliteration` VALUES (1731, 'selâmün \\`aleyküm bimâ ṣabertüm feni\\`me \\`uḳbe-ddâr.');
INSERT INTO `tr_transliteration` VALUES (1732, 'velleẕîne yenḳuḍûne \\`ahde-llâhi mim ba\\`di mîŝâḳihî veyaḳṭa\\`ûne mâ emera-llâhü bihî ey yûṣale veyüfsidûne fi-l\'arḍi ülâike lehümü-lla\\`netü velehüm sûü-ddâr.');
INSERT INTO `tr_transliteration` VALUES (1733, 'allâhü yebsüṭu-rrizḳa limey yeşâü veyaḳdir. veferiḥû bilḥayâti-ddünyâ. veme-lḥayâtü-ddünyâ fi-l\'âḫirati illâ metâ\\`.');
INSERT INTO `tr_transliteration` VALUES (1734, 'veyeḳûlü-lleẕîne keferû levlâ ünzile \\`aleyhi âyetüm mir rabbih. ḳul inne-llâhe yüḍillü mey yeşâü veyehdî ileyhi men enâb.');
INSERT INTO `tr_transliteration` VALUES (1735, 'elleẕîne âmenû vetaṭmeinnü ḳulûbühüm biẕikri-llâh. elâ biẕikri-llâhi taṭmeinnü-lḳulûb.');
INSERT INTO `tr_transliteration` VALUES (1736, 'elleẕîne âmenû ve\\`amilu-ṣṣâliḥâti ṭûbâ lehüm veḥusnü meâb.');
INSERT INTO `tr_transliteration` VALUES (1737, 'keẕâlike erselnâke fî ümmetin ḳad ḫalet min ḳablihâ ümemül litetlüve \\`aleyhimü-lleẕî evḥaynâ ileyke vehüm yekfürûne birraḥmân. ḳul hüve rabbî lâ ilâhe illâ hû. \\`aleyhi tevekkeltü veileyhi metâb.');
INSERT INTO `tr_transliteration` VALUES (1738, 'velev enne ḳur\'ânen süyyirat bihi-lcibâlü ev ḳuṭṭi\\`at bihi-l\'arḍu ev küllime bihi-lmevtâ. bel lillâhi-l\'emru cemî\\`â. efelem yey\'esi-lleẕîne âmenû el lev yeşâü-llâhü lehede-nnâse cemî\\`â. velâ yezâlü-lleẕîne keferû tüṣîbühüm bimâ ṣane\\`û ḳâri\\`atün ev teḥullü ḳarîbem min dârihim ḥattâ ye\'tiye va\\`dü-llâh. inne-llâhe lâ yuḫlifü-lmî\\`âd.');
INSERT INTO `tr_transliteration` VALUES (1739, 'veleḳadi-stühzie birusülim min ḳablike feemleytü lilleẕîne keferû ŝümme eḫaẕtühüm. fekeyfe kâne \\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (1740, 'efemen hüve ḳâimün \\`alâ külli nefsim bimâ kesebet. vece\\`alû lillâhi şürakâ\'. ḳul semmûhüm. em tünebbiûnehû bimâ lâ ya\\`lemü fi-l\'arḍi em biżâhirim mine-lḳavl. bel züyyine lilleẕîne keferû mekruhüm veṣuddû \\`ani-ssebîl. vemey yuḍlili-llâhü femâ lehû min hâd.');
INSERT INTO `tr_transliteration` VALUES (1741, 'lehüm \\`aẕâbün fi-lḥayâti-ddünyâ vele\\`aẕâbü-l\'âḫirati eşeḳḳ. vemâ lehüm mine-llâhi miv vâḳ.');
INSERT INTO `tr_transliteration` VALUES (1742, 'meŝelü-lcenneti-lletî vu\\`ide-lmütteḳûn. tecrî min taḥtihe-l\'enhâr. ükülühâ dâimüv veżillühâ. tilke \\`uḳbe-lleẕîne-tteḳav. ve\\`uḳbe-lkâfirîne-nnâr.');
INSERT INTO `tr_transliteration` VALUES (1743, 'velleẕîne âteynâhümü-lkitâbe yefraḥûne bimâ ünzile ileyke vemine-l\'aḥzâbi mey yünkiru ba\\`ḍah. ḳul innemâ ümirtü en a\\`büde-llâhe velâ üşrike bih. ileyhi ed\\`û veileyhi meâb.');
INSERT INTO `tr_transliteration` VALUES (1744, 'vekeẕâlike enzelnâhü ḥukmen \\`arabiyyâ. veleini-tteba\\`te ehvâehüm ba\\`de mâ câeke mine-l\\`ilmi mâ leke mine-llâhi miv veliyyiv velâ vâḳ.');
INSERT INTO `tr_transliteration` VALUES (1745, 'veleḳad erselnâ rusülem min ḳablike vece\\`alnâ lehüm ezvâcev veẕürriyyeh. vemâ kâne lirasûlin ey ye\'tiye biâyetin illâ biiẕni-llâh. likülli ecelin kitâb.');
INSERT INTO `tr_transliteration` VALUES (1746, 'yemḥü-llâhü mâ yeşâü veyüŝbit. ve\\`indehû ümmü-lkitâb.');
INSERT INTO `tr_transliteration` VALUES (1747, 'veim mâ nüriyenneke ba\\`ḍa-lleẕî ne\\`idühüm ev neteveffeyenneke feinnemâ \\`aleyke-lbelâgu ve\\`aleyne-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (1748, 'evelem yerav ennâ ne\'ti-l\'arḍa nenḳuṣuhâ min aṭrâfihâ. vellâhü yaḥkümü lâ mü\\`aḳḳibe liḥukmih. vehüve serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (1749, 'veḳad mekera-lleẕîne min ḳablihim felillâhi-lmekru cemî\\`â. ya\\`lemü mâ teksibü küllü nefs. veseya\\`lemü-lküffâru limen \\`uḳbe-ddâr.');
INSERT INTO `tr_transliteration` VALUES (1750, 'veyeḳûlü-lleẕîne keferû leste mürselâ. ḳul kefâ billâhi şehîdem beynî vebeyneküm vemen \\`indehû \\`ilmü-lkitâb.');
INSERT INTO `tr_transliteration` VALUES (1751, 'elif-lâm-râ. kitâbün enzelnâhü ileyke lituḫrice-nnâse mine-żżulümâti ile-nnûri biiẕni rabbihim ilâ ṣirâṭi-l\\`azîzi-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (1752, 'allâhi-lleẕî lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. veveylül lilkâfirîne min \\`aẕâbin şedîd.');
INSERT INTO `tr_transliteration` VALUES (1753, 'elleẕîne yesteḥibbûne-lḥayâte-ddünyâ \\`ale-l\'âḫirati veyeṣuddûne \\`an sebîli-llâhi veyebgûnehâ \\`ivecâ. ülâike fî ḍalâlim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (1754, 'vemâ erselnâ mir rasûlin illâ bilisâni ḳavmihî liyübeyyine lehüm. feyüḍillü-llâhü mey yeşâü veyehdî mey yeşâ\'. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1755, 'veleḳad erselnâ mûsâ biâyâtinâ en aḫric ḳavmeke mine-żżulümâti ile-nnûri veẕekkirhüm bieyyâmi-llâh. inne fî ẕâlike leâyâtil likülli ṣabbârin şekûr.');
INSERT INTO `tr_transliteration` VALUES (1756, 'veiẕ ḳâle mûsâ liḳavmihi-ẕkürû ni\\`mete-llâhi \\`aleyküm iẕ encâküm min âli fir\\`avne yesûmûneküm sûe-l\\`aẕâbi veyüẕebbiḥûne ebnâeküm veyestaḥyûne nisâeküm. vefî ẕâliküm belâüm mir rabbiküm \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1757, 'veiẕ teeẕẕene rabbüküm lein şekertüm leezîdenneküm velein kefertüm inne \\`aẕâbî leşedîd.');
INSERT INTO `tr_transliteration` VALUES (1758, 'veḳâle mûsâ in tekfürû entüm vemen fi-l\'arḍi cemî\\`an feinne-llâhe leganiyyün ḥamîd.');
INSERT INTO `tr_transliteration` VALUES (1759, 'elem ye\'tiküm nebeü-lleẕîne min ḳabliküm ḳavmi nûḥiv ve\\`âdiv veŝemûd. velleẕîne mim ba\\`dihim. lâ ya\\`lemühüm ille-llâh. câethüm rusülühüm bilbeyyinâti feraddû eydiyehüm fî efvâhihim veḳâlû innâ kefernâ bimâ ürsiltüm bihî veinnâ lefî şekkim mimmâ ted\\`ûnenâ ileyhi mürîbün.');
INSERT INTO `tr_transliteration` VALUES (1760, 'ḳâlet rusülühüm efi-llâhi şekkün fâṭiri-ssemâvâti vel\'arḍ. yed\\`ûküm liyagfira leküm min ẕünûbiküm veyüeḫḫiraküm ilâ ecelim müsemmâ. ḳâlû in entüm illâ beşerum miŝlünâ. türîdûne en teṣuddûnâ \\`ammâ kâne ya\\`büdü âbâünâ fe\'tûnâ bisülṭânim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1761, 'ḳâlet lehüm rusülühüm in naḥnü illâ beşerum miŝlüküm velâkinne-llâhe yemünnü \\`alâ mey yeşâü min \\`ibâdih. vemâ kâne lenâ en ne\'tiyeküm bisülṭânin illâ biiẕni-llâh. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1762, 'vemâ lenâ ellâ netevekkele \\`ale-llâhi veḳad hedânâ sübülenâ. velenaṣbiranne \\`alâ mâ âẕeytümûnâ. ve\\`ale-llâhi felyetevekkeli-lmütevekkilûn.');
INSERT INTO `tr_transliteration` VALUES (1763, 'veḳâle-lleẕîne keferû lirusülihim lenuḫricenneküm min arḍinâ ev lete\\`ûdünne fî milletinâ. feevḥâ ileyhim rabbühüm lenühlikenne-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (1764, 'velenüskinennekümü-l\'arḍa mim ba\\`dihim. ẕâlike limen ḫâfe meḳâmî veḫâfe ve\\`îd.');
INSERT INTO `tr_transliteration` VALUES (1765, 'vestefteḥû veḫâbe küllü cebbârin \\`anîd.');
INSERT INTO `tr_transliteration` VALUES (1766, 'miv verâihî cehennemü veyüsḳâ mim mâin ṣadîd.');
INSERT INTO `tr_transliteration` VALUES (1767, 'yetecerra\\`uhû velâ yekâdü yüsîguhû veye\'tîhi-lmevtü min külli mekâniv vemâ hüve bimeyyit. vemiv verâihî \\`aẕâbün galîż.');
INSERT INTO `tr_transliteration` VALUES (1768, 'meŝelü-lleẕîne keferû birabbihim a\\`mâlühüm keramâdin-şteddet bihi-rrîḥu fî yevmin \\`âṣif. lâ yaḳdirûne mimmâ kesebû \\`alâ şey\'. ẕâlike hüve-ḍḍalâlü-lbe\\`îd.');
INSERT INTO `tr_transliteration` VALUES (1769, 'elem tera enne-llâhe ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳ. iy yeşe\' yüẕhibküm veye\'ti biḫalḳin cedîd.');
INSERT INTO `tr_transliteration` VALUES (1770, 'vemâ ẕâlike \\`ale-llâhi bi\\`azîz.');
INSERT INTO `tr_transliteration` VALUES (1771, 'veberazû lillâhi cemî\\`an feḳâle-ḍḍu\\`afâü lilleẕîne-stekberû innâ künnâ leküm tebe\\`an fehel entüm mugnûne \\`annâ min \\`aẕâbi-llâhi min şey\'. ḳâlû lev hedâne-llâhü lehedeynâküm. sevâün \\`aleynâ ecezi\\`nâ em ṣabernâ mâ lenâ mim meḥîṣ.');
INSERT INTO `tr_transliteration` VALUES (1772, 'veḳâle-şşeyṭânü lemmâ ḳuḍiye-l\'emru inne-llâhe ve\\`adeküm va\\`de-lḥaḳḳi veve\\`attüküm feaḫleftüküm. vemâ kâne liye \\`aleyküm min sülṭânin illâ en de\\`avtüküm festecebtüm lî. felâ telûmûnî velûmû enfüseküm. mâ ene bimuṣriḫiküm vemâ entüm bimuṣriḫîy. innî kefertü bimâ eşraktümûni min ḳabl. inne-żżâlimîne lehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1773, 'veüdḫile-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ biiẕni rabbihim. teḥiyyetühüm fîhâ selâm.');
INSERT INTO `tr_transliteration` VALUES (1774, 'elem tera keyfe ḍarabe-llâhü meŝelen kelimeten ṭayyibeten keşeceratin ṭayyibetin aṣlühâ ŝâbitüv vefer\\`uhâ fi-ssemâ\'.');
INSERT INTO `tr_transliteration` VALUES (1775, 'tü\'tî ükülehâ külle ḥînim biiẕni rabbihâ. veyaḍribü-llâhü-l\'emŝâle linnâsi le\\`allehüm yeteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1776, 'vemeŝelü kelimetin ḫabîŝetin keşeceratin ḫabîŝetin-ctüŝŝet min fevḳi-l\'arḍi mâ lehâ min ḳarâr.');
INSERT INTO `tr_transliteration` VALUES (1777, 'yüŝebbitü-llâhü-lleẕîne âmenû bilḳavli-ŝŝâbiti fi-lḥayâti-ddünyâ vefi-l\'âḫirah. veyüḍillü-llâhu-żżâlimîne veyef\\`alü-llâhü mâ yeşâ\'.');
INSERT INTO `tr_transliteration` VALUES (1778, 'elem tera ile-lleẕîne beddelû ni\\`mete-llâhi küfrav veeḥallû ḳavmehüm dâra-lbevâr.');
INSERT INTO `tr_transliteration` VALUES (1779, 'cehennem. yaṣlevnehâ. vebi\'se-lḳarâr.');
INSERT INTO `tr_transliteration` VALUES (1780, 'vece\\`alû lillâhi endâdel liyüḍillû \\`an sebîlih. ḳul temette\\`û feinne meṣîraküm ile-nnâr.');
INSERT INTO `tr_transliteration` VALUES (1781, 'ḳul li\\`ibâdiye-lleẕîne âmenû yüḳîmu-ṣṣalâte veyünfiḳû mimmâ razaḳnâhüm sirrav ve\\`alâniyetem min ḳabli ey ye\'tiye yevmül lâ bey\\`un fîhi velâ ḫilâl.');
INSERT INTO `tr_transliteration` VALUES (1782, 'allâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa veenzele mine-ssemâi mâen feaḫrace bihî mine-ŝŝemerâti rizḳal leküm. veseḫḫara lekümü-lfülke litecriye fi-lbaḥri biemrih. veseḫḫara lekümü-l\'enhâr.');
INSERT INTO `tr_transliteration` VALUES (1783, 'veseḫḫara lekümü-şşemse velḳamera dâibeyn. veseḫḫara lekümü-lleyle vennehâr.');
INSERT INTO `tr_transliteration` VALUES (1784, 'veâtâküm min külli mâ seeltümûh. vein te\\`uddû ni\\`mete-llâhi lâ tuḥṣûhâ. inne-l\'insâne leżalûmün keffâr.');
INSERT INTO `tr_transliteration` VALUES (1785, 'veiẕ ḳâle ibrâhîmü rabbi-c\\`al hâẕe-lbelede âminev vecnübnî vebenîye en na\\`büde-l\'aṣnâm.');
INSERT INTO `tr_transliteration` VALUES (1786, 'rabbi innehünne aḍlelne keŝîram mine-nnâs. femen tebi\\`anî feinnehû minnî. vemen \\`aṣânî feinneke gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (1787, 'rabbenâ innî eskentü min ẕürriyyetî bivâdin gayri ẕî zer\\`in \\`inde beytike-lmüḥarrami rabbenâ liyüḳîmu-ṣṣalâte fec\\`al ef\'idetem mine-nnâsi tehvî ileyhim verzuḳhüm mine-ŝŝemerâti le\\`allehüm yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (1788, 'rabbenâ inneke ta\\`lemü mâ nuḫfî vemâ nü\\`lin. vemâ yaḫfâ \\`ale-llâhi min şey\'in fi-l\'arḍi velâ fi-ssemâ\'.');
INSERT INTO `tr_transliteration` VALUES (1789, 'elḥamdü lillâhi-lleẕî vehebe lî \\`ale-lkiberi ismâ\\`île veisḥâḳ. inne rabbî lesemî\\`u-ddü\\`â\'.');
INSERT INTO `tr_transliteration` VALUES (1790, 'rabbi-c\\`alnî müḳîme-ṣṣalâti vemin ẕürriyyetî. rabbenâ veteḳabbel dü\\`â\'.');
INSERT INTO `tr_transliteration` VALUES (1791, 'rabbene-gfir lî velivâlideyye velilmü\'minîne yevme yeḳûmü-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (1792, 'velâ taḥsebenne-llâhe gâfilen \\`ammâ ya\\`melu-żżâlimûne. innemâ yü\'eḫḫiruhüm liyevmin teşḫaṣu fîhi-l\'ebṣâr.');
INSERT INTO `tr_transliteration` VALUES (1793, 'mühti\\`îne muḳni\\`î ruûsihim lâ yerteddü ileyhim ṭarfühüm. veef\'idetühüm hevâ\'.');
INSERT INTO `tr_transliteration` VALUES (1794, 'veenẕiri-nnâse yevme ye\'tîhimü-l\\`aẕâbü feyeḳûlü-lleẕîne żalemû rabbenâ eḫḫirnâ ilâ ecelin ḳarîbin nücib da\\`veteke venettebi\\`i-rrusül. evelem tekûnû aḳsemtüm min ḳablü mâ leküm min zevâl.');
INSERT INTO `tr_transliteration` VALUES (1795, 'vesekentüm fî mesâkini-lleẕîne żalemû enfüsehüm vetebeyyene leküm keyfe fe\\`alnâ bihim veḍarabnâ lekümü-l\'emŝâl.');
INSERT INTO `tr_transliteration` VALUES (1796, 'veḳad mekerû mekrahüm ve\\`inde-llâhi mekruhüm. vein kâne mekruhüm litezûle minhü-lcibâl.');
INSERT INTO `tr_transliteration` VALUES (1797, 'felâ taḥsebenne-llâhe muḫlife va\\`dihî rusüleh. inne-llâhe \\`azîzün ẕü-ntiḳâm.');
INSERT INTO `tr_transliteration` VALUES (1798, 'yevme tübeddelü-l\'arḍu gayra-l\'arḍi vessemâvâtü veberazû lillâhi-lvâḥidi-lḳahhâr.');
INSERT INTO `tr_transliteration` VALUES (1799, 'vetera-lmücrimîne yevmeiẕim müḳarranîne fi-l\'aṣfâd.');
INSERT INTO `tr_transliteration` VALUES (1800, 'serâbîlühüm min ḳaṭirâniv vetagşâ vucûhehümü-nnâr.');
INSERT INTO `tr_transliteration` VALUES (1801, 'liyecziye-llâhü külle nefsim mâ kesebet. inne-llâhe serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (1802, 'hâẕâ belâgul linnâsi veliyünẕerû bihî veliya\\`lemû ennemâ hüve ilâhüv vâḥidüv veliyeẕẕekkera ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (1803, 'elif-lâm-râ. tilke âyâtü-lkitâbi veḳur\'ânim mübîn.');
INSERT INTO `tr_transliteration` VALUES (1804, 'rubemâ yeveddü-lleẕîne keferû lev kânû müslimîn.');
INSERT INTO `tr_transliteration` VALUES (1805, 'ẕerhüm ye\'külû veyetemette\\`û veyülhihimü-l\'emelü fesevfe ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1806, 'vemâ ehleknâ min ḳaryetin illâ velehâ kitâbüm ma\\`lûm.');
INSERT INTO `tr_transliteration` VALUES (1807, 'mâ tesbiḳu min ümmetin ecelehâ vemâ yeste\'ḫirûn.');
INSERT INTO `tr_transliteration` VALUES (1808, 'veḳâlû yâ eyyühe-lleẕî nüzzile \\`aleyhi-ẕẕikru inneke lemecnûn.');
INSERT INTO `tr_transliteration` VALUES (1809, 'lev mâ te\'tînâ bilmelâiketi in künte mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (1810, 'mâ nünezzilü-lmelâikete illâ bilḥaḳḳi vemâ kânû iẕem münżarîn.');
INSERT INTO `tr_transliteration` VALUES (1811, 'innâ naḥnü nezzelne-ẕẕikra veinnâ lehû leḥâfiżûn.');
INSERT INTO `tr_transliteration` VALUES (1812, 'veleḳad erselnâ min ḳablike fî şiye\\`i-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (1813, 'vemâ ye\'tîhim mir rasûlin illâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (1814, 'keẕâlike neslükühû fî ḳulûbi-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (1815, 'lâ yü\'minûne bihî veḳad ḫalet sünnetü-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (1816, 'velev fetaḥnâ \\`aleyhim bâbem mine-ssemâi feżallû fîhi ya\\`rucûn.');
INSERT INTO `tr_transliteration` VALUES (1817, 'leḳâlû innemâ sükkirat ebṣârunâ bel naḥnü ḳavmüm mesḥûrûn.');
INSERT INTO `tr_transliteration` VALUES (1818, 'veleḳad ce\\`alnâ fi-ssemâi bürûcev vezeyyennâhâ linnâżirîn.');
INSERT INTO `tr_transliteration` VALUES (1819, 'veḥafiżnâhâ min külli şeyṭânir racîm.');
INSERT INTO `tr_transliteration` VALUES (1820, 'illâ meni-steraḳa-ssem\\`a feetbe\\`ahû şihâbüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (1821, 'vel\'arḍa medednâhâ veelḳaynâ fîhâ ravâsiye veembetnâ fîhâ min külli şey\'im mevzûn.');
INSERT INTO `tr_transliteration` VALUES (1822, 'vece\\`alnâ leküm fîhâ me\\`âyişe vemel lestüm lehû birâziḳîn.');
INSERT INTO `tr_transliteration` VALUES (1823, 'veim min şey\'in illâ \\`indenâ ḫazâinüh. vemâ nünezzilühû illâ biḳaderim ma\\`lûm.');
INSERT INTO `tr_transliteration` VALUES (1824, 'veerselne-rriyâḥa levâḳiḥa feenzelnâ mine-ssemâi mâen feesḳaynâkümûh. vemâ entüm lehû biḫâzinîn.');
INSERT INTO `tr_transliteration` VALUES (1825, 'veinnâ lenaḥnü nuḥyî venümîtü venaḥnü-lvâriŝûn.');
INSERT INTO `tr_transliteration` VALUES (1826, 'veleḳad \\`alimne-lmüstaḳdimîne minküm veleḳad \\`alimne-lmüste\'ḫirîn.');
INSERT INTO `tr_transliteration` VALUES (1827, 'veinne rabbeke hüve yaḥşüruhüm. innehû ḥakîmün \\`alîm.');
