INSERT INTO `tr_transliteration` VALUES (1958, 'veyec\\`alûne lillâhi-lbenâti sübḥânehû velehüm mâ yeştehûn.');
INSERT INTO `tr_transliteration` VALUES (1959, 'veiẕâ büşşira eḥadühüm bil\'ünŝâ żalle vechühû müsveddev vehüve keżîm.');
INSERT INTO `tr_transliteration` VALUES (1960, 'yetevârâ mine-lḳavmi min sûi mâ büşşira bih. eyümsikühû \\`alâ hûnin em yedüssühû fi-ttürâb. elâ sâe mâ yaḥkümûn.');
INSERT INTO `tr_transliteration` VALUES (1961, 'lilleẕîne lâ yü\'minûne bil\'âḫirati meŝelü-ssev\'. velillâhi-lmeŝelü-l\'a\\`lâ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (1962, 'velev yüâḫiẕü-llâhü-nnâse biżulmihim mâ terake \\`aleyhâ min dâbbetiv velâkiy yü\'eḫḫiruhüm ilâ ecelim müsemmâ. feiẕâ câe ecelühüm lâ yeste\'ḫirûne sâ\\`atev velâ yestaḳdimûn.');
INSERT INTO `tr_transliteration` VALUES (1963, 'veyec\\`alûne lillâhi mâ yekrahûne veteṣifü elsinetühümü-lkeẕibe enne lehümü-lḥusnâ. lâ cerame enne lehümü-nnâra veennehüm müfraṭûn.');
INSERT INTO `tr_transliteration` VALUES (1964, 'tellâhi leḳad erselnâ ilâ ümemim min ḳablike fezeyyene lehümü-şşeyṭânü a\\`mâlehüm fehüve veliyyühümü-lyevme velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (1965, 'vemâ enzelnâ \\`aleyke-lkitâbe illâ litübeyyine lehümü-lleẕi-ḫtelefû fîhi vehüdev veraḥmetel liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1966, 'vellâhü enzele mine-ssemâi mâen feaḥyâ bihi-l\'arḍa ba\\`de mevtihâ. inne fî ẕâlike leâyetel liḳavmiy yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1967, 'veinne leküm fi-l\'en\\`âmi le\\`ibrah. nüsḳîküm mimmâ fî büṭûnihî mim beyni ferŝiv vedemil lebenen ḫâliṣan sâigal lişşâribîn.');
INSERT INTO `tr_transliteration` VALUES (1968, 'vemin ŝemerâti-nneḫîli vel\'a\\`nâbi tetteḫiẕûne minhü sekerav verizḳan ḥasenâ. inne fî ẕâlike leâyetel liḳavmiy ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (1969, 'veevḥâ rabbüke ile-nnaḥli eni-tteḫiẕî mine-lcibâli büyûtev vemine-şşeceri vemimmâ ya\\`rişûn.');
INSERT INTO `tr_transliteration` VALUES (1970, 'ŝümme külî min külli-ŝŝemerâti feslükî sübüle rabbiki ẕülülâ. yaḫrucü mim büṭûnihâ şerâbüm muḫtelifün elvânühû fîhi şifâül linnâs. inne fî ẕâlike leâyetel liḳavmiy yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1971, 'vellâhü ḫaleḳaküm ŝümme yeteveffâküm veminküm mey yüraddü ilâ erẕeli-l\\`umüri likey lâ ya\\`leme ba\\`de \\`ilmin şey\'â. inne-llâhe \\`alîmün ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (1972, 'vellâhü feḍḍale ba\\`ḍaküm \\`alâ ba\\`ḍin fi-rrizḳ. feme-lleẕîne füḍḍilû birâddî rizḳihim \\`alâ mâ meleket eymânühüm fehüm fîhi sevâün. efebini\\`meti-llâhi yecḥadûn.');
INSERT INTO `tr_transliteration` VALUES (1973, 'vellâhü ce\\`ale leküm min enfüsiküm ezvâcev vece\\`ale leküm min ezvâciküm benîne veḥafedetev verazeḳaküm mine-ṭṭayyibât. efebilbâṭili yü\'minûne vebini\\`meti-llâhi hüm yekfürûn.');
INSERT INTO `tr_transliteration` VALUES (1974, 'veya\\`büdûne min dûni-llâhi mâ lâ yemlikü lehüm rizḳam mine-ssemâvâti vel\'arḍi şey\'ev velâ yesteṭî\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (1975, 'felâ taḍribû lillâhi-l\'emŝâl. inne-llâhe ya\\`lemü veentüm lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1976, 'ḍarabe-llâhü meŝelen \\`abdem memlûkel lâ yaḳdiru \\`alâ şey\'iv vemer razaḳnâhü minnâ rizḳan ḥasenen fehüve yünfiḳu minhü sirrav vecehrâ. hel yestevûn. elḥamdü lillâh. bel ekŝeruhüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1977, 'veḍarabe-llâhü meŝeler racüleyni eḥadühümâ ebkemü lâ yaḳdiru \\`alâ şey\'iv vehüve kellün \\`alâ mevlâhü eynemâ yüveccihhü lâ ye\'ti biḫayr. hel yestevî hüve vemey ye\'müru bil\\`adli vehüve \\`alâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (1978, 'velillâhi gaybü-ssemâvâti vel\'arḍ. vemâ emru-ssâ\\`ati illâ kelemḥi-lbeṣari ev hüve aḳrab. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (1979, 'vellâhü aḫraceküm mim büṭûni ümmehâtiküm lâ ta\\`lemûne şey\'ev vece\\`ale lekümü-ssem\\`a vel\'ebṣâra vel\'ef\'idete le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (1980, 'elem yerav ile-ṭṭayri müseḫḫarâtin fî cevvi-ssemâ\'. mâ yümsikühünne ille-llâh. inne fî ẕâlike leâyâtil liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (1981, 'vellâhü ce\\`ale leküm mim büyûtiküm sekenev vece\\`ale leküm min cülûdi-l\'en\\`âmi büyûten testeḫiffûnehâ yevme ża\\`niküm veyevme iḳâmetiküm vemin aṣvâfihâ veevbârihâ veeş\\`ârihâ eŝâŝev vemetâ\\`an ilâ ḥîn.');
INSERT INTO `tr_transliteration` VALUES (1982, 'vellâhü ce\\`ale leküm mimmâ ḫaleḳa żilâlev vece\\`ale leküm mine-lcibâli eknânev vece\\`ale leküm serâbîle teḳîkümü-lḥarra veserabîle teḳîküm be\'seküm. keẕâlike yütimmü ni\\`metehû \\`aleyküm le\\`alleküm tüslimûn.');
INSERT INTO `tr_transliteration` VALUES (1983, 'fein tevellev feinnemâ \\`aleyke-lbelâgu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (1984, 'ya\\`rifûne ni\\`mete-llâhi ŝümme yünkirûnehâ veekŝeruhümü-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (1985, 'veyevme neb\\`aŝü min külli ümmetin şehîden ŝümme lâ yü\'ẕenü lilleẕîne keferû velâ hüm yüsta\\`tebûn.');
INSERT INTO `tr_transliteration` VALUES (1986, 'veiẕâ rae-lleẕîne żalemü-l\\`aẕâbe felâ yüḫaffefü \\`anhüm velâ hüm yünżarûn.');
INSERT INTO `tr_transliteration` VALUES (1987, 'veiẕâ rae-lleẕîne eşrakû şürakâehüm ḳâlû rabbenâ hâülâi şürakâüne-lleẕîne künnâ ned\\`û min dûnik. feelḳav ileyhimü-lḳavle inneküm lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (1988, 'veelḳav ile-llâhi yevmeiẕin-sseleme veḍalle \\`anhüm mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (1989, 'elleẕîne keferû veṣaddû \\`an sebîli-llâhi zidnâhüm \\`aẕâben fevḳa-l\\`aẕâbi bimâ kânû yüfsidûn.');
INSERT INTO `tr_transliteration` VALUES (1990, 'veyevme neb\\`aŝü fî külli ümmetin şehîden \\`aleyhim min enfüsihim veci\'nâ bike şehîden \\`alâ hâülâ\'. venezzelnâ \\`aleyke-lkitâbe tibyânel likülli şey\'iv vehüdev veraḥmetev vebüşrâ lilmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (1991, 'inne-llâhe ye\'müru bil\\`adli vel\'iḥsâni veîtâi ẕi-lḳurbâ veyenhâ \\`ani-lfaḥşâi velmünkeri velbagy. ye\\`iżuküm le\\`alleküm teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (1992, 'veevfû bi\\`ahdi-llâhi iẕâ \\`âhettüm velâ tenḳuḍü-l\'eymâne ba\\`de tevkîdihâ veḳad ce\\`altümü-llâhe \\`aleyküm kefîlâ. inne-llâhe ya\\`lemü mâ tef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (1993, 'velâ tekûnû kelletî neḳaḍat gazlehâ mim ba\\`di ḳuvvetin enkâŝâ. tetteḫiẕûne eymâneküm deḫalem beyneküm en tekûne ümmetün hiye erbâ min ümmeh. innemâ yeblûkümü-llâhü bih. veleyübeyyinenne leküm yevme-lḳiyâmeti mâ küntüm fîhi taḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (1994, 'velev şâe-llâhü lece\\`aleküm ümmetev vâḥidetev velâkiy yüḍillü mey yeşâü veyehdî mey yeşâ\'. veletüs\'elünne \\`ammâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1995, 'velâ tetteḫiẕû eymâneküm deḫalem beyneküm fetezille ḳademüm ba\\`de ŝübûtihâ veteẕûḳu-ssûe bimâ ṣadettüm \\`an sebîli-llâh. veleküm \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (1996, 'velâ teşterû bi\\`ahdi-llâhi ŝemenen ḳalîlâ. innemâ \\`inde-llâhi hüve ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (1997, 'mâ \\`indeküm yenfedü vemâ \\`inde-llâhi bâḳ. velenecziyenne-lleẕîne ṣaberû ecrahüm biaḥseni mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1998, 'men \\`amile ṣâliḥam min ẕekerin ev ünŝâ vehüve mü\'minün felenuḥyiyennehû ḥayâten ṭayyibeh. velenecziyennehüm ecrahüm biaḥseni mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (1999, 'feiẕâ ḳara\'te-lḳur\'âne feste\\`iẕ billâhi mine-şşeyṭâni-rracîm.');
INSERT INTO `tr_transliteration` VALUES (2000, 'innehû leyse lehû sülṭânün \\`ale-lleẕîne âmenû ve\\`alâ rabbihim yetevekkelûn.');
INSERT INTO `tr_transliteration` VALUES (2001, 'innemâ sülṭânühû \\`ale-lleẕîne yetevellevnehû velleẕîne hüm bihî müşrikûn.');
INSERT INTO `tr_transliteration` VALUES (2002, 'veiẕâ beddelnâ âyetem mekâne âyetiv vellâhü a\\`lemü bimâ yünezzilü ḳâlû innemâ ente müfter. bel ekŝeruhüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (2003, 'ḳul nezzelehû rûḥu-lḳudüsi mir rabbike bilḥaḳḳi liyüŝebbite-lleẕîne âmenû vehüdev vebüşrâ lilmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (2004, 'veleḳad na\\`lemü ennehüm yeḳûlûne innemâ yü\\`allimühû beşer. lisânü-lleẕî yülḥidûne ileyhi a\\`cemiyyüv vehâẕâ lisânün \\`arabiyyüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (2005, 'inne-lleẕîne lâ yü\'minûne biâyâti-llâhi lâ yehdîhimü-llâhü velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (2006, 'innemâ yefteri-lkeẕibe-lleẕîne lâ yü\'minûne biâyâti-llâh. veülâike hümü-lkâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (2007, 'men kefera billâhi mim ba\\`di îmânihî illâ men ükrihe veḳalbühû muṭmeinnüm bil\'îmâni velâkim men şeraḥa bilküfri ṣadran fe\\`aleyhim gaḍabüm mine-llâh. velehüm \\`aẕâbün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (2008, 'ẕâlike biennehümü-steḥabbü-lḥayâte-ddünyâ \\`ale-l\'âḫirati veenne-llâhe lâ yehdi-lḳavme-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (2009, 'ülâike-lleẕîne ṭabe\\`a-llâhü \\`alâ ḳulûbihim vesem\\`ihim veebṣârihim. veülâike hümü-lgâfilûn.');
INSERT INTO `tr_transliteration` VALUES (2010, 'lâ cerame ennehüm fi-l\'âḫirati hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (2011, 'ŝümme inne rabbeke lilleẕîne hâcerû mim ba\\`di mâ fütinû ŝümme câhedû veṣaberû inne rabbeke mim ba\\`dihâ legafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (2012, 'yevme te\'tî küllü nefsin tücâdilü \\`an nefsihâ vetüveffâ küllü nefsim mâ \\`amilet vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (2013, 'veḍarabe-llâhü meŝelen ḳaryeten kânet âminetem muṭmeinnetey ye\'tîhâ rizḳuhâ ragadem min külli mekânin fekeferat bien\\`umi-llâhi feeẕâḳahe-llâhü libâse-lcû\\`i velḫavfi bimâ kânû yaṣne\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2014, 'veleḳad câehüm rasûlüm minhüm fekeẕẕebûhü feeḫaẕehümü-l\\`aẕâbü vehüm żâlimûn.');
INSERT INTO `tr_transliteration` VALUES (2015, 'fekülû mimmâ razeḳakümü-llâhü ḥalâlen ṭayyibâ. veşkürû ni\\`mete-llâhi in küntüm iyyâhü ta\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (2016, 'innemâ ḥarrame \\`aleykümü-lmeytete veddeme velaḥme-lḫinzîri vemâ ühille ligayri-llâhi bih. femeni-ḍṭurra gayra bâgiv velâ \\`âdin feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (2017, 'velâ teḳûlû limâ teṣifü elsinetükümü-lkeẕibe hâẕâ ḥalâlüv vehâẕâ ḥarâmül litefterû \\`ale-llâhi-lkeẕib. inne-lleẕîne yefterûne \\`ale-llâhi-lkeẕibe lâ yüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (2018, 'metâ\\`un ḳalîl. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (2019, 've\\`ale-lleẕîne hâdû ḥarramnâ mâ ḳaṣaṣnâ \\`aleyke min ḳabl. vemâ żalemnâhüm velâkin kânû enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (2020, 'ŝümme inne rabbeke lilleẕîne \\`amilü-ssûe bicehâletin ŝümme tâbû mim ba\\`di ẕâlike veaṣleḥû inne rabbeke mim ba\\`dihâ legafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (2021, 'inne ibrâhîme kâne ümmeten ḳânitel lillâhi ḥanîfâ. velem yekü mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (2022, 'şâkiral lien\\`umih. ictebâhü vehedâhü ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (2023, 'veâteynâhü fi-ddünyâ ḥaseneh. veinnehû fi-l\'âḫirati lemine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (2024, 'ŝümme evḥaynâ ileyke eni-ttebi\\` millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (2025, 'innemâ cü\\`ile-ssebtü \\`ale-lleẕîne-ḫtelefû fîh. veinne rabbeke leyaḥkümü beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (2026, 'üd\\`u ilâ sebîli rabbike bilḥikmeti velmev\\`iżati-lḥaseneti vecâdilhüm billetî hiye aḥsen. inne rabbeke hüve a\\`lemü bimen ḍalle \\`an sebîlihî vehüve a\\`lemü bilmühtedîn.');
INSERT INTO `tr_transliteration` VALUES (2027, 'vein \\`âḳabtüm fe\\`âḳibû bimiŝli mâ \\`ûḳibtüm bih. velein ṣabertüm lehüve ḫayrul liṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (2028, 'vaṣbir vemâ ṣabruke illâ billâhi velâ taḥzen \\`aleyhim velâ tekü fî ḍayḳim mimmâ yemkürûn.');
INSERT INTO `tr_transliteration` VALUES (2029, 'inne-llâhe me\\`a-lleẕîne-tteḳav velleẕîne hüm muḥsinûn.');
INSERT INTO `tr_transliteration` VALUES (2030, 'sübḥâne-lleẕî esrâ bi\\`abdihî leylem mine-lmescidi-lḥarâmi ile-lmescidi-l\'aḳṣe-lleẕî bâraknâ ḥavlehû linüriyehû min âyâtinâ. innehû hüve-ssemî\\`u-lbeṣîr.');
INSERT INTO `tr_transliteration` VALUES (2031, 'veâteynâ mûse-lkitâbe vece\\`alnâhü hüdel libenî isrâîle ellâ tetteḫiẕû min dûnî vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (2032, 'ẕürriyyete men ḥamelnâ me\\`a nûḥ. innehû kâne \\`abden şekûrâ.');
INSERT INTO `tr_transliteration` VALUES (2033, 'veḳaḍaynâ ilâ benî isrâîle fi-lkitâbi letüfsidünne fi-l\'arḍi merrateyni veleta\\`lünne \\`ulüvven kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2034, 'feiẕâ câe va\\`dü ûlâhümâ be\\`aŝnâ \\`aleyküm \\`ibâdel lenâ ülî be\'sin şedîdin fecâsû ḫilâle-ddiyâr. vekâne va\\`dem mef\\`ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2035, 'ŝümme radednâ lekümü-lkerrate \\`aleyhim veemdednâküm biemvâliv vebenîne vece\\`alnâküm ekŝera nefîrâ.');
INSERT INTO `tr_transliteration` VALUES (2036, 'in aḥsentüm aḥsentüm lienfüsiküm vein ese\'tüm felehâ. feiẕâ câe va\\`dü-l\'âḫirati liyesûû vucûheküm veliyedḫulü-lmescide kemâ deḫalûhü evvele merrativ veliyütebbirû mâ \\`alev tetbîrâ.');
INSERT INTO `tr_transliteration` VALUES (2037, '\\`asâ rabbüküm ey yerḥameküm. vein \\`uttüm \\`udnâ. vece\\`alnâ cehenneme lilkâfirîne ḥaṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2038, 'inne hâẕe-lḳur\'âne yehdî lilletî hiye aḳvemü veyübeşşiru-lmü\'minîne-lleẕîne ya\\`melûne-ṣṣâliḥâti enne lehüm ecran kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2039, 'veenne-lleẕîne lâ yü\'minûne bil\'âḫirati a\\`tednâ lehüm \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (2040, 'veyed\\`u-l\'insânü bişşerri dü\\`âehû bilḫayr. vekâne-l\'insânü \\`acûlâ.');
INSERT INTO `tr_transliteration` VALUES (2041, 'vece\\`alne-lleyle vennehâra âyeteyni femeḥavnâ âyete-lleyli vece\\`alnâ âyete-nnehâri mübṣiratel litebtegû faḍlem mir rabbiküm velita\\`lemû \\`adede-ssinîne velḥisâb. vekülle şey\'in feṣṣalnâhü tefṣîlâ.');
INSERT INTO `tr_transliteration` VALUES (2042, 'vekülle insânin elzemnâhü ṭâirahû fî \\`unüḳih. venuḫricü lehû yevme-lḳiyâmeti kitâbey yelḳâhü menşûrâ.');
INSERT INTO `tr_transliteration` VALUES (2043, 'iḳra\' kitâbek. kefâ binefsike-lyevme \\`aleyke ḥasîbâ.');
INSERT INTO `tr_transliteration` VALUES (2044, 'meni-htedâ feinnemâ yehtedî linefsih. vemen ḍalle feinnemâ yeḍillü \\`aleyhâ. velâ teziru vâziratüv vizra uḫrâ. vemâ künnâ mü\\`aẕẕibîne ḥattâ neb\\`aŝe rasûlâ.');
INSERT INTO `tr_transliteration` VALUES (2045, 'veiẕâ eradnâ en nühlike ḳaryeten emernâ mütrafîhâ fefeseḳû fîhâ feḥaḳḳa \\`aleyhe-lḳavlü fedemmernâhâ tedmîrâ.');
INSERT INTO `tr_transliteration` VALUES (2046, 'vekem ehleknâ mine-lḳurûni mim ba\\`di nûḥ. vekefâ birabbike biẕünûbi \\`ibâdihî ḫabîram beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2047, 'men kâne yürîdü-l\\`âcilete \\`accelnâ lehû fîhâ mâ neşâü limen nürîdü ŝümme ce\\`alnâ lehû cehennem. yaṣlâhâ meẕmûmem medḥûrâ.');
INSERT INTO `tr_transliteration` VALUES (2048, 'vemen erâde-l\'âḫirate vese\\`â lehâ sa\\`yehâ vehüve mü\'minün feülâike kâne sa\\`yühüm meşkûrâ.');
INSERT INTO `tr_transliteration` VALUES (2049, 'küllen nümiddü hâülâi vehâülâi min \\`aṭâi rabbik. vemâ kâne \\`aṭâü rabbike maḥżûrâ.');
INSERT INTO `tr_transliteration` VALUES (2050, 'ünżur keyfe feḍḍalnâ ba\\`ḍahüm \\`alâ ba\\`ḍ. velel\'âḫiratü ekberu deracâtiv veekberu tefḍîlâ.');
INSERT INTO `tr_transliteration` VALUES (2051, 'lâ tec\\`al me\\`a-llâhi ilâhen âḫara fetaḳ\\`ude meẕmûmem maḫẕûlâ.');
INSERT INTO `tr_transliteration` VALUES (2052, 'veḳaḍâ rabbüke ellâ ta\\`büdû illâ iyyâhü vebilvâlideyni iḥsânâ. immâ yeblüganne \\`indeke-lkibera eḥadühümâ ev kilâhümâ felâ teḳul lehümâ üffiv velâ tenherhümâ veḳul lehümâ ḳavlen kerîmâ.');
INSERT INTO `tr_transliteration` VALUES (2053, 'vaḫfiḍ lehümâ cenâḥa-ẕẕülli mine-rraḥmeti veḳur rabbi-rḥamhümâ kemâ rabbeyânî ṣagîrâ.');
INSERT INTO `tr_transliteration` VALUES (2054, 'rabbüküm a\\`lemü bimâ fî nüfûsiküm. in tekûnû ṣâliḥîne feinnehû kâne lil\'evvâbîne gafûrâ.');
INSERT INTO `tr_transliteration` VALUES (2055, 'veâti ẕe-lḳurbâ ḥaḳḳahû velmiskîne vebne-ssebîli velâ tübeẕẕir tebẕîrâ.');
INSERT INTO `tr_transliteration` VALUES (2056, 'inne-lmübeẕẕirîne kânû iḫvâne-şşeyâṭîn. vekâne-şşeyṭânü lirabbihî kefûrâ.');
INSERT INTO `tr_transliteration` VALUES (2057, 'veimmâ tü\\`riḍanne \\`anhümü-btigâe raḥmetim mir rabbike tercûhâ feḳul lehüm ḳavlem meysûrâ.');
INSERT INTO `tr_transliteration` VALUES (2058, 'velâ tec\\`al yedeke maglûleten ilâ \\`unüḳike velâ tebsuṭhâ külle-lbesṭi fetaḳ\\`ude melûmem maḥsûrâ.');
INSERT INTO `tr_transliteration` VALUES (2059, 'inne rabbeke yebsüṭu-rrizḳa limey yeşâü veyaḳdir. innehû kâne bi\\`ibâdihî ḫabîram beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2060, 'velâ taḳtülû evlâdeküm ḫaşyete imlâḳ. naḥnü nerzüḳuhüm veiyyâküm. inne ḳatlehüm kâne ḫiṭ\'en kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2061, 'velâ taḳrabü-zzinâ innehû kâne fâḥişeten. vesâe sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2062, 'velâ taḳtülü-nnefse-lletî ḥarrame-llâhü illâ bilḥaḳḳ. vemen ḳutile mażlûmen feḳad ce\\`alnâ liveliyyihî sülṭânen felâ yüsrif fi-lḳatl. innehû kâne menṣûrâ.');
INSERT INTO `tr_transliteration` VALUES (2063, 'velâ taḳrabû mâle-lyetîmi illâ billetî hiye aḥsenü ḥattâ yeblüga eşüddeh. veevfû bil\\`ahd. inne-l\\`ahde kâne mes\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2064, 'veevfü-lkeyle iẕâ kiltüm vezinû bilḳisṭâsi-lmüsteḳîm. ẕâlike ḫayruv veaḥsenü te\'vîlâ.');
INSERT INTO `tr_transliteration` VALUES (2065, 'velâ taḳfü mâ leyse leke bihî \\`ilm. inne-ssem\\`a velbeṣara velfüâde küllü ülâike kâne \\`anhü mes\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2066, 'velâ temşi fi-l\'arḍi meraḥâ. inneke len taḫriḳa-l\'arḍa velen teblüga-lcibâle ṭûlâ.');
INSERT INTO `tr_transliteration` VALUES (2067, 'küllü ẕâlike kâne seyyiühû \\`inde rabbike mekrûhâ.');
INSERT INTO `tr_transliteration` VALUES (2068, 'ẕâlike mimmâ evḥâ ileyke rabbüke mine-lḥikmeh. velâ tec\\`al me\\`a-llâhi ilâhen âḫara fetülḳâ fî cehenneme melûmem medḥûrâ.');
INSERT INTO `tr_transliteration` VALUES (2069, 'efeaṣfâküm rabbüküm bilbenîne vetteḫaẕe mine-lmelâiketi inâŝâ. inneküm leteḳûlûne ḳavlen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (2070, 'veleḳad ṣarrafnâ fî hâẕe-lḳur\'âni liyeẕẕekkerû. vemâ yezîdühüm illâ nüfûrâ.');
INSERT INTO `tr_transliteration` VALUES (2071, 'ḳul lev kâne me\\`ahû âlihetün kemâ yeḳûlûne iẕel lebtegav ilâ ẕi-l\\`arşi sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2072, 'sübḥânehû vete\\`âlâ \\`ammâ yeḳûlûne \\`ulüvven kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2073, 'tüsebbiḥu lehü-ssemâvâtü-sseb\\`u vel\'arḍu vemen fîhinn. veim min şey\'in illâ yüsebbiḥu biḥamdihî velâkil lâ tefḳahûne tesbîḥahüm. innehû kâne ḥalîmen gafûrâ.');
INSERT INTO `tr_transliteration` VALUES (2074, 'veiẕâ ḳara\'te-lḳur\'âne ce\\`alnâ beyneke vebeyne-lleẕîne lâ yü\'minûne bil\'âḫirati ḥicâbem mestûrâ.');
INSERT INTO `tr_transliteration` VALUES (2075, 'vece\\`alnâ \\`alâ ḳulûbihim ekinneten ey yefḳahûhü vefî âẕânihim vaḳrâ. veiẕâ ẕekerte rabbeke fi-lḳur\'âni vaḥdehû vellev \\`alâ edbârihim nüfûrâ.');
INSERT INTO `tr_transliteration` VALUES (2076, 'naḥnü a\\`lemü bimâ yestemi\\`ûne bihî iẕ yestemi\\`ûne ileyke veiẕ hüm necvâ iẕ yeḳûlu-żżâlimûne in tettebi\\`ûne illâ racülem mesḥûrâ.');
INSERT INTO `tr_transliteration` VALUES (2077, 'ünżur keyfe ḍarabû leke-l\'emŝâle feḍallû felâ yesteṭî\\`ûne sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2078, 'veḳâlû eiẕâ künnâ \\`iżâmev verufâten einnâ lemeb\\`ûŝûne ḫalḳan cedîdâ.');
INSERT INTO `tr_transliteration` VALUES (2079, 'ḳul kûnû ḥicâraten ev ḥadîdâ.');
INSERT INTO `tr_transliteration` VALUES (2080, 'ev ḫalḳam mimmâ yekbüru fî ṣudûriküm. feseyeḳûlûne mey yü\\`îdünâ. ḳuli-lleẕî feṭaraküm evvele merrah. feseyüngiḍûne ileyke ruûsehüm veyeḳûlûne metâ hû. ḳul \\`asâ ey yekûne ḳarîbâ.');
INSERT INTO `tr_transliteration` VALUES (2081, 'yevme yed\\`ûküm fetestecîbûne biḥamdihî veteżunnûne il lebiŝtüm illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2082, 'veḳul li\\`ibâdî yeḳûlü-lletî hiye aḥsen. inne-şşeyṭâne yenzegu beynehüm. inne-şşeyṭâne kâne lil\'insâni \\`adüvvem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (2083, 'rabbüküm a\\`lemü biküm. iy yeşe\' yerḥamküm ev iy yeşe\' yü\\`aẕẕibküm. vemâ erselnâke \\`aleyhim vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (2084, 'verabbüke a\\`lemü bimen fi-ssemâvâti vel\'arḍ. veleḳad feḍḍalnâ ba\\`ḍa-nnebiyyîne \\`alâ ba\\`ḍiv veâteynâ dâvûde zebûrâ.');
INSERT INTO `tr_transliteration` VALUES (2085, 'ḳuli-d\\`ü-lleẕîne za\\`amtüm min dûnihî felâ yemlikûne keşfe-ḍḍurri \\`anküm velâ taḥvîlâ.');
INSERT INTO `tr_transliteration` VALUES (2086, 'ülâike-lleẕîne yed\\`ûne yebtegûne ilâ rabbihimü-lvesîlete eyyühüm aḳrabü veyercûne raḥmetehû veyeḫâfûne \\`aẕâbeh. inne \\`aẕâbe rabbike kâne maḥẕûrâ.');
INSERT INTO `tr_transliteration` VALUES (2087, 'veim min ḳaryetin illâ naḥnü mühlikûhâ ḳable yevmi-lḳiyâmeti ev mü\\`aẕẕibûhâ \\`aẕâben şedîdâ. kâne ẕâlike fi-lkitâbi mesṭûrâ.');
INSERT INTO `tr_transliteration` VALUES (2088, 'vemâ mene\\`anâ en nürsile bil\'âyâti illâ en keẕẕebe bihe-l\'evvelûn. veâteynâ ŝemûde-nnâḳate mübṣiraten feżalemû bihâ. vemâ nürsilü bil\'âyâti illâ taḫvîfâ.');
INSERT INTO `tr_transliteration` VALUES (2089, 'veiẕ ḳulnâ leke inne rabbeke eḥâṭa binnâs. vemâ ce\\`alne-rru\'ye-lletî eraynâke illâ fitnetel linnâsi veşşecerate-lmel\\`ûnete fi-lḳur\'ân. venüḫavvifühüm femâ yezîdühüm illâ ṭugyânen kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2090, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. ḳâle eescüdü limen ḫalaḳte ṭînâ.');
INSERT INTO `tr_transliteration` VALUES (2091, 'ḳâle era\'eyteke hâẕe-lleẕî kerramte \\`aleyy. lein eḫḫarteni ilâ yevmi-lḳiyâmeti leaḥtenikenne ẕürriyyetehû illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2092, 'ḳâle-ẕheb femen tebi\\`ake minhüm feinne cehenneme cezâüküm cezâem mevfûrâ.');
INSERT INTO `tr_transliteration` VALUES (2093, 'vestefziz meni-steṭa\\`te minhüm biṣavtike veeclib \\`aleyhim biḫaylike veraclike veşârikhüm fi-l\'emvâli vel\'evlâdi ve\\`idhüm. vemâ ye\\`idühümü-şşeyṭânü illâ gurûrâ.');
INSERT INTO `tr_transliteration` VALUES (2094, 'inne \\`ibâdî leyse leke \\`aleyhim sülṭân. vekefâ birabbike vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (2095, 'rabbükümü-lleẕî yüzcî lekümü-lfülke fi-lbaḥri litebtegû min faḍlih. innehû kâne biküm raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (2096, 'veiẕâ messekümu-ḍḍurru fi-lbaḥri ḍalle men ted\\`ûne illâ iyyâh. felemmâ neccâküm ile-lberri a\\`raḍtüm. vekâne-l\'insânü kefûrâ.');
INSERT INTO `tr_transliteration` VALUES (2097, 'efeemintüm ey yaḫsife biküm cânibe-lberri ev yürsile \\`aleyküm ḥâṣiben ŝümme lâ tecidû leküm vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (2098, 'em emintüm ey yü\\`îdeküm fîhi târaten uḫrâ feyürsile \\`aleyküm ḳâṣifem mine-rrîḥi feyugriḳaküm bimâ kefertüm ŝümme lâ tecidû leküm \\`aleynâ bihî tebî\\`â.');
INSERT INTO `tr_transliteration` VALUES (2099, 'veleḳad kerramnâ benî âdeme veḥamelnâhüm fi-lberri velbaḥri verazaḳnâhüm mine-ṭṭayyibâti vefeḍḍalnâhüm \\`alâ keŝîrim mimmen ḫalaḳnâ tefḍîlâ.');
INSERT INTO `tr_transliteration` VALUES (2100, 'yevme ned\\`û külle ünâsim biimâmihim. femen ûtiye kitâbehû biyemînihî feülâike yaḳraûne kitâbehüm velâ yużlemûne fetîlâ.');
INSERT INTO `tr_transliteration` VALUES (2101, 'vemen kâne fî hâẕihî a\\`mâ fehüve fi-l\'âḫirati a\\`mâ veeḍallü sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2102, 'vein kâdû leyeftinûneke \\`ani-lleẕî evḥaynâ ileyke litefteriye \\`aleynâ gayrah. veiẕel letteḫaẕûke ḫalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2103, 'velevlâ en ŝebbetnâke leḳad kitte terkenü ileyhim şey\'en ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2104, 'iẕel leeẕaḳnâke ḍi\\`fe-lḥayâti veḍi\\`fe-lmemâti ŝümme lâ tecidü leke \\`aleynâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2105, 'vein kâdû leyestefizzûneke mine-l\'arḍi liyuḫricûke minhâ veiẕel lâ yelbeŝûne ḫilâfeke illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2106, 'sünnete men ḳad erselnâ ḳableke mir rusülinâ velâ tecidü lisünnetinâ taḥvîlâ.');
INSERT INTO `tr_transliteration` VALUES (2107, 'eḳimi-ṣṣalâte lidülûki-şşemsi ilâ gaseḳi-lleyli veḳur\'âne-lfecr. inne ḳur\'âne-lfecri kâne meşhûdâ.');
INSERT INTO `tr_transliteration` VALUES (2108, 'vemine-lleyli fetehecced bihî nâfiletel lek. \\`asâ ey yeb\\`aŝeke rabbüke meḳâmem maḥmûdâ.');
INSERT INTO `tr_transliteration` VALUES (2109, 'veḳur rabbi edḫilnî müdḫale ṣidḳiv veaḫricnî muḫrace ṣidḳiv vec\\`al lî mil ledünke sülṭânen neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2110, 'veḳul câe-lḥaḳḳu vezeheḳa-lbâṭil. inne-lbâṭile kâne zehûḳâ.');
INSERT INTO `tr_transliteration` VALUES (2111, 'venünezzilü mine-lḳur\'âni mâ hüve şifâüv veraḥmetül lilmü\'minîne velâ yezîdu-żżâlimîne illâ ḫasârâ.');
INSERT INTO `tr_transliteration` VALUES (2112, 'veiẕâ en\\`amnâ \\`ale-l\'insâni a\\`raḍa veneâ bicânibih. veiẕâ messehü-şşerru kâne yeûsâ.');
INSERT INTO `tr_transliteration` VALUES (2113, 'ḳul küllüy ya\\`melü \\`alâ şâkiletih. ferabbüküm a\\`lemü bimen hüve ehdâ sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2114, 'veyes\'elûneke \\`ani-rrûḥ. ḳuli-rrûḥu min emri rabbî vemâ ûtîtüm mine-l\\`ilmi illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2115, 'velein şi\'nâ leneẕhebenne billeẕî evḥaynâ ileyke ŝümme lâ tecidü leke bihî \\`aleynâ vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (2116, 'illâ raḥmetem mir rabbik. inne faḍlehû kâne \\`aleyke kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2117, 'ḳul leini-cteme\\`ati-l\'insü velcinnü \\`alâ ey ye\'tû bimiŝli hâẕe-lḳur\'âni lâ ye\'tûne bimiŝlihî velev kâne ba\\`ḍuhüm liba\\`ḍin żahîrâ.');
INSERT INTO `tr_transliteration` VALUES (2118, 'veleḳad ṣarrafnâ linnâsi fî hâẕe-lḳur\'âni min külli meŝel. feebâ ekŝeru-nnâsi illâ küfûrâ.');
INSERT INTO `tr_transliteration` VALUES (2119, 'veḳâlû len nü\'mine leke ḥattâ tefcüra lenâ mine-l\'arḍi yembû\\`â.');
INSERT INTO `tr_transliteration` VALUES (2120, 'ev tekûne leke cennetüm min neḫîliv ve\\`inebin fetüfeccira-l\'enhâra ḫilâlehâ tefcîrâ.');
INSERT INTO `tr_transliteration` VALUES (2121, 'ev tüsḳiṭa-ssemâe kemâ za\\`amte \\`aleynâ kisefen ev te\'tiye billâhi velmelâiketi ḳabîlâ.');
INSERT INTO `tr_transliteration` VALUES (2122, 'ev yekûne leke beytüm min zuḫrufin ev terḳâ fi-ssemâ\'. velen nü\'mine liruḳiyyike ḥattâ tünezzile \\`aleynâ kitâben naḳraüh. ḳul sübḥâne rabbî hel küntü illâ beşerar rasûlâ.');
INSERT INTO `tr_transliteration` VALUES (2123, 'vemâ mene\\`a-nnâse ey yü\'minû iẕ câehümü-lhüdâ illâ en ḳâlû ebe\\`aŝe-llâhü beşerar rasûlâ.');
INSERT INTO `tr_transliteration` VALUES (2124, 'ḳul lev kâne fi-l\'arḍi melâiketüy yemşûne muṭmeinnîne lenezzelnâ \\`aleyhim mine-ssemâi meleker rasûlâ.');
INSERT INTO `tr_transliteration` VALUES (2125, 'ḳul kefâ billâhi şehîdem beynî vebeyneküm. innehû kâne bi\\`ibâdihî ḫabîram beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2126, 'vemey yehdi-llâhü fehüve-lmühted. vemey yuḍlil felen tecide lehüm evliyâe min dûnih. venaḥşüruhüm yevme-lḳiyâmeti \\`alâ vucûhihim \\`umyev vebükmev veṣummâ. me\'vâhüm cehennem. küllemâ ḫabet zidnâhüm se\\`îrâ.');
INSERT INTO `tr_transliteration` VALUES (2127, 'ẕâlike cezâühüm biennehüm keferû biâyâtinâ veḳâlû eiẕâ künnâ \\`iżâmev verufâten einnâ lemeb\\`ûŝûne ḫalḳan cedîdâ.');
INSERT INTO `tr_transliteration` VALUES (2128, 'evelem yerav enne-llâhe-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa ḳâdirun \\`alâ ey yaḫlüḳa miŝlehüm vece\\`ale lehüm ecelel lâ raybe fîh. feebe-żżâlimûne illâ küfûrâ.');
INSERT INTO `tr_transliteration` VALUES (2129, 'ḳul lev entüm temlikûne ḫazâine raḥmeti rabbî iẕel leemsektüm ḫaşyete-l\'infâḳ. vekâne-l\'insânü ḳatûrâ.');
INSERT INTO `tr_transliteration` VALUES (2130, 'veleḳad âteynâ mûsâ tis\\`a âyâtim beyyinâtin fes\'el benî isrâîle iẕ câehüm feḳâle lehû fir\\`avnü innî leeżunnüke yâ mûsâ mesḥûrâ.');
INSERT INTO `tr_transliteration` VALUES (2131, 'ḳâle leḳad \\`alimte mâ enzele hâülâi illâ rabbü-ssemâvâti vel\'arḍi beṣâir. veinnî leeżunnüke yâ fir\\`avnü meŝbûrâ.');
INSERT INTO `tr_transliteration` VALUES (2132, 'feerâde ey yestefizzehüm mine-l\'arḍi feagraḳnâhü vemem me\\`ahû cemî\\`â.');
INSERT INTO `tr_transliteration` VALUES (2133, 'veḳulnâ mim ba\\`dihî libenî isrâîle-skünü-l\'arḍa feiẕâ câe va\\`dü-l\'âḫirati ci\'nâ biküm lefîfâ.');
INSERT INTO `tr_transliteration` VALUES (2134, 'vebilḥaḳḳi enzelnâhü vebilḥaḳḳi nezel. vemâ erselnâke illâ mübeşşirav veneẕîrâ.');
INSERT INTO `tr_transliteration` VALUES (2135, 'veḳur\'ânen feraḳnâhü litaḳraehû \\`ale-nnâsi \\`alâ mükŝiv venezzelnâhü tenzîlâ.');
INSERT INTO `tr_transliteration` VALUES (2136, 'ḳul âminû bihî ev lâ tü\'minû. inne-lleẕîne ûtü-l\\`ilme min ḳablihî iẕâ yütlâ \\`aleyhim yeḫirrûne lil\'eẕḳâni süccedâ.');
INSERT INTO `tr_transliteration` VALUES (2137, 'veyeḳûlûne sübḥâne rabbinâ in kâne va\\`dü rabbinâ lemef\\`ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2138, 'veyeḫirrûne lil\'eẕḳâni yebkûne veyezîdühüm ḫuşû\\`â.');
INSERT INTO `tr_transliteration` VALUES (2139, 'ḳuli-d\\`ü-llâhe evi-d\\`ü-rraḥmân. eyyem mâ ted\\`û felehü-l\'esmâü-lḥusnâ. velâ techer biṣalâtike velâ tüḫâfit bihâ vebtegi beyne ẕâlike sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2140, 'veḳuli-lḥamdü lillâhi-lleẕî lem yetteḫiẕ veledev velem yekül lehû şerîkün fi-lmülki velem yekül lehû veliyyüm mine-ẕẕülli vekebbirhü tekbîrâ.');
INSERT INTO `tr_transliteration` VALUES (2141, 'elḥamdü lillâhi-lleẕî enzele \\`alâ \\`abdihi-lkitâbe velem yec\\`al lehû \\`ivecâ.');
INSERT INTO `tr_transliteration` VALUES (2142, 'ḳayyimel liyünẕira be\'sen şedîdem mil ledünhü veyübeşşira-lmü\'minîne-lleẕîne ya\\`melûne-ṣṣâliḥâti enne lehüm ecran ḥasenâ.');
INSERT INTO `tr_transliteration` VALUES (2143, 'mâkiŝîne fîhi ebedâ.');
INSERT INTO `tr_transliteration` VALUES (2144, 'veyünẕira-lleẕîne ḳâlü-tteḫaẕe-llâhü veledâ.');
INSERT INTO `tr_transliteration` VALUES (2145, 'mâ lehüm bihî min \\`ilmiv velâ liâbâihim. kebürat kelimeten taḫrucü min efvâhihim. iy yeḳûlûne illâ keẕibâ.');
INSERT INTO `tr_transliteration` VALUES (2146, 'fele\\`alleke bâḫi\\`un nefseke \\`alâ âŝârihim il lem yü\'minû bihâẕe-lḥadîŝi esefâ.');
INSERT INTO `tr_transliteration` VALUES (2147, 'innâ ce\\`alnâ mâ \\`ale-l\'arḍi zînetel lehâ lineblüvehüm eyyühüm aḥsenü \\`amelâ.');
INSERT INTO `tr_transliteration` VALUES (2148, 'veinnâ lecâ\\`ilûne mâ \\`aleyhâ ṣa\\`îden cüruzâ.');
INSERT INTO `tr_transliteration` VALUES (2149, 'em ḥasibte enne aṣḥâbe-lkehfi verraḳîmi kânû min âyâtinâ \\`acebâ.');
INSERT INTO `tr_transliteration` VALUES (2150, 'iẕ eve-lfityetü ile-lkehfi feḳâlû rabbenâ âtinâ mil ledünke raḥmetev veheyyi\' lenâ min emrinâ raşedâ.');
INSERT INTO `tr_transliteration` VALUES (2151, 'feḍarabnâ \\`alâ âẕânihim fi-lkehfi sinîne \\`adedâ.');
INSERT INTO `tr_transliteration` VALUES (2152, 'ŝümme be\\`aŝnâhüm lina\\`leme eyyü-lḥizbeyni aḥṣâ limâ lebiŝû emedâ.');
INSERT INTO `tr_transliteration` VALUES (2153, 'naḥnü neḳuṣṣu \\`aleyke nebeehüm bilḥaḳḳ. innehüm fityetün âmenû birabbihim vezidnâhüm hüdâ.');
INSERT INTO `tr_transliteration` VALUES (2154, 'verabaṭnâ \\`alâ ḳulûbihim iẕ ḳâmû feḳâlû rabbünâ rabbü-ssemâvâti vel\'arḍi len ned\\`uve min dûnihî ilâhel leḳad ḳulnâ iẕen şeṭaṭâ.');
INSERT INTO `tr_transliteration` VALUES (2155, 'hâülâi ḳavmüne-tteḫaẕû min dûnihî âliheh. levlâ ye\'tûne \\`aleyhim bisülṭânim beyyin. femen ażlemü mimmeni-fterâ \\`ale-llâhi keẕibâ.');
INSERT INTO `tr_transliteration` VALUES (2156, 'veiẕi-\\`tezeltümûhüm vemâ ya\\`büdûne ille-llâhe fe\'vû ile-lkehfi yenşür leküm rabbüküm mir raḥmetihî veyüheyyi\' leküm min emriküm mirfeḳâ.');
INSERT INTO `tr_transliteration` VALUES (2157, 'vetera-şşemse iẕâ ṭale\\`at tezâveru \\`an kehfihim ẕâte-lyemîni veiẕâ garabet taḳriḍuhüm ẕâte-şşimâli vehüm fî fecvetim minh. ẕâlike min âyâti-llâh. mey yehdi-llâhü fehüve-lmühted. vemey yuḍlil felen tecide lehû veliyyem mürşidâ.');
INSERT INTO `tr_transliteration` VALUES (2158, 'vetaḥsebühüm eyḳâżav vehüm ruḳûd. venüḳallibühüm ẕâte-lyemîni veẕâte-şşimâl. vekelbühüm bâsiṭun ẕirâ\\`ayhi bilveṣîd. levi-ṭṭala\\`te \\`aleyhim levelleyte minhüm firârav velemüli\'te minhüm ru\\`bâ.');
INSERT INTO `tr_transliteration` VALUES (2159, 'vekeẕâlike be\\`aŝnâhüm liyetesâelû beynehüm. ḳâle ḳâilüm minhüm kem lebiŝtüm. ḳâlû lebiŝnâ yevmen ev ba\\`ḍa yevm. ḳâlû rabbüküm a\\`lemü bimâ lebiŝtüm feb\\`aŝû eḥadeküm biveriḳiküm hâẕihî ile-lmedîneti felyenżur eyyühâ ezkâ ṭa\\`âmen felye\'tiküm birizḳim minhü velyeteleṭṭaf velâ yüş\\`iranne biküm eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2160, 'innehüm iy yażherû \\`aleyküm yercümûküm ev yü\\`îdûküm fî milletihim velen tüfliḥû iẕen ebedâ.');
INSERT INTO `tr_transliteration` VALUES (2161, 'vekeẕâlike a\\`ŝernâ \\`aleyhim liya\\`lemû enne va\\`de-llâhi ḥaḳḳuv veenne-ssâ\\`ate lâ raybe fîhâ. iẕ yetenâza\\`ûne beynehüm emrahüm feḳâlü-bnû \\`aleyhim bünyânâ. rabbühüm a\\`lemü bihim. ḳâle-lleẕîne galebû \\`alâ emrihim lenetteḫiẕenne \\`aleyhim mescidâ.');
INSERT INTO `tr_transliteration` VALUES (2162, 'seyeḳûlûne ŝelâŝetür râbi\\`uhüm kelbühüm. veyeḳûlûne ḫamsetün sâdisühüm kelbühüm racmem bilgayb. veyeḳûlûne seb\\`atüv veŝâminühüm kelbühüm. ḳur rabbî a\\`lemü bi\\`iddetihim mâ ya\\`lemühüm illâ ḳalîlün. felâ tümâri fîhim illâ mirâen żâhirâ. velâ testefti fîhim minhüm eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2163, 'velâ teḳûlenne lişey\'in innî fâ\\`ilün ẕâlike gadâ.');
INSERT INTO `tr_transliteration` VALUES (2164, 'illâ ey yeşâe-llâh. veẕkür rabbeke iẕâ nesîte veḳul \\`asâ ey yehdiyeni rabbî liaḳrabe min hâẕâ raşedâ.');
INSERT INTO `tr_transliteration` VALUES (2165, 'velebiŝû fî kehfihim ŝelâŝe mietin sinîne vezdâdû tis\\`â.');
INSERT INTO `tr_transliteration` VALUES (2166, 'ḳuli-llâhü a\\`lemü bimâ lebiŝû. lehû gaybü-ssemâvâti vel\'arḍ. ebṣir bihî veesmi\\`. mâ lehüm min dûnihî miv veliyy. velâ yüşrikü fî ḥukmihî eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2167, 'vetlü mâ ûḥiye ileyke min kitâbi rabbik. lâ mübeddile likelimâtihî velen tecide min dûnihî mülteḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2168, 'vaṣbir nefseke me\\`a-lleẕîne yed\\`ûne rabbehüm bilgadâti vel\\`aşiyyi yürîdûne vechehû velâ ta\\`dü \\`aynâke \\`anhüm. türîdü zînete-lḥayâti-ddünyâ velâ tüṭi\\` men agfelnâ ḳalbehû \\`an ẕikrinâ vettebe\\`a hevâhû vekâne emruhû füruṭâ.');
INSERT INTO `tr_transliteration` VALUES (2169, 'veḳuli-lḥaḳḳu mir rabbiküm femen şâe felyü\'miv vemen şâe felyekfür innâ a\\`tednâ liżżâlimîne nâran eḥâṭa bihim sürâdiḳuhâ. veiy yestegîŝû yügâŝû bimâin kelmühli yeşvi-lvucûh. bi\'se-şşerâb. vesâet mürtefeḳâ.');
INSERT INTO `tr_transliteration` VALUES (2170, 'inne-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti innâ lâ nüḍî\\`u ecra men aḥsene \\`amelâ.');
INSERT INTO `tr_transliteration` VALUES (2171, 'ülâike lehüm cennâtü \\`adnin tecrî min taḥtihimü-l\'enhâru yüḥallevne fîhâ min esâvira min ẕehebiv veyelbesûne ŝiyâben ḫuḍram min sündüsiv veistebraḳim müttekiîne fîhâ \\`ale-l\'erâik. ni\\`me-ŝŝevâb. veḥasünet mürtefeḳâ.');
INSERT INTO `tr_transliteration` VALUES (2172, 'vaḍrib lehüm meŝeler racüleyni ce\\`alnâ lieḥadihimâ cenneteyni min a\\`nâbiv veḥafefnâhümâ binaḫliv vece\\`alnâ beynehümâ zer\\`â.');
INSERT INTO `tr_transliteration` VALUES (2173, 'kilte-lcenneteyni âtet ükülehâ velem tażlim minhü şey\'ev vefeccernâ ḫilâlehümâ neherâ.');
INSERT INTO `tr_transliteration` VALUES (2174, 'vekâne lehû ŝemer. feḳâle liṣâḥibihî vehüve yüḥâviruhû ene ekŝeru minke mâlev vee\\`azzü neferâ.');
INSERT INTO `tr_transliteration` VALUES (2175, 'vedeḫale cennetehû vehüve żâlimül linefsih. ḳâle mâ eżunnü en tebîde hâẕihî ebedâ.');
INSERT INTO `tr_transliteration` VALUES (2176, 'vemâ eżunnü-ssâ\\`ate ḳâimetev veleir rudittü ilâ rabbî leecidenne ḫayram minhâ münḳalebâ.');
INSERT INTO `tr_transliteration` VALUES (2177, 'ḳâle lehû ṣâḥibühû vehüve yüḥâviruhû ekeferte billeẕî ḫaleḳake min türâbin ŝümme min nuṭfetin ŝümme sevvâke racülâ.');
INSERT INTO `tr_transliteration` VALUES (2178, 'lâkinnâ hüve-llâhü rabbî velâ üşrikü birabbî eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2179, 'velevlâ iẕ deḫalte cenneteke ḳulte mâ şâe-llâhü lâ ḳuvvete illâ billâh. in terani ene eḳalle minke mâlev veveledâ.');
INSERT INTO `tr_transliteration` VALUES (2180, 'fe\\`asâ rabbî ey yü\'tiyeni ḫayram min cennetike veyürsile \\`aleyhâ ḥusbânem mine-ssemâi fetuṣbiḥa ṣa\\`îden zeleḳâ.');
INSERT INTO `tr_transliteration` VALUES (2181, 'ev yuṣbiḥa mâühâ gavran felen testeṭî\\`a lehû ṭalebâ.');
INSERT INTO `tr_transliteration` VALUES (2182, 'veüḥîṭa biŝemerihî feaṣbeḥa yüḳallibü keffeyhi \\`alâ mâ enfeḳa fîhâ vehiye ḫâviyetün \\`alâ \\`urûşihâ veyeḳûlü yâ leytenî lem üşrik birabbî eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2183, 'velem tekül lehû fietüy yenṣurûnehû min dûni-llâhi vemâ kâne münteṣirâ.');
INSERT INTO `tr_transliteration` VALUES (2184, 'hünâlike-lvelâyetü lillâhi-lḥaḳḳ. hüve ḫayrun ŝevâbev veḫayrun \\`uḳbâ.');
INSERT INTO `tr_transliteration` VALUES (2185, 'vaḍrib lehüm meŝele-lḥayâti-ddünyâ kemâin enzelnâhü mine-ssemâi faḫteleṭa bihî nebâtü-l\'arḍi feaṣbeḥa heşîmen teẕrûhü-rriyâḥ. vekâne-llâhü \\`alâ külli şey\'im muḳtedirâ.');
INSERT INTO `tr_transliteration` VALUES (2186, 'elmâlü velbenûne zînetü-lḥayâti-ddünyâ. velbâḳiyâtu-ṣṣâliḥâtü ḫayrun \\`inde rabbike ŝevâbev veḫayrun emelâ.');
INSERT INTO `tr_transliteration` VALUES (2187, 'veyevme nüseyyiru-lcibâle vetera-l\'arḍa bârizetev veḥaşernâhüm felem nügâdir minhüm eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2188, 've\\`uriḍû \\`alâ rabbike ṣaffâ. leḳad ci\'tümûnâ kemâ ḫalaḳnâküm evvele merrah. bel za\\`amtüm ellen nec\\`ale leküm mev\\`idâ.');
INSERT INTO `tr_transliteration` VALUES (2189, 'vevuḍi\\`a-lkitâbü fetere-lmücrimîne müşfiḳîne mimmâ fîhi veyeḳûlûne yâ veyletenâ mâ lihâẕe-lkitâbi lâ yügâdiru ṣagîratev velâ kebîraten illâ aḥṣâhâ. vevecedû mâ \\`amilû ḥâḍirâ. velâ yażlimü rabbüke eḥadâ.');
INSERT INTO `tr_transliteration` VALUES (2190, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. kâne mine-lcinni fefeseḳa \\`an emri rabbih. efetetteḫiẕûnehû veẕürriyyetehû evliyâe min dûnî vehüm leküm \\`adüvv. bi\'se liżżâlimîne bedelâ.');
INSERT INTO `tr_transliteration` VALUES (2191, 'mâ eşhettühüm ḫalḳa-ssemâvâti vel\'arḍi velâ ḫalḳa enfüsihim. vemâ küntü mütteḫiẕe-lmüḍillîne \\`aḍudâ.');
INSERT INTO `tr_transliteration` VALUES (2192, 'veyevme yeḳûlü nâdû şürakâiye-lleẕîne za\\`amtüm fede\\`avhüm felem yestecîbû lehüm vece\\`alnâ beynehüm mevbiḳâ.');
INSERT INTO `tr_transliteration` VALUES (2193, 'verae-lmücrimûne-nnâra feżannû ennehüm müvâḳi\\`ûhâ velem yecidû \\`anhâ maṣrifâ.');
INSERT INTO `tr_transliteration` VALUES (2194, 'veleḳad ṣarrafnâ fî hâẕe-lḳur\'âni linnâsi min külli meŝel. vekâne-l\'insânü ekŝera şey\'in cedelâ.');
INSERT INTO `tr_transliteration` VALUES (2195, 'vemâ mene\\`a-nnâse ey yü\'minû iẕ câehümü-lhüdâ veyestagfirû rabbehüm illâ en te\'tiyehüm sünnetü-l\'evvelîne ev ye\'tiyehümü-l\\`aẕâbü ḳubülâ.');
INSERT INTO `tr_transliteration` VALUES (2196, 'vemâ nürsilü-lmürselîne illâ mübeşşirîne vemünẕirîn. veyücâdilü-lleẕîne keferû bilbâṭili liyüdḥiḍû bihi-lḥaḳḳa vetteḫaẕû âyâtî vemâ ünẕirû hüzüvâ.');
INSERT INTO `tr_transliteration` VALUES (2197, 'vemen ażlemü mimmen ẕükkira biâyâti rabbihî fea\\`raḍa \\`anhâ venesiye mâ ḳaddemet yedâh. innâ ce\\`alnâ \\`alâ ḳulûbihim ekinneten ey yefḳahûhü vefî âẕânihim vaḳrâ. vein ted\\`uhüm ile-lhüdâ feley yehtedû iẕen ebedâ.');
INSERT INTO `tr_transliteration` VALUES (2198, 'verabbüke-lgafûru ẕü-rraḥmeh. lev yüâḫiẕühüm bimâ kesebû le\\`accele lehümü-l\\`aẕâb. bel lehüm mev\\`idül ley yecidû min dûnihî mev\'ilâ.');
INSERT INTO `tr_transliteration` VALUES (2199, 'vetilke-lḳurâ ehleknâhüm lemmâ żalemû vece\\`alnâ limehlikihim mev\\`idâ.');
INSERT INTO `tr_transliteration` VALUES (2200, 'veiẕ ḳâle mûsâ lifetâhü lâ ebraḥu ḥattâ eblüga mecme\\`a-lbaḥrayni ev emḍiye ḥuḳubâ.');
INSERT INTO `tr_transliteration` VALUES (2201, 'felemmâ belegâ mecme\\`a beynihimâ nesiyâ ḥûtehümâ fetteḫaẕe sebîlehû fi-lbaḥri serabâ.');
INSERT INTO `tr_transliteration` VALUES (2202, 'felemmâ câvezâ ḳâle lifetâhü âtinâ gadâenâ. leḳad leḳînâ min seferinâ hâẕâ neṣabâ.');
INSERT INTO `tr_transliteration` VALUES (2203, 'ḳâle era\'eyte iẕ eveynâ ile-ṣṣaḫrati feinnî nesîtü-lḥût. vemâ ensânîhü ille-şşeyṭânü en eẕkürah. vetteḫaẕe sebîlehû fi-lbaḥr. \\`acebâ.');
INSERT INTO `tr_transliteration` VALUES (2204, 'ḳâle ẕâlike mâ künnâ nebgi. ferteddâ \\`alâ âŝârihimâ ḳaṣaṣâ.');
INSERT INTO `tr_transliteration` VALUES (2205, 'fevecedâ \\`abdem min \\`ibâdinâ âteynâhü raḥmetem min \\`indinâ ve\\`allemnâhü mil ledünnâ \\`ilmâ.');
INSERT INTO `tr_transliteration` VALUES (2206, 'ḳâle lehû mûsâ hel ettebi\\`uke \\`alâ en tü\\`allimeni mimmâ \\`ullimte ruşdâ.');
INSERT INTO `tr_transliteration` VALUES (2207, 'ḳâle inneke len testeṭî\\`a me\\`iye ṣabrâ.');
INSERT INTO `tr_transliteration` VALUES (2208, 'vekeyfe taṣbiru \\`alâ mâ lem tüḥiṭ bihî ḫubrâ.');
INSERT INTO `tr_transliteration` VALUES (2209, 'ḳâle setecidünî in şâe-llâhü ṣâbirav velâ a\\`ṣî leke emrâ.');
INSERT INTO `tr_transliteration` VALUES (2210, 'ḳâle feini-tteba\\`tenî felâ tes\'elnî \\`an şey\'in ḥattâ uḥdiŝe leke minhü ẕikrâ.');
INSERT INTO `tr_transliteration` VALUES (2211, 'fenṭaleḳâ. ḥattâ iẕâ rakibâ fi-ssefîneti ḫaraḳahâ. ḳâle eḫaraḳtehâ litugriḳa ehlehâ. leḳad ci\'te şey\'en imrâ.');
INSERT INTO `tr_transliteration` VALUES (2212, 'ḳâle elem eḳul inneke len testeṭî\\`a me\\`iye ṣabrâ.');
INSERT INTO `tr_transliteration` VALUES (2213, 'ḳâle lâ tüâḫiẕnî bimâ nesîtü velâ türhiḳnî min emrî \\`usrâ.');
INSERT INTO `tr_transliteration` VALUES (2214, 'fenṭaleḳâ. ḥattâ iẕâ leḳiyâ gulâmen feḳatelehû ḳâle eḳatelte nefsen zekiyyetem bigayri nefs. leḳad ci\'te şey\'en nükrâ.');
INSERT INTO `tr_transliteration` VALUES (2215, 'ḳâle elem eḳul leke inneke len testeṭî\\`a me\\`iye ṣabrâ.');
INSERT INTO `tr_transliteration` VALUES (2216, 'ḳâle in seeltüke \\`an şey\'im ba\\`dehâ felâ tüṣâḥibnî. ḳad belagte mil ledünnî \\`uẕrâ.');
INSERT INTO `tr_transliteration` VALUES (2217, 'fenṭaleḳâ. ḥattâ iẕâ eteyâ ehle ḳaryetin-staṭ\\`amâ ehlehâ feebev ey yüḍayyifûhümâ fevecedâ fîhâ cidâray yürîdü ey yenḳaḍḍa feeḳâmeh. ḳâle lev şi\'te letteḫaẕte \\`aleyhi ecrâ.');
INSERT INTO `tr_transliteration` VALUES (2218, 'ḳâle hâẕâ firâḳu beynî vebeynik. seünebbiüke bite\'vîli mâ lem testeṭi\\` \\`aleyhi ṣabrâ.');
INSERT INTO `tr_transliteration` VALUES (2219, 'emme-ssefînetü fekânet limesâkîne ya\\`melûne fi-lbaḥri feerattü en e\\`îbehâ vekâne verâehüm meliküy ye\'ḫuẕü külle sefînetin gaṣbâ.');
INSERT INTO `tr_transliteration` VALUES (2220, 'veemme-lgulâmü fekâne ebevâhü mü\'mineyni feḫaşînâ ey yürhiḳahümâ ṭugyânev veküfrâ.');
INSERT INTO `tr_transliteration` VALUES (2221, 'feeradnâ ey yübdilehümâ rabbühümâ ḫayram minhü zekâtev veaḳrabe ruḥmâ.');
INSERT INTO `tr_transliteration` VALUES (2222, 'veemme-lcidâru fekâne ligulâmeyni yetîmeyni fi-lmedîneti vekâne taḥtehû kenzül lehümâ vekâne ebûhümâ ṣâliḥâ. feerâde rabbüke ey yeblügâ eşüddehümâ veyestaḫricâ kenzehümâ. raḥmetem mir rabbik. vemâ fe\\`altühû \\`an emrî. ẕâlike te\'vîlü mâ lem tesṭi\\` \\`aleyhi ṣabrâ.');
INSERT INTO `tr_transliteration` VALUES (2223, 'veyes\'elûneke \\`an ẕi-lḳarneyn. ḳul seetlû \\`aleyküm minhü ẕikrâ.');
INSERT INTO `tr_transliteration` VALUES (2224, 'innâ mekkennâ lehû fi-l\'arḍi veâteynâhü min külli şey\'in sebebâ.');
INSERT INTO `tr_transliteration` VALUES (2225, 'feetbe\\`a sebebâ.');
INSERT INTO `tr_transliteration` VALUES (2226, 'ḥattâ iẕâ belega magribe-şşemsi vecedehâ tagrubü fî \\`aynin ḥamietiv vevecede \\`indehâ ḳavmâ. ḳulnâ yâ ẕe-lḳarneyni immâ en tü\\`aẕẕibe veimmâ en tetteḫiẕe fîhim ḥusnâ.');
INSERT INTO `tr_transliteration` VALUES (2227, 'ḳâle emmâ men żaleme fesevfe nü\\`aẕẕibühû ŝümme yüraddü ilâ rabbihî feyü\\`aẕẕibühû \\`aẕâben nükrâ.');
INSERT INTO `tr_transliteration` VALUES (2228, 'veemmâ men âmene ve\\`amile ṣâliḥan felehû cezâen-lḥusnâ. veseneḳûlü lehû min emrinâ yüsrâ.');
INSERT INTO `tr_transliteration` VALUES (2229, 'ŝümme etbe\\`a sebebâ.');
INSERT INTO `tr_transliteration` VALUES (2230, 'ḥattâ iẕâ belega maṭli\\`a-şşemsi vecedehâ taṭlü\\`u \\`alâ ḳavmil lem nec\\`al lehüm min dûnihâ sitrâ.');
INSERT INTO `tr_transliteration` VALUES (2231, 'keẕâlik. veḳad eḥaṭnâ bimâ ledeyhi ḫubrâ.');
INSERT INTO `tr_transliteration` VALUES (2232, 'ŝümme etbe\\`a sebebâ.');
INSERT INTO `tr_transliteration` VALUES (2233, 'ḥattâ iẕâ belegae beyne-sseddeyni vecede min dûnihimâ ḳavmel lâ yekâdûne yefḳahûne ḳavlâ.');
INSERT INTO `tr_transliteration` VALUES (2234, 'ḳâlû yâ ẕe-lḳarneyni inne ye\'cûce veme\'cûce müfsidûne fi-l\'arḍi fehel nec\\`alü leke ḫarcen \\`alâ en tec\\`ale beynenâ vebeynehüm seddâ.');
INSERT INTO `tr_transliteration` VALUES (2235, 'ḳâle mâ mekkennî fîhi rabbî ḫayrun fee\\`înûnî biḳuvvetin ec\\`al beyneküm vebeynehüm radmâ.');
INSERT INTO `tr_transliteration` VALUES (2236, 'âtûnî zübera-lḥadîd. ḥattâ iẕâ sâvâ beyne-ṣṣadefeyni ḳâle-nfüḫû. ḥattâ iẕâ ce\\`alehû nâran ḳâle âtûnî üfrig \\`aleyhi ḳiṭrâ.');
INSERT INTO `tr_transliteration` VALUES (2237, 'feme-sṭâ\\`û ey yażherûhü veme-steṭâ\\`û lehû naḳbâ.');
