INSERT INTO `tr_transliteration` VALUES (2366, 'ḳâle hiye \\`aṣây. etevekkeü \\`aleyhâ veehüşşü bihâ \\`alâ ganemî veliye fîhâ meâribü uḫrâ.');
INSERT INTO `tr_transliteration` VALUES (2367, 'ḳâle elḳihâ yâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2368, 'feelḳâhâ feiẕâ hiye ḥayyetün tes\\`â.');
INSERT INTO `tr_transliteration` VALUES (2369, 'ḳâle ḫuẕhâ velâ teḫaf. senü\\`îdühâ sîratehe-l\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2370, 'vaḍmüm yedeke ilâ cenâḥike taḫruc beyḍâe min gayri sûin âyeten uḫrâ.');
INSERT INTO `tr_transliteration` VALUES (2371, 'linüriyeke min âyâtine-lkübrâ.');
INSERT INTO `tr_transliteration` VALUES (2372, 'iẕheb ilâ fir\\`avne innehû ṭagâ.');
INSERT INTO `tr_transliteration` VALUES (2373, 'ḳâle rabbi-şraḥ lî ṣadrî.');
INSERT INTO `tr_transliteration` VALUES (2374, 'veyessir lî emrî.');
INSERT INTO `tr_transliteration` VALUES (2375, 'vaḥlül \\`uḳdetem mil lisânî.');
INSERT INTO `tr_transliteration` VALUES (2376, 'yefḳahû ḳavlî.');
INSERT INTO `tr_transliteration` VALUES (2377, 'vec\\`al lî vezîram min ehlî.');
INSERT INTO `tr_transliteration` VALUES (2378, 'hârûne eḫî.');
INSERT INTO `tr_transliteration` VALUES (2379, 'üşdüd bihî ezrî.');
INSERT INTO `tr_transliteration` VALUES (2380, 'veeşrikhü fî emrî.');
INSERT INTO `tr_transliteration` VALUES (2381, 'key nüsebbiḥake keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (2382, 'veneẕkürake keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (2383, 'inneke künte binâ beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2384, 'ḳâle ḳad ûtîte sü\'leke yâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2385, 'veleḳad menennâ \\`aleyke merraten uḫrâ.');
INSERT INTO `tr_transliteration` VALUES (2386, 'iẕ evḥaynâ ilâ ümmike mâ yûḥâ.');
INSERT INTO `tr_transliteration` VALUES (2387, 'eni-ḳẕifîhi fi-ttâbûti faḳẕifîhi fi-lyemmi felyülḳihi-lyemmü bissâḥili ye\'ḫuẕhü \\`adüvvül lî ve\\`adüvvul leh. veelḳaytü \\`aleyke meḥabbetem minnî. velituṣne\\`a \\`alâ \\`aynî.');
INSERT INTO `tr_transliteration` VALUES (2388, 'iẕ temşî uḫtüke feteḳûlü hel edüllüküm \\`alâ mey yekfülüh. feraca\\`nâke ilâ ümmike key teḳarra \\`aynühâ velâ taḥzen. veḳatelte nefsen fenecceynâke mine-lgammi vefetennâke fütûnâ. felebiŝte sinîne fî ehli medyene ŝümme ci\'te \\`alâ ḳaderiy yâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2389, 'vaṣṭana\\`tüke linefsî.');
INSERT INTO `tr_transliteration` VALUES (2390, 'iẕheb ente veeḫûke biâyâtî velâ teniyâ fî ẕikrî.');
INSERT INTO `tr_transliteration` VALUES (2391, 'iẕhebâ ilâ fir\\`avne innehû ṭagâ.');
INSERT INTO `tr_transliteration` VALUES (2392, 'feḳûlâ lehû ḳavlel leyyinel le\\`allehû yeteẕekkeru ev yaḫşâ.');
INSERT INTO `tr_transliteration` VALUES (2393, 'ḳâlâ rabbenâ innenâ neḫâfü ey yefruṭa \\`aleynâ ev ey yaṭgâ.');
INSERT INTO `tr_transliteration` VALUES (2394, 'ḳâle lâ teḫâfâ innenî me\\`akümâ esme\\`u veerâ.');
INSERT INTO `tr_transliteration` VALUES (2395, 'fe\'tiyâhü feḳûlâ innâ rasûlâ rabbike feersil me\\`anâ benî isrâîle velâ tü\\`aẕẕibhüm. ḳad ci\'nâke biâyetim mir rabbik. vesselâmü \\`alâ meni-ttebe\\`a-lhüdâ.');
INSERT INTO `tr_transliteration` VALUES (2396, 'innâ ḳad ûḥiye ileynâ enne-l\\`aẕâbe \\`alâ men keẕẕebe vetevellâ.');
INSERT INTO `tr_transliteration` VALUES (2397, 'ḳâle femer rabbükümâ yâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2398, 'ḳâle rabbüne-lleẕî a\\`ṭâ külle şey\'in ḫalḳahû ŝümme hedâ.');
INSERT INTO `tr_transliteration` VALUES (2399, 'ḳâle femâ bâlü-lḳurûni-l\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2400, 'ḳâle \\`ilmühâ \\`inde rabbî fî kitâb. lâ yeḍillü rabbî velâ yensâ.');
INSERT INTO `tr_transliteration` VALUES (2401, 'elleẕî ce\\`ale lekümü-l\'arḍa mehdev veseleke leküm fîhâ sübülev veenzele mine-ssemâi mââ. feaḫracnâ bihî ezvâcem min nebetin şettâ.');
INSERT INTO `tr_transliteration` VALUES (2402, 'külû ver\\`av en\\`âmeküm. inne fî ẕâlike leâyâtil liüli-nnühâ.');
INSERT INTO `tr_transliteration` VALUES (2403, 'minhâ ḫalaḳnâküm vefîhâ nü\\`îdüküm veminhâ nuḫricüküm târaten uḫrâ.');
INSERT INTO `tr_transliteration` VALUES (2404, 'veleḳad eraynâhü âyâtinâ küllehâ fekeẕẕebe veebâ.');
INSERT INTO `tr_transliteration` VALUES (2405, 'ḳâle eci\'tenâ lituḫricenâ min arḍinâ bisiḥrike yâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2406, 'felene\'tiyenneke bisiḥrim miŝlihî fec\\`al beynenâ vebeyneke mev\\`idel lâ nuḫlifühû naḥnü velâ ente mekânen süvâ.');
INSERT INTO `tr_transliteration` VALUES (2407, 'ḳâle mev\\`idüküm yevmü-zzîneti veey yuḥşera-nnâsü ḍuḥâ.');
INSERT INTO `tr_transliteration` VALUES (2408, 'fetevellâ fir\\`avnü feceme\\`a keydehû ŝümme etâ.');
INSERT INTO `tr_transliteration` VALUES (2409, 'ḳâle lehüm mûsâ veyleküm lâ tefterû \\`ale-llâhi keẕiben feyüsḥiteküm bi\\`aẕâb. veḳad ḫâbe meni-fterâ.');
INSERT INTO `tr_transliteration` VALUES (2410, 'fetenâza\\`û emrahüm beynehüm veeserrü-nnecvâ.');
INSERT INTO `tr_transliteration` VALUES (2411, 'ḳâlû in hâẕâni lesâḥirâni yürîdâni ey yuḫricâküm min arḍiküm bisiḥrihimâ veyeẕhebâ biṭarîḳatikümü-lmüŝlâ.');
INSERT INTO `tr_transliteration` VALUES (2412, 'feecmi\\`û keydeküm ŝümme-\'tû ṣaffâ. veḳad efleḥa-lyevme meni-sta\\`lâ.');
INSERT INTO `tr_transliteration` VALUES (2413, 'ḳâlû yâ mûsâ immâ en tülḳiye veimmâ en nekûne evvele men elḳâ.');
INSERT INTO `tr_transliteration` VALUES (2414, 'ḳâle bel elḳû. feiẕâ ḥibâlühüm ve\\`iṣiyyühüm yüḫayyelü ileyhi min siḥrihim ennehâ tes\\`â.');
INSERT INTO `tr_transliteration` VALUES (2415, 'feevcese fî nefsihî ḫîfetem mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2416, 'ḳulnâ lâ teḫaf inneke ente-l\'a\\`lâ.');
INSERT INTO `tr_transliteration` VALUES (2417, 'veelḳi mâ fî yemînike telḳaf mâ ṣane\\`û. innemâ ṣane\\`û keydü sâḥir. velâ yüfliḥu-ssâḥiru ḥayŝü etâ.');
INSERT INTO `tr_transliteration` VALUES (2418, 'feülḳiye-sseḥaratü sücceden ḳâlû âmennâ birabbi hârûne vemûsâ.');
INSERT INTO `tr_transliteration` VALUES (2419, 'ḳâle âmentüm lehû ḳable en âẕene leküm. innehû lekebîrukümü-lleẕî \\`allemekümü-ssiḥr. feleüḳaṭṭi\\`anne eydiyeküm veercüleküm min ḫilâfiv veleüṣallibenneküm fî cüẕû\\`i-nnaḫl. veleta\\`lemünne eyyünâ eşeddü \\`aẕâbev veebḳâ.');
INSERT INTO `tr_transliteration` VALUES (2420, 'ḳâlû len nü\'ŝirake \\`alâ mâ câenâ mine-lbeyyinâti velleẕî feṭaranâ faḳḍi mâ ente ḳâḍ. innemâ taḳḍî hâẕihi-lḥayâte-ddünyâ.');
INSERT INTO `tr_transliteration` VALUES (2421, 'innâ âmennâ birabbinâ liyagfira lenâ ḫaṭâyânâ vemâ ekrahtenâ \\`aleyhi mine-ssiḥr. vellâhü ḫayruv veebḳâ.');
INSERT INTO `tr_transliteration` VALUES (2422, 'innehû mey ye\'ti rabbehû mücrimen feinne lehû cehennem. lâ yemûtü fîhâ velâ yaḥyâ.');
INSERT INTO `tr_transliteration` VALUES (2423, 'vemey ye\'tihî mü\'minen ḳad \\`amile-ṣṣâliḥâti feülâike lehümü-dderacâtü-l\\`ulâ.');
INSERT INTO `tr_transliteration` VALUES (2424, 'cennâtü \\`adnin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veẕâlike cezâü men tezekkâ.');
INSERT INTO `tr_transliteration` VALUES (2425, 'veleḳad evḥaynâ ilâ mûsâ en esri bi\\`ibâdî faḍrib lehüm ṭarîḳan fi-lbaḥri yebesâ. lâ teḫâfü derakev velâ taḫşâ.');
INSERT INTO `tr_transliteration` VALUES (2426, 'feetbe\\`ahüm fir\\`avnü bicünûdihî fegaşiyehüm mine-lyemmi mâ gaşiyehüm.');
INSERT INTO `tr_transliteration` VALUES (2427, 'veeḍalle fir\\`avnü ḳavmehû vemâ hedâ.');
INSERT INTO `tr_transliteration` VALUES (2428, 'yâ benî isrâîle ḳad enceynâküm min \\`adüvviküm veve\\`adnâküm cânibe-ṭṭûri-l\'eymene venezzelnâ \\`aleykümü-lmenne vesselvâ.');
INSERT INTO `tr_transliteration` VALUES (2429, 'külû min ṭayyibâti mâ razaḳnâküm velâ taṭgav fîhi feyeḥille \\`aleyküm gaḍabî. vemey yaḥlil \\`aleyhi gaḍabî feḳad hevâ.');
INSERT INTO `tr_transliteration` VALUES (2430, 'veinnî legaffârul limen tâbe veâmene ve\\`amile ṣâliḥan ŝümme-htedâ.');
INSERT INTO `tr_transliteration` VALUES (2431, 'vemâ a\\`celeke \\`an ḳavmike yâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2432, 'ḳâle hüm ülâi \\`alâ eŝerî ve\\`aciltü ileyke rabbi literḍâ.');
INSERT INTO `tr_transliteration` VALUES (2433, 'ḳâle feinnâ ḳad fetennâ ḳavmeke mim ba\\`dike veeḍallehümü-ssâmiriyy.');
INSERT INTO `tr_transliteration` VALUES (2434, 'ferace\\`a mûsâ ilâ ḳavmihî gaḍbâne esifâ. ḳâle yâ ḳavmi elem ye\\`idküm rabbüküm va\\`den ḥasenâ. efeṭâle \\`aleykümü-l\\`ahdü em erattüm ey yeḥille \\`aleyküm gaḍabüm mir rabbiküm feaḫleftüm mev\\`idî.');
INSERT INTO `tr_transliteration` VALUES (2435, 'ḳâlû mâ aḫlefnâ mev\\`ideke bimelkinâ velâkinnâ ḥummilnâ evzâram min zîneti-lḳavmi feḳaẕefnâhâ fekeẕâlike elḳa-ssâmiriyy.');
INSERT INTO `tr_transliteration` VALUES (2436, 'feaḫrace lehüm \\`iclen cesedel lehû ḫuvârun feḳâlû hâẕâ ilâhüküm veilâhü mûsâ fenesî.');
INSERT INTO `tr_transliteration` VALUES (2437, 'efelâ yeravne ellâ yerci\\`u ileyhim ḳavlev velâ yemlikü lehüm ḍarrav velâ nef\\`â.');
INSERT INTO `tr_transliteration` VALUES (2438, 'veleḳad ḳâle lehüm hârûnü min ḳablü yâ ḳavmi innemâ fütintüm bih. veinne rabbekümü-rraḥmânü fettebi\\`ûnî veeṭî\\`û emrî.');
INSERT INTO `tr_transliteration` VALUES (2439, 'ḳâlû len nebraḥa \\`aleyhi \\`âkifîne ḥattâ yerci\\`a ileynâ mûsâ.');
INSERT INTO `tr_transliteration` VALUES (2440, 'ḳâle yâ hârûnü mâ mene\\`ake iẕ raeytehüm ḍallû.');
INSERT INTO `tr_transliteration` VALUES (2441, 'ellâ tettebi\\`an. efe\\`aṣayte emrî.');
INSERT INTO `tr_transliteration` VALUES (2442, 'ḳâle yebneümme lâ te\'ḫuẕ biliḥyetî velâ bira\'sî. innî ḫaşîtü en teḳûle ferraḳte beyne benî isrâîle velem terḳub ḳavlî.');
INSERT INTO `tr_transliteration` VALUES (2443, 'ḳâle femâ ḫaṭbüke yâ sâmiriyy.');
INSERT INTO `tr_transliteration` VALUES (2444, 'ḳâle beṣurtü bimâ lem yebṣurû bihî feḳabaḍtü ḳabḍatem min eŝeri-rrasûli fenebeẕtühâ vekeẕâlike sevvelet lî nefsî.');
INSERT INTO `tr_transliteration` VALUES (2445, 'ḳâle feẕheb feinne leke fi-lḥayâti en teḳûle lâ misâs. veinne leke mev\\`idel len tuḫlefeh. venżur ilâ ilâhike-lleẕî żalte \\`aleyhi \\`âkifâ. lenüḥarriḳannehû ŝümme lenensifennehû fi-lyemmi nesfâ.');
INSERT INTO `tr_transliteration` VALUES (2446, 'innemâ ilâhükümü-llâhü-lleẕî lâ ilâhe illâ hû. vesi\\`a külle şey\'in \\`ilmâ.');
INSERT INTO `tr_transliteration` VALUES (2447, 'keẕâlike neḳuṣṣu \\`aleyke min embâi mâ ḳad sebeḳ. veḳad âteynâke mil ledünnâ ẕikrâ.');
INSERT INTO `tr_transliteration` VALUES (2448, 'men a\\`raḍa \\`anhü feinnehû yaḥmilü yevme-lḳiyâmeti vizrâ.');
INSERT INTO `tr_transliteration` VALUES (2449, 'ḫâlidîne fîh. vesâe lehüm yevme-lḳiyâmeti ḥimlâ.');
INSERT INTO `tr_transliteration` VALUES (2450, 'yevme yünfeḫu fi-ṣṣûri venaḥşüru-lmücrimîne yevmeiẕin zürḳâ.');
INSERT INTO `tr_transliteration` VALUES (2451, 'yeteḫâfetûne beynehüm il lebiŝtüm illâ \\`aşrâ.');
INSERT INTO `tr_transliteration` VALUES (2452, 'naḥnü a\\`lemü bimâ yeḳûlûne iẕ yeḳûlü emŝelühüm ṭarîḳaten il lebiŝtüm illâ yevmâ.');
INSERT INTO `tr_transliteration` VALUES (2453, 'veyes\'elûneke \\`ani-lcibâli feḳul yensifühâ rabbî nesfâ.');
INSERT INTO `tr_transliteration` VALUES (2454, 'feyeẕeruhâ ḳâ\\`an ṣafṣafâ.');
INSERT INTO `tr_transliteration` VALUES (2455, 'lâ terâ fîhâ \\`ivecev velâ emtâ.');
INSERT INTO `tr_transliteration` VALUES (2456, 'yevmeiẕiy yettebi\\`ûne-ddâ\\`iye lâ \\`ivece leh. veḫaşe\\`ati-l\'aṣvâtü lirraḥmâni felâ tesme\\`u illâ hemsâ.');
INSERT INTO `tr_transliteration` VALUES (2457, 'yevmeiẕil lâ tenfe\\`u-şşefâ\\`atü illâ men eẕine lehü-rraḥmânü veraḍiye lehû ḳavlâ.');
INSERT INTO `tr_transliteration` VALUES (2458, 'ya\\`lemü mâ beyne eydîhim vemâ ḫalfehüm velâ yüḥîṭûne bihî \\`ilmâ.');
INSERT INTO `tr_transliteration` VALUES (2459, 've\\`aneti-lvucûhü lilḥayyi-lḳayyûm. veḳad ḫâbe men ḥamele żulmâ.');
INSERT INTO `tr_transliteration` VALUES (2460, 'vemey ya\\`mel mine-ṣṣâliḥâti vehüve mü\'minün felâ yeḫâfü żulmev velâ haḍmâ.');
INSERT INTO `tr_transliteration` VALUES (2461, 'vekeẕâlike enzelnâhü ḳur\'ânen \\`arabiyyev veṣarrafnâ fîhi mine-lve\\`îdi le\\`allehüm yetteḳûne ev yuḥdiŝü lehüm ẕikrâ.');
INSERT INTO `tr_transliteration` VALUES (2462, 'fete\\`âle-llâhü-lmelikü-lḥaḳḳ. velâ ta\\`cel bilḳur\'âni min ḳabli ey yuḳḍâ ileyke vaḥyüh. veḳur rabbi zidnî \\`ilmâ.');
INSERT INTO `tr_transliteration` VALUES (2463, 'veleḳad \\`ahidnâ ilâ âdeme min ḳablü fenesiye velem necid lehû \\`azmâ.');
INSERT INTO `tr_transliteration` VALUES (2464, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. ebâ.');
INSERT INTO `tr_transliteration` VALUES (2465, 'feḳulnâ yâ âdemü inne hâẕâ \\`adüvvül leke velizevcike felâ yuḫricennekümâ mine-lcenneti feteşḳâ.');
INSERT INTO `tr_transliteration` VALUES (2466, 'inne leke ellâ tecû\\`a fîhâ velâ ta\\`râ.');
INSERT INTO `tr_transliteration` VALUES (2467, 'veenneke lâ tażmeü fîhâ velâ taḍḥâ.');
INSERT INTO `tr_transliteration` VALUES (2468, 'fevesvese ileyhi-şşeyṭânü ḳâle yâ âdemü hel edüllüke \\`alâ şecerati-lḫuldi vemülkil lâ yeblâ.');
INSERT INTO `tr_transliteration` VALUES (2469, 'feekelâ minhâ febedet lehümâ sev\'âtühümâ veṭafiḳâ yaḫṣifâni \\`aleyhimâ miv veraḳi-lcenneh. ve\\`aṣâ âdemü rabbehû fegavâ.');
INSERT INTO `tr_transliteration` VALUES (2470, 'ŝümme-ctebâhü rabbühû fetâbe \\`aleyhi vehedâ.');
INSERT INTO `tr_transliteration` VALUES (2471, 'ḳâle-hbiṭâ minhâ cemî\\`am ba\\`ḍuküm liba\\`ḍin \\`adüvv. feimmâ ye\'tiyenneküm minnî hüden femeni-ttebe\\`a hüdâye felâ yeḍillü velâ yeşḳâ.');
INSERT INTO `tr_transliteration` VALUES (2472, 'vemen a\\`raḍa \\`an ẕikrî feinne lehû me\\`îşeten ḍankev venaḥşüruhû yevme-lḳiyâmeti a\\`mâ.');
INSERT INTO `tr_transliteration` VALUES (2473, 'ḳâle rabbi lime ḥaşertenî a\\`mâ veḳad küntü beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2474, 'ḳâle keẕâlike etetke âyâtünâ fenesîtehâ. vekeẕâlike-lyevme tünsâ.');
INSERT INTO `tr_transliteration` VALUES (2475, 'vekeẕâlike neczî men esrafe velem yü\'mim biâyâti rabbih. vele\\`aẕâbü-l\'âḫirati eşeddü veebḳâ.');
INSERT INTO `tr_transliteration` VALUES (2476, 'efelem yehdi lehüm kem ehleknâ ḳablehüm mine-lḳurûni yemşûne fî mesâkinihim. inne fî ẕâlike leâyâtil liüli-nnühâ.');
INSERT INTO `tr_transliteration` VALUES (2477, 'velevlâ kelimetün sebeḳat mir rabbike lekâne lizâmev veecelüm müsemmâ.');
INSERT INTO `tr_transliteration` VALUES (2478, 'faṣbir \\`alâ mâ yeḳûlûne vesebbiḥ biḥamdi rabbike ḳable ṭulû\\`i-şşemsi veḳable gurûbihâ. vemin ânâi-lleyli fesebbiḥ veaṭrâfe-nnehâri le\\`alleke terḍâ.');
INSERT INTO `tr_transliteration` VALUES (2479, 'velâ temüddenne \\`ayneyke ilâ mâ metta\\`nâ bihî ezvâcem minhüm zehrate-lḥayâti-ddünyâ lineftinehüm fîh. verizḳu rabbike ḫayruv veebḳâ.');
INSERT INTO `tr_transliteration` VALUES (2480, 've\'mür ehleke biṣṣalâti vaṣṭabir \\`aleyhâ. lâ nes\'elüke rizḳâ. naḥnü nerzüḳuk. vel\\`âḳibetü littaḳvâ.');
INSERT INTO `tr_transliteration` VALUES (2481, 'veḳâlû levlâ ye\'tînâ biâyetim mir rabbih. evelem te\'tihim beyyinetü mâ fi-ṣṣuḥufi-l\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2482, 'velev ennâ ehleknâhüm bi\\`aẕâbim min ḳablihî leḳâlû rabbenâ levlâ erselte ileynâ rasûlen fenettebi\\`a âyâtike min ḳabli en neẕille venaḫzâ.');
INSERT INTO `tr_transliteration` VALUES (2483, 'ḳul küllüm müterabbiṣun feterabbeṣû. feseta\\`lemûne men aṣḥâbu-ṣṣirâṭi-sseviyyi vemeni-htedâ.');
INSERT INTO `tr_transliteration` VALUES (2484, 'iḳterabe linnâsi ḥisâbühüm vehüm fî gafletim mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (2485, 'mâ ye\'tîhim min ẕikrim mir rabbihim muḥdeŝin ille-steme\\`ûhü vehüm yel\\`abûn.');
INSERT INTO `tr_transliteration` VALUES (2486, 'lâhiyeten ḳulûbühüm. veeserrü-nnecvâ. elleẕîne żalemû. hel hâẕâ illâ beşer miŝlüküm. efete\'tûne-ssiḥra veentüm tübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (2487, 'ḳâle rabbî ya\\`lemü-lḳavle fi-ssemâi vel\'arḍ. vehüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (2488, 'bel ḳâlû aḍgâŝü aḥlâmim beli-fterâhü bel hüve şâ\\`ir. felye\'tinâ biâyetin kemâ ürsile-l\'evvelûn.');
INSERT INTO `tr_transliteration` VALUES (2489, 'mâ âmenet ḳablehüm min ḳaryetin ehleknâhâ. efehüm yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (2490, 'vemâ erselnâ ḳableke illâ ricâlen nûḥî ileyhim fes\'elû ehle-ẕẕikri in küntüm lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (2491, 'vemâ ce\\`alnâhüm cesedel lâ ye\'külûne-ṭṭa\\`âme vemâ kânû ḫâlidîn.');
INSERT INTO `tr_transliteration` VALUES (2492, 'ŝümme ṣadaḳnâhümü-lva\\`de feenceynâhüm vemen neşâü veehlekne-lmüsrifîn.');
INSERT INTO `tr_transliteration` VALUES (2493, 'leḳad enzelnâ ileyküm kitâben fîhi ẕikruküm. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (2494, 'vekem ḳaṣamnâ min ḳaryetin kânet żâlimetev veenşe\'nâ ba\\`dehâ ḳavmen âḫarîn.');
INSERT INTO `tr_transliteration` VALUES (2495, 'felemmâ eḥassû be\'senâ iẕâ hüm minhâ yerküḍûn.');
INSERT INTO `tr_transliteration` VALUES (2496, 'lâ terküḍû verci\\`û ilâ mâ ütriftüm fîhi vemesâkiniküm le\\`alleküm tüs\'elûn.');
INSERT INTO `tr_transliteration` VALUES (2497, 'ḳâlû yâ veylenâ innâ künnâ żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (2498, 'femâ zâlet tilke da\\`vâhüm ḥattâ ce\\`alnâhüm ḥaṣîden ḫâmidîn.');
INSERT INTO `tr_transliteration` VALUES (2499, 'vemâ ḫalaḳne-ssemâe vel\'arḍa vemâ beynehümâ lâ\\`ibîn.');
INSERT INTO `tr_transliteration` VALUES (2500, 'lev eradnâ en netteḫiẕe lehvel letteḫaẕnâhü mil ledünnâ. in künnâ fâ\\`ilîn.');
INSERT INTO `tr_transliteration` VALUES (2501, 'bel naḳẕifü bilḥaḳḳi \\`ale-lbâṭili feyedmeguhû feiẕâ hüve zâhiḳ. velekümü-lveylü mimmâ teṣifûn.');
INSERT INTO `tr_transliteration` VALUES (2502, 'velehû men fi-ssemâvâti vel\'arḍ. vemen \\`indehû lâ yestekbirûne \\`an \\`ibâdetihî velâ yestaḥsirûn.');
INSERT INTO `tr_transliteration` VALUES (2503, 'yüsebbiḥûne-lleyle vennehâra lâ yeftürûn.');
INSERT INTO `tr_transliteration` VALUES (2504, 'emi-tteḫaẕû âlihetem mine-l\'arḍi hüm yünşirûn.');
INSERT INTO `tr_transliteration` VALUES (2505, 'lev kâne fîhimâ âlihetün ille-llâhü lefesedetâ. fesübḥâne-llâhi rabbi-l\\`arşi \\`ammâ yeṣifûn.');
INSERT INTO `tr_transliteration` VALUES (2506, 'lâ yüs\'elü \\`ammâ yef\\`alü vehüm yüs\'elûn.');
INSERT INTO `tr_transliteration` VALUES (2507, 'emi-tteḫaẕû min dûnihî âliheh. ḳul hâtû bürhâneküm. hâẕâ ẕikru mem me\\`iye veẕikru men ḳablî. bel ekŝeruhüm lâ ya\\`lemûne-lḥaḳḳa fehüm mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (2508, 'vemâ erselnâ min ḳablike mir rasûlin illâ nûḥî ileyhi ennehû lâ ilâhe illâ ene fa\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (2509, 'veḳâlü-tteḫaẕe-rraḥmânü veleden sübḥâneh. bel \\`ibâdüm mükramûn.');
INSERT INTO `tr_transliteration` VALUES (2510, 'lâ yesbiḳûnehû bilḳavli vehüm biemrihî ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (2511, 'ya\\`lemü mâ beyne eydîhim vemâ ḫalfehüm velâ yeşfe\\`ûne illâ limeni-rteḍâ vehüm min ḫaşyetihî müşfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (2512, 'vemey yeḳul minhüm innî ilâhüm min dûnihî feẕâlike neczîhi cehennem. keẕâlike neczi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (2513, 'evelem yera-lleẕîne keferû enne-ssemâvâti vel\'arḍa kânetâ ratḳan fefetaḳnâhümâ. vece\\`alnâ mine-lmâi külle şey\'in ḥayy. efelâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (2514, 'vece\\`alnâ fi-l\'arḍi ravâsiye en temîde bihim vece\\`alnâ fîhâ ficâcen sübülel le\\`allehüm yehtedûn.');
INSERT INTO `tr_transliteration` VALUES (2515, 'vece\\`alne-ssemâe saḳfem maḥfûżâ. vehüm \\`an âyâtihâ mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (2516, 'vehüve-lleẕî ḫaleḳa-lleyle vennehâra veşşemse velḳamer. küllün fî felekiy yesbeḥûn.');
INSERT INTO `tr_transliteration` VALUES (2517, 'vemâ ce\\`alnâ libeşerim min ḳablike-lḫuld. efeim mitte fehümü-lḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (2518, 'küllü nefsin ẕâiḳatü-lmevt. veneblûküm bişşerri velḫayri fitneh. veileynâ türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2519, 'veiẕâ raâke-lleẕîne keferû iy yetteḫiẕûneke illâ hüzüvâ. ehâẕe-lleẕî yeẕküru âliheteküm. vehüm biẕikri-rraḥmâni hüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (2520, 'ḫuliḳa-l\'insânü min \\`acel. seürîküm âyâtî felâ testa\\`cilûn.');
INSERT INTO `tr_transliteration` VALUES (2521, 'veyeḳûlûne metâ hâẕe-lva\\`dü in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (2522, 'lev ya\\`lemü-lleẕîne keferû ḥîne lâ yeküffûne \\`av vucûhihimü-nnâra velâ \\`an żuhûrihim velâ hüm yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (2523, 'bel te\'tîhim bagteten fetebhetühüm felâ yesteṭî\\`ûne raddehâ velâ hüm yünżarûn.');
INSERT INTO `tr_transliteration` VALUES (2524, 'veleḳadi-stühzie birusülim min ḳablike feḥâḳa billeẕîne seḫirû minhüm mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (2525, 'ḳul mey yekleüküm billeyli vennehâri mine-rraḥmân. bel hüm \\`an ẕikri rabbihim mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (2526, 'em lehüm âlihetün temne\\`uhüm min dûninâ. lâ yesteṭî\\`ûne naṣra enfüsihim velâ hüm minnâ yuṣḥabûn.');
INSERT INTO `tr_transliteration` VALUES (2527, 'bel metta\\`nâ hâülâi veâbâehüm ḥattâ ṭâle \\`aleyhimü-l\\`umür. efelâ yeravne ennâ ne\'ti-l\'arḍa nenḳuṣuhâ min aṭrâfihâ. efehümü-lgâlibûn.');
INSERT INTO `tr_transliteration` VALUES (2528, 'ḳul innemâ ünẕiruküm bilvaḥy. velâ yesme\\`u-ṣṣummü-ddü\\`âe iẕâ mâ yünẕerûn.');
INSERT INTO `tr_transliteration` VALUES (2529, 'veleim messethüm nefḥatüm min \\`aẕâbi rabbike leyeḳûlünne yâ veylenâ innâ künnâ żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (2530, 'veneḍa\\`u-lmevâzîne-lḳiṣṭa liyevmi-lḳiyâmeti felâ tużlemü nefsün şey\'â. vein kâne miŝḳâle ḥabbetim min ḫardelin eteynâ bihâ. vekefâ binâ ḥâsibîn.');
INSERT INTO `tr_transliteration` VALUES (2531, 'veleḳad âteynâ mûsâ vehârûne-lfürḳâne veḍiyâev veẕikral lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (2532, 'elleẕîne yaḫşevne rabbehüm bilgaybi vehüm mine-ssâ\\`ati müşfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (2533, 'vehâẕâ ẕikrum mübârakün enzelnâh. efeentüm lehû münkirûn.');
INSERT INTO `tr_transliteration` VALUES (2534, 'veleḳad âteynâ ibrâhîme ruşdehû min ḳablü vekünnâ bihî \\`âlimîn.');
INSERT INTO `tr_transliteration` VALUES (2535, 'iẕ ḳâle liebîhi veḳavmihî mâ hâẕihi-ttemâŝîlü-lletî entüm lehâ \\`âkifûn.');
INSERT INTO `tr_transliteration` VALUES (2536, 'ḳâlû vecednâ âbâenâ lehâ \\`âbidîn.');
INSERT INTO `tr_transliteration` VALUES (2537, 'ḳâle leḳad küntüm entüm veâbâüküm fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (2538, 'ḳâlû eci\'tenâ bilḥaḳḳi em ente mine-llâ\\`ibîn.');
INSERT INTO `tr_transliteration` VALUES (2539, 'ḳâle ber rabbüküm rabbü-ssemâvâti vel\'arḍi-lleẕî feṭarahünn. veenâ \\`alâ ẕâliküm mine-şşâhidîn.');
INSERT INTO `tr_transliteration` VALUES (2540, 'vetellâhi leekîdenne aṣnâmeküm ba\\`de en tüvellû müdbirîn.');
INSERT INTO `tr_transliteration` VALUES (2541, 'fece\\`alehüm cüŝeŝen illâ kebîral lehüm le\\`allehüm ileyhi yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2542, 'ḳâlû men fe\\`ale hâẕâ biâlihetinâ innehû lemine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (2543, 'ḳâlû semi\\`nâ fetey yeẕküruhüm yüḳâlü lehû ibrâhîm.');
INSERT INTO `tr_transliteration` VALUES (2544, 'ḳâlû fe\'tû bihî \\`alâ a\\`yüni-nnâsi le\\`allehüm yeşhedûn.');
INSERT INTO `tr_transliteration` VALUES (2545, 'ḳâlû eente fe\\`alte hâẕâ biâlihetinâ yâ ibrâhîm.');
INSERT INTO `tr_transliteration` VALUES (2546, 'ḳâle bel fe\\`aleh. kebîruhüm hâẕâ fes\'elûhüm in kânû yenṭiḳûn.');
INSERT INTO `tr_transliteration` VALUES (2547, 'ferace\\`û ilâ enfüsihim feḳâlû inneküm entümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (2548, 'ŝümme nükisû \\`alâ ruûsihim. leḳad \\`alimte mâ hâülâi yenṭiḳûn.');
INSERT INTO `tr_transliteration` VALUES (2549, 'ḳâle efeta\\`büdûne min dûni-llâhi mâ lâ yenfe\\`uküm şey\'ev velâ yeḍurruküm.');
INSERT INTO `tr_transliteration` VALUES (2550, 'üffil leküm velimâ ta\\`büdûne min dûni-llâh. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (2551, 'ḳâlû ḥarriḳûhü venṣurû âliheteküm in küntüm fâ\\`ilîn.');
INSERT INTO `tr_transliteration` VALUES (2552, 'ḳulnâ yâ nâru kûnî berdev veselâmen \\`alâ ibrâhîm.');
INSERT INTO `tr_transliteration` VALUES (2553, 'veerâdû bihî keyden fece\\`alnâhümü-l\'aḫserîn.');
INSERT INTO `tr_transliteration` VALUES (2554, 'venecceynâhü velûṭan ile-l\'arḍi-lletî bâraknâ fîhâ lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (2555, 'vevehebnâ lehû isḥâḳ. veya\\`ḳûbe nâfileten. veküllen ce\\`alnâ ṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (2556, 'vece\\`alnâhüm eimmetey yehdûne biemrinâ veevḥaynâ ileyhim fi\\`le-lḫayrâti veiḳâme-ṣṣalâti veîtâe-zzekâh. vekânû lenâ \\`âbidîn.');
INSERT INTO `tr_transliteration` VALUES (2557, 'velûṭan âteynâhü ḥukmev ve\\`ilmev venecceynâhü mine-lḳaryeti-lletî kânet ta\\`melü-lḫabâiŝ. innehüm kânû ḳavme sev\'in fâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (2558, 'veedḫalnâhü fî raḥmetinâ. innehû mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (2559, 'venûḥan iẕ nâdâ min ḳablü festecebnâ lehû fenecceynâhü veehlehû mine-lkerbi-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (2560, 'veneṣarnâhü mine-lḳavmi-lleẕîne keẕẕebû biâyâtinâ. innehüm kânû ḳavme sev\'in feagraḳnâhüm ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (2561, 'vedâvûde vesüleymâne iẕ yaḥkümâni fi-lḥarŝi iẕ nefeşet fîhi ganemü-lḳavm. vekünnâ liḥukmihim şâhidîn.');
INSERT INTO `tr_transliteration` VALUES (2562, 'fefehhemnâhâ süleymân. veküllen âteynâ ḥukmev ve\\`ilmâ. veseḫḫarnâ me\\`a dâvûde-lcibâle yüsebbiḥne veṭṭayr. vekünnâ fâ\\`ilîn.');
INSERT INTO `tr_transliteration` VALUES (2563, 've\\`allemnâhü ṣan\\`ate lebûsil leküm lituḥṣineküm mim be\'siküm. fehel entüm şâkirûn.');
INSERT INTO `tr_transliteration` VALUES (2564, 'velisüleymâne-rrîḥa \\`âṣifeten tecrî biemrih ile-l\'arḍi-lletî bâraknâ fîhâ. vekünnâ bikülli şey\'in \\`âlimîn.');
INSERT INTO `tr_transliteration` VALUES (2565, 'vemine-şşeyâṭîni mey yegûṣûne lehû veya\\`melûne \\`amelen dûne ẕâlik. vekünnâ lehüm ḥâfiżîn.');
INSERT INTO `tr_transliteration` VALUES (2566, 'veeyyûbe iẕ nâdâ rabbehû ennî messeniye-ḍḍurru veente erḥamü-rrâḥimîn.');
INSERT INTO `tr_transliteration` VALUES (2567, 'festecebnâ lehû fekeşefnâ mâ bihî min ḍurriv veâteynâhü ehlehû vemiŝlehüm me\\`ahüm raḥmetem min \\`indinâ veẕikrâ lil\\`âbidîn.');
INSERT INTO `tr_transliteration` VALUES (2568, 'veismâ\\`île veidrîse veẕe-lkifl. küllüm mine-ṣṣâbirîn.');
INSERT INTO `tr_transliteration` VALUES (2569, 'veedḫalnâhüm fî raḥmetinâ. innehüm mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (2570, 'veẕe-nnûni iẕ ẕehebe mügâḍiben feżanne el len naḳdira \\`aleyhi fenâdâ fi-żżulümâti el lâ ilâhe illâ ente sübḥânek. innî küntü mine-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (2571, 'festecebnâ lehû venecceynâhü mine-lgamm. vekeẕâlike nünci-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (2572, 'vezekeriyyâ iẕ nâdâ rabbehû rabbi lâ teẕernî ferdev veente ḫayru-lvâriŝîn.');
INSERT INTO `tr_transliteration` VALUES (2573, 'festecebnâ leh. vevehebnâ lehû yaḥyâ veaṣlaḥnâ lehû zevceh. innehüm kânû yüsâri\\`ûne fi-lḫayrâti veyed\\`ûnenâ ragabev verahebâ. vekânû lenâ ḫâşi\\`în.');
INSERT INTO `tr_transliteration` VALUES (2574, 'velletî aḥṣanet fercehâ fenefaḫnâ fîhâ mir rûḥinâ vece\\`alnâhâ vebnehâ âyetel lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (2575, 'inne hâẕihî ümmetüküm ümmetev vâḥideh. veenâ rabbüküm fa\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (2576, 'veteḳaṭṭa\\`û emrahüm beynehüm. küllün ileynâ râci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2577, 'femey ya\\`mel mine-ṣṣâliḥâti vehüve mü\'minün felâ küfrâne lisa\\`yih. veinnâ lehû kâtibûn.');
INSERT INTO `tr_transliteration` VALUES (2578, 'veḥarâmün \\`alâ ḳaryetin ehleknâhâ ennehüm lâ yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2579, 'ḥattâ iẕâ fütiḥat ye\'cûcü veme\'cûcü vehüm min külli ḥadebiy yensilûn.');
INSERT INTO `tr_transliteration` VALUES (2580, 'vaḳterabe-lva\\`dü-lḥaḳḳu feiẕâ hiye şâḫiṣatün ebṣâru-lleẕîne keferû. yâ veylenâ ḳad künnâ fî gafletim min hâẕâ bel künnâ żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (2581, 'inneküm vemâ ta\\`büdûne min dûni-llâhi ḥaṣabü cehennem. entüm lehâ vâridûn.');
INSERT INTO `tr_transliteration` VALUES (2582, 'lev kâne hâülâi âlihetem mâ veradûhâ. veküllün fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (2583, 'lehüm fîhâ zefîruv vehüm fîhâ lâ yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2584, 'inne-lleẕîne sebeḳat lehüm minne-lḥusnâ ülâike \\`anhâ müb\\`adûn.');
INSERT INTO `tr_transliteration` VALUES (2585, 'lâ yesme\\`ûne ḥasîsehâ. vehüm fî me-ştehet enfüsühüm ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (2586, 'lâ yaḥzünühümü-lfeza\\`u-l\'ekberu veteteleḳḳâhümü-lmelâikeh. hâẕâ yevmükümü-lleẕî küntüm tû\\`adûn.');
INSERT INTO `tr_transliteration` VALUES (2587, 'yevme naṭvi-ssemâe keṭayyi-ssicilli lilkütüb. kemâ bede\'nâ evvele ḫalḳin nü\\`îdüh. va\\`den \\`aleynâ. innâ künnâ fâ\\`ilîn.');
INSERT INTO `tr_transliteration` VALUES (2588, 'veleḳad ketebnâ fi-zzebûri mim ba\\`di-ẕẕikri enne-l\'arḍa yeriŝühâ \\`ibâdiye-ṣṣâliḥûn.');
INSERT INTO `tr_transliteration` VALUES (2589, 'inne fî hâẕâ lebelâgal liḳavmin \\`âbidîn.');
INSERT INTO `tr_transliteration` VALUES (2590, 'vemâ erselnâke illâ raḥmetel lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (2591, 'ḳul innemâ yûḥâ ileyye ennemâ ilâhüküm ilâhüv vâḥid. fehel entüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (2592, 'fein tevellev feḳul ehentüküm \\`alâ sevâ\'. vein edrî eḳarîbün em be\\`îdüm mâ tû\\`adûn.');
INSERT INTO `tr_transliteration` VALUES (2593, 'innehû ya\\`lemü-lcehra mine-lḳavli veya\\`lemü mâ tektümûn.');
INSERT INTO `tr_transliteration` VALUES (2594, 'vein edrî le\\`allehû fitnetül leküm vemetâ\\`un ilâ ḥîn.');
INSERT INTO `tr_transliteration` VALUES (2595, 'ḳâle rabbi-ḥküm bilḥaḳḳ. verabbüne-rraḥmânü-lmüste\\`ânü \\`alâ mâ teṣifûn.');
INSERT INTO `tr_transliteration` VALUES (2596, 'yâ eyyühe-nnâsü-tteḳû rabbeküm. inne zelzelete-ssâ\\`ati şey\'ün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (2597, 'yevme teravnehâ teẕhelü küllü mürḍi\\`atin \\`ammâ erḍa\\`at vetede\\`u küllü ẕâti ḥamlin ḥamlehâ vetera-nnâse sükârâ vemâ hüm bisükârâ velâkinne \\`aẕâbe-llâhi şedîd.');
INSERT INTO `tr_transliteration` VALUES (2598, 'vemine-nnâsi mey yücâdilü fi-llâhi bigayri \\`ilmiv veyettebi\\`u külle şeyṭânim merîd.');
INSERT INTO `tr_transliteration` VALUES (2599, 'kütibe \\`aleyhi ennehû men tevellâhü feennehû yüḍillühû veyehdîhi ilâ \\`aẕâbi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (2600, 'yâ eyyühe-nnâsü in küntüm fî raybim mine-lba\\`ŝi feinnâ ḫalaḳnâküm min türâbin ŝümme min nuṭfetin ŝümme min \\`aleḳatin ŝümme mim muḍgatim müḫalleḳativ vegayri müḫalleḳatil linübeyyine leküm. venüḳirru fi-l\'erḥâmi mâ neşâü ilâ ecelim müsemmen ŝümme nuḫricüküm ṭiflen ŝümme liteblügû eşüddeküm. veminküm mey yüteveffâ veminküm mey yüraddü ilâ erẕeli-l\\`umüri likeylâ ya\\`leme mim ba\\`di \\`ilmin şey\'â. vetera-l\'arḍa hâmideten feiẕâ enzelnâ \\`aleyhe-lmâe-htezzet verabet veembetet min külli zevcim behîc.');
INSERT INTO `tr_transliteration` VALUES (2601, 'ẕâlike bienne-llâhe hüve-lḥaḳḳu veennehû yuḥyi-lmevtâ veennehû \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (2602, 'veenne-ssâ\\`ate âtiyetül lâ raybe fîhâ veenne-llâhe yeb\\`aŝü men fi-lḳubûr.');
INSERT INTO `tr_transliteration` VALUES (2603, 'vemine-nnâsi mey yücâdilü fi-llâhi bigayri \\`ilmiv velâ hüdev velâ kitâbim münîr.');
INSERT INTO `tr_transliteration` VALUES (2604, 'ŝâniye \\`iṭfihî liyüḍille \\`an sebîli-llâh. lehû fi-ddünyâ ḫizyüv venüẕîḳuhû yevme-lḳiyâmeti \\`aẕâbe-lḥarîḳ.');
INSERT INTO `tr_transliteration` VALUES (2605, 'ẕâlike bimâ ḳaddemet yedâke veenne-llâhe leyse biżallâmil lil\\`abîd.');
INSERT INTO `tr_transliteration` VALUES (2606, 'vemine-nnâsi mey ya\\`büdü-llâhe \\`alâ ḥarf. fein eṣâbehû ḫayrun-ṭmeenne bih. vein eṣâbethü fitnetün-nḳalebe \\`alâ vechih. ḫasira-ddünyâ vel\'âḫirah. ẕâlike hüve-lḫusrânü-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (2607, 'yed\\`û min dûni-llâhi mâ lâ yeḍurruhû vemâ lâ yenfe\\`uh. ẕâlike hüve-ḍḍalâlü-lbe\\`îd.');
INSERT INTO `tr_transliteration` VALUES (2608, 'yed\\`û lemen ḍarruhû aḳrabü min nef\\`ih. lebi\'se-lmevlâ velebi\'se-l\\`aşîr.');
INSERT INTO `tr_transliteration` VALUES (2609, 'inne-llâhe yüdḫilü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâr. inne-llâhe yef\\`alü mâ yürîd.');
INSERT INTO `tr_transliteration` VALUES (2610, 'men kâne yeżunnü el ley yenṣurahü-llâhü fi-ddünyâ vel\'âḫirati felyemdüd bisebebin ile-ssemâi ŝümme liyaḳṭa\\` felyenżur hel yüẕhibenne keydühû mâ yegîż.');
INSERT INTO `tr_transliteration` VALUES (2611, 'vekeẕâlike enzelnâhü âyâtim beyyinâtiv veenne-llâhe yehdî mey yürîd.');
INSERT INTO `tr_transliteration` VALUES (2612, 'inne-lleẕîne âmenû velleẕîne hâdû veṣṣâbi\'îne venneṣârâ velmecûse velleẕîne eşrakû. inne-llâhe yefṣilü beynehüm yevme-lḳiyâmeh. inne-llâhe \\`alâ külli şey\'in şehîd.');
INSERT INTO `tr_transliteration` VALUES (2613, 'elem tera enne-llâhe yescüdü lehû men fi-ssemâvâti vemen fi-l\'arḍi veşşemsü velḳameru vennücûmü velcibâlü veşşeceru veddevâbbü vekeŝîrum mine-nnâs. vekeŝîrun ḥaḳḳa \\`aleyhi-l\\`aẕâb. vemey yühini-llâhü femâ lehû mim mükrim. inne-llâhe yef\\`alü mâ yeşâ\'.');
INSERT INTO `tr_transliteration` VALUES (2614, 'hâẕâni ḫaṣmâni-ḫteṣamû fî rabbihim. felleẕîne keferû ḳuṭṭi\\`at lehüm ŝiyâbüm min nârin. yüṣabbü min fevḳi ruûsihimü-lḥamîm.');
INSERT INTO `tr_transliteration` VALUES (2615, 'yuṣheru bihî mâ fî büṭûnihim velcülûd.');
INSERT INTO `tr_transliteration` VALUES (2616, 'velehüm meḳâmi\\`u min ḥadîd.');
INSERT INTO `tr_transliteration` VALUES (2617, 'küllemâ erâdû ey yaḫrucû minhâ min gammin ü\\`îdû fîhâ veẕûḳû \\`aẕâbe-lḥarîḳ.');
INSERT INTO `tr_transliteration` VALUES (2618, 'inne-llâhe yüdḫilü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâru yüḥallevne fîhâ min esâvira min ẕehebiv velü\'lüâ. velibâsühüm fîhâ ḥarîr.');
INSERT INTO `tr_transliteration` VALUES (2619, 'vehüdû ile-ṭṭayyibi mine-lḳavl. vehüdû ilâ ṣirâṭi-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (2620, 'inne-lleẕîne keferû veyeṣuddûne \\`an sebîli-llâhi velmescidi-lḥarâmi-lleẕî ce\\`alnâhü linnâsi sevâeni-l\\`âkifü fîhi velbâd. vemey yürid fîhi biilḥâdim biżulmin nüẕiḳhü min \\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (2621, 'veiẕ bevve\'nâ liibrâhîme mekâne-lbeyti el lâ tüşrik bî şey\'ev veṭahhir beytiye liṭṭâifîne velḳâimîne verrukke\\`i-ssücûd.');
INSERT INTO `tr_transliteration` VALUES (2622, 'veeẕẕin fi-nnâsi bilḥacci ye\'tûke ricâlev ve\\`alâ külli ḍâmiriy ye\'tîne min külli feccin \\`amîḳ.');
INSERT INTO `tr_transliteration` VALUES (2623, 'liyeşhedû menâfi\\`a lehüm veyeẕkürü-sme-llâhi fî eyyâmim ma\\`lûmâtin \\`alâ mâ razeḳahüm mim behîmeti-l\'en\\`âm. fekülû minhâ veaṭ\\`imü-lbâise-lfeḳîr.');
INSERT INTO `tr_transliteration` VALUES (2624, 'ŝümme liyaḳḍû tefeŝehüm velyûfû nüẕûrahüm velyeṭṭavvefû bilbeyti-l\\`atîḳ.');
INSERT INTO `tr_transliteration` VALUES (2625, 'ẕâlik. vemey yü\\`ażżim ḥurumâti-llâhi fehüve ḫayrul lehû \\`inde rabbih. veüḥillet lekümü-l\'en\\`âmü illâ mâ yütlâ \\`aleyküm fectenibü-rricse mine-l\'evŝâni vectenibû ḳavle-zzûr.');
INSERT INTO `tr_transliteration` VALUES (2626, 'ḥunefâe lillâhi gayra müşrikîne bih. vemey yüşrik billâhi fekeennemâ ḫarra mine-ssemâi fetaḫṭafühu-ṭṭayru ev tehvî bihi-rrîḥu fî mekânin seḥîḳ.');
INSERT INTO `tr_transliteration` VALUES (2627, 'ẕâlik. vemey yü\\`ażżim şe\\`âira-llâhi feinnehâ min taḳve-lḳulûb.');
INSERT INTO `tr_transliteration` VALUES (2628, 'leküm fîhâ menâfi\\`u ilâ ecelim müsemmen ŝümme meḥillühâ ile-lbeyti-l\\`atîḳ.');
INSERT INTO `tr_transliteration` VALUES (2629, 'velikülli ümmetin ce\\`alnâ mensekel liyeẕkürü-sme-llâhi \\`alâ mâ razeḳahüm mim behîmeti-l\'en\\`âm. feilâhüküm ilâhüv vâḥidün felehû eslimû. vebeşşiri-lmuḫbitîn.');
INSERT INTO `tr_transliteration` VALUES (2630, 'elleẕîne iẕâ ẕükira-llâhü vecilet ḳulûbühüm veṣṣâbirîne \\`alâ mâ eṣâbehüm velmüḳîmi-ṣṣalâti vemimmâ razaḳnâhüm yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (2631, 'velbüdne ce\\`alnâhâ leküm min şe\\`âiri-llâhi leküm fîhâ ḫayr. feẕkürü-sme-llâhi \\`aleyhâ ṣavâff. feiẕâ vecebet cünûbühâ fekülû minhâ veaṭ\\`imü-lḳâni\\`a velmü\\`terr. keẕâlike seḫḫarnâhâ leküm le\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (2632, 'ley yenâle-llâhe lüḥûmühâ velâ dimâühâ velâkiy yenâlühü-ttaḳvâ minküm. keẕâlike seḫḫarahâ leküm litükebbirü-llâhe \\`alâ mâ hedâküm. vebeşşiri-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (2633, 'inne-llâhe yüdâfi\\`u \\`ani-lleẕîne âmenû. inne-llâhe lâ yüḥibbü külle ḫavvânin kefûr.');
INSERT INTO `tr_transliteration` VALUES (2634, 'üẕine lilleẕîne yüḳâtelûne biennehüm żulimû. veinne-llâhe \\`alâ naṣrihim leḳadîr.');
INSERT INTO `tr_transliteration` VALUES (2635, 'elleẕîne uḫricû min diyârihim bigayri ḥaḳḳin illâ ey yeḳûlû rabbüne-llâh. velevlâ def\\`u-llâhi-nnâse ba\\`ḍahüm biba\\`ḍil lehüddimet ṣavâmi\\`u vebiye\\`uv veṣalevâtüv vemesâcidü yüẕkeru fîhe-smü-llâhi keŝîrâ. veleyenṣuranne-llâhü mey yenṣuruh. inne-llâhe leḳaviyyün \\`azîz.');
INSERT INTO `tr_transliteration` VALUES (2636, 'elleẕîne im mekkennâhüm fi-l\'arḍi eḳâmu-ṣṣalâte veâtevu-zzekâte veemerû bilma\\`rûfi venehev \\`ani-lmünker. velillâhi \\`âḳibetü-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (2637, 'veiy yükeẕẕibûke feḳad keẕẕebet ḳablehüm ḳavmü nûḥiv ve\\`âdüv veŝemûd.');
INSERT INTO `tr_transliteration` VALUES (2638, 'veḳavmü ibrâhîme veḳavmü lûṭ.');
INSERT INTO `tr_transliteration` VALUES (2639, 'veaṣḥâbü medyen. veküẕẕibe mûsâ feemleytü lilkâfirîne ŝümme eḫaẕtühüm. fekeyfe kâne nekîr.');
INSERT INTO `tr_transliteration` VALUES (2640, 'fekeeyyim min ḳaryetin ehleknâhâ vehiye żâlimetün fehiye ḫâviyetün \\`alâ \\`urûşihâ vebi\'rim mü\\`aṭṭaletiv veḳaṣrim meşîd.');
INSERT INTO `tr_transliteration` VALUES (2641, 'efelem yesîrû fi-l\'arḍi fetekûne lehüm ḳulûbüy ya\\`ḳilûne bihâ ev âẕânüy yesme\\`ûne bihâ. feinnehâ lâ ta\\`me-l\'ebṣâru velâkin ta\\`me-lḳulûbü-lletî fi-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (2642, 'veyesta\\`cilûneke bil\\`aẕâbi veley yuḫlife-llâhü va\\`deh. veinne yevmen \\`inde rabbike keelfi senetim mimmâ te\\`uddûn.');
INSERT INTO `tr_transliteration` VALUES (2643, 'vekeeyyim min ḳaryetin emleytü lehâ vehiye żâlimetün ŝümme eḫaẕtühâ. veileyye-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (2644, 'ḳul yâ eyyühe-nnâsü innemâ ene leküm neẕîrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (2645, 'felleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lehüm magfiratüv verizḳun kerîm.');
INSERT INTO `tr_transliteration` VALUES (2646, 'velleẕîne se\\`av fî âyâtinâ mü\\`âcizîne ülâike aṣḥâbü-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (2647, 'vemâ erselnâ min ḳablike mir rasûliv velâ nebiyyin illâ iẕâ temennâ elḳa-şşeyṭânü fî ümniyyetih. feyenseḫu-llâhü mâ yülḳi-şşeyṭânü ŝümme yuḥkimü-llâhü âyâtih. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (2648, 'liyec\\`ale mâ yülḳi-şşeyṭânü fitnetel lilleẕîne fî ḳulûbihim meraḍuv velḳâsiyeti ḳulûbühüm. veinne-żżâlimîne lefî şiḳâḳim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (2649, 'veliya\\`leme-lleẕîne ûtü-l\\`ilme ennehü-lḥaḳḳu mir rabbike feyü\'minû bihî fetuḫbiṭa lehû ḳulûbühüm. veinne-llâhe lehâdi-lleẕîne âmenû ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (2650, 'velâ yezâlü-lleẕîne keferû fî miryetim minhü ḥattâ te\'tiyehümü-ssâ\\`atü bagteten ev ye\'tiyehüm \\`aẕâbü yevmin \\`aḳîm.');
INSERT INTO `tr_transliteration` VALUES (2651, 'elmülkü yevmeiẕil lillâh. yaḥkümü beynehüm. felleẕîne âmenû ve\\`amilu-ṣṣâliḥâti fî cennâti-nne\\`îm.');
INSERT INTO `tr_transliteration` VALUES (2652, 'velleẕîne keferû vekeẕẕebû biâyâtinâ feülâike lehüm \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (2653, 'velleẕîne hâcerû fî sebîli-llâhi ŝümme ḳutilû ev mâtû leyerzüḳannehümü-llâhü rizḳan ḥasenâ. veinne-llâhe lehüve ḫayru-rrâziḳîn.');
INSERT INTO `tr_transliteration` VALUES (2654, 'leyüdḫilennehüm müdḫaley yerḍavneh. veinne-llâhe le\\`alîmün ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (2655, 'ẕâlik. vemen \\`âḳabe bimiŝli mâ \\`ûḳibe bihî ŝümme bügiye \\`aleyhi leyenṣurannehü-llâh. inne-llâhe le\\`afüvvun gafûr.');
INSERT INTO `tr_transliteration` VALUES (2656, 'ẕâlike bienne-llâhe yûlicü-lleyle fi-nnehâri veyûlicü-nnehâra fi-lleyli veenne-llâhe semî\\`um beṣîr.');
INSERT INTO `tr_transliteration` VALUES (2657, 'ẕâlike bienne-llâhe hüve-lḥaḳḳu veenne mâ yed\\`ûne min dûnihî hüve-lbâṭilü veenne-llâhe hüve-l\\`aliyyü-lkebîr.');
INSERT INTO `tr_transliteration` VALUES (2658, 'elem tera enne-llâhe enzele mine-ssemâi mââ. fetuṣbiḥu-l\'arḍu muḫḍarrah. inne-llâhe leṭîfün ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (2659, 'lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. veinne-llâhe lehüve-lganiyyü-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (2660, 'elem tera enne-llâhe seḫḫara leküm mâ fi-l\'arḍi velfülke tecrî fi-lbaḥri biemrih. veyümsikü-ssemâe en teḳa\\`a \\`ale-l\'arḍi illâ biiẕnih. inne-llâhe binnâsi leraûfür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (2661, 'vehüve-lleẕî aḥyâküm. ŝümme yümîtüküm ŝümme yuḥyîküm. inne-l\'insâne lekefûr.');
INSERT INTO `tr_transliteration` VALUES (2662, 'likülli ümmetin ce\\`alnâ menseken hüm nâsikûhü felâ yünâzi\\`anneke fi-l\'emri ved\\`u ilâ rabbik. inneke le\\`alâ hüdem müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (2663, 'vein câdelûke feḳuli-llâhü a\\`lemü bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (2664, 'allâhü yaḥkümü beyneküm yevme-lḳiyâmeti fîmâ küntüm fîhi taḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (2665, 'elem ta\\`lem enne-llâhe ya\\`lemü mâ fi-ssemâi vel\'arḍ. inne ẕâlike fî kitâb. inne ẕâlike \\`ale-llâhi yesîr.');
INSERT INTO `tr_transliteration` VALUES (2666, 'veya\\`büdûne min dûni-llâhi mâ lem yünezzil bihî sülṭânev vemâ leyse lehüm bihî \\`ilm. vemâ liżżâlimîne min neṣîr.');
INSERT INTO `tr_transliteration` VALUES (2667, 'veiẕâ tütlâ \\`aleyhim âyâtünâ beyyinâtin ta\\`rifü fî vucûhi-lleẕîne keferü-lmünker. yekâdûne yesṭûne billeẕîne yetlûne \\`aleyhim âyâtinâ. ḳul efeünebbiüküm bişerrim min ẕâliküm. ennâr. ve\\`adehe-llâhü-lleẕîne keferû. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (2668, 'yâ eyyühe-nnâsü ḍuribe meŝelün festemi\\`û leh. inne-lleẕîne ted\\`ûne min dûni-llâhi ley yaḫlüḳû ẕübâbev velevi-cteme\\`û leh. veiy yeslübhümü-ẕẕübâbü şey\'el lâ yestenḳiẕûhü minh. ḍa\\`ufe-ṭṭâlibü velmaṭlûb.');
INSERT INTO `tr_transliteration` VALUES (2669, 'mâ ḳaderü-llâhe ḥaḳḳa ḳadrih. inne-llâhe leḳaviyyün \\`azîz.');
INSERT INTO `tr_transliteration` VALUES (2670, 'allâhü yaṣṭafî mine-lmelâiketi rusülev vemine-nnâs. inne-llâhe semî\\`um beṣîr.');
INSERT INTO `tr_transliteration` VALUES (2671, 'ya\\`lemü mâ beyne eydîhim vemâ ḫalfehüm. veile-llâhi türce\\`u-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (2672, 'yâ eyyühe-lleẕîne âmenü-rke\\`û vescüdû va\\`büdû rabbeküm vef\\`alü-lḫayra le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (2673, 'vecâhidû fi-llâhi ḥaḳḳa cihâdih. hüve-ctebâküm vemâ ce\\`ale \\`aleyküm fi-ddîni min ḥarac. millete ebîküm ibrâhîm. hüve semmâkümü-lmüslimîne min ḳablü vefî hâẕâ liyekûne-rrasûlü şehîden \\`aleyküm vetekûnû şühedâe \\`ale-nnâs. feeḳîmu-ṣṣalâte veâtü-zzekâte va\\`teṣimû billâh. hüve mevlâküm. feni\\`me-lmevlâ veni\\`me-nneṣîr.');
INSERT INTO `tr_transliteration` VALUES (2674, 'ḳad efleḥa-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (2675, 'elleẕîne hüm fî ṣalâtihim ḫâşi\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2676, 'velleẕîne hüm \\`ani-llagvi mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (2677, 'velleẕîne hüm lilzekâti fâ\\`ilûn.');
INSERT INTO `tr_transliteration` VALUES (2678, 'velleẕîne hüm lifürûcihim ḥâfiżûn.');
INSERT INTO `tr_transliteration` VALUES (2679, 'illâ \\`alâ ezvâcihim ev mâ meleket eymânühüm feinnehüm gayru melûmîn.');
INSERT INTO `tr_transliteration` VALUES (2680, 'femeni-btegâ verâe ẕâlike feülâike hümü-l\\`âdûn.');
INSERT INTO `tr_transliteration` VALUES (2681, 'velleẕîne hüm liemânâtihim ve\\`ahdihim râ\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2682, 'velleẕîne hüm \\`alâ ṣalevâtihim yüḥâfiżûn.');
INSERT INTO `tr_transliteration` VALUES (2683, 'ülâike hümü-lvâriŝûn.');
INSERT INTO `tr_transliteration` VALUES (2684, 'elleẕîne yeriŝûne-lfirdevs. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (2685, 'veleḳad ḫalaḳne-l\'insâne min sülâletim min ṭîn.');
INSERT INTO `tr_transliteration` VALUES (2686, 'ŝümme ce\\`alnâhü nuṭfeten fî ḳarârim mekîn.');
INSERT INTO `tr_transliteration` VALUES (2687, 'ŝümme ḫalaḳne-nnuṭfete \\`aleḳaten feḫalaḳne-l\\`aleḳate muḍgaten feḫalaḳne-lmuḍgate \\`iżâmen fekesevne-l\\`iżâme laḥmâ. ŝümme enşe\'nâhü ḫalḳan âḫar. fetebârake-llâhü aḥsenü-lḫâliḳîn.');
INSERT INTO `tr_transliteration` VALUES (2688, 'ŝümme inneküm ba\\`de ẕâlike lemeyyitûn.');
INSERT INTO `tr_transliteration` VALUES (2689, 'ŝümme inneküm yevme-lḳiyâmeti tüb\\`aŝûn.');
INSERT INTO `tr_transliteration` VALUES (2690, 'veleḳad ḫalaḳnâ fevḳaküm seb\\`a ṭarâiḳ. vemâ künnâ \\`ani-lḫalḳi gâfilîn.');
INSERT INTO `tr_transliteration` VALUES (2691, 'veenzelnâ mine-ssemâi mâem biḳaderin feeskennâhü fi-l\'arḍ. veinnâ \\`alâ ẕehâbim bihî leḳâdirûn.');
INSERT INTO `tr_transliteration` VALUES (2692, 'feenşe\'nâ leküm bihî cennâtim min neḫîliv vea\\`nâb. leküm fîhâ fevâkihü keŝîratüv veminhâ te\'külûn.');
INSERT INTO `tr_transliteration` VALUES (2693, 'veşeceraten taḫrucü min ṭûri seynâe tembütü biddühni veṣibgil lil\'âkilîn.');
INSERT INTO `tr_transliteration` VALUES (2694, 'veinne leküm fi-l\'en\\`âmi le\\`ibrah. nüsḳîküm mimmâ fî büṭûnihâ veleküm fîhâ menâfi\\`u keŝîratüv veminhâ te\'külûn.');
INSERT INTO `tr_transliteration` VALUES (2695, 've\\`aleyhâ ve\\`ale-lfülki tuḥmelûn.');
INSERT INTO `tr_transliteration` VALUES (2696, 'veleḳad erselnâ nûḥan ilâ ḳavmihî feḳâle yâ ḳavmi-\\`büdü-llâhe mâ leküm min ilâhin gayruh. efelâ tetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (2697, 'feḳâle-lmeleü-lleẕîne keferû min ḳavmihî mâ hâẕâ illâ beşerum miŝlüküm yürîdü ey yetefeḍḍale \\`aleyküm. velev şâe-llâhü leenzele melâikeh. mâ semi\\`nâ bihâẕâ fî âbâine-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (2698, 'in hüve illâ racülüm bihî cinnetün feterabbeṣû bihî ḥattâ ḥîn.');
