INSERT INTO `tr_transliteration` VALUES (2815, 'yevme teşhedü \\`aleyhim elsinetühüm veeydîhim veercülühüm bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (2816, 'yevmeiẕiy yüveffîhimü-llâhü dînehümü-lḥaḳḳa veya\\`lemûne enne-llâhe hüve-lḥaḳḳu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (2817, 'elḫabîŝâtü lilḫabîŝîne velḫabîŝûne lilḫabîŝât. veṭṭayyibâtü liṭṭayyibîne veṭṭayyibûne liṭṭayyibât. ülâike müberraûne mimmâ yeḳûlûn. lehüm magfiratüv verizḳun kerîm.');
INSERT INTO `tr_transliteration` VALUES (2818, 'yâ eyyühe-lleẕîne âmenû lâ tedḫulû büyûten gayra büyûtiküm ḥattâ teste\'nisû vetüsellimû \\`alâ ehlihâ. ẕâliküm ḫayrul leküm le\\`alleküm teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (2819, 'feil lem tecidû fîhâ eḥaden felâ tedḫulûhâ ḥattâ yü\'ẕene leküm. vein ḳîle lekümü-rci\\`û ferci\\`û hüve ezkâ leküm. vellâhü bimâ ta\\`melûne \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (2820, 'leyse \\`aleyküm cünâḥun en tedḫulû büyûten gayra meskûnetin fîhâ metâ\\`ul leküm. vellâhü ya\\`lemü mâ tübdûne vemâ tektümûn.');
INSERT INTO `tr_transliteration` VALUES (2821, 'ḳul lilmü\'minîne yeguḍḍû min ebṣârihim veyaḥfeżû fürûcehüm. ẕâlike ezkâ lehüm. inne-llâhe ḫabîrum bimâ yaṣne\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (2822, 'veḳul lilmü\'minâti yagḍuḍne min ebṣârihinne veyaḥfażne fürûcehünne velâ yübdîne zînetehünne illâ mâ żahera minhâ velyaḍribne biḫumürihinne \\`alâ cüyûbihinn. velâ yübdîne zînetehünne illâ libü\\`ûletihinne ev âbâihinne ev âbâi bü\\`ûletihinne ev ebnâihinne ev ebnâi bü\\`ûletihinne ev iḫvânihinne ev benî iḫvânihinne ev benî eḫavâtihinne ev nisâihinne ev mâ meleket eymânühünne evi-ttâbi\\`îne gayri üli-l\'irbeti mine-rricâli evi-ṭṭifli-lleẕîne lem yażherû \\`alâ \\`avrati-nnisâ\'. velâ yaḍribne biercülihinne liyü\\`leme mâ yuḫfîne min zînetihinn. vetûbû ile-llâhi cemî\\`an eyyühe-lmü\'minûne le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (2823, 'venkiḥü-l\'eyâmâ minküm veṣṣâliḥîne min \\`ibâdiküm veimâiküm. iy yekûnû füḳarâe yugnîhimü-llâhü min faḍlih. vellâhü vâsi\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (2824, 'velyesta\\`fifi-lleẕîne lâ yecidûne nikâḥan ḥattâ yugniyehümü-llâhü min faḍlih. velleẕîne yebtegûne-lkitâbe mimmâ meleket eymânüküm fekâtibûhüm in \\`alimtüm fîhim ḫayrâ. veâtûhüm mim mâli-llâhi-lleẕî âtâküm. velâ tükrihû feteyâtiküm \\`ale-lbigâi in eradne teḥaṣṣunel litebtegû \\`araḍa-lḥayâti-ddünyâ. vemey yükrihhünne feinne-llâhe mim ba\\`di ikrâhihinne gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (2825, 'veleḳad enzelnâ ileyküm âyâtim mübeyyinâtiv vemeŝelem mine-lleẕîne ḫalev min ḳabliküm vemev\\`iżatel lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (2826, 'allâhü nûru-ssemâvâti vel\'arḍ. meŝelü nûrihî kemişkâtin fîhâ miṣbâḥ. elmiṣbâḥu fî zücâceh. ezzücâcetü keennehâ kevkebün dürriyyüy yûḳadü min şeceratim mübâraketin zeytûnetil lâ şerḳiyyetiv velâ garbiyyetiy yekâdü zeytühâ yüḍîü velev lem temseshü nârun. nûrun \\`alâ nûr. yehdi-llâhü linûrihî mey yeşâ\'. veyaḍribü-llâhü-l\'emŝâle linnâs. vellâhü bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (2827, 'fî büyûtin eẕine-llâhü en türfe\\`a veyüẕkera fîhe-smühû yüsebbiḥu lehû fîhâ bilgudüvvi vel\'eṣâl.');
INSERT INTO `tr_transliteration` VALUES (2828, 'ricâlül lâ tülhîhim ticâratüv velâ bey\\`un \\`an ẕikri-llâhi veiḳâmi-ṣṣalâti veîtâi-zzekâti yeḫâfûne yevmen teteḳallebü fîhi-lḳulûbü vel\'ebṣâr.');
INSERT INTO `tr_transliteration` VALUES (2829, 'liyecziyehümü-llâhü aḥsene mâ \\`amilû veyezîdehüm min faḍlih. vellâhü yerzüḳu mey yeşâü bigayri ḥisâb.');
INSERT INTO `tr_transliteration` VALUES (2830, 'velleẕîne keferû a\\`mâlühüm keserâbim biḳî\\`atiy yaḥsebühu-żżam\'ânü mââ. ḥattâ iẕâ câehû lem yecidhü şey\'ev vevecede-llâhe \\`indehû feveffâhü ḥisâbeh. vellâhü serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (2831, 'ev keżulümâtin fî baḥril lücciyyiy yagşâhü mevcüm min fevḳihî mevcüm min fevḳihî seḥâb. żulümâtüm ba\\`ḍuhâ fevḳa ba\\`ḍ. iẕâ aḫrace yedehû lem yeked yerâhâ. vemel lem yec\\`ali-llâhü lehû nûran femâ lehû min nûr.');
INSERT INTO `tr_transliteration` VALUES (2832, 'elem tera enne-llâhe yüsebbiḥu lehû men fi-ssemâvâti vel\'arḍi veṭṭayru ṣâffât. küllün ḳad \\`alime ṣalâtehû vetesbîḥah. vellâhü \\`alîmüm bimâ yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (2833, 'velillâhi mülkü-ssemâvâti vel\'arḍ. veile-llâhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (2834, 'elem tera enne-llâhe yüzcî seḥâben ŝümme yü\'ellifü beynehû ŝümme yec\\`alühû rukâmen fetere-lvedḳa yaḫrucü min ḫilâlih. veyünezzilü mine-ssemâi min cibâlin fîhâ mim beradin feyüṣîbü bihî mey yeşâü veyaṣrifühû \\`am mey yeşâ\'. yekâdü senâ berḳihî yeẕhebü bil\'ebṣâr.');
INSERT INTO `tr_transliteration` VALUES (2835, 'yüḳallibü-llâhü-lleyle vennehâr. inne fî ẕâlike le\\`ibratel liüli-l\'ebṣâr.');
INSERT INTO `tr_transliteration` VALUES (2836, 'vellâhü ḫaleḳa külle dâbbetim mim mâ\'. feminhüm mey yemşî \\`alâ baṭnih. veminhüm mey yemşî \\`alâ ricleyn. veminhüm mey yemşî \\`alâ erba\\`. yaḫlüḳu-llâhü mâ yeşâ\'. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (2837, 'leḳad enzelnâ âyâtim mübeyyinât. vellâhü yehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (2838, 'veyeḳûlûne âmennâ billâhi vebirrasûli veeṭa\\`nâ ŝümme yetevellâ ferîḳum minhüm mim ba\\`di ẕâlik. vemâ ülâike bilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (2839, 'veiẕâ dü\\`û ile-llâhi verasûlihî liyaḥküme beynehüm iẕâ ferîḳum minhüm mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (2840, 'veiy yekül lehümü-lḥaḳḳu ye\'tû ileyhi müẕ\\`inîn.');
INSERT INTO `tr_transliteration` VALUES (2841, 'efî ḳulûbihim meraḍun emi-rtâbû em yeḫâfûne ey yeḥîfe-llâhü \\`aleyhim verasûlüh. bel ülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (2842, 'innemâ kâne ḳavle-lmü\'minîne iẕâ dü\\`û ile-llâhi verasûlihî liyaḥküme beynehüm ey yeḳûlû semi\\`nâ veeṭa\\`nâ. veülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (2843, 'vemey yüṭi\\`i-llâhe verasûlehû veyaḫşe-llâhe veyettaḳhi feülâike hümü-lfâizûn.');
INSERT INTO `tr_transliteration` VALUES (2844, 'veaḳsemû billâhi cehde eymânihim lein emertehüm leyaḫrucünn. ḳul lâ tuḳsimû. ṭâ\\`atüm ma\\`rûfeh. inne-llâhe ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (2845, 'ḳul eṭî\\`ü-llâhe veeṭî\\`ü-rrasûl. fein tevellev feinnemâ \\`aleyhi mâ ḥummile ve\\`aleyküm mâ ḥummiltüm. vein tüṭî\\`ûhü tehtedû. vemâ \\`ale-rrasûli ille-lbelâgu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (2846, 've\\`ade-llâhü-lleẕîne âmenû minküm ve\\`amilu-ṣṣâliḥâti leyestaḫlifennehüm fi-l\'arḍi keme-staḫlefe-lleẕîne min ḳablihim. veleyümekkinenne lehüm dînehümü-lleẕi-rteḍâ lehüm veleyübeddilennehüm mim ba\\`di ḫavfihim emnâ. ya\\`büdûnenî lâ yüşrikûne bî şey\'â. vemen kefera ba\\`de ẕâlike feülâike hümü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (2847, 'veeḳîmu-ṣṣalâte veâtü-zzekâte veeṭî\\`ü-rrasûle le\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (2848, 'lâ taḥsebenne-lleẕîne keferû mü\\`cizîne fi-l\'arḍ. veme\'vâhümü-nnâr. velebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (2849, 'yâ eyyühe-lleẕîne âmenû liyeste\'ẕinkümü-lleẕîne meleket eymânüküm velleẕîne lem yeblügü-lḥulûme minküm ŝelâŝe merrât. min ḳabli ṣalâti-lfecri veḥîne teḍa\\`ûne ŝiyâbeküm mine-żżahîrati vemim ba\\`di ṣalâti-l\\`işâ\'. ŝelâŝü \\`avrâtil leküm. leyse \\`aleyküm velâ \\`aleyhim cünâḥum ba\\`dehünn. ṭavvâfûne \\`aleyküm ba\\`ḍuküm \\`alâ ba\\`ḍ. keẕâlike yübeyyinü-llâhü lekümü-l\'âyât. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (2850, 'veiẕâ belega-l\'aṭfâlü minkümü-lḥulûme felyeste\'ẕinû keme-ste\'ẕene-lleẕîne min ḳablihim. keẕâlike yübeyyinü-llâhü leküm âyâtih. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (2851, 'velḳavâ\\`idü mine-nnisâi-llâtî lâ yercûne nikâḥan feleyse \\`aleyhinne cünâḥun ey yeḍa\\`ne ŝiyâbehünne gayra müteberricâtim bizîneh. veey yesta\\`fifne ḫayrul lehünn. vellâhü semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (2852, 'leyse \\`ale-l\'a\\`mâ ḥaracüv velâ \\`ale-l\'a\\`raci ḥaracüv velâ \\`ale-lmerîḍi ḥaracüv velâ \\`alâ enfüsiküm en te\'külû mim büyûtiküm ev büyûti âbâiküm ev büyûti ümmehâtiküm ev büyûti iḫvâniküm ev büyûti eḫavâtiküm ev büyûti a\\`mâmiküm ev büyûti \\`ammâtiküm ev büyûti aḫvâliküm ev büyûti ḫâlâtiküm ev mâ melektüm mefâtiḥahû ev ṣadîḳiküm. leyse \\`aleyküm cünâḥun en te\'külû cemî\\`an ev eştâtâ. feiẕâ deḫaltüm büyûten fesellimû \\`alâ enfüsiküm teḥiyyetem min \\`indi-llâhi mübâraketen ṭayyibeh. keẕâlike yübeyyinü-llâhü lekümü-l\'âyâti le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (2853, 'inneme-lmü\'minûne-lleẕîne âmenû billâhi verasûlihî veiẕâ kânû me\\`ahû \\`alâ emrin câmi\\`il lem yeẕhebû ḥattâ yeste\'ẕinûh. inne-lleẕîne yeste\'ẕinûneke ülâike-lleẕîne yü\'minûne billâhi verasûlih. feiẕe-ste\'ẕenûke liba\\`ḍi şe\'nihim fe\'ẕel limen şi\'te minhüm vestagfir lehümü-llâh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (2854, 'lâ tec\\`alû dü\\`âe-rrasûli beyneküm kedü\\`âi ba\\`ḍiküm ba\\`ḍâ. ḳad ya\\`lemü-llâhü-lleẕîne yetesellelûne minküm livâẕâ. felyaḥẕeri-lleẕîne yüḫâlifûne \\`an emrih en tüṣîbehüm fitnetün ev yüṣîbehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (2855, 'elâ inne lillâhi mâ fi-ssemâvâti vel\'arḍ. ḳad ya\\`lemü mâ entüm \\`aleyh. veyevme yürce\\`ûne ileyhi feyünebbiühüm bimâ \\`amilû. vellâhü bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (2856, 'tebârake-lleẕî nezzele-lfürḳâne \\`alâ \\`abdihî liyekûne lil\\`âlemîne neẕîrâ.');
INSERT INTO `tr_transliteration` VALUES (2857, 'elleẕî lehû mülkü-ssemâvâti vel\'arḍi velem yetteḫiẕ veledev velem yekül lehû şerîkün fi-lmülki veḫaleḳa külle şey\'in feḳadderahû taḳdîrâ.');
INSERT INTO `tr_transliteration` VALUES (2858, 'vetteḫaẕû min dûnihî âlihetel lâ yaḫlüḳûne şey\'ev vehüm yuḫleḳûne velâ yemlikûne lienfüsihim ḍarrav velâ nef\\`av velâ yemlikûne mevtev velâ ḥayâtev velâ nüşûrâ.');
INSERT INTO `tr_transliteration` VALUES (2859, 'veḳâle-lleẕîne keferû in hâẕâ illâ ifkün-fterâhü vee\\`ânehû \\`aleyhi ḳavmün âḫarûn. feḳad câû żulmev vezûrâ.');
INSERT INTO `tr_transliteration` VALUES (2860, 'veḳâlû esâṭîru-l\'evvelîne-ktetebehâ fehiye tümlâ \\`aleyhi bükratev veeṣîlâ.');
INSERT INTO `tr_transliteration` VALUES (2861, 'ḳul enzelehü-lleẕî ya\\`lemü-ssirra fi-ssemâvâti vel\'arḍ. innehû kâne gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (2862, 'veḳâlû mâ lihâẕe-rrasûli ye\'külu-ṭṭa\\`âme veyemşî fi-l\'esvâḳ. levlâ ünzile ileyhi melekün feyekûne me\\`ahû neẕîrâ.');
INSERT INTO `tr_transliteration` VALUES (2863, 'ev yülḳâ ileyhi kenzün ev tekûnü lehû cennetüy ye\'külü minhâ. veḳâle-żżâlimûne in tettebi\\`ûne illâ racülem mesḥûrâ.');
INSERT INTO `tr_transliteration` VALUES (2864, 'ünżur keyfe ḍarabû leke-l\'emŝâle feḍallû felâ yesteṭî\\`ûne sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2865, 'tebârake-lleẕî in şâe ce\\`ale leke ḫayram min ẕâlike cennâtin tecrî min taḥtihe-l\'enhâru veyec\\`al leke ḳuṣûrâ.');
INSERT INTO `tr_transliteration` VALUES (2866, 'bel keẕẕebû bissâ\\`ati vea\\`tednâ limen keẕẕebe bissâ\\`ati se\\`îrâ.');
INSERT INTO `tr_transliteration` VALUES (2867, 'iẕâ raethüm mim mekânim be\\`îdin semi\\`û lehâ tegayyüżav vezefîrâ.');
INSERT INTO `tr_transliteration` VALUES (2868, 'veiẕâ ülḳû minhâ mekânen ḍayyiḳam müḳarranîne de\\`av hünâlike ŝübûrâ.');
INSERT INTO `tr_transliteration` VALUES (2869, 'lâ ted\\`ü-lyevme ŝübûrav vâḥidev ved\\`û ŝübûran keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (2870, 'ḳul eẕâlike ḫayrun em cennetü-lḫuldi-lletî vu\\`ide-lmütteḳûn. kânet lehüm cezâev vemeṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2871, 'lehüm fîhâ mâ yeşâûne ḫâlidîn. kâne \\`alâ rabbike va\\`dem mes\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (2872, 'veyevme yaḥşüruhüm vemâ ya\\`büdûne min dûni-llâhi feyeḳûlü eentüm aḍleltüm \\`ibâdî hâülâi em hüm ḍallü-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (2873, 'ḳâlû sübḥâneke mâ kâne yembegî lenâ en netteḫiẕe min dûnike min evliyâe velâkim metta\\`tehüm veâbâehüm ḥattâ nesü-ẕẕikr. vekânû ḳavmem bûrâ.');
INSERT INTO `tr_transliteration` VALUES (2874, 'feḳad keẕẕebûküm bimâ teḳûlûne femâ testeṭî\\`ûne ṣarfev velâ naṣrâ. vemey yażlim minküm nüẕiḳhü \\`aẕâben kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2875, 'vemâ erselnâ ḳableke mine-lmürselîne illâ innehüm leye\'külûne-ṭṭa\\`âme veyemşûne fi-l\'esvâḳ. vece\\`alnâ ba\\`ḍaküm liba\\`ḍin fitneh. etaṣbirûn. vekâne rabbüke beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2876, 'veḳâle-lleẕîne lâ yercûne liḳâenâ levlâ ünzile \\`aleyne-lmelâiketü ev nerâ rabbenâ. leḳadi-stekberû fî enfüsihim ve\\`atev \\`utüvven kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (2877, 'yevme yeravne-lmelâikete lâ büşrâ yevmeiẕil lilmücrimîne veyeḳûlûne ḥicram maḥcûrâ.');
INSERT INTO `tr_transliteration` VALUES (2878, 'veḳadimnâ ilâ mâ \\`amilû min \\`amelin fece\\`alnâhü hebâem menŝûrâ.');
INSERT INTO `tr_transliteration` VALUES (2879, 'aṣḥâbü-lcenneti yevmeiẕin ḫayrum müsteḳarrav veaḥsenü meḳîlâ.');
INSERT INTO `tr_transliteration` VALUES (2880, 'veyevme teşeḳḳaḳu-ssemâü bilgamâmi venüzzile-lmelâiketü tenzîlâ.');
INSERT INTO `tr_transliteration` VALUES (2881, 'elmülkü yevmeiẕin-lḥaḳḳu lirraḥmân. vekâne yevmen \\`ale-lkâfirîne \\`asîrâ.');
INSERT INTO `tr_transliteration` VALUES (2882, 'veyevme ye\\`aḍḍu-żżâlimü \\`alâ yedeyhi yeḳûlü yâ leyteni-tteḫaẕtü me\\`a-rrasûli sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2883, 'yâ veyletâ leytenî lem etteḫiẕ fülânen ḫalîlâ.');
INSERT INTO `tr_transliteration` VALUES (2884, 'leḳad eḍallenî \\`ani-ẕẕikri ba\\`de iẕ câenî. vekâne-şşeyṭânü lil\'insâni ḫaẕûlâ.');
INSERT INTO `tr_transliteration` VALUES (2885, 'veḳâle-rrasûlü yâ rabbi inne ḳavmi-tteḫaẕû hâẕe-lḳur\'âne mehcûrâ.');
INSERT INTO `tr_transliteration` VALUES (2886, 'vekeẕâlike ce\\`alnâ likülli nebiyyin \\`adüvvem mine-lmücrimîn. vekefâ birabbike hâdiyev veneṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (2887, 'veḳâle-lleẕîne keferû levlâ nüzzile \\`aleyhi-lḳur\'ânü cümletev vâḥideh. keẕâlike linüŝebbite bihî füâdeke verattelnâhü tertîlâ.');
INSERT INTO `tr_transliteration` VALUES (2888, 'velâ ye\'tûneke bimeŝelin illâ ci\'nâke bilḥaḳḳi veaḥsene tefsîrâ.');
INSERT INTO `tr_transliteration` VALUES (2889, 'elleẕîne yuḥşerûne \\`alâ vucûhihim ilâ cehenneme ülâike şerrum mekânev veeḍallü sebîlâ.');
INSERT INTO `tr_transliteration` VALUES (2890, 'veleḳad âteynâ mûse-lkitâbe vece\\`alnâ me\\`ahû eḫâhü hârûne vezîrâ.');
INSERT INTO `tr_transliteration` VALUES (2891, 'feḳulne-ẕhebâ ile-lḳavmi-lleẕîne keẕẕebû biâyâtinâ. fedemmernâhüm tedmîrâ.');
