INSERT INTO `tr_transliteration` VALUES (3196, 'irci\\` ileyhim felene\'tiyennehüm bicünûdil lâ ḳibele lehüm bihâ velenuḫricennehüm minhâ eẕilletev vehüm ṣâgirûn.');
INSERT INTO `tr_transliteration` VALUES (3197, 'ḳâle yâ eyyühe-lmeleü eyyüküm ye\'tînî bi\\`arşihâ ḳable ey ye\'tûnî müslimîn.');
INSERT INTO `tr_transliteration` VALUES (3198, 'ḳâle \\`ifrîtüm mine-lcinni ene âtîke bihî ḳable en teḳûme mim meḳâmik. veinnî \\`aleyhi leḳaviyyün emîn.');
INSERT INTO `tr_transliteration` VALUES (3199, 'ḳâle-lleẕî \\`indehû \\`ilmüm mine-lkitâbi ene âtîke bihî ḳable ey yertedde ileyke ṭarfük. felemmâ raâhü müsteḳirran \\`indehû ḳâle hâẕâ min faḍli rabbî. liyeblüvenî eeşküru em ekfür. vemen şekera feinnemâ yeşküru linefsih. vemen kefera feinne rabbî ganiyyün kerîm.');
INSERT INTO `tr_transliteration` VALUES (3200, 'ḳâle nekkirû lehâ \\`arşehâ nenżur etehtedî em tekûnü mine-lleẕîne lâ yehtedûn.');
INSERT INTO `tr_transliteration` VALUES (3201, 'felemmâ câet ḳîle ehâkeẕâ \\`arşük. ḳâlet keennehû hû. veûtîne-l\\`ilme min ḳablihâ vekünnâ müslimîn.');
INSERT INTO `tr_transliteration` VALUES (3202, 'veṣaddehâ mâ kânet ta\\`büdü min dûni-llâh. innehâ kânet min ḳavmin kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (3203, 'ḳîle lehe-dḫuli-ṣṣarḥ. felemmâ raethü ḥasibethü lüccetev vekeşefet \\`an sâḳayhâ. ḳâle innehû ṣarḥum mümerradüm min ḳavârîr. ḳâlet rabbi innî żalemtü nefsî veeslemtü me\\`a süleymâne lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3204, 'veleḳad erselnâ ilâ ŝemûde eḫâhüm ṣâliḥan eni-\\`büdü-llâhe feiẕâ hüm ferîḳâni yaḫteṣimûn.');
INSERT INTO `tr_transliteration` VALUES (3205, 'ḳâle yâ ḳavmi lime testa\\`cilûne bisseyyieti ḳable-lḥaseneh. levlâ testagfirûne-llâhe le\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (3206, 'ḳâlu-ṭṭayyernâ bike vebimem me\\`ak. ḳâle ṭâiruküm \\`inde-llâhi bel entüm ḳavmün tüftenûn.');
INSERT INTO `tr_transliteration` VALUES (3207, 'vekâne fi-lmedîneti tis\\`atü rahṭiy yüfsidûne fi-l\'arḍi velâ yuṣliḥûn.');
INSERT INTO `tr_transliteration` VALUES (3208, 'ḳâlû teḳâsemû billâhi lenübeyyitennehû veehlehû ŝümme leneḳûlenne liveliyyihî mâ şehidnâ mehlike ehlihî veinnâ leṣâdiḳûn.');
INSERT INTO `tr_transliteration` VALUES (3209, 'vemekerû mekrav vemekernâ mekrav vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (3210, 'fenżur keyfe kâne \\`âḳibetü mekrihim ennâ demmernâhüm veḳavmehüm ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (3211, 'fetilke büyûtühüm ḫâviyetem bimâ żalemû. inne fî ẕâlike leâyetel liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3212, 'veenceyne-lleẕîne âmenû vekânû yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (3213, 'velûṭan iẕ ḳâle liḳavmihî ete\'tûne-lfâḥişete veentüm tübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (3214, 'einneküm lete\'tûne-rricâle şehvetem min dûni-nnisâi. bel entüm ḳavmün techelûn.');
INSERT INTO `tr_transliteration` VALUES (3215, 'femâ kâne cevâbe ḳavmihî illâ en ḳâlû aḫricû âle lûṭim min ḳaryetiküm. innehüm ünâsüy yeteṭahherûn.');
INSERT INTO `tr_transliteration` VALUES (3216, 'feenceynâhü veehlehû ille-mraeteh. ḳaddernâhâ mine-lgâbirîn.');
INSERT INTO `tr_transliteration` VALUES (3217, 'veemṭarnâ \\`aleyhim meṭarâ. fesâe meṭaru-lmünẕerîn.');
INSERT INTO `tr_transliteration` VALUES (3218, 'ḳuli-lḥamdü lillâhi veselâmün \\`alâ \\`ibâdihi-lleẕîne-ṣṭafâ. ellâhü ḫayrun emmâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3219, 'emmen ḫaleḳa-ssemâvâti vel\'arḍa veenzele leküm mine-ssemâi mââ. feembetnâ bihî ḥadâiḳa ẕâte behceh. mâ kâne leküm en tümbitû şecerahâ. eilâhüm me\\`a-llâh. bel hüm ḳavmüy ya\\`dilûn.');
INSERT INTO `tr_transliteration` VALUES (3220, 'emmen ce\\`ale-l\'arḍa ḳarârav vece\\`ale ḫilâlehâ enhârav vece\\`ale lehâ ravâsiye vece\\`ale beyne-lbaḥrayni ḥâcizâ. eilâhüm me\\`a-llâh. bel ekŝeruhüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3221, 'emmey yücîbü-lmuḍṭarra iẕâ de\\`âhü veyekşifü-ssûe veyec\\`alüküm ḫulefâe-l\'arḍ. eilâhüm me\\`a-llâh. ḳalîlem mâ teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (3222, 'emmey yehdîküm fî żulümâti-lberri velbaḥri vemey yürsilü-rriyâḥa büşram beyne yedey raḥmetih. eilâhüm me\\`a-llâh. te\\`âle-llâhü \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3223, 'emmey yebdeü-lḫalḳa ŝümme yü\\`îdühû vemey yerzüḳuküm mine-ssemâi vel\'arḍ. eilâhüm me\\`a-llâh. ḳul hâtû bürhâneküm in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3224, 'ḳul lâ ya\\`lemü men fi-ssemâvâti vel\'arḍi-lgaybe ille-llâh. vemâ yeş\\`urûne eyyâne yüb\\`aŝûn.');
INSERT INTO `tr_transliteration` VALUES (3225, 'beli-ddârake \\`ilmühüm fi-l\'âḫirah. bel hüm fî şekkim minhâ. bel hüm minhâ \\`amûn.');
INSERT INTO `tr_transliteration` VALUES (3226, 'veḳâle-lleẕîne keferû eiẕâ künnâ türâbev veâbâünâ einnâ lemuḫracûn.');
INSERT INTO `tr_transliteration` VALUES (3227, 'leḳad vu\\`idnâ hâẕâ naḥnü veâbâünâ min ḳablü in hâẕâ illâ esâṭîru-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (3228, 'ḳul sîrû fi-l\'arḍi fenżurû keyfe kâne \\`âḳibetü-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (3229, 'velâ taḥzen \\`aleyhim velâ tekün fî ḍayḳim mimmâ yemkürûn.');
INSERT INTO `tr_transliteration` VALUES (3230, 'veyeḳûlûne metâ hâẕe-lva\\`dü in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3231, 'ḳul \\`asâ ey yekûne radife leküm ba\\`ḍu-lleẕî testa\\`cilûn.');
INSERT INTO `tr_transliteration` VALUES (3232, 'veinne rabbeke leẕû faḍlin \\`ale-nnâsi velâkinne ekŝerahüm lâ yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (3233, 'veinne rabbeke leya\\`lemü mâ tükinnü ṣudûruhüm vemâ yü\\`linûn.');
INSERT INTO `tr_transliteration` VALUES (3234, 'vemâ min gâibetin fi-ssemâi vel\'arḍi illâ fî kitâbim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3235, 'inne hâẕe-lḳur\'âne yeḳuṣṣu \\`alâ benî isrâîle ekŝera-lleẕî hüm fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (3236, 'veinnehû lehüdev veraḥmetül lilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3237, 'inne rabbeke yaḳḍî beynehüm biḥukmih. vehüve-l\\`azîzü-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (3238, 'fetevekkel \\`ale-llâh. inneke \\`ale-lḥaḳḳi-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (3239, 'inneke lâ tüsmi\\`u-lmevtâ velâ tüsmi\\`u-ṣṣumme-ddü\\`âe iẕâ vellev müdbirîn.');
INSERT INTO `tr_transliteration` VALUES (3240, 'vemâ ente bihâdi-l\\`umyi \\`an ḍalâletihim. in tüsmi\\`u illâ mey yü\'minü biâyâtinâ fehüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (3241, 'veiẕâ veḳa\\`a-lḳavlü \\`aleyhim aḫracnâ lehüm dâbbetem mine-l\'arḍi tükellimühüm enne-nnâse kânû biâyâtinâ lâ yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (3242, 'veyevme naḥşüru min külli ümmetin fevcem mimmey yükeẕẕibü biâyâtinâ fehüm yûza\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3243, 'ḥattâ iẕâ câû ḳâle ekeẕẕebtüm biâyâtî velem tüḥîṭû bihâ \\`ilmen emmâẕâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3244, 'veveḳa\\`a-lḳavlü \\`aleyhim bimâ żalemû fehüm lâ yenṭiḳûn.');
INSERT INTO `tr_transliteration` VALUES (3245, 'elem yerav ennâ ce\\`alne-lleyle liyeskünû fîhi vennehâra mübṣirâ. inne fî ẕâlike leâyâtil liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3246, 'veyevme yünfeḫu fi-ṣṣûri fefezi\\`a men fi-ssemâvâti vemen fi-l\'arḍi illâ men şâe-llâh. veküllün etevhü dâḫirîn.');
INSERT INTO `tr_transliteration` VALUES (3247, 'vetera-lcibâle taḥsebühâ câmidetev vehiye temürru merra-sseḥâb. ṣun\\`a-llâhi-lleẕî etḳane külle şey\'. innehû ḫabîrum bimâ tef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (3248, 'men câe bilḥaseneti felehû ḫayrum minhâ. vehüm min feza\\`iy yevmeiẕin âminûn.');
INSERT INTO `tr_transliteration` VALUES (3249, 'vemen câe bisseyyieti fekübbet vucûhühüm fi-nnâr. hel tüczevne illâ mâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3250, 'innemâ ümirtü en a\\`büde rabbe hâẕihi-lbeldeti-lleẕî ḥarramehâ velehû küllü şey\'. veümirtü en ekûne mine-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (3251, 'veen etlüve-lḳur\'ân. femeni-htedâ feinnemâ yehtedî linefsih. vemen ḍalle feḳul innemâ ene mine-lmünẕirîn.');
INSERT INTO `tr_transliteration` VALUES (3252, 'veḳuli-lḥamdü lillâhi seyürîküm âyâtihî feta\\`rifûnehâ. vemâ rabbüke bigâfilin \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3253, 'ṭâ-sîn-mîm.');
INSERT INTO `tr_transliteration` VALUES (3254, 'tilke âyâtü-lkitâbi-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (3255, 'netlû \\`aleyke min nebei mûsâ vefir\\`avne bilḥaḳḳi liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3256, 'inne fir\\`avne \\`alâ fi-l\'arḍi vece\\`ale ehlehâ şiye\\`ay yestaḍ\\`ifü ṭâifetem minhüm yüẕebbiḥu ebnâehüm veyestaḥyî nisâehüm. innehû kâne mine-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (3257, 'venürîdü en nemünne \\`ale-lleẕîne-stuḍ\\`ifû fi-l\'arḍi venec\\`alehüm eimmetev venec\\`alehümü-lvâriŝîn.');
INSERT INTO `tr_transliteration` VALUES (3258, 'venümekkine lehüm fi-l\'arḍi venüriye fir\\`avne vehâmâne vecünûdehümâ minhüm mâ kânû yaḥẕerûn.');
INSERT INTO `tr_transliteration` VALUES (3259, 'veevḥaynâ ilâ ümmi mûsâ en arḍi\\`îh. feiẕâ ḫifti \\`aleyhi feelḳîhi fi-lyemmi velâ teḫâfî velâ taḥzenî. innâ râddûhü ileyki vecâ\\`ilûhü mine-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (3260, 'felteḳaṭahû âlü fir\\`avne liyekûne lehüm \\`adüvvev veḥazenâ. inne fir\\`avne vehâmâne vecünûdehümâ kânû ḫâṭiîn.');
INSERT INTO `tr_transliteration` VALUES (3261, 'veḳâleti-mraetü fir\\`avne ḳurratü \\`aynil lî velek. lâ taḳtülûh. \\`asâ ey yenfe\\`anâ ev netteḫiẕehû veledev vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (3262, 'veaṣbeḥa füâdü ümmi mûsâ fârigâ. in kâdet letübdî bihî levlâ er rabaṭnâ \\`alâ ḳalbihâ litekûne mine-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3263, 'veḳâlet liuḫtihî ḳuṣṣîh. febeṣurat bihî \\`an cünübiv vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (3264, 'veḥarramnâ \\`aleyhi-lmerâḍi\\`a min ḳablü feḳâlet hel edüllüküm \\`alâ ehli beytiy yekfülûnehû leküm vehüm lehû nâṣiḥûn.');
INSERT INTO `tr_transliteration` VALUES (3265, 'feradednâhü ilâ ümmihî key teḳarra \\`aynühâ velâ taḥzene velita\\`leme enne va\\`de-llâhi ḥaḳḳuv velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3266, 'velemmâ belega eşüddehû vestevâ âteynâhü ḥukmev ve\\`ilmâ. vekeẕâlike neczi-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (3267, 'vedeḫale-lmedînete \\`alâ ḥîni gafletim min ehlihâ fevecede fîhâ racüleyni yaḳtetilân. hâẕâ min şî\\`atihî vehâẕâ min \\`adüvvih. festegâŝehü-lleẕî min şî\\`atihî \\`ale-lleẕî min \\`adüvvihî fevekezehû mûsâ feḳaḍâ \\`aleyh. ḳâle hâẕâ min \\`ameli-şşeyṭân. innehû \\`adüvvüm müḍillüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (3268, 'ḳâle rabbi innî żalemtü nefsî fagfir lî fegafera leh. innehû hüve-lgafûru-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (3269, 'ḳâle rabbi bimâ en\\`amte \\`aleyye felen ekûne żahîral lilmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (3270, 'feaṣbeḥa fi-lmedîneti ḫâifey yeteraḳḳabü feiẕe-lleẕi-stenṣarahû bil\'emsi yestaṣriḫuh. ḳâle lehû mûsâ inneke legaviyyüm mübîn.');
INSERT INTO `tr_transliteration` VALUES (3271, 'felemmâ en erâde ey yebṭişe billeẕî hüve \\`adüvvül lehümâ ḳâle yâ mûsâ etürîdü en taḳtülenî kemâ ḳatelte nefsem bil\'ems. in türîdü illâ en tekûne cebbâran fi-l\'arḍi vemâ türîdü en tekûne mine-lmuṣliḥîn.');
INSERT INTO `tr_transliteration` VALUES (3272, 'vecâe racülüm min aḳṣe-lmedîneti yes\\`â. ḳâle yâ mûsâ inne-lmelee ye\'temirûne bike liyaḳtülûke faḫruc innî leke mine-nnâṣiḥîn.');
INSERT INTO `tr_transliteration` VALUES (3273, 'feḫarace minhâ ḫâifey yeteraḳḳab. ḳâle rabbi neccinî mine-lḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (3274, 'velemmâ teveccehe tilḳâe medyene ḳâle \\`asâ rabbî ey yehdiyenî sevâe-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (3275, 'velemmâ verade mâe medyene vecede \\`aleyhi ümmetem mine-nnâsi yesḳûn. vevecede min dûnihimü-mraeteyni teẕûdân. ḳâle mâ ḫaṭbükümâ. ḳâletâ lâ nesḳî ḥattâ yuṣdira-rri\\`âü veebûnâ şeyḫun kebîr.');
INSERT INTO `tr_transliteration` VALUES (3276, 'feseḳâ lehümâ ŝümme tevellâ ile-żżilli feḳâle rabbi innî limâ enzelte ileyye min ḫayrin feḳîr.');
INSERT INTO `tr_transliteration` VALUES (3277, 'fecâethü iḥdâhümâ temşî \\`ale-stiḥyâ\'. ḳâlet inne ebî yed\\`ûke liyecziyeke ecra mâ seḳayte lenâ. felemmâ câehû veḳaṣṣa \\`aleyhi-lḳaṣaṣa ḳâle lâ teḫaf. necevte mine-lḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (3278, 'ḳâlet iḥdâhümâ yâ ebeti-ste\'cirh. inne ḫayra meni-ste\'certe-lḳaviyyü-l\'emîn.');
INSERT INTO `tr_transliteration` VALUES (3279, 'ḳâle innî ürîdü en ünkiḥake iḥde-bneteyye hâteyni \\`alâ en te\'cüranî ŝemâniye ḥicec. fein etmemte \\`aşran femin \\`indik. vemâ ürîdü en eşüḳḳa \\`aleyk. setecidünî in şâe-llâhü mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (3280, 'ḳâle ẕâlike beynî vebeynek. eyyeme-l\'eceleyni ḳaḍaytü felâ \\`udvâne \\`aleyy. vellâhü \\`alâ mâ neḳûlü vekîl.');
INSERT INTO `tr_transliteration` VALUES (3281, 'felemmâ ḳaḍâ mûse-l\'ecele vesâra biehlihî ânese min cânibi-ṭṭûri nârâ. ḳâle liehlihi-mküŝû innî ânestü nâral le\\`allî âtîküm minhâ biḫaberin ev ceẕvetim mine-nnâri le\\`alleküm taṣṭalûn.');
INSERT INTO `tr_transliteration` VALUES (3282, 'felemmâ etâhâ nûdiye min şâṭii-lvâdi-l\'eymeni fi-lbuḳ\\`ati-lmübâraketi mine-şşecerati ey yâ mûsâ innî ene-llâhü rabbü-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3283, 'veen elḳi \\`aṣâk. felemmâ raâhâ tehtezzü keennehâ cânnüv vellâ müdbirav velem yü\\`aḳḳib. yâ mûsâ aḳbil velâ teḫaf. inneke mine-l\'âminîn.');
INSERT INTO `tr_transliteration` VALUES (3284, 'üslük yedeke fî ceybike taḫruc beyḍâe min gayri sû\'. vaḍmüm ileyke cenâḥake mine-rrahbi feẕânike bürhânâni mir rabbike ilâ fir\\`avne vemeleih. innehüm kânû ḳavmen fâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3285, 'ḳâle rabbi innî ḳateltü minhüm nefsen feeḫâfü ey yaḳtülûn.');
INSERT INTO `tr_transliteration` VALUES (3286, 'veeḫî hârûnü hüve efṣaḥu minnî lisânen feersilhü me\\`iye rid\'ey yüṣaddiḳunî. innî eḫâfü ey yükeẕẕibûn.');
INSERT INTO `tr_transliteration` VALUES (3287, 'ḳâle seneşüddü \\`aḍudeke bieḫîke venec\\`alü lekümâ sülṭânen felâ yeṣilûne ileykümâ biâyâtinâ. entümâ vemeni-ttebe\\`aküme-lgâlibûn.');
INSERT INTO `tr_transliteration` VALUES (3288, 'felemmâ câehüm mûsâ biâyâtinâ beyyinâtin ḳâlû mâ hâẕâ illâ siḥrum müfterav vemâ semi\\`nâ bihâẕâ fî âbâine-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (3289, 'veḳâle mûsâ rabbî a\\`lemü bimen câe bilhüdâ min \\`indihî vemen tekûnü lehû \\`âḳibetü-ddâr. innehû lâ yüfliḥu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (3290, 'veḳâle fir\\`avnü yâ eyyühe-lmeleü mâ \\`alimtü leküm min ilâhin gayrî. feevḳid lî yâ hâmânü \\`ale-ṭṭîni fec\\`al lî ṣarḥal le\\`allî eṭṭali\\`u ilâ ilâhi mûsâ veinnî leeżunnühû mine-lkâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (3291, 'vestekbera hüve vecünûdühû fi-l\'arḍi bigayri-lḥaḳḳi veżannû ennehüm ileynâ lâ yürce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3292, 'feeḫaẕnâhü vecünûdehû fenebeẕnâhüm fi-lyemm. fenżur keyfe kâne \\`âḳibetu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (3293, 'vece\\`alnâhüm eimmetey yed\\`ûne ile-nnâr. veyevme-lḳiyâmeti lâ yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (3294, 'veetba\\`nâhüm fî hâẕihi-ddünyâ la\\`neten. veyevme-lḳiyâmeti hüm mine-lmaḳbûḥîn.');
INSERT INTO `tr_transliteration` VALUES (3295, 'veleḳad âteynâ mûse-lkitâbe mim ba\\`di mâ ehlekne-lḳurûne-l\'ûlâ beṣâira linnâsi vehüdev veraḥmetel le\\`allehüm yeteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (3296, 'vemâ künte bicânibi-lgarbiyyi iẕ ḳaḍaynâ ilâ mûse-l\'emra vemâ künte mine-şşâhidîn.');
INSERT INTO `tr_transliteration` VALUES (3297, 'velâkinnâ enşe\'nâ ḳurûnen feteṭâvele \\`aleyhimü-l\\`umür. vemâ künte ŝâviyen fî ehli medyene tetlû \\`aleyhim âyâtinâ velâkinnâ künnâ mürsilîn.');
INSERT INTO `tr_transliteration` VALUES (3298, 'vemâ künte bicânibi-ṭṭûri iẕ nâdeynâ velâkir raḥmetem mir rabbike litünẕira ḳavmem mâ etâhüm min neẕîrim min ḳablike le\\`allehüm yeteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (3299, 'velevlâ en tüṣîbehüm müṣîbetüm bimâ ḳaddemet eydîhim feyeḳûlû rabbenâ levlâ erselte ileynâ rasûlen fenettebi\\`a âyâtike venekûne mine-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3300, 'felemmâ câehümü-lḥaḳḳu min \\`indinâ ḳâlû levlâ ûtiye miŝle mâ ûtiye mûsâ. evelem yekfürû bimâ ûtiye mûsâ min ḳabl. ḳâlû siḥrâni teżâherâ. veḳâlû innâ biküllin kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (3301, 'ḳul fe\'tû bikitâbim min \\`indi-llâhi hüve ehdâ minhümâ ettebi\\`hü in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3302, 'feil lem yestecîbû leke fa\\`lem ennemâ yettebi\\`ûne ehvâehüm. vemen eḍallü mimmeni-ttebe\\`a hevâhü bigayri hüdem mine-llâh. inne-llâhe lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (3303, 'veleḳad veṣṣalnâ lehümü-lḳavle le\\`allehüm yeteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (3304, 'elleẕîne âteynâhümü-lkitâbe min ḳablihî hüm bihî yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3305, 'veiẕâ yütlâ \\`aleyhim ḳâlû âmennâ bihî innehü-lḥaḳḳu mir rabbinâ innâ künnâ min ḳablihî müslimîn.');
INSERT INTO `tr_transliteration` VALUES (3306, 'ülâike yü\'tevne ecrahüm merrateyni bimâ ṣaberû veyedraûne bilḥaseneti-sseyyiete vemimmâ razaḳnâhüm yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (3307, 'veiẕâ semi\\`ü-llagve a\\`raḍû \\`anhü veḳâlû lenâ a\\`mâlünâ veleküm a\\`mâlüküm. selâmün \\`aleyküm. lâ nebtegi-lcâhilîn.');
INSERT INTO `tr_transliteration` VALUES (3308, 'inneke lâ tehdî men aḥbebte velâkinne-llâhe yehdî mey yeşâ\'. vehüve a\\`lemü bilmühtedîn.');
INSERT INTO `tr_transliteration` VALUES (3309, 'veḳâlû in nettebi\\`i-lhüdâ me\\`ake nüteḫattef min arḍinâ. evelem nümekkil lehüm ḥaramen âminey yücbâ ileyhi ŝemerâtü külli şey\'ir rizḳam mil ledünnâ velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3310, 'vekem ehleknâ min ḳaryetim beṭirat me\\`îşetehâ. fetilke mesâkinühüm lem tüskem mim ba\\`dihim illâ ḳalîlâ. vekünnâ naḥnü-lvâriŝîn.');
INSERT INTO `tr_transliteration` VALUES (3311, 'vemâ kâne rabbüke mühlike-lḳurâ ḥattâ yeb\\`aŝe fî ümmihâ rasûley yetlû \\`aleyhim âyâtinâ. vemâ künnâ mühliki-lḳurâ illâ veehlühâ żâlimûn.');
INSERT INTO `tr_transliteration` VALUES (3312, 'vemâ ûtîtüm min şey\'in femetâ\\`u-lḥayâti-ddünyâ vezînetühâ. vemâ \\`inde-llâhi ḫayruv veebḳâ. efelâ ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (3313, 'efemev ve\\`adnâhü va\\`den ḥasenen fehüve lâḳîhi kemem metta\\`nâhü metâ\\`a-lḥayâti-ddünyâ ŝümme hüve yevme-lḳiyâmeti mine-lmuḥḍarîn.');
INSERT INTO `tr_transliteration` VALUES (3314, 'veyevme yünâdîhim feyeḳûlü eyne şürakâiye-lleẕîne küntüm tez\\`umûn.');
INSERT INTO `tr_transliteration` VALUES (3315, 'ḳâle-lleẕîne ḥaḳḳa \\`aleyhimü-lḳavlü rabbenâ hâülâi-lleẕîne agveynâ. agveynâhüm kemâ gaveynâ. teberra\'nâ ileyk. mâ kânû iyyânâ ya\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (3316, 'veḳîle-d\\`û şürakâeküm fede\\`avhüm felem yestecîbû lehüm veraevü-l\\`aẕâb. lev ennehüm kânû yehtedûn.');
INSERT INTO `tr_transliteration` VALUES (3317, 'veyevme yünâdîhim feyeḳûlü mâẕâ ecebtümü-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (3318, 'fe\\`amiyet \\`aleyhimü-l\'embâü yevmeiẕin fehüm lâ yetesâelûn.');
INSERT INTO `tr_transliteration` VALUES (3319, 'feemmâ men tâbe veâmene ve\\`amile ṣâliḥan fe\\`asâ ey yekûne mine-lmüfliḥîn.');
INSERT INTO `tr_transliteration` VALUES (3320, 'verabbüke yaḫlüḳu mâ yeşâü veyaḫtâr. mâ kâne lehümü-lḫiyerah. sübḥâne-llâhi vete\\`âlâ \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3321, 'verabbüke ya\\`lemü mâ tükinnü ṣudûruhüm vemâ yü\\`linûn.');
INSERT INTO `tr_transliteration` VALUES (3322, 'vehüve-llâhü lâ ilâhe illâ hû. lehü-lḥamdü fi-l\'ûlâ vel\'âḫirah. velehü-lḥukmü veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3323, 'ḳul era\'eytüm in ce\\`ale-llâhü \\`aleykümü-lleyle sermeden ilâ yevmi-lḳiyâmeti men ilâhün gayru-llâhi ye\'tîküm biḍiyâ\'. efelâ tesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3324, 'ḳul era\'eytüm in ce\\`ale-llâhü \\`aleykümü-nnehâra sermeden ilâ yevmi-lḳiyâmeti men ilâhün gayru-llâhi ye\'tîküm bileylin teskünûne fîh. efelâ tübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (3325, 'vemir raḥmetihî ce\\`ale lekümü-lleyle vennehâra liteskünû fîhi velitebtegû min faḍlihî vele\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (3326, 'veyevme yünâdîhim feyeḳûlü eyne şürakâiye-lleẕîne küntüm tez\\`umûn.');
INSERT INTO `tr_transliteration` VALUES (3327, 'veneza\\`nâ min külli ümmetin şehîden feḳulnâ hâtû bürhâneküm fe\\`alimû enne-lḥaḳḳa lillâhi veḍalle \\`anhüm mâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (3328, 'inne ḳârûne kâne min ḳavmi mûsâ febegâ \\`aleyhim. veâteynâhü mine-lkünûzi mâ inne mefâtiḥahû letenûü bil\\`uṣbeti üli-lḳuvveh. iẕ ḳâle lehû ḳavmühû lâ tefraḥ inne-llâhe lâ yüḥibbü-lferiḥîn.');
INSERT INTO `tr_transliteration` VALUES (3329, 'vebtegi fîmâ âtâke-llâhü-ddâra-l\'âḫirate velâ tense neṣîbeke mine-ddünyâ veaḥsin kemâ aḥsene-llâhü ileyke velâ tebgi-lfesâde fi-l\'arḍ. inne-llâhe lâ yüḥibbü-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (3330, 'ḳâle innemâ ûtîtühû \\`alâ \\`ilmin \\`indî. evelem ya\\`lem enne-llâhe ḳad ehleke min ḳablihî mine-lḳurûni men hüve eşeddü minhü ḳuvvetev veekŝeru cem\\`â. velâ yüs\'elü \\`an ẕünûbihimü-lmücrimûn.');
INSERT INTO `tr_transliteration` VALUES (3331, 'feḫarace \\`alâ ḳavmihî fî zînetih. ḳâle-lleẕîne yürîdûne-lḥayâte-ddünyâ yâ leyte lenâ miŝle mâ ûtiye ḳârûnü innehû leẕû ḥażżin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (3332, 'veḳâle-lleẕîne ûtü-l\\`ilme veyleküm ŝevâbü-llâhi ḫayrul limen âmene ve\\`amile ṣâliḥâ. velâ yüleḳḳâhâ ille-ṣṣâbirûn.');
INSERT INTO `tr_transliteration` VALUES (3333, 'feḫasefnâ bihî vebidârihi-l\'arḍa femâ kâne lehû min fietiy yenṣurûnehû min dûni-llâh. vemâ kâne mine-lmünteṣirîn.');
INSERT INTO `tr_transliteration` VALUES (3334, 'veaṣbeḥa-lleẕîne temennev mekânehû bil\'emsi yeḳûlûne veykeenne-llâhe yebsüṭu-rrizḳa limey yeşâü min \\`ibâdihî veyaḳdir. levlâ em menne-llâhü \\`aleynâ leḫasefe binâ. veykeennehû lâ yüfliḥu-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (3335, 'tilke-ddâru-l\'âḫiratü nec\\`alühâ lilleẕîne lâ yürîdûne \\`ulüvven fi-l\'arḍi velâ fesâdâ. vel\\`âḳibetü lilmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (3336, 'men câe bilḥaseneti felehû ḫayrum minhâ. vemen câe bisseyyieti felâ yücze-lleẕîne \\`amilü-sseyyiâti illâ mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3337, 'inne-lleẕî feraḍa \\`aleyke-lḳur\'âne lerâddüke ilâ me\\`âd. ḳur rabbî a\\`lemü men câe bilhüdâ vemen hüve fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3338, 'vemâ künte tercû ey yülḳâ ileyke-lkitâbü illâ raḥmetem mir rabbike felâ tekûnenne żahîral lilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (3339, 'velâ yeṣuddünneke \\`an âyâti-llâhi ba\\`de iẕ ünzilet ileyke ved\\`u ilâ rabbike velâ tekûnenne mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (3340, 'velâ ted\\`u me\\`a-llâhi ilâhen âḫar. lâ ilâhe illâ hû. küllü şey\'in hâlikün illâ vecheh. lehü-lḥukmü veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3341, 'elif-lâm-mîm.');
INSERT INTO `tr_transliteration` VALUES (3342, 'eḥasibe-nnâsü ey yütrakû ey yeḳûlû âmennâ vehüm lâ yüftenûn.');
INSERT INTO `tr_transliteration` VALUES (3343, 'veleḳad fetenne-lleẕîne min ḳablihim feleya\\`lemenne-llâhü-lleẕîne ṣadeḳû veleya\\`lemenne-lkâẕibîn.');
INSERT INTO `tr_transliteration` VALUES (3344, 'em ḥasibe-lleẕîne ya\\`melûne-sseyyiâti ey yesbiḳûnâ. sâe mâ yaḥkümûn.');
INSERT INTO `tr_transliteration` VALUES (3345, 'men kâne yercû liḳâe-llâhi feinne ecele-llâhi leâtin. vehüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (3346, 'vemen câhede feinnemâ yücâhidü linefsih. inne-llâhe leganiyyün \\`ani-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3347, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lenükeffiranne \\`anhüm seyyiâtihim velenecziyennehüm aḥsene-lleẕî kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3348, 'veveṣṣayne-l\'insâne bivâlideyhi ḥusnâ. vein câhedâke litüşrike bî mâ leyse leke bihî \\`ilmün felâ tüṭi\\`hümâ. ileyye merci\\`uküm feünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3349, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lenüdḫilennehüm fi-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (3350, 'vemine-nnâsi mey yeḳûlü âmennâ billâhi feiẕâ ûẕiye fi-llâhi ce\\`ale fitnete-nnâsi ke\\`aẕâbi-llâh. velein câe naṣrum mir rabbike leyeḳûlünne innâ künnâ me\\`aküm. eveleyse-llâhü bia\\`leme bimâ fî ṣudûri-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3351, 'veleya\\`lemenne-llâhü-lleẕîne âmenû veleya\\`lemenne-lmünâfiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3352, 'veḳâle-lleẕîne keferû lilleẕîne âmenü-ttebi\\`û sebîlenâ velnaḥmil ḫaṭâyâküm. vemâ hüm biḥâmilîne min ḫaṭâyâhüm min şey\'. innehüm lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (3353, 'veleyaḥmilünne eŝḳâlehüm eŝḳâlem me\\`a eŝḳâleküm. veleyüs\'elünne yevme-lḳiyâmeti \\`ammâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (3354, 'veleḳad erselnâ nûḥan ilâ ḳavmihî felebiŝe fîhim elfe senetin illâ ḫamsîne \\`âmâ. feeḫaẕehümu-ṭṭûfânü vehüm żâlimûn.');
INSERT INTO `tr_transliteration` VALUES (3355, 'feenceynâhü veaṣḥâbe-ssefîneti vece\\`alnâhâ âyetel lil\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3356, 'veibrâhime iẕ ḳâle liḳavmihi-\\`büdü-llâhe vetteḳûh. ẕâliküm ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3357, 'innemâ ta\\`büdûne min dûni-llâhi evŝânev vetaḫluḳûne ifkâ. inne-lleẕîne ta\\`büdûne min dûni-llâhi lâ yemlikûne leküm rizḳan febtegû \\`inde-llâhi-rrizḳa va\\`büdûhü veşkürû leh. ileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3358, 'vein tükeẕẕibû feḳad keẕẕebe ümemüm min ḳabliküm. vemâ \\`ale-rrasûli ille-lbelâgu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (3359, 'evelem yerav keyfe yübdiü-llâhü-lḫalḳa ŝümme yü\\`îdüh. inne ẕâlike \\`ale-llâhi yesîr.');
INSERT INTO `tr_transliteration` VALUES (3360, 'ḳul sîrû fi-l\'arḍi fenżurû keyfe bedee-lḫalḳa ŝümme-llâhü yünşiü-nneş\'ete-l\'âḫirah. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (3361, 'yü\\`aẕẕibü mey yeşâü veyerḥamü mey yeşâ\'. veileyhi tuḳlebûn.');
INSERT INTO `tr_transliteration` VALUES (3362, 'vemâ entüm bimü\\`cizîne fi-l\'arḍi velâ fi-ssemâ\'. vemâ leküm min dûni-llâhi miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (3363, 'velleẕîne keferû biâyâti-llâhi veliḳâihî ülâike yeisû mir raḥmetî veülâike lehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (3364, 'femâ kâne cevâbe ḳavmihî illâ en ḳâlu-ḳtülûhü ev ḥarriḳûhü feencâhü-llâhü mine-nnâr. inne fî ẕâlike leâyâtil liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3365, 'veḳâle inneme-tteḫaẕtüm min dûni-llâhi evŝânem meveddete beyniküm fi-lḥayâti-ddünyâ. ŝümme yevme-lḳiyâmeti yekfüru ba\\`ḍuküm biba\\`ḍiv veyel\\`anü ba\\`ḍuküm ba\\`ḍâ. veme\'vâküm-nnâru vemâ leküm min nâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (3366, 'feâmene lehû lûṭ. veḳâle innî mühâcirun ilâ rabbî. innehû hüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3367, 'vevehebnâ lehû isḥâḳa veya\\`ḳûbe vece\\`alnâ fî ẕürriyyetehü-nnübüvvete velkitâbe veâteynâhü ecrahû fi-ddünyâ. veinnehû fi-l\'âḫirati lemine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (3368, 'velûṭan iẕ ḳâle liḳavmihî inneküm lete\'tûne-lfâḥişeh. mâ sebeḳaküm bihâ min eḥadim mine-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3369, 'einneküm lete\'tûne-rricâle vetaḳṭa\\`ûne-ssebîle vete\'tûne fî nâdîkümü-lmünker. femâ kâne cevâbe ḳavmihî illâ en ḳâlü-\'tinâ bi\\`aẕâbi-llâhi in künte mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3370, 'ḳâle rabbi-nṣurnî \\`ale-lḳavmi-lmüfsidîn.');
INSERT INTO `tr_transliteration` VALUES (3371, 'velemmâ câet rusülünâ ibrâhîme bilbüşrâ ḳâlû innâ mühlikû ehli hâẕihi-lḳaryeh. inne ehlehâ kânû żâlimîn.');
INSERT INTO `tr_transliteration` VALUES (3372, 'ḳâle inne fîhâ lûṭâ. ḳâlû naḥnü a\\`lemü bimen fîhâ. lenünecciyennehû veehlehû ille-mraeteh. kânet mine-lgâbirîn.');
INSERT INTO `tr_transliteration` VALUES (3373, 'velemmâ en câet rusülünâ lûṭan sîe bihim veḍâḳa bihim ẕer\\`av veḳâlû lâ teḫaf velâ taḥzen. innâ müneccûke veehleke ille-mraeteke kânet mine-lgâbirîn.');
INSERT INTO `tr_transliteration` VALUES (3374, 'innâ münzilûne \\`alâ ehli hâẕihi-lḳaryeti riczem mine-ssemâi bimâ kânû yefsüḳûn.');
INSERT INTO `tr_transliteration` VALUES (3375, 'veleḳat teraknâ minhâ âyetem beyyinetel liḳavmiy ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (3376, 'veilâ medyene eḫâhüm şu\\`ayben feḳâle yâ ḳavmi-\\`büdü-llâhe veercü-lyevme-l\'âhira velâ ta\\`ŝev fi-l\'arḍi müfsidîn.');
INSERT INTO `tr_transliteration` VALUES (3377, 'fekeẕẕebûhü feeḫaẕethümü-rracfetü feaṣbeḥû fî dârihim câŝimîn.');
INSERT INTO `tr_transliteration` VALUES (3378, 've\\`âdev veŝemûde veḳat tebeyyene leküm mim mesâkinihim. vezeyyene lehümü-şşeyṭânü a\\`mâlehüm feṣaddehüm \\`ani-ssebîli vekânû müstebṣirîn.');
INSERT INTO `tr_transliteration` VALUES (3379, 'veḳârûne vefir\\`avne vehâmâne veleḳad câehüm mûsâ bilbeyyinâti festekberû fi-l\'arḍi vemâ kânû sâbiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3380, 'feküllen eḫaẕnâ biẕembih. feminhüm men erselnâ \\`aleyhi ḥâṣibâ. veminhüm men eḫaẕethu-ṣṣayḥah. veminhüm men ḫasefnâ bihi-l\'arḍ. veminhüm men agraḳnâ. vemâ kâne-llâhü liyażlimehüm velâkin kânû enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (3381, 'meŝelü-lleẕîne-tteḫaẕû min dûni-llâhi evliyâe kemeŝeli-l\\`nkebût. itteḫaẕet beytâ. veinne evhene-lbüyûti lebeytü-l\\`nkebût. lev kânû ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3382, 'inne-llâhe ya\\`lemü mâ yed\\`ûne min dûnihî min şey\'. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3383, 'vetilke-l\'emŝâlü naḍribühâ linnâs. vemâ ya\\`ḳilühâ ille-l\\`âlimûn.');
INSERT INTO `tr_transliteration` VALUES (3384, 'ḫaleḳa-llâhü-ssemâvâti vel\'arḍa bilḥaḳḳ. inne fî ẕâlike leâyetel lilmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3385, 'ütlü mâ ûḥiye ileyke mine-lkitâbi veeḳimi-ṣṣalâh. inne-ṣṣalâte tenhâ \\`ani-lfaḥşâi velmünker. veleẕikru-llâhi ekber. vellâhü ya\\`lemü mâ taṣne\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3386, 'velâ tücâdilû ehle-lkitâbi illâ billetî hiye aḥsen. ille-lleẕîne żalemû minhüm veḳûlû âmennâ billeẕî ünzile ileynâ veünzile ileyküm veileyhâ veilâhüküm vâḥidüv venaḥnü lehû müslimûn.');
INSERT INTO `tr_transliteration` VALUES (3387, 'vekeẕâlike enzelnâ ileyke-lkitâb. felleẕîne âteynâhümü-lkitâbe yü\'minûne bih. vemin hâülâi mey yü\'minü bih. vemâ yecḥadü biâyâtinâ ille-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (3388, 'vemâ künte tetlû min ḳablihî min kitâbiv velâ teḫuṭṭuhû biyemînike iẕel lertâbe-lmübṭilûn.');
INSERT INTO `tr_transliteration` VALUES (3389, 'bel hüve âyâtüm beyyinâtün fî ṣudûri-lleẕîne ûtü-l\\`ilm. vemâ yecḥadü biâyâtinâ ille-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (3390, 'veḳâlû levlâ ünzile \\`aleyhi âyâtüm mir rabbih. ḳul inneme-l\'âyâtü \\`inde-llâh. veinnemâ ene neẕîrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (3391, 'evelem yekfihim ennâ enzelnâ \\`aleyke-lkitâbe yütlâ \\`aleyhim. inne fî ẕâlike leraḥmetev veẕikrâ liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3392, 'ḳul kefâ billâhi beynî vebeyneküm şehîdâ. ya\\`lemü mâ fi-ssemâvâti vel\'arḍ. velleẕîne âmenû bilbâṭili vekeferû billâhi ülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (3393, 'veyesta\\`cilûneke bil\\`aẕâb. velevlâ ecelüm müsemmel lecâehüm-l\\`aẕâb. veleye\'tiyennehüm bagtetev vehüm lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (3394, 'yesta\\`cilûneke bil\\`aẕâb. veinne cehenneme lemüḥîṭatüm bilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (3395, 'yevme yagşâhümü-l\\`aẕâbü min fevḳihim vemin taḥti ercülihim veyeḳûlü ẕûḳû mâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3396, 'yâ \\`ibâdîye-lleẕîne âmenû inne arḍî vâsi\\`aten feiyyâye fa\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (3397, 'küllü nefsin ẕâiḳatü-lmevti ŝümme ileynâ türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3398, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lenübevviennehüm mine-lcenneti gurafen tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. ni\\`me ecru-l\\`âmilîn.');
INSERT INTO `tr_transliteration` VALUES (3399, 'elleẕîne ṣaberû ve\\`alâ rabbihim yetevekkelûn.');
INSERT INTO `tr_transliteration` VALUES (3400, 'vekeeyyim min dâbbetil lâ taḥmilü rizḳahâ. allâhü yerzüḳuhâ veiyyâküm. vehüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (3401, 'velein seeltehüm men ḫaleḳa-ssemâvâti vel\'arḍa veseḫḫara-şşemse velḳamera leyeḳûlünne-llâh. feennâ yü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (3402, 'allâhü yebsüṭu-rrizḳa limey yeşâü min \\`ibâdihî veyaḳdiru leh. inne-llâhe bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (3403, 'velein seeltehüm men nezzele mine-ssemâi mâen feaḥyâ bihi-l\'arḍa mim ba\\`di mevtihâ leyeḳûlünne-llâh. ḳuli-lḥamdü lillâh. bel ekŝeruhüm lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (3404, 'vemâ hâẕihi-lḥayâtü-ddünyâ illâ lehvüv vele\\`ib. veinne-ddâra-l\'âḫirate lehiye-lḥayevân. lev kânû ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3405, 'feiẕâ rakibû fi-lfülki de\\`avu-llâhe muḫliṣîne lehü-ddîn. felemmâ neccâhüm ile-lberri iẕâ hüm yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3406, 'liyekfürû bimâ âteynâhüm veliyetemette\\`û. fesevfe ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3407, 'evelem yerav ennâ ce\\`alnâ ḥaramen âminev veyüteḫaṭṭafü-nnâsü min ḥavlehüm. efebilbâṭili yü\'minûne vebini\\`meti-llâhi yekfürûn.');
INSERT INTO `tr_transliteration` VALUES (3408, 'vemen ażlemü mimmeni-fterâ \\`ale-llâhi keẕiben ev keẕẕebe bilḥaḳḳi lemmâ câeh. eleyse fî cehenneme meŝvel lilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (3409, 'velleẕîne câhedû fînâ lenehdiyennehüm sübülenâ. veinne-llâhe leme\\`a-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (3410, 'elif-lâm-mîm.');
INSERT INTO `tr_transliteration` VALUES (3411, 'gulibeti-rrûm.');
INSERT INTO `tr_transliteration` VALUES (3412, 'fî edne-l\'arḍi vehüm mim ba\\`di galebihim seyaglibûn.');
INSERT INTO `tr_transliteration` VALUES (3413, 'fî biḍ\\`i sinîn. lillâhi-l\'emru min ḳablü vemim ba\\`d. veyevmeiẕiy yefraḥu-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3414, 'binaṣri-llâh. yenṣuru mey yeşâ\'. vehüve-l\\`azîzü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (3415, 'va\\`de-llâh. lâ yuḫlifü-llâhü va\\`dehû velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3416, 'ya\\`lemûne żâhiram mine-lḥayâti-ddünyâ. vehüm \\`ani-l\'âḫirati hüm gâfilûn.');
INSERT INTO `tr_transliteration` VALUES (3417, 'evelem yetefekkerû fî enfüsihim. mâ ḫaleḳa-llâhü-ssemâvâti vel\'arḍa vemâ beynehümâ illâ bilḥaḳḳi veecelim müsemmâ. veinne keŝîram mine-nnâsi biliḳâi rabbihim lekâfirûn.');
INSERT INTO `tr_transliteration` VALUES (3418, 'evelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne \\`âḳibetü-lleẕîne min ḳablihim. kânû eşedde minhüm ḳuvvetev veeŝârü-l\'arḍa ve\\`amerûhâ ekŝera mimmâ \\`amerûhâ vecâethüm rusülühüm bilbeyyinât. femâ kâne-llâhü liyażlimehüm velâkin kânû enfüsehüm yażlimûn.');
INSERT INTO `tr_transliteration` VALUES (3419, 'ŝümme kâne \\`âḳibete-lleẕîne esâü-ssûâ en keẕẕebû biâyâti-llâhi vekânû bihâ yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (3420, 'allâhü yebdeü-lḫalḳa ŝümme yü\\`îdühû ŝümme ileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3421, 'veyevme teḳûmü-ssâ\\`atü yüblisü-lmücrimûn.');
INSERT INTO `tr_transliteration` VALUES (3422, 'velem yekül lehüm min şürakâihim şüf\\`âü vekânû bişürakâihim kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (3423, 'veyevme teḳûmü-ssâ\\`atü yevmeiẕiy yeteferraḳûn.');
INSERT INTO `tr_transliteration` VALUES (3424, 'feemme-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti fehüm fî ravḍatey yuḥberûn.');
INSERT INTO `tr_transliteration` VALUES (3425, 'veemme-lleẕîne keferû vekeẕẕebû biâyâtinâ veliḳâi-l\'âḫirati feülâike fi-l\\`aẕâbi muḥḍarûn.');
INSERT INTO `tr_transliteration` VALUES (3426, 'fesübḥâne-llâhi ḥîne tümsûne veḥîne tuṣbiḥûn.');
INSERT INTO `tr_transliteration` VALUES (3427, 'velehü-lḥamdü fi-ssemâvâti vel\'arḍi ve\\`aşiyyev veḥîne tużhirûn.');
INSERT INTO `tr_transliteration` VALUES (3428, 'yuḫricü-lḥayye mine-lmeyyiti veyuḫricü-lmeyyite mine-lḥayyi veyuḥyi-l\'arḍa ba\\`de mevtihâ. vekeẕâlike tuḫracûn.');
INSERT INTO `tr_transliteration` VALUES (3429, 'vemin âyâtihî en ḫaleḳaküm min türâbin ŝümme iẕâ entüm beşerun tenteşirûn.');
INSERT INTO `tr_transliteration` VALUES (3430, 'vemin âyâtihî en ḫaleḳa leküm min enfüsiküm ezvâcel liteskünû ileyhâ vece\\`ale beyneküm meveddetev veraḥmeh. inne fî ẕâlike leâyâtil liḳavmiy yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (3431, 'vemin âyâtihî ḫalḳu-ssemâvâti vel\'arḍi vaḫtilâfü elsinetiküm veelvâniküm. inne fî ẕâlike leâyâtil lil\'\\`âlimîn.');
INSERT INTO `tr_transliteration` VALUES (3432, 'vemin âyâtihî menâmüküm billeyli vennehâri vebtigâüküm min faḍlih. inne fî ẕâlike leâyâtil liḳavmiy yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3433, 'vemin âyâtihî yürîkümü-lberḳa ḫavfev veṭame\\`av veyünezzilü mine-ssemâi mâen feyuḥyî bihi-l\'arḍa ba\\`de mevtihâ. inne fî ẕâlike leâyâtil liḳavmiy ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (3434, 'vemin âyâtihî en teḳûme-ssemâü vel\'arḍu biemrih. ŝümme iẕâ de\\`âküm da\\`vetem mine-l\'arḍi iẕâ entüm tuḫracûn.');
INSERT INTO `tr_transliteration` VALUES (3435, 'velehû men fi-ssemâvâti vel\'arḍ. küllül lehû ḳânitûn.');
INSERT INTO `tr_transliteration` VALUES (3436, 'vehüve-lleẕî yebdeü-lḫalḳa ŝümme yü\\`îdühû vehüve ehvenü \\`aleyh. velehü-lmeŝelü-l\'a\\`lâ fi-ssemâvâti vel\'arḍ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3437, 'ḍarabe leküm meŝelem min enfüsiküm. hel leküm mim mâ meleket eymânüküm min şürakâe fî mâ razaḳnâküm feentüm fîhi sevâün teḫâfûnehüm keḫîfetiküm enfüseküm. keẕâlike nüfeṣṣilü-l\'âyâti liḳavmiy ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (3438, 'beli-ttebe\\`a-lleẕîne żalemû ehvâehüm bigayri \\`ilmin. femey yehdî men eḍalle-llâh. vemâ lehüm min nâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (3439, 'feeḳim vecheke lildîni ḥanîfâ. fiṭrate-llâhi-lletî feṭara-nnâse \\`aleyhâ. lâ tebdîle liḫalḳi-llâh. ẕâlike-ddînü-lḳayyim. velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3440, 'münîbîne ileyhi vetteḳûhü veeḳîmu-ṣṣalâte velâ tekûnû mine-lmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (3441, 'mine-lleẕîne ferraḳû dînehüm vekânû şiye\\`â. küllü ḥizbim bimâ ledeyhim feriḥûn.');
INSERT INTO `tr_transliteration` VALUES (3442, 'veiẕâ messe-nnâse ḍurrun de\\`av rabbehüm münîbîne ileyhi ŝümme iẕâ eẕâḳahüm minhü raḥmeten iẕâ ferîḳum minhüm birabbihim yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3443, 'liyekfürû bimâ âteynâhüm. fetemette\\`û. fesevfe ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3444, 'em enzelnâ \\`aleyhim sülṭânen fehüve yetekellemü bimâ kânû bihî yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3445, 'veiẕâ eẕaḳne-nnâse raḥmeten feriḥû bihâ. vein tüṣibhüm seyyietüm bimâ ḳaddemet eydîhim iẕâ hüm yaḳneṭûn.');
INSERT INTO `tr_transliteration` VALUES (3446, 'evelem yerav enne-llâhe yebsüṭu-rrizḳa limey yeşâü veyaḳdir. inne fî ẕâlike leâyâtil liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3447, 'feâti ẕe-lḳurbâ ḥaḳḳahû velmiskîne vebne-ssebîl. ẕâlike ḫayrul lilleẕîne yürîdûne veche-llâh. veülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (3448, 'vemâ âteytüm mir ribel liyerbüve fî emvâli-nnâsi felâ yerbû \\`inde-llâh. vemâ âteytüm min zekâtin türîdûne veche-llâhi feülâike hümü-lmuḍ\\`ifûn.');
INSERT INTO `tr_transliteration` VALUES (3449, 'allâhü-lleẕî ḫaleḳaküm ŝümme razeḳaküm ŝümme yümîtüküm ŝümme yuḥyîküm. hel min şürakâiküm mey yef\\`alü min ẕâliküm min şey\'. sübḥânehû vete\\`âlâ \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (3450, 'żahera-lfesâdü fi-lberri velbaḥri bimâ kesebet eydi-nnâsi liyüẕîḳahüm ba\\`ḍa-lleẕî \\`amilû le\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3451, 'ḳul sîrû fi-l\'arḍi fenżurû keyfe kâne \\`âḳibetü-lleẕîne min ḳabl. kâne ekŝeruhüm müşrikîn.');
INSERT INTO `tr_transliteration` VALUES (3452, 'feeḳim vecheke lildîni-lḳayyimi min ḳabli ey ye\'tiye yevmül lâ meradde lehû mine-llâhi yevmeiẕiy yeṣṣadde\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3453, 'men kefera fe\\`aleyhi küfruh. vemen \\`amile ṣâliḥan felienfüsihim yemhedûn.');
INSERT INTO `tr_transliteration` VALUES (3454, 'liyecziye-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti min faḍlih. innehû lâ yüḥibbü-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (3455, 'vemin âyâtihî ey yürsile-rriyâḥa mübeşşirâtiv veliyüẕîḳaküm mir raḥmetihî velitecriye-lfülkü biemrihî velitebtegû min faḍlihî vele\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (3456, 'veleḳad erselnâ min ḳablike rusülen ilâ ḳavmihim fecâûhüm bilbeyyinâti fenteḳamnâ mine-lleẕîne ecramû. vekâne ḥaḳḳan \\`aleynâ naṣru-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3457, 'allâhü-lleẕî yürsilü-rriyâḥa fetüŝîru seḥâben feyebsüṭuhû fi-ssemâi keyfe yeşâü veyec\\`alühû kisefen fetere-lvedḳa yaḫrucü min ḫilâlih. feiẕâ eṣâbe bihî mey yeşâü min \\`ibâdihî iẕâ hüm yestebşirûn.');
INSERT INTO `tr_transliteration` VALUES (3458, 'vein kânû min ḳabli ey yünezzele \\`aleyhim min ḳablihî lemüblisîn.');
INSERT INTO `tr_transliteration` VALUES (3459, 'fenżur ilâ âŝeri raḥmeti-llâhi keyfe yuḥyi-l\'arḍa ba\\`de mevtihâ. inne ẕâlike lemuḥyi-lmevtâ. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (3460, 'velein erselnâ rîḥan feraevhü muṣferral leżallû mim ba\\`dihî yekfürûn.');
INSERT INTO `tr_transliteration` VALUES (3461, 'feinneke lâ tüsmi\\`u-lmevtâ velâ tüsmi\\`u-ṣṣumme-ddü\\`âe iẕâ vellev müdbirîn.');
INSERT INTO `tr_transliteration` VALUES (3462, 'vemâ ente bihâdi-l\\`umyi \\`an ḍalâletihim. in tüsmi\\`u illâ mey yü\'minü biâyâtinâ fehüm müslimûn.');
INSERT INTO `tr_transliteration` VALUES (3463, 'allâhü-lleẕî ḫaleḳaküm min ḍa\\`fin ŝümme ce\\`ale mim ba\\`di ḍa\\`fin ḳuvveten ŝümme ce\\`ale mim ba\\`di ḳuvvetin ḍa\\`fev veşeybeh. yaḫlüḳu mâ yeşâ\'. vehüve-l\\`alîmü-lḳadîr.');
INSERT INTO `tr_transliteration` VALUES (3464, 'veyevme teḳûmü-ssâ\\`atü yuḳsimü-lmücrimûne mâ lebiŝû gayra sâ\\`ah. keẕâlike kânû yü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (3465, 'veḳâle-lleẕîne ûtü-l\\`ilme vel\'îmâne leḳad lebiŝtüm fî kitâbi-llâhi ilâ yevmi-lba\\`ŝ. fehâẕâ yevmü-lba\\`ŝi velâkinneküm küntüm lâ ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3466, 'feyevmeiẕil lâ yenfe\\`u-lleẕîne żalemû ma\\`ẕiratühüm velâ hüm yüsta\\`tebûn.');
INSERT INTO `tr_transliteration` VALUES (3467, 'veleḳad ḍarabnâ linnâsi fî hâẕe-lḳur\'âni min külli meŝel. velein ci\'tehüm biâyetil leyeḳûlenne-lleẕîne keferû in entüm illâ mübṭilûn.');
INSERT INTO `tr_transliteration` VALUES (3468, 'keẕâlike yaṭbe\\`u-llâhü \\`alâ ḳulûbi-lleẕîne lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3469, 'faṣbir inne va\\`de-llâhi ḥaḳḳuv velâ yesteḫiffenneke-lleẕîne lâ yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (3470, 'elif-lâm-mîm.');
INSERT INTO `tr_transliteration` VALUES (3471, 'tilke âyâtü-lkitâbi-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3472, 'hüdev veraḥmetel lilmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (3473, 'elleẕîne yüḳîmûne-ṣṣalâte veyü\'tûne-zzekâte vehüm bil\'âḫirati hüm yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (3474, 'ülâike \\`alâ hüdem mir rabbihim veülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (3475, 'vemine-nnâsi mey yeşterî lehve-lḥadîŝi liyüḍille \\`an sebîli-llâhi bigayri \\`ilmiv veyetteḫiẕehâ hüzüvâ. ülâike lehüm \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (3476, 'veiẕâ tütlâ \\`aleyhi âyâtünâ vellâ müstekbiran keel lem yesma\\`hâ keenne fî üẕüneyhi vaḳrâ. febeşşirhü bi\\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (3477, 'inne-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lehüm cennâtü-nne\\`îm.');
INSERT INTO `tr_transliteration` VALUES (3478, 'ḫâlidîne fîhâ. va\\`de-llâhi ḥaḳḳâ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3479, 'ḫaleḳa-ssemâvâti bigayri \\`amedin teravnehâ veelḳâ fi-l\'arḍi ravâsiye en temîde biküm vebeŝŝe fîhâ min külli dâbbeh. veenzelnâ mine-ssemâi mâen feembetnâ fîhâ min külli zevcin kerîm.');
INSERT INTO `tr_transliteration` VALUES (3480, 'hâẕâ ḫalḳu-llâhi feerûnî mâẕâ ḫaleḳa-lleẕîne min dûnih. beli-żżâlimûne fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3481, 'veleḳad âteynâ luḳmâne-lḥikmete eni-şkür lillâh. vemey yeşkür feinnemâ yeşküru linefsih. vemen kefera feinne-llâhe ganiyyün ḥamîd.');
INSERT INTO `tr_transliteration` VALUES (3482, 'veiẕ ḳâle luḳmânü libnihî vehüve ye\\`iżuhû yâ büneyye lâ tüşrik billâh. inne-şşirke leżulmün \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (3483, 'veveṣṣayne-l\'insâne bivâlideyh. ḥamelethü ümmühû vehnen \\`alâ vehniv vefiṣâlühû fî \\`âmeyni eni-şkür lî velivâlideyk. ileyye-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (3484, 'vein câhedâke \\`alâ en tüşrike bî mâ leyse leke bihî \\`ilmün felâ tüṭi\\`hümâ veṣâḥibhümâ fi-ddünyâ ma\\`rûfâ. vettebi\\` sebîle men enâbe ileyy. ŝümme ileyye merci\\`uküm feünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3485, 'yâ büneyye innehâ in tekü miŝḳâle ḥabbetim min ḫardelin fetekün fî ṣaḫratin ev fi-ssemâvâti ev fi-l\'arḍi ye\'ti bihe-llâh. inne-llâhe leṭîfün ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (3486, 'yâ büneyye eḳimi-ṣṣalâte ve\'mür bilma\\`rûfi venhe \\`ani-lmünkeri vaṣbir \\`alâ mâ eṣâbek. inne ẕâlike min \\`azmi-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (3487, 'velâ tüṣa\\`\\`ir ḫaddeke linnâsi velâ temşi fi-l\'arḍi meraḥâ. inne-llâhe lâ yüḥibbü külle muḫtâlin feḫûr.');
INSERT INTO `tr_transliteration` VALUES (3488, 'vaḳṣid fî meşyike vagḍuḍ min ṣavtik. inne enkera-l\'aṣvâti leṣavtü-lḥamîr.');
INSERT INTO `tr_transliteration` VALUES (3489, 'elem terav enne-llâhe seḫḫara leküm mâ fi-ssemâvâti vemâ fi-l\'arḍi veesbega \\`aleyküm ni\\`amehû żâhiratev vebâṭineh. vemine-nnâsi mey yücâdilü fi-llâhi bigayri \\`ilmiv velâ hüdev velâ kitâbim münîr.');
INSERT INTO `tr_transliteration` VALUES (3490, 'veiẕâ ḳîle lehümü-ttebi\\`û mâ enzele-llâhü ḳâlû bel nettebi\\`u mâ vecednâ \\`aleyhi âbâenâ. evelev kâne-şşeyṭânü yed\\`ûhüm ilâ \\`aẕâbi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (3491, 'vemey yüslim vechehû ile-llâhi vehüve muḥsinün feḳadi-stemseke bil\\`urveti-lvuŝḳâ. veile-llâhi \\`âḳibetü-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (3492, 'vemen kefera felâ yaḥzünke küfruh. ileynâ merci\\`uhüm fenünebbiühüm bimâ \\`amilû. inne-llâhe \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (3493, 'nümetti\\`uhüm ḳalîlen ŝümme naḍṭarruhüm ilâ \\`aẕâbin galîż.');
INSERT INTO `tr_transliteration` VALUES (3494, 'velein seeltehüm men ḫaleḳa-ssemâvâti vel\'arḍa leyeḳûlünne-llâh. ḳuli-lḥamdü lillâh. bel ekŝeruhüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3495, 'lillâhi mâ fi-ssemâvâti vel\'arḍ. inne-llâhe hüve-lganiyyü-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (3496, 'velev enne mâ fi-l\'arḍi min şeceratin aḳlâmüv velbaḥru yemüddühû mim ba\\`dihî seb\\`atü ebḥurim mâ nefidet kelimâtü-llâh. inne-llâhe \\`azîzün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3497, 'mâ ḫalḳuküm velâ ba\\`ŝüküm illâ kenefsiv vâḥideh. inne-llâhe semî\\`um beṣîr.');
INSERT INTO `tr_transliteration` VALUES (3498, 'elem tera enne-llâhe yûlicü-lleyle fi-nnehâri veyûlicü-nnehâra fi-lleyli veseḫḫara-şşemse velḳamer. küllüy yecrî ilâ ecelim müsemmev veenne-llâhe bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (3499, 'ẕâlike bienne-llâhe hüve-lḥaḳḳu veenne mâ yed\\`ûne min dûnihi-lbâṭili veenne-llâhe hüve-l\\`aliyyü-lkebîr.');
INSERT INTO `tr_transliteration` VALUES (3500, 'elem tera enne-lfülke tecrî fi-lbaḥri bini\\`meti-llâhi liyüriyeküm min âyâtih. inne fî ẕâlike leâyâtil likülli ṣabbârin şekûr.');
INSERT INTO `tr_transliteration` VALUES (3501, 'veiẕâ gaşiyehüm mevcün keżżuleli de\\`avu-llâhe muḫliṣîne lehü-ddîn. felemmâ neccâhüm ile-lberri feminhüm muḳteṣid. vemâ yecḥadü biâyâtinâ illâ küllü ḫattârin kefûr.');
INSERT INTO `tr_transliteration` VALUES (3502, 'yâ eyyühe-nnâsü-tteḳû rabbeküm vaḫşev yevmel lâ yeczî vâlidün \\`av veledihi. velâ mevlûdün hüve câzin \\`av vâlidihî şey\'â. inne va\\`de-llâhi ḥaḳḳun felâ tegurrannekümü-lḥayâtü-ddünyâ. velâ yegurranneküm billâhi-lgarûr.');
INSERT INTO `tr_transliteration` VALUES (3503, 'inne-llâhe \\`indehû \\`ilmü-ssâ\\`ah. veyünezzilü-lgayŝ. veya\\`lemü mâ fi-l\'erḥâm. vemâ tedrî nefsüm mâẕâ teksibü gadâ. vemâ tedrî nefsüm bieyyi arḍin temût. inne-llâhe \\`alîmün ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (3504, 'elif-lâm-mîm.');
INSERT INTO `tr_transliteration` VALUES (3505, 'tenzîlü-lkitâbi lâ raybe fîhi mir rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (3506, 'em yeḳûlûne-fterâh. bel hüve-lḥaḳḳu mir rabbike litünẕira ḳavmem mâ etâhüm min neẕîrim min ḳablike le\\`allehüm yehtedûn.');
INSERT INTO `tr_transliteration` VALUES (3507, 'allâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa vemâ beynehümâ fî sitteti eyyâmin ŝümme-stevâ \\`ale-l\\`arş. mâ leküm min dûnihî miv veliyyiv velâ şefî\\`. efelâ teteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (3508, 'yüdebbiru-l\'emra mine-ssemâi ile-l\'arḍi ŝümme ya\\`rucü ileyhi fî yevmin kâne miḳdâruhû elfe senetim mimmâ te\\`uddûn.');
INSERT INTO `tr_transliteration` VALUES (3509, 'ẕâlike \\`âlimü-lgaybi veşşehâdeti-l\\`azîzü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (3510, '-lleẕî aḥsene külle şey\'in ḫaleḳahû vebedee ḫalḳa-l\'insâni min ṭîn.');
INSERT INTO `tr_transliteration` VALUES (3511, 'ŝümme ce\\`ale neslehû min sülâletim mim mâim mehîn.');
INSERT INTO `tr_transliteration` VALUES (3512, 'ŝümme sevvâhü venefeḫa fîhi mir rûḥihî vece\\`ale lekümü-ssem\\`a vel\'ebṣâra vel\'ef\'ideh. ḳalîlem mâ teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (3513, 'veḳâlû eiẕâ ḍalelnâ fi-l\'arḍi einnâ lefî ḫalḳin cedîd. bel hüm biliḳâi rabbihim kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (3514, 'ḳul yeteveffâküm melekü-lmevti-lleẕî vukkile biküm ŝümme ilâ rabbiküm türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3515, 'velev terâ iẕi-lmücrimûne nâkisû ruûsihim \\`inde rabbihim. rabbenâ ebṣarnâ vesemi\\`nâ ferci\\`nâ na\\`mel ṣâliḥan innâ mûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (3516, 'velev şi\'nâ leâteynâ külle nefsin hüdâhâ velâkin ḥaḳḳa-lḳavlü minnî leemleenne cehenneme mine-lcinneti vennâsi ecme\\`în.');
INSERT INTO `tr_transliteration` VALUES (3517, 'feẕûḳû bimâ nesîtüm liḳâe yevmiküm hâẕâ. innâ nesînâküm veẕûḳû \\`aẕâbe-lḫuldi bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3518, 'innemâ yü\'minü biâyâtine-lleẕîne iẕâ ẕükkirû bihâ ḫarrû süccedev vesebbeḥû biḥamdi rabbihim vehüm lâ yestekbirûn.');
INSERT INTO `tr_transliteration` VALUES (3519, 'tetecâfâ cünûbühüm \\`ani-lmeḍâci\\`i yed\\`ûne rabbehüm ḫavfev veṭame\\`â. vemimmâ razaḳnâhüm yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (3520, 'felâ ta\\`lemü nefsüm mâ uḫfiye lehüm min ḳurrati a\\`yün. cezâem bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3521, 'efemen kâne mü\'minen kemen kâne fâsiḳâ. lâ yestevûn.');
INSERT INTO `tr_transliteration` VALUES (3522, 'emme-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti felehüm cennâtü-lme\'vâ. nüzülem bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3523, 'veemme-lleẕîne feseḳû feme\'vâhümü-nnâr. küllemâ erâdû ey yaḫrucû minhâ ü\\`îdû fîhâ veḳîle lehüm ẕûḳû \\`aẕâbe-nnâri-lleẕî küntüm bihî tükeẕẕibûn.');
INSERT INTO `tr_transliteration` VALUES (3524, 'velenüẕîḳannehüm mine-l\\`aẕâbi-l\'ednâ dûne-l\\`aẕâbi-l\'ekberi le\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3525, 'vemen ażlemü mimmen ẕükkira biâyâti rabbihî ŝümme a\\`raḍa \\`anhâ. innâ mine-lmücrimîne münteḳimûn.');
INSERT INTO `tr_transliteration` VALUES (3526, 'veleḳad âteynâ mûse-lkitâbe felâ tekün fî miryetim mil liḳâehi vece\\`alnâhü hüdel libenî isrâîl.');
INSERT INTO `tr_transliteration` VALUES (3527, 'vece\\`alnâ minhüm eimmetey yehdûne biemrinâ lemmâ ṣaberû. vekânû biâyâtinâ yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (3528, 'inne rabbeke hüve yefṣilü beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (3529, 'evelem yehdi lehüm kem ehleknâ min ḳablihim mine-lḳurûni yemşûne fî mesâkinihim. inne fî ẕâlike leâyât. efelâ yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3530, 'evelem yerav ennâ nesûḳu-lmâe ile-l\'arḍi-lcüruzi fenuḫricü bihî zer\\`an te\'külü minhü en\\`âmühüm veenfüsühüm. efelâ yübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (3531, 'veyeḳûlûne metâ hâẕe-lfetḥu in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3532, 'ḳul yevme-lfetḥi lâ yenfe\\`u-lleẕîne keferû îmânühüm velâ hüm yünżarûn.');
INSERT INTO `tr_transliteration` VALUES (3533, 'fea\\`riḍ \\`anhüm venteżir innehüm münteżirûn.');
INSERT INTO `tr_transliteration` VALUES (3534, 'yâ eyyühe-nnebiyyü-tteḳi-llâhe velâ tüṭi\\`i-lkâfirîne velmünâfiḳîn. inne-llâhe kâne \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (3535, 'vettebi\\` mâ yûḥâ ileyke mir rabbik. inne-llâhe kâne bimâ ta\\`melûne ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (3536, 'vetevekkel \\`ale-llâh. vekefâ billâhi vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (3537, 'mâ ce\\`ale-llâhü liracülim min ḳalbeyni fî cevfih. vemâ ce\\`ale ezvâcekümü-llâî tüżâhirûne minhünne ümmehâtiküm. vemâ ce\\`ale ed\\`iyâeküm ebnâeküm. ẕâliküm ḳavlüküm biefvâhiküm. vellâhü yeḳûlü-lḥaḳḳa vehüve yehdi-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (3538, 'üd\\`ûhüm liâbâihim hüve aḳseṭu \\`inde-llâh. feil lem ta\\`lemû âbâehüm feiḫvânüküm fi-ddîni vemevâlîküm. veleyse \\`aleyküm cünâḥun fîmâ aḫṭa\'tüm bihî velâkim mâ te\\`ammedet ḳulûbüküm. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (3539, 'ennebiyyü evlâ bilmü\'minîne min enfüsihim veezvâcühû ümmehâtühüm. veülü-l\'erḥâmi ba\\`ḍuhüm evlâ biba\\`ḍin fî kitâbi-llâhi mine-lmü\'minîne velmühâcirîne illâ en tef\\`alû ilâ evliyâiküm ma\\`rûfâ. kâne ẕâlike fi-lkitâbi mesṭûrâ.');
INSERT INTO `tr_transliteration` VALUES (3540, 'veiẕ eḫaẕnâ mine-nnebiyyîne mîŝâḳahüm veminke vemin nûḥiv veibrâhime vemûsâ ve\\`îse-bni meryem. veeḫaẕnâ minhüm mîŝâḳan galîżâ.');
INSERT INTO `tr_transliteration` VALUES (3541, 'liyes\'ele-ṣṣâdiḳîne \\`an ṣidḳihim. vee\\`adde lilkâfirîne \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (3542, 'yâ eyyühe-lleẕîne âmenü-ẕkürû ni\\`mete-llâhi \\`aleyküm iẕ câetküm cünûdün feerselnâ \\`aleyhim rîḥav vecünûdel lem teravhâ. vekâne-llâhü bimâ ta\\`melûne beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (3543, 'iẕ câûküm min fevḳiküm vemin esfele minküm veiẕ zâgati-l\'ebṣâru vebelegati-lḳulûbü-lḥanâcira veteżunnûne billâhi-żżunûnâ.');
INSERT INTO `tr_transliteration` VALUES (3544, 'hünâlike-btüliye-lmü\'minûne vezülzilû zilzâlen şedîdâ.');
INSERT INTO `tr_transliteration` VALUES (3545, 'veiẕ yeḳûlü-lmünâfiḳûne velleẕîne fî ḳulûbihim meraḍum mâ ve\\`adene-llâhü verasûlühû illâ gurûrâ.');
INSERT INTO `tr_transliteration` VALUES (3546, 'veiẕ ḳâleṭ ṭâifetüm minhüm yâ ehle yeŝribe lâ müḳâme leküm ferci\\`û. veyeste\'ẕinü ferîḳum minhümü-nnebiyye yeḳûlûne inne büyûtenâ \\`avratüv vemâ hiye bi\\`avrah. iy yürîdûne illâ firârâ.');
INSERT INTO `tr_transliteration` VALUES (3547, 'velev düḫilet \\`aleyhim min aḳṭârihâ ŝümme süilü-lfitnete leetevhâ vemâ telebbeŝû bihâ illâ yesîrâ.');
INSERT INTO `tr_transliteration` VALUES (3548, 'veleḳad kânû \\`âhedü-llâhe min ḳablü lâ yüvellûne-l\'edbâr. vekâne \\`ahdü-llâhi mes\'ûlâ.');
INSERT INTO `tr_transliteration` VALUES (3549, 'ḳul ley yenfe\\`akümü-lfirâru in ferartüm mine-lmevti evi-lḳatli veiẕel lâ tümette\\`ûne illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (3550, 'ḳul men ẕe-lleẕî ya\\`ṣimüküm mine-llâhi in erâde biküm sûen ev erâde biküm raḥmeh. velâ yecidûne lehüm min dûni-llâhi veliyyev velâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (3551, 'ḳad ya\\`lemü-llâhü-lmü\\`avviḳîne minküm velḳâilîne liiḫvânihim helümme ileynâ. velâ ye\'tûne-lbe\'se illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (3552, 'eşiḥḥaten \\`aleyküm. feiẕâ câe-lḫavfü raeytehüm yenżurûne ileyke tedûru a\\`yünühüm kelleẕî yugşâ \\`aleyhi mine-lmevt. feiẕâ ẕehebe-lḫavfü seleḳûküm bielsinetin ḥidâdin eşiḥḥaten \\`ale-lḫayr. ülâike lem yü\'minû feaḥbeṭa-llâhü a\\`mâlehüm. vekâne ẕâlike \\`ale-llâhi yesîrâ.');
INSERT INTO `tr_transliteration` VALUES (3553, 'yaḥsebûne-l\'aḥzâbe lem yeẕhebû. veiy ye\'ti-l\'aḥzâbü yeveddû lev ennehüm bâdûne fi-l\'a\\`râbi yes\'elûne \\`an embâiküm. velev kânû fîküm mâ ḳâtelû illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (3554, 'leḳad kâne leküm fî rasûli-llâhi üsvetün ḥasenetül limen kâne yercü-llâhe velyevme-l\'âhira veẕekera-llâhe keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (3555, 'velemmâ rae-lmü\'minûne-l\'aḥzâbe ḳâlû hâẕâ mâ ve\\`adene-llâhü verasûlühû veṣadeḳa-llâhü verasûlüh. vemâ zâdehüm illâ îmânev veteslîmâ.');
INSERT INTO `tr_transliteration` VALUES (3556, 'mine-lmü\'minîne ricâlün ṣadeḳû mâ \\`âhedü-llâhe \\`aleyh. feminhüm men ḳaḍâ naḥbehû veminhüm mey yenteżir. vemâ beddelû tebdîlâ.');
INSERT INTO `tr_transliteration` VALUES (3557, 'liyecziye-llâhu-ṣṣâdiḳîne biṣidḳihim veyü\\`aẕẕibe-lmünâfiḳîne in şâe ev yetûbe \\`aleyhim. inne-llâhe kâne gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (3558, 'veradde-llâhü-lleẕîne keferû bigayżihim lem yenâlû ḫayrâ. vekefe-llâhü-lmü\'minîne-lḳitâl. vekâne-llâhü ḳaviyyen \\`azîzâ.');
INSERT INTO `tr_transliteration` VALUES (3559, 'veenzele-lleẕîne żâherûhüm min ehli-lkitâbi min ṣayâṣîhim veḳaẕefe fî ḳulûbihimü-rru\\`be ferîḳan taḳtülûne vete\'sirûne ferîḳâ.');
INSERT INTO `tr_transliteration` VALUES (3560, 'veevraŝeküm arḍahüm vediyârahüm veemvâlehüm vearḍal lem teṭaûhâ. vekâne-llâhü \\`alâ külli şey\'in ḳadîrâ.');
INSERT INTO `tr_transliteration` VALUES (3561, 'yâ eyyühe-nnebiyyü ḳul liezvâcike in küntünne türidne-lḥayâte-ddünyâ vezînetehâ fete\\`âleyne ümetti\\`künne veüserriḥkünne serâḥan cemîlâ.');
INSERT INTO `tr_transliteration` VALUES (3562, 'vein küntünne türidne-llâhe verasûlehû veddâra-l\'âḫirate feinne-llâhe e\\`adde lilmuḥsinâti minkünne ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (3563, 'yâ nisâe-nnebiyyi mey ye\'ti minkünne bifâḥişetim mübeyyinetiy yüḍâ\\`af lehe-l\\`aẕâbü ḍi\\`feyn. vekâne ẕâlike \\`ale-llâhi yesîrâ.');
INSERT INTO `tr_transliteration` VALUES (3564, 'vemey yaḳnüt minkünne lillâhi verasûlihî veta\\`mel ṣâliḥan nü\'tihâ ecrahâ merrateyni vea\\`tednâ lehâ rizḳan kerîmâ.');
INSERT INTO `tr_transliteration` VALUES (3565, 'yâ nisâe-nnebiyyi lestünne keeḥadim mine-nnisâi ini-tteḳaytünne felâ taḫḍa\\`ne bilḳavli feyaṭme\\`a-lleẕî fî ḳalbihî meraḍuv veḳulne ḳavlem ma\\`rûfâ.');
INSERT INTO `tr_transliteration` VALUES (3566, 'veḳarne fî büyûtikünne velâ teberracne teberruce-lcâhiliyyeti-l\'ûlâ veeḳimne-ṣṣalâte veâtîne-zzekâte veeṭi\\`ne-llâhe verasûleh. innemâ yürîdü-llâhü liyüẕhibe \\`ankümü-rricse ehle-lbeyti veyüṭahhiraküm taṭhîrâ.');
INSERT INTO `tr_transliteration` VALUES (3567, 'veẕkürne mâ yütlâ fî büyûtikünne min âyâti-llâhi velḥikmeh. inne-llâhe kâne leṭîfen ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (3568, 'inne-lmüslimîne velmüslimâti velmü\'minîne velmü\'minâti velḳânitîne velḳânitâti veṣṣâdiḳîne veṣṣâdiḳâti veṣṣâbirîne veṣṣâbirâti velḫâşi\\`îne velḫâşi\\`âti velmüteṣaddiḳîne velmüteṣaddiḳâti veṣṣâimîne veṣṣâimâti velḥâfiżîne fürûcehüm velḥâfiżâti veẕẕâkirîne-llâhe keŝîrav veẕẕâkirâti e\\`adde-llâhü lehüm magfiratev veecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (3569, 'vemâ kâne limü\'miniv velâ mü\'minetin iẕâ ḳaḍa-llâhü verasûlühû emran ey yekûne lehümü-lḫiyeratü min emrihim. vemey ya\\`ṣi-llâhe verasûlehû feḳad ḍalle ḍalâlem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (3570, 'veiẕ teḳûlü lilleẕî en\\`ame-llâhü \\`aleyhi veen\\`amte \\`aleyhi emsik \\`aleyke zevceke vetteḳi-llâhe vetuḫfî fî nefsike me-llâhü mübdîhi vetaḫşe-nnâs. vellâhü eḥaḳḳu en taḫşâh. felemmâ ḳaḍâ zeydüm minhâ veṭaran zevvecnâkehâ likey lâ yekûne \\`ale-lmü\'minîne ḥaracün fî ezvâci ed\\`iyâihim iẕâ ḳaḍav minhünne veṭarâ. vekâne emru-llâhi mef\\`ûlâ.');
INSERT INTO `tr_transliteration` VALUES (3571, 'mâ kâne \\`ale-nnebiyyi min ḥaracin fîmâ feraḍa-llâhü leh. sünnete-llâhi fi-lleẕîne ḫalev min ḳabl. vekâne emru-llâhi ḳaderam maḳdûrâ.');
INSERT INTO `tr_transliteration` VALUES (3572, 'elleẕîne yübelligûne risâlâti-llâhi veyaḫşevnehû velâ yaḫşevne eḥaden ille-llâh. vekefâ billâhi ḥasîbâ.');
INSERT INTO `tr_transliteration` VALUES (3573, 'mâ kâne müḥammedün ebâ eḥadim mir ricâliküm velâkir rasûle-llâhi veḫâteme-nnebiyyîn. vekâne-llâhü bikülli şey\'in \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (3574, 'yâ eyyühe-lleẕîne âmenü-ẕkürü-llâhe ẕikran keŝîrâ.');
INSERT INTO `tr_transliteration` VALUES (3575, 'vesebbiḥûhü bükratev veeṣîlâ.');
INSERT INTO `tr_transliteration` VALUES (3576, 'hüve-lleẕî yüṣallî \\`aleyküm vemelâiketühû liyuḫriceküm mine-żżulümâti ile-nnûr. vekâne bilmü\'minîne raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (3577, 'teḥiyyetühüm yevme yelḳavnehû selâm. vee\\`adde lehüm ecran kerîmâ.');
INSERT INTO `tr_transliteration` VALUES (3578, 'yâ eyyühe-nnebiyyü innâ erselnâke şâhidev vemübeşşirav veneẕîrâ.');
INSERT INTO `tr_transliteration` VALUES (3579, 'vedâ\\`iyen ile-llâhi biiẕnihî vesirâcem münîrâ.');
INSERT INTO `tr_transliteration` VALUES (3580, 'vebeşşiri-lmü\'minîne bienne lehüm mine-llâhi faḍlen kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (3581, 'velâ tüṭi\\`i-lkâfirîne velmünâfiḳîne veda\\` eẕâhüm vetevekkel \\`ale-llâh. vekefâ billâhi vekîlâ.');
INSERT INTO `tr_transliteration` VALUES (3582, 'yâ eyyühe-lleẕîne âmenû iẕâ nekaḥtümü-lmü\'minâti ŝümme ṭallaḳtümûhünne min ḳabli en temessûhünne femâ leküm \\`aleyhinne min \\`iddetin ta\\`teddûnehâ. femetti\\`ûhünne veserriḥûhünne serâḥan cemîlâ.');
INSERT INTO `tr_transliteration` VALUES (3583, 'yâ eyyühe-nnebiyyü innâ aḥlelnâ leke ezvâceke-llâtî âteyte ücûrahünne vemâ meleket yemînüke mimmâ efâe-llâhü \\`aleyke vebenâti \\`ammike vebenâti \\`ammetike vebenâti ḫâlike vebenâti ḫâletike-llâtî hâcerne me\\`ak. vemraetem mü\'mineten iv vehebet nefsehâ linnebiyyi in erâde-nnebiyyü ey yestenkiḥahâ. ḫâliṣatel leke min dûni-lmü\'minîn. ḳad \\`alimnâ mâ feraḍnâ \\`aleyhim fî ezvâcihim vemâ meleket eymânühüm likeylâ yekûne \\`aleyke ḥarac. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (3584, 'türcî men teşâü minhünne vetü\'vî ileyke men teşâ\'. vemeni-btegayte mimmen \\`azelte felâ cünâḥa \\`aleyk. ẕâlike ednâ en teḳarra a\\`yünühünne velâ yaḥzenne veyerḍayne bimâ âteytehünne küllühünn. vellâhü ya\\`lemü mâ fî ḳulûbiküm. vekâne-llâhü \\`alîmen ḥalîmâ.');
INSERT INTO `tr_transliteration` VALUES (3585, 'lâ yeḥillü leke-nnisâü mim ba\\`dü velâ en tebeddele bihinne min ezvâciv velev a\\`cebeke ḥusnühünne illâ mâ meleket yemînük. vekâne-llâhü \\`alâ külli şey\'ir raḳîbâ.');
INSERT INTO `tr_transliteration` VALUES (3586, 'yâ eyyühe-lleẕîne âmenû lâ tedḫulû büyûte-nnebiyyi illâ ey yü\'ẕene leküm ilâ ṭa\\`âmin gayra nâżirîne inâhü velâkin iẕâ dü\\`îtüm fedḫulû feiẕâ ṭa\\`imtüm fenteşirû velâ müste\'nisîne liḥadîŝ. inne ẕâliküm kâne yü\'ẕi-nnebiyye feyestaḥyî minküm. vellâhü lâ yestaḥyî mine-lḥaḳḳ. veiẕâ seeltümûhünne metâ\\`an fes\'elûhünne miv verâi ḥicâb. ẕâliküm aṭheru liḳulûbiküm veḳulûbihinn. vemâ kâne leküm en tü\'ẕû rasûle-llâhi velâ en tenkiḥû ezvâcehû mim ba\\`dihî ebedâ. inne ẕâliküm kâne \\`inde-llâhi \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (3587, 'in tübdû şey\'en ev tuḫfûhü feinne-llâhe kâne bikülli şey\'in \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (3588, 'lâ cünâḥa \\`aleyhinne fî âbâihinne velâ ebnâihinne velâ iḫvânihinne velâ ebnâi iḫvânihinne velâ ebnâi eḫavâtihinne velâ nisâihinne velâ mâ meleket eymânühünn. vetteḳîne-llâh. inne-llâhe kâne \\`alâ külli şey\'in şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (3589, 'inne-llâhe vemelâiketehû yüṣallûne \\`ale-nnebiyy. yâ eyyühe-lleẕîne âmenû ṣallû \\`aleyhi vesellimû teslîmâ.');
INSERT INTO `tr_transliteration` VALUES (3590, 'inne-lleẕîne yü\'ẕûne-llâhe verasûlehû le\\`anehümü-llâhü fi-ddünyâ vel\'âḫirati vee\\`adde lehüm \\`aẕâbem mühînâ.');
INSERT INTO `tr_transliteration` VALUES (3591, 'velleẕîne yü\'ẕûne-lmü\'minîne velmü\'minâti bigayri me-ktesebû feḳadi-ḥtemelû bühtânev veiŝmem mübînâ.');
INSERT INTO `tr_transliteration` VALUES (3592, 'yâ eyyühe-nnebiyyü ḳul liezvâcike vebenâtike venisâi-lmü\'minîne yüdnîne \\`aleyhinne min celâbîbihinn. ẕâlike ednâ ey yü\\`rafne felâ yü\'ẕeyn. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (3593, 'leil lem yentehi-lmünâfiḳûne velleẕîne fî ḳulûbihim meraḍuv velmürcifûne fi-lmedîneti lenugriyenneke bihim ŝümme lâ yücâvirûneke fîhâ illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (3594, 'mel\\`ûnîn. eyne mâ ŝüḳifû üḫiẕû veḳuttilû taḳtîlâ.');
INSERT INTO `tr_transliteration` VALUES (3595, 'sünnete-llâhi fi-lleẕîne ḫalev min ḳabl. velen tecide lisünneti-llâhi tebdîlâ.');
INSERT INTO `tr_transliteration` VALUES (3596, 'yes\'elüke-nnâsü \\`ani-ssâ\\`ah. ḳul innemâ \\`ilmühâ \\`inde-llâh. vemâ yüdrîke le\\`alle-ssâ\\`ate tekûnü ḳarîbâ.');
INSERT INTO `tr_transliteration` VALUES (3597, 'inne-llâhe le\\`ane-lkâfirîne vee\\`adde lehüm se\\`îrâ.');
INSERT INTO `tr_transliteration` VALUES (3598, 'ḫâlidîne fîhâ ebedâ. lâ yecidûne veliyyev velâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (3599, 'yevme tüḳallebü vucûhühüm fi-nnâri yeḳûlûne yâ leytenâ eṭa\\`ne-llâhe veeṭa\\`ne-rrasûl.');
INSERT INTO `tr_transliteration` VALUES (3600, 'veḳâlû rabbenâ innâ eṭa\\`nâ sâdetenâ veküberâenâ feeḍallûne-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (3601, 'rabbenâ âtihim ḍi\\`feyni mine-l\\`aẕâbi vel\\`anhüm la\\`nen kebîrâ.');
INSERT INTO `tr_transliteration` VALUES (3602, 'yâ eyyühe-lleẕîne âmenû lâ tekûnû kelleẕîne âẕev mûsâ feberra\'ehü-llâhü mimmâ ḳâlû. vekâne \\`inde-llâhi vecîhâ.');
INSERT INTO `tr_transliteration` VALUES (3603, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veḳûlû ḳavlen sedîdâ.');
INSERT INTO `tr_transliteration` VALUES (3604, 'yuṣliḥ leküm a\\`mâleküm veyagfir leküm ẕünûbeküm. vemey yüṭi\\`i-llâhe verasûlehû feḳad fâze fevzen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (3605, 'innâ \\`araḍne-l\'emânete \\`ale-ssemâvâti vel\'arḍi velcibâli feebeynâ ey yaḥmilnehâ veeşfaḳne minhâ veḥamelehe-l\'insân. innehû kâne żalûmen cehûlâ.');
INSERT INTO `tr_transliteration` VALUES (3606, 'liyü\\`aẕẕibe-llâhü-lmünâfiḳîne velmünâfiḳâti velmüşrikîne velmüşrikâti veyetûbe-llâhü \\`ale-lmü\'minîne velmü\'minât. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (3607, 'elḥamdü lillâhi-lleẕî lehû mâ fi-ssemâvâti vemâ fi-l\'arḍi velehü-lḥamdü fi-l\'âḫirah. vehüve-lḥakîmü-lḫabîr.');
INSERT INTO `tr_transliteration` VALUES (3608, 'ya\\`lemü mâ yelicü fi-l\'arḍi vemâ yaḫrucü minhâ vemâ yenzilü mine-ssemâi vemâ ya\\`rucü fîhâ. vehüve-rraḥîmü-lgafûr.');
INSERT INTO `tr_transliteration` VALUES (3609, 'veḳâle-lleẕîne keferû lâ te\'tîne-ssâ\\`ah. ḳul belâ verabbî lete\'tiyenneküm \\`âlimi-lgaybi. lâ ya\\`zübü \\`anhü miŝḳâlü ẕerratin fi-ssemâvâti velâ fi-l\'arḍi velâ aṣgaru min ẕâlike velâ ekberu illâ fî kitâbim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3610, 'liyecziye-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti. ülâike lehüm magfiratüv verizḳun kerîm.');
INSERT INTO `tr_transliteration` VALUES (3611, 'velleẕîne se\\`av fî âyâtinâ mü\\`âcizîne ülâike lehüm \\`aẕâbüm mir riczin elîm.');
INSERT INTO `tr_transliteration` VALUES (3612, 'veyere-lleẕîne ûtü-l\\`ilme-lleẕî ünzile ileyke mir rabbike hüve-lḥaḳḳa veyehdî ilâ ṣirâṭi-l\\`azîzi-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (3613, 'veḳâle-lleẕîne keferû hel nedüllüküm \\`alâ racüliy yünebbiüküm iẕâ müzziḳtüm külle mümezzeḳin inneküm lefî ḫalḳin cedîd.');
INSERT INTO `tr_transliteration` VALUES (3614, 'efterâ \\`ale-llâhi keẕiben em bihî cinneh. beli-lleẕîne lâ yü\'minûne bil\'âḫirati fi-l\\`aẕâbi veḍḍalâli-lbe\\`îd.');
INSERT INTO `tr_transliteration` VALUES (3615, 'efelem yerav ilâ mâ beyne eydîhim vemâ ḫalfehüm mine-ssemâi vel\'arḍ. in neşe\' naḫsif bihimü-l\'arḍa ev nüsḳiṭ \\`aleyhim kisefem mine-ssemâ\'. inne fî ẕâlike leâyetel likülli \\`abdim münîb.');
INSERT INTO `tr_transliteration` VALUES (3616, 'veleḳad âteynâ dâvûde minnâ faḍlâ. yâ cibâlü evvibî me\\`ahû veṭṭayr. veelennâ lehü-lḥadîd.');
INSERT INTO `tr_transliteration` VALUES (3617, 'eni-\\`mel sâbigâtiv veḳaddir fi-sserdi va\\`melû ṣâliḥâ. innî bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (3618, 'velisüleymâne-rrîḥa gudüvvuhâ şehruv veravâḥuhâ şehr. veeselnâ lehû \\`ayne-lḳiṭr. vemine-lcinni mey ya\\`melü beyne yedeyhi biiẕni rabbihî. vemey yezig minhüm \\`an emrinâ nüẕiḳhü min \\`aẕâbi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (3619, 'ya\\`melûne lehû mâ yeşâü mim meḥârîbe vetemâŝîle vecifânin kelcevâbi veḳudûrir râsiyât. i\\`melû âle dâvûde şükrâ. veḳalîlüm min \\`ibâdiye-şşekûr.');
INSERT INTO `tr_transliteration` VALUES (3620, 'felemmâ ḳaḍaynâ \\`aleyhi-lmevte mâ dellehüm \\`alâ mevtih illâ dâbbetü-l\'arḍi te\'külü minseeteh. felemmâ ḫarra tebeyyeneti-lcinnü el lev kânû ya\\`lemûne-lgaybe mâ lebiŝû fi-l\\`aẕâbi-lmühîn.');
INSERT INTO `tr_transliteration` VALUES (3621, 'leḳad kâne lisebein fî meskenihim âyeh. cennetâni \\`ay yemîniv veşimâl. külû mir rizḳi rabbiküm veşkürû leh. beldetün ṭayyibetüv verabbün gafûr.');
INSERT INTO `tr_transliteration` VALUES (3622, 'fea\\`raḍû feerselnâ \\`aleyhim seyle-l\\`arimi vebeddelnâhüm bicenneteyhim cenneteyni ẕevâtey ükülin ḫamṭiv veeŝliv veşey\'im min sidrin ḳalîl.');
INSERT INTO `tr_transliteration` VALUES (3623, 'ẕâlike cezeynâhüm bimâ keferû. vehel nücezî ille-lkefûra.');
INSERT INTO `tr_transliteration` VALUES (3624, 'vece\\`alnâ beynehüm vebeyne-lḳura-lletî bâraknâ fîhâ ḳuran żâhiratev veḳaddernâ fîhe-sseyr. sîrû fîhâ leyâliye veeyyâmen âminîn.');
INSERT INTO `tr_transliteration` VALUES (3625, 'feḳâlû rabbenâ bâ\\`id beyne esfârinâ veżalemû enfüsehüm fece\\`alnâhüm eḥâdîŝe vemezzaḳnâhüm külle mümezzeḳ. inne fî ẕâlike leâyâtil likülli ṣabbârin şekûr.');
INSERT INTO `tr_transliteration` VALUES (3626, 'veleḳad ṣaddeḳa \\`aleyhim iblîsü żannehû fettebe\\`ûhü illâ ferîḳam mine-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3627, 'vemâ kâne lehû \\`aleyhim min sülṭânin illâ lina\\`leme mey yü\'minü bil\'âḫirati mimmen hüve minhâ fî şekkin. verabbüke \\`alâ külli şey\'in ḥafîż.');
INSERT INTO `tr_transliteration` VALUES (3628, 'ḳuli-d\\`ü-lleẕîne za\\`amtüm min dûni-llâh. lâ yemlikûne miŝḳâle ẕerratin fi-ssemâvâti velâ fi-l\'arḍi vemâ lehüm fîhimâ min şirkiv vemâ lehû minhüm min żahîr.');
INSERT INTO `tr_transliteration` VALUES (3629, 'velâ tenfe\\`u-şşefâ\\`atü \\`indehû illâ limen eẕine leh. ḥattâ iẕâ füzzi\\`a \\`an ḳulûbihim ḳâlû mâẕâ ḳâle rabbüküm. ḳâlü-lḥaḳḳ. vehüve-l\\`aliyyü-lkebîr.');
INSERT INTO `tr_transliteration` VALUES (3630, 'ḳul mey yerzüḳuküm mine-ssemâvâti vel\'arḍ. ḳuli-llâhü veinnâ ev iyyâküm le\\`alâ hüden ev fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3631, 'ḳul lâ tüs\'elûne \\`ammâ ecramnâ velâ nüs\'elü \\`ammâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3632, 'ḳul yecme\\`u beynenâ rabbünâ ŝümme yefteḥu beynenâ bilḥaḳḳ. vehüve-lfettâḥu-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (3633, 'ḳul erûniye-lleẕîne elḥaḳtüm bihî şürakâe kellâ. bel hüve-llâhü-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3634, 'vemâ erselnâke illâ kâffetel linnâsi beşîrav veneẕîrav velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3635, 'veyeḳûlûne metâ hâẕe-lva\\`dü in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (3636, 'ḳul leküm mî\\`âdü yevmil lâ teste\'ḫirûne \\`anhü sâ\\`atev velâ testaḳdimûn.');
INSERT INTO `tr_transliteration` VALUES (3637, 'veḳâle-lleẕîne keferû len nü\'mine bihâẕe-lḳur\'âni velâ billeẕî beyne yedeyh. velev terâ iẕi-żżâlimûne mevḳûfûne \\`inde rabbihim. yerci\\`u ba\\`ḍuhüm ilâ ba\\`ḍin-lḳavle. yeḳûlü-lleẕîne-stuḍ\\`ifû lilleẕîne-stekberû levlâ entüm lekünnâ mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (3638, 'ḳâle-lleẕîne-stekberû lilleẕîne-stuḍ\\`ifû enaḥnü ṣadednâküm \\`ani-lhüdâ ba\\`de iẕ câeküm bel küntüm mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (3639, 'veḳâle-lleẕîne-stuḍ\\`ifû lilleẕîne-stekberû bel mekru-lleyli vennehâri iẕ te\'mürûnenâ en nekfüra billâhi venec\\`ale lehû endâdâ. veeserrü-nnedâmete lemmâ raevu-l\\`aẕâb. vece\\`alne-l\'aglâle fî a\\`nâḳi-lleẕîne keferû. hel yüczevne illâ mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (3640, 'vemâ erselnâ fî ḳaryetim min neẕîrin illâ ḳâle mütrafûhâ innâ bimâ ürsiltüm bihî kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (3641, 'veḳâlû naḥnü ekŝeru emvâlev veevlâdev vemâ naḥnü bimü\\`aẕẕebîn.');
INSERT INTO `tr_transliteration` VALUES (3642, 'ḳul inne rabbî yebsüṭu-rrizḳa limey yeşâü veyaḳdiru velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3643, 'vemâ emvâlüküm velâ evlâdüküm billetî tüḳarribüküm \\`indenâ zülfâ illâ men âmene ve\\`amile ṣâliḥâ. feülâike lehüm cezâu-ḍḍi\\`fi bimâ \\`amilû vehüm fi-lgurufâti âminûn.');
INSERT INTO `tr_transliteration` VALUES (3644, 'velleẕîne yes\\`avne fî âyâtinâ mü\\`âcizîne ülâike fi-l\\`aẕâbi muḥḍarûn.');
INSERT INTO `tr_transliteration` VALUES (3645, 'ḳul inne rabbî yebsüṭu-rrizḳa limey yeşâü min \\`ibâdihî veyaḳdiru leh. vemâ enfaḳtüm min şey\'in fehüve yuḫlifüh. vehüve ḫayru-rrâziḳîn.');
INSERT INTO `tr_transliteration` VALUES (3646, 'veyevme yaḥşüruhüm cemî\\`an ŝümme yeḳûlü lilmelâiketi ehâülâi iyyâküm kânû ya\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (3647, 'ḳâlû sübḥâneke ente veliyyünâ min dûnihim. bel kânû ya\\`büdûne-lcinn. ekŝeruhüm bihim mü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3648, 'felyevme lâ yemlikü ba\\`ḍuküm liba\\`ḍin nef\\`av velâ ḍarrâ. veneḳûlü lilleẕîne żalemû ẕûḳû \\`aẕâbe-nnâri-lletî küntüm bihâ tükeẕẕibûn.');
INSERT INTO `tr_transliteration` VALUES (3649, 'veiẕâ tütlâ \\`aleyhim âyâtünâ beyyinâtin ḳâlû mâ hâẕâ illâ racülüy yürîdü ey yeṣuddeküm \\`ammâ kâne ya\\`büdü âbâüküm. veḳâlû mâ hâẕâ illâ ifküm müfterâ. veḳâle-lleẕîne keferû lilḥaḳḳi lemmâ câehüm in hâẕâ illâ siḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (3650, 'vemâ âteynâhüm min kütübiy yedrusûnehâ vemâ erselnâ ileyhim ḳableke min neẕîr.');
INSERT INTO `tr_transliteration` VALUES (3651, 'vekeẕẕebe-lleẕîne min ḳablihim vemâ belegû mi\\`şâra mâ âteynâhüm fekeẕẕebû rusülî. fekeyfe kâne nekîr.');
INSERT INTO `tr_transliteration` VALUES (3652, 'ḳul innemâ e\\`iżuküm bivâḥideh. en teḳûmû lillâhi meŝnâ vefürâdâ ŝümme tetefekkerû. mâ biṣâḥibiküm min cinneh. in hüve illâ neẕîrul leküm beyne yedey \\`aẕâbin şedîd.');
INSERT INTO `tr_transliteration` VALUES (3653, 'ḳul mâ seeltüküm min ecrin fehüve leküm. in ecriye illâ \\`ale-llâh. vehüve \\`alâ külli şey\'in şehîd.');
INSERT INTO `tr_transliteration` VALUES (3654, 'ḳul inne rabbî yaḳẕifü bilḥaḳḳ. \\`allâmü-lguyûb.');
INSERT INTO `tr_transliteration` VALUES (3655, 'ḳul câe-lḥaḳḳu vemâ yübdiü-lbâṭilü vemâ yü\\`îd.');
INSERT INTO `tr_transliteration` VALUES (3656, 'ḳul in ḍaleltü feinnemâ eḍillü \\`alâ nefsî. veini-htedeytü febimâ yûḥî ileyye rabbî. innehû semî\\`un ḳarîb.');
INSERT INTO `tr_transliteration` VALUES (3657, 'velev terâ iẕ fezi\\`û felâ fevte veüḫiẕû mim mekânin ḳarîb.');
INSERT INTO `tr_transliteration` VALUES (3658, 'veḳâlû âmennâ bih. veennâ lehümü-ttenâvuşü mim mekânim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (3659, 'veḳad keferû bihî min ḳabl. veyaḳẕifûne bilgaybi mim mekânim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (3660, 'veḥîle beynehüm vebeyne mâ yeştehûne kemâ fu\\`ile bieşyâ\\`ihim min ḳabl. innehüm kânû fî şekkim mürîbün.');
INSERT INTO `tr_transliteration` VALUES (3661, 'elḥamdü lillâhi fâṭiri-ssemâvâti vel\'arḍi câ\\`ili-lmelâiketi rusülen ülî ecniḥatim meŝnâ veŝülâŝe verubâ\\`. yezîdü fi-lḫalḳi mâ yeşâ\'. inne-llâhe \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (3662, 'mâ yefteḥi-llâhü linnâsi mir raḥmetin felâ mümsike lehâ. vemâ yümsik felâ mürsile lehû mim ba\\`dih. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3663, 'yâ eyyühe-nnâsü-ẕkürû ni\\`mete-llâhi \\`aleyküm. hel min ḫâliḳin gayru-llâhi yerzüḳuküm mine-ssemâi vel\'arḍ. lâ ilâhe illâ hû. feennâ tü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (3664, 'veiy yükeẕẕibûke feḳad küẕẕibet rusülüm min ḳablik. veile-llâhi türce\\`u-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (3665, 'yâ eyyühe-nnâsü inne va\\`de-llâhi ḥaḳḳun felâ tegurrannekümü-lḥayâtü-ddünyâ. velâ yegurranneküm billâhi-lgarûr.');
INSERT INTO `tr_transliteration` VALUES (3666, 'inne-şşeyṭâne leküm \\`adüvvün fetteḫiẕûhü \\`adüvvâ. innemâ yed\\`û ḥizbehû liyekûnû min aṣḥâbi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (3667, 'elleẕîne keferû lehüm \\`aẕâbün şedîd. velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lehüm magfiratüv veecrun kebîr.');
INSERT INTO `tr_transliteration` VALUES (3668, 'efemen züyyine lehû sûü \\`amelihî feraâhü ḥasenâ. feinne-llâhe yüḍillü mey yeşâü veyehdî mey yeşâ\'. felâ teẕheb nefsüke \\`aleyhim ḥaserât. inne-llâhe \\`alîmüm bimâ yaṣne\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3669, 'vellâhü-lleẕî ersele-rriyâḥa fetüŝîru seḥâben fesuḳnâhü ilâ beledim meyyitin feaḥyeynâ bihi-l\'arḍa ba\\`de mevtihâ. keẕâlike-nnüşûr.');
INSERT INTO `tr_transliteration` VALUES (3670, 'men kâne yürîdü-l\\`izzete felillâhi-l\\`izzetü cemî\\`â. ileyhi yaṣ\\`adü-lkelimu-ṭṭayyibü vel\\`amelu-ṣṣâliḥu yerfe\\`uh. velleẕîne yemkürûne-sseyyiâti lehüm \\`aẕâbün şedîd. vemekru ülâike hüve yebûr.');
INSERT INTO `tr_transliteration` VALUES (3671, 'vellâhü ḫaleḳaküm min türâbin ŝümme min nuṭfetin ŝümme ce\\`aleküm ezvâcâ. vemâ taḥmilü min ünŝâ velâ teḍa\\`u illâ bi\\`ilmih. vemâ yü\\`ammeru mim mü\\`ammeriv velâ yünḳaṣu min \\`umürihî illâ fî kitâb. inne ẕâlike \\`ale-llâhi yesîr.');
INSERT INTO `tr_transliteration` VALUES (3672, 'vemâ yestevi-lbaḥrân. hâẕâ \\`aẕbün fürâtün sâigun şerâbühû vehâẕâ milḥun ücâc. vemin küllin te\'külûne laḥmen ṭariyyev vetestaḫricûne ḥilyeten telbesûnehâ. vetera-lfülke fîhi mevâḫira litebtegû min faḍlihî vele\\`alleküm teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (3673, 'yûlicü-lleyle fi-nnehâri veyûlicü-nnehâra fi-lleyli veseḫḫara-şşemse velḳamer. küllüy yecrî liecelim müsemmâ. ẕâlikümü-llâhü rabbüküm lehü-lmülk. velleẕîne ted\\`ûne min dûnihî mâ yemlikûne min ḳiṭmîr.');
INSERT INTO `tr_transliteration` VALUES (3674, 'in ted\\`ûhüm lâ yesme\\`û dü\\`âeküm. velev semi\\`û me-stecâbû leküm. veyevme-lḳiyâmeti yekfürûne bişirkiküm. velâ yünebbiüke miŝlü ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (3675, 'yâ eyyühe-nnâsü entümü-lfüḳarâü ile-llâh. vellâhü hüve-lganiyyü-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (3676, 'iy yeşe\' yüẕhibküm veye\'ti biḫalḳin cedîd.');
INSERT INTO `tr_transliteration` VALUES (3677, 'vemâ ẕâlike \\`ale-llâhi bi\\`azîz.');
INSERT INTO `tr_transliteration` VALUES (3678, 'velâ teziru vâziratüv vizra uḫrâ. vein ted\\`u müŝḳaletün ilâ ḥimlihâ lâ yuḥmel minhü şey\'üv velev kâne ẕâ ḳurbâ. innemâ tünẕiru-lleẕîne yaḫşevne rabbehüm bilgaybi veeḳâmu-ṣṣalâh. vemen tezekkâ feinnemâ yetezekkâ linefsih. veile-llâhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (3679, 'vemâ yestevi-l\'a\\`mâ velbeṣîr.');
INSERT INTO `tr_transliteration` VALUES (3680, 'vele-żżulümâtü vele-nnûr.');
INSERT INTO `tr_transliteration` VALUES (3681, 'vele-żżillü vele-lḥarûr.');
INSERT INTO `tr_transliteration` VALUES (3682, 'vemâ yestevi-l\'aḥyâü vele-l\'emvât. inne-llâhe yüsmi\\`u mey yeşâ\'. vemâ ente bimüsmi\\`im men fi-lḳubûr.');
INSERT INTO `tr_transliteration` VALUES (3683, 'in ente illâ neẕîr.');
INSERT INTO `tr_transliteration` VALUES (3684, 'innâ erselnâke bilḥaḳḳi beşîrav veneẕîrâ. veim min ümmetin illâ ḫalâ fîhâ neẕîr.');
INSERT INTO `tr_transliteration` VALUES (3685, 'veiy yükeẕẕibûke feḳad keẕẕebe-lleẕîne min ḳablihim. câethüm rusülühüm bilbeyyinâti vebizzübüri vebilkitâbi-lmünîr.');
INSERT INTO `tr_transliteration` VALUES (3686, 'ŝümme eḫaẕtü-lleẕîne keferû fekeyfe kâne nekîr.');
INSERT INTO `tr_transliteration` VALUES (3687, 'elem tera enne-llâhe enzele mine-ssemâi mââ. feaḫracnâ bihî ŝemerâtim muḫtelifen elvânühâ. vemine-lcibâli cüdedüm bîḍuv veḥumrum muḫtelifün elvânühâ vegarâbîbü sûd.');
INSERT INTO `tr_transliteration` VALUES (3688, 'vemine-nnâsi veddevâbbi vel\'en\\`âmi muḫtelifün elvânühû keẕâlik. innemâ yaḫşe-llâhe min \\`ibâdihi-l\\`ulemâ\'. inne-llâhe \\`azîzün gafûr.');
INSERT INTO `tr_transliteration` VALUES (3689, 'inne-lleẕîne yetlûne kitâbe-llâhi veeḳâmu-ṣṣalâte veenfeḳû mimmâ razaḳnâhüm sirrav ve\\`alâniyetey yercûne ticâratel len tebûra.');
INSERT INTO `tr_transliteration` VALUES (3690, 'liyüveffiyehüm ücûrahüm veyezîdehüm min faḍlih. innehû gafûrun şekûr.');
INSERT INTO `tr_transliteration` VALUES (3691, 'velleẕî evḥaynâ ileyke mine-lkitâbi hüve-lḥaḳḳu müṣaddiḳal limâ beyne yedeyh. inne-llâhe bi\\`ibâdihî leḫabîrum beṣîr.');
INSERT INTO `tr_transliteration` VALUES (3692, 'ŝümme evraŝne-lkitâbe-lleẕîne-ṣṭafeynâ min \\`ibâdinâ. feminhüm żâlimül linefsih. veminhüm muḳteṣid. veminhüm sâbiḳum bilḫayrâti biiẕni-llâh. ẕâlike hüve-lfaḍlü-lkebîr.');
INSERT INTO `tr_transliteration` VALUES (3693, 'cennâtü \\`adniy yedḫulûnehâ yüḥallevne fîhâ min esâvira min ẕehebiv velü\'lüâ. velibâsühüm fîhâ ḥarîr.');
INSERT INTO `tr_transliteration` VALUES (3694, 'veḳâlü-lḥamdü lillâhi-lleẕî eẕhebe \\`anne-lḥazen. inne rabbenâ legafûrun şekûr.');
INSERT INTO `tr_transliteration` VALUES (3695, 'elleẕî eḥallenâ dâra-lmüḳâmeti min faḍlih. lâ yemessünâ fîhâ neṣabüv velâ yemessünâ fîhâ lügûb.');
INSERT INTO `tr_transliteration` VALUES (3696, 'velleẕîne keferû lehüm nâru cehennem. lâ yuḳḍâ \\`aleyhim feyemûtû velâ yüḫaffefü \\`anhüm min \\`aẕâbihâ. keẕâlike neczî külle kefûr.');
INSERT INTO `tr_transliteration` VALUES (3697, 'vehüm yaṣṭariḫûne fîhâ. rabbenâ aḫricnâ na\\`mel ṣâliḥan gayra-lleẕî künnâ na\\`mel. evelem nü\\`ammirküm mâ yeteẕekkeru fîhi men teẕekkera vecâekümü-nneẕîr. feẕûḳû femâ liżżâlimîne min neṣîr.');
INSERT INTO `tr_transliteration` VALUES (3698, 'inne-llâhe \\`âlimü gaybi-ssemâvâti vel\'arḍ. innehû \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (3699, 'hüve-lleẕî ce\\`aleküm ḫalâife fi-l\'arḍ. femen kefera fe\\`aleyhi küfruh. velâ yezîdü-lkâfirîne küfruhüm \\`inde rabbihim illâ maḳtâ. velâ yezîdü-lkâfirîne küfruhüm illâ ḫasârâ.');
INSERT INTO `tr_transliteration` VALUES (3700, 'ḳul era\'eytüm şürakâekümü-lleẕîne ted\\`ûne min dûni-llâh. erûnî mâẕâ ḫaleḳû mine-l\'arḍi em lehüm şirkün fi-ssemâvât. em âteynâhüm kitâben fehüm \\`alâ beyyinetim minh. bel iy ye\\`idu-żżâlimûne ba\\`ḍuhüm ba\\`ḍan illâ gurûrâ.');
INSERT INTO `tr_transliteration` VALUES (3701, 'inne-llâhe yümsikü-ssemâvâti vel\'arḍa en tezûlâ. velein zâletâ in emsekehümâ min eḥadim mim ba\\`dih. innehû kâne ḥalîmen gafûrâ.');
INSERT INTO `tr_transliteration` VALUES (3702, 'veaḳsemû billâhi cehde eymânihim lein câehüm neẕîrul leyekûnünne ehdâ min iḥde-l\'ümem. felemmâ câehüm neẕîrum mâ zâdehüm illâ nüfûrâ.');
INSERT INTO `tr_transliteration` VALUES (3703, 'istikbâran fi-l\'arḍi vemekra-sseyyi\'. velâ yeḥîḳu-lmekru-sseyyiü illâ biehlih. fehel yenżurûne illâ sünnete-l\'evvelîn. felen tecide lisünneti-llâhi tebdîlâ. velen tecide lisünneti-llâhi taḥvîlâ.');
INSERT INTO `tr_transliteration` VALUES (3704, 'evelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne \\`âḳibetü-lleẕîne min ḳablihim vekânû eşedde minhüm ḳuvveh. vemâ kâne-llâhü liyü\\`cizehû min şey\'in fi-ssemâvâti velâ fi-l\'arḍ. innehû kâne \\`alîmen ḳadîrâ.');
INSERT INTO `tr_transliteration` VALUES (3705, 'velev yüâḫiẕü-llâhü-nnâse bimâ kesebû mâ terake \\`alâ żahrihâ min dâbbetiv velâkiy yü\'eḫḫiruhüm ilâ ecelim müsemmâ. feiẕâ câe ecelühüm feinne-llâhe kâne bi\\`ibâdihî beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (3706, 'yâ-sîn.');
INSERT INTO `tr_transliteration` VALUES (3707, 'velḳur\'âni-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (3708, 'inneke lemine-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (3709, '\\`alâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (3710, 'tenzîle-l\\`azîzi-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (3711, 'litünẕira ḳavmem mâ ünẕira âbâühüm fehüm gâfilûn.');
INSERT INTO `tr_transliteration` VALUES (3712, 'leḳad ḥaḳḳa-lḳavlü \\`alâ ekŝerihim fehüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3713, 'innâ ce\\`alnâ fî a\\`nâḳihim aglâlen fehiye ile-l\'eẕḳâni fehüm muḳmeḥûn.');
INSERT INTO `tr_transliteration` VALUES (3714, 'vece\\`alnâ mim beyni eydîhim seddev vemin ḫalfihim sedden feagşeynâhüm fehüm lâ yübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (3715, 'vesevâün \\`aleyhim eenẕertehüm em lem tünẕirhüm lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (3716, 'innemâ tünẕiru meni-ttebe\\`a-ẕẕikra veḫaşiye-rraḥmâne bilgayb. febeşşirhü bimagfirativ veecrin kerîm.');
INSERT INTO `tr_transliteration` VALUES (3717, 'innâ naḥnü nuḥyi-lmevtâ venektübü mâ ḳaddemû veâŝârahüm. vekülle şey\'in aḥṣaynâhü fî imâmim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3718, 'vaḍrib lehüm meŝelen aṣḥâbe-lḳaryeh. iẕ câehe-lmürselûn.');
INSERT INTO `tr_transliteration` VALUES (3719, 'iẕ erselnâ ileyhimü-ŝneyni fekeẕẕebûhümâ fe\\`azzeznâ biŝâliŝin feḳâlû innâ ileyküm mürselûn.');
INSERT INTO `tr_transliteration` VALUES (3720, 'ḳâlû mâ entüm illâ beşerum miŝlünâ vemâ enzele-rraḥmânü min şey\'in in entüm illâ tekẕibûn.');
INSERT INTO `tr_transliteration` VALUES (3721, 'ḳâlû rabbünâ ya\\`lemü innâ ileyküm lemürselûn.');
INSERT INTO `tr_transliteration` VALUES (3722, 'vemâ \\`aleynâ ille-lbelâgu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (3723, 'ḳâlû innâ teṭayyernâ biküm. leil lem tentehû lenercümenneküm veleyemessenneküm minnâ \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (3724, 'ḳâlû ṭâiruküm me\\`aküm. ein ẕükkirtüm. bel entüm ḳavmüm müsrifûn.');
INSERT INTO `tr_transliteration` VALUES (3725, 'vecâe min aḳṣe-lmedîneti racülüy yes\\`â ḳâle yâ ḳavmi-ttebi\\`ü-lmürselîn.');
INSERT INTO `tr_transliteration` VALUES (3726, 'ittebi\\`û mel lâ yes\'elüküm ecrav vehüm mühtedûn.');
INSERT INTO `tr_transliteration` VALUES (3727, 'vemâ liye lâ a\\`büdü-lleẕî feṭaranî veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3728, 'eetteḫiẕü min dûnihî âliheten iy yüridni-rraḥmânü biḍurril lâ tugni \\`annî şefâ\\`atühüm şey\'ev velâ yünḳiẕûn.');
INSERT INTO `tr_transliteration` VALUES (3729, 'innî iẕel lefî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (3730, 'innî âmentü birabbiküm fesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3731, 'ḳîle-dḫuli-lcenneh. ḳâle yâ leyte ḳavmî ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3732, 'bimâ gafera lî rabbî vece\\`alenî mine-lmükramîn.');
INSERT INTO `tr_transliteration` VALUES (3733, 'vemâ enzelnâ \\`alâ ḳavmihî mim ba\\`dihî min cündim mine-ssemâi vemâ künnâ münzilîn.');
INSERT INTO `tr_transliteration` VALUES (3734, 'in kânet illâ ṣayḥatev vâḥideten feiẕâ hüm ḫâmidûn.');
INSERT INTO `tr_transliteration` VALUES (3735, 'yâ ḥasraten \\`ale-l\\`ibâd. mâ ye\'tîhim mir rasûlin illâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (3736, 'elem yerav kem ehleknâ ḳablehüm mine-lḳurûni ennehüm ileyhim lâ yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (3737, 'vein küllül lemmâ cemî\\`ul ledeynâ muḥḍarûn.');
INSERT INTO `tr_transliteration` VALUES (3738, 'veâyetül lehümü-l\'arḍu-lmeyteh. aḥyeynâhâ veaḫracnâ minhâ ḥabben feminhü ye\'külûn.');
INSERT INTO `tr_transliteration` VALUES (3739, 'vece\\`alnâ fîhâ cennâtim min neḫîliv vea\\`nâbiv vefeccernâ fîhâ mine-l\\`uyûn.');
INSERT INTO `tr_transliteration` VALUES (3740, 'liye\'külû min ŝemerihî vemâ \\`amilethü eydîhim. efelâ yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (3741, 'sübḥâne-lleẕî ḫaleḳa-l\'ezvâce küllehâ mimmâ tümbitü-l\'arḍu vemin enfüsihim vemimmâ lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (3742, 'veâyetül lehümü-lleyl. nesleḫu minhü-nnehâra feiẕâ hüm mużlimûn.');
INSERT INTO `tr_transliteration` VALUES (3743, 'veşşemsü tecrî limüsteḳarril lehâ. ẕâlike taḳdîru-l\\`azîzi-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (3744, 'velḳamera ḳaddernâhü menâzile ḥattâ \\`âde kel\\`urcûni-lḳadîm.');
INSERT INTO `tr_transliteration` VALUES (3745, 'le-şşemsü yembegî lehâ en tüdrike-lḳamera vele-lleylü sâbiḳu-nnehâr. veküllün fî felekiy yesbeḥûn.');
INSERT INTO `tr_transliteration` VALUES (3746, 'veâyetül lehüm ennâ ḥamelnâ ẕürriyyetehüm fi-lfülki-lmeşḥûn.');
INSERT INTO `tr_transliteration` VALUES (3747, 'veḫalaḳnâ lehüm mim miŝlihî mâ yerkebûn.');
INSERT INTO `tr_transliteration` VALUES (3748, 'vein neşe\' nugriḳhüm felâ ṣarîḫa lehüm velâ hüm yünḳaẕûn.');
