INSERT INTO `tr_transliteration` VALUES (4065, 'in tekfürû feinne-llâhe ganiyyün \\`anküm velâ yerḍâ li\\`ibâdihi-lküfr. vein teşkürû yerḍahü leküm. velâ teziru vâziratüv vizra uḫrâ. ŝümme ilâ rabbiküm merci\\`uküm feyünebbiüküm bimâ küntüm ta\\`melûn. innehû \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (4066, 'veiẕâ messe-l\'insâne ḍurrun de\\`â rabbehû münîben ileyhi ŝümme iẕâ ḫavvelehû ni\\`metem minhü nesiye mâ kâne yed\\`û ileyhi min ḳablü vece\\`ale lillâhi endâdel liyüḍille \\`an sebîlih. ḳul temetta\\` biküfrike ḳalîlen. inneke min aṣḥâbi-nnâr.');
INSERT INTO `tr_transliteration` VALUES (4067, 'emmen hüve ḳânitün ânâe-lleyli sâcidev veḳâimey yaḥẕeru-l\'âḫirate veyercû raḥmete rabbih. ḳul hel yestevi-lleẕîne ya\\`lemûne velleẕîne lâ ya\\`lemûn. innemâ yeteẕekkeru ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (4068, 'ḳul yâ \\`ibâdi-lleẕîne âmenü-tteḳû rabbeküm. lilleẕîne aḥsenû fî hâẕihi-ddünyâ ḥaseneh. vearḍu-llâhi vâsi\\`ah. innemâ yüveffe-ṣṣâbirûne ecrahüm bigayri ḥisâb.');
INSERT INTO `tr_transliteration` VALUES (4069, 'ḳul innî ümirtü en a\\`büde-llâhe muḫliṣal lehü-ddîn.');
INSERT INTO `tr_transliteration` VALUES (4070, 'veümirtü lien ekûne evvele-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (4071, 'ḳul innî eḫâfü in \\`aṣaytü rabbî \\`aẕâbe yevmin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (4072, 'ḳuli-llâhe a\\`büdü muḫliṣal lehû dînî.');
INSERT INTO `tr_transliteration` VALUES (4073, 'fa\\`büdû mâ şi\'tüm min dûnih. ḳul inne-lḫâsirîne-lleẕîne ḫasirû enfüsehüm veehlîhim yevme-lḳiyâmeh. elâ ẕâlike hüve-lḫusrânü-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (4074, 'lehüm min fevḳihim żulelüm mine-nnâri vemin taḥtihim żulelün. ẕâlike yüḫavvifü-llâhü bihî \\`ibâdeh. yâ \\`ibâdi fetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (4075, 'velleẕîne-ctenebu-ṭṭâgûte ey ya\\`büdûhâ veenâbû ile-llâhi lehümü-lbüşrâ. febeşşir \\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (4076, 'elleẕîne yestemi\\`ûne-lḳavle feyettebi\\`ûne aḥseneh. ülâike-lleẕîne hedâhümü-llâhü veülâike hüm ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (4077, 'efemen ḥaḳḳa \\`aleyhi kelimetü-l\\`aẕâb. efeente tünḳiẕü men fi-nnâr.');
INSERT INTO `tr_transliteration` VALUES (4078, 'lâkini-lleẕîne-tteḳav rabbehüm lehüm gurafüm min fevḳihâ gurafüm mebniyyetün tecrî min taḥtihe-l\'enhâr. va\\`de-llâh. lâ yuḫlifü-llâhü-lmî\\`âd.');
INSERT INTO `tr_transliteration` VALUES (4079, 'elem tera enne-llâhe enzele mine-ssemâi mâen feselekehû yenâbî\\`a fi-l\'arḍi ŝümme yuḫricü bihî zer\\`am muḫtelifen elvânühû ŝümme yehîcü feterâhü muṣferran ŝümme yec\\`alühû ḥuṭâmâ. inne fî ẕâlike leẕikrâ liüli-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (4080, 'efemen şeraḥa-llâhü ṣadrahû lil\'islâmi fehüve \\`alâ nûrum mir rabbih. feveylül lilḳâsiyeti ḳulûbühüm min ẕikri-llâh. ülâike fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (4081, 'allâhü nezzele aḥsene-lḥadîŝi kitâbem müteşâbihem meŝânî. taḳşe\\`irru minhü cülûdü-lleẕîne yaḫşevne rabbehüm. ŝümme telînü cülûdühüm veḳulûbühüm ilâ ẕikri-llâh. ẕâlike hüde-llâhi yehdî bihî mey yeşâ\'. vemey yuḍlili-llâhü femâ lehû min hâd.');
INSERT INTO `tr_transliteration` VALUES (4082, 'efemey yetteḳî bivechihî sûe-l\\`aẕâbi yevme-lḳiyâmeh. veḳîle liżżâlimîne ẕûḳû mâ küntüm teksibûn.');
INSERT INTO `tr_transliteration` VALUES (4083, 'keẕẕebe-lleẕîne min ḳablihim feetâhümü-l\\`aẕâbü min ḥayŝü lâ yeş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (4084, 'feeẕâḳahümü-llâhü-lḫizye fi-lḥayâti-ddünyâ. vele\\`aẕâbü-l\'âḫirati ekber. lev kânû ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4085, 'veleḳad ḍarabnâ linnâsi fî hâẕe-lḳur\'âni min külli meŝelil le\\`allehüm yeteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (4086, 'ḳur\'ânen \\`arabiyyen gayra ẕî \\`ivecil le\\`allehüm yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (4087, 'ḍarabe-llâhü meŝeler racülen fîhi şürakâü müteşâkisûne veracülen selemel liracül. hel yesteviyâni meŝelâ. elḥamdü lillâh. bel ekŝeruhüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4088, 'inneke meyyitüv veinnehüm meyyitûn.');
INSERT INTO `tr_transliteration` VALUES (4089, 'ŝümme inneküm yevme-lḳiyâmeti \\`inde rabbiküm taḫteṣimûn.');
INSERT INTO `tr_transliteration` VALUES (4090, 'femen ażlemü mimmen keẕebe \\`ale-llâhi vekeẕẕebe biṣṣidḳi iẕ câeh. eleyse fî cehenneme meŝvel lilkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (4091, 'velleẕî câe biṣṣidḳi veṣaddeḳa bihî ülâike hümü-lmütteḳûn.');
INSERT INTO `tr_transliteration` VALUES (4092, 'lehüm mâ yeşâûne \\`inde rabbihim. ẕâlike cezâü-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (4093, 'liyükeffira-llâhü \\`anhüm esvee-lleẕî \\`amilû veyecziyehüm ecrahüm biaḥseni-lleẕî kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4094, 'eleyse-llâhü bikâfin \\`abdeh. veyüḫavvifûneke billeẕîne min dûnih. vemey yuḍlili-llâhü femâ lehû min hâd.');
INSERT INTO `tr_transliteration` VALUES (4095, 'vemey yehdi-llâhü femâ lehû mim müḍill. eleyse-llâhü bi\\`azîzin ẕi-ntiḳâm.');
INSERT INTO `tr_transliteration` VALUES (4096, 'velein seeltehüm men ḫaleḳa-ssemâvâti vel\'arḍa leyeḳûlünne-llâh. ḳul eferaeytüm mâ ted\\`ûne min dûni-llâhi in erâdeniye-llâhü biḍurrin hel hünne kâşifâtü ḍurrihî ev erâdenî biraḥmetin hel hünne mümsikâtü raḥmetih. ḳul ḥasbiye-llâh. \\`aleyhi yetevekkelü-lmütevekkilûn.');
INSERT INTO `tr_transliteration` VALUES (4097, 'ḳul yâ ḳavmi-\\`melû \\`alâ mekânetiküm innî \\`âmil. fesevfe ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4098, 'mey ye\'tîhi \\`aẕâbüy yuḫzîhi veyeḥillü \\`aleyhi \\`aẕâbüm müḳîm.');
INSERT INTO `tr_transliteration` VALUES (4099, 'innâ enzelnâ \\`aleyke-lkitâbe linnâsi bilḥaḳḳ. femeni-htedâ felinefsih. vemen ḍalle feinnemâ yeḍillü \\`aleyhâ. vemâ ente \\`aleyhim bivekîl.');
INSERT INTO `tr_transliteration` VALUES (4100, 'allâhü yeteveffe-l\'enfüse ḥîne mevtihâ velletî lem temüt fî menâmihâ. feyümsikü-lletî ḳaḍâ \\`aleyhe-lmevte veyürsilü-l\'uḫrâ ilâ ecelim müsemmâ. inne fî ẕâlike leâyâtil liḳavmiy yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (4101, 'emi-tteḫaẕû min dûni-llâhi şüfe\\`â\'. ḳul evelev kânû lâ yemlikûne şey\'ev velâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (4102, 'ḳul lillâhi-şşefâ\\`atü cemî\\`â. lehû mülkü-ssemâvâti vel\'arḍ. ŝümme ileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4103, 'veiẕâ ẕükira-llâhü vaḥdehü-şmeezzet ḳulûbü-lleẕîne lâ yü\'minûne bil\'âḫirati. veiẕâ ẕükira-lleẕîne min dûnihî iẕâ hüm yestebşirûn.');
INSERT INTO `tr_transliteration` VALUES (4104, 'ḳuli-llâhümme fâṭira-ssemâvâti vel\'arḍi \\`âlime-lgaybi veşşehâdeti ente taḥkümü beyne \\`ibâdike fîmâ kânû fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (4105, 'velev enne lilleẕîne żalemû mâ fi-l\'arḍi cemî\\`av vemiŝlehû me\\`ahû leftedev bihî min sûi-l\\`aẕâbi yevme-lḳiyâmeh. vebedâ lehüm mine-llâhi mâ lem yekûnû yaḥtesibûn.');
INSERT INTO `tr_transliteration` VALUES (4106, 'vebedâ lehüm seyyietü mâ kesebû veḥâḳa bihim mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (4107, 'feiẕâ messe-l\'insâne ḍurrun de\\`ânâ. ŝümme iẕâ ḫavvelnâhü ni\\`metem minnâ ḳâle innemâ ûtîtühû \\`alâ \\`ilm. bel hiye fitnetüv velâkinne ekŝerahüm lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4108, 'ḳad ḳâlehe-lleẕîne min ḳablihim femâ agnâ \\`anhüm mâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (4109, 'feeṣâbehüm seyyietü mâ kesebû. velleẕîne żalemû min hâülâi seyüṣîbühüm seyyietü mâ kesebû vemâ hüm bimü\\`cizîn.');
INSERT INTO `tr_transliteration` VALUES (4110, 'evelem ya\\`lemû enne-llâhe yebsüṭu-rrizḳa limey yeşâü veyaḳdir. inne fî ẕâlike leâyâtil liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (4111, 'ḳul yâ \\`ibâdîye-lleẕîne esrafû \\`alâ enfüsihim lâ taḳneṭû mir raḥmeti-llâh. inne-llâhe yagfiru-ẕẕünûbe cemî\\`â. innehû hüve-lgafûru-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (4112, 'veenîbû ilâ rabbiküm veeslimû lehû min ḳabli ey ye\'tiyekümü-l\\`aẕâbü ŝümme lâ tünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (4113, 'vettebi\\`û aḥsene mâ ünzile ileyküm mir rabbiküm min ḳabli ey ye\'tiyekümü-l\\`aẕâbü bagtetev veentüm lâ teş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (4114, 'en teḳûle nefsüy yâ ḥasratâ \\`alâ mâ ferraṭtü fî cembi-llâhi vein küntü lemine-ssâḫirîn.');
INSERT INTO `tr_transliteration` VALUES (4115, 'ev teḳûle lev enne-llâhe hedânî leküntü mine-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (4116, 'ev teḳûle ḥîne tera-l\\`aẕâbe lev enne lî kerraten feekûne mine-lmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (4117, 'belâ ḳad câetke âyâtî fekeẕẕebte bihâ vestekberte vekünte mine-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (4118, 'veyevme-lḳiyâmeti tera-lleẕîne keẕebû \\`ale-llâhi vucûhühüm müsveddeh. eleyse fî cehenneme meŝvel lilmütekebbirîn.');
INSERT INTO `tr_transliteration` VALUES (4119, 'veyünecci-llâhü-lleẕîne-tteḳav bimefâzetihim. lâ yemessühümü-ssûü velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (4120, 'allâhü ḫâliḳu külli şey\'. vehüve \\`alâ külli şey\'iv vekîl.');
INSERT INTO `tr_transliteration` VALUES (4121, 'lehû meḳâlîdü-ssemâvâti vel\'arḍ. velleẕîne keferû biâyâti-llâhi ülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (4122, 'ḳul efegayra-llâhi te\'mürûnnî a\\`büdü eyyühe-lcâhilûn.');
INSERT INTO `tr_transliteration` VALUES (4123, 'veleḳad ûḥiye ileyke veile-lleẕîne min ḳablik. lein eşrakte leyaḥbeṭanne \\`amelüke veletekûnenne mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (4124, 'beli-llâhe fa\\`büd veküm mine-şşâkirîn.');
INSERT INTO `tr_transliteration` VALUES (4125, 'vemâ ḳaderü-llâhe ḥaḳḳa ḳadrih. vel\'arḍu cemî\\`an ḳabḍatühû yevme-lḳiyâmeti vessemâvâtü maṭviyyâtüm biyemînih. sübḥânehû vete\\`âlâ \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (4126, 'venüfiḫa fi-ṣṣûri feṣa\\`iḳa men fi-ssemâvâti vemen fi-l\'arḍi illâ men şâe-llâh. ŝümme nüfiḫa fîhi uḫrâ feiẕâ hüm ḳiyâmüy yenżurûn.');
INSERT INTO `tr_transliteration` VALUES (4127, 'veeşraḳati-l\'arḍu binûri rabbihâ vevuḍi\\`a-lkitâbü vecîe binnebiyyîne veşşühedâi veḳuḍiye beynehüm bilḥaḳḳi vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (4128, 'vevuffiyet küllü nefsim mâ \\`amilet vehüve a\\`lemü bimâ yef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (4129, 'vesîḳa-lleẕîne keferû ilâ cehenneme zümerâ. ḥattâ iẕâ câûhâ fütiḥat ebvâbühâ veḳâle lehüm ḫazenetühâ elem ye\'tiküm rusülüm minküm yetlûne \\`aleyküm âyâti rabbiküm veyünẕirûneküm liḳâe yevmiküm hâẕâ. ḳâlû belâ velâkin ḥaḳḳat kelimetü-l\\`aẕâbi \\`ale-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (4130, 'ḳîle-dḫulû ebvâbe cehenneme ḫâlidîne fîhâ. febi\'se meŝve-lmütekebbirîn.');
INSERT INTO `tr_transliteration` VALUES (4131, 'vesîḳa-lleẕîne-tteḳav rabbehüm ile-lcenneti zümerâ. ḥattâ iẕâ câûhâ vefütiḥat ebvâbühâ veḳâle lehüm ḫazenetühâ selâmün \\`aleyküm ṭibtüm fedḫulûhâ ḫâlidîn.');
INSERT INTO `tr_transliteration` VALUES (4132, 'veḳâlü-lḥamdü lillâhi-lleẕî ṣadeḳanâ va\\`dehû veevraŝene-l\'arḍa netebevveü mine-lcenneti ḥayŝü neşâ\'. feni\\`me ecru-l\\`âmilîn.');
INSERT INTO `tr_transliteration` VALUES (4133, 'vetera-lmelâikete ḥâffîne min ḥavli-l\\`arşi yüsebbiḥûne biḥamdi rabbihim. veḳuḍiye beynehüm bilḥaḳḳi veḳîle-lḥamdü lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4134, 'ḥâ-mîm.');
INSERT INTO `tr_transliteration` VALUES (4135, 'tenzîlü-lkitâbi mine-llâhi-l\\`azîzi-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4136, 'gâfiri-ẕẕembi veḳâbili-ttevbi şedîdi-l\\`iḳâbi ẕi-ṭṭavl. lâ ilâhe illâ hû. ileyhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (4137, 'mâ yücâdilü fî âyâti-llâhi ille-lleẕîne keferû felâ yagrurke teḳallübühüm fi-lbilâd.');
INSERT INTO `tr_transliteration` VALUES (4138, 'keẕẕebet ḳablehüm ḳavmü nûḥiv vel\'aḥzâbü mim ba\\`dihim. vehemmet küllü ümmetim birasûlihim liye\'ḫuẕûhü vecâdelû bilbâṭili liyüdḥiḍû bihi-lḥaḳḳa feeḫaẕtühüm. fekeyfe kâne \\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (4139, 'vekeẕâlike ḥaḳḳat kelimetü rabbike \\`ale-lleẕîne keferû ennehüm aṣḥâbü-nnâr.');
INSERT INTO `tr_transliteration` VALUES (4140, 'elleẕîne yaḥmilûne-l\\`arşe vemen ḥavlehû yüsebbiḥûne biḥamdi rabbihim veyü\'minûne bihî veyestagfirûne lilleẕîne âmenû. rabbenâ vesi\\`te külle şey\'ir raḥmetev ve\\`ilmen fagfir lilleẕîne tâbû vettebe\\`û sebîleke veḳihim \\`aẕâbe-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (4141, 'rabbenâ veedḫilhüm cennâti \\`adnini-lletî ve\\`attehüm vemen ṣaleḥa min âbâihim veezvâcihim veẕürriyyâtihim. inneke ente-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4142, 'veḳihimü-sseyyiât. vemen teḳi-sseyyiâti yevmeiẕin feḳad raḥimteh. veẕâlike hüve-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (4143, 'inne-lleẕîne keferû yünâdevne lemaḳtü-llâhi ekberu mim maḳtiküm enfüseküm iẕ tüd\\`avne ile-l\'îmâni fetekfürûn.');
INSERT INTO `tr_transliteration` VALUES (4144, 'ḳâlû rabbenâ emettene-ŝneteyni veaḥyeytene-ŝneteyni fa\\`terafnâ biẕünûbinâ fehel ilâ ḫurûcim min sebîl.');
INSERT INTO `tr_transliteration` VALUES (4145, 'ẕâliküm biennehû iẕâ dü\\`iye-llâhü vaḥdehû kefertüm. veiy yüşrak bihî tü\'minû. felḥukmü lillâhi-l\\`aliyyi-lkebîr.');
INSERT INTO `tr_transliteration` VALUES (4146, 'hüve-lleẕî yürîküm âyâtihî veyünezzilü leküm mine-ssemâi rizḳâ. vemâ yeteẕekkeru illâ mey yünîb.');
INSERT INTO `tr_transliteration` VALUES (4147, 'fed\\`ü-llâhe muḫliṣîne lehü-ddîne velev kerihe-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (4148, 'rafî\\`u-dderacâti ẕü-l\\`arş. yülḳi-rrûḥa min emrihî \\`alâ mey yeşâü min \\`ibâdihî liyünẕira yevme-ttelâḳ.');
INSERT INTO `tr_transliteration` VALUES (4149, 'yevme hüm bârizûn. lâ yaḫfâ \\`ale-llâhi minhüm şey\'ün. limeni-lmülkü-lyevm. lillâhi-lvâḥidi-lḳahhâr.');
INSERT INTO `tr_transliteration` VALUES (4150, 'elyevme tüczâ küllü nefsim bimâ kesebet. lâ żulme-lyevm. inne-llâhe serî\\`u-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (4151, 'veenẕirhüm yevme-l\'âzifeti iẕi-lḳulûbü lede-lḥanâciri kâżimîn. mâ liżżâlimîne min ḥamîmiv velâ şefî\\`iy yüṭâ\\`.');
INSERT INTO `tr_transliteration` VALUES (4152, 'ya\\`lemü ḫâinete-l\'a\\`yüni vemâ tuḫfi-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (4153, 'vellâhü yaḳḍî bilḥaḳḳ. velleẕîne yed\\`ûne min dûnihî lâ yaḳḍûne bişey\'. inne-llâhe hüve-ssemî\\`u-lbeṣîr.');
INSERT INTO `tr_transliteration` VALUES (4154, 'evelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne \\`âḳibetü-lleẕîne kânû min ḳablihim. kânû hüm eşedde minhüm ḳuvvetev veâŝâran fi-l\'arḍi feeḫaẕehümü-llâhü biẕünûbihim vemâ kâne lehüm mine-llâhi miv vâḳ.');
INSERT INTO `tr_transliteration` VALUES (4155, 'ẕâlike biennehüm kânet te\'tîhim rusülühüm bilbeyyinâti fekeferû feeḫaẕehümü-llâh. innehû ḳaviyyün şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (4156, 'veleḳad erselnâ mûsâ biâyâtinâ vesülṭânim mübîn.');
INSERT INTO `tr_transliteration` VALUES (4157, 'ilâ fir\\`avne vehâmâne veḳârûne feḳâlû sâḥirun keẕẕâb.');
INSERT INTO `tr_transliteration` VALUES (4158, 'felemmâ câehüm bilḥaḳḳi min \\`indinâ ḳâlu-ḳtülû ebnâe-lleẕîne âmenû me\\`ahû vestaḥyû nisâehüm. vemâ keydü-lkâfirîne illâ fî ḍalâl.');
INSERT INTO `tr_transliteration` VALUES (4159, 'veḳâle fir\\`avnü ẕerûnî aḳtül mûsâ velyed\\`u rabbeh. innî eḫâfü ey yübeddile dîneküm ev ey yużhira fi-l\'arḍi-lfesâd.');
INSERT INTO `tr_transliteration` VALUES (4160, 'veḳâle mûsâ innî \\`uẕtü birabbî verabbiküm min külli mütekebbiril lâ yü\'minü biyevmi-lḥisâb.');
INSERT INTO `tr_transliteration` VALUES (4161, 'veḳâle racülüm mü\'min. min âli fir\\`avne yektümü îmânehû etaḳtülûne racülen ey yeḳûle rabbiye-llâhü veḳad câeküm bilbeyyinâti mir rabbiküm. veiy yekü kâẕiben fe\\`aleyhi keẕibüh. veiy yekü ṣâdiḳay yüṣibküm ba\\`ḍu-lleẕî ye\\`idüküm. inne-llâhe lâ yehdî men hüve müsrifün keẕẕâb.');
INSERT INTO `tr_transliteration` VALUES (4162, 'yâ ḳavmi lekümü-lmülkü-lyevme żâhirîne fi-l\'arḍ. femey yenṣurunâ mim be\'si-llâhi in câenâ. ḳâle fir\\`avnü mâ ürîküm illâ mâ erâ vemâ ehdîküm illâ sebîle-rraşâd.');
INSERT INTO `tr_transliteration` VALUES (4163, 'veḳâle-lleẕî âmene yâ ḳavmi innî eḫâfü \\`aleyküm miŝle yevmi-l\'aḥzâb.');
INSERT INTO `tr_transliteration` VALUES (4164, 'miŝle de\'bi ḳavmi nûḥiv ve\\`âdiv veŝemûde velleẕîne mim ba\\`dihim. veme-llâhü yürîdü żulmel lil\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (4165, 'veyâ ḳavmi innî eḫâfü \\`aleyküm yevme-ttenâd.');
INSERT INTO `tr_transliteration` VALUES (4166, 'yevme tüvellûne müdbirîn. mâ leküm mine-llâhi min \\`âṣim. vemey yuḍlili-llâhü femâ lehû min hâd.');
INSERT INTO `tr_transliteration` VALUES (4167, 'veleḳad câeküm yûsüfü min ḳablü bilbeyyinâti femâ ziltüm fî şekkim mimmâ câeküm bih. ḥattâ iẕâ heleke ḳultüm ley yeb\\`aŝe-llâhü mim ba\\`dihî rasûlâ. keẕâlike yüḍillü-llâhü men hüve müsrifüm mürtâb.');
INSERT INTO `tr_transliteration` VALUES (4168, 'elleẕîne yücâdilûne fî âyâti-llâhi bigayri sülṭânin etâhüm. kebüra maḳten \\`inde-llâhi ve\\`inde-lleẕîne âmenû. keẕâlike yaṭbe\\`u-llâhü \\`alâ külli ḳalbi mütekebbirin cebbâr.');
INSERT INTO `tr_transliteration` VALUES (4169, 'veḳâle fir\\`avnü yâ hâmânü-bni lî ṣarḥal le\\`allî eblügu-l\'esbâb.');
INSERT INTO `tr_transliteration` VALUES (4170, 'esbâbe-ssemâvâti feeṭṭali\\`a ilâ ilâhi mûsâ veinnî leeżunnühû kâẕibâ. vekeẕâlike züyyine lifir\\`avne sûü \\`amelihî veṣudde \\`ani-ssebîl. vemâ keydü fir\\`avne illâ fî tebâb.');
INSERT INTO `tr_transliteration` VALUES (4171, 'veḳâle-lleẕî âmene yâ ḳavmi-ttebi\\`ûni ehdiküm sebîle-rraşâd.');
INSERT INTO `tr_transliteration` VALUES (4172, 'yâ ḳavmi innemâ hâẕihi-lḥayâtü-ddünyâ metâ\\`. veinne-l\'âḫirate hiye dâru-lḳarâr.');
INSERT INTO `tr_transliteration` VALUES (4173, 'men \\`amile seyyieten felâ yüczâ illâ miŝlehâ. vemen \\`amile ṣâliḥam min ẕekerin ev ünŝâ vehüve mü\'minün feülâike yedḫulûne-lcennete yürzeḳûne fîhâ bigayri ḥisâb.');
INSERT INTO `tr_transliteration` VALUES (4174, 'veyâ ḳavmi mâ lî ed\\`ûküm ile-nnecâti veted\\`ûnenî ile-nnâr.');
INSERT INTO `tr_transliteration` VALUES (4175, 'ted\\`ûnenî liekfüra billâhi veüşrike bihî mâ leyse lî bihî \\`ilm. veenâ ed\\`ûküm ile-l\\`azîzi-lgaffâr.');
INSERT INTO `tr_transliteration` VALUES (4176, 'lâ cerame ennemâ ted\\`ûnenî ileyhi leyse lehû da\\`vetün fi-ddünyâ velâ fi-l\'âḫirati veenne meraddenâ ile-llâhi veenne-lmüsrifîne hüm aṣḥâbü-nnâr.');
INSERT INTO `tr_transliteration` VALUES (4177, 'feseteẕkürûne mâ eḳûlü leküm. veüfevviḍu emrî ile-llâh. inne-llâhe beṣîrum bil\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (4178, 'feveḳâhü-llâhü seyyiâti mâ mekerû veḥâḳa biâli fir\\`avne sûü-l\\`aẕâb.');
INSERT INTO `tr_transliteration` VALUES (4179, 'ennâru yü\\`raḍûne \\`aleyhâ gudüvvev ve\\`aşiyyâ. veyevme teḳûmü-ssâ\\`ah. edḫilû âle fir\\`avne eşedde-l\\`aẕâb.');
INSERT INTO `tr_transliteration` VALUES (4180, 'veiẕ yeteḥâccûne fi-nnâri feyeḳûlu-ḍḍu\\`afâü lilleẕîne-stekberû innâ künnâ leküm tebe\\`an fehel entüm mugnûne \\`annâ neṣîbem mine-nnâr.');
INSERT INTO `tr_transliteration` VALUES (4181, 'ḳâle-lleẕîne-stekberû innâ küllün fîhâ inne-llâhe ḳad ḥakeme beyne-l\\`ibâd.');
INSERT INTO `tr_transliteration` VALUES (4182, 'veḳâle-lleẕîne fi-nnâri liḫazeneti cehenneme-d\\`û rabbeküm yüḫaffif \\`annâ yevmem mine-l\\`aẕâb.');
INSERT INTO `tr_transliteration` VALUES (4183, 'ḳâlû evelem tekü te\'tîküm rusülüküm bilbeyyinât. ḳâlû belâ. ḳâlû fed\\`û. vemâ dü\\`âü-lkâfirîne illâ fî ḍalâl.');
INSERT INTO `tr_transliteration` VALUES (4184, 'innâ lenenṣuru rusülenâ velleẕîne âmenû fi-lḥayâti-ddünyâ veyevme yeḳûmü-l\'eşhâd.');
INSERT INTO `tr_transliteration` VALUES (4185, 'yevme lâ yenfe\\`u-żżâlimîne ma\\`ẕiratühüm velehümü-lla\\`netü velehüm sûü-ddâr.');
INSERT INTO `tr_transliteration` VALUES (4186, 'veleḳad âteynâ mûse-lhüdâ veevraŝnâ benî isrâîle-lkitâb.');
INSERT INTO `tr_transliteration` VALUES (4187, 'hüdev veẕikrâ liüli-l\'elbâb.');
INSERT INTO `tr_transliteration` VALUES (4188, 'faṣbir inne va\\`de-llâhi ḥaḳḳuv vestagfir liẕembike vesebbiḥ biḥamdi rabbike bil\\`aşiyyi vel\'ibkâr.');
INSERT INTO `tr_transliteration` VALUES (4189, 'inne-lleẕîne yücâdilûne fî âyâti-llâhi bigayri sülṭânin etâhüm in fî ṣudûrihim illâ kibrum mâ hüm bibâligîh. feste\\`iẕ billâh. innehû hüve-ssemî\\`u-lbeṣîr.');
INSERT INTO `tr_transliteration` VALUES (4190, 'leḫalḳu-ssemâvâti vel\'arḍi ekberu min ḫalḳi-nnâsi velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4191, 'vemâ yestevi-l\'a\\`mâ velbeṣîru velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti vele-lmüsî\'. ḳalîlem mâ teteẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (4192, 'inne-ssâ\\`ate leâtiyetül lâ raybe fîhâ velâkinne ekŝera-nnâsi lâ yü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (4193, 'veḳâle rabbükümü-d\\`ûnî estecib leküm. inne-lleẕîne yestekbirûne \\`an \\`ibâdetî seyedḫulûne cehenneme dâḫirîn.');
INSERT INTO `tr_transliteration` VALUES (4194, 'allâhü-lleẕî ce\\`ale lekümü-lleyle liteskünû fîhi vennehâra mübṣirâ. inne-llâhe leẕû faḍlin \\`ale-nnâsi velâkinne ekŝera-nnâsi lâ yeşkürûn.');
INSERT INTO `tr_transliteration` VALUES (4195, 'ẕâlikümü-llâhü rabbüküm ḫâliḳu külli şey\'. lâ ilâhe illâ hû. feennâ tü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (4196, 'keẕâlike yü\'fekü-lleẕîne kânû biâyâti-llâhi yecḥadûn.');
INSERT INTO `tr_transliteration` VALUES (4197, 'allâhü-lleẕî ce\\`ale lekümü-l\'arḍa ḳarârav vessemâe binâev veṣavveraküm feaḥsene ṣuveraküm verazeḳaküm mine-ṭṭayyibât. ẕâlikümü-llâhü rabbüküm. fetebârake-llâhü rabbü-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4198, 'hüve-lḥayyü lâ ilâhe illâ hüve fed\\`ûhü muḫliṣîne lehü-ddîn. elḥamdü lillâhi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4199, 'ḳul innî nühîtü en a\\`büde-lleẕîne ted\\`ûne min dûni-llâhi lemmâ câeniye-lbeyyinâtü mir rabbî veümirtü en üslime lirabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4200, 'hüve-lleẕî ḫaleḳaküm min türâbin ŝümme min nuṭfetin ŝümme min \\`aleḳatin ŝümme yuḫricüküm ṭiflen ŝümme liteblügû eşüddeküm ŝümme litekûnû şüyûḫâ. veminküm mey yüteveffâ min ḳablü veliteblügû ecelem müsemmev vele\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (4201, 'hüve-lleẕî yuḥyî veyümît. feiẕâ ḳaḍâ emran feinnemâ yeḳûlü lehû kün feyekûn.');
INSERT INTO `tr_transliteration` VALUES (4202, 'elem tera ile-lleẕîne yücâdilûne fî âyâti-llâh. ennâ yuṣrafûn.');
INSERT INTO `tr_transliteration` VALUES (4203, 'elleẕîne keẕẕebû bilkitâbi vebimâ erselnâ bihî rusülenâ. fesevfe ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4204, 'iẕi-l\'aglâlü fî a\\`nâḳihim vesselâsil. yüsḥabûn.');
INSERT INTO `tr_transliteration` VALUES (4205, 'fi-lḥamîmi ŝümme fi-nnâri yüscerûn.');
INSERT INTO `tr_transliteration` VALUES (4206, 'ŝümme ḳîle lehüm eyne mâ küntüm tüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (4207, 'min dûni-llâh. ḳâlû ḍallû \\`annâ bel lem nekün ned\\`û min ḳablü şey\'â. keẕâlike yüḍillü-llâhü-lkâfirîn.');
INSERT INTO `tr_transliteration` VALUES (4208, 'ẕâliküm bimâ küntüm tefraḥûne fi-l\'arḍi bigayri-lḥaḳḳi vebimâ küntüm temraḥûn.');
INSERT INTO `tr_transliteration` VALUES (4209, 'üdḫulû ebvâbe cehenneme ḫâlidîne fîhâ. febi\'se meŝve-lmütekebbirîn.');
INSERT INTO `tr_transliteration` VALUES (4210, 'faṣbir inne va\\`de-llâhi ḥaḳḳun. feimmâ nüriyenneke ba\\`ḍa-lleẕî ne\\`idühüm ev neteveffeyenneke feileynâ yürce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4211, 'veleḳad erselnâ rusülem min ḳablike minhüm men ḳaṣaṣnâ \\`aleyke veminhüm mel lem naḳṣuṣ \\`aleyk. vemâ kâne lirasûlin ey ye\'tiye biâyetin illâ biiẕni-llâh. feiẕâ câe emru-llâhi ḳuḍiye bilḥaḳḳi veḫasira hünâlike-lmübṭilûn.');
INSERT INTO `tr_transliteration` VALUES (4212, 'allâhü-lleẕî ce\\`ale lekümü-l\'en\\`âme literkebû minhâ veminhâ te\'külûn.');
INSERT INTO `tr_transliteration` VALUES (4213, 'veleküm fîhâ menâfi\\`u veliteblügû \\`aleyhâ ḥâceten fî ṣudûriküm ve\\`aleyhâ ve\\`ale-lfülki tuḥmelûn.');
INSERT INTO `tr_transliteration` VALUES (4214, 'veyürîküm âyâtih. feeyye âyâti-llâhi tünkirûn.');
INSERT INTO `tr_transliteration` VALUES (4215, 'efelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne \\`âḳibetü-lleẕîne min ḳablihim. kânû ekŝera minhüm veeşedde ḳuvvetev veâŝâran fi-l\'arḍi femâ agnâ \\`anhüm mâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (4216, 'felemmâ câethüm rusülühüm bilbeyyinâti feriḥû bimâ \\`indehüm mine-l\\`ilmi veḥâḳa bihim mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (4217, 'felemmâ raev be\'senâ ḳâlû âmennâ billâhi vaḥdehû vekefernâ bimâ künnâ bihî müşrikîn.');
INSERT INTO `tr_transliteration` VALUES (4218, 'felem yekü yenfe\\`uhüm îmânühüm lemmâ raev be\'senâ. sünnete-llâhi-lletî ḳad ḫalet fî \\`ibâdih. veḫasira hünâlike-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (4219, 'ḥâ-mîm.');
INSERT INTO `tr_transliteration` VALUES (4220, 'tenzîlüm mine-rraḥmâni-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (4221, 'kitâbün füṣṣilet âyâtühû ḳur\'ânen \\`arabiyyel liḳavmiy ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4222, 'beşîrav veneẕîrâ. fea\\`raḍa ekŝeruhüm fehüm lâ yesme\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4223, 'veḳâlû ḳulûbünâ fî ekinnetim mimmâ ted\\`ûnâ ileyhi vefî âẕâninâ vaḳruv vemim beyninâ vebeynike ḥicâbün fa\\`mel innenâ \\`âmilûn.');
INSERT INTO `tr_transliteration` VALUES (4224, 'ḳul innemâ ene beşerum miŝlüküm yûḥâ ileyye ennemâ ilâhüküm ilâhüv vâḥidün festeḳîmû ileyhi vestagfirûh. veveylül lilmüşrikîn.');
INSERT INTO `tr_transliteration` VALUES (4225, 'elleẕîne lâ yü\'tûne-zzekâte vehüm bil\'âḫirati hüm kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (4226, 'inne-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti lehüm ecrun gayru memnûn.');
INSERT INTO `tr_transliteration` VALUES (4227, 'ḳul einneküm letekfürûne billeẕî ḫaleḳa-l\'arḍa fî yevmeyni vetec\\`alûne lehû endâdâ. ẕâlike rabbü-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4228, 'vece\\`ale fîhâ ravâsiye min fevḳihâ vebârake fîhâ veḳaddera fîhâ aḳvâtehâ fî erbe\\`ati eyyâm. sevâel lissâilîn.');
INSERT INTO `tr_transliteration` VALUES (4229, 'ŝümme-stevâ ile-ssemâi vehiye düḫânün feḳâle lehâ velil\'arḍi-\'tiyâ ṭav\\`an ev kerhâ. ḳâletâ eteynâ ṭâi\\`în.');
INSERT INTO `tr_transliteration` VALUES (4230, 'feḳaḍâhünne seb\\`a semâvâtin fî yevmeyni veevḥâ fî külli semâin emrahâ. vezeyyenne-ssemâe-ddünyâ bimeṣâbîḥ. veḥifżâ. ẕâlike taḳdîru-l\\`azîzi-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4231, 'fein a\\`raḍû feḳul enẕertüküm ṣâ\\`iḳatem miŝle ṣâ\\`iḳati \\`âdiv veŝemûd.');
INSERT INTO `tr_transliteration` VALUES (4232, 'iẕ câethümü-rrusülü mim beyni eydîhim vemin ḫalfihim ellâ ta\\`büdû ille-llâh. ḳâlû lev şâe rabbünâ leenzele melâiketen feinnâ bimâ ürsiltüm bihî kâfirûn.');
INSERT INTO `tr_transliteration` VALUES (4233, 'feemmâ \\`âdün festekberû fi-l\'arḍi bigayri-lḥaḳḳi veḳâlû men eşeddü minnâ ḳuvveh. evelem yerav enne-llâhe-lleẕî ḫaleḳahüm hüve eşeddü minhüm ḳuvveh. vekânû biâyâtinâ yecḥadûn.');
INSERT INTO `tr_transliteration` VALUES (4234, 'feerselnâ \\`aleyhim rîḥan ṣarṣaran fî eyyâmin neḥisâtil linüẕîḳahüm \\`aẕâbe-lḫizyi fi-lḥayâti-ddünyâ. vele\\`aẕâbü-l\'âḫirati aḫzâ vehüm lâ yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (4235, 'veemmâ ŝemûdü fehedeynâhüm festeḥabbü-l\\`amâ \\`ale-lhüdâ feeḫaẕethüm ṣâ\\`iḳatü-l\\`aẕâbi-lhûni bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (4236, 'venecceyne-lleẕîne âmenû vekânû yetteḳûn.');
INSERT INTO `tr_transliteration` VALUES (4237, 'veyevme yuḥşeru a\\`dâü-llâhi ile-nnâri fehüm yûza\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4238, 'ḥattâ iẕâ mâ câûhâ şehide \\`aleyhim sem\\`uhüm veebṣâruhüm vecülûdühüm bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4239, 'veḳâlû licülûdihim lime şehittüm \\`aleynâ. ḳâlû enṭaḳane-llâhü-lleẕî enṭaḳa külle şey\'iv vehüve ḫaleḳaküm evvele merrativ veileyhi türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4240, 'vemâ küntüm testetirûne ey yeşhede \\`aleyküm sem\\`uküm velâ ebṣâruküm velâ cülûdüküm velâkin żanentüm enne-llâhe lâ ya\\`lemü keŝîram mimmâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4241, 'veẕâliküm żannükümü-lleẕî żanentüm birabbiküm erdâküm feaṣbaḥtüm mine-lḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (4242, 'feiy yaṣbirû felnâru meŝvel lehüm. veiy yesta\\`tibû femâ hüm mine-lmü\\`tebîn.');
INSERT INTO `tr_transliteration` VALUES (4243, 'veḳayyaḍnâ lehüm ḳuranâe fezeyyenû lehüm mâ beyne eydîhim vemâ ḫalfehüm veḥaḳḳa \\`aleyhimü-lḳavlü fî ümemin ḳad ḫalet min ḳablihim mine-lcinni vel\'ins. innehüm kânû ḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (4244, 'veḳâle-lleẕîne keferû lâ tesme\\`û lihâẕe-lḳur\'âni velgav fîhi le\\`alleküm taglibûn.');
INSERT INTO `tr_transliteration` VALUES (4245, 'felenüẕîḳanne-lleẕîne keferû \\`aẕâben şedîdev velenecziyennehüm esvee-lleẕî kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4246, 'ẕâlike cezâü a\\`dâi-llâhi-nnâru. lehüm fîhâ dâru-lḫuld. cezâem bimâ kânû biâyâtinâ yecḥadûn.');
INSERT INTO `tr_transliteration` VALUES (4247, 'veḳâle-lleẕîne keferû rabbenâ erine-lleẕeyni eḍallânâ mine-lcinni vel\'insi nec\\`alhümâ taḥte aḳdâminâ liyekûnâ mine-l\'esfelîn.');
INSERT INTO `tr_transliteration` VALUES (4248, 'inne-lleẕîne ḳâlû rabbüne-llâhü ŝümme-steḳâmû tetenezzelü \\`aleyhimü-lmelâiketü ellâ teḫâfû velâ taḥzenû veebşirû bilcenneti-lletî küntüm tû\\`adûn.');
INSERT INTO `tr_transliteration` VALUES (4249, 'naḥnü evliyâüküm fi-lḥayâti-ddünyâ vefi-l\'âḫirah. veleküm fîhâ mâ teştehî enfüsüküm veleküm fîhâ mâ tedde\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4250, 'nüzülem min gafûrir raḥîm.');
INSERT INTO `tr_transliteration` VALUES (4251, 'vemen aḥsenü ḳavlem mimmen de\\`â ile-llâhi ve\\`amile ṣâliḥav veḳâle innenî mine-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (4252, 'velâ testevi-lḥasenetü vele-sseyyieh. idfa\\` billetî hiye aḥsenü feiẕe-lleẕî beyneke vebeynehû \\`adâvetün keennehû veliyyün ḥamîm.');
INSERT INTO `tr_transliteration` VALUES (4253, 'vemâ yüleḳḳâhâ ille-lleẕîne ṣaberû. vemâ yüleḳḳâhâ illâ ẕû ḥażżin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (4254, 'veimmâ yenzeganneke mine-şşeyṭâni nezgun feste\\`iẕ billâh. innehû hüve-ssemî\\`u-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4255, 'vemin âyâtihi-lleylü vennehâru veşşemsü velḳamer. lâ tescüdû lişşemsi velâ lilḳameri vescüdû lillâhi-lleẕî ḫaleḳahünne in küntüm iyyâhü ta\\`büdûn.');
INSERT INTO `tr_transliteration` VALUES (4256, 'feini-stekberû felleẕîne \\`inde rabbike yüsebbiḥûne lehû billeyli vennehâri vehüm lâ yes\'emûn.');
INSERT INTO `tr_transliteration` VALUES (4257, 'vemin âyâtihî enneke tera-l\'arḍa ḫâşi\\`aten feiẕâ enzelnâ \\`aleyhe-lmâe-htezzet verabet. inne-lleẕî aḥyâhâ lemuḥyi-lmevtâ. innehû \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (4258, 'inne-lleẕîne yülḥidûne fî âyâtinâ lâ yaḫfevne \\`aleynâ. efemey yülḳâ fi-nnâri ḫayrun em mey ye\'tî âminey yevme-lḳiyâmeh. i\\`melû mâ şi\'tüm innehû bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (4259, 'inne-lleẕîne keferû biẕẕikri lemmâ câehüm. veinnehû lekitâbün \\`azîz.');
INSERT INTO `tr_transliteration` VALUES (4260, 'lâ ye\'tîhi-lbâṭilü mim beyni yedeyhi velâ min ḫalfih. tenzîlüm min ḥakîmin ḥamîd.');
INSERT INTO `tr_transliteration` VALUES (4261, 'mâ yüḳâlü leke illâ mâ ḳad ḳîle lirrusüli min ḳablik. inne rabbeke leẕû magfirativ veẕû \\`iḳâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (4262, 'velev ce\\`alnâhü ḳur\'ânen a\\`cemiyyel leḳâlû levlâ füṣṣilet âyâtüh. ea\\`cemiyyüv ve\\`arabiyy. ḳul hüve lilleẕîne âmenû hüdev veşifâün. velleẕîne lâ yü\'minûne fî âẕânihim vaḳruv vehüve \\`aleyhim \\`amâ. ülâike yünâdevne mim mekânim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4263, 'veleḳad âteynâ mûse-lkitâbe faḫtülife fîh. velevlâ kelimetün sebeḳat mir rabbike leḳuḍiye beynehüm. veinnehüm lefî şekkim minhü mürîbün.');
INSERT INTO `tr_transliteration` VALUES (4264, 'men \\`amile ṣâliḥan felinefsihî vemen esâe fe\\`aleyhâ. vemâ rabbüke biżallâmil lil\\`abîd.');
INSERT INTO `tr_transliteration` VALUES (4265, 'ileyhi yüraddü \\`ilmü-ssâ\\`ah. vemâ taḫrucü min ŝemerâtim min ekmâmihâ vemâ taḥmilü min ünŝâ velâ teḍa\\`u illâ bi\\`ilmih. veyevme yünâdîhim eyne şürakâî ḳâlû âẕennâke mâ minnâ min şehîd.');
INSERT INTO `tr_transliteration` VALUES (4266, 'veḍalle \\`anhüm mâ kânû yed\\`ûne min ḳablü veżannû mâ lehüm mim meḥîṣ.');
INSERT INTO `tr_transliteration` VALUES (4267, 'lâ yes\'emü-l\'insânü min dü\\`âi-lḫayr. veim messehü-şşerru feyeûsün ḳanûṭ.');
INSERT INTO `tr_transliteration` VALUES (4268, 'velein eẕaḳnâhü raḥmetem minnâ mim ba\\`di ḍarrâe messethü leyeḳûlenne hâẕâ lî vemâ eżunnü-ssâ\\`ate ḳâimetev veleir ruci\\`tü ilâ rabbî inne lî \\`indehû lelḥusnâ. felenünebbienne-lleẕîne keferû bimâ \\`amilû. velenüẕîḳannehüm min \\`aẕâbin galîż.');
INSERT INTO `tr_transliteration` VALUES (4269, 'veiẕâ en\\`amnâ \\`ale-l\'insâni a\\`raḍa veneâ bicânibih. veiẕâ messehü-şşerru feẕû dü\\`âin \\`arîḍ.');
INSERT INTO `tr_transliteration` VALUES (4270, 'ḳul era\'eytüm in kâne min \\`indi-llâhi ŝümme kefertüm bihî men eḍallü mimmen hüve fî şiḳâḳim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4271, 'senürîhim âyâtinâ fi-l\'âfâḳi vefî enfüsihim ḥattâ yetebeyyene lehüm ennehü-lḥaḳḳ. evelem yekfi birabbike ennehû \\`alâ külli şey\'in şehîd.');
INSERT INTO `tr_transliteration` VALUES (4272, 'elâ innehüm fî miryetim mil liḳâi rabbihim. elâ innehû bikülli şey\'im müḥîṭ.');
INSERT INTO `tr_transliteration` VALUES (4273, 'ḥâ-mîm.');
INSERT INTO `tr_transliteration` VALUES (4274, '\\`ayn-sîn-ḳâf.');
INSERT INTO `tr_transliteration` VALUES (4275, 'keẕâlike yûḥî ileyke veile-lleẕîne min ḳablike-llâhü-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4276, 'lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. vehüve-l\\`aliyyü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (4277, 'tekâdü-ssemâvâtü yetefeṭṭarne min fevḳihinne velmelâiketü yüsebbiḥûne biḥamdi rabbihim veyestagfirûne limen fi-l\'arḍ. elâ inne-llâhe hüve-lgafûru-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (4278, 'velleẕîne-tteḫaẕû min dûnihî evliyâe-llâhü ḥafîżun \\`aleyhim. vemâ ente \\`aleyhim bivekîl.');
INSERT INTO `tr_transliteration` VALUES (4279, 'vekeẕâlike evḥaynâ ileyke ḳur\'ânen \\`arabiyyel litünẕira ümme-lḳurâ vemen ḥavlehâ vetünẕira yevme-lcem\\`i lâ raybe fîh. ferîḳun fi-lcenneti veferîḳun fi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (4280, 'velev şâe-llâhü lece\\`alehüm ümmetev vâḥidetev velâkiy yüdḫilü mey yeşâü fî raḥmetih. veżżâlimûne mâ lehüm miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (4281, 'emi-tteḫaẕû min dûnihî evliyâ\'. fellâhü hüve-lveliyyü vehüve yuḥyi-lmevtâ. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (4282, 'veme-ḫteleftüm fîhi min şey\'in feḥukmühû ile-llâh. ẕâlikümü-llâhü rabbî \\`aleyhi tevekkelt. veileyhi ünîb.');
INSERT INTO `tr_transliteration` VALUES (4283, 'fâṭiru-ssemâvâti vel\'arḍ. ce\\`ale leküm min enfüsiküm ezvâcev vemine-l\'en\\`âmi ezvâcâ. yeẕraüküm fîh. leyse kemiŝlihî şey\'. vehüve-ssemî\\`u-lbeṣîr.');
INSERT INTO `tr_transliteration` VALUES (4284, 'lehû meḳâlîdü-ssemâvâti vel\'arḍ. yebsüṭu-rrizḳa limey yeşâü veyaḳdir. innehû bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4285, 'şera\\`a leküm mine-ddîni mâ veṣṣâ bihî nûḥav velleẕî evḥaynâ ileyke vemâ veṣṣaynâ bihî ibrâhîme vemûsâ ve\\`îsâ en eḳîmü-ddîne velâ teteferraḳû fîh. kebüra \\`ale-lmüşrikîne mâ ted\\`ûhüm ileyh. allâhü yectebî ileyhi mey yeşâü veyehdî ileyhi mey yünîb.');
INSERT INTO `tr_transliteration` VALUES (4286, 'vemâ teferraḳû illâ mim ba\\`di mâ câehümü-l\\`ilmü bagyem beynehüm. velevlâ kelimetün sebeḳat mir rabbike ilâ ecelim müsemmel leḳuḍiye beynehüm. veinne-lleẕîne ûriŝü-lkitâbe mim ba\\`dihim lefî şekkim minhü mürîbün.');
INSERT INTO `tr_transliteration` VALUES (4287, 'feliẕâlike fed\\`u. vesteḳim kemâ ümirt. velâ tettebi\\` ehvâehüm. veḳul âmentü bimâ enzele-llâhü min kitâb. veümirtü lia\\`dile beyneküm. allâhü rabbünâ verabbüküm. lenâ a\\`mâlünâ veleküm a\\`mâlüküm. lâ ḥuccete beynenâ vebeyneküm. allâhü yecme\\`u beynenâ. veileyhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (4288, 'velleẕîne yüḥâccûne fi-llâhi mim ba\\`di me-stücîbe lehû ḥuccetühüm dâḥiḍatün \\`inde rabbihim ve\\`aleyhim gaḍabüv velehüm \\`aẕâbün şedîd.');
INSERT INTO `tr_transliteration` VALUES (4289, 'allâhü-lleẕî enzele-lkitâbe bilḥaḳḳi velmîzân. vemâ yüdrîke le\\`alle-ssâ\\`ate ḳarîb.');
INSERT INTO `tr_transliteration` VALUES (4290, 'yesta\\`cilü bihe-lleẕîne lâ yü\'minûne bihâ. velleẕîne âmenû müşfiḳûne minhâ veya\\`lemûne ennehe-lḥaḳḳ. elâ inne-lleẕîne yümârûne fi-ssâ\\`ati lefî ḍalâlim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4291, 'allâhü leṭîfüm bi\\`ibâdihî yerzüḳu mey yeşâ\'. vehüve-lḳaviyyü-l\\`azîz.');
INSERT INTO `tr_transliteration` VALUES (4292, 'men kâne yürîdü ḥarŝe-l\'âḫirati nezid lehû fî ḥarŝih. vemen kâne yürîdü ḥarŝe-ddünyâ nü\'tihî minhâ vemâ lehû fi-l\'âḫirati min neṣîb.');
INSERT INTO `tr_transliteration` VALUES (4293, 'em lehüm şürakâü şera\\`û lehüm mine-ddîni mâ lem ye\'ẕem bihi-llâh. velevlâ kelimetü-lfaṣli leḳuḍiye beynehüm. veinne-żżâlimîne lehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (4294, 'tera-żżâlimîne müşfiḳîne mimmâ kesebû vehüve vâḳi\\`um bihim. velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti fî ravḍâti-lcennât. lehüm mâ yeşâûne \\`inde rabbihim. ẕâlike hüve-lfaḍlü-lkebîr.');
INSERT INTO `tr_transliteration` VALUES (4295, 'ẕâlike-lleẕî yübeşşiru-llâhü \\`ibâdehü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti. ḳul lâ es\'elüküm \\`aleyhi ecran ille-lmeveddete fi-lḳurbâ. vemey yaḳterif ḥaseneten nezid lehû fîhâ ḥusnâ. inne-llâhe gafûrun şekûr.');
INSERT INTO `tr_transliteration` VALUES (4296, 'em yeḳûlûne-fterâ \\`ale-llâhi keẕibâ. feiy yeşei-llâhü yaḫtim \\`alâ ḳalbik. veyemḥu-llâhü-lbâṭile veyüḥiḳḳu-lḥaḳḳa bikelimâtih. innehû \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (4297, 'vehüve-lleẕî yaḳbelü-ttevbete \\`an \\`ibâdihî veya\\`fû \\`ani-sseyyiâti veya\\`lemü mâ tef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (4298, 'veyestecîbü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti veyezîdühüm min faḍlih. velkâfirûne lehüm \\`aẕâbün şedîd.');
INSERT INTO `tr_transliteration` VALUES (4299, 'velev beseṭa-llâhü-rrizḳa li\\`ibâdihî lebegav fi-l\'arḍi velâkiy yünezzilü biḳaderim mâ yeşâ\'. innehû bi\\`ibâdihî ḫabîrum beṣîr.');
INSERT INTO `tr_transliteration` VALUES (4300, 'vehüve-lleẕî yünezzilü-lgayŝe mim ba\\`di mâ ḳaneṭû veyenşüru raḥmeteh. vehüve-lveliyyü-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (4301, 'vemin âyâtihî ḫalḳu-ssemâvâti vel\'arḍi vemâ beŝŝe fîhimâ min dâbbeh. vehüve \\`alâ cem\\`ihim iẕâ yeşâü ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (4302, 'vemâ eṣâbeküm mim müṣîbetin febimâ kesebet eydîküm veya\\`fû \\`an keŝîr.');
INSERT INTO `tr_transliteration` VALUES (4303, 'vemâ entüm bimü\\`cizîne fi-l\'arḍ. vemâ leküm min dûni-llâhi miv veliyyiv velâ neṣîr.');
INSERT INTO `tr_transliteration` VALUES (4304, 'vemin âyâtihi-lcevâri fi-lbaḥri kel\'a\\`lâm.');
INSERT INTO `tr_transliteration` VALUES (4305, 'iy yeşe\' yüskini-rrîḥa feyażlelne ravâkide \\`alâ żahrih. inne fî ẕâlike leâyâtil likülli ṣabbârin şekûr.');
INSERT INTO `tr_transliteration` VALUES (4306, 'ev yûbiḳhünne bimâ kesebû veya\\`fü \\`an keŝîr.');
INSERT INTO `tr_transliteration` VALUES (4307, 'veya\\`leme-lleẕîne yücâdilûne fî âyâtinâ. mâ lehüm mim meḥîṣ.');
INSERT INTO `tr_transliteration` VALUES (4308, 'femâ ûtîtüm min şey\'in femetâ\\`u-lḥayâti-ddünyâ. vemâ \\`inde-llâhi ḫayruv veebḳâ lilleẕîne âmenû ve\\`alâ rabbihim yetevekkelûn.');
INSERT INTO `tr_transliteration` VALUES (4309, 'velleẕîne yectenibûne kebâira-l\'iŝmi velfevâḥişe veiẕâ mâ gaḍibû hüm yagfirûn.');
INSERT INTO `tr_transliteration` VALUES (4310, 'velleẕîne-stecâbû lirabbihim veeḳâmu-ṣṣalâh. veemruhüm şûrâ beynehüm. vemimmâ razaḳnâhüm yünfiḳûn.');
INSERT INTO `tr_transliteration` VALUES (4311, 'velleẕîne iẕâ eṣâbehümü-lbagyü hüm yenteṣirûn.');
INSERT INTO `tr_transliteration` VALUES (4312, 'vecezâü seyyietin seyyietüm miŝlühâ. femen \\`afâ veaṣleḥa feecruhû \\`ale-llâh. innehû lâ yüḥibbu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (4313, 'velemeni-nteṣara ba\\`de żulmihî feülâike mâ \\`aleyhim min sebîl.');
INSERT INTO `tr_transliteration` VALUES (4314, 'inneme-ssebîlü \\`ale-lleẕîne yażlimûne-nnâse veyebgûne fi-l\'arḍi bigayri-lḥaḳḳ. ülâike lehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (4315, 'velemen ṣabera vegafera inne ẕâlike lemin \\`azmi-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (4316, 'vemey yuḍlili-llâhü femâ lehû miv veliyyim mim ba\\`dih. vetera-żżâlimîne lemmâ raevu-l\\`aẕâbe yeḳûlûne hel ilâ meraddim min sebîl.');
INSERT INTO `tr_transliteration` VALUES (4317, 'veterâhüm yü\\`raḍûne \\`aleyhâ ḫâşi\\`îne mine-ẕẕülli yenżurûne min ṭarfin ḫafiyy. veḳâle-lleẕîne âmenû inne-lḫâsirîne-lleẕîne ḫasirû enfüsehüm veehlîhim yevme-lḳiyâmeh. elâ inne-żżâlimîne fî \\`aẕâbim müḳîm.');
INSERT INTO `tr_transliteration` VALUES (4318, 'vemâ kâne lehüm min evliyâe yenṣurûnehüm min dûni-llâh. vemey yuḍlili-llâhü femâ lehû min sebîl.');
INSERT INTO `tr_transliteration` VALUES (4319, 'istecîbû lirabbiküm min ḳabli ey ye\'tiye yevmül lâ meradde lehû mine-llâh. mâ leküm mim melceiy yevmeiẕiv vemâ leküm min nekîr.');
INSERT INTO `tr_transliteration` VALUES (4320, 'fein a\\`raḍû femâ erselnâke \\`aleyhim ḥafîżâ. in \\`aleyke ille-lbelâg. veinnâ iẕâ eẕaḳne-l\'insâne minnâ raḥmeten feriḥa bihâ. vein tüṣibhüm seyyietüm bimâ ḳaddemet eydîhim feinne-l\'insâne kefûr.');
INSERT INTO `tr_transliteration` VALUES (4321, 'lillâhi mülkü-ssemâvâti vel\'arḍ. yaḫlüḳu mâ yeşâ\'. yehebü limey yeşâü inâŝev veyehebü limey yeşâü-ẕẕükûra.');
INSERT INTO `tr_transliteration` VALUES (4322, 'ev yüzevvicühüm ẕükrânev veinâŝâ. veyec\\`alü mey yeşâü \\`aḳîmâ. innehû \\`alîmün ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (4323, 'vemâ kâne libeşerin ey yükellimehü-llâhü illâ vaḥyen ev miv verâi ḥicâbin ev yürsile rasûlen feyûḥiye biiẕnihî mâ yeşâ\'. innehû \\`aliyyün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4324, 'vekeẕâlike evḥaynâ ileyke rûḥam min emrinâ. mâ künte tedrî me-lkitâbü vele-l\'îmânü velâkin ce\\`alnâhü nûran nehdî bihî men neşâü min \\`ibâdinâ. veinneke letehdî ilâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (4325, 'ṣirâṭi-llâhi-lleẕî lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. elâ ile-llâhi teṣîru-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (4326, 'ḥâ-mîm.');
INSERT INTO `tr_transliteration` VALUES (4327, 'velkitâbi-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (4328, 'innâ ce\\`alnâhü ḳur\'ânen \\`arabiyyel le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (4329, 'veinnehû fî ümmi-lkitâbi ledeynâ le\\`aliyyün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4330, 'efenaḍribü \\`ankümü-ẕẕikra ṣafḥan en küntüm ḳavmem müsrifîn.');
INSERT INTO `tr_transliteration` VALUES (4331, 'vekem erselnâ min nebiyyin fi-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (4332, 'vemâ ye\'tîhim min nebiyyin illâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (4333, 'feehleknâ eşedde minhüm baṭşev vemeḍâ meŝelü-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (4334, 'velein seeltehüm men ḫaleḳa-ssemâvâti vel\'arḍa leyeḳûlünne ḫaleḳahünne-l\\`azîzü-l\\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4335, 'elleẕî ce\\`ale lekümü-l\'arḍa mehdev vece\\`ale leküm fîhâ sübülel le\\`alleküm tehtedûn.');
INSERT INTO `tr_transliteration` VALUES (4336, 'velleẕî nezzele mine-ssemâi mâem biḳader. feenşernâ bihî beldetem meytâ. keẕâlike tuḫracûn.');
INSERT INTO `tr_transliteration` VALUES (4337, 'velleẕî ḫaleḳa-l\'ezvâce küllehâ vece\\`ale leküm mine-lfülki vel\'en\\`âmi mâ terkebûn.');
INSERT INTO `tr_transliteration` VALUES (4338, 'litestevû \\`alâ żuhûrihî ŝümme teẕkürû ni\\`mete rabbiküm iẕe-steveytüm \\`aleyhi veteḳûlû sübḥâne-lleẕî seḫḫara lenâ hâẕâ vemâ künnâ lehû muḳrinîn.');
INSERT INTO `tr_transliteration` VALUES (4339, 'veinnâ ilâ rabbinâ lemünḳalibûn.');
INSERT INTO `tr_transliteration` VALUES (4340, 'vece\\`alû lehû min \\`ibâdihî cüz\'â. inne-l\'insâne lekefûrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (4341, 'emi-tteḫaẕe mimmâ yaḫlüḳu benâtiv veaṣfâküm bilbenîn.');
INSERT INTO `tr_transliteration` VALUES (4342, 'veiẕâ büşşira eḥadühüm bimâ ḍarabe lirraḥmâni meŝelen żalle vechühû müsveddev vehüve keżîm.');
INSERT INTO `tr_transliteration` VALUES (4343, 'evemey yüneşşeü fi-lḥilyeti vehüve fi-lḫiṣâmi gayru mübîn.');
INSERT INTO `tr_transliteration` VALUES (4344, 'vece\\`alü-lmelâikete-lleẕîne hüm \\`ibâdü-rraḥmâni inâŝâ. eşehidû ḫalḳahüm. setüktebü şehâdetühüm veyüs\'elûn.');
INSERT INTO `tr_transliteration` VALUES (4345, 'veḳâlû lev şâe-rraḥmânü mâ \\`abednâhüm. mâ lehüm biẕâlike min \\`ilm. in hüm illâ yaḫruṣûn.');
INSERT INTO `tr_transliteration` VALUES (4346, 'em âteynâhüm kitâbem min ḳablihî fehüm bihî müstemsikûn.');
INSERT INTO `tr_transliteration` VALUES (4347, 'bel ḳâlû innâ vecednâ âbâenâ \\`alâ ümmetiv veinnâ \\`alâ âŝârihim mühtedûn.');
