INSERT INTO `tr_transliteration` VALUES (4486, 'veseḫḫara leküm mâ fi-ssemâvâti vemâ fi-l\'arḍi cemî\\`am minh. inne fî ẕâlike leâyâtil liḳavmiy yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (4487, 'ḳul lilleẕîne âmenû yagfirû lilleẕîne lâ yercûne eyyâme-llâhi liyecziye ḳavmem bimâ kânû yeksibûn.');
INSERT INTO `tr_transliteration` VALUES (4488, 'men \\`amile ṣâliḥan felinefsih. vemen esâe fe\\`aleyhâ. ŝümme ilâ rabbiküm türce\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4489, 'veleḳad âteynâ benî isrâîle-lkitâbe velḥukme vennübüvvete verazaḳnâhüm mine-ṭṭayyibâti vefeḍḍalnâhüm \\`ale-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4490, 'veâteynâhüm beyyinâtim mine-l\'emr. feme-ḫtelefû illâ mim ba\\`di mâ câehümü-l\\`ilmü bagyem beynehüm. inne rabbeke yaḳḍî beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.');
INSERT INTO `tr_transliteration` VALUES (4491, 'ŝümme ce\\`alnâke \\`alâ şerî\\`atim mine-l\'emri fettebi\\`hâ velâ tettebi\\` ehvâe-lleẕîne lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4492, 'innehüm ley yugnû \\`anke mine-llâhi şey\'â. veinne-żżâlimîne ba\\`ḍuhüm evliyâü ba\\`ḍ. vellâhü veliyyü-lmütteḳîn.');
INSERT INTO `tr_transliteration` VALUES (4493, 'hâẕâ beṣâiru linnâsi vehüdev veraḥmetül liḳavmiy yûḳinûn.');
INSERT INTO `tr_transliteration` VALUES (4494, 'em ḥasibe-lleẕîne-cteraḥü-sseyyiâti en nec\\`alehüm kelleẕîne âmenû ve\\`amilu-ṣṣâliḥâti sevâem maḥyâhüm vememâtühüm. sâe mâ yaḥkümûn.');
INSERT INTO `tr_transliteration` VALUES (4495, 'veḫaleḳa-llâhü-ssemâvâti vel\'arḍa bilḥaḳḳi velitüczâ küllü nefsim bimâ kesebet vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (4496, 'eferaeyte meni-tteḫaẕe ilâhehû hevâhü veeḍallehü-llâhü \\`alâ \\`ilmiv veḫateme \\`alâ sem\\`ihî veḳalbihî vece\\`ale \\`alâ beṣarihî gişâveh. femey yehdîhi mim ba\\`di-llâh. efelâ teẕekkerûn.');
INSERT INTO `tr_transliteration` VALUES (4497, 'veḳâlû mâ hiye illâ ḥayâtüne-ddünyâ nemûtü venaḥyâ vemâ yühlikünâ ille-ddehr. vemâ lehüm biẕâlike min \\`ilmin. in hüm illâ yeżunnûn.');
INSERT INTO `tr_transliteration` VALUES (4498, 'veiẕâ tütlâ \\`aleyhim âyâtünâ beyyinâtim mâ kâne ḥuccetehüm illâ en ḳâlü-\'tû biâbâinâ in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (4499, 'ḳuli-llâhü yuḥyîküm ŝümme yümîtüküm ŝümme yecme\\`uküm ilâ yevmi-lḳiyâmeti lâ raybe fîhi velâkinne ekŝera-nnâsi lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (4500, 'velillâhi mülkü-ssemâvâti vel\'arḍ. veyevme teḳûmü-ssâ\\`atü yevmeiẕiy yaḫseru-lmübṭilûn.');
INSERT INTO `tr_transliteration` VALUES (4501, 'veterâ külle ümmetin câŝiyeh. küllü ümmetin tüd\\`â ilâ kitâbihâ. elyevme tüczevne mâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4502, 'hâẕâ kitâbünâ yenṭiḳu \\`aleyküm bilḥaḳḳ. innâ künnâ nestensiḫu mâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4503, 'feemme-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti feyüdḫilühüm rabbühüm fî raḥmetih. ẕâlike hüve-lfevzü-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (4504, 'veemme-lleẕîne keferû. efelem tekün âyâtî tütlâ \\`aleyküm festekbertüm veküntüm ḳavmem mücrimîn.');
INSERT INTO `tr_transliteration` VALUES (4505, 'veiẕâ ḳîle inne va\\`de-llâhi ḥaḳḳuv vessâ\\`atü lâ raybe fîhâ ḳultüm mâ nedrî me-ssâ\\`atü in neżunnü illâ żannâ vemâ naḥnü bimüsteyḳinîn.');
INSERT INTO `tr_transliteration` VALUES (4506, 'vebedâ lehüm seyyietü mâ \\`amilû veḥâḳa bihim mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (4507, 'veḳîle-lyevme nensâküm kemâ nesîtüm liḳâe yevmiküm hâẕâ veme\'vâküm-nnâru vemâ leküm min nâṣirîn.');
INSERT INTO `tr_transliteration` VALUES (4508, 'ẕâliküm biennekümü-tteḫaẕtüm âyâti-llâhi hüzüvev vegarratkümü-lḥayâtü-ddünyâ. felyevme lâ yuḫracûne minhâ velâ hüm yüsta\\`tebûn.');
INSERT INTO `tr_transliteration` VALUES (4509, 'felillâhi-lḥamdü rabbi-ssemâvâti verabbi-l\'arḍi rabbi-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (4510, 'velehü-lkibriyâü fi-ssemâvâti vel\'arḍ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4511, 'ḥâ-mîm.');
INSERT INTO `tr_transliteration` VALUES (4512, 'tenzîlü-lkitâbi mine-llâhi-l\\`azîzi-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4513, 'mâ ḫalaḳne-ssemâvâti vel\'arḍa vemâ beynehümâ illâ bilḥaḳḳi veecelim müsemmâ. velleẕîne keferû \\`ammâ ünẕirû mü\\`riḍûn.');
INSERT INTO `tr_transliteration` VALUES (4514, 'ḳul era\'eytüm mâ ted\\`ûne min dûni-llâhi erûnî mâẕâ ḫaleḳû mine-l\'arḍi em lehüm şirkün fi-ssemâvât. îtûnî bikitâbim min ḳabli hâẕâ ev eŝâratim min \\`ilmin in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (4515, 'vemen eḍallü mimmey yed\\`û min dûni-llâhi mel lâ yestecîbü lehû ilâ yevmi-lḳiyâmeti vehüm \\`an dü\\`âihim gâfilûn.');
INSERT INTO `tr_transliteration` VALUES (4516, 'veiẕâ ḥuşira-nnâsü kânû lehüm a\\`dâev vekânû bi\\`ibâdetihim kâfirîn.');
INSERT INTO `tr_transliteration` VALUES (4517, 'veiẕâ tütlâ \\`aleyhim âyâtünâ beyyinâtin ḳâle-lleẕîne keferû lilḥaḳḳi lemmâ câehüm hâẕâ siḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (4518, 'em yeḳûlûne-fterâh. ḳul ini-fteraytühû felâ temlikûne lî mine-llâhi şey\'â. hüve a\\`lemü bimâ tüfîḍûne fîh. kefâ bihî şehîdem beynî vebeyneküm. vehüve-lgafûru-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (4519, 'ḳul mâ küntü bid\\`am mine-rrusüli vemâ edrî mâ yüf\\`alü bî velâ biküm. in ettebi\\`u illâ mâ yûḥâ ileyye vemâ ene illâ neẕîrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (4520, 'ḳul era\'eytüm in kâne min \\`indi-llâhi vekefertüm bihî veşehide şâhidüm mim benî isrâîle \\`alâ miŝlihî feâmene vestekbertüm. inne-llâhe lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (4521, 'veḳâle-lleẕîne keferû lilleẕîne âmenû lev kâne ḫayram mâ sebeḳûnâ ileyh. veiẕ lem yehtedû bihî feseyeḳûlûne hâẕâ ifkün ḳadîm.');
INSERT INTO `tr_transliteration` VALUES (4522, 'vemin ḳablihî kitâbü mûsâ imâmev veraḥmeh. vehâẕâ kitâbüm müṣaddiḳul lisânen \\`arabiyyel liyünẕira-lleẕîne żalemû. vebüşrâ lilmuḥsinîn.');
INSERT INTO `tr_transliteration` VALUES (4523, 'inne-lleẕîne ḳâlû rabbüne-llâhü ŝümme-steḳâmû felâ ḫavfün \\`aleyhim velâ hüm yaḥzenûn.');
INSERT INTO `tr_transliteration` VALUES (4524, 'ülâike aṣḥâbü-lcenneti ḫâlidîne fîhâ. cezâem bimâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4525, 'veveṣṣayne-l\'insâne bivâlideyhi iḥsânâ. ḥamelethü ümmühû kürhev veveḍa\\`athü kürhâ. veḥamlühû vefiṣâlühû ŝelâŝûne şehrâ. ḥattâ iẕâ belega eşüddehû vebelega erbe\\`îne seneten ḳâle rabbi evzi\\`nî en eşküra ni\\`meteke-lletî en\\`amte \\`aleyye ve\\`alâ vâlideyye veen a\\`mele ṣâliḥan tarḍâhü veaṣliḥ lî fî ẕürriyyetî. innî tübtü ileyke veinnî mine-lmüslimîn.');
INSERT INTO `tr_transliteration` VALUES (4526, 'ülâike-lleẕîne neteḳabbelü \\`anhüm aḥsene mâ \\`amilû venetecâvezü \\`an seyyiâtihim fî aṣḥâbi-lcenneh. va\\`de-ṣṣidḳi-lleẕî kânû yû\\`adûn.');
INSERT INTO `tr_transliteration` VALUES (4527, 'velleẕî ḳâle livâlideyhi üffil lekümâ eta\\`idâninî en uḫrace veḳad ḫaleti-lḳurûnü min ḳablî vehümâ yestegîŝâni-llâhe veyleke âmin. inne va\\`de-llâhi ḥaḳḳun. feyeḳûlü mâ hâẕâ illâ esâṭîru-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (4528, 'ülâike-lleẕîne ḥaḳḳa \\`aleyhimü-lḳavlü fî ümemin ḳad ḫalet min ḳablihim mine-lcinni vel\'ins. innehüm kânû ḫâsirîn.');
INSERT INTO `tr_transliteration` VALUES (4529, 'veliküllin deracâtüm mimmâ \\`amilû. veliyüveffiyehüm a\\`mâlehüm vehüm lâ yużlemûn.');
INSERT INTO `tr_transliteration` VALUES (4530, 'veyevme yü\\`raḍu-lleẕîne keferû \\`ale-nnâr. eẕhebtüm ṭayyibâtiküm fî ḥayâtikümü-ddünyâ vestemta\\`tüm bihâ. felyevme tüczevne \\`aẕâbe-lhûni bimâ küntüm testekbirûne fi-l\'arḍi bigayri-lḥaḳḳi vebimâ küntüm tefsüḳûn.');
INSERT INTO `tr_transliteration` VALUES (4531, 'veẕkür eḫâ \\`âdin. iẕ enẕera ḳavmehû bil\'aḥḳâfi veḳad ḫaleti-nnüẕüru mim beyni yedeyhi vemin ḫalfihî ellâ ta\\`büdû ille-llâh. innî eḫâfü \\`aleyküm \\`aẕâbe yevmin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (4532, 'ḳâlû eci\'tenâ lite\'fikenâ \\`an âlihetinâ. fe\'tinâ bimâ te\\`idünâ in künte mine-ṣṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (4533, 'ḳâle inneme-l\\`ilmü \\`inde-llâh. veübelliguküm mâ ürsiltü bihî velâkinnî erâküm ḳavmen techelûn.');
INSERT INTO `tr_transliteration` VALUES (4534, 'felemmâ raevhü \\`âriḍam müstaḳbile evdiyetihim ḳâlû hâẕâ \\`âriḍum mümṭirunâ. bel hüve me-sta\\`celtüm bih. rîḥun fîhâ \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (4535, 'tüdemmiru külle şey\'im biemri rabbihâ feaṣbeḥû lâ yürâ illâ mesâkinühüm. keẕâlike neczi-lḳavme-lmücrimîn.');
INSERT INTO `tr_transliteration` VALUES (4536, 'veleḳad mekkennâhüm fîmâ im mekkennâküm fîhi vece\\`alnâ lehüm sem\\`av veebṣârav veef\'ideh. femâ agnâ \\`anhüm sem\\`uhüm velâ ebṣâruhüm velâ ef\'idetühüm min şey\'in iẕ kânû yecḥadûne biâyâti-llâhi veḥâḳa bihim mâ kânû bihî yestehziûn.');
INSERT INTO `tr_transliteration` VALUES (4537, 'veleḳad ehleknâ mâ ḥavleküm mine-lḳurâ veṣarrafne-l\'âyâti le\\`allehüm yerci\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (4538, 'felevlâ neṣarahümü-lleẕîne-tteḫaẕû min dûni-llâhi ḳurbânen âliheh. bel ḍallû \\`anhüm. veẕâlike ifkühüm vemâ kânû yefterûn.');
INSERT INTO `tr_transliteration` VALUES (4539, 'veiẕ ṣarafnâ ileyke neferam mine-lcinni yestemi\\`ûne-lḳur\'ân. felemmâ ḥaḍarûhü ḳâlû enṣitû. felemmâ ḳuḍiye vellev ilâ ḳavmihim münẕirîn.');
INSERT INTO `tr_transliteration` VALUES (4540, 'ḳâlû yâ ḳavmenâ innâ semi\\`nâ kitâben ünzile mim ba\\`di mûsâ müṣaddiḳal limâ beyne yedeyhi yehdî ile-lḥaḳḳi veilâ ṭarîḳim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (4541, 'yâ ḳavmenâ ecîbû dâ\\`iye-llâhi veâminû bihî yagfir leküm min ẕünûbiküm veyücirküm min \\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (4542, 'vemel lâ yücib dâ\\`iye-llâhi feleyse bimü\\`cizin fi-l\'arḍi veleyse lehû min dûnihî evliyâ\'. ülâike fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (4543, 'evelem yerav enne-llâhe-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa velem ya\\`ye biḫalḳihinne biḳâdirin \\`alâ ey yuḥyiye-lmevtâ. belâ innehû \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (4544, 'veyevme yü\\`raḍu-lleẕîne keferû \\`ale-nnâr. eleyse hâẕâ bilḥaḳḳ. ḳâlû belâ verabbinâ. ḳâle feẕûḳu-l\\`aẕâbe bimâ küntüm tekfürûn.');
INSERT INTO `tr_transliteration` VALUES (4545, 'faṣbir kemâ ṣabera ülü-l\\`azmi mine-rrusüli velâ testa\\`cil lehüm. keennehüm yevme yeravne mâ yû\\`adûne lem yelbeŝû illâ sâ\\`atem min nehâr. belâg. fehel yühlekü ille-lḳavmü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (4546, 'elleẕîne keferû veṣaddû \\`an sebîli-llâhi eḍalle a\\`mâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4547, 'velleẕîne âmenû ve\\`amilu-ṣṣâliḥâti veâmenû bimâ nüzzile \\`alâ müḥammediv vehüve-lḥaḳḳu mir rabbihim keffera \\`anhüm seyyiâtihim veaṣleḥa bâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4548, 'ẕâlike bienne-lleẕîne keferü-ttebe\\`ü-lbâṭile veenne-lleẕîne âmenü-ttebe\\`ü-lḥaḳḳa mir rabbihim. keẕâlike yaḍribü-llâhü linnâsi emŝâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4549, 'feiẕâ leḳîtümü-lleẕîne keferû feḍarbe-rriḳâb. ḥattâ iẕâ eŝḫantümûhüm feşüddü-lveŝâḳa feimmâ mennem ba\\`dü veimmâ fidâen ḥattâ teḍa\\`a-lḥarbü evzârahâ. ẕâlik. velev yeşâü-llâhü lenteṣara minhüm velâkil liyeblüve ba\\`ḍaküm biba\\`ḍ. velleẕîne ḳutilû fî sebîli-llâhi feley yüḍille a\\`mâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4550, 'seyehdîhim veyuṣliḥu bâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4551, 'veyüdḫilühümü-lcennete \\`arrafehâ lehüm.');
INSERT INTO `tr_transliteration` VALUES (4552, 'yâ eyyühe-lleẕîne âmenû in tenṣurü-llâhe yenṣurküm veyüŝebbit aḳdâmeküm.');
INSERT INTO `tr_transliteration` VALUES (4553, 'velleẕîne keferû feta\\`sel lehüm veeḍalle a\\`mâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4554, 'ẕâlike biennehüm kerihû mâ enzele-llâhü feaḥbeṭa a\\`mâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4555, 'efelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne \\`âḳibetü-lleẕîne min ḳablihim. demmera-llâhü \\`aleyhim. velilkâfirîne emŝâlühâ.');
INSERT INTO `tr_transliteration` VALUES (4556, 'ẕâlike bienne-llâhe mevle-lleẕîne âmenû veenne-lkâfirîne lâ mevlâ lehüm.');
INSERT INTO `tr_transliteration` VALUES (4557, 'inne-llâhe yüdḫilü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâr. velleẕîne keferû yetemette\\`ûne veye\'külûne kemâ te\'külü-l\'en\\`âmü vennâru meŝvel lehüm.');
INSERT INTO `tr_transliteration` VALUES (4558, 'vekeeyyim min ḳaryetin hiye eşeddü ḳuvvetem min ḳaryetike-lletî aḫracetk. ehleknâhüm felâ nâṣira lehüm.');
INSERT INTO `tr_transliteration` VALUES (4559, 'efemen kâne \\`alâ beyyinetim mir rabbihî kemen züyyine lehû sûü \\`amelihî vettebe\\`û ehvâehüm.');
INSERT INTO `tr_transliteration` VALUES (4560, 'meŝelü-lcenneti-lletî vu\\`ide-lmütteḳûn. fîhâ enhârum mim mâin gayri âsin. veenhârum mil lebenil lem yetegayyer ṭa\\`müh. veenhârum min ḫamril leẕẕetil lişşâribîn. veenhârum min \\`aselim müṣaffâ. velehüm fîhâ min külli-ŝŝemerâti vemagfiratüm mir rabbihim. kemen hüve ḫâlidün fi-nnâri vesüḳû mâen ḥamîmen feḳaṭṭa\\`a em\\`âehüm.');
INSERT INTO `tr_transliteration` VALUES (4561, 'veminhüm mey yestemi\\`u ileyk. ḥattâ iẕâ ḫaracû min \\`indike ḳâlû lilleẕîne ûtü-l\\`ilme mâẕâ ḳâle ânifâ. ülâike-lleẕîne ṭabe\\`a-llâhü \\`alâ ḳulûbihim vettebe\\`û ehvâehüm.');
INSERT INTO `tr_transliteration` VALUES (4562, 'velleẕîne-htedev zâdehüm hüdev veâtâhüm taḳvâhüm.');
INSERT INTO `tr_transliteration` VALUES (4563, 'fehel yenżurûne ille-ssâ\\`ate en te\'tiyehüm bagteten. feḳad câe eşrâṭuhâ. feennâ lehüm iẕâ câethüm ẕikrâhüm.');
INSERT INTO `tr_transliteration` VALUES (4564, 'fa\\`lem ennehû lâ ilâhe ille-llâhü vestagfir liẕembike velilmü\'minîne velmü\'minât. vellâhü ya\\`lemü müteḳallebeküm vemeŝvâküm.');
INSERT INTO `tr_transliteration` VALUES (4565, 'veyeḳûlü-lleẕîne âmenû levlâ nüzzilet sûratün. feiẕâ ünzilet sûratüm muḥkemetüv veẕükira fîhe-lḳitâlü raeyte-lleẕîne fî ḳulûbihim meraḍuy yenżurûne ileyke neżara-lmagşiyyi \\`aleyhi mine-lmevt. feevlâ lehüm.');
INSERT INTO `tr_transliteration` VALUES (4566, 'ṭâ\\`atüv veḳavlüm ma\\`rûfün. feiẕâ \\`azeme-l\'emr. felev ṣadeḳu-llâhe lekâne ḫayral lehüm.');
INSERT INTO `tr_transliteration` VALUES (4567, 'fehel \\`aseytüm in tevelleytüm en tüfsidû fi-l\'arḍi vetüḳaṭṭi\\`û erḥâmeküm.');
INSERT INTO `tr_transliteration` VALUES (4568, 'ülâike-lleẕîne le\\`anehümü-llâhü feeṣammehüm vea\\`mâ ebṣârahüm.');
INSERT INTO `tr_transliteration` VALUES (4569, 'efelâ yetedebberûne-lḳur\'âne em \\`alâ ḳulûbin aḳfâlühâ.');
INSERT INTO `tr_transliteration` VALUES (4570, 'inne-lleẕîne-rteddû \\`alâ edbârihim mim ba\\`di mâ tebeyyene lehümü-lhüde-şşeyṭânü sevvele lehüm. veemlâ lehüm.');
INSERT INTO `tr_transliteration` VALUES (4571, 'ẕâlike biennehüm ḳâlû lilleẕîne kerihû mâ nezzele-llâhü senüṭî\\`uküm fî ba\\`ḍi-l\'emr. vellâhü ya\\`lemü isrârahüm.');
INSERT INTO `tr_transliteration` VALUES (4572, 'fekeyfe iẕâ teveffethümü-lmelâiketü yaḍribûne vucûhehüm veedbârahüm.');
INSERT INTO `tr_transliteration` VALUES (4573, 'ẕâlike biennehümü-ttebe\\`û mâ esḫaṭa-llâhe vekerihû riḍvânehû feaḥbeṭa a\\`mâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4574, 'em ḥasibe-lleẕîne fî ḳulûbihim meraḍun el ley yuḫrice-llâhü aḍgânehüm.');
INSERT INTO `tr_transliteration` VALUES (4575, 'velev neşâü leeraynâkehüm fele\\`araftehüm bisîmâhüm. veleta\\`rifennehüm fî laḥni-lḳavl. vellâhü ya\\`lemü a\\`mâleküm.');
INSERT INTO `tr_transliteration` VALUES (4576, 'veleneblüvenneküm ḥattâ na\\`leme-lmücâhidîne minküm veṣṣâbirîne veneblüve aḫbâraküm.');
INSERT INTO `tr_transliteration` VALUES (4577, 'inne-lleẕîne keferû veṣaddû \\`an sebîli-llâhi veşâḳḳu-rrasûle mim ba\\`di mâ tebeyyene lehümü-lhüdâ ley yeḍurrü-llâhe şey\'â. veseyuḥbiṭu a\\`mâlehüm.');
INSERT INTO `tr_transliteration` VALUES (4578, 'yâ eyyühe-lleẕîne âmenû eṭî\\`ü-llâhe veeṭî\\`ü-rrasûle velâ tübṭilû a\\`mâleküm.');
INSERT INTO `tr_transliteration` VALUES (4579, 'inne-lleẕîne keferû veṣaddû \\`an sebîli-llâhi ŝümme mâtû vehüm küffârun feley yagfira-llâhü lehüm.');
INSERT INTO `tr_transliteration` VALUES (4580, 'felâ tehinû veted\\`û ile-sselm. veentümü-l\'a\\`levn. vellâhü me\\`aküm veley yetiraküm a\\`mâleküm.');
INSERT INTO `tr_transliteration` VALUES (4581, 'inneme-lḥayâtü-ddünyâ le\\`ibüv velehv. vein tü\'minû vetetteḳû yü\'tiküm ücûraküm velâ yes\'elküm emvâleküm.');
INSERT INTO `tr_transliteration` VALUES (4582, 'iy yes\'elkümûhâ feyuḥfiküm tebḫalû veyuḫric aḍgâneküm.');
INSERT INTO `tr_transliteration` VALUES (4583, 'hâentüm hâülâi tüd\\`avne litünfiḳû fî sebîli-llâh. feminküm mey yebḫal. vemey yebḫal feinnemâ yebḫalü \\`an nefsih. vellâhü-lganiyyü veentümü-lfüḳarâ\'. vein tetevellev yestebdil ḳavmen gayraküm ŝümme lâ yekûnû emŝâleküm.');
INSERT INTO `tr_transliteration` VALUES (4584, 'innâ fetaḥnâ leke fetḥam mübînâ.');
INSERT INTO `tr_transliteration` VALUES (4585, 'liyagfira leke-llâhü mâ teḳaddeme min ẕembike vemâ teeḫḫara veyütimme ni\\`metehû \\`aleyke veyehdiyeke ṣirâṭam müsteḳîmâ.');
INSERT INTO `tr_transliteration` VALUES (4586, 'veyenṣurake-llâhü naṣran \\`azîzâ.');
INSERT INTO `tr_transliteration` VALUES (4587, 'hüve-lleẕî enzele-ssekînete fî ḳulûbi-lmü\'minîne liyezdâdû îmânem me\\`a îmânihim. velillâhi cünûdü-ssemâvâti vel\'arḍ. vekâne-llâhü \\`alîmen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (4588, 'liyüdḫile-lmü\'minîne velmü\'minâti cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ veyükeffira \\`anhüm seyyiâtihim. vekâne ẕâlike \\`inde-llâhi fevzen \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (4589, 'veyü\\`aẕẕibe-lmünâfiḳîne velmünâfiḳâti velmüşrikîne velmüşrikâti-żżânnîne billâhi żanne-ssev\'. \\`aleyhim dâiratü-ssev\'. vegaḍibe-llâhü \\`aleyhim vele\\`anehüm vee\\`adde lehüm cehennem. vesâet meṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (4590, 'velillâhi cünûdü-ssemâvâti vel\'arḍ. vekâne-llâhü \\`azîzen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (4591, 'innâ erselnâke şâhidev vemübeşşirav veneẕîrâ.');
INSERT INTO `tr_transliteration` VALUES (4592, 'litü\'minû billâhi verasûlihî vetü\\`azzirûhü vetüveḳḳirûh. vetüsebbiḥûhü bükratev veeṣîlâ.');
INSERT INTO `tr_transliteration` VALUES (4593, 'inne-lleẕîne yübâyi\\`ûneke innemâ yübâyi\\`ûne-llâh. yedü-llâhi fevḳa eydîhim. femen nekeŝe feinnemâ yenküŝü \\`alâ nefsih. vemen evfâ bimâ \\`âhede \\`aleyhü-llâhe feseyü\'tîhi ecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (4594, 'seyeḳûlü leke-lmüḫallefûne mine-l\'a\\`râbi şegaletnâ emvâlünâ veehlûnâ festagfir lenâ. yeḳûlûne bielsinetihim mâ leyse fî ḳulûbihim. ḳul femey yemlikü leküm mine-llâhi şey\'en in erâde biküm ḍarran ev erâde biküm nef\\`â. bel kâne-llâhü bimâ ta\\`melûne ḫabîrâ.');
INSERT INTO `tr_transliteration` VALUES (4595, 'bel żanentüm el ley yenḳalibe-rrasûlü velmü\'minûne ilâ ehlîhim ebedev vezüyyine ẕâlike fî ḳulûbiküm veżanentüm żanne-ssev\'. veküntüm ḳavmem bûrâ.');
INSERT INTO `tr_transliteration` VALUES (4596, 'vemel lem yü\'mim billâhi verasûlihî feinnâ a\\`tednâ lilkâfirîne se\\`îrâ.');
INSERT INTO `tr_transliteration` VALUES (4597, 'velillâhi mülkü-ssemâvâti vel\'arḍ. yagfiru limey yeşâü veyü\\`aẕẕibü mey yeşâ\'. vekâne-llâhü gafûrar raḥîmâ.');
INSERT INTO `tr_transliteration` VALUES (4598, 'seyeḳûlü-lmüḫallefûne iẕe-nṭalaḳtüm ilâ megânime lite\'ḫuẕûhâ ẕerûnâ nettebi\\`küm. yürîdûne ey yübeddilû kelâme-llâh. ḳul len tettebi\\`ûnâ keẕâliküm ḳâle-llâhü min ḳabl. feseyeḳûlûne bel taḥsüdûnenâ. bel kânû lâ yefḳahûne illâ ḳalîlâ.');
INSERT INTO `tr_transliteration` VALUES (4599, 'ḳul lilmüḫallefîne mine-l\'a\\`râbi setüd\\`avne ilâ ḳavmin ülî be\'sin şedîdin tüḳâtilûnehüm ev yüslimûn. fein tüṭî\\`û yü\'tikümü-llâhü ecran ḥasenâ. vein tetevellev kemâ tevelleytüm min ḳablü yü\\`aẕẕibküm \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (4600, 'leyse \\`ale-l\'a\\`mâ ḥaracüv velâ \\`ale-l\'a\\`raci ḥaracüv velâ \\`ale-lmerîḍi ḥarac. vemey yüṭi\\`i-llâhe verasûlehû yüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâr. vemey yetevelle yü\\`aẕẕibhü \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (4601, 'leḳad raḍiye-llâhü \\`ani-lmü\'minîne iẕ yübâyi\\`ûneke taḥte-şşecerati fe\\`alime mâ fî ḳulûbihim feenzele-ssekînete \\`aleyhim veeŝâbehüm fetḥan ḳarîbâ.');
INSERT INTO `tr_transliteration` VALUES (4602, 'vemegânime keŝîratey ye\'ḫuẕûnehâ. vekâne-llâhü \\`azîzen ḥakîmâ.');
INSERT INTO `tr_transliteration` VALUES (4603, 've\\`adekümü-llâhü megânime keŝîraten te\'ḫuẕûnehâ fe\\`accele leküm hâẕihî vekeffe eydiye-nnâsi \\`anküm. velitekûne âyetel lilmü\'minîne veyehdiyeküm ṣirâṭam müsteḳîmâ.');
INSERT INTO `tr_transliteration` VALUES (4604, 'veuḫrâ lem taḳdirû \\`aleyhâ ḳad eḥâṭa-llâhü bihâ. vekâne-llâhü \\`alâ külli şey\'in ḳadîrâ.');
INSERT INTO `tr_transliteration` VALUES (4605, 'velev ḳâtelekümü-lleẕîne keferû levellevu-l\'edbâra ŝümme lâ yecidûne veliyyev velâ neṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (4606, 'sünnete-llâhi-lletî ḳad ḫalet min ḳabl. velen tecide lisünneti-llâhi tebdîlâ.');
INSERT INTO `tr_transliteration` VALUES (4607, 'vehüve-lleẕî keffe eydiyehüm \\`anküm veeydiyeküm \\`anhüm bibaṭni mekkete mim ba\\`di en ażferaküm \\`aleyhim. vekâne-llâhü bimâ ta\\`melûne beṣîrâ.');
INSERT INTO `tr_transliteration` VALUES (4608, 'hümü-lleẕîne keferû veṣaddûküm \\`ani-lmescidi-lḥarâmi velhedye ma\\`kûfen ey yeblüga meḥilleh. velevlâ ricâlüm mü\'minûne venisâüm mü\'minâtül lem ta\\`lemûhüm en teṭaûhüm fetüṣîbeküm minhüm me\\`arratüm bigayri \\`ilmin. liyüdḫile-llâhü fî raḥmetihî mey yeşâ\'. lev tezeyyelû le\\`aẕẕebne-lleẕîne keferû minhüm \\`aẕâben elîmâ.');
INSERT INTO `tr_transliteration` VALUES (4609, 'iẕ ce\\`ale-lleẕîne keferû fî ḳulûbihimü-lḥamiyyete ḥamiyyete-lcâhiliyyeti feenzele-llâhü sekînetehû \\`alâ rasûlihî ve\\`ale-lmü\'minîne veelzemehüm kelimete-ttaḳvâ vekânû eḥaḳḳa bihâ veehlehâ. vekâne-llâhü bikülli şey\'in \\`alîmâ.');
INSERT INTO `tr_transliteration` VALUES (4610, 'leḳad ṣadeḳa-llâhü rasûlehü-rru\'yâ bilḥaḳḳ. letedḫulünne-lmescide-lḥarâme in şâe-llâhü âminîne müḥalliḳîne ruûseküm vemüḳaṣṣirîne lâ teḫâfûn. fe\\`alime mâ lem ta\\`lemû fece\\`ale min dûni ẕâlike fetḥan ḳarîbâ.');
INSERT INTO `tr_transliteration` VALUES (4611, 'hüve-lleẕî ersele rasûlehû bilhüdâ vedîni-lḥaḳḳi liyużhirahû \\`ale-ddîni küllih. vekefâ billâhi şehîdâ.');
INSERT INTO `tr_transliteration` VALUES (4612, 'müḥammedür rasûlü-llâh. velleẕîne me\\`ahû eşiddâü \\`ale-lküffâri ruḥamâü beynehüm terâhüm rukke\\`an süccedey yebtegûne faḍlem mine-llâhi veriḍvânâ. sîmâhüm fî vucûhihim min eŝeri-ssücûd. ẕâlike meŝelühüm fi-ttevrâh. vemeŝelühüm fi-l\'incîl. kezer\\`in aḫrace şaṭ\'ehû feâzerahû festagleża festevâ \\`alâ sûḳihî yü\\`cibü-zzürrâ\\`a liyegîża bihimü-lküffâr. ve\\`ade-llâhü-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti minhüm magfiratev veecran \\`ażîmâ.');
INSERT INTO `tr_transliteration` VALUES (4613, 'yâ eyyühe-lleẕîne âmenû lâ tüḳaddimû beyne yedeyi-llâhi verasûlihî vetteḳu-llâh. inne-llâhe semî\\`un \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4614, 'yâ eyyühe-lleẕîne âmenû lâ terfe\\`û aṣvâteküm fevḳa ṣavti-nnebiyyi velâ techerû lehû bilḳavli kecehri ba\\`ḍiküm liba\\`ḍin en taḥbeṭa a\\`mâlüküm veentüm lâ teş\\`urûn.');
INSERT INTO `tr_transliteration` VALUES (4615, 'inne-lleẕîne yeguḍḍûne aṣvâtehüm \\`inde rasûli-llâhi ülâike-lleẕîne-mteḥane-llâhü ḳulûbehüm littaḳvâ. lehüm magfiratüv veecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (4616, 'inne-lleẕîne yünâdûneke miv verâi-lḥucürâti ekŝeruhüm lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (4617, 'velev ennehüm ṣaberû ḥattâ taḫruce ileyhim lekâne ḫayral lehüm. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (4618, 'yâ eyyühe-lleẕîne âmenû in câeküm fâsiḳum binebein fetebeyyenû en tüṣîbû ḳavmem bicehâletin fetuṣbiḥû \\`alâ mâ fe\\`altüm nâdimîn.');
INSERT INTO `tr_transliteration` VALUES (4619, 'va\\`lemû enne fîküm rasûle-llâh. lev yüṭî\\`uküm fî keŝîrim mine-l\'emri le\\`anittüm velâkinne-llâhe ḥabbebe ileykümü-l\'îmâne vezeyyenehû fî ḳulûbiküm vekerrahe ileykümü-lküfra velfüsûḳa vel\\`iṣyân. ülâike hümü-rrâşidûn.');
INSERT INTO `tr_transliteration` VALUES (4620, 'faḍlem mine-llâhi veni\\`meh. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (4621, 'vein ṭâifetâni mine-lmü\'minîne-ḳtetelû feaṣliḥû beynehümâ. feim begat iḥdâhümâ \\`ale-l\'uḫrâ feḳâtilü-lletî tebgî ḥattâ tefîe ilâ emri-llâh. fein fâet feaṣliḥû beynehümâ bil\\`adli veaḳsiṭû. inne-llâhe yüḥibbü-lmuḳsiṭîn.');
INSERT INTO `tr_transliteration` VALUES (4622, 'inneme-lmü\'minûne iḫvetün feaṣliḥû beyne eḫaveyküm vetteḳu-llâhe le\\`alleküm türḥamûn.');
INSERT INTO `tr_transliteration` VALUES (4623, 'yâ eyyühe-lleẕîne âmenû lâ yesḫar ḳavmüm min ḳavmin \\`asâ ey yekûnû ḫayram minhüm velâ nisâüm min nisâin \\`asâ ey yekünne ḫayram minhünn. velâ telmizû enfüseküm velâ tenâbezû bil\'elḳâb. bi\'se-lismü-lfüsûḳu ba\\`de-l\'îmân. vemel lem yetüb feülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (4624, 'yâ eyyühe-lleẕîne âmenü-ctenibû keŝîram mine-żżann. inne ba\\`ḍa-żżanni iŝmüv velâ tecessesû velâ yagteb ba\\`ḍuküm ba\\`ḍâ. eyüḥibbü eḥadüküm ey ye\'küle laḥme eḫîhi meyten fekerihtümûh. vetteḳu-llâh. inne-llâhe tevvâbür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (4625, 'yâ eyyühe-nnâsü innâ ḫalaḳnâküm min ẕekeriv veünŝâ vece\\`alnâküm şu\\`ûbev veḳabâile lite\\`ârafû. inne ekrameküm \\`inde-llâhi etḳâküm. inne-llâhe \\`alîmün ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (4626, 'ḳâleti-l\'a\\`râbü âmennâ. ḳul lem tü\'minû velâkin ḳûlû eslemnâ velemmâ yedḫuli-l\'îmânü fî ḳulûbiküm. vein tüṭî\\`ü-llâhe verasûlehû lâ yelitküm min a\\`mâliküm şey\'â. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (4627, 'inneme-lmü\'minûne-lleẕîne âmenû billâhi verasûlihî ŝümme lem yertâbû vecâhedû biemvâlihim veenfüsihim fî sebîli-llâh. ülâike hümu-ṣṣâdiḳûn.');
INSERT INTO `tr_transliteration` VALUES (4628, 'ḳul etü\\`allimûne-llâhe bidîniküm vellâhü ya\\`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍ. vellâhü bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (4629, 'yemünnûne \\`aleyke en eslemû. ḳul lâ temünnû \\`aleyye islâmeküm. beli-llâhü yemünnü \\`aleyküm en hedâküm lil\'îmâni in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (4630, 'inne-llâhe ya\\`lemü gaybe-ssemâvâti vel\'arḍ. vellâhü beṣîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (4631, 'ḳâf. velḳur\'âni-lmecîd.');
INSERT INTO `tr_transliteration` VALUES (4632, 'bel \\`acibû en câehüm münẕirum minhüm feḳâle-lkâfirûne hâẕâ şey\'ün \\`acîb.');
INSERT INTO `tr_transliteration` VALUES (4633, 'eiẕâ mitnâ vekünnâ türâbâ. ẕâlike rac\\`um be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4634, 'ḳad \\`alimnâ mâ tenḳuṣu-l\'arḍu minhüm. ve\\`indenâ kitâbün ḥafîż.');
INSERT INTO `tr_transliteration` VALUES (4635, 'bel keẕẕebû bilḥaḳḳi lemmâ câehüm fehüm fî emrim merîc.');
INSERT INTO `tr_transliteration` VALUES (4636, 'efelem yenżurû ile-ssemâi fevḳahüm keyfe beneynâhâ vezeyyennâhâ vemâ lehâ min fürûc.');
INSERT INTO `tr_transliteration` VALUES (4637, 'vel\'arḍa medednâhâ veelḳaynâ fîhâ ravâsiye veembetnâ fîhâ min külli zevcim behîc.');
INSERT INTO `tr_transliteration` VALUES (4638, 'tebṣiratev veẕikrâ likülli \\`abdim münîb.');
INSERT INTO `tr_transliteration` VALUES (4639, 'venezzelnâ mine-ssemâi mâem mübâraken feembetnâ bihî cennâtiv veḥabbe-lḥaṣîd.');
INSERT INTO `tr_transliteration` VALUES (4640, 'vennaḫle bâsiḳâtil lehâ ṭal\\`un neḍîd.');
INSERT INTO `tr_transliteration` VALUES (4641, 'rizḳal lil\\`ibâdi veaḥyeynâ bihî beldetem meytâ. keẕâlike-lḫurûc.');
INSERT INTO `tr_transliteration` VALUES (4642, 'keẕẕebet ḳablehüm ḳavmü nûḥiv veaṣḥâbü-rrassi veŝemûd.');
INSERT INTO `tr_transliteration` VALUES (4643, 've\\`âdüv vefir\\`avnü veiḫvânü lûṭ.');
INSERT INTO `tr_transliteration` VALUES (4644, 'veaṣḥâbü-l\'eyketi veḳavmü tübba\\`. küllün keẕẕebe-rrusüle feḥaḳḳa ve\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4645, 'efe\\`ayînâ bilḫalḳi-l\'evvel. bel hüm fî lebsim min ḫalḳin cedîd.');
INSERT INTO `tr_transliteration` VALUES (4646, 'veleḳad ḫalaḳne-l\'insâne vena\\`lemü mâ tüvesvisü bihî nefsüh. venaḥnü aḳrabü ileyhi min ḥabli-lverîd.');
INSERT INTO `tr_transliteration` VALUES (4647, 'iẕ yeteleḳḳe-lmüteleḳḳiyâni \\`ani-lyemîni ve\\`ani-şşimâli ḳa\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4648, 'mâ yelfiżu min ḳavlin illâ ledeyhi raḳîbün \\`atîd.');
INSERT INTO `tr_transliteration` VALUES (4649, 'vecâet sekratü-lmevt bilḥaḳḳ. ẕâlike mâ künte minhü teḥîd.');
INSERT INTO `tr_transliteration` VALUES (4650, 'venüfiḫa fi-ṣṣûr. ẕâlike yevmü-lve\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4651, 'vecâet küllü nefsim me\\`ahâ sâiḳuv veşehîd.');
INSERT INTO `tr_transliteration` VALUES (4652, 'leḳad künte fî gafletim min hâẕâ fekeşefnâ \\`anke giṭâeke febeṣaruke-lyevme ḥadîd.');
INSERT INTO `tr_transliteration` VALUES (4653, 'veḳâle ḳarînühû hâẕâ mâ ledeyye \\`atîd.');
INSERT INTO `tr_transliteration` VALUES (4654, 'elḳiyâ fî cehenneme külle keffârin \\`anîd.');
INSERT INTO `tr_transliteration` VALUES (4655, 'mennâ\\`il lilḫayri mü\\`tedim mürîbün.');
INSERT INTO `tr_transliteration` VALUES (4656, 'elleẕî ce\\`ale me\\`a-llâhi ilâhen âḫara feelḳiyâhü fi-l\\`aẕâbi-şşedîd.');
INSERT INTO `tr_transliteration` VALUES (4657, 'ḳâle ḳarînühû rabbenâ mâ aṭgaytühû velâkin kâne fî ḍalâlim be\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4658, 'ḳâle lâ taḫteṣimû ledeyye veḳad ḳaddemtü ileyküm bilve\\`îd.');
INSERT INTO `tr_transliteration` VALUES (4659, 'mâ yübeddelü-lḳavlü ledeyye vemâ ene biżallâmil lil\\`abîd.');
INSERT INTO `tr_transliteration` VALUES (4660, 'yevme neḳûlü licehenneme heli-mtele\'ti veteḳûlü hel mim mezîd.');
