INSERT INTO `tr_transliteration` VALUES (5076, 'sebbeḥa lillâhi mâ fi-ssemâvâti vel\'arḍ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5077, 'lehû mülkü-ssemâvâti vel\'arḍ. yuḥyî veyümît. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (5078, 'hüve-l\'evvelü vel\'âḫiru veżżâhiru velbâṭin. vehüve bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (5079, 'hüve-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmin ŝümme-stevâ \\`ale-l\\`arş. ya\\`lemü mâ yelicü fi-l\'arḍi vemâ yaḫrucü minhâ vemâ yenzilü mine-ssemâi vemâ ya\\`rucü fîhâ. vehüve me\\`aküm eyne mâ küntüm. vellâhü bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (5080, 'lehû mülkü-ssemâvâti vel\'arḍ. veile-llâhi türce\\`u-l\'ümûr.');
INSERT INTO `tr_transliteration` VALUES (5081, 'yûlicü-lleyle fi-nnehâri veyûlicü-nnehâra fi-lleyl. vehüve \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (5082, 'âminû billâhi verasûlihî veenfiḳû mimmâ ce\\`aleküm müstaḫlefîne fîh. felleẕîne âmenû minküm veenfeḳû lehüm ecrun kebîr.');
INSERT INTO `tr_transliteration` VALUES (5083, 'vemâ leküm lâ tü\'minûne billâh. verrasûlü yed\\`ûküm litü\'minû birabbiküm veḳad eḫaẕe mîŝâḳaküm in küntüm mü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (5084, 'hüve-lleẕî yünezzilü \\`alâ \\`abdihî âyâtim beyyinâtil liyuḫriceküm mine-żżulümâti ile-nnûr. veinne-llâhe biküm leraûfür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5085, 'vemâ leküm ellâ tünfiḳû fî sebîli-llâhi velillâhi mîrâŝü-ssemâvâti vel\'arḍ. lâ yestevî minküm men enfeḳa min ḳabli-lfetḥi veḳâtel. ülâike a\\`żamü deracetem mine-lleẕîne enfeḳû mim ba\\`dü veḳâtelû. veküllev ve\\`ade-llâhü-lḥusnâ. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (5086, 'men ẕe-lleẕî yuḳriḍu-llâhe ḳarḍan ḥasenen feyüḍâ\\`ifehû lehû velehû ecrun kerîm.');
INSERT INTO `tr_transliteration` VALUES (5087, 'yevme tera-lmü\'minîne velmü\'minâti yes\\`â nûruhüm beyne eydîhim vebieymânihim büşrâkümü-lyevme cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. ẕâlike hüve-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5088, 'yevme yeḳûlü-lmünâfiḳûne velmünâfiḳâtü lilleẕîne âmenü-nżurûnâ naḳtebis min nûriküm ḳîle-rci\\`û verâeküm feltemisû nûrâ. feḍuribe beynehüm bisûril lehû bâb. bâṭinühû fîhi-rraḥmetü veżâhiruhû min ḳibelihi-l\\`aẕâb.');
INSERT INTO `tr_transliteration` VALUES (5089, 'yünâdûnehüm elem neküm me\\`aküm. ḳâlû belâ velâkinneküm fetentüm enfüseküm veterabbaṣtüm vertebtüm vegarratkümü-l\'emâniyyü ḥattâ câe emru-llâhi vegarraküm billâhi-lgarûr.');
INSERT INTO `tr_transliteration` VALUES (5090, 'felyevme lâ yü\'ḫaẕü minküm fidyetüv velâ mine-lleẕîne keferû. me\'vâkümü-nnâr. hiye mevlâküm. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5091, 'elem ye\'ni lilleẕîne âmenû en taḫşe\\`a ḳulûbühüm liẕikri-llâhi vemâ nezele mine-lḥaḳḳi velâ yekûnû kelleẕîne ûtü-lkitâbe min ḳablü feṭâle \\`aleyhimü-l\'emedü feḳaset ḳulûbühüm. vekeŝîrum minhüm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (5092, 'i\\`lemû enne-llâhe yuḥyi-l\'arḍa ba\\`de mevtihâ. ḳad beyyennâ lekümü-l\'âyâti le\\`alleküm ta\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (5093, 'inne-lmüṣṣaddiḳîne velmüṣṣaddiḳâti veaḳraḍu-llâhe ḳarḍan ḥaseney yüḍâ\\`afü lehüm velehüm ecrun kerîm.');
INSERT INTO `tr_transliteration` VALUES (5094, 'velleẕîne âmenû billâhi verusülihî ülâike hümu-ṣṣiddîḳûn. veşşühedâü \\`inde rabbihim. lehüm ecruhüm venûruhüm. velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-lceḥîm.');
INSERT INTO `tr_transliteration` VALUES (5095, 'i\\`lemû enneme-lḥayâtü-ddünyâ le\\`ibüv velehvüv vezînetüv vetefâḫurum beyneküm vetekâŝürun fi-l\'emvâli vel\'evlâd. kemeŝeli gayŝin a\\`cebe-lküffâra nebâtühû ŝümme yehîcü feterâhü muṣferran ŝümme yekûnü ḥuṭâmâ. vefi-l\'âḫirati \\`aẕâbün şedîdüv vemagfiratüm mine-llâhi veriḍvân. veme-lḥayâtü-ddünyâ illâ metâ\\`u-lgurûr.');
INSERT INTO `tr_transliteration` VALUES (5096, 'sâbiḳû ilâ magfiratim mir rabbiküm vecennetin \\`arḍuhâ ke\\`arḍi-ssemâi vel\'arḍi ü\\`iddet lilleẕîne âmenû billâhi verusülih. ẕâlike faḍlü-llâhi yü\'tîhi mey yeşâ\'. vellâhü ẕü-lfaḍli-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5097, 'mâ eṣâbe mim müṣîbetin fi-l\'arḍi velâ fî enfüsiküm illâ fî kitâbim min ḳabli en nebraehâ. inne ẕâlike \\`ale-llâhi yesîr.');
INSERT INTO `tr_transliteration` VALUES (5098, 'likeylâ te\'sev \\`alâ mâ fâteküm velâ tefraḥû bimâ âtâküm. vellâhü lâ yüḥibbü külle muḫtâlin feḫûr.');
INSERT INTO `tr_transliteration` VALUES (5099, 'elleẕîne yebḫalûne veye\'mürûne-nnâse bilbuḫl. vemey yetevelle feinne-llâhe hüve-lganiyyü-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (5100, 'leḳad erselnâ rusülenâ bilbeyyinâti veenzelnâ me\\`ahümü-lkitâbe velmîzâne liyeḳûme-nnâsü bilḳisṭ. veenzelne-lḥadîde fîhi be\'sün şedîdüv vemenâfi\\`u linnâsi veliya\\`leme-llâhü mey yenṣuruhû verusülehû bilgayb. inne-llâhe ḳaviyyün \\`azîz.');
INSERT INTO `tr_transliteration` VALUES (5101, 'veleḳad erselnâ nûḥav veibrâhime vece\\`alnâ fî ẕürriyyetihime-nnübüvvete velkitâbe feminhüm mühted. vekeŝîrum minhüm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (5102, 'ŝümme ḳaffeynâ \\`alâ âŝârihim birusülinâ veḳaffeynâ bi\\`îse-bni meryeme veâteynâhü-l\'incîle vece\\`alnâ fî ḳulûbi-lleẕîne-ttebe\\`ûhü ra\'fetev veraḥmeh. verahbâniyyeten-btede\\`ûhâ mâ ketebnâhâ \\`aleyhim ille-btigâe riḍvâni-llâhi femâ ra\\`avhâ ḥaḳḳa ri\\`âyetihâ. feâteyne-lleẕîne âmenû minhüm ecrahüm. vekeŝîrum minhüm fâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (5103, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veâminû birasûlihî yü\'tiküm kifleyni mir raḥmetihî veyec\\`al leküm nûran temşûne bihî veyagfir leküm. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5104, 'liellâ ya\\`leme ehlü-lkitâbi ellâ yaḳdirûne \\`alâ şey\'im min faḍli-llâhi veenne-lfaḍle biyedi-llâhi yü\'tîhi mey yeşâ\'. vellâhü ẕü-lfaḍli-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5105, 'ḳad semi\\`a-llâhü ḳavle-lletî tücâdilüke fi zevcihâ veteştekî ile-llâh. vellâhü yesme\\`u teḥâvurakümâ. inne-llâhe semî\\`um beṣîr.');
INSERT INTO `tr_transliteration` VALUES (5106, 'elleẕîne yüżâhirûne minküm min nisâihim mâ hünne ümmehâtihim. in ümmehâtühüm ille-llâî velednâhüm. veinnehüm leyeḳûlûne münkeram mine-lḳavli vezûrâ. veinne-llâhe le\\`afüvvun gafûr.');
INSERT INTO `tr_transliteration` VALUES (5107, 'velleẕîne yüżâhirûne min nisâihim ŝümme ye\\`ûdûne limâ ḳâlû fetaḥrîru raḳabetim min ḳabli ey yetemâssâ. ẕâliküm tû\\`ażûne bih. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (5108, 'femel lem yecid feṣiyâmü şehrayni mütetâbi\\`ayni min ḳabli ey yetemâssâ. femel lem yesteṭi\\` feiṭ\\`âmü sittîne miskînâ. ẕâlike litü\'minû billâhi verasûlih. vetilke ḥudûdü-llâh. velilkâfirîne \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (5109, 'inne-lleẕîne yüḥâddûne-llâhe verasûlehû kübitû kemâ kübite-lleẕîne min ḳablihim veḳad enzelnâ âyâtim beyyinât. velilkâfirîne \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (5110, 'yevme yeb\\`aŝühümü-llâhü cemî\\`an feyünebbiühüm bimâ \\`amilû. aḥṣâhü-llâhü venesûh. vellâhü \\`alâ külli şey\'in şehîd.');
INSERT INTO `tr_transliteration` VALUES (5111, 'elem tera enne-llâhe ya\\`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍ. mâ yekûnü min necvâ ŝelâŝetin illâ hüve râbi\\`uhüm velâ ḫamsetin illâ hüve sâdisühüm velâ ednâ min ẕâlike velâ ekŝera illâ hüve me\\`ahüm eyne mâ kânû. ŝümme yünebbiühüm bimâ \\`amilû yevme-lḳiyâmeh. inne-llâhe bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (5112, 'elem tera ile-lleẕîne nühû \\`ani-nnecvâ ŝümme ye\\`ûdûne limâ nühû \\`anhü veyetenâcevne bil\'iŝmi vel\\`udvâv vema\\`ṣiyeti-rrasûl. veiẕâ câûke ḥayyevke bimâ lem yüḥayyike bihi-llâhü veyeḳûlûne fî enfüsihim levlâ yü\\`aẕẕibüne-llâhü bimâ neḳûl. ḥasbühüm cehennem. yaṣlevnehâ. febi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5113, 'yâ eyyühe-lleẕîne âmenû iẕâ tenâceytüm felâ tetenâcev bil\'iŝmi vel\\`udvâni vema\\`ṣiyeti-rrasûli vetenâcev bilbirri vettaḳvâ. vetteḳu-llâhe-lleẕî ileyhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (5114, 'inneme-nnecvâ mine-şşeyṭâni liyaḥzüne-lleẕîne âmenû veleyse biḍârrihim şey\'en illâ biiẕni-llâh. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (5115, 'yâ eyyühe-lleẕîne âmenû iẕâ ḳîle leküm tefesseḥû fi-lmecâlisi fefseḥû yefseḥi-llâhü leküm. veiẕâ ḳîle-nşüzû fenşüzû yerfe\\`i-llâhü-lleẕîne âmenû minküm velleẕîne ûtü-l\\`ilme deracât. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (5116, 'yâ eyyühe-lleẕîne âmenû iẕâ nâceytümü-rrasûle feḳaddimû beyne yedey necvâküm ṣadeḳaten. ẕâlike ḫayrul leküm veaṭher. feil lem tecidû feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5117, 'eeşfaḳtüm en tüḳaddimû beyne yedey necvâküm ṣadeḳât. feiẕ lem tef\\`alû vetâbe-llâhü \\`aleyküm feeḳîmu-ṣṣalâte veâtü-zzekâte veeṭî\\`ü-llâhe verasûleh. vellâhü ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5118, 'elem tera ile-lleẕîne tevellev ḳavmen gaḍibe-llâhü \\`aleyhim. mâ hüm minküm velâ minhüm veyaḥlifûne \\`ale-lkeẕibi vehüm ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (5119, 'e\\`adde-llâhü lehüm \\`aẕâben şedîdâ. innehüm sâe mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5120, 'itteḫaẕû eymânehüm cünneten feṣaddû \\`an sebîli-llâhi felehüm \\`aẕâbüm mühîn.');
INSERT INTO `tr_transliteration` VALUES (5121, 'len tugniye \\`anhüm emvâlühüm velâ evlâdühüm mine-llâhi şey\'â. ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.');
INSERT INTO `tr_transliteration` VALUES (5122, 'yevme yeb\\`aŝühümü-llâhü cemî\\`an feyaḥlifûne lehû kemâ yaḥlifûne leküm veyaḥsebûne ennehüm \\`alâ şey\'. elâ innehüm hümü-lkâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (5123, 'istaḥveẕe \\`aleyhimü-şşeyṭânü feensâhüm ẕikra-llâh. ülâike ḥizbü-şşeyṭân. elâ inne ḥizbe-şşeyṭâni hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (5124, 'inne-lleẕîne yüḥâddûne-llâhe verasûlehû ülâike fi-l\'eẕellîn.');
INSERT INTO `tr_transliteration` VALUES (5125, 'ketebe-llâhü leaglibenne ene verusülî. inne-llâhe ḳaviyyün \\`azîz.');
INSERT INTO `tr_transliteration` VALUES (5126, 'lâ tecidü ḳavmey yü\'minûne billâhi velyevmi-l\'âḫiri yüvâddûne men ḥâdde-llâhe verasûlehû velev kânû âbâehüm ev ebnâehüm ev iḫvânehüm ev \\`aşîratehüm. ülâike ketebe fî ḳulûbihimü-l\'îmâne veeyyedehüm birûḥim minh. veyüdḫilühüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. raḍiye-llâhü \\`anhüm veraḍû \\`anh. ülâike ḥizbü-llâh. elâ inne ḥizbe-llâhi hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (5127, 'sebbeḥa lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5128, 'hüve-lleẕî aḫrace-lleẕîne keferû min ehli-lkitâbi min diyârihim lievveli-lḥaşr. mâ żanentüm ey yaḫrucû veżannû ennehüm mâni\\`atühüm ḥuṣûnühüm mine-llâhi feetâhümü-llâhü min ḥayŝü lem yaḥtesibû veḳaẕefe fî ḳulûbihimü-rru\\`be yuḫribûne büyûtehüm bieydîhim veeydi-lmü\'minîne fa\\`tebirû yâ ûli-l\'ebṣâr.');
INSERT INTO `tr_transliteration` VALUES (5129, 'velevlâ en ketebe-llâhü \\`aleyhimü-lcelâe le\\`aẕẕebehüm fi-ddünyâ. velehüm fi-l\'âḫirati \\`aẕâbü-nnâr.');
INSERT INTO `tr_transliteration` VALUES (5130, 'ẕâlike biennehüm şâḳḳu-llâhe verasûleh. vemey yüşâḳḳi-llâhe feinne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (5131, 'mâ ḳata\\`tüm mil lînetin ev teraktümûhâ ḳâimeten \\`alâ üṣûlihâ febiiẕni-llâhi veliyuḫziye-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (5132, 'vemâ efâe-llâhü \\`alâ rasûlihî minhüm femâ evceftüm \\`aleyhi min ḫayliv velâ rikâbiv velâkinne-llâhe yüselliṭu rusülehû \\`alâ mey yeşâ\'. vellâhü \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (5133, 'mâ efâe-llâhü \\`alâ rasûlihî min ehli-lḳurâ felillâhi velirrasûli veliẕi-lḳurbâ velyetâmâ velmesâkîni vebni-ssebîli key lâ yekûne dûletem beyne-l\'agniyâi minküm. vemâ âtâkümü-rrasûlü feḫuẕûhü vemâ nehâküm \\`anhü fentehû. vetteḳu-llâh. inne-llâhe şedîdü-l\\`iḳâb.');
INSERT INTO `tr_transliteration` VALUES (5134, 'lilfüḳarâi-lmühâcirîne-lleẕîne uḫricû min diyârihim veemvâlihim yebtegûne faḍlem mine-llâhi veriḍvânev veyenṣurûne-llâhe verasûleh. ülâike hümu-ṣṣâdiḳûn.');
INSERT INTO `tr_transliteration` VALUES (5135, 'velleẕîne tebevveü-ddâra vel\'îmâne min ḳablihim yüḥibbûne men hâcera ileyhim velâ yecidûne fî ṣudûrihim ḥâcetem mimmâ ûtû veyü\'ŝirûne \\`alâ enfüsihim velev kâne bihim ḫaṣâṣah. vemey yûḳa şüḥḥa nefsihî feülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (5136, 'velleẕîne câû mim ba\\`dihim yeḳûlûne rabbene-gfir lenâ veliiḫvânine-lleẕîne sebeḳûnâ bil\'îmâni velâ tec\\`al fî ḳulûbinâ gillel lilleẕîne âmenû rabbenâ inneke raûfür raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5137, 'elem tera ile-lleẕîne nâfeḳû yeḳûlûne liiḫvânihimü-lleẕîne keferû min ehli-lkitâbi lein uḫrictüm lenaḫrucenne me\\`aküm velâ nüṭî\\`u fîküm eḥaden ebedev vein ḳûtiltüm lenenṣuranneküm. vellâhü yeşhedü innehüm lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (5138, 'lein uḫricû lâ yaḫrucûne me\\`ahüm. velein ḳûtilû lâ yenṣurûnehüm. velein neṣarûhüm leyüvellünne-l\'edbâr. ŝümme lâ yünṣarûn.');
INSERT INTO `tr_transliteration` VALUES (5139, 'leentüm eşeddü rahbeten fî ṣudûrihim mine-llâh. ẕâlike biennehüm ḳavmül lâ yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (5140, 'lâ yüḳâtilûneküm cemî\\`an illâ fî ḳuram müḥaṣṣanetin ev miv verâi cüdür. be\'sühüm beynehüm şedîd. taḥsebühüm cemî\\`av veḳulûbühüm şettâ. ẕâlike biennehüm ḳavmül lâ ya\\`ḳilûn.');
INSERT INTO `tr_transliteration` VALUES (5141, 'kemeŝeli-lleẕîne min ḳablihim ḳarîben ẕâḳû vebâle emrihim. velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (5142, 'kemeŝeli-şşeyṭâni iẕ ḳâle lil\'insâni-kfür. felemmâ kefera ḳâle innî berîüm minke innî eḫâfü-llâhe rabbe-l\\`âlemîn.');
INSERT INTO `tr_transliteration` VALUES (5143, 'fekâne \\`âḳibetehümâ ennehümâ fi-nnâri ḫâlideyni fîhâ. veẕâlike cezâu-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (5144, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veltenżur nefsüm mâ ḳaddemet ligad. vetteḳu-llâh. inne-llâhe ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5145, 'velâ tekûnû kelleẕîne nesü-llâhe feensâhüm enfüsehüm. ülâike hümü-lfâsiḳûn.');
INSERT INTO `tr_transliteration` VALUES (5146, 'lâ yestevî aṣḥâbü-nnâri veaṣḥâbü-lcenneh. aṣḥâbü-lcenneti hümü-lfâizûn.');
INSERT INTO `tr_transliteration` VALUES (5147, 'lev enzelnâ hâẕe-lḳur\'âne \\`alâ cebelil lera\'eytehû ḫâşi\\`am müteṣaddi\\`am min ḫaşyeti-llâh. vetilke-l\'emŝâlü naḍribühâ linnâsi le\\`allehüm yetefekkerûn.');
INSERT INTO `tr_transliteration` VALUES (5148, 'hüve-llâhü-lleẕî lâ ilâhe illâ hû. \\`âlimü-lgaybi veşşehâdeh. hüve-rraḥmânü-rraḥîm.');
INSERT INTO `tr_transliteration` VALUES (5149, 'hüve-llâhü-lleẕî lâ ilâhe illâ hû. elmelikü-lḳuddûsü-sselâmü-lmü\'minü-lmüheyminü-l\\`azîzü-lcebbâru-lmütekebbir. sübḥâne-llâhi \\`ammâ yüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (5150, 'hüve-llâhü-lḫâliḳu-lbâriü-lmüṣavviru lehü-l\'esmâü-lḥusnâ. yüsebbiḥu lehû mâ fi-ssemâvâti vel\'arḍ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5151, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕû \\`adüvvî ve\\`adüvveküm evliyâe tülḳûne ileyhim bilmeveddeti veḳad keferû bimâ câeküm mine-lḥaḳḳ. yuḫricûne-rrasûle veiyyâküm en tü\'minû billâhi rabbiküm. in küntüm ḫaractüm cihâden fî sebîlî vebtigâe merḍâtî tüsirrûne ileyhim bilmeveddeh. veenâ a\\`lemü bimâ aḫfeytüm vemâ a\\`lentüm. vemey yef\\`alhü minküm feḳad ḍalle sevâe-ssebîl.');
INSERT INTO `tr_transliteration` VALUES (5152, 'iy yeŝḳafûküm yekûnû leküm a\\`dâev veyebsüṭû ileyküm eydiyehüm veelsinetehüm bissûi veveddû lev tekfürûn.');
INSERT INTO `tr_transliteration` VALUES (5153, 'len tenfe\\`aküm erḥâmüküm velâ evlâdüküm. yevme-lḳiyâmeh. yefṣilü beyneküm. vellâhü bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (5154, 'ḳad kânet leküm üsvetün ḥasenetün fî ibrâhîme velleẕîne me\\`ah. iẕ ḳâlû liḳavmihim innâ büraâü minküm vemimmâ ta\\`büdûne min dûni-llâh. kefernâ biküm vebedâ beynenâ vebeynekümü-l\\`adâvetü vebagḍâü ebeden ḥattâ tü\'minû billâhi vaḥdehû illâ ḳavle ibrâhîme liebîhi leestagfiranne leke vemâ emlikü leke mine-llâhi min şey\'. rabbenâ \\`aleyke tevekkelnâ veileyke enebnâ veileyke-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5155, 'rabbenâ lâ tec\\`alnâ fitnetel lilleẕîne keferû vagfir lenâ rabbenâ. inneke ente-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5156, 'leḳad kâne leküm fîhim üsvetün ḥasenetül limen kâne yercü-llâhe velyevme-l\'âhir. vemey yetevelle feinne-llâhe hüve-lganiyyü-lḥamîd.');
INSERT INTO `tr_transliteration` VALUES (5157, '\\`asa-llâhü ey yec\\`ale beyneküm vebeyne-lleẕîne \\`âdeytüm minhüm meveddeh. vellâhü ḳadîr. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5158, 'lâ yenhâkümü-llâhü \\`ani-lleẕîne lem yüḳâtilûküm fi-ddîni velem yuḫricûküm min diyâriküm en teberrûhüm vetuḳsiṭû ileyhim. inne-llâhe yüḥibbü-lmuḳsiṭîn.');
INSERT INTO `tr_transliteration` VALUES (5159, 'innemâ yenhâkümü-llâhü \\`ani-lleẕîne ḳâtelûküm fi-ddîni veaḫracûküm min diyâriküm veżâherû \\`alâ iḫrâciküm en tevellevhüm. vemey yetevellehüm feülâike hümu-żżâlimûn.');
INSERT INTO `tr_transliteration` VALUES (5160, 'yâ eyyühe-lleẕîne âmenû iẕâ câekümü-lmü\'minâtü mühâcirâtin femteḥinûhünn. allâhü a\\`lemü biîmânihinn. fein \\`alimtümûhünne mü\'minâtin felâ terci\\`ûhünne ile-lküffâr. lâ hünne ḥillül lehüm velâ hüm yeḥillûne lehünn. veâtûhüm mâ enfeḳû. velâ cünâḥa \\`aleyküm en tenkiḥûhünne iẕâ âteytümûhünne ücûrahünn. velâ tümsikû bi\\`iṣami-lkevâfiri ves\'elû mâ enfaḳtüm velyes\'elû mâ enfeḳû. ẕâliküm ḥukmü-llâh. yaḥkümü beyneküm. vellâhü \\`alîmün ḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5161, 'vein fâteküm şey\'üm min ezvâciküm ile-lküffâri fe\\`âḳabtüm feâtü-lleẕîne ẕehebet ezvâcühüm miŝle mâ enfeḳû. vetteḳu-llâhe-lleẕî entüm bihî mü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (5162, 'yâ eyyühe-nnebiyyü iẕâ câeke-lmü\'minâtü yübâyi\\`neke \\`alâ el lâ yüşrikne billâhi şey\'ev velâ yesriḳne velâ yeznîne velâ yaḳtülne evlâdehünne velâ ye\'tîne bibühtâniy yefterînehû beyne eydîhinne veercülihinne velâ ya\\`ṣîneke fî ma\\`rûfin febâyi\\`hünne vestagfir lehünne-llâh. inne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5163, 'yâ eyyühe-lleẕîne âmenû lâ tetevellev ḳavmen gaḍibe-llâhü \\`aleyhim ḳad yeisû mine-l\'âḫirati kemâ yeise-lküffâru min aṣḥâbi-lḳubûr.');
INSERT INTO `tr_transliteration` VALUES (5164, 'sebbeḥa lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5165, 'yâ eyyühe-lleẕîne âmenû lime teḳûlûne mâ lâ tef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (5166, 'kebüra maḳten \\`inde-llâhi en teḳûlû mâ lâ tef\\`alûn.');
INSERT INTO `tr_transliteration` VALUES (5167, 'inne-llâhe yüḥibbü-lleẕîne yüḳâtilûne fî sebîlihî ṣaffen keennehüm bünyânüm merṣûṣ.');
INSERT INTO `tr_transliteration` VALUES (5168, 'veiẕ ḳâle mûsâ liḳavmihî yâ ḳavmi lime tü\'ẕûnenî veḳat ta\\`lemûne ennî rasûlü-llâhi ileyküm. felemmâ zâgû ezâga-llâhü ḳulûbehüm. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (5169, 'veiẕ ḳâle \\`îse-bnü meryeme yâ benî isrâîle innî rasûlü-llâhi ileyküm müṣaddiḳal limâ beyne yedeyye mine-ttevrâti vemübeşşiram birasûliy ye\'tî mim ba\\`di-smühû aḥmed. felemmâ câehüm bilbeyyinâti ḳâlû hâẕâ siḥrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (5170, 'vemen ażlemü mimmeni-fterâ \\`ale-llâhi-lkeẕibe vehüve yüd\\`â ile-l\'islâm. vellâhü lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (5171, 'yürîdûne liyuṭfiû nûra-llâhi biefvâhihim vellâhü mütimmü nûrihî velev kerihe-lkâfirûn.');
INSERT INTO `tr_transliteration` VALUES (5172, 'hüve-lleẕî ersele rasûlehû bilhüdâ vedîni-lḥaḳḳi liyużhirahû \\`ale-ddîni küllihî velev kerihe-lmüşrikûn.');
INSERT INTO `tr_transliteration` VALUES (5173, 'yâ eyyühe-lleẕîne âmenû hel edüllüküm \\`alâ ticâratin tüncîküm min \\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (5174, 'tü\'minûne billâhi verasûlihî vetücâhidûne fî sebîli-llâhi biemvâliküm veenfüsiküm. ẕâliküm ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (5175, 'yagfir leküm ẕünûbeküm veyüdḫilküm cennâtin tecrî min taḥtihe-l\'enhâru vemesâkine ṭayyibeten fî cennâti \\`adn. ẕâlike-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5176, 'veuḫrâ tüḥibbûnehâ. naṣrum mine-llâhi vefetḥun ḳarîb. vebeşşiri-lmü\'minîn.');
INSERT INTO `tr_transliteration` VALUES (5177, 'yâ eyyühe-lleẕîne âmenû kûnû enṣâra-llâhi kemâ ḳâle \\`îse-bnü meryeme lilḥavâriyyîne men enṣârî ile-llâh. ḳâle-lḥavâriyyûne naḥnü enṣâru-llâhi feâmeneṭ ṭâifetüm mim benî isrâîle vekeferaṭ ṭâifeh. feeyyedne-lleẕîne âmenû \\`alâ \\`adüvvihim feaṣbeḥû żâhirîn.');
INSERT INTO `tr_transliteration` VALUES (5178, 'yüsebbiḥu lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍi-lmeliki-lḳuddûsi-l\\`azîzi-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5179, 'hüve-lleẕî be\\`aŝe fi-l\'ümmiyyîne rasûlem minhüm yetlû \\`aleyhim âyâtihî veyüzekkîhim veyü\\`allimühümü-lkitâbe velḥikmeh. vein kânû min ḳablü lefî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (5180, 'veâḫarîne minhüm lemmâ yelḥaḳû bihim. vehüve-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5181, 'ẕâlike faḍlü-llâhi yü\'tîhi mey yeşâ\'. vellâhü ẕü-lfaḍli-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5182, 'meŝelü-lleẕîne ḥummilü-ttevrâte ŝümme lem yaḥmilûhâ kemeŝeli-lḥimâri yaḥmilü esfârâ. bi\'se meŝelü-lḳavmi-lleẕîne keẕẕebû biâyâti-llâh. vellâhü lâ yehdi-lḳavme-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (5183, 'ḳul yâ eyyühe-lleẕîne hâdû in za\\`amtüm enneküm evliyâü lillâhi min dûni-nnâsi fetemennevu-lmevte in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (5184, 'velâ yetemennevnehû ebedem bimâ ḳaddemet eydîhim. vellâhü \\`alîmüm biżżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (5185, 'ḳul inne-lmevte-lleẕî tefirrûne minhü feinnehû mülâḳîküm ŝümme türaddûne ilâ \\`âlimi-lgaybi veşşehâdeti feyünebbiüküm bimâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5186, 'yâ eyyühe-lleẕîne âmenû iẕâ nûdiye liṣṣalâti miy yevmi-lcümü\\`ati fes\\`av ilâ ẕikri-llâhi veẕerü-lbey\\`. ẕâliküm ḫayrul leküm in küntüm ta\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (5187, 'feiẕâ ḳuḍiyeti-ṣṣâletü fenteşirû fi-l\'arḍi vebtegû min faḍli-llâhi veẕkürü-llâhe keŝîral le\\`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (5188, 'veiẕâ raev ticâraten ev lehven-nfeḍḍû ileyhâ veterakûke ḳâimâ. ḳul mâ \\`inde-llâhi ḫayrum mine-llehvi vemine-tticârah. vellâhü ḫayru-rrâziḳîn.');
INSERT INTO `tr_transliteration` VALUES (5189, 'iẕâ câeke-lmünâfiḳûne ḳâlû neşhedü inneke lerasûlü-llâh. vellâhü ya\\`lemü inneke lerasûlüh. vellâhü yeşhedü inne-lmünâfiḳîne lekâẕibûn.');
INSERT INTO `tr_transliteration` VALUES (5190, 'itteḫaẕû eymânehüm cünneten feṣaddû \\`an sebîli-llâh. innehüm sâe mâ kânû ya\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5191, 'ẕâlike biennehüm âmenû ŝümme keferû feṭubi\\`a \\`alâ ḳulûbihim fehüm lâ yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (5192, 'veiẕâ raeytehüm tü\\`cibüke ecsâmühüm. veiy yeḳûlû tesma\\` liḳavlihim. keennehüm ḫuşübüm müsennedeh. yaḥsebûne külle ṣayḥatin \\`aleyhim. hümü-l\\`adüvvu faḥẕerhüm. ḳâtelehümü-llâh. ennâ yü\'fekûn.');
INSERT INTO `tr_transliteration` VALUES (5193, 'veiẕâ ḳîle lehüm te\\`âlev yestagfir leküm rasûlü-llâhi levvev ruûsehüm veraeytehüm yeṣuddûne vehüm müstekbirûn.');
INSERT INTO `tr_transliteration` VALUES (5194, 'sevâün \\`aleyhim estagferte lehüm em lem testagfir lehüm. ley yagfira-llâhü lehüm. inne-llâhe lâ yehdi-lḳavme-lfâsiḳîn.');
INSERT INTO `tr_transliteration` VALUES (5195, 'hümü-lleẕîne yeḳûlûne lâ tünfiḳû \\`alâ men \\`inde rasûli-llâhi ḥattâ yenfeḍḍû. velillâhi ḫazâinü-ssemâvâti vel\'arḍi velâkinne-lmünâfiḳîne lâ yefḳahûn.');
INSERT INTO `tr_transliteration` VALUES (5196, 'yeḳûlûne leir raca\\`nâ ile-lmedîneti leyuḫricenne-l\'e\\`azzü minhe-l\'eẕell. velillâhi-l\\`izzetü velirasûlihî velilmü\'minîne velâkinne-lmünâfiḳîne lâ ya\\`lemûn.');
INSERT INTO `tr_transliteration` VALUES (5197, 'yâ eyyühe-lleẕîne âmenû lâ tülhiküm emvâlüküm velâ evlâdüküm \\`an ẕikri-llâh. vemey yef\\`al ẕâlike feülâike hümü-lḫâsirûn.');
INSERT INTO `tr_transliteration` VALUES (5198, 'veenfiḳû mimmâ razaḳnâküm min ḳabli ey ye\'tiye eḥadekümü-lmevtü feyeḳûle rabbi levlâ eḫḫartenî ilâ ecelin ḳarîbin feeṣṣaddeḳa veeküm mine-ṣṣâliḥîn.');
INSERT INTO `tr_transliteration` VALUES (5199, 'veley yü\'eḫḫira-llâhü nefsen iẕâ câe ecelühâ. vellâhü ḫabîrum bimâ ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5200, 'yüsebbiḥu lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. lehü-lmülkü velehü-lḥamd. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (5201, 'hüve-lleẕî ḫaleḳaküm feminküm kâfiruv veminküm mü\'min. vellâhü bimâ ta\\`melûne beṣîr.');
INSERT INTO `tr_transliteration` VALUES (5202, 'ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳi veṣavveraküm feaḥsene ṣuveraküm. veileyhi-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5203, 'ya\\`lemü mâ fi-ssemâvâti vel\'arḍi veya\\`lemü mâ tüsirrûne vemâ tü\\`linûn. vellâhü \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (5204, 'elem ye\'tiküm nebeü-lleẕîne keferû min ḳabl. feẕâḳû vebâle emrihim velehüm \\`aẕâbün elîm.');
INSERT INTO `tr_transliteration` VALUES (5205, 'ẕâlike biennehû kânet te\'tîhim rusülühüm bilbeyyinâti feḳâlû ebeşeruy yehdûnenâ. fekeferû vetevellev vestagne-llâh. vellâhü ganiyyün ḥamîd.');
INSERT INTO `tr_transliteration` VALUES (5206, 'za\\`ame-lleẕîne keferû el ley yüb\\`aŝû. ḳul belâ verabbî letüb\\`aŝünne ŝümme letünebbeünne bimâ \\`amiltüm. veẕâlike \\`ale-llâhi yesîr.');
INSERT INTO `tr_transliteration` VALUES (5207, 'feâminû billâhi verasûlihî vennûri-lleẕî enzelnâ. vellâhü bimâ ta\\`melûne ḫabîr.');
INSERT INTO `tr_transliteration` VALUES (5208, 'yevme yecme\\`uküm liyevmi-lcem\\`i ẕâlike yevmü-ttegâbün. vemey yü\'mim billâhi veya\\`mel ṣâliḥay yükeffir \\`anhü seyyiâtihî veyüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. ẕâlike-lfevzü-l\\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5209, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-nnâri ḫâlidîne fîhâ. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5210, 'mâ eṣâbe mim müṣîbetin illâ biiẕni-llâh. vemey yü\'mim billâhi yehdi ḳalbeh. vellâhü bikülli şey\'in \\`alîm.');
INSERT INTO `tr_transliteration` VALUES (5211, 'veeṭî\\`ü-llâhe veeṭî\\`ü-rrasûl. fein tevelleytüm feinnemâ \\`alâ rasûline-lbelâgu-lmübîn.');
INSERT INTO `tr_transliteration` VALUES (5212, 'allâhü lâ ilâhe illâ hû. ve\\`ale-llâhi felyetevekkeli-lmü\'minûn.');
INSERT INTO `tr_transliteration` VALUES (5213, 'yâ eyyühe-lleẕîne âmenû inne min ezvâciküm veevlâdiküm \\`adüvvel leküm faḥẕerûhüm. vein ta\\`fû vetaṣfeḥû vetagfirû feinne-llâhe gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5214, 'innemâ emvâlüküm veevlâdüküm fitnetün. vellâhü \\`indehû ecrun \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5215, 'fetteḳu-llâhe me-steṭa\\`tüm vesme\\`û veeṭî\\`û veenfiḳû ḫayral lienfüsiküm. vemey yûḳa şüḥḥa nefsihî feülâike hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` VALUES (5216, 'in tuḳriḍü-llâhe ḳarḍan ḥaseney yüḍâ\\`ifhü leküm veyagfir leküm. vellâhü şekûrun ḥalîm.');
INSERT INTO `tr_transliteration` VALUES (5217, '\\`âlimü-lgaybi veşşehâdeti-l\\`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5218, 'yâ eyyühe-nnebiyyü iẕâ ṭallaḳtümü-nnisâe feṭalliḳûhünne li\\`iddetihinne veaḥṣü-l\\`iddeh. vetteḳu-llâhe rabbeküm. lâ tuḫricûhünne mim büyûtihinne velâ yaḫrucne illâ ey ye\'tîne bifâḥişetim mübeyyineh. vetilke ḥudûdü-llâh. vemey yete\\`adde ḥudûde-llâhi feḳad żaleme nefseh. lâ tedrî le\\`alle-llâhe yuḥdiŝü ba\\`de ẕâlike emrâ.');
INSERT INTO `tr_transliteration` VALUES (5219, 'feiẕâ belagne ecelehünne feemsikûhünne bima\\`rûfin ev fâriḳûhünne bima\\`rûfiv veeşhidû ẕevey \\`adlim minküm veeḳîmü-şşehâdete lillâh. ẕâliküm yû\\`ażu bihî men kâne yü\'minü billâhi velyevmi-l\'âḫir. vemey yetteḳi-llâhe yec\\`al lehû maḫracâ.');
INSERT INTO `tr_transliteration` VALUES (5220, 'veyerzuḳhü min ḥayŝü lâ yaḥtesib. vemey yetevekkel \\`ale-llâhi fehüve ḥasbüh. inne-llâhe bâligu emrih. ḳad ce\\`ale-llâhü likülli şey\'in ḳadrâ.');
INSERT INTO `tr_transliteration` VALUES (5221, 'vellâî yeisne mine-lmeḥîḍi min nisâiküm ini-rtebtüm fe\\`iddetühünne ŝelâŝetü eşhüriv vellâî lem yeḥiḍn. veülâtü-l\'aḥmâli ecelühünne ey yeḍa\\`ne ḥamlehünn. vemey yetteḳi-llâhe yec\\`al lehû min emrihî yüsrâ.');
INSERT INTO `tr_transliteration` VALUES (5222, 'ẕâlike emru-llâhi enzelehû ileyküm. vemey yetteḳi-llâhe yükeffir \\`anhü seyyiâtihî veyü\\`żim lehû ecrâ.');
INSERT INTO `tr_transliteration` VALUES (5223, 'eskinûhünne min ḥayŝü sekentüm miv vucdiküm velâ tüḍârrûhünne litüḍayyiḳû \\`aleyhinn. vein künne ülâti ḥamlin feenfiḳû \\`aleyhinne ḥattâ yeḍa\\`ne ḥamlehünn. fein erḍa\\`ne leküm feâtûhünne ücûrahünn. ve\'temirû beyneküm bima\\`rûf. vein te\\`âsertüm fesetürḍi\\`u lehû uḫrâ.');
INSERT INTO `tr_transliteration` VALUES (5224, 'liyünfiḳ ẕû se\\`atim min se\\`atih. vemen ḳudira \\`aleyhi rizḳuhû felyünfiḳ mimmâ âtâhü-llâh. lâ yükellifü-llâhü nefsen illâ mâ âtâhâ. seyec\\`alü-llâhü ba\\`de \\`usriy yüsrâ.');
INSERT INTO `tr_transliteration` VALUES (5225, 'vekeeyyim min ḳaryetin \\`atet \\`an emri rabbihâ verusülihî feḥâsebnâhâ ḥisâben şedîdev ve\\`aẕẕebnâhâ \\`aẕâben nükrâ.');
INSERT INTO `tr_transliteration` VALUES (5226, 'feẕâḳat vebâle emrihâ vekâne \\`âḳibetü emrihâ ḫusrâ.');
INSERT INTO `tr_transliteration` VALUES (5227, 'e\\`adde-llâhü lehüm \\`aẕâben şedîden fetteḳu-llâhe yâ ûli-l\'elbâb. elleẕîne âmenû. ḳad enzele-llâhü ileyküm ẕikrâ.');
INSERT INTO `tr_transliteration` VALUES (5228, 'rasûley yetlû \\`aleyküm âyâti-llâhi mübeyyinâtil liyuḫrice-lleẕîne âmenû ve\\`amilu-ṣṣâliḥâti mine-żżulümâti ile-nnûr. vemey yü\'mim billâhi veya\\`mel ṣâliḥay yüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. ḳad aḥsene-llâhü lehû rizḳâ.');
INSERT INTO `tr_transliteration` VALUES (5229, 'allâhü-lleẕî ḫaleḳa seb\\`a semâvâtiv vemine-l\'arḍi miŝlehünn. yetenezzelü-l\'emru beynehünne lita\\`lemû enne-llâhe \\`alâ külli şey\'in ḳadîruv veenne-llâhe ḳad eḥâṭa bikülli şey\'in \\`ilmâ.');
INSERT INTO `tr_transliteration` VALUES (5230, 'yâ eyyühe-nnebiyyü lime tüḥarrimü mâ eḥalle-llâhü leke. tebtegî merḍâte ezvâcik. vellâhü gafûrur raḥîm.');
INSERT INTO `tr_transliteration` VALUES (5231, 'ḳad feraḍa-llâhü leküm teḥillete eymâniküm. vellâhü mevlâküm. vehüve-l\\`alîmü-lḥakîm.');
INSERT INTO `tr_transliteration` VALUES (5232, 'veiẕ eserra-nnebiyyü ilâ ba\\`ḍi ezvâcihî ḥadîŝâ. felemmâ nebbeet bihî veażherahü-llâhü \\`aleyhi \\`arrafe ba\\`ḍahû vea\\`raḍa \\`am ba\\`ḍ. felemmâ nebbeehâ bihî ḳâlet men embeeke hâẕâ. ḳâle nebbeeniye-l\\`alîmü-lḫabîr.');
INSERT INTO `tr_transliteration` VALUES (5233, 'in tetûbâ ile-llâhi feḳad ṣagat ḳulûbükümâ. vein teżâherâ \\`aleyhi feinne-llâhe hüve mevlâhü vecibrîlü veṣâliḥu-lmü\'minîn. velmelâiketü ba\\`de ẕâlike żahîr.');
INSERT INTO `tr_transliteration` VALUES (5234, '\\`asâ rabbühû in ṭalleḳakünne ey yübdilehû ezvâcen ḫayram minkünne müslimâtim mü\'minâtin ḳânitâtin tâibâtin \\`âbidâtin sâiḥâtin ŝeyyibâtiv veebkârâ.');
INSERT INTO `tr_transliteration` VALUES (5235, 'yâ eyyühe-lleẕîne âmenû ḳû enfüseküm veehlîküm nârav veḳûdühe-nnâsü velḥicâratü \\`aleyhâ melâiketün gilâżun şidâdül lâ ya\\`ṣûne-llâhe mâ emerahüm veyef\\`alûne mâ yü\'merûn.');
INSERT INTO `tr_transliteration` VALUES (5236, 'yâ eyyühe-lleẕîne keferû lâ ta\\`teẕirü-lyevm. innemâ tüczevne mâ küntüm ta\\`melûn.');
INSERT INTO `tr_transliteration` VALUES (5237, 'yâ eyyühe-lleẕîne âmenû tûbû ile-llâhi tevbeten neṣûḥâ. \\`asâ rabbüküm ey yükeffira \\`anküm seyyiâtiküm veyüdḫileküm cennâtin tecrî min taḥtihe-l\'enhâru yevme lâ yuḫzi-llâhü-nnebiyye velleẕîne âmenû me\\`ah. nûruhüm yes\\`â beyne eydîhim vebieymânihim yeḳûlûne rabbenâ etmim lenâ nûranâ vagfir lenâ. inneke \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (5238, 'yâ eyyühe-nnebiyyü câhidi-lküffâra velmünâfiḳîne vagluż \\`aleyhim. veme\'vâhüm cehennem. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5239, 'ḍarabe-llâhü meŝelel lilleẕîne keferü-mraete nûḥiv vemraete lûṭ. kânetâ taḥte \\`abdeyni min \\`ibâdinâ ṣâliḥayni feḫânetâhümâ felem yugniyâ \\`anhümâ mine-llâhi şey\'ev veḳîle-dḫule-nnâra me\\`a-ddâḫilîn.');
INSERT INTO `tr_transliteration` VALUES (5240, 'veḍarabe-llâhü meŝelel lilleẕîne âmenü-mraete fir\\`avn. iẕ ḳâlet rabbi-bni lî \\`indeke beyten fi-lcenneti veneccinî min fir\\`avne ve\\`amelihî veneccinî mine-lḳavmi-żżâlimîn.');
INSERT INTO `tr_transliteration` VALUES (5241, 'vemeryeme-bnete \\`imrâne-lletî aḥṣanet fercehâ fenefaḫnâ fîhi mir rûḥinâ veṣaddeḳat bikelimâti rabbihâ vekütübihî vekânet mine-lḳânitîn.');
INSERT INTO `tr_transliteration` VALUES (5242, 'tebârake-lleẕî biyedihi-lmülk. vehüve \\`alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` VALUES (5243, 'elleẕî ḫaleḳa-lmevte velḥayâte liyeblüveküm eyyüküm aḥsenü \\`amelâ. vehüve-l\\`azîzü-lgafûr.');
INSERT INTO `tr_transliteration` VALUES (5244, 'elleẕî ḫaleḳa seb\\`a semâvâtin ṭibâḳâ. mâ terâ fî ḫalḳi-rraḥmâni min tefâvüt. ferci\\`i-lbeṣara hel terâ min füṭûr.');
INSERT INTO `tr_transliteration` VALUES (5245, 'ŝümme-rci\\`i-lbeṣara kerrateyni yenḳalib ileyke-lbeṣaru ḫâsiev vehüve ḥasîr.');
INSERT INTO `tr_transliteration` VALUES (5246, 'veleḳad zeyyenne-ssemâe-ddünyâ bimeṣâbîḥa vece\\`alnâhâ rucûmel lişşeyâṭîni vea\\`tednâ lehüm \\`aẕâbe-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (5247, 'velilleẕîne keferû birabbihim \\`aẕâbü cehennem. vebi\'se-lmeṣîr.');
INSERT INTO `tr_transliteration` VALUES (5248, 'iẕâ ülḳû fîhâ semi\\`û lehâ şehîḳav vehiye tefûr.');
INSERT INTO `tr_transliteration` VALUES (5249, 'tekâdü temeyyezü mine-lgayż. küllemâ ülḳiye fîhâ fevcün seelehüm ḫazenetühâ elem ye\'tiküm neẕîr.');
INSERT INTO `tr_transliteration` VALUES (5250, 'ḳâlû belâ ḳad câenâ neẕîrun fekeẕẕebnâ veḳulnâ mâ nezzele-llâhü min şey\'. in entüm illâ fî ḍalâlin kebîr.');
INSERT INTO `tr_transliteration` VALUES (5251, 'veḳâlû lev künnâ nesme\\`u ev na\\`ḳilü mâ künnâ fî aṣḥâbi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (5252, 'fa\\`terafû biẕembihim. fesuḥḳal liaṣḥâbi-sse\\`îr.');
INSERT INTO `tr_transliteration` VALUES (5253, 'inne-lleẕîne yaḫşevne rabbehüm bilgaybi lehüm magfiratüv veecrun kebîr.');
INSERT INTO `tr_transliteration` VALUES (5254, 'veesirrû ḳavleküm evi-cherû bih. innehû \\`alîmüm biẕâti-ṣṣudûr.');
INSERT INTO `tr_transliteration` VALUES (5255, 'elâ ya\\`lemü men ḫaleḳ. vehüve-lleṭîfü-lḫabîr.');
INSERT INTO `tr_transliteration` VALUES (5256, 'hüve-lleẕî ce\\`ale lekümü-l\'arḍa ẕelûlen femşû fî menâkibihâ vekülû mir rizḳih. veileyhi-nnüşûr.');
INSERT INTO `tr_transliteration` VALUES (5257, 'eemintüm men fi-ssemâi ey yaḫsife bikümü-l\'arḍa feiẕâ hiye temûr.');
INSERT INTO `tr_transliteration` VALUES (5258, 'em emintüm men fi-ssemâi ey yürsile \\`aleyküm ḥâṣibâ. feseta\\`lemûne keyfe neẕîr.');
INSERT INTO `tr_transliteration` VALUES (5259, 'veleḳad keẕẕebe-lleẕîne min ḳablihim fekeyfe kâne nekîr.');
INSERT INTO `tr_transliteration` VALUES (5260, 'evelem yerav ile-ṭṭayri fevḳahüm ṣâffâtiv veyaḳbiḍn. mâ yümsikühünne ille-rraḥmân. innehû bikülli şey\'im beṣîr.');
INSERT INTO `tr_transliteration` VALUES (5261, 'emmen hâẕe-lleẕî hüve cündül leküm yenṣuruküm min dûni-rraḥmân. ini-lkâfirûne illâ fî gurûr.');
INSERT INTO `tr_transliteration` VALUES (5262, 'emmen hâẕe-lleẕî yerzüḳuküm in emseke rizḳah. bel leccû fî \\`utüvviv venüfûr.');
INSERT INTO `tr_transliteration` VALUES (5263, 'efemey yemşî mükibben \\`alâ vechihî ehdâ emmey yemşî seviyyen \\`alâ ṣirâṭim müsteḳîm.');
INSERT INTO `tr_transliteration` VALUES (5264, 'ḳul hüve-lleẕî enşeeküm vece\\`ale lekümü-ssem\\`a vel\'ebṣâra vel\'ef\'ideh. ḳalîlem mâ teşkürûn.');
INSERT INTO `tr_transliteration` VALUES (5265, 'ḳul hüve-lleẕî ẕera\'eküm fi-l\'arḍi veileyhi tuḥşerûn.');
INSERT INTO `tr_transliteration` VALUES (5266, 'veyeḳûlûne metâ hâẕe-lva\\`dü in küntüm ṣâdiḳîn.');
INSERT INTO `tr_transliteration` VALUES (5267, 'ḳul inneme-l\\`ilmü \\`inde-llâh. veinnemâ ene neẕîrum mübîn.');
INSERT INTO `tr_transliteration` VALUES (5268, 'felemmâ raevhü zülfeten sîet vucûhü-lleẕîne keferû veḳîle hâẕe-lleẕî küntüm bihî tedde\\`ûn.');
INSERT INTO `tr_transliteration` VALUES (5269, 'ḳul era\'eytüm in ehlekeniye-llâhü vemem me\\`iye ev raḥimenâ femey yücîru-lkâfirîne min \\`aẕâbin elîm.');
INSERT INTO `tr_transliteration` VALUES (5270, 'ḳul hüve-rraḥmânü âmennâ bihî ve\\`aleyhi tevekkelnâ. feseta\\`lemûne men hüve fî ḍalâlim mübîn.');
INSERT INTO `tr_transliteration` VALUES (5271, 'ḳul era\'eytüm in aṣbeḥa mâüküm gavran femey ye\'tîküm bimâim me\\`în.');
INSERT INTO `tr_transliteration` VALUES (5272, 'nûn. velḳalemi vemâ yesṭurûn.');
INSERT INTO `tr_transliteration` VALUES (5273, 'mâ ente bini\\`meti rabbike bimecnûn.');
INSERT INTO `tr_transliteration` VALUES (5274, 'veinne leke leecran gayra memnûn.');
INSERT INTO `tr_transliteration` VALUES (5275, 'veinneke le\\`alâ ḫulüḳin \\`ażîm.');
INSERT INTO `tr_transliteration` VALUES (5276, 'fesetübṣiru veyübṣirûn.');
INSERT INTO `tr_transliteration` VALUES (5277, 'bieyyikümü-lmeftûn.');
INSERT INTO `tr_transliteration` VALUES (5278, 'inne rabbeke hüve a\\`lemü bimen ḍalle \\`an sebîlih. vehüve a\\`lemü bilmühtedîn.');
INSERT INTO `tr_transliteration` VALUES (5279, 'felâ tüṭi\\`i-lmükeẕẕibîn.');
INSERT INTO `tr_transliteration` VALUES (5280, 'veddû lev tüdhinü feyüdhinûn.');
INSERT INTO `tr_transliteration` VALUES (5281, 'velâ tüṭi\\` külle ḥallâfim mehîn.');
INSERT INTO `tr_transliteration` VALUES (5282, 'hemmâzim meşşâim binemîm.');
INSERT INTO `tr_transliteration` VALUES (5283, 'mennâ\\`il lilḫayri mü\\`tedin eŝîm.');
INSERT INTO `tr_transliteration` VALUES (5284, '\\`utüllim ba\\`de ẕâlike zenîm.');
INSERT INTO `tr_transliteration` VALUES (5285, 'en kâne ẕâ mâliv vebenîn.');
INSERT INTO `tr_transliteration` VALUES (5286, 'iẕâ tütlâ \\`aleyhi âyâtünâ ḳâle esâṭîru-l\'evvelîn.');
INSERT INTO `tr_transliteration` VALUES (5287, 'senesimühû \\`ale-lḫurṭûm.');
INSERT INTO `tr_transliteration` VALUES (5288, 'innâ belevnâhüm kemâ belevnâ aṣḥâbe-lcenneh. iẕ aḳsemû leyaṣrimünnehâ muṣbiḥîn.');
INSERT INTO `tr_transliteration` VALUES (5289, 'velâ yesteŝnûn.');
INSERT INTO `tr_transliteration` VALUES (5290, 'feṭâfe \\`aleyhâ ṭâifüm mir rabbike vehüm nâimûn.');
INSERT INTO `tr_transliteration` VALUES (5291, 'feaṣbeḥat keṣṣarîm.');
INSERT INTO `tr_transliteration` VALUES (5292, 'fetenâdev muṣbiḥîn.');
INSERT INTO `tr_transliteration` VALUES (5293, 'eni-gdû \\`alâ ḥarŝiküm in küntüm ṣârimîn.');
INSERT INTO `tr_transliteration` VALUES (5294, 'fenṭaleḳû vehüm yeteḫâfetûn.');
INSERT INTO `tr_transliteration` VALUES (5295, 'el lâ yedḫulennehe-lyevme \\`aleyküm miskîn.');
INSERT INTO `tr_transliteration` VALUES (5296, 'vegadev \\`alâ ḥardin ḳâdirîn.');
INSERT INTO `tr_transliteration` VALUES (5297, 'felemmâ raevhâ ḳâlû innâ leḍâllûn.');
INSERT INTO `tr_transliteration` VALUES (5298, 'bel naḥnü maḥrûmûn.');
INSERT INTO `tr_transliteration` VALUES (5299, 'ḳâle evseṭuhüm elem eḳul leküm levlâ tüsebbiḥûn.');
INSERT INTO `tr_transliteration` VALUES (5300, 'ḳâlû sübḥâne rabbinâ innâ künnâ żâlimîn.');
